INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(1, 'Bệnh phổi kẽ', '\n   <h1 class=\"title\">Bệnh phổi kẽ</h1>   <span class=\"des\">Bệnh phổi kẽ! Bệnh phổi kẽ thực sự mô tả một nhóm các rối loạn, hầu hết trong số đó gây ra sẹo tiến triển của mô phổi. Điều này đã ảnh hưởng đến khả năng để thở và có đủ oxy vào máu. Các rối loạn khác nhau rất nhiều.  </span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh phổi kẽ thực sự m&ocirc; tả một nh&oacute;m c&aacute;c rối loạn, hầu hết trong số đ&oacute; g&acirc;y ra sẹo tiến triển của m&ocirc; phổi. Điều n&agrave;y đ&atilde; ảnh hưởng đến khả năng để thở v&agrave; c&oacute; đủ oxy v&agrave;o m&aacute;u. C&aacute;c rối loạn kh&aacute;c nhau rất nhiều. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hầu hết c&aacute;c trường hợp bệnh phổi kẽ ph&aacute;t triển dần dần, nhưng một số xuất hiện đột ngột. C&aacute;c b&aacute;c sĩ c&oacute; thể x&aacute;c định tại sao một số trường hợp bệnh phổi kẽ xảy ra, nhưng nhiều người kh&ocirc;ng c&oacute; nguy&ecirc;n nh&acirc;n. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong mọi trường hợp, một khi phổi sẹo xảy ra, thường kh&ocirc;ng thể đảo ngược. Thuốc đ&ocirc;i khi c&oacute; thể l&agrave;m chậm sự thiệt hại của c&aacute;c bệnh phổi kẽ, nhưng nhiều người kh&ocirc;ng bao giờ lấy lại được sử dụng đầy đủ phổi. C&aacute;c nh&agrave; nghi&ecirc;n cứu hy vọng rằng loại thuốc mới hơn, nhiều người vẫn c&ograve;n thử nghiệm, cuối c&ugrave;ng c&oacute; thể chứng minh hiệu quả trong điều trị bệnh phổi kẽ. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c dấu hiệu v&agrave; triệu chứng của nhiều loại rối loạn ph&acirc;n loại l&agrave; bệnh phổi kẽ c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một cảm gi&aacute;c kh&oacute; thở, đặc biệt l&agrave; trong hoặc sau khi hoạt động thể chất.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho khan.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thở kh&ograve; kh&egrave;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau ngực.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">M&oacute;ng tay c&oacute; đường cong tr&ecirc;n c&aacute;c đỉnh (club). </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở v&agrave; ho kh&ocirc; thường l&agrave; dấu hiệu ban đầu v&agrave; c&aacute;c triệu chứng. Bởi v&igrave; những vấn đề n&agrave;y l&agrave; mơ hồ v&agrave; c&oacute; xu hướng ph&aacute;t triển dần dần - thường l&agrave; rất l&acirc;u sau khi bị tổn thương phổi kh&ocirc;ng thể đảo ngược -<span style=\"\">&nbsp; </span>c&oacute; thể cho rằng l&agrave; do l&atilde;o h&oacute;a, bệnh suyễn bị thừa c&acirc;n, hoặc của h&uacute;t thuốc l&aacute;, loại h&igrave;nh hoặc những t&aacute;c động k&eacute;o d&agrave;i của một nhiễm tr&ugrave;ng h&ocirc; hấp tr&ecirc;n. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Triệu chứng c&oacute; xu hướng dần dần trở th&agrave;nh tồi tệ hơn. Cuối c&ugrave;ng c&oacute; thể nhận thấy hơi thở trong c&aacute;c hoạt động thường xuy&ecirc;n - mặc quần &aacute;o, n&oacute;i chuyện tr&ecirc;n điện thoại, thậm ch&iacute; ăn uống. Tại thời điểm n&agrave;y, kh&oacute; thở trở n&ecirc;n kh&ocirc;ng thể bỏ qua. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Đến gặp b&aacute;c sĩ khi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bằng c&aacute;c dấu hiệu thời gian v&agrave; c&aacute;c triệu chứng như kh&oacute; thở v&agrave; ho xuất hiện, kh&ocirc;ng thể đảo ngược tổn thương phổi thường đ&atilde; xảy ra. Tuy nhi&ecirc;n, điều quan trọng l&agrave; gặp b&aacute;c sĩ tại c&aacute;c dấu hiệu đầu ti&ecirc;n của kh&oacute; thở. Nhiều điều kiện kh&aacute;c với bệnh phổi kẽ c&oacute; thể ảnh hưởng đến phổi, v&agrave; nhận được chẩn đo&aacute;n sớm v&agrave; ch&iacute;nh x&aacute;c l&agrave; rất quan trọng để điều trị th&iacute;ch hợp. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mỗi khi h&iacute;t v&agrave;o, kh&ocirc;ng kh&iacute; đi v&agrave;o phổi th&ocirc;ng qua đường dẫn kh&iacute; lớn được gọi l&agrave; phế quản. B&ecirc;n trong phổi, chia nhỏ phế quản v&agrave;o đường h&ocirc; hấp nhỏ hơn (tiểu phế quản) m&agrave; cuối c&ugrave;ng kết th&uacute;c bằng cụm t&uacute;i kh&ocirc;ng kh&iacute; nhỏ (phế nang). Trong c&aacute;c bức th&agrave;nh của c&aacute;c t&uacute;i kh&iacute; l&agrave; c&aacute;c mạch m&aacute;u nhỏ (mao mạch), nơi lượng oxy được th&ecirc;m v&agrave;o m&aacute;u v&agrave; kh&iacute; carbon dioxide - một sản phẩm chất thải của sự trao đổi chất - được lấy ra. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Sẹo của t&uacute;i kh&ocirc;ng kh&iacute; nhỏ </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong bệnh phổi kẽ, c&aacute;c bức th&agrave;nh của c&aacute;c t&uacute;i kh&iacute; c&oacute; thể bị vi&ecirc;m, v&agrave; c&aacute;c m&ocirc; (interstitium) đường v&agrave; hỗ trợ c&aacute;c t&uacute;i trở n&ecirc;n ng&agrave;y c&agrave;ng d&agrave;y l&ecirc;n v&agrave; sẹo. Th&ocirc;ng thường, c&aacute;c t&uacute;i kh&iacute; đ&agrave;n hồi cao, mở rộng v&agrave; k&yacute; kết hợp đồng như bong b&oacute;ng nhỏ với từng hơi thở. Nhưng sẹo (xơ h&oacute;a) g&acirc;y ra c&aacute;c m&ocirc;, kẽ mỏng trở n&ecirc;n cứng v&agrave; d&agrave;y hơn, l&agrave;m cho t&uacute;i kh&ocirc;ng kh&iacute; &iacute;t linh hoạt hơn. Thay v&igrave; mềm mại v&agrave; đ&agrave;n hồi, t&uacute;i kh&iacute; sẹo c&oacute; kết cấu của một miếng bọt biển cứng, m&agrave; l&agrave;m cho kh&oacute; thở v&agrave; kh&oacute; khăn hơn cho oxy v&agrave;o m&aacute;u qua những bức th&agrave;nh d&agrave;y. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Phản ứng bất thường chữa bệnh </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sẹo ở bệnh phổi kẽ dường như xảy ra khi một thương t&iacute;ch cho phổi g&acirc;y ra một phản ứng chữa bệnh bất thường. Th&ocirc;ng thường, cơ thể tạo ra một lượng m&ocirc; để sửa chữa thiệt hại. Nhưng trong bệnh phổi kẽ, qu&aacute; tr&igrave;nh sửa chữa đi xi&ecirc;n, tạo m&ocirc; sẹo m&agrave; ng&agrave;y c&agrave;ng g&acirc;y trở ngại cho chức năng phổi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Một rối loạn, nhiều nguy&ecirc;n nh&acirc;n </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bởi v&igrave; bệnh phổi kẽ c&oacute; một loạt c&aacute;c nguy&ecirc;n nh&acirc;n, x&aacute;c định l&yacute; do chấn thương ban đầu m&ocirc; phổi c&oacute; thể kh&oacute; khăn. Một số c&oacute; thể c&oacute; nhiều yếu tố g&oacute;p phần bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Lao động v&agrave; c&aacute;c yếu tố m&ocirc;i trường.</span></b><span style=\"font-family: Arial;\"> Tiếp x&uacute;c l&acirc;u d&agrave;i với một số độc tố hoặc c&aacute;c chất &ocirc; nhiễm c&oacute; thể dẫn đến tổn thương phổi nghi&ecirc;m trọng. Người lao động thường xuy&ecirc;n h&iacute;t bụi silic, sợi amiăng, bụi kim loại cứng đặc biệt c&oacute; nguy cơ bị bệnh phổi nghi&ecirc;m trọng. V&igrave; vậy, những người tiếp x&uacute;c với kh&oacute;i ho&aacute; chất nhất định v&agrave; amoniac hoặc clo kh&iacute;. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tiếp x&uacute;c với một loạt c&aacute;c chất kinh ni&ecirc;n, nhiều trong ch&uacute;ng hữu cơ, cũng c&oacute; thể g&acirc;y hại phổi. Trong số n&agrave;y c&oacute; ngũ cốc, m&iacute;a đường, v&agrave; bụi từ ph&acirc;n chim v&agrave; động vật. C&aacute;c chất kh&aacute;c, chẳng hạn như hay bị mốc c&oacute; thể l&agrave; một vấn đề khi ch&uacute;ng g&acirc;y ra một phản ứng qu&aacute; mẫn ở phổi (vi&ecirc;m phổi qu&aacute; mẫn). Ngay cả vi khuẩn hoặc nấm ph&aacute;t triển qu&aacute; mức trong ẩm v&agrave; bồn tắm n&oacute;ng c&oacute; thể g&acirc;y tổn thương phổi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Nhiễm tr&ugrave;ng.</span></b><span style=\"font-family: Arial;\"> Ch&uacute;ng bao gồm nhiễm virus như cytomegalovirus, một vấn đề cụ thể nếu c&oacute; một hệ thống miễn dịch suy yếu, một số vi khuẩn l&acirc;y nhiễm bao gồm vi&ecirc;m phổi, nấm bệnh truyền nhiễm như histoplasmosis v&agrave; nhiễm k&yacute; sinh tr&ugrave;ng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Bức xạ.</span></b><span style=\"font-family: Arial;\"> Một số người nhận được trị liệu bức xạ đối với bệnh ung thư phổi hoặc ung thư v&uacute; c&oacute; dấu hiệu tổn thương phổi l&acirc;u sau khi kết th&uacute;c điều trị ph&oacute;ng xạ. C&aacute;c mức độ nghi&ecirc;m trọng của thiệt hại c&oacute; thể phụ thuộc v&agrave;o bao nhi&ecirc;u của phổi tiếp x&uacute;c với bức xạ, tổng số nhận được bức xạ, h&oacute;a trị liệu cũng được sử dụng v&agrave; cho d&ugrave; c&oacute; bệnh phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc.</span></b><span style=\"font-family: Arial;\"> Một số loại thuốc c&oacute; thể l&agrave;m hỏng c&aacute;c đường d&acirc;y m&ocirc; phổi. Những người c&oacute; nhiều khả năng g&acirc;y ra vấn đề về phổi bao gồm c&aacute;c loại thuốc h&oacute;a trị, thuốc điều trị rối loạn nhịp tim v&agrave; c&aacute;c vấn đề tim mạch kh&aacute;c, thuốc t&acirc;m thần nhất định v&agrave; một số thuốc kh&aacute;ng sinh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Điều kiện y tế kh&aacute;c.</span></b><span style=\"font-family: Arial;\"> Bệnh phổi kẽ c&oacute; thể xảy ra với c&aacute;c rối loạn kh&aacute;c. Th&ocirc;ng thường, những điều kiện kh&ocirc;ng trực tiếp tấn c&ocirc;ng phổi, nhưng thay v&agrave;o đ&oacute; ảnh hưởng đến qu&aacute; tr&igrave;nh tế b&agrave;o khắp cơ thể. Trong số n&agrave;y c&oacute; lupus, xơ cứng b&igrave;, vi&ecirc;m khớp dạng thấp, dermatomyositis, polymyositis, hội chứng Sjogren v&agrave; sarcoidosis. Một số nh&agrave; nghi&ecirc;n cứu nghĩ rằng bệnh tr&agrave;o ngược dạ d&agrave;y thực quản (GERD) - trong đ&oacute; phổi kinh ni&ecirc;n ăn một lượng nhỏ c&aacute;c chất dạ d&agrave;y - c&oacute; thể dẫn đến xơ h&oacute;a phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n kh&ocirc;ng được biết đến </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mặc d&ugrave; c&aacute;c b&aacute;c sĩ c&oacute; thể x&aacute;c định tại sao một số người bị bệnh phổi kẽ, trong nhiều trường hợp nguy&ecirc;n nh&acirc;n kh&ocirc;ng được biết. Rối loạn m&agrave; kh&ocirc;ng c&oacute; một nguy&ecirc;n nh&acirc;n được nh&oacute;m lại với nhau dưới nh&atilde;n tự ph&aacute;t xơ h&oacute;a phổi, bệnh phổi kẽ tự ph&aacute;t. Mặc d&ugrave; c&aacute;c bệnh tự ph&aacute;t c&oacute; một số t&iacute;nh năng chung, mỗi người cũng c&oacute; những đặc trưng độc đ&aacute;o. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Th&ocirc;ng thường vi&ecirc;m phổi kẽ, c&ograve;n được gọi l&agrave; xơ h&oacute;a phổi tự ph&aacute;t, l&agrave; phổ biến nhất của bệnh phổi kẽ tự ph&aacute;t. Bởi v&igrave; vi&ecirc;m phổi kẽ th&ocirc;ng thường ph&aacute;t triển trong c&aacute;c bản v&aacute; lỗi, một số khu vực của phổi l&agrave; b&igrave;nh thường, những nơi kh&aacute;c đang bị vi&ecirc;m v&agrave; vẫn c&ograve;n những nơi kh&aacute;c được đ&aacute;nh dấu bằng m&ocirc; sẹo. Căn bệnh n&agrave;y ảnh hưởng đến đ&agrave;n &ocirc;ng nhiều hơn phụ nữ v&agrave; thường ph&aacute;t triển ở những người tr&ecirc;n 50 tuổi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Loại kh&aacute;c, loại &iacute;t phổ biến của chứng xơ ho&aacute; phổi tự ph&aacute;t bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m phổi kẽ kh&ocirc;ng đặc hiệu.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m tiểu phế quản obliterans với tổ chức vi&ecirc;m phổi (Boop).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Li&ecirc;n quan h&ocirc; hấp vi&ecirc;m tiểu phế quản - phổi kẽ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Desquamative vi&ecirc;m phổi kẽ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bạch vi&ecirc;m phổi kẽ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m phổi kẽ cấp t&iacute;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Yếu tố c&oacute; thể l&agrave;m cho dễ bị bệnh phổi kẽ, bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tuổi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh phổi kẽ l&agrave; nhiều hơn c&oacute; khả năng ảnh hưởng đến người lớn, trẻ sơ sinh v&agrave; trẻ em mặc d&ugrave; đ&ocirc;i khi ph&aacute;t triển c&aacute;c rối loạn. H&igrave;nh thức tự ph&aacute;t của bệnh thường ph&aacute;t triển ở người lớn tuổi hơn 50. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tiếp x&uacute;c với độc tố lao động v&agrave; m&ocirc;i trường </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu l&agrave;m việc trong n&ocirc;ng nghiệp, khai th&aacute;c, x&acirc;y dựng hoặc v&igrave; l&yacute; do n&agrave;o tiếp x&uacute;c với c&aacute;c chất &ocirc; nhiễm được biết đến tổn thương phổi, nguy cơ mắc bệnh phổi kẽ tăng đ&aacute;ng kể. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Bức xạ v&agrave; h&oacute;a trị</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; phương ph&aacute;p điều trị ph&oacute;ng xạ ngực<span style=\"\">&nbsp; </span>hoặc sử dụng một số loại thuốc h&oacute;a trị liệu l&agrave;m cho n&oacute; nhiều khả năng sẽ ph&aacute;t triển bệnh phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Oxy</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tiếp tục h&iacute;t v&agrave;o mức độ rất cao của oxy điều trị cho hơn 48 giờ c&oacute; thể g&acirc;y hại cho phổi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ bệnh phổi kẽ tự ph&aacute;t </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c nh&agrave; nghi&ecirc;n cứu đ&atilde; x&aacute;c định được yếu tố n&agrave;o đ&oacute; c&oacute; vẻ như l&agrave;m tăng nguy cơ bệnh phổi tự ph&aacute;t, mặc d&ugrave; nguy&ecirc;n nh&acirc;n của rối loạn n&agrave;y vẫn chưa được biết: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">H&uacute;t thuốc.</span></b><span style=\"font-family: Arial;\"> Một số h&igrave;nh thức bệnh phổi kẽ c&oacute; nhiều khả năng xảy ra ở những người c&oacute; tiền sử h&uacute;t thuốc v&agrave; hoạt động h&uacute;t thuốc l&aacute; c&oacute; thể l&agrave;m cho t&igrave;nh trạng tồi tệ hơn. Nguy cơ n&agrave;y dường như tăng với số năm v&agrave; số lượng thuốc l&aacute; h&uacute;t. Tuy nhi&ecirc;n, với trường hợp ngoại lệ hiếm hoi, h&uacute;t thuốc l&aacute; kh&ocirc;ng trực tiếp g&acirc;y ra bệnh phổi kẽ. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Yếu tố di truyền.</span></b><span style=\"font-family: Arial;\"> Hiếm loại bệnh phổi kẽ tự ph&aacute;t chạy trong gia đ&igrave;nh. Được gọi l&agrave; chứng xơ phổi gia đ&igrave;nh, n&oacute; tương tự như c&aacute;c h&igrave;nh thức kh&aacute;c của bệnh. Mặc d&ugrave; nghi&ecirc;n cứu đang được thực hiện tr&ecirc;n xơ h&oacute;a phổi gia đ&igrave;nh, c&aacute;c nh&agrave; nghi&ecirc;n cứu vẫn chưa x&aacute;c định được những gen c&oacute; thể sẽ được tham gia.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tr&agrave;o ngược dạ d&agrave;y thực quản bệnh (GERD).</span></b><span style=\"font-family: Arial;\"> C&aacute;c nh&agrave; nghi&ecirc;n cứu đang điều tra một li&ecirc;n kết c&oacute; thể c&oacute; giữa bệnh tự ph&aacute;t phổi kẽ v&agrave; bệnh tr&agrave;o ngược dạ d&agrave;y thực quản. Trong GERD, acid dạ d&agrave;y hoặc thỉnh thoảng muối mật trở lại v&agrave;o thực quản v&agrave; sau đ&oacute; được v&agrave;o phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&igrave;nh th&agrave;nh m&ocirc; sẹo trong phổi c&oacute; thể dẫn đến một loạt c&aacute;c biến chứng đe dọa t&iacute;nh mạng, bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Mức độ oxy trong m&aacute;u thấp (thiếu oxy)</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bởi v&igrave; bệnh phổi kẽ l&agrave;m giảm lượng oxy trong v&agrave; đi v&agrave;o m&aacute;u, c&oacute; khả năng ph&aacute;t triển thấp hơn nồng độ &ocirc;xy trong m&aacute;u b&igrave;nh thường. Thiếu oxy nghi&ecirc;m trọng c&oacute; thể ph&aacute; vỡ chức năng cơ bản của cơ thể.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Cao huyết &aacute;p ở phổi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng giống như hệ thống &aacute;p huyết cao, t&igrave;nh trạng n&agrave;y chỉ ảnh hưởng đến c&aacute;c động mạch trong phổi. N&oacute; bắt đầu khi m&ocirc; sẹo hạn chế c&aacute;c mạch m&aacute;u nhỏ nhất, hạn chế lưu lượng m&aacute;u trong phổi. Điều n&agrave;y sẽ l&agrave;m tăng &aacute;p suất trong động mạch phổi. Tăng huyết &aacute;p động mạch phổi l&agrave; một bệnh nghi&ecirc;m trọng dần dần trở n&ecirc;n tồi tệ hơn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Suy tim</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">T&igrave;nh trạng nghi&ecirc;m trọng xảy ra khi buồng b&ecirc;n phải tr&aacute;i tim yếu hơn - &iacute;t cơ bắp hơn so với tr&aacute;i - phải bơm mạnh hơn hơn b&igrave;nh thường để di chuyển m&aacute;u qua động mạch phổi bị tắc nghẽn. Cuối c&ugrave;ng, t&acirc;m thất phải căng thẳng th&ecirc;m.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Suy h&ocirc; hấp</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong giai đoạn cuối của bệnh m&atilde;n t&iacute;nh về phổi kẽ, suy h&ocirc; hấp nghi&ecirc;m trọng xảy ra khi nồng độ &ocirc;xy trong m&aacute;u thấp c&ugrave;ng với &aacute;p lực gia tăng ở c&aacute;c động mạch phổi g&acirc;y suy tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">X&aacute;c định v&agrave; x&aacute;c định nguy&ecirc;n nh&acirc;n g&acirc;y ra bệnh phổi kẽ c&oacute; thể cực kỳ kh&oacute; khăn. Một số lượng lớn bất thường của chứng rối loạn thuộc loại n&agrave;y rộng. Hơn nữa, sự kh&aacute;c biệt giữa bệnh phổi kẽ với nhận dạng v&agrave; những nguy&ecirc;n nh&acirc;n kh&ocirc;ng r&otilde;, nguy&ecirc;n nh&acirc;n kh&ocirc;ng phải lu&ocirc;n lu&ocirc;n r&otilde; r&agrave;ng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ngo&agrave;i ra, c&aacute;c dấu hiệu v&agrave; triệu chứng của một loạt c&aacute;c điều kiện y tế - trong đ&oacute; bệnh phổi tắc nghẽn mạn t&iacute;nh (COPD), suy tim v&agrave; bệnh suyễn - c&oacute; thể bắt chước bệnh phổi kẽ, v&agrave; c&aacute;c b&aacute;c sĩ phải loại trừ ra trước khi đưa ra chẩn đo&aacute;n x&aacute;c định. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Để gi&uacute;p cắt giảm th&ocirc;ng qua sự nhầm lẫn v&agrave; loại trừ c&aacute;c bệnh kh&aacute;c c&oacute; thể, b&aacute;c sĩ<span style=\"\">&nbsp; </span>c&oacute; thể bắt đầu với một lịch sử y tế chi tiết v&agrave; c&aacute;c kỳ kh&aacute;m thực thể. Một khi điều kiện nhất định đ&atilde; được loại bỏ, b&aacute;c sĩ sẽ khuy&ecirc;n n&ecirc;n thử nghiệm như: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Chụp X - quang</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mặc d&ugrave; điều n&agrave;y thường l&agrave; thử nghiệm đầu ti&ecirc;n được đưa ra trong c&aacute;c trường hợp nghi ngờ bệnh về phổi, chụp X - quang l&agrave; kh&ocirc;ng c&oacute; hiệu quả như l&agrave; một cắt lớp vi t&iacute;nh (CT) qu&eacute;t trong chẩn đo&aacute;n bệnh phổi kẽ. N&oacute; c&oacute; thể, tuy nhi&ecirc;n, gi&uacute;p loại bỏ c&aacute;c điều kiện g&acirc;y ra c&aacute;c dấu hiệu v&agrave; triệu chứng tương tự như bệnh phổi kẽ, bao gồm kh&iacute; phế thũng v&agrave; xẹp th&ugrave;y trong phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Độ ph&acirc;n giải cao chụp cắt lớp vi t&iacute;nh (HRCT) qu&eacute;t</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong khi ngực truyền thống X - ray sản xuất h&igrave;nh ảnh hai chiều của phổi, chụp CT sử dụng một X - quang cảm ứng v&agrave; một m&aacute;y t&iacute;nh lớn để tạo ra h&igrave;nh ảnh mặt cắt ngang được th&ecirc;m rất nhiều chi tiết. HRCT qu&eacute;t đi xa hơn nữa, cho thấy m&ocirc; phổi rất chi tiết v&agrave; cung cấp th&ocirc;ng tin nhiều hơn qu&eacute;t CT th&ocirc;ng thường.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">X&eacute;t nghiệm chức năng phổi (PFTs)</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c x&eacute;t nghiệm kh&ocirc;ng x&acirc;m lấn kiểm tra tốt chức năng phổi. Để thử nghiệm, thường y&ecirc;u cầu thổi v&agrave;o một dụng cụ đơn giản gọi l&agrave; phế dung kế, bao nhi&ecirc;u kh&ocirc;ng kh&iacute; phổi c&oacute; thể giữ v&agrave; d&ograve;ng chảy của kh&ocirc;ng kh&iacute; ra khỏi phổi. Sẹo trở n&ecirc;n tồi tệ hơn, c&oacute; thể mất &iacute;t kh&ocirc;ng kh&iacute; v&agrave;o v&agrave; thổi ra &iacute;t hơn. Một số x&eacute;t nghiệm chức năng phổi cũng đo lượng kh&iacute; trao đổi qua m&agrave;ng giữa c&aacute;c phế nang v&agrave; mạch m&aacute;u mao mạch.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tập thể dục thử nghiệm</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bởi v&igrave; c&aacute;c triệu chứng của bệnh phổi kẽ l&agrave; tồi tệ hơn khi đang hoạt động, b&aacute;c sĩ c&oacute; thể đ&aacute;nh gi&aacute; chức năng phổi trong khi<span style=\"\">&nbsp; </span>tập thể dục, thường l&agrave; tr&ecirc;n một chiếc xe đạp cố định hoặc m&aacute;y chạy bộ. Mặc d&ugrave; c&aacute;c x&eacute;t nghiệm cụ thể kh&aacute;c nhau, &aacute;p lực của m&aacute;u v&agrave; nồng độ &ocirc;xy trong m&aacute;u thường được gi&aacute;m s&aacute;t như l&agrave; những kh&oacute; khăn của việc gia tăng tập thể dục.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nội soi phế quản (transbronchial sinh thiết)</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong nhiều trường hợp, bệnh phổi kẽ c&oacute; thể được chẩn đo&aacute;n chỉ dứt kho&aacute;t bằng c&aacute;ch kiểm tra một số lượng nhỏ m&ocirc; phổi (sinh thiết). Trong transbronchial sinh thiết, b&aacute;c sĩ vượt qua một ống sợi quang linh hoạt (soi phế quản) qua miệng v&agrave;o phổi v&agrave; loại bỏ một hoặc nhiều mẫu m&ocirc;, về k&iacute;ch thước của đầu kim. Sau đ&oacute; được kiểm tra trong ph&ograve;ng th&iacute; nghiệm. Nội soi phế quản được thực hiện tr&ecirc;n cơ sở bệnh nh&acirc;n ngoại tr&uacute; bằng c&aacute;ch sử dụng g&acirc;y t&ecirc; tại chỗ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Rửa phế quản</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong thủ thuật n&agrave;y, b&aacute;c sĩ ti&ecirc;m nước muối qua soi phế quản v&agrave; sau đ&oacute; ngay lập tức h&uacute;t n&oacute; ra. C&aacute;c giải ph&aacute;p thu hồi c&oacute; chứa c&aacute;c tế b&agrave;o từ c&aacute;c t&uacute;i kh&iacute;. Mặc d&ugrave; mẫu rửa phế quản một khu vực lớn hơn của phổi hơn so với c&aacute;c thủ tục kh&aacute;c, n&oacute; c&oacute; thể kh&ocirc;ng cung cấp đủ th&ocirc;ng tin để chẩn đo&aacute;n một bệnh phổi kẽ cụ thể. Thay v&agrave;o đ&oacute;, c&aacute;c b&aacute;c sĩ thường sử dụng n&oacute; để kiểm tra sự tiến bộ của một rối loạn phổi hay để gi&uacute;p x&aacute;c định điều trị tốt nhất.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Video thoracoscopic hỗ trợ phẫu thuật</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi x&eacute;t nghiệm &iacute;t x&acirc;m lấn kh&ocirc;ng mang lại một chẩn đo&aacute;n cụ thể, b&aacute;c sĩ phẫu thuật lồng ngực c&oacute; thể thực hiện phẫu thuật sinh thiết phổi. Trong tiến tr&igrave;nh n&agrave;y, một ống dẻo với một camera (nội soi) được đưa qua một đường rạch nhỏ giữa c&aacute;c xương sườn, cho ph&eacute;p c&aacute;c b&aacute;c sĩ phẫu thuật xem phổi tr&ecirc;n một m&agrave;n h&igrave;nh video. Dụng cụ phẫu thuật sau đ&oacute; được đưa v&agrave;o qua đường rạch kh&aacute;c, v&agrave; b&aacute;c sĩ phẫu thuật loại bỏ c&aacute;c mẫu m&ocirc; trong phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh phổi kẽ g&acirc;y ra bởi chất độc hoặc thuốc đ&ocirc;i khi c&oacute; thể bị đảo ngược khi<span style=\"\">&nbsp; </span>kh&ocirc;ng c&ograve;n tiếp x&uacute;c với c&aacute;c chất. Nhưng ở những người kh&ocirc;ng phải l&agrave; trường hợp triển vọng, &iacute;t triển vọng. Bởi v&igrave; c&aacute;c phương ph&aacute;p điều trị loại thuốc hiện đang c&oacute; sẵn c&oacute; thể c&oacute; t&aacute;c dụng phụ nghi&ecirc;m trọng v&agrave; thường kh&ocirc;ng hiệu quả. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều trị cho bệnh phổi kẽ thường bao gồm một sự kết hợp của c&aacute;c loại thuốc sau đ&acirc;y: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thuốc corticosteroid</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c thuốc chống vi&ecirc;m gi&uacute;p một số &iacute;t người bị bệnh phổi kẽ. Những người c&oacute; nhiều khả năng mang lại lợi &iacute;ch bị rối loạn với một nguy&ecirc;n nh&acirc;n v&agrave; đảo ngược những thay đổi trong phổi của họ. Corticosteroid hiếm khi cải thiện chức năng phổi ở những người bị xơ h&oacute;a phổi tự ph&aacute;t, v&agrave; những lợi &iacute;ch n&agrave;y thường tạm thời. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thực hiện cho thời gian d&agrave;i hoặc với liều lượng lớn corticosteroid c&oacute; thể g&acirc;y ra một số t&aacute;c dụng phụ, trong đ&oacute; c&oacute; bệnh tăng nh&atilde;n &aacute;p, lo&atilde;ng xương, cao lượng đường trong m&aacute;u dẫn đến bệnh tiểu đường, chậm chữa l&agrave;nh vết thương v&agrave; tăng t&iacute;nh nhạy cảm với nhiễm tr&ugrave;ng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Azathioprine</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thuốc n&agrave;y g&acirc;y độc tế b&agrave;o, thường được sử dụng để ngăn chặn loại bỏ cơ quan sau khi cấy gh&eacute;p, cũng c&oacute; thể được sử dụng kết hợp với corticosteroid để điều trị bệnh phổi kẽ. Azathioprine c&oacute; thể g&acirc;y ra t&aacute;c dụng phụ nghi&ecirc;m trọng, bao gồm cả sản xuất giảm c&aacute;c tế b&agrave;o m&aacute;u v&agrave; tăng nguy cơ nhiễm tr&ugrave;ng v&agrave; ung thư. Một loại thuốc g&acirc;y độc tế b&agrave;o, cyclophosphamide c&oacute; thể cố gắng nếu azathioprine kh&ocirc;ng hiệu quả. Tuy nhi&ecirc;n, c&aacute;c t&aacute;c dụng phụ li&ecirc;n kết với cyclophosphamide thậm ch&iacute; c&ograve;n nghi&ecirc;m trọng hơn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Acetylcystein</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thuốc n&agrave;y thuộc về một nh&oacute;m thuốc gọi l&agrave; chất chống oxy h&oacute;a. Qu&aacute; tr&igrave;nh oxy h&oacute;a l&agrave; một qu&aacute; tr&igrave;nh tự nhi&ecirc;n dẫn đến tế b&agrave;o v&agrave; m&ocirc; thiệt hại. Qu&aacute; tr&igrave;nh c&oacute; thể đ&oacute;ng một vai tr&ograve; trong sự ph&aacute;t triển của sẹo trong phổi (xơ phổi). Kết quả từ một thử nghiệm l&acirc;m s&agrave;ng cho thấy rằng, khi kết hợp với corticosteroid v&agrave; acetylcystein azathioprine cải thiện chức năng phổi ở những người bị xơ h&oacute;a phổi tự ph&aacute;t. Kh&ocirc;ng c&oacute; thay đổi quan trọng đ&atilde; được nh&igrave;n thấy trong tỷ lệ tử vong.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Anti - fibrotics</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những thuốc n&agrave;y đ&ocirc;i khi được sử dụng để gi&uacute;p l&agrave;m giảm sự ph&aacute;t triển của m&ocirc; sẹo. Trong c&aacute;c nghi&ecirc;n cứu l&acirc;m s&agrave;ng, c&aacute;c thuốc bosentan v&agrave; pirfenidone đ&atilde; cho thấy triển vọng l&agrave;m chậm sự tiến triển của tổn thương phổi. Theo d&otilde;i c&aacute;c nghi&ecirc;n cứu để c&oacute; được th&ocirc;ng tin về những rủi ro v&agrave; lợi &iacute;ch của c&aacute;c loại thuốc n&agrave;y trong điều trị bệnh phổi kẽ đang thực hiện. C&aacute;c loại kh&aacute;c chống xơ đ&atilde; được nghi&ecirc;n cứu như l&agrave; phương ph&aacute;p điều trị c&oacute; thể cho t&igrave;nh trạng n&agrave;y - bao gồm cả colchicine, penicillamine v&agrave; interferon gamma - 1b - đ&atilde; kh&ocirc;ng thể t&igrave;m thấy hiệu quả.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">&Ocirc;xy liệu ph&aacute;p</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">T&ugrave;y thuộc v&agrave;o mức độ nghi&ecirc;m trọng của c&aacute;c triệu chứng<span style=\"\">&nbsp; </span>v&agrave; mức độ hoạt động , b&aacute;c sĩ c&oacute; thể khuy&ecirc;n n&ecirc;n điều trị bằng oxy. Mặc d&ugrave; oxy kh&ocirc;ng thể dừng lại tổn thương phổi, n&oacute; c&oacute; thể l&agrave;m cho hơi thở v&agrave; tập thể dục dễ d&agrave;ng hơn v&agrave; ngăn ngừa hoặc l&agrave;m giảm c&aacute;c biến chứng từ mức oxy thấp trong m&aacute;u. &Ocirc;xy liệu ph&aacute;p cũng c&oacute; thể cải thiện giấc ngủ v&agrave; cảm gi&aacute;c tốt được. N&oacute; cũng c&oacute; thể l&agrave;m giảm huyết &aacute;p ở ph&iacute;a b&ecirc;n phải của tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Phục hồi chức năng phổi </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đ&acirc;y l&agrave; một chương tr&igrave;nh ch&iacute;nh thức cho những người bị bệnh phổi m&atilde;n t&iacute;nh bao gồm quản l&yacute; y tế. Mục đ&iacute;ch của phục hồi chức năng phổi kh&ocirc;ng chỉ để điều trị một bệnh hoặc thậm ch&iacute; cải thiện hoạt động h&agrave;ng ng&agrave;y, m&agrave; c&ograve;n để gi&uacute;p đỡ những người bị xơ phổi sống đầy đủ, đ&aacute;p ứng cuộc sống. C&aacute;c chương tr&igrave;nh phục hồi chức năng phổi tập trung v&agrave;o tập thể dục, giảng dạy l&agrave;m thế n&agrave;o để h&iacute;t thở hiệu quả hơn, gi&aacute;o dục v&agrave; hỗ trợ tinh thần v&agrave; tư vấn dinh dưỡng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Th&ocirc;ng thường, c&aacute;ch tiếp cận đa diện đ&ograve;i hỏi một đội ngũ c&aacute;c nh&agrave; cung cấp chăm s&oacute;c sức khỏe m&agrave; c&oacute; thể bao gồm một b&aacute;c sĩ, y t&aacute;, chuy&ecirc;n vi&ecirc;n phục hồi chức năng, chuy&ecirc;n vi&ecirc;n dinh dưỡng v&agrave; nh&acirc;n vi&ecirc;n x&atilde; hội. Chương tr&igrave;nh c&oacute; thể rất kh&aacute;c nhau, tuy nhi&ecirc;n. B&aacute;c sĩ thường c&oacute; thể cho biết về c&aacute;c chương tr&igrave;nh phục hồi chức năng phổi trong khu vực.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Cấy gh&eacute;p phổi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đ&acirc;y c&oacute; thể l&agrave; một lựa chọn cho những người bị bệnh phổi trầm trọng kẽ, những người kh&ocirc;ng c&oacute; khả năng hưởng lợi từ c&aacute;c lựa chọn điều trị kh&aacute;c. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Để được xem x&eacute;t để cấy gh&eacute;p, phải đồng &yacute; bỏ thuốc nếu<span style=\"\">&nbsp; </span>h&uacute;t thuốc, khỏe mạnh đủ để trải qua phẫu thuật v&agrave; điều trị sau gh&eacute;p, phải sẵn s&agrave;ng v&agrave; c&oacute; thể thực hiện theo c&aacute;c chương tr&igrave;nh y tế đưa ra bởi nh&oacute;m phục hồi chức năng v&agrave; cấy gh&eacute;p, ki&ecirc;n nhẫn v&agrave; sức mạnh tinh thần v&agrave; hỗ trợ để trải qua sự chờ đợi cho một cơ quan t&agrave;i trợ. Cuối c&ugrave;ng l&agrave; đặc biệt quan trọng bởi v&igrave; c&aacute;c cơ quan nh&agrave; t&agrave;i trợ được cung cấp đủ. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phong c&aacute;ch sống v&agrave; biện ph&aacute;p khắc phục</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Được tham gia v&agrave;o điều trị v&agrave; ở lại c&agrave;ng khỏe mạnh c&agrave;ng tốt l&agrave; rất cần thiết để sống chung với bệnh phổi kẽ. H&atilde;y c&aacute;c bước sau để lợi cho sức khỏe: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ngưng h&uacute;t thuốc l&aacute;</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; một mối li&ecirc;n hệ giữa h&uacute;t thuốc v&agrave; một số loại bệnh tự ph&aacute;t phổi kẽ, h&igrave;nh thức nghi&ecirc;m trọng nhất v&agrave; c&oacute; khả năng g&acirc;y chết người của sự rối loạn. N&oacute;i chuyện với b&aacute;c sĩ về c&aacute;c t&ugrave;y chọn cho việc ngừng h&uacute;t, bao gồm cả c&aacute;c chương tr&igrave;nh cai thuốc l&aacute;, sử dụng nhiều kỹ thuật đ&atilde; được chứng minh để gi&uacute;p người d&acirc;n bỏ thuốc l&aacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ghi danh v&agrave;o một chương tr&igrave;nh phục hồi chức năng phổi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c chương tr&igrave;nh n&agrave;y rất kh&aacute;c nhau, nhưng n&oacute;i chung họ tập trung v&agrave;o việc cải thiện khả năng để tập thể dục v&agrave; thực hiện c&aacute;c hoạt động b&igrave;nh thường, quản l&yacute;, kh&oacute; thở với kỹ thuật h&iacute;t thở, cải thiện sự ngon miệng v&agrave; t&igrave;nh trạng dinh dưỡng, giao dịch với c&aacute;c kh&iacute;a cạnh t&acirc;m l&yacute; kh&oacute; khăn của cuộc sống với bệnh phổi, v&agrave; n&acirc;ng cao chất lượng tổng thể của cuộc sống.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tập thể dục thường xuy&ecirc;n theo quy định của b&aacute;c sĩ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tập thể dục l&agrave; một con dao hai lưỡi đối với người bị bệnh phổi, n&oacute; đ&ograve;i hỏi một lượng tăng oxy, v&agrave; n&oacute; l&agrave;m cho c&aacute;c triệu chứng nặng hơn. Đồng thời, tập thể dục l&agrave; điều cần thiết để duy tr&igrave; chức năng phổi, giảm căng thẳng v&agrave; trầm cảm, v&agrave; duy tr&igrave; sức khỏe tổng thể v&agrave; hạnh ph&uacute;c. H&atilde;y hỏi b&aacute;c sĩ cho một giới thiệu v&agrave;o một chương tr&igrave;nh phục hồi chức năng phổi trước khi bắt đầu hoặc tiếp tục thực hiện. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ăn uống tốt</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những người bị bệnh phổi c&oacute; thể bị mất trọng lượng bởi v&igrave; n&oacute; kh&ocirc;ng thoải m&aacute;i để ăn v&agrave; v&igrave; năng lượng th&ecirc;m cần để h&iacute;t thở. Tuy nhi&ecirc;n, một chế độ ăn uống gi&agrave;u dinh dưỡng c&oacute; chứa đủ lượng calo cần thiết. C&aacute;c loại thực phẩm ăn, thời gian trong ng&agrave;y ăn v&agrave; k&iacute;ch thước của c&aacute;c phần đều c&oacute; thể đ&oacute;ng một vai tr&ograve; trong việc nu&ocirc;i dưỡng cần. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bởi v&igrave; n&oacute; thường dễ d&agrave;ng hơn để thở khi dạ d&agrave;y kh&ocirc;ng ho&agrave;n to&agrave;n đầy đủ, c&oacute; thể muốn ăn bữa ăn nhỏ trong ng&agrave;y thay v&igrave; hai hoặc ba bữa lớn. Cũng c&oacute; thể thử lựa chọn nhẹ hơn, chẳng hạn như tr&aacute;i c&acirc;y v&agrave; rau trộn, chứ kh&ocirc;ng phải c&aacute;c loại thực phẩm gi&agrave;u chất b&eacute;o, trong đ&oacute; c&oacute; nhiều năng lượng hơn để ti&ecirc;u h&oacute;a. Một chuy&ecirc;n vi&ecirc;n dinh dưỡng c&oacute; thể cung cấp hướng dẫn th&ecirc;m cho việc ăn uống khỏe mạnh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu đang thừa c&acirc;n, giảm c&acirc;n để đạt được một chỉ số khối cơ thể khỏe mạnh (BMI) c&oacute; thể c&oacute; ảnh hưởng rất lớn v&agrave;o khả năng để thở v&agrave; khoan dung tập thể dục. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Đối ph&oacute; v&agrave; hỗ trợ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sống với một bệnh phổi m&atilde;n t&iacute;nh l&agrave; t&igrave;nh cảm v&agrave; thể chất đầy th&aacute;ch thức. Th&oacute;i quen h&agrave;ng ng&agrave;y v&agrave; hoạt động c&oacute; thể cần phải được điều chỉnh, đ&ocirc;i khi ho&agrave;n to&agrave;n, như hơi thở vấn đề tồi tệ hơn hoặc chăm s&oacute;c sức khỏe cần được ưu ti&ecirc;n trong cuộc sống. Cảm gi&aacute;c giận dữ, sợ h&atilde;i v&agrave; buồn b&atilde; l&agrave; b&igrave;nh thường khi đau buồn v&igrave; sự mất m&aacute;t của cuộc sống cũ v&agrave; lo lắng về những g&igrave; tiếp theo cho bản th&acirc;n v&agrave; gia đ&igrave;nh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những đề nghị n&agrave;y c&oacute; thể gi&uacute;p đối ph&oacute; với bệnh phổi kẽ: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Chia sẻ cảm gi&aacute;c với những người th&acirc;n y&ecirc;u v&agrave; b&aacute;c sĩ </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">N&oacute;i chuyện cởi mở c&oacute; thể gi&uacute;p v&agrave; những người th&acirc;n y&ecirc;u đối ph&oacute; với những th&aacute;ch thức cảm x&uacute;c của bệnh. Ngo&agrave;i ra, r&otilde; r&agrave;ng giao tiếp sẽ gi&uacute;p v&agrave; kế hoạch gia đ&igrave;nh c&oacute; hiệu quả cho c&aacute;c nhu cầu nếu bệnh tiến triển.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">H&atilde;y xem x&eacute;t tham gia một nh&oacute;m hỗ trợ </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong một nh&oacute;m hỗ trợ, sẽ được với những người đang phải đối mặt với những th&aacute;ch thức tương tự. Nh&oacute;m th&agrave;nh vi&ecirc;n c&oacute; thể chia sẻ chiến lược đối ph&oacute;, trao đổi th&ocirc;ng tin về c&aacute;ch chữa trị mới, hoặc đơn giản chỉ lắng nghe khi thể hiện cảm x&uacute;c. Nếu một nh&oacute;m kh&ocirc;ng phải d&agrave;nh cho, c&oacute; thể muốn n&oacute;i chuyện với một nh&agrave; cung cấp sức khỏe t&acirc;m thần hoặc c&aacute;c th&agrave;nh vi&ecirc;n gi&aacute;o sĩ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&atilde;y hỏi b&aacute;c sĩ về c&aacute;c nh&oacute;m tự gi&uacute;p đỡ hoặc trị liệu trong cộng đồng. Địa phương, thư viện c&ocirc;ng cộng, danh bạ điện thoại v&agrave; Internet cũng c&oacute; thể l&agrave; nguồn tốt cho việc t&igrave;m kiếm một nh&oacute;m hỗ trợ trong khu vực.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: right;\"><span style=\"color: rgb(128, 0, 0);\"><b style=\"\"><i style=\"\"><span style=\"font-family: Arial;\"> </span></i></b></span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: right;\">&nbsp;</p></span>\n', 1);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(2, 'Bệnh phổi tắc nghẽn mãn tính (copd)', '\n   <h1 class=\"title\">Bệnh phổi tắc nghẽn mãn tính (copd)</h1>   <span class=\"des\">Bệnh phổi tắc nghẽn mãn tính - COPD! Thể hiện khác nhau tùy bệnh phổi nổi bật: Giãn phế nang hay viêm phế quản mãn tính...Yếu tố nguy cơ: Tiếp xúc với khói thuốc, tiếp xúc bụi nghề nghiệp, Gerd, tuổi ít nhất 40, di truyền...</span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh phổi tắc nghẽn m&atilde;n t&iacute;nh (COPD) l&agrave; một nh&oacute;m c&aacute;c bệnh phổi chặn luồng kh&ocirc;ng kh&iacute; v&agrave; l&agrave;m cho n&oacute; ng&agrave;y c&agrave;ng kh&oacute; khăn để thở. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh kh&iacute; thũng v&agrave; vi&ecirc;m phế quản m&atilde;n t&iacute;nh l&agrave; hai điều kiện ch&iacute;nh tạo n&ecirc;n COPD, nhưng COPD cũng c&oacute; thể do c&aacute;c thiệt hại g&acirc;y ra do vi&ecirc;m phế quản m&atilde;n t&iacute;nh trong hen. Trong mọi trường hợp, thiệt hại cho đường h&ocirc; hấp cuối c&ugrave;ng g&acirc;y trở ngại cho việc trao đổi oxy v&agrave; carbon dioxide trong phổi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">COPD l&agrave; một nguy&ecirc;n nh&acirc;n tử vong h&agrave;ng đầu tr&ecirc;n to&agrave;n thế giới về bệnh tật. Hầu hết COPD l&agrave; do h&uacute;t thuốc l&acirc;u d&agrave;i v&agrave; c&oacute; thể được ngăn ngừa bằng c&aacute;ch kh&ocirc;ng h&uacute;t thuốc hoặc bỏ thuốc ngay sau khi bắt đầu. Thiệt hại cho phổi kh&ocirc;ng thể đảo ngược, v&igrave; vậy điều trị tập trung v&agrave;o kiểm so&aacute;t triệu chứng v&agrave; giảm thiểu thiệt hại th&ecirc;m. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nh&igrave;n chung, c&aacute;c triệu chứng của COPD kh&ocirc;ng xuất hiện cho đến khi phổi thiệt hại đ&atilde; xảy ra v&agrave; n&oacute; thường tồi tệ hơn theo thời gian. Những người bị COPD cũng c&oacute; khả năng trải nghiệm gọi l&agrave; đợt kịch ph&aacute;t, trong đ&oacute; c&aacute;c triệu chứng của họ đột nhi&ecirc;n nhận được nhiều tồi tệ hơn. Ngoai ra, dấu hiệu v&agrave; triệu chứng của bệnh COPD c&oacute; thể kh&aacute;c nhau, t&ugrave;y thuộc v&agrave;o bệnh phổi nổi bật nhất. N&oacute; cũng c&oacute; thể c&oacute; nhiều trong số c&aacute;c triệu chứng n&agrave;y c&ugrave;ng một l&uacute;c. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Gi&atilde;n phế nang</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c dấu hiệu v&agrave; triệu chứng của bệnh kh&iacute; thũng bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở, đặc biệt l&agrave; trong c&aacute;c hoạt động thể chất.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thở kh&ograve; kh&egrave;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tức ngực.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Vi&ecirc;m phế quản m&atilde;n t&iacute;nh </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m phế quản m&atilde;n t&iacute;nh xảy ra chủ yếu ở người h&uacute;t thuốc. Định nghĩa l&agrave; ho m&agrave; c&oacute; &iacute;t nhất ba th&aacute;ng một năm cho hai năm li&ecirc;n tiếp. Những người tiếp tục h&uacute;t thuốc l&aacute; c&oacute; thể tiếp tục ph&aacute;t triển kh&iacute; thũng, nhưng trong những người h&uacute;t thuốc c&oacute; thể bỏ thuốc l&aacute;, ho c&oacute; thể r&otilde; r&agrave;ng trong một v&agrave;i ng&agrave;y hoặc v&agrave;i tuần. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c dấu hiệu v&agrave; triệu chứng của vi&ecirc;m phế quản m&atilde;n t&iacute;nh bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; đờm họng v&agrave;o buổi s&aacute;ng, đặc biệt l&agrave; nếu h&uacute;t thuốc.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho m&atilde;n t&iacute;nh m&agrave; sản xuất đờm v&agrave;ng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở ở c&aacute;c giai đoạn sau n&agrave;y.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thường xuy&ecirc;n bị nhiễm tr&ugrave;ng đường h&ocirc; hấp.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Hen phế quản m&atilde;n t&iacute;nh </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hen phế quản m&atilde;n t&iacute;nh thường l&agrave; vi&ecirc;m phế quản m&atilde;n t&iacute;nh kết hợp với bệnh suyễn (co thắt phế quản). Suyễn c&oacute; thể xảy ra khi bị vi&ecirc;m nhiễm chất tiết ra k&iacute;ch th&iacute;ch cơ trơn ở đường h&ocirc; hấp. C&aacute;c triệu chứng tương tự như của vi&ecirc;m phế quản m&atilde;n, nhưng cũng c&oacute; thể c&oacute; gi&aacute;n đoạn hoặc thậm ch&iacute; h&agrave;ng ng&agrave;y thở kh&ograve; kh&egrave;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Phổi l&agrave;m việc thế n&agrave;o </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng kh&iacute; đi xuống kh&iacute; quản v&agrave; v&agrave;o phổi<span style=\"\">&nbsp; </span>th&ocirc;ng qua hai ống lớn (phế quản). B&ecirc;n trong phổi, những ống ph&acirc;n chia nhiều lần như c&aacute;c chi nh&aacute;nh của c&acirc;y v&agrave;o nhiều ống nhỏ hơn (bronchioles) m&agrave; kết th&uacute;c bằng cụm t&uacute;i kh&iacute; nhỏ (phế nang). C&aacute;c t&uacute;i kh&iacute; c&oacute; bức th&agrave;nh rất mỏng đầy đủ c&aacute;c mạch m&aacute;u nhỏ (mao mạch). &Ocirc;xy trong kh&ocirc;ng kh&iacute; h&iacute;t đi v&agrave;o c&aacute;c mạch m&aacute;u v&agrave; đi v&agrave;o d&ograve;ng m&aacute;u. Đồng thời, carbon dioxide - một sản phẩm chất thải của sự trao đổi chất được lấy th&igrave; thở ra. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Động t&aacute;c thở của phổi dựa v&agrave;o t&iacute;nh đ&agrave;n hồi tự nhi&ecirc;n của c&aacute;c ống phế quản v&agrave; t&uacute;i kh&iacute;. Khi bị hư hỏng, n&oacute; bị mất t&iacute;nh đ&agrave;n hồi v&agrave; một phần sẹp lại khi thở ra, bẫy kh&ocirc;ng kh&iacute; vượt ra ngo&agrave;i khu vực sẹp. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n g&acirc;y tắc nghẽn đường th&ocirc;ng kh&iacute; </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">COPD chủ yếu đề cập đến tắc nghẽn trong phổi từ hai điều kiện phổi m&atilde;n t&iacute;nh. Nhiều người bị COPD c&oacute; cả hai. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Bệnh gi&atilde;n phế nang.</span></strong><span style=\"font-family: Arial;\"> Điều n&agrave;y g&acirc;y ra bệnh vi&ecirc;m phổi, c&oacute; thể ph&aacute; hủy một số c&aacute;c bức th&agrave;nh v&agrave; sợi đ&agrave;n hồi, cho ph&eacute;p đường h&ocirc; hấp nhỏ để sẹp khi thở ra, l&agrave;m suy yếu luồng kh&ocirc;ng kh&iacute; ra của phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Vi&ecirc;m phế quản m&atilde;n t&iacute;nh.</span></strong><span style=\"font-family: Arial;\"> Điều kiện n&agrave;y, đ&oacute; l&agrave; đặc trưng của ho li&ecirc;n tục, g&acirc;y vi&ecirc;m v&agrave; thu hẹp của c&aacute;c ống phế quản. Vi&ecirc;m phế quản m&atilde;n t&iacute;nh cũng l&agrave;m tăng sản xuất chất nhờn, m&agrave; hơn nữa c&oacute; thể thu hẹp chặn c&aacute;c ống phế quản.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hen phế quản đề cập đến vi&ecirc;m phế quản m&atilde;n t&iacute;nh k&egrave;m theo c&aacute;c cơn co thắt của sợi cơ trong lớp m&agrave;ng của đường h&ocirc; hấp (co thắt phế quản). Hen phế quản m&atilde;n t&iacute;nh đ&ocirc;i khi được ph&acirc;n loại l&agrave; COPD. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Kh&oacute;i thuốc l&aacute; v&agrave; c&aacute;c chất k&iacute;ch th&iacute;ch. </span></strong><span style=\"font-family: Arial;\">Trong hầu hết c&aacute;c trường hợp, những thiệt hại phổi dẫn đến COPD l&agrave; do h&uacute;t thuốc l&aacute; d&agrave;i hạn. Tuy nhi&ecirc;n, chất k&iacute;ch th&iacute;ch kh&aacute;c c&oacute; thể g&acirc;y COPD bao gồm cả kh&oacute;i x&igrave; g&agrave;, kh&oacute;i thuốc, kh&oacute;i ống, &ocirc; nhiễm kh&ocirc;ng kh&iacute; v&agrave; kh&oacute;i nghề nghiệp nhất định. Tr&agrave;o ngược dạ d&agrave;y (GERD), trong đ&oacute; xảy ra khi axit dạ d&agrave;y sao lưu v&agrave;o thực quản c&oacute; thể nặng th&ecirc;m COPD v&agrave; thậm ch&iacute; c&oacute; thể g&acirc;y ra n&oacute; trong một số người. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong trường hợp hiếm hoi, COPD l&agrave; kết quả từ một rối loạn di truyền g&acirc;y ra c&aacute;c mức thấp của một protein gọi l&agrave; alpha- 1 antitrypsin. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Yếu tố nguy cơ đối với COPD bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Tiếp x&uacute;c với kh&oacute;i thuốc l&aacute;. </span></strong><span style=\"font-family: Arial;\">C&aacute;c yếu tố nguy cơ quan trọng nhất đối với COPD l&agrave; h&uacute;t thuốc l&aacute; d&agrave;i hạn. Nhiều năm h&uacute;t thuốc v&agrave; c&aacute;c g&oacute;i h&uacute;t thuốc th&ecirc;m nguy cơ c&agrave;ng lớn. C&aacute;c triệu chứng của COPD thường xuất hiện khoảng 10 năm sau khi bắt đầu h&uacute;t thuốc. H&uacute;t thuốc ống, người h&uacute;t thuốc x&igrave; g&agrave; v&agrave; những người tiếp x&uacute;c với số lượng lớn kh&oacute;i thuốc cũng c&oacute; nguy cơ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Nghề nghiệp tiếp x&uacute;c với bụi v&agrave; h&oacute;a chất.</span></strong><span style=\"font-family: Arial;\"> L&acirc;u d&agrave;i tiếp x&uacute;c với kh&oacute;i ho&aacute; chất, hơi v&agrave; bụi c&oacute; thể g&acirc;y k&iacute;ch ứng v&agrave; l&agrave;m vi&ecirc;m phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Tr&agrave;o ngược dạ d&agrave;y thực quản (GERD).</span></strong><span style=\"font-family: Arial;\"> T&igrave;nh trạng n&agrave;y l&agrave; một h&igrave;nh thức nghi&ecirc;m trọng của acid tr&agrave;o ngược. GERD c&oacute; thể l&agrave;m cho COPD nặng hơn v&agrave; thậm ch&iacute; c&oacute; thể g&acirc;y ra n&oacute; trong một số người. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Tuổi. </span></strong><span style=\"font-family: Arial;\">COPD ph&aacute;t triển chậm hơn, do đ&oacute; hầu hết mọi người &iacute;t nhất 40 tuổi khi c&aacute;c triệu chứng bắt đầu.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Di truyền học. </span></strong><span style=\"font-family: Arial;\">Một rối loạn di truyền hiếm được gọi l&agrave; alpha - 1 - antitrypsin l&agrave; nguồn gốc của một số trường hợp COPD. C&aacute;c nh&agrave; nghi&ecirc;n cứu nghi ngờ rằng yếu tố di truyền kh&aacute;c cũng c&oacute; thể l&agrave;m cho một số người h&uacute;t thuốc l&aacute; dễ bị bệnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c biến chứng của bệnh COPD, bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Nhiễm tr&ugrave;ng đường h&ocirc; hấp. </span></strong><span style=\"font-family: Arial;\">Khi đ&atilde; COPD, sẽ dễ c&oacute; cảm lạnh thường xuy&ecirc;n, bệnh c&uacute;m hoặc vi&ecirc;m phổi. Cộng th&ecirc;m v&agrave;o, bất kỳ nhiễm tr&ugrave;ng đường h&ocirc; hấp c&oacute; thể l&agrave;m cho n&oacute; kh&oacute; khăn hơn nhiều để thở v&agrave; th&ecirc;m thiệt hại kh&ocirc;ng thể đảo ngược đến m&ocirc; phổi. N&oacute;i chuyện với b&aacute;c sĩ về mũi ch&iacute;ch ngừa c&uacute;m h&agrave;ng năm v&agrave; vắc xin phế cầu khuẩn thường xuy&ecirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Tăng &aacute;p động mạch phổi. </span></strong><span style=\"font-family: Arial;\">COPD c&oacute; thể g&acirc;y ra cao huyết &aacute;p trong động mạch đưa m&aacute;u tới phổi (pulmonary tăng huyết &aacute;p).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Vấn đề về tim.</span></strong><span style=\"font-family: Arial;\"> V&igrave; những l&yacute; do kh&ocirc;ng ho&agrave;n to&agrave;n hiểu, COPD l&agrave;m tăng nguy cơ mắc bệnh tim, bao gồm đau tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Ung thư phổi. </span></strong><span style=\"font-family: Arial;\">Người h&uacute;t thuốc với vi&ecirc;m phế quản m&atilde;n t&iacute;nh c&oacute; nguy cơ cao mắc ung thư phổi hơn so với người h&uacute;t thuốc l&agrave; người kh&ocirc;ng c&oacute; vi&ecirc;m phế quản m&atilde;n t&iacute;nh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Trầm cảm. </span></strong><span style=\"font-family: Arial;\">Kh&oacute; thở c&oacute; thể hạn chế hoạt động m&agrave; họ th&iacute;ch. V&agrave; n&oacute; c&oacute; thể rất kh&oacute; khăn để đối ph&oacute; với một căn bệnh v&agrave; kh&ocirc;ng chữa được. N&oacute;i chuyện với b&aacute;c sĩ nếu cảm thấy buồn hay bất lực hoặc nghĩ rằng c&oacute; thể trải qua trầm cảm. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; triệu chứng của bệnh COPD v&agrave; một lịch sử tiếp x&uacute;c với chất k&iacute;ch th&iacute;ch phổi, đặc biệt l&agrave; kh&oacute;i thuốc l&aacute;, b&aacute;c sĩ c&oacute; thể khuy&ecirc;n n&ecirc;n c&aacute;c x&eacute;t nghiệm n&agrave;y: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">X&eacute;t nghiệm chức năng phổi (đo phế dung &ndash; spirometry). Spirometry l&agrave; thử nghiệm chức năng phổi phổ biến nhất. Trong thử nghiệm n&agrave;y, sẽ được y&ecirc;u cầu thổi v&agrave;o một ống lớn kết nối với phế dung kế. Spirometry c&oacute; thể ph&aacute;t hiện COPD, ngay cả trước khi c&oacute; c&aacute;c triệu chứng của bệnh. N&oacute; cũng c&oacute; thể được sử dụng để theo d&otilde;i tiến triển của bệnh v&agrave; theo d&otilde;i điều trị như thế n&agrave;o. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">X - quang ngực. X - quang c&oacute; thể hiển thị kh&iacute; thũng - một trong những nguy&ecirc;n nh&acirc;n ch&iacute;nh của COP. X - quang cũng c&oacute; thể loại trừ vấn đề về phổi kh&aacute;c hoặc suy tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&iacute; m&aacute;u động mạch. X&eacute;t nghiệm m&aacute;u n&agrave;y cho biết phổi đưa oxy v&agrave;o m&aacute;u v&agrave; loại bỏ carbon dioxide như thế n&agrave;o. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kiểm tra đờm. Ph&acirc;n t&iacute;ch c&aacute;c tế b&agrave;o trong đờm c&oacute; thể gi&uacute;p x&aacute;c định nguy&ecirc;n nh&acirc;n của c&aacute;c vấn đề phổi v&agrave; gi&uacute;p loại trừ một số bệnh ung thư phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Chụp cắt lớp vi t&iacute;nh (CT scan). CT scan l&agrave; một kỹ thuật X - ray m&agrave; sản xuất nhiều h&igrave;nh ảnh chi tiết của c&aacute;c cơ quan nội tạng hơn so với sản xuất bằng X - quang quy ước. CT scan của phổi c&oacute; thể gi&uacute;p ph&aacute;t hiện bệnh kh&iacute; thũng v&agrave; gi&uacute;p x&aacute;c định xem c&oacute; thể hưởng lợi từ phẫu thuật khi c&oacute; COPD. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng c&oacute; c&aacute;ch chữa bệnh COPD, v&agrave; kh&ocirc;ng thể hồi phục những thiệt hại cho phổi. Nhưng phương ph&aacute;p điều trị COPD c&oacute; thể kiểm so&aacute;t triệu chứng, giảm nguy cơ biến chứng v&agrave; đợt kịch ph&aacute;t, v&agrave; cải thiện khả năng<span style=\"\">&nbsp; </span>để dẫn dắt hoạt động cuộc sống. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ngừng h&uacute;t thuốc l&aacute; </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bước quan trọng nhất trong bất kỳ kế hoạch điều trị cho người h&uacute;t thuốc với COPD l&agrave; dừng tất cả h&uacute;t thuốc. Đ&oacute; l&agrave; c&aacute;ch duy nhất để giữ cho COPD kh&ocirc;ng trở nặng - m&agrave; cuối c&ugrave;ng c&oacute; thể dẫn đến mất khả năng thở. Tuy nhi&ecirc;n, bỏ h&uacute;t thuốc kh&ocirc;ng bao giờ l&agrave; dễ d&agrave;ng. V&agrave; nhiệm vụ n&agrave;y c&oacute; thể c&oacute; vẻ đặc biệt kh&oacute; khăn nếu đ&atilde; cố gắng để bỏ thuốc l&aacute; trước đ&acirc;y. N&oacute;i chuyện với b&aacute;c sĩ về c&aacute;c sản phẩm c&oacute; thể gi&uacute;p thay thế nicotin v&agrave; thuốc, cũng như l&agrave;m thế n&agrave;o c&oacute; thể xử l&yacute; t&aacute;i ph&aacute;t. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thuốc men </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c b&aacute;c sĩ sử dụng một v&agrave;i nh&oacute;m cơ bản của thuốc để điều trị c&aacute;c triệu chứng v&agrave; biến chứng của COPD. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Thuốc gi&atilde;n phế quản. </span></strong><span style=\"font-family: Arial;\">C&aacute;c loại thuốc n&agrave;y m&agrave; thường trong một ống thuốc - thư gi&atilde;n cơ xung quanh đường thở. Điều n&agrave;y c&oacute; thể gi&uacute;p giảm ho v&agrave; kh&oacute; thở v&agrave; l&agrave;m cho hơi thở dễ d&agrave;ng hơn. T&ugrave;y thuộc v&agrave;o mức độ nghi&ecirc;m trọng của bệnh, c&oacute; thể cần thuốc d&atilde;n phế quản ngắn h&agrave;nh động trước khi hoạt động, thuốc d&atilde;n phế quản t&aacute;c dụng l&acirc;u d&agrave;i m&agrave; sử dụng h&agrave;ng ng&agrave;y, hoặc cả hai. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">H&iacute;t steroid.</span></strong><span style=\"font-family: Arial;\"> Thuốc h&iacute;t corticosteroid c&oacute; thể l&agrave;m giảm vi&ecirc;m đường th&ocirc;ng kh&iacute; v&agrave; gi&uacute;p thở tốt hơn. Nhưng k&eacute;o d&agrave;i sử dụng của c&aacute;c loại thuốc n&agrave;y c&oacute; thể l&agrave;m yếu xương v&agrave; l&agrave;m tăng nguy cơ cao huyết &aacute;p, đục thủy tinh thể v&agrave; tiểu đường. N&oacute; thường d&agrave;nh ri&ecirc;ng cho những người bị COPD trung b&igrave;nh hoặc nặng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Thuốc kh&aacute;ng sinh. </span></strong><span style=\"font-family: Arial;\">Nhiễm tr&ugrave;ng đường h&ocirc; hấp, như vi&ecirc;m phế quản cấp t&iacute;nh, vi&ecirc;m phổi v&agrave; c&uacute;m c&oacute; thể l&agrave;m nặng th&ecirc;m c&aacute;c triệu chứng COPD. Kh&aacute;ng sinh c&oacute; thể gi&uacute;p chống lại nhiễm tr&ugrave;ng do vi khuẩn, nhưng chỉ được đề nghị khi cần thiết.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Phẫu thuật </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Phẫu thuật l&agrave; một lựa chọn cho một số người với một số h&igrave;nh thức của bệnh kh&iacute; thũng nặng người kh&ocirc;ng đ&aacute;p ứng với thuốc: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Giảm khối lượng phổi. </span></strong><span style=\"font-family: Arial;\">Trong phẫu thuật n&agrave;y, b&aacute;c sĩ phẫu thuật loại bỏ phần nhỏ m&ocirc; phổi bị hư hỏng. Điều n&agrave;y tạo ra th&ecirc;m kh&ocirc;ng gian trong khoang ngực để cho m&ocirc; phổi c&ograve;n lại v&agrave; cơ ho&agrave;nh thực hiện c&aacute;c c&ocirc;ng việc hiệu quả hơn. Phẫu thuật n&agrave;y c&oacute; một số rủi ro v&agrave; kết quả l&acirc;u d&agrave;i c&oacute; thể l&agrave; kh&ocirc;ng tốt hơn so với c&aacute;c c&aacute;ch tiếp cận kh&ocirc;ng phẫu thuật. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Gh&eacute;p phổi. </span></strong><span style=\"font-family: Arial;\">Phổi cấy gh&eacute;p c&oacute; thể l&agrave; một lựa chọn cho những người nhất định với bệnh kh&iacute; thũng nặng, những người đ&aacute;p ứng ti&ecirc;u ch&iacute; cụ thể. Cấy gh&eacute;p c&oacute; thể cải thiện khả năng để thở, nhưng n&oacute; kh&ocirc;ng xuất hiện để k&eacute;o d&agrave;i cuộc sống v&agrave; c&oacute; thể phải chờ một thời gian d&agrave;i để nhận được một cơ quan hiến tặng. V&igrave; vậy, c&aacute;c quyết định gh&eacute;p phổi l&agrave; phức tạp.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">C&aacute;c liệu ph&aacute;p </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c b&aacute;c sĩ thường sử dụng c&aacute;c liệu ph&aacute;p bổ sung cho những người COPD trung b&igrave;nh hoặc nặng: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">&Ocirc;xy liệu ph&aacute;p. </span></strong><span style=\"font-family: Arial;\">Nếu kh&ocirc;ng c&oacute; đủ oxy trong m&aacute;u c&oacute; thể cần oxy bổ sung. C&oacute; rất nhiều thiết bị để cung cấp &ocirc;xy cho phổi, bao gồm cả trọng lượng nhẹ, đơn vị x&aacute;ch tay m&agrave; c&oacute; thể mang theo b&ecirc;n m&igrave;nh để chạy việc vặt. Một số người bị COPD chỉ sử dụng oxy trong qu&aacute; tr&igrave;nh hoạt động hoặc trong khi ngủ. Những người kh&aacute;c sử dụng oxy tất cả c&aacute;c thời gian. &Ocirc;xy liệu ph&aacute;p c&oacute; thể cải thiện chức năng tim, dung lượng tập thể dục, trầm cảm, r&otilde; n&eacute;t tinh thần v&agrave; chất lượng cuộc sống. Ở một số người, n&oacute; cũng c&oacute; thể k&eacute;o d&agrave;i đời sống. N&oacute;i chuyện với b&aacute;c sĩ về nhu cầu v&agrave; lựa chọn. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Chương tr&igrave;nh phục hồi chức năng phổi. </span></strong><span style=\"font-family: Arial;\">To&agrave;n diện phục hồi chức năng phổi c&oacute; thể giảm độ d&agrave;i của bất kỳ y&ecirc;u cầu nằm viện, tăng khả năng tham gia hoạt động h&agrave;ng ng&agrave;y v&agrave; cải thiện chất lượng sống. C&aacute;c chương tr&igrave;nh n&agrave;y thường kết hợp gi&aacute;o dục, tập thể dục, tư vấn dinh dưỡng. Nếu đang c&oacute; một chương tr&igrave;nh, c&oacute; thể sẽ l&agrave;m việc với một loạt c&aacute;c chuy&ecirc;n gia chăm s&oacute;c sức khỏe, bao gồm vật l&yacute; trị liệu, trị liệu h&ocirc; hấp, c&aacute;c chuy&ecirc;n gia tập thể dục v&agrave; chuy&ecirc;n gia dinh dưỡng. C&aacute;c chuy&ecirc;n gia c&oacute; thể thay đổi chương tr&igrave;nh phục hồi chức năng để đ&aacute;p ứng nhu cầu. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Quản trị đợt kịch ph&aacute;t </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ngay cả với điều trị li&ecirc;n tục, c&oacute; thể gặp khi triệu chứng đột nhi&ecirc;n tồi tệ hơn. Điều n&agrave;y được gọi l&agrave; cấp t&iacute;nh trầm trọng, v&agrave; n&oacute; c&oacute; thể g&acirc;y suy phổi nếu kh&ocirc;ng nhận được điều trị kịp thời. Bệnh trở nặng c&oacute; thể được g&acirc;y ra bởi nhiễm tr&ugrave;ng đường h&ocirc; hấp hoặc thay đổi về nhiệt độ hay &ocirc; nhiễm kh&ocirc;ng kh&iacute;. D&ugrave; nguy&ecirc;n nh&acirc;n, điều quan trọng để t&igrave;m kiếm nhanh ch&oacute;ng trợ gi&uacute;p y tế nếu ch&uacute; &yacute; hơn khi ho, một thay đổi trong chất nhờn hoặc nếu c&oacute; một hơi thở kh&oacute; khăn hơn. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi đợt kịch ph&aacute;t xảy ra, c&oacute; thể cần th&ecirc;m thuốc men, oxy bổ sung hoặc điều trị trong bệnh viện. Một khi c&aacute;c triệu chứng cải thiện, sẽ c&oacute; biện ph&aacute;p để ngăn chặn đợt kịch ph&aacute;t trong tương lai. Điều n&agrave;y c&oacute; thể bao gồm bỏ h&uacute;t thuốc, tr&aacute;nh g&acirc;y &ocirc; nhiễm trong nh&agrave; v&agrave; ngo&agrave;i trời như tập thể dục nhiều c&agrave;ng tốt, v&agrave; điều trị GERD.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phong c&aacute;ch sống v&agrave; biện ph&aacute;p khắc phục </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; COPD, c&oacute; thể thực hiện c&aacute;c bước để cảm thấy tốt hơn v&agrave; l&agrave;m chậm sự thiệt hại cho phổi: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Kiểm so&aacute;t hơi thở.</span></strong><span style=\"font-family: Arial;\"> N&oacute;i chuyện với b&aacute;c sĩ hoặc b&aacute;c sĩ chuy&ecirc;n khoa h&ocirc; hấp về kỹ thuật h&iacute;t thở hiệu quả hơn trong cả ng&agrave;y. Ngo&agrave;i ra h&atilde;y chắc chắn để thảo luận về vị tr&iacute; thở v&agrave; kỹ thuật thư gi&atilde;n m&agrave; c&oacute; thể sử d���ng khi đang bị hụt hơi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Đường h&ocirc; hấp r&otilde; r&agrave;ng. </span></strong><span style=\"font-family: Arial;\">Trong COPD, chất nhờn c&oacute; xu hướng thu thập trong đoạn th&ocirc;ng kh&iacute; v&agrave; c&oacute; thể kh&oacute; r&otilde; r&agrave;ng. Kiểm so&aacute;t ho, uống thật nhiều nước v&agrave; sử dụng m&aacute;y tạo độ ẩm c&oacute; thể gi&uacute;p đỡ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Tập thể dục thường xuy&ecirc;n.</span></strong><span style=\"font-family: Arial;\"> N&oacute; c&oacute; thể c&oacute; vẻ kh&oacute; thực hiện khi c&oacute; kh&oacute; thở, nhưng thường xuy&ecirc;n tập thể dục c&oacute; thể cải thiện tổng thể sức mạnh v&agrave; sự dẻo dai v&agrave; tăng cường cơ bắp h&ocirc; hấp.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Ăn c&aacute;c loại thực phẩm l&agrave;nh mạnh. </span></strong><span style=\"font-family: Arial;\">Một chế độ ăn uống l&agrave;nh mạnh c&oacute; thể gi&uacute;p duy tr&igrave; sức mạnh. Nếu đang thiếu c&acirc;n, b&aacute;c sĩ c&oacute; thể khuy&ecirc;n n&ecirc;n bổ sung dinh dưỡng. Nếu đang thừa c&acirc;n, giảm c&acirc;n đ&aacute;ng kể c&oacute; thể gi&uacute;p hơi thở, đặc biệt l&agrave; trong thời kỳ gắng sức. .</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Tr&aacute;nh h&uacute;t thuốc. </span></strong><span style=\"font-family: Arial;\">Ngo&agrave;i việc bỏ h&uacute;t thuốc, điều quan trọng để tr&aacute;nh những nơi m&agrave; những người kh&aacute;c h&uacute;t thuốc. Kh&oacute;i thuốc c&oacute; thể g&oacute;p phần thiệt hại phổi th&ecirc;m. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Ch&uacute; &yacute; đến ợ n&oacute;ng thường xuy&ecirc;n.</span></strong><span style=\"font-family: Arial;\"> Thường xuy&ecirc;n ợ n&oacute;ng c&oacute; thể chỉ ra bệnh tr&agrave;o ngược dạ d&agrave;y (GERD), một điều kiện trong đ&oacute; acid dạ d&agrave;y hoặc, thỉnh thoảng mật chảy trở lại v&agrave;o đường ống thực quản. Điều n&agrave;y thường xuy&ecirc;n c&oacute; thể l&agrave;m nặng th&ecirc;m bệnh COPD, nhưng điều trị GERD c&oacute; thể gi&uacute;p đỡ. N&oacute;i chuyện với b&aacute;c sĩ nếu c&oacute; ợ n&oacute;ng thường xuy&ecirc;n. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Đi kh&aacute;m b&aacute;c sĩ thường xuy&ecirc;n. </span></strong><span style=\"font-family: Arial;\">L&ecirc;n lịch hẹn, ngay cả khi đang cảm thấy tốt. Điều quan trọng l&agrave; đều đặn theo d&otilde;i chức năng phổi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Đối ph&oacute; v&agrave; hỗ trợ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sống với COPD c&oacute; thể kh&oacute; khăn, đặc biệt l&agrave; khi n&oacute; trở n&ecirc;n nhiều hơn v&agrave; kh&oacute; khăn hơn để nắm bắt hơi thở. C&oacute; thể phải từ bỏ c&aacute;c hoạt động trước đ&acirc;y rất th&iacute;ch. Gia đ&igrave;nh v&agrave; người th&acirc;n c&oacute; thể gặp phải thay đổi quan trọng v&agrave; th&aacute;ch thức trong một nỗ lực để gi&uacute;p. Cũng c&oacute; thể thấy m&igrave;nh phải đối mặt với một số c&acirc;u hỏi kh&oacute; khăn, chẳng hạn như bao l&acirc;u phải sống v&agrave; những g&igrave; sẽ l&agrave;m g&igrave; nếu kh&ocirc;ng c&ograve;n c&oacute; thể chăm s&oacute;c bản th&acirc;n m&igrave;nh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">N&oacute; c&oacute; thể gi&uacute;p để chia sẻ nỗi sợ h&atilde;i v&agrave; cảm x&uacute;c với gia đ&igrave;nh, người th&acirc;n v&agrave; b&aacute;c sĩ. Cũng c&oacute; thể muốn xem x&eacute;t tham gia một nh&oacute;m hỗ trợ cho những người bị COPD. V&agrave; c&oacute; thể hưởng lợi từ tư vấn nếu cảm thấy ch&aacute;n nản hoặc qu&aacute; tải. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Ph&ograve;ng chống</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng giống như một số bệnh, COPD c&oacute; nguy&ecirc;n nh&acirc;n r&otilde; r&agrave;ng v&agrave; một con đường r&otilde; r&agrave;ng về c&ocirc;ng t&aacute;c ph&ograve;ng chống. Phần lớn c&aacute;c trường hợp li&ecirc;n quan trực tiếp đến h&uacute;t thuốc l&aacute; v&agrave; c&aacute;ch tốt nhất để ngăn ngừa COPD l&agrave; kh&ocirc;ng bao giờ h&uacute;t thuốc l&aacute; - hoặc bỏ h&uacute;t thuốc. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu l&agrave; một người h&uacute;t thuốc l&acirc;u năm, c&aacute;c b&aacute;o c&aacute;o n&agrave;y kh&ocirc;ng đơn giản c&oacute; thể c&oacute; vẻ đơn giản như vậy, đặc biệt l&agrave; nếu đ&atilde; cố gắng bỏ thuốc một lần, hai lần hoặc nhiều lần trước. Nhưng n&oacute; quan trọng để t&igrave;m một chương tr&igrave;nh cai nghiện thuốc l&aacute; c&oacute; thể gi&uacute;p bỏ th&oacute;i quen. Đ&oacute; l&agrave; cơ hội tốt nhất để ngăn ngừa thiệt hại cho phổi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nghề nghiệp tiếp x&uacute;c với kh&oacute;i ho&aacute; chất v&agrave; bụi l&agrave; một yếu tố nguy cơ COPD. Nếu l&agrave;m việc với c&aacute;c loại chất k&iacute;ch th&iacute;ch phổi, n&oacute;i chuyện với người gi&aacute;m s&aacute;t về những c&aacute;ch tốt nhất để bảo vệ m&igrave;nh, chẳng hạn như đeo khẩu trang. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: right;\"><b style=\"\"><i style=\"\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(128, 0, 0);\"></span></span></i></b></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\" class=\"MsoNormal\">&nbsp;</p></span>\n', 1);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(3, 'Cơn hen phế quản', '\n   <h1 class=\"title\">Cơn hen phế quản</h1>   <span class=\"des\">Cơn hen phế quản! Chìa khóa để ngăn chặn một cuộc tấn công của bệnh hen là chẩn đoán và điều trị một cơn hen sớm. Thực hiện theo các kế hoạch điều trị đã làm việc với bác sĩ trước.</span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong một cơn hen, gọi l&agrave; trầm trọng trong bệnh hen, c&aacute;c cơ xung quanh đường h&ocirc; hấp trở n&ecirc;n sưng v&agrave; vi&ecirc;m, g&acirc;y ra thu hẹp ống ống phế quản. Trong một cơn hen, c&oacute; thể ho, thở kh&ograve; kh&egrave; v&agrave; kh&oacute; thở. Một cuộc tấn c&ocirc;ng của bệnh hen c&oacute; thể ở trẻ vị th&agrave;nh ni&ecirc;n, với c&aacute;c triệu chứng m&agrave; điều trị tại nh&agrave; c&oacute; thể cải thiện tốt nhanh ch&oacute;ng, hoặc n&oacute; c&oacute; thể nghi&ecirc;m trọng hơn. Một cơn hen nặng m&agrave; kh&ocirc;ng cải thiện với điều trị tại nh&agrave; c&oacute; thể trở th&agrave;nh một trường hợp khẩn cấp đe dọa t&iacute;nh mạng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ch&igrave;a kh&oacute;a để ngăn chặn một cuộc tấn c&ocirc;ng của bệnh hen l&agrave; chẩn đo&aacute;n v&agrave; điều trị một cơn hen sớm. Thực hiện theo c&aacute;c kế hoạch điều trị đ&atilde; l&agrave;m việc với b&aacute;c sĩ trước. Kế hoạch n&agrave;y n&ecirc;n bao gồm những việc cần l&agrave;m khi bệnh hen bắt đầu trở nặng, v&agrave; để đối ph&oacute; với cơn hen tiến triển như thế n&agrave;o. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c triệu chứng cơn hen bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hụt nghi&ecirc;m trọng của hơi thở, đau ngực hoặc đau thắt v&agrave; ho hoặc thở kh&ograve; kh&egrave;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Lưu lượng đỉnh thở thấp (PEF), nếu sử dụng một m&aacute;y đo lưu lượng đỉnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sử dụng cứu trợ nhanh bằng thuốc ống l&agrave; kh&ocirc;ng đủ để kiềm chế c&aacute;c triệu chứng xấu đi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c dấu hiệu v&agrave; triệu chứng của cơn hen kh&aacute;c nhau từ người n&agrave;y sang người kh&aacute;c. L&agrave;m việc với b&aacute;c sĩ để x&aacute;c định dấu hiệu cụ thể v&agrave; triệu chứng của bệnh hen xấu đi v&agrave; phải l&agrave;m g&igrave; khi ch&uacute;ng xảy ra. Nếu c&aacute;c triệu chứng hen tiếp tục xuất hiện thậm ch&iacute; tồi tệ hơn sau khi uống thuốc như b&aacute;c sĩ chỉ dẫn, c&oacute; thể cần đến ph&ograve;ng cấp cứu. B&aacute;c sĩ c&oacute; thể gi&uacute;p t&igrave;m hiểu để chẩn đo&aacute;n một trường hợp cơn hen khẩn cấp, do đ&oacute; sẽ biết khi n&agrave;o cần c&oacute; sự gi&uacute;p đỡ. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu bệnh hen r&otilde; r&agrave;ng, ngay lập tức l&agrave;m theo c&aacute;c bước điều trị v&agrave; b&aacute;c sĩ ra chỉ định l&agrave;m việc trước khi ghi th&agrave;nh văn bản. Nếu c&aacute;c triệu chứng v&agrave; lưu lượng đỉnh thở (PEF) được cải thiện, điều trị đ&oacute; c&oacute; thể l&agrave; cần thiết. Nếu c&aacute;c triệu chứng kh&ocirc;ng cải thiện khi điều trị tại nh&agrave;, cần phải t&igrave;m kiếm sự chăm s&oacute;c khẩn cấp. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi c&aacute;c triệu chứng hen b&ugrave;ng l&ecirc;n, h&atilde;y l&agrave;m theo c&aacute;c hướng dẫn trong đơn để sử dụng, h&agrave;nh động nhanh ch&oacute;ng cấp cứu. Nếu sử dụng đo lưu lượng đỉnh cao để theo d&otilde;i bệnh hen, c&aacute;ch đọc kh&aacute;c nhau, PEF 50-79 phần trăm mức tốt nhất l&agrave; một dấu hiệu cần sử dụng h&agrave;nh động nhanh với thuốc theo quy định của b&aacute;c sĩ. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hen c&oacute; thể thay đổi theo thời gian, do đ&oacute; cần điều chỉnh định kỳ để kế hoạch điều trị, để giữ cho c&aacute;c triệu chứng h&agrave;ng ng&agrave;y dưới sự kiểm so&aacute;t. Nếu bệnh hen kh&ocirc;ng được kiểm so&aacute;t tốt, n&oacute; l&agrave;m tăng nguy cơ cơn hen trong tương lai. Vi&ecirc;m phổi k&eacute;o d&agrave;i c&oacute; nghĩa l&agrave; bệnh hen c&oacute; thể b&ugrave;ng l&ecirc;n bất cứ l&uacute;c n&agrave;o. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; bệnh hen thường xuy&ecirc;n l&ecirc;n cơn, đọc lưu lượng đỉnh thấp hoặc c&aacute;c dấu hiệu bệnh hen kh&ocirc;ng được kiểm so&aacute;t tốt, gặp b&aacute;c sĩ. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">T&igrave;m kiếm sự chăm s&oacute;c y tế ngay nếu c&oacute; dấu hiệu của một cơn hen nghi&ecirc;m trọng, trong đ&oacute; bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở hoặc thở kh&ograve; kh&egrave; nặng, đặc biệt l&agrave; v&agrave;o ban đ&ecirc;m hoặc v&agrave;o s&aacute;ng sớm.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng c&oacute; khả năng n&oacute;i được nhiều hơn c&aacute;c cụm từ ngắn do kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Cơ ngực co để thở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Lưu lượng đỉnh thấp khi sử dụng một m&aacute;y đo lưu lượng đỉnh cao.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một hệ thống miễn dịch qu&aacute; nhạy cảm l&agrave;m cho đường thở (ống phế quản) bị vi&ecirc;m v&agrave; sưng l&ecirc;n khi tiếp x&uacute;c với một số k&iacute;ch hoạt. Hen g&acirc;y n&ecirc;n kh&aacute;c nhau từ người n&agrave;y sang người kh&aacute;c. Thường g&acirc;y cơn hen bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Phấn hoa, vật nu&ocirc;i, nấm mốc v&agrave; bụi ve.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhiễm tr&ugrave;ng đường h&ocirc; hấp tr&ecirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute;i thuốc l&aacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tập thể dục.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&iacute;t v&agrave;o lạnh, kh&ocirc;ng kh&iacute; kh&ocirc;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tr&agrave;o ngược dạ d&agrave;y thực quản (GERD).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đối với nhiều người, c&aacute;c triệu chứng hen nặng hơn với một nhiễm tr&ugrave;ng đường h&ocirc; hấp như cảm lạnh. Một số người c&oacute; bệnh hen l&ecirc;n cơn g&acirc;y ra bởi một c&aacute;i g&igrave; đ&oacute; trong m&ocirc;i trường c&ocirc;ng việc của họ. Đ&ocirc;i khi, cơn hen xảy ra m&agrave; kh&ocirc;ng c&oacute; nguy&ecirc;n nh&acirc;n r&otilde; r&agrave;ng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bất cứ ai bị hen c&oacute; nguy cơ một cuộc tấn c&ocirc;ng của cơn hen. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; thể c&oacute; nguy cơ gia tăng của một cơn hen nghi&ecirc;m trọng nếu: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đ&atilde; c&oacute; một cơn hen nghi&ecirc;m trọng trong qu&aacute; khứ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đ&atilde; được nhập viện hoặc phải đi đến ph&ograve;ng cấp cứu cho bệnh hen.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sử dụng nhiều hơn hai lần thuốc h&iacute;t giảm nhanh triệu chứng / một th&aacute;ng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c cuộc tấn c&ocirc;ng của bệnh hen c&oacute; xu hướng đột ngột trước khi nhận thấy c&aacute;c tiền triệu th&igrave; triệu chứng đ&atilde; xấu.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; vấn đề sức khỏe kinh ni&ecirc;n kh&aacute;c, chẳng hạn như vi&ecirc;m xoang hoặc polip mũi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hen tấn c&ocirc;ng c&oacute; thể nghi&ecirc;m trọng. C&oacute; thể l&agrave;m gi&aacute;n đoạn hoạt động h&agrave;ng ng&agrave;y như ngủ, học, l&agrave;m việc v&agrave; tập thể dục, g&acirc;y ra một t&aacute;c động đ&aacute;ng kể đến chất lượng sống v&agrave; c&oacute; thể ph&aacute; vỡ cuộc sống của những người xung quanh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Cơn hen nghi&ecirc;m trọng c&oacute; nghĩa l&agrave; c&oacute; lẽ phải đi đến ph&ograve;ng cấp cứu, c&oacute; thể căng thẳng v&agrave; tốn k&eacute;m.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một cơn hen rất nặng c&oacute; thể dẫn đến ngừng h&ocirc; hấp v&agrave; tử vong.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Những chuẩn bị cho việc kh&aacute;m bệnh</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">L&agrave;m việc với b&aacute;c sĩ để tạo ra một kế hoạch. Kế hoạch n&agrave;y n&ecirc;n thảo luận l&agrave;m thế n&agrave;o để điều trị cơn hen.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&atilde;y lưu kết quả lưu lượng đỉnh v&agrave; tất cả c&aacute;c thuốc. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&atilde;y chuẩn bị để thảo luận về c&aacute;c triệu chứng, v&agrave; những lo lắng. Th&ocirc;ng thường, thay đổi định kỳ trong điều trị l&agrave; cần thiết để giữ cho bệnh hen được kiểm so&aacute;t v&agrave; ngăn ngừa cơn hen.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&atilde;y chuẩn bị để chứng minh c&aacute;ch sử dụng ống thuốc (xịt). Sử dụng kh&ocirc;ng đ&uacute;ng c&aacute;ch c&oacute; thể l&agrave;m giảm hiệu quả của một ống thuốc.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thời gian với b&aacute;c sĩ l&agrave; c&oacute; hạn, n&ecirc;n chuẩn bị một danh s&aacute;ch c&aacute;c c&acirc;u hỏi sẽ gi&uacute;p tận dụng tối đa thời gian với nhau. Một số c&acirc;u hỏi tốt để y&ecirc;u cầu b&aacute;c sĩ bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c bước cần l&agrave;m để giữ cho bệnh hen dưới sự kiểm so&aacute;t?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thuốc hoặc kế hoạch điều trị cần phải được thay đổi?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c dấu hiệu của một cuộc tấn c&ocirc;ng sắp xảy ra bệnh hen l&agrave; g&igrave;?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi c&aacute;c dấu hiệu v&agrave; triệu chứng hen nặng hơn, những g&igrave; t&ocirc;i cần phải l&agrave;m để ngăn chặn một cuộc tấn c&ocirc;ng?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c bước cần phải thực hiện để ngăn chặn một cuộc tấn c&ocirc;ng bệnh hen trong tương lai?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi n&agrave;o cần phải đi đến ph&ograve;ng cấp cứu hoặc t&igrave;m kiếm sự điều trị khẩn cấp kh&aacute;c? </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đang gặp ợ n&oacute;ng nhiều hơn. Những g&igrave; t&ocirc;i c&oacute; thể l&agrave;m để ngăn chặn điều n&agrave;y?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; thời gian để ch&iacute;ch ngừa c&uacute;m kh&ocirc;ng? </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Biện ph&aacute;p g&igrave; c&oacute; thể thực hiện trong m&ugrave;a lạnh v&agrave; c&uacute;m?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ngo&agrave;i những c&acirc;u hỏi m&agrave; đ&atilde; chuẩn bị sẵn s&agrave;ng để y&ecirc;u cầu b&aacute;c sĩ, đừng ngần ngại đặt c&acirc;u hỏi bất kỳ thời gian m&agrave; kh&ocirc;ng hiểu điều g&igrave; đ&oacute;. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đối với người lớn v&agrave; trẻ em tr&ecirc;n năm tuổi, thử nghiệm chức năng phổi được sử dụng để kiểm tra. Chức năng phổi ngh&egrave;o n&agrave;n l&agrave; một dấu hiệu cho thấy bệnh hen kh&ocirc;ng được kiểm so&aacute;t tốt. Trong một số trường hợp, c&aacute;c x&eacute;t nghiệm chức năng phổi được sử dụng trong trường hợp khẩn cấp để gi&uacute;p kiểm tra mức độ nghi&ecirc;m trọng của một cuộc tấn c&ocirc;ng hoặc điều trị như thế n&agrave;o. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">X&eacute;t nghiệm chức năng phổi bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Peak d&ograve;ng chảy. Cũng c&oacute; thể sử dụng đo lưu lượng đỉnh ở nh&agrave; để theo d&otilde;i chức năng phổi. C&aacute;c kết quả của thử nghiệm n&agrave;y được gọi l&agrave; d&ograve;ng chảy thở đỉnh (PEF). Một b&agrave;i kiểm tra lưu lượng đỉnh được thực hiện bằng c&aacute;ch thổi v&agrave;o một ống tẩu mạnh v&agrave; nhanh như c&oacute; thể với một hơi thở đơn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đo phế dung (spirometry). Trong spirometry, h&iacute;t thở s&acirc;u v&agrave; thở ra mạnh mẽ v&agrave;o một ống nối với một m&aacute;y được gọi l&agrave; phế dung kế. Một đo lường spirometry phổ biến l&agrave; khối lượng buộc phải thở, m&agrave; c&aacute;c biện ph&aacute;p bao nhi&ecirc;u c&oacute; thể h&iacute;t thở kh&ocirc;ng kh&iacute; trong một gi&acirc;y. C&aacute;c kết quả của thử nghiệm n&agrave;y được gọi l&agrave; khối lượng buộc phải thở (FEV). Spirometry cũng c&oacute; thể đo được bao nhi&ecirc;u kh&ocirc;ng kh&iacute; phổi c&oacute; thể giữ v&agrave; tỷ lệ m&agrave; tại đ&oacute; c&oacute; thể h&iacute;t v&agrave;o v&agrave; thở ra.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đo lường nitric oxide. Một x&eacute;t nghiệm chẩn đo&aacute;n mới hơn, đo lượng kh&iacute; oxit nitric c&oacute; trong hơi thở. Oxit nitric cao cho biết t&igrave;nh trạng vi&ecirc;m của c&aacute;c ống phế quản. Để l&agrave;m kiểm tra n&agrave;y, thở ra từ từ v&agrave;o một c&aacute;i loa gắn liền với một thiết bị đo lường điện tử. Thiết bị n&agrave;y được gắn với một m&aacute;y t&iacute;nh với một m&agrave;n h&igrave;nh hiển thị c&aacute;c kết quả thử nghiệm.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"\"><span style=\"font-family: Arial;\">Định lượng &ocirc; xy động mạch</span></span><span style=\"font-family: Arial;\">. Thử nghiệm n&agrave;y được sử dụng trong một cơn hen nặng. N&oacute; đo lượng &ocirc;xy trong m&aacute;u. N&oacute; được đo th&ocirc;ng qua m&oacute;ng tay v&agrave; chỉ mất v&agrave;i gi&acirc;y.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu gặp cơn hen, theo c&aacute;c bước trong kế hoạch hen đ&atilde; l&agrave;m việc với b&aacute;c sĩ. Nếu c&aacute;c triệu chứng kh&ocirc;ng cải thiện, t&igrave;m kiếm sự chăm s&oacute;c y tế ngay lập tức. Bước điều trị để ngăn chặn một cuộc tấn c&ocirc;ng bệnh hen thường bao gồm việc 2 - 6 ống albuterol, sử dụng thuốc h&agrave;nh động nhanh v&agrave;i ph&uacute;t. N&oacute;i chung, thuốc d&ugrave;ng &iacute;t hơn l&agrave; cần thiết cho trẻ em v&agrave; người lớn với c&aacute;c triệu chứng &iacute;t nghi&ecirc;m trọng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu sử dụng đo lưu lượng đỉnh cao để theo d&otilde;i bệnh hen, c&aacute;ch đọc kh&aacute;c nhau, lưu lượng đỉnh thở 50 - 79 phần trăm tốt nhất l&agrave; một dấu hiệu cần sử dụng albuterol hoặc c&aacute;c h&agrave;nh động nhanh thuốc h&iacute;t. Thường xuy&ecirc;n kiểm tra đo lưu lượng đỉnh l&agrave; quan trọng, bởi v&igrave; chức năng phổi c&oacute; thể giảm trước khi nhận thấy bất kỳ dấu hiệu hoặc triệu chứng của bệnh hen xấu đi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thuốc ống HFA: Một thay đổi gần đ&acirc;y: C&aacute;c chlorofluorocarbon (CFC) nổ đẩy trong ống thuốc giảm nhanh bệnh hen đ&atilde; được thay thế bằng một ống thuốc được gọi l&agrave; hydrofluoroalkane (HFA). Kh&ocirc;ng giống như c&aacute;c thuốc h&iacute;t CFC, HFA ống kh&ocirc;ng g&acirc;y hại m&ocirc;i trường. Việc phun từ c&aacute;c ống thuốc mới c&oacute; thể hương vị kh&aacute;c nhau. Mặc d&ugrave; phun từ một ống thuốc HFA c&oacute; thể kh&ocirc;ng c&oacute; vẻ mạnh mẽ, vẫn nhận được liều to&agrave;n bộ của thuốc. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều trị khẩn cấp. Nếu đi đến ph&ograve;ng cấp cứu cho một cuộc tấn c&ocirc;ng bệnh hen trong tiến tr&igrave;nh, sẽ cần thuốc để c&oacute; được bệnh dưới sự kiểm so&aacute;t ngay lập tức. Đ&acirc;y c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Chủ vận beta, như albuterol. C&aacute;c loại thuốc n&agrave;y l&agrave; thuốc giống như những thuốc d&ugrave;ng trong h&agrave;nh động nhanh ch&oacute;ng. C&oacute; thể cần phải sử dụng một m&aacute;y được gọi l&agrave; nebulizer, m&agrave; lần lượt c&aacute;c thuốc th&agrave;nh sương m&ugrave; c&oacute; thể được h&iacute;t s&acirc;u v&agrave;o phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Corticosteroid uống. C&aacute;c loại thuốc n&agrave;y gi&uacute;p giảm vi&ecirc;m phổi v&agrave; nhận được c&aacute;c triệu chứng hen dưới sự kiểm so&aacute;t. Đối với cơn hen nhiều hơn, nặng, corticosteroid c&oacute; thể được ti&ecirc;m.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ipratropium (Atrovent). Ipratropium đ&ocirc;i khi được sử dụng như một thuốc gi&atilde;n phế quản để điều trị một cơn hen nghi&ecirc;m trọng, đặc biệt l&agrave; nếu albuterol kh&ocirc;ng ho&agrave;n to&agrave;n hiệu quả.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đặt ống thở, th&ocirc;ng kh&iacute; cơ học v&agrave; oxy. Nếu cơn hen đe dọa t&iacute;nh mạng, b&aacute;c sĩ c&oacute; thể đặt một ống thở v&agrave;o cổ họng v&agrave;o đường h&ocirc; hấp tr&ecirc;n. Sử dụng một m&aacute;y bơm dưỡng kh&iacute; v&agrave;o phổi sẽ gi&uacute;p thở trong khi b&aacute;c sĩ sẽ cho thuốc để c&oacute; được bệnh hen dưới sự kiểm so&aacute;t.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sau khi c&aacute;c triệu chứng hen được tốt hơn, b&aacute;c sĩ c&oacute; thể muốn cho ở lại trong khoa cấp cứu cho một v&agrave;i giờ hoặc l&acirc;u hơn để đảm bảo rằng kh&ocirc;ng c&oacute; một cuộc tấn c&ocirc;ng bệnh hen. Khi b&aacute;c sĩ cảm thấy bệnh hen l&agrave; đủ dưới sự kiểm so&aacute;t, sẽ c&oacute; thể về nh&agrave;. B&aacute;c sĩ sẽ hướng dẫn phải l&agrave;m g&igrave; nếu c&oacute; một cuộc tấn c&ocirc;ng bệnh hen. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&aacute;c triệu chứng hen kh&ocirc;ng cải thiện sau khi điều trị cấp cứu, b&aacute;c sĩ c&oacute; thể chỉ định đến bệnh viện v&agrave; cung cấp thuốc mỗi giờ hoặc mỗi v&agrave;i giờ. Nếu c&oacute; c&aacute;c triệu chứng hen nghi&ecirc;m trọng, c&oacute; thể cần phải thở oxy qua mặt nạ. Trong một số trường hợp nghi&ecirc;m trọng, cơn hen dai dẳng đ&ograve;i hỏi ở lại chăm s&oacute;c đặc biệt (ICU). </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phong c&aacute;ch sống v&agrave; biện ph&aacute;p khắc phục</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tất cả c&aacute;c cơn hen cần điều trị với một thuốc cấp cứu h&iacute;t như albuterol. Một trong những bước quan trọng trong việc ngăn ngừa một cuộc tấn c&ocirc;ng bệnh l&agrave; để tr&aacute;nh k&iacute;ch hoạt. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&aacute;c cuộc tấn c&ocirc;ng của bệnh dường như được đặt ra bởi b&ecirc;n ngo&agrave;i g&acirc;y ra, b&aacute;c sĩ c&oacute; thể gi&uacute;p t&igrave;m hiểu l&agrave;m thế n&agrave;o để giảm thiểu tiếp x&uacute;c với n&oacute;. X&eacute;t nghiệm dị ứng c&oacute; thể gi&uacute;p x&aacute;c định nguy&ecirc;n nh&acirc;n g&acirc;y ra dị ứng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Rửa tay thường xuy&ecirc;n c&oacute; thể gi&uacute;p giảm nguy cơ một loại virus lạnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu bệnh khởi ph&aacute;t l&ecirc;n khi tập thể dục trong lạnh, n&oacute; c&oacute; thể gi&uacute;p đỡ để che khu&ocirc;n mặt với một mặt nạ hoặc khăn cho đến khi ấm l&ecirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Ph&ograve;ng chống</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;ch tốt nhất để tr&aacute;nh một cuộc tấn c&ocirc;ng bệnh l&agrave; để đảm bảo bệnh được kiểm so&aacute;t trong những nơi đầu ti&ecirc;n. Điều n&agrave;y c&oacute; nghĩa sau một kế hoạch hen bằng văn bản để theo d&otilde;i c&aacute;c triệu chứng v&agrave; điều chỉnh thuốc men. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong khi kh&ocirc;ng thể loại trừ nguy cơ một cuộc tấn c&ocirc;ng bệnh, sẽ &iacute;t c&oacute; khả năng điều trị giữ bệnh dưới sự kiểm so&aacute;t. Đi kh&aacute;m b&aacute;c sĩ nếu đang theo kế hoạch điều trị, nhưng vẫn c&oacute; c&aacute;c triệu chứng thường xuy&ecirc;n hoặc kh&oacute; chịu hoặc lưu lượng thấp. Đ&acirc;y l&agrave; một dấu hiệu cho thấy bệnh kh&ocirc;ng được kiểm so&aacute;t tốt, v&agrave; cần phải l&agrave;m việc với b&aacute;c sĩ để thay đổi điều trị. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&aacute;c triệu chứng hen b&ugrave;ng l&ecirc;n khi c&oacute; một cảm lạnh hoặc c&uacute;m, c&aacute;c bước để tr&aacute;nh một cuộc tấn c&ocirc;ng bệnh bằng c&aacute;ch xem chức năng phổi v&agrave; c&aacute;c triệu chứng v&agrave; điều chỉnh điều trị c&oacute; khi cần thiết. H&atilde;y chắc chắn để giảm tiếp x&uacute;c với dị ứng g&acirc;y ra. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi thực hiện trong thời tiết lạnh, phải đeo khẩu trang. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: right;\"><b style=\"\"><i style=\"\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(128, 0, 0);\"></span></span></i></b></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">&nbsp;</span></p></span>\n', 1);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(4, 'Hen phế quản', '\n   <h1 class=\"title\">Hen phế quản</h1>   <span class=\"des\">Hen phế quản! Các triệu chứng hen từ nhẹ đến nặng và rất khác nhau, bao gồm: Khó thở, tức ngực hoặc đau, ho hoặc thở khò khè, tiếng rít khi thở ra, cơn ho hoặc khó thở trở lên tồi tệ bởi một loại virus đường hô hấp như cảm lạnh hoặc cúm...</span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi c&oacute; bệnh hen, đường h&ocirc; hấp thu hẹp v&agrave; sưng l&ecirc;n. N&oacute; sản xuất ra chất nhờn th&ecirc;m, v&agrave; hơi thở trở n&ecirc;n kh&oacute; khăn. C&aacute;c dấu hiệu hen v&agrave; c&aacute;c triệu chứng thường gặp nhất l&agrave; ho, thở kh&ograve; kh&egrave; v&agrave; kh&oacute; thở. Đối với một số người, c&aacute;c triệu chứng hen l&agrave; một mối phiền to&aacute;i nhỏ. Đối với những người kh&aacute;c, họ đang l&agrave; một vấn đề ch&iacute;nh m&agrave; g&acirc;y trở ngại cho hoạt động h&agrave;ng ng&agrave;y. Nếu c&oacute; bệnh hen trầm trọng, c&oacute; thể c&oacute; nguy cơ của một cuộc tấn c&ocirc;ng đe dọa t&iacute;nh mạng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hen kh&ocirc;ng thể chữa khỏi, nhưng c&aacute;c triệu chứng của n&oacute; c&oacute; thể được kiểm so&aacute;t. Điều trị bao gồm thực hiện c&aacute;c bước để tr&aacute;nh g&acirc;y ra cơn hen cụ thể, bằng c&aacute;ch sử dụng thuốc kiểm so&aacute;t d&agrave;i hạn v&agrave; sử dụng một ống thuốc nhanh ch&oacute;ng để ngăn ngừa cơn hen đồng thời để kiểm so&aacute;t c&aacute;c triệu chứng sau khi n&oacute; bắt đầu. Bởi v&igrave; hen thay đổi theo thời gian, l&agrave;m việc với b&aacute;c sĩ để theo d&otilde;i c&aacute;c dấu hiệu v&agrave; triệu chứng v&agrave; điều chỉnh điều trị khi cần thiết. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c triệu chứng hen từ nhẹ đến nặng v&agrave; kh&aacute;c nhau từ người n&agrave;y sang người kh&aacute;c. Từ nhẹ c&oacute; thể cảm thấy b&igrave;nh thường v&agrave; kh&ocirc;ng c&oacute; kh&oacute; thở đến c&oacute; thể c&oacute; c&aacute;c triệu chứng nhẹ v&agrave; cơn hen c&oacute; thể gặp thường xuy&ecirc;n. C&oacute; thể c&oacute; c&aacute;c triệu chứng chủ yếu v&agrave;o ban đ&ecirc;m, trong khi luyện tập hoặc khi đang tiếp x&uacute;c với dị nguy&ecirc;n cụ thể. Hoặc c&oacute; thể c&oacute; c&aacute;c triệu chứng hen tất cả c&aacute;c thời gian. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Dấu hiệu v&agrave; triệu chứng hen suyễn bao gồm: </span></b></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tức ngực hoặc đau.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; ngủ g&acirc;y ra kh&oacute; thở, ho hoặc thở kh&ograve; kh&egrave;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tiếng r&iacute;t hoặc kh&ograve; kh&egrave; khi thở ra (thở kh&ograve; kh&egrave; l&agrave; một dấu hiệu phổ biến của bệnh hen ở trẻ em).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Cơn ho hoặc thở kh&ograve; kh&egrave; đang tr�� n&ecirc;n tồi tệ bởi một loại virus đường h&ocirc; hấp như cảm lạnh hoặc c&uacute;m.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Dấu hiệu cho thấy bệnh hen c&oacute; thể trở nặng bao gồm: </span></b></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thường xuy&ecirc;n hơn v&agrave; dấu hiệu kh&oacute; chịu về triệu chứng hen.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tăng kh&oacute; thở (điều n&agrave;y c&oacute; thể được đo bằng một đo lưu lượng đỉnh cao, một thiết bị đơn giản sử dụng để kiểm tra phổi đang l&agrave;m việc tốt như thế n&agrave;o).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một nhu cầu ng&agrave;y c&agrave;ng thường xuy&ecirc;n sử dụng một ống thuốc giảm nhanh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đối với một số người, c&aacute;c triệu chứng hen b&ugrave;ng l&ecirc;n trong những t&igrave;nh huống nhất định: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh hen xảy ra trong thời gian tập thể dục. Đối với nhiều người, tập thể dục g&acirc;y ra bệnh hen tồi tệ hơn khi gặp kh&ocirc;ng kh&iacute; lạnh v&agrave; kh&ocirc;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hen nghề nghiệp l&agrave; bệnh hen g&acirc;y ra hoặc trở n&ecirc;n tồi tệ bởi v&agrave;o một nơi l&agrave;m việc với chất k&iacute;ch th&iacute;ch như kh&oacute;i ho&aacute; chất, kh&iacute; hoặc bụi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Dị ứng g&acirc;y ra bệnh hen. Một số người c&oacute; triệu chứng hen được k&iacute;ch hoạt bởi c&aacute;c chất g&acirc;y dị ứng đặc biệt, chẳng hạn như l&ocirc;ng vật nu&ocirc;i, gi&aacute;n hoặc phấn hoa.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Những trường hợp n&agrave;y c&oacute; thể n&oacute;i chuyện với b&aacute;c sĩ về bệnh hen: </span></b></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu nghĩ rằng c&oacute; bệnh hen. Nếu thường xuy&ecirc;n ho k&eacute;o d&agrave;i hơn một v&agrave;i ng&agrave;y hoặc bất kỳ dấu hiệu hoặc triệu chứng của bệnh hen h&atilde;y gặp b&aacute;c sĩ. Điều trị bệnh hen sớm, đặc biệt l&agrave; ở trẻ em, c&oacute; thể ngăn ngừa tổn thương phổi l&acirc;u d&agrave;i v&agrave; gi&uacute;p giữ cho t&igrave;nh trạng kh&ocirc;ng xấu theo thời gian.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Để theo d&otilde;i bệnh hen sau khi chẩn đo&aacute;n. Nếu biết c&oacute; bệnh hen, l&agrave;m việc với b&aacute;c sĩ để giữ cho n&oacute; được kiểm so&aacute;t. Kiểm so&aacute;t tốt hen l&acirc;u d&agrave;i kh&ocirc;ng chỉ gi&uacute;p cảm thấy tốt hơn tr&ecirc;n cơ sở h&agrave;ng ng&agrave;y, c&ograve;n c&oacute; thể ngăn chặn một cuộc tấn c&ocirc;ng đe dọa t&iacute;nh mạng từ cơn hen.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&aacute;c triệu chứng hen nặng hơn. Li&ecirc;n lạc với b&aacute;c sĩ ngay nếu thuốc dường như kh&ocirc;ng dễ d&agrave;ng cải thiện triệu chứng hoặc cần sử dụng nhanh ch&oacute;ng cứu trợ thuốc ống nhanh nhiều hơn v&agrave; thường xuy&ecirc;n hơn. Đừng cố gắng giải quyết vấn đề bằng c&aacute;ch uống thuốc m&agrave; kh&ocirc;ng tham khảo &yacute; kiến b&aacute;c sĩ. Lạm dụng thuốc hen c&oacute; thể g&acirc;y ra t&aacute;c dụng phụ v&agrave; thậm ch&iacute; c&oacute; thể l&agrave;m bệnh hen nặng hơn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Để xem lại điều trị. Hen thay đổi theo thời gian. Gặp gỡ với b&aacute;c sĩ một c&aacute;ch thường xuy&ecirc;n để thảo luận về c&aacute;c triệu chứng v&agrave; l&agrave;m bất kỳ điều chỉnh cần thiết để điều trị.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">T&igrave;m kiếm điều trị khẩn cấp khi:</span></b></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Cơn hen nặng c&oacute; thể đe dọa t&iacute;nh mạng. L&agrave;m việc với b&aacute;c sĩ trước thời hạn để x&aacute;c định phải l&agrave;m g&igrave; khi c&aacute;c dấu hiệu v&agrave; triệu chứng xấu đi, v&agrave; khi cần điều trị khẩn cấp. Nếu thuốc cứu trợ nhanh ch&oacute;ng kh&ocirc;ng giảm c&aacute;c triệu chứng của một cơn hen nghi&ecirc;m trọng, t&igrave;m kiếm sự gi&uacute;p đỡ khẩn cấp ngay lập tức. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Dấu hiệu của bệnh hen trường hợp khẩn cấp bao gồm: </span></b></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhanh ch&oacute;ng xấu đi của kh&oacute; thở hoặc thở kh&ograve; kh&egrave;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng c&oacute; cải thiện, ngay cả sau khi sử dụng một ống thuốc giảm nhanh như albuterol.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở khi đang l&agrave;m tối thiểu hoạt động thể chất.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">N&oacute; kh&ocirc;ng phải l&agrave; l&yacute; do r&otilde; r&agrave;ng tại sao một số người c&oacute; bệnh hen v&agrave; những người kh&aacute;c kh&ocirc;ng, nhưng đ&acirc;y c&oacute; thể l&agrave; do một sự kết hợp của m&ocirc;i trường v&agrave; c&aacute;c yếu tố di truyền. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hen g&acirc;y n&ecirc;n triệu chứng l&agrave; kh&aacute;c nhau từ người sang người kh&aacute;c. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tiếp x&uacute;c với một số chất g&acirc;y dị ứng kh&aacute;c nhau v&agrave; c&aacute;c chất k&iacute;ch th&iacute;ch c&oacute; thể g&acirc;y ra c&aacute;c dấu hiệu v&agrave; triệu chứng của bệnh hen, bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Phấn hoa, l&ocirc;ng động vật, nấm mốc, gi&aacute;n v&agrave; bọ ve trong bụi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhiễm tr&ugrave;ng đường h&ocirc; hấp, chẳng hạn như cảm lạnh th&ocirc;ng thường.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hoạt động thể chất (tập thể dục g&acirc;y ra bệnh hen suyễn).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng kh&iacute; lạnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c chất &ocirc; nhiễm kh&ocirc;ng kh&iacute; v&agrave; chất k&iacute;ch th&iacute;ch, chẳng hạn như h&uacute;t thuốc l&aacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một số thuốc, bao gồm thuốc chẹn beta, aspirin v&agrave; c&aacute;c thuốc chống vi&ecirc;m kh&ocirc;ng steroid kh&aacute;c.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Cảm x&uacute;c mạnh mẽ v&agrave; căng thẳng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sulfite, chất bảo quản được th&ecirc;m v&agrave;o một số loại thực phẩm v&agrave; đồ uống.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tr&agrave;o ngược dạ d&agrave;y thực bệnh (GERD), một t&igrave;nh trạng m&agrave; trong đ&oacute; c&aacute;c ax&iacute;t dạ d&agrave;y lưu v&agrave;o họng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Chu</span><span style=\"font-family: Arial;\"> kỳ kinh nguyệt ở phụ nữ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Phản ứng dị ứng với một số loại thực phẩm, chẳng hạn như đậu phộng hoặc động vật c&oacute; vỏ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hen l&agrave; phổ biến, ảnh hưởng đến h&agrave;ng triệu người lớn v&agrave; trẻ em. Một số lượng ng&agrave;y c&agrave;ng tăng của người được chẩn đo&aacute;n mỗi năm, nhưng l&yacute; do tại sao l&agrave; kh&ocirc;ng r&otilde; r&agrave;ng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một số yếu tố được cho l&agrave; tăng khả năng ph&aacute;t triển bệnh hen. Ch&uacute;ng bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Lịch sử gia đ&igrave;nh (chẳng hạn như cha mẹ hoặc anh chị em ruột) bị bệnh hen.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; một t&igrave;nh trạng dị ứng, chẳng hạn như vi&ecirc;m da dị ứng hoặc vi&ecirc;m mũi dị ứng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thừa c&acirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">L&agrave; một người h&uacute;t thuốc.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tiếp x&uacute;c với kh&oacute;i thuốc l&aacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; một b&agrave; mẹ h&uacute;t thuốc khi mang thai.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tiếp x&uacute;c với kh&iacute; thải hay c&aacute;c loại &ocirc; nhiễm.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tiếp x&uacute;c với nghề nghiệp g&acirc;y ra, chẳng hạn như ho&aacute; chất d&ugrave;ng trong n&ocirc;ng nghiệp, l&agrave;m t&oacute;c v&agrave; sản xuất.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tiếp x&uacute;c với chất g&acirc;y dị ứng, tiếp x&uacute;c với vi tr&ugrave;ng nhất định, v&agrave; c&oacute; một số loại bệnh nhiễm tr&ugrave;ng do vi khuẩn hoặc si&ecirc;u vi cũng c&oacute; thể l&agrave; yếu tố nguy cơ. Tuy nhi&ecirc;n, cần nghi&ecirc;n cứu th&ecirc;m để x&aacute;c định những g&igrave; n&oacute; c&oacute; thể đ&oacute;ng vai tr&ograve; trong việc ph&aacute;t triển bệnh hen. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hen c&oacute; thể g&acirc;y ra một số biến chứng, bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c triệu chứng g&acirc;y trở ngại cho giấc ngủ, l&agrave;m việc hoặc hoạt động giải tr&iacute;. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh ảnh hưởng tới c&ocirc;ng việc h&agrave;ng ng&agrave;y hay việc học.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thường trực thu hẹp của c&aacute;c ống phế quản.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khẩn cấp cấp cứu v&agrave; nằm viện cho cơn hen nặng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Phản ứng phụ từ việc sử dụng l&acirc;u d&agrave;i của một số thuốc được sử dụng để ổn định hen nặng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều trị th&iacute;ch hợp tạo ra một sự kh&aacute;c biệt lớn trong việc ngăn ngừa biến chứng cả ngắn hạn v&agrave; d&agrave;i hạn do hen. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Những chuẩn bị cho việc kh&aacute;m bệnh</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bởi v&igrave; c&aacute;c buổi kh&aacute;m c&oacute; thể giới hạn về thời gian, v&agrave; bởi v&igrave; thường c&oacute; rất nhiều vấn đề để tr&igrave;nh bầy. Dưới đ&acirc;y l&agrave; một số th&ocirc;ng tin để gi&uacute;p sẵn s&agrave;ng cho buổi kh&aacute;m v&agrave; những g&igrave; mong đợi từ b&aacute;c sĩ. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Những bước n&agrave;y c&oacute; thể gi&uacute;p tận dụng tối đa của cuộc hẹn: </span></b></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Viết ra bất kỳ triệu chứng gặp phải, bao gồm bất kỳ c&oacute; vẻ kh&ocirc;ng li&ecirc;n quan đến l&yacute; do dự kh&aacute;m.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Lưu &yacute; khi c&aacute;c triệu chứng phiền nhiều nhất - V&iacute; dụ, nếu c&aacute;c triệu chứng c&oacute; xu hướng nặng hơn v&agrave;o những thời điểm nhất định trong ng&agrave;y, trong m&ugrave;a nhất định, hoặc khi đang tiếp x&uacute;c với kh&ocirc;ng kh&iacute; lạnh, phấn hoa hoặc kh&aacute;c g&acirc;y n&ecirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ghi th&ocirc;ng tin c&aacute; nh&acirc;n ch&iacute;nh, bao gồm bất kỳ điểm ch&iacute;nh n&agrave;o hoặc thay đổi cuộc sống gần đ&acirc;y.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tạo một danh s&aacute;ch tất cả thuốc men, vitamin v&agrave; bổ sung đang d&ugrave;ng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&atilde;y c&ugrave;ng th&agrave;nh vi&ecirc;n gia đ&igrave;nh, nếu c&oacute; thể. Đ&ocirc;i khi n&oacute; c&oacute; thể kh&oacute; hấp thụ được tất cả c&aacute;c th&ocirc;ng tin cung cấp trong thời gian kh&aacute;m. Một người n&agrave;o đ&oacute; đi c&ugrave;ng c&oacute; thể nhớ một c&aacute;i g&igrave; đ&oacute; m&agrave; bị mất hoặc qu&ecirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thời gian với b&aacute;c sĩ l&agrave; c&oacute; hạn, n&ecirc;n chuẩn bị một danh s&aacute;ch c&aacute;c c&acirc;u hỏi sẽ gi&uacute;p tận dụng tối đa thời gian. Danh s&aacute;ch c&aacute;c c&acirc;u hỏi từ quan trọng nhất đến &iacute;t quan trọng trong trường hợp thời gian ngắn. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đối với bệnh hen, một số c&acirc;u hỏi cơ bản để y&ecirc;u cầu b&aacute;c sĩ bao gồm: </span></b></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nguy&ecirc;n nh&acirc;n g&acirc;y ra bệnh hen v&agrave; c&aacute;c vấn đề về hơi thở?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&aacute;c hơn l&agrave; nguy&ecirc;n nh&acirc;n rất c&oacute; thể, nguy&ecirc;n nh&acirc;n kh&aacute;c c&oacute; thể l&agrave; triệu chứng g&igrave;?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những loại kiểm tra cần?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c triệu chứng c&oacute; thể tạm thời hoặc m&atilde;n t&iacute;nh?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều trị tốt nhất l&agrave; g&igrave;?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c lựa chọn thay thế cho phương ph&aacute;p tiếp cận ch&iacute;nh đang đề xuất l&agrave; g&igrave;?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">T&ocirc;i c&oacute; điều kiện y tế kh&aacute;c. L&agrave;m thế n&agrave;o t&ocirc;i c&oacute; thể quản l&yacute; ch&uacute;ng tốt nhất với nhau?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; bất kỳ hạn chế n&agrave;o cần phải l&agrave;m theo?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Chi ph&iacute;, v&agrave; bảo hiểm?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; g&igrave; kh&aacute;c để thay thế thuốc đang chỉ định?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; bất kỳ t&agrave;i liệu quảng c&aacute;o hay t&agrave;i liệu in kh&aacute;c c&oacute; thể mang về nh&agrave; kh&ocirc;ng? </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ngo&agrave;i những c&acirc;u hỏi đ&atilde; chuẩn bị sẵn s&agrave;ng để y&ecirc;u cầu b&aacute;c sĩ, đừng ngần ngại đặt c&acirc;u hỏi trong buổi kh&aacute;m kh&aacute;c. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">B&aacute;c sĩ c&oacute; thể sẽ hỏi một số c&acirc;u hỏi. Sẵn s&agrave;ng để trả lời họ. B&aacute;c sĩ c&oacute; thể y&ecirc;u cầu: </span></b></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ch&iacute;nh x&aacute;c những c&aacute;c triệu chứng g&igrave; đang c&oacute;?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c triệu chứng đầu ti&ecirc;n c&oacute; khi n&agrave;o?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi n&agrave;o triệu chứng nặng?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; kh&oacute; thở hầu hết thời gian, hoặc chỉ v&agrave;o những thời điểm nhất định hoặc trong c&aacute;c t&igrave;nh huống nhất định?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; bị dị ứng, chẳng hạn như vi&ecirc;m da dị ứng hay sốt cỏ kh&ocirc;?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều g&igrave;, nếu bất cứ điều g&igrave;, xuất hiện c&aacute;c triệu chứng xấu đi?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều g&igrave;, nếu bất cứ điều g&igrave;, dường như để cải thiện c&aacute;c triệu chứng?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Do dị ứng hoặc hen c&oacute; trong lịch sử gia đ&igrave;nh?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; vấn đề về sức khỏe m&atilde;n t&iacute;nh n&agrave;o?</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Chẩn đo&aacute;n hen c&oacute; thể kh&oacute; khăn. C&aacute;c dấu hiệu v&agrave; triệu chứng c&oacute; thể từ nhẹ đến nặng v&agrave; thường tương tự như của c&aacute;c bệnh kh&aacute;c, bao gồm kh&iacute; thũng, suy tim sung huyết sớm hoặc c&aacute;c vấn đề d&acirc;y thanh &acirc;m. Trẻ em thường ph&aacute;t triển c&aacute;c điều kiện thở tạm thời c&oacute; c&aacute;c triệu chứng tương tự như bệnh hen. V&iacute; dụ, c&oacute; thể kh&oacute; khăn để n&oacute;i bệnh hen từ vi&ecirc;m phế quản, vi&ecirc;m phổi, bệnh đường h&ocirc; hấp phản ứng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Để loại trừ c&aacute;c điều kiện kh&aacute;c c&oacute; thể, b&aacute;c sĩ sẽ l&agrave;m một kiểm tra thể chất v&agrave; y&ecirc;u cầu những c&acirc;u hỏi về c&aacute;c dấu hiệu v&agrave; triệu chứng v&agrave; về vấn đề sức khỏe n&agrave;o kh&aacute;c. Cũng c&oacute; thể x&eacute;t nghiệm chức năng phổi để x&aacute;c định c&aacute;ch thức di chuyển kh&ocirc;ng kh&iacute; khi h&iacute;t thở. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">C&aacute;c x&eacute;t nghiệm để đo chức năng phổi bao gồm</span></b><span style=\"font-family: Arial;\">: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đo phế dung. Thử nghiệm n&agrave;y đo thu hẹp ống phế quản bằng c&aacute;ch kiểm tra bao nhi&ecirc;u kh&ocirc;ng kh&iacute; c&oacute; thể thở ra sau khi một h&iacute;t hơi thở s&acirc;u v&agrave; nhanh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đỉnh d&ograve;ng chảy. Đo lưu lượng đỉnh cao l&agrave; một thiết bị đơn giản rằng c&aacute;c biện ph&aacute;p l&agrave;m thế n&agrave;o c&oacute; thể kh&oacute; thở ra. Đỉnh d&ograve;ng chảy thấp hơn th&ocirc;ng thường l&agrave; một dấu hiệu phổi c&oacute; thể kh&ocirc;ng được l&agrave;m việc tốt v&agrave; bệnh hen c&oacute; thể trở nặng. B&aacute;c sĩ sẽ hướng dẫn c&aacute;ch theo d&otilde;i v&agrave; đối ph&oacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">X&eacute;t nghiệm chức năng thường được thực hiện trước v&agrave; sau khi uống thuốc d&atilde;n phế quản như albuterol để mở đường thở. Nếu chức năng phổi cải thiện từ việc sử dụng thuốc d&atilde;n phế quản, n&oacute; c&oacute; khả năng c&oacute; bệnh hen. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">C&aacute;c kiểm tra để chẩn đo&aacute;n hen bao gồm: </span></b></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Test methacholine. Nếu c&oacute; bệnh hen, bệnh hen h&iacute;t phải một k&iacute;ch hoạt được biết đến gọi l&agrave; methacholine sẽ g&acirc;y ra thắt nhẹ của đường h&ocirc; hấp. Nếu phản ứng k&iacute;ch hoạt, c&oacute; thể c&oacute; bệnh hen. Thử nghiệm n&agrave;y c&oacute; thể được sử dụng nếu ban đầu thử nghiệm chức năng phổi l&agrave; b&igrave;nh thường.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thử nghiệm nitric oxide. Thử nghiệm n&agrave;y đ&ocirc;i khi được d&ugrave;ng để chẩn đo&aacute;n v&agrave; theo d&otilde;i bệnh hen. N&oacute; đo lượng kh&iacute; gọi l&agrave; nitric oxide c&oacute; trong hơi thở. Nếu bị vi&ecirc;m đường h&ocirc; hấp - một dấu hiệu của bệnh hen c&oacute; thể c&oacute; mức oxit nitric cao hơn. Thử nghiệm n&agrave;y kh&ocirc;ng phải l&agrave; phổ biến rộng r&atilde;i .</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">L&agrave;m thế n&agrave;o bệnh hen được ph&acirc;n loại:</span></b></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Để ph&acirc;n loại mức độ bệnh hen, b&aacute;c sĩ sẽ xem x&eacute;t c&acirc;u trả lời cho c&aacute;c c&acirc;u hỏi về c&aacute;c triệu chứng (chẳng hạn như mức độ thường xuy&ecirc;n c&oacute; cơn suyễn v&agrave; l&agrave;m thế n&agrave;o giảm ch&uacute;ng), c&ugrave;ng với kết quả kh&aacute;m l&acirc;m s&agrave;ng v&agrave; x&eacute;t nghiệm chẩn đo&aacute;n. X&aacute;c định mức độ trầm trọng của bệnh hen sẽ gi&uacute;p b&aacute;c sĩ lựa chọn điều trị tốt nhất. Mức độ hen thường xuy&ecirc;n thay đổi theo thời gian, đ&ograve;i hỏi phải điều chỉnh để điều trị. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Hen được ph&acirc;n loại th&agrave;nh bốn loại ch&iacute;nh: </span></b></p>\r\n<table cellspacing=\"0\" cellpadding=\"0\" border=\"0\" class=\"MsoTableGrid\" style=\"border-collapse: collapse; width: 459px; height: 290px;\">\r\n    <tbody>\r\n        <tr style=\"height: 53.5pt;\">\r\n            <td width=\"175\" style=\"width: 131.4pt; padding: 0in 5.4pt; height: 53.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">Ph&acirc;n loại hen</span></strong></p>\r\n            </td>\r\n            <td width=\"415\" valign=\"bottom\" style=\"width: 311.4pt; padding: 0in 5.4pt; height: 53.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><strong><span style=\"font-family: Arial;\">C&aacute;c dấu hiệu v&agrave; triệu   chứng</span></strong></p>\r\n            </td>\r\n        </tr>\r\n        <tr style=\"height: 80.1pt;\">\r\n            <td width=\"175\" style=\"width: 131.4pt; padding: 0in 5.4pt; height: 80.1pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhẹ li&ecirc;n tục</span></p>\r\n            </td>\r\n            <td width=\"415\" valign=\"top\" style=\"width: 311.4pt; padding: 0in 5.4pt; height: 80.1pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Triệu chứng nhẹ đến hai   lần trong tuần v&agrave; l&ecirc;n đến hai đ&ecirc;m một th&aacute;ng</span></p>\r\n            </td>\r\n        </tr>\r\n        <tr style=\"\">\r\n            <td width=\"175\" style=\"width: 131.4pt; padding: 0in 5.4pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhẹ dai dẳng</span></p>\r\n            </td>\r\n            <td width=\"415\" valign=\"top\" style=\"width: 311.4pt; padding: 0in 5.4pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c triệu chứng nhiều   hơn hai lần một tuần, nhưng kh&ocirc;ng qu&aacute; một lần trong một ng&agrave;y</span></p>\r\n            </td>\r\n        </tr>\r\n        <tr style=\"\">\r\n            <td width=\"175\" style=\"width: 131.4pt; padding: 0in 5.4pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trung b&igrave;nh dai dẳng</span></p>\r\n            </td>\r\n            <td width=\"415\" valign=\"top\" style=\"width: 311.4pt; padding: 0in 5.4pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c triệu chứng ng&agrave;y   một lần, nhiều hơn một đ&ecirc;m một tuần</span></p>\r\n            </td>\r\n        </tr>\r\n        <tr style=\"height: 40.5pt;\">\r\n            <td width=\"175\" style=\"width: 131.4pt; padding: 0in 5.4pt; height: 40.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nặng dai dẳng</span></p>\r\n            </td>\r\n            <td width=\"415\" valign=\"top\" style=\"width: 311.4pt; padding: 0in 5.4pt; height: 40.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c triệu chứng trong   suốt cả ng&agrave;y tr&ecirc;n hầu hết c&aacute;c ng&agrave;y v&agrave; thường xuy&ecirc;n v&agrave;o ban đ&ecirc;m</span></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ph&ograve;ng, chống v&agrave; kiểm so&aacute;t l&acirc;u d&agrave;i l&agrave; ch&igrave;a kh&oacute;a để ngăn chặn cơn hen. Điều trị thường li&ecirc;n quan thực hiện để nhận ra v&agrave; tạo n&ecirc;n c&aacute;c bước để tr&aacute;nh cho họ, v&agrave; theo d&otilde;i hơi thở để chắc chắn h&agrave;ng ng&agrave;y thuốc chữa bệnh hen được lưu giữ dưới sự kiểm so&aacute;t triệu chứng. Trong trường hợp cơn hen xuất hiện, c&oacute; thể cần phải sử dụng một ống thuốc giảm nhanh như albuterol. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thuốc men</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c loại thuốc ph&ugrave; hợp phụ thuộc v&agrave;o một số sự việc, bao gồm tuổi, c&aacute;c triệu chứng của bệnh hen g&acirc;y ra v&agrave; những g&igrave; c&oacute; vẻ để l&agrave;m việc tốt nhất giữ cho bệnh hen dưới sự kiểm so&aacute;t. Ph&ograve;ng ngừa, kiểm so&aacute;t thuốc l&acirc;u d&agrave;i l&agrave;m giảm vi&ecirc;m ở đường h&ocirc; hấp m&agrave; dẫn đến c&aacute;c triệu chứng. Thuốc gi&atilde;n phế quản cứu trợ nhanh sẽ nhanh ch&oacute;ng mở đường h&ocirc; hấp sưng l&ecirc;n g&acirc;y hạn chế hơi thở. Trong một số trường hợp, thuốc để điều trị dị ứng cụ thể l&agrave; cần thiết. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thuốc kiểm so&aacute;t d&agrave;i hạn. Trong hầu hết trường hợp, c&aacute;c loại thuốc n&agrave;y cần phải được d&ugrave;ng h&agrave;ng ng&agrave;y. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">C&aacute;c loại thuốc kiểm so&aacute;t d&agrave;i hạn</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Corticosteroid h&iacute;t.</span></b><span style=\"font-family: Arial;\"> C&aacute;c loại thuốc n&agrave;y bao gồm fluticasone, budesonide, mometasone, flunisolide, beclomethasone v&agrave; những loại kh&aacute;c. Đ&oacute; l&agrave; những loại phổ biến nhất theo quy định của thuốc hen l&acirc;u d&agrave;i. C&oacute; thể cần phải sử dụng c&aacute;c loại thuốc n&agrave;y trong v&agrave;i ng&agrave;y đến v&agrave;i tuần trước khi n&oacute; đạt được lợi &iacute;ch tối đa. Kh&ocirc;ng giống như corticosteroid uống, c&aacute;c thuốc corticosteroid dạng n&agrave;y c&oacute; nguy cơ tương đối thấp c&aacute;c t&aacute;c dụng phụ v&agrave; n&oacute;i chung l&agrave; an to&agrave;n để sử dụng l&acirc;u d&agrave;i.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Bổ Leukotriene. </span></b><span style=\"font-family: Arial;\">C&aacute;c thuốc uống bao gồm montelukast, zafirlukast v&agrave; zileuton. Gi&uacute;p ngăn ngừa c&aacute;c triệu chứng hen cho đến 24 giờ. Trong trường hợp hiếm, c&aacute;c loại thuốc n&agrave;y c&oacute; li&ecirc;n quan đến phản ứng t&acirc;m l&yacute; như lo &acirc;u, x&acirc;m lược, ảo gi&aacute;c, trầm cảm v&agrave; suy nghĩ tự tử. T&igrave;m tư vấn y tế ngay lập tức cho bất kỳ phản ứng bất thường.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">LABAs.</span></b><span style=\"font-family: Arial;\"> Những thuốc h&iacute;t bao gồm salmeterol v&agrave; formoterol. LABAs mở đường h&ocirc; hấp v&agrave; giảm vi&ecirc;m. Tuy nhi&ecirc;n, n&oacute; được li&ecirc;n kết trong c&aacute;c cơn hen nặng. LABAs chỉ n&ecirc;n được kết hợp với một corticosteroid h&iacute;t.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kết hợp thuốc h&iacute;t như fluticasone v&agrave; salmeterol, budesonide v&agrave; formoterol. C&aacute;c loại thuốc n&agrave;y c&oacute; chứa một c&ugrave;ng một LABA với corticosteroid. Giống như c&aacute;c thuốc LABA kh&aacute;c, c&aacute;c loại thuốc n&agrave;y c&oacute; thể l&agrave;m tăng nguy cơ của việc c&oacute; một cơn hen nặng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Theophylline.</span></b><span style=\"font-family: Arial;\"> Đ&acirc;y l&agrave; thuốc vi&ecirc;n mỗi ng&agrave;y để gi&uacute;p giữ cho đường thở mở. Theophylline thư gi&atilde;n c&aacute;c cơ xung quanh để l&agrave;m cho đường h&ocirc; hấp thở dễ d&agrave;ng hơn. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thuốc cứu trợ nhanh</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thuốc được sử dụng khi cần thiết để nhanh ch&oacute;ng giảm triệu chứng ngắn hạn trong một cuộc tấn c&ocirc;ng bệnh hen hoặc trước khi tập thể dục nếu b&aacute;c sĩ đề nghị. C&aacute;c loại thuốc nhanh ch&oacute;ng cứu trợ bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Dẫn xuất chủ vận beta ngắn.</span></b><span style=\"font-family: Arial;\"> Những thuốc h&iacute;t gi&atilde;n phế quản cứu trợ nhanh ch&oacute;ng c&oacute; thể dễ d&agrave;ng cải thiện triệu chứng trong một cuộc bệnh hen tấn c&ocirc;ng. Ch&uacute;ng bao gồm albuterol, levalbuterol v&agrave; pirbuterol. C&aacute;c loại thuốc n&agrave;y hoạt động trong v&ograve;ng v&agrave;i ph&uacute;t, v&agrave; hiệu ứng qua nhiều giờ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ipratropium (Atrovent).</span></b><span style=\"font-family: Arial;\"> B&aacute;c sĩ c&oacute; thể k&ecirc; toa thuốc n&agrave;y, h&iacute;t cho cứu trợ ngay lập tức c&aacute;c triệu chứng. Giống như c&aacute;c thuốc gi&atilde;n phế quản kh&aacute;c, ipratropium thư gi&atilde;n đường h&ocirc; hấp, l&agrave;m cho n&oacute; dễ d&agrave;ng hơn để thở. Ipratropium chủ yếu được sử dụng cho bệnh kh&iacute; thũng v&agrave; vi&ecirc;m phế quản m&atilde;n t&iacute;nh, nhưng n&oacute; đ&ocirc;i khi được d&ugrave;ng để điều trị cơn hen.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">C&aacute;c corticosteroid ti&ecirc;m tĩnh mạch v&agrave; d&ugrave;ng đường miệng</span></b><span style=\"font-family: Arial;\">. Những thuốc l&agrave;m giảm vi&ecirc;m đường h&ocirc; hấp g&acirc;y ra bởi bệnh hen nặng. V&iacute; dụ như prednisone v&agrave; methylprednisolone. C&oacute; thể g&acirc;y ra t&aacute;c dụng phụ nghi&ecirc;m trọng khi được sử dụng l&acirc;u d&agrave;i, v&igrave; vậy sử dụng chỉ tr&ecirc;n một cơ sở ngắn hạn để điều trị triệu chứng hen nặng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều trị dị ứng g&acirc;y ra bệnh hen. Nếu bệnh hen được k&iacute;ch hoạt hoặc xấu đi do dị ứng, c&oacute; thể hưởng lợi từ điều trị dị ứng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị dị ứng bao gồm</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Miễn dịch liệu ph&aacute;p.</span></b><span style=\"font-family: Arial;\"> Ti&ecirc;m miễn dịch liệu ph&aacute;p thường được đưa ra mỗi tuần một lần cho một v&agrave;i th&aacute;ng, sau đ&oacute; mỗi th&aacute;ng một lần trong thời hạn 3 - 5 năm. Theo thời gian, dần dần giảm hệ thống miễn dịch phản ứng với dị nguy&ecirc;n cụ thể.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Omalizumab (Xolair). </span></b><span style=\"font-family: Arial;\">Thuốc n&agrave;y l&agrave; đặc biệt cho những người c&oacute; bệnh dị ứng v&agrave; hen nặng. N&oacute; hoạt động bằng c&aacute;ch thay đổi hệ thống miễn dịch. Omalizumab được ti&ecirc;m mỗi 2 - 4 tuần.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc kh&aacute;ng dị ứng.</span></b><span style=\"font-family: Arial;\"> Ch&uacute;ng bao gồm thuốc kh&aacute;ng histamine uống v&agrave; xịt mũi v&agrave; thuốc th&ocirc;ng mũi cũng như corticosteroid, thuốc xịt mũi cromolyn v&agrave; ipratropium.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đừng dựa v&agrave;o thuốc giảm nhanh.</span></b><span style=\"font-family: Arial;\"> Thuốc kiểm so&aacute;t bệnh hen d&agrave;i hạn - như corticosteroid h&iacute;t l&agrave; nền tảng của điều trị hen. Những loại thuốc hen giữ dưới sự kiểm so&aacute;t tr&ecirc;n cơ sở h&agrave;ng ng&agrave;y v&agrave; l&agrave;m cho n&oacute; &iacute;t c&oacute; khả năng sẽ c&oacute; một cơn hen tấn c&ocirc;ng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; một cơn hen, một ống thuốc cứu trợ nhanh c&oacute; thể dễ d&agrave;ng cải thiện triệu chứng ngay. Nhưng nếu thuốc kiểm so&aacute;t l&acirc;u d&agrave;i đang l&agrave;m việc đ&uacute;ng c&aacute;ch, kh&ocirc;ng n&ecirc;n cần phải sử dụng cứu trợ nhanh ch&oacute;ng h&iacute;t thường xuy&ecirc;n. Nếu cần sử dụng thuốc cứu trợ nhanh ch&oacute;ng thường xuy&ecirc;n hơn b&aacute;c sĩ khuyến c&aacute;o, h&atilde;y gặp b&aacute;c sĩ. C&oacute; lẽ cần phải điều chỉnh thuốc kiểm so&aacute;t d&agrave;i hạn. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thermoplasty phế quản.</span></b><span style=\"font-family: Arial;\"> Điều trị n&agrave;y được sử dụng cho bệnh hen trầm trọng m&agrave; kh&ocirc;ng cải thiện với corticosteroid h&iacute;t hoặc thuốc chữa bệnh hen d&agrave;i hạn kh&aacute;c. Th&ocirc;ng thường thực hiện trong ba lần ngoại tr&uacute;, thermoplasty phế quản l&agrave;m n&oacute;ng b&ecirc;n trong của đường h&ocirc; hấp trong phổi với một điện cực, l&agrave;m gi&atilde;n cơ trơn trong đường h&ocirc; hấp. Điều n&agrave;y giới hạn khả năng thắt chặt đường h&ocirc; hấp, l&agrave;m cho thở dễ d&agrave;ng hơn v&agrave; c&oacute; thể l&agrave;m giảm cơn hen. Thermoplasty phế quản kh&ocirc;ng phải l&agrave; phổ biến rộng r&atilde;i. Nghi&ecirc;n cứu th&ecirc;m l&agrave; cần thiết để x&aacute;c định xem liệu những lợi &iacute;ch của điều trị n&agrave;y c&oacute; thể c&oacute; gi&aacute; trị hơn những rủi ro v&agrave; t&aacute;c dụng phụ tiềm năng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Điều trị dựa tr&ecirc;n kiểm so&aacute;t hen c&oacute; thể gi&uacute;p quản l&yacute; bệnh hen</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều trị b���nh hen cần được linh hoạt v&agrave; dựa tr&ecirc;n những thay đổi về triệu chứng, m&agrave; n&ecirc;n được đ&aacute;nh gi&aacute; thật kỹ mỗi khi gặp b&aacute;c sĩ. Sau đ&oacute;, điều trị c&oacute; thể được điều chỉnh cho ph&ugrave; hợp. V&iacute; dụ, nếu bệnh hen được kiểm so&aacute;t, b&aacute;c sĩ c&oacute; thể k&ecirc; toa thuốc &iacute;t hơn. Nếu bệnh hen kh&ocirc;ng được kiểm so&aacute;t tốt hoặc trở nặng, b&aacute;c sĩ c&oacute; thể l&agrave;m tăng thuốc v&agrave; khuy&ecirc;n n&ecirc;n kh&aacute;m thường xuy&ecirc;n hơn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">L&agrave;m việc với b&aacute;c sĩ để tạo ra một kế hoạch h&agrave;nh động hen suyễn m&agrave; ph&aacute;c thảo bằng văn bản để uống thuốc nhất định, hoặc để tăng hoặc giảm liều thuốc dựa tr&ecirc;n c&aacute;c triệu chứng. Kế hoạch h&agrave;nh động hen cũng n&ecirc;n k&iacute;ch hoạt danh s&aacute;ch v&agrave; c&aacute;c bước cần l&agrave;m. Kế hoạch cũng c&oacute; thể li&ecirc;n quan đến theo d&otilde;i c&aacute;c triệu chứng hen hoặc sử dụng đo lưu lượng đỉnh một c&aacute;ch thường xuy&ecirc;n để theo d&otilde;i như điều trị thế n&agrave;o.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phong c&aacute;ch sống v&agrave; biện ph&aacute;p khắc phục</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mặc d&ugrave; nhiều người bị bệnh hen dựa v&agrave;o thuốc để ngăn ngừa v&agrave; giảm triệu chứng, C&oacute; thể l&agrave;m một v&agrave;i điều ri&ecirc;ng để duy tr&igrave; sức khỏe v&agrave; l&agrave;m giảm bớt khả năng xảy ra cơn hen. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tr&aacute;nh k&iacute;ch hoạt. Giảm tiếp x&uacute;c với những thứ k&iacute;ch hoạt c&aacute;c triệu chứng hen l&agrave; một phần quan trọng của kiểm so&aacute;t hen. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Dưới đ&acirc;y l&agrave; một v&agrave;i điều c&oacute; thể gi&uacute;p </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sử dụng điều h&ograve;a kh&ocirc;ng kh&iacute;. Điều h&ograve;a kh&ocirc;ng kh&iacute; l&agrave;m giảm lượng phấn hoa trong kh&ocirc;ng kh&iacute; từ c&acirc;y, cỏ v&agrave; cỏ dại m&agrave; t&igrave;m thấy trong nh&agrave;. Điều ho&agrave; nhiệt độ cũng l&agrave;m giảm độ ẩm trong nh&agrave; v&agrave; c&oacute; thể giảm tiếp x&uacute;c với mạt bụi. Nếu kh&ocirc;ng c&oacute; điều h&ograve;a kh&ocirc;ng kh&iacute;, cố gắng giữ k&iacute;n c&aacute;c cửa sổ trong m&ugrave;a phấn hoa.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">T&acirc;̉y uế nội thất. Bụi c&oacute; thể l&agrave;m trầm trọng th&ecirc;m c&aacute;c triệu chứng ban đ&ecirc;m. Giảm thiểu bằng c&aacute;ch thay thế c&aacute;c mục nhất định trong ph&ograve;ng ngủ. V&iacute; dụ, bọc gối, nệm l&ograve; xo v&agrave; hộp. Hủy bỏ thảm v&agrave; l&oacute;t s&agrave;n v&agrave; c&oacute; thể giặt r&egrave;m cửa v&agrave; lau k&iacute;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Duy tr&igrave; độ ẩm tối ưu. Nếu sống trong một kh&iacute; hậu ẩm ướt, h&atilde;y n&oacute;i chuyện với b&aacute;c sĩ về việc sử dụng một m&aacute;y chống ẩm.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Giữ sạch kh&ocirc;ng kh&iacute; trong nh&agrave;. C&oacute; c&ocirc;ng ty tiện &iacute;ch kiểm tra điều ho&agrave; v&agrave; l&ograve; sưởi mỗi năm một lần. Thay đổi bộ lọc trong l&ograve; v&agrave; điều h&ograve;a kh&ocirc;ng kh&iacute; theo hướng dẫn của nh&agrave; sản xuất. Cũng n&ecirc;n c&agrave;i đặt một bộ lọc hạt nhỏ trong hệ thống th&ocirc;ng gi&oacute;. Nếu sử dụng m&aacute;y tạo độ ẩm, thay nước h&agrave;ng ng&agrave;y.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Giảm l&ocirc;ng th&uacute;. Nếu dị ứng với l&ocirc;ng, tr&aacute;nh vật nu&ocirc;i c&oacute; l&ocirc;ng. Vật nu&ocirc;i thường xuy&ecirc;n tắm hoặc chải l&ocirc;ng<span style=\"\">&nbsp; </span>cũng c&oacute; thể l&agrave;m giảm lượng l&ocirc;ng ở xung quanh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">L&agrave;m sạch thường xuy&ecirc;n. L&agrave;m sạch nh&agrave; &iacute;t nhất một lần một tuần. Nếu c&oacute; khả năng khuấy động bụi, đeo khẩu trang hoặc c&oacute; người kh&aacute;c l&agrave;m c&aacute;c việc l&agrave;m sạch.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu bệnh hen trở n&ecirc;n tồi tệ bởi kh&ocirc;ng kh&iacute; lạnh, kh&ocirc;, khẩu trang c&oacute; thể gi&uacute;p đỡ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Lu&ocirc;n khỏe mạnh. Chăm s&oacute;c bản th&acirc;n v&agrave; điều trị c&aacute;c điều kiện kh&aacute;c li&ecirc;n quan đến bệnh hen sẽ gi&uacute;p giữ c&aacute;c triệu chứng dưới sự kiểm so&aacute;t. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Một v&agrave;i điều c&oacute; thể l&agrave;m bao gồm</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">H&atilde;y tập thể dục thường xuy&ecirc;n.</span></b><span style=\"font-family: Arial;\"> C&oacute; bệnh hen kh&ocirc;ng c&oacute; nghĩa l&agrave; phải &iacute;t hoạt động hơn. Điều trị c&oacute; thể ngăn chặn cơn hen v&agrave; c&aacute;c triệu chứng trong qu&aacute; tr&igrave;nh kiểm so&aacute;t hoạt động. Thường xuy&ecirc;n tập thể dục c&oacute; thể tăng cường sức khỏe tim v&agrave; phổi, gi&uacute;p giảm c&aacute;c triệu chứng hen. H&atilde;y nhớ rằng tập thể dục trong thời tiết lạnh c&oacute; thể g&acirc;y ra c&aacute;c triệu chứng hen. Nếu tập thể dục ở nhiệt độ lạnh, đeo khẩu trang để l&agrave;m ấm kh&ocirc;ng kh&iacute; h&iacute;t thở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Duy tr&igrave; một trọng lượng khỏe mạnh.</span></b><span style=\"font-family: Arial;\"> Thừa c&acirc;n c&oacute; thể l&agrave;m trầm trọng th&ecirc;m c&aacute;c triệu chứng hen, v&agrave; n&oacute; đặt ra nguy cơ cao của c&aacute;c vấn đề sức khỏe kh&aacute;c.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ăn tr&aacute;i c&acirc;y v&agrave; rau.</span></b><span style=\"font-family: Arial;\"> Ăn nhiều tr&aacute;i c&acirc;y v&agrave; rau c&oacute; thể l&agrave;m tăng chức năng phổi v&agrave; l&agrave;m giảm c&aacute;c triệu chứng hen. Những thực phẩm n&agrave;y rất gi&agrave;u chất dinh dưỡng bảo vệ (chất chống oxy h&oacute;a) m&agrave; tăng cường hệ miễn dịch.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Kiểm so&aacute;t ợ n&oacute;ng v&agrave; bệnh tr&agrave;o ngược dạ d&agrave;y (GERD).</span></b><span style=\"font-family: Arial;\"> C&oacute; thể c&aacute;c acid reflux l&agrave; nguy&ecirc;n nh&acirc;n g&acirc;y ợ n&oacute;ng c&oacute; thể g&acirc;y hại phổi v&agrave; đường h&ocirc; hấp trầm trọng th&ecirc;m c&aacute;c triệu chứng hen. Nếu c&oacute; ợ n&oacute;ng thường xuy&ecirc;n hoặc kh&ocirc;ng đổi, n&oacute;i chuyện với b&aacute;c sĩ về c&aacute;c lựa chọn điều trị. C&oacute; thể cần điều trị GERD trước khi c&aacute;c triệu chứng hen cải thiện.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Thuốc thay thế</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; một số bằng chứng cho thấy một số phương ph&aacute;p điều trị thay thế c&oacute; thể gi&uacute;p cho c&aacute;c triệu chứng hen suyễn. Tuy nhi&ecirc;n, h&atilde;y ghi nhớ rằng những phương ph&aacute;p điều trị kh&ocirc;ng phải l&agrave; một thay thế cho điều trị y tế - đặc biệt l&agrave; nếu c&oacute; bệnh suyễn nặng. N&oacute;i chuyện với b&aacute;c sĩ trước khi d&ugrave;ng bất cứ loại dược thảo bổ sung, như một số c&oacute; thể tương t&aacute;c với c&aacute;c thuốc đang d&ugrave;ng. Trong khi một số biện ph&aacute;p thay thế được sử dụng cho bệnh hen, trong nhiều trường hợp cần nghi&ecirc;n cứu th&ecirc;m để xem c&aacute;ch cơ chế t&aacute;c dụng tốt v&agrave; để đo lường mức độ phản ứng phụ c&oacute; thể. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p thay thế điều trị bệnh suyễn bao gồm </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thở kỹ thuật.</span></b><span style=\"font-family: Arial;\"> V&iacute; dụ bao gồm c&aacute;c kỹ thuật thở Buteyko, phương ph&aacute;p Papworth, v&agrave; hơi thở Pranayama yoga. Những b&agrave;i tập c&oacute; thể l&agrave;m giảm lượng thuốc cần để giữ cho c&aacute;c triệu chứng hen suyễn dưới sự kiểm so&aacute;t. C&aacute;c lớp học Yoga tăng thể dục v&agrave; giảm stress, c&oacute; thể gi&uacute;p đỡ với bệnh hen tốt.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ch&acirc;m cứu.</span></b><span style=\"font-family: Arial;\"> Kỹ thuật n&agrave;y bao gồm việc đặt kim rất mỏng tại c&aacute;c điểm chiến lược tr&ecirc;n cơ thể. Đ&oacute; l&agrave; an to&agrave;n v&agrave; thường kh&ocirc;ng đau.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">C&aacute;c kỹ thuật thư gi&atilde;n.</span></b><span style=\"font-family: Arial;\"> C&aacute;c kỹ thuật như thiền, phản hồi sinh học, th&ocirc;i mi&ecirc;n v&agrave; thư gi&atilde;n cơ bắp tiến bộ c&oacute; thể gi&uacute;p bệnh hen bằng c&aacute;ch giảm căng thẳng v&agrave; stress.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thảo dược.</span></b><span style=\"font-family: Arial;\"> Một v&agrave;i biện ph&aacute;p thảo dược đ&atilde; cho thấy một số hứa hẹn trong điều trị triệu chứng hen bao gồm bơ nho kh&ocirc; v&agrave; chiết xuất bạch quả. Pha trộn của c&aacute;c loại thảo dược thường được sử dụng trong truyền thống y học Trung Quốc, Ấn Độ, Nhật Bản v&agrave; Việt nam. Tuy nhi&ecirc;n, c&aacute;c nghi&ecirc;n cứu th&ecirc;m l&agrave; cần thiết để x&aacute;c định tốt như thế n&agrave;o m&agrave; c&aacute;c chế phẩm thảo dược l&agrave;m việc cho bệnh hen.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Omega - 3 fatty acid.</span></b><span style=\"font-family: Arial;\"> T&igrave;m thấy trong c&aacute;, hạt lanh v&agrave; c&aacute;c loại thực phẩm kh&aacute;c, những loại dầu n&agrave;y c&oacute; thể l&agrave;m giảm vi&ecirc;m dẫn đến triệu chứng hen suyễn. N&oacute; cũng xuất hiện để c&oacute; một số lợi &iacute;ch sức khỏe kh&aacute;c.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Giải mẫn cảm.</span></b><span style=\"font-family: Arial;\"> Giải mẫn cảm nhằm mục đ&iacute;ch k&iacute;ch th&iacute;ch phản ứng tự phục hồi của cơ thể bằng c&aacute;ch sử dụng liều lượng rất nhỏ c&aacute;c chất g&acirc;y ra triệu chứng. Trong trường hợp của bệnh hen suyễn, c&aacute;c biện ph&aacute;p giải mẫn cảm được l&agrave;m từ những chất thường g&acirc;y ra một phản ứng hen, chẳng hạn như phấn hoa hoặc cỏ dại. Vẫn c&ograve;n kh&ocirc;ng đủ bằng chứng r&otilde; r&agrave;ng để x&aacute;c định giải mẫn cảm gi&uacute;p điều trị bệnh hen do dị ứng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Đối ph&oacute; v&agrave; hỗ trợ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hen c&oacute; thể g&acirc;y kh&oacute; khăn v&agrave; căng thẳng. Đ&ocirc;i khi c&oacute; thể trở th&agrave;nh thất vọng, giận dữ hay ch&aacute;n nản bởi v&igrave; cần phải cắt giảm hoạt động b&igrave;nh thường để tr&aacute;nh m&ocirc;i trường g&acirc;y n&ecirc;n. Cũng c&oacute; thể cảm thấy bị cản trở hoặc xấu hổ với c&aacute;c triệu chứng của bệnh v&agrave; do th&oacute;i quen quản l&yacute; phức tạp. Trẻ em đặc biệt c&oacute; thể miễn cưỡng sử dụng một ống thuốc trước mặt đồng nghiệp. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tuy nhi&ecirc;n, bệnh hen kh&ocirc;ng phải l&agrave; một điều kiện hạn chế. C&aacute;ch tốt nhất để vượt qua sự lo lắng v&agrave; cảm gi&aacute;c bất lực l&agrave; hiểu t&igrave;nh trạng v&agrave; kiểm so&aacute;t điều trị.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Dưới đ&acirc;y l&agrave; một số gợi &yacute; c&oacute; thể gi&uacute;p</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&atilde;y ph&aacute; vỡ giữa c&aacute;c t&aacute;c vụ v&agrave; tr&aacute;nh c&aacute;c hoạt động m&agrave; l&agrave;m cho c&aacute;c triệu chứng nặng hơn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thực hiện một danh s&aacute;ch c&ocirc;ng việc phải l&agrave;m h&agrave;ng ng&agrave;y. Điều n&agrave;y c&oacute; thể gi&uacute;p tr&aacute;nh cảm gi&aacute;c cho&aacute;ng ngợp. Tự thưởng cho m&igrave;nh để ho&agrave;n th&agrave;nh mục ti&ecirc;u đơn giản.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">N&oacute;i chuyện với những người kh&aacute;c. Ph&ograve;ng chat v&agrave; bảng tin tr&ecirc;n Internet hoặc hỗ trợ c&aacute;c nh&oacute;m trong khu vực c&oacute; thể kết với những người phải đối mặt với những th&aacute;ch thức tương tự v&agrave; cho biết kh&ocirc;ng một m&igrave;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu trẻ bị suyễn. Được khuyến kh&iacute;ch, tập trung ch&uacute; &yacute; v&agrave;o những việc trẻ c&oacute; thể l&agrave;m, kh&ocirc;ng phải tr&ecirc;n những điều người đ&oacute; c&oacute; thể kh&ocirc;ng. Li&ecirc;n quan đến gi&aacute;o vi&ecirc;n, y t&aacute; trường học, huấn luyện vi&ecirc;n, người th&acirc;n trong việc gi&uacute;p tr��� em quản l&yacute; bệnh suyễn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Ph&ograve;ng chống</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">L&agrave;m việc c&ugrave;ng nhau, c&ugrave;ng với b&aacute;c sĩ c&oacute; thể thiết kế một kế hoạch c&aacute;c bước cho cuộc sống với t&igrave;nh trạng v&agrave; ngăn ngừa cơn hen. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thực hiện theo kế hoạch h&agrave;nh động. Với đội ngũ b&aacute;c sĩ v&agrave; chăm s&oacute;c sức khỏe, viết một kế hoạch chi tiết cho uống thuốc v&agrave; quản l&yacute; một cơn hen. Sau đ&oacute;, h&atilde;y chắc chắn l&agrave;m theo kế hoạch. Hen l&agrave; một t&igrave;nh trạng đang diễn ra v&agrave; cần theo d&otilde;i thường xuy&ecirc;n v&agrave; điều trị. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">X&aacute;c định v&agrave; tr&aacute;nh nguy&ecirc;n nh&acirc;n g&acirc;y n&ecirc;n bệnh hen. Một số chất g&acirc;y dị ứng ngo&agrave;i trời v&agrave; chất k&iacute;ch th&iacute;ch từ phấn hoa v&agrave; nấm mốc với kh&ocirc;ng kh&iacute; lạnh v&agrave; &ocirc; nhiễm kh&ocirc;ng kh&iacute;, cơn hen c&oacute; thể k&iacute;ch hoạt. T&igrave;m hiểu những g&igrave; g&acirc;y ra hoặc xấu đi bệnh suyễn, v&agrave; thực hiện c&aacute;c bước để tr&aacute;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Theo d&otilde;i hơi thở. C&oacute; thể t&igrave;m hiểu để nhận ra dấu hiệu cảnh b&aacute;o của một cuộc tấn c&ocirc;ng sắp xảy ra, chẳng hạn như hơi ho, thở kh&ograve; kh&egrave; hoặc kh&oacute; thở. Nhưng bởi v&igrave; chức năng phổi c&oacute; thể giảm trước khi nhận thấy bất kỳ dấu hiệu hoặc triệu chứng, thường xuy&ecirc;n đo lường v&agrave; ghi lại luồng kh&ocirc;ng kh&iacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhận biết v&agrave; điều trị sớm c&aacute;c cơn hen. Nếu h&agrave;nh động nhanh ch&oacute;ng, sẽ &iacute;t c&oacute; khả năng c&oacute; một cuộc tấn c&ocirc;ng nghi&ecirc;m trọng. Cũng kh&ocirc;ng cần nhiều thuốc để kiểm so&aacute;t c&aacute;c triệu chứng. Khi đo lưu lượng đỉnh giảm v&agrave; cảnh b&aacute;o về một cuộc tấn c&ocirc;ng sắp xảy ra, uống thuốc theo hướng dẫn v&agrave; ngay lập tức v&agrave; ngừng bất kỳ hoạt động c&oacute; thể đ&atilde; g&acirc;y ra cuộc tấn c&ocirc;ng. Nếu c&aacute;c triệu chứng kh&ocirc;ng cải thiện, được gi&uacute;p đỡ y tế theo chỉ dẫn trong kế hoạch h&agrave;nh động.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Uống thuốc theo quy định. Chỉ v&igrave; bệnh hen dường như được cải thiện, kh&ocirc;ng thay đổi bất cứ điều g&igrave; m&agrave; đầu ti&ecirc;n kh&ocirc;ng n&oacute;i chuyện với b&aacute;c sĩ, do đ&oacute; b&aacute;c sĩ c&oacute; thể kiểm tra lại rằng đang sử dụng thuốc đ&uacute;ng c&aacute;ch v&agrave; đ&uacute;ng liều.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ch&uacute; &yacute; đến sử dụng thuốc ống cứu trợ tăng nhanh. Nếu thấy m&igrave;nh dựa v&agrave;o ống thuốc cứu trợ nhanh ch&oacute;ng như albuterol, hen kh&ocirc;ng được kiểm so&aacute;t. Đi kh&aacute;m b&aacute;c sĩ về việc điều chỉnh điều trị.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: right;\"><b style=\"\"><i style=\"\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(128, 0, 0);\"></span></span></i></b></p>\r\n<p class=\"MsoNormal\" style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">&nbsp;</span></p></span>\n', 1);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(5, 'Hen suyễn, Khó thở khi tập thể dục', '\n   <h1 class=\"title\">Hen suyễn, Khó thở khi tập thể dục</h1>   <span class=\"des\">Hen suyễn - Khó thở khi tập thể dục! Gặp bác sỹ khi: Ho, thở khò khè, khó thở, tức ngực hoặc đau ngực, mệt mỏi khi tập thể dục - vận động.</span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu bị ho, thở kh&ograve; kh&egrave; hoặc cảm thấy hết hơi trong v&agrave; sau khi tập thể dục, n&oacute; c&oacute; thể nhiều hơn c&aacute;c triệu chứng khi gắng sức g&acirc;y ra. C&oacute; thể tập thể dục g&acirc;y ra hen suyễn. Như với bệnh hen suyễn g&acirc;y ra bởi những thứ kh&aacute;c, tập thể dục g&acirc;y ra c&aacute;c triệu chứng hen suyễn khi đường h&ocirc; hấp thắt chặt v&agrave; sản xuất chất nhờn th&ecirc;m. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu tập thể dục g&acirc;y ra bệnh hen suyễn, c&ograve;n được gọi l&agrave; tập thể dục g&acirc;y ra co thắt phế quản, gắng sức vật l&yacute; c&oacute; thể l&agrave; điều duy nhất g&acirc;y n&ecirc;n c&aacute;c triệu chứng, hoặc tập thể dục c&oacute; thể chỉ l&agrave; một trong một v&agrave;i điều m&agrave; g&acirc;y bệnh hen suyễn. Nhưng c&oacute; tập thể dục g&acirc;y ra bệnh hen suyễn kh&ocirc;ng c&oacute; nghĩa l&agrave; kh&ocirc;ng n&ecirc;n tập thể dục. Th&iacute;ch hợp điều trị v&agrave; ph&ograve;ng ngừa c&oacute; thể giữ cho hoạt động.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tập thể dục g&acirc;y ra c&aacute;c triệu chứng hen suyễn c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thở kh&ograve; kh&egrave;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tức ngực hoặc đau.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mệt mỏi trong khi tập luyện.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hoạt động thể thao ngh&egrave;o n&agrave;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tập thể dục g&acirc;y ra c&aacute;c triệu chứng hen suyễn c&oacute; thể bắt đầu v&agrave;i ph&uacute;t sau khi<span style=\"\">&nbsp; </span>bắt đầu tập thể dục. Một số người c&oacute; c&aacute;c triệu chứng 10 đến 15 ph&uacute;t sau khi ho&agrave;n th&agrave;nh tập luyện. C&oacute; thể c&oacute; c&aacute;c triệu chứng trong v&agrave; sau khi tập thể dục. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Cảm thấy một ch&uacute;t hơi thở ngắn hoặc mệt mỏi khi l&agrave;m việc l&agrave; b&igrave;nh thường, đặc biệt l&agrave; nếu đang kh&ocirc;ng ở trong h&igrave;nh dạng tuyệt vời. Nhưng với hen do gắng sức, c&aacute;c triệu chứng n&agrave;y c&oacute; thể nặng hơn. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đối với nhiều người, tập thể dục chỉ l&agrave; một trong một v&agrave;i g&acirc;y n&ecirc;n bệnh suyễn. Những người kh&aacute;c c&oacute; thể bao gồm phấn hoa, l&ocirc;ng vật nu&ocirc;i v&agrave; chất g&acirc;y dị ứng trong kh&ocirc;ng kh&iacute; kh&aacute;c. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Đến gặp b&aacute;c sĩ khi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đi kh&aacute;m b&aacute;c sĩ nếu bị ho, thở kh&ograve; kh&egrave; hay bị đau ngực hoặc đau thắt trong hoặc sau khi tập thể dục. Nhiều người kh&ocirc;ng nhận ra họ c&oacute; tập thể dục g&acirc;y ra hen suyễn bởi v&igrave; họ nghĩ rằng đ&acirc;y l&agrave; những phản hồi của cơ thể b&igrave;nh thường để l&agrave;m việc. Đừng cho rằng c&aacute;c triệu chứng l&agrave; do ngắn hạn về độ bền. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">T&igrave;m kiếm sự điều trị y tế ngay lập tức nếu c&oacute; c&aacute;c triệu chứng xấu đi. Cơn suyễn nặng c&oacute; thể đe dọa t&iacute;nh mạng. Dấu hiệu của một cuộc tấn c&ocirc;ng bệnh suyễn cần điều trị khẩn cấp bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở hoặc thở kh&ograve; kh&egrave;, nhanh ch&oacute;ng trở n&ecirc;n tệ hơn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng cải thiện, ngay cả sau khi d&ugrave;ng một thuốc gi&atilde;n phế quản t&aacute;c dụng ngắn (chẳng hạn như một ống h&iacute;t albuterol).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở vẫn tiếp tục ngay cả sau khi đ&atilde; ngừng tập luyện. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng r&otilde; ch&iacute;nh x&aacute;c những g&igrave; g&acirc;y ra tập thể dục g&acirc;y ra bệnh hen suyễn, v&agrave; tại sao một số người nhận được n&oacute; v&agrave; những người kh&aacute;c th&igrave; kh&ocirc;ng. Trong c&aacute; nh&acirc;n nhạy cảm, c&aacute;c triệu chứng c&oacute; thể được k&iacute;ch hoạt bằng c&aacute;ch l&agrave;m kh&ocirc; hoặc l&agrave;m lạnh của đường h&ocirc; hấp trong hơi thở mạnh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c yếu tố c&oacute; thể k&iacute;ch hoạt hoặc xấu đi tập thể dục g&acirc;y ra bệnh hen suyễn bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng kh&iacute; lạnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng kh&iacute; kh&ocirc;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">&Ocirc; nhiễm kh&ocirc;ng kh&iacute; như h&uacute;t thuốc hoặc kh&oacute;i.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Phấn hoa cao t&iacute;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; một bệnh nhiễm tr&ugrave;ng đường h&ocirc; hấp như cảm lạnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&oacute;a chất như chlorine, sơn, ph&acirc;n b&oacute;n hay thuốc diệt cỏ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng tập thể dục đặc biệt m&agrave; phải tr&aacute;nh khi tập thể dục g&acirc;y ra bệnh hen suyễn, c&aacute;c hoạt động đ&oacute; l&agrave;m cho kh&oacute; thở c&oacute; nhiều khả năng g&acirc;y ra triệu chứng. V&iacute; dụ, b&agrave;i tập aerobic, chẳng hạn như chạy hoặc chơi b&oacute;ng rổ, kh&uacute;c c&ocirc;n cầu hay b&oacute;ng đ&aacute;, c&oacute; nhiều khả năng g&acirc;y ra c&aacute;c triệu chứng hơn l&agrave; cử tạ, chơi golf hay đi bộ nhịp độ vừa phải. Tương tự như vậy, tập thể dục trong thời tiết lạnh cũng c&oacute; thể l&agrave;m tăng c&aacute;c triệu chứng hen suyễn bởi v&igrave; đang thở trong rất nhiều kh&ocirc;ng kh&iacute; lạnh v&agrave; kh&ocirc;. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhưng đừng để điều đ&oacute; ngăn cản. Với điều trị th&iacute;ch hợp, c&oacute; thể l&agrave;m c&aacute;c hoạt động aerobic cường độ cao v&agrave; tập luyện thời tiết lạnh m&agrave; kh&ocirc;ng c&oacute; triệu chứng hen suyễn. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tập thể dục g&acirc;y ra bệnh hen suyễn c&oacute; thể xảy ra ở những người của bất kỳ độ tuổi v&agrave; c&aacute;c hoạt động, nhưng một số người c&oacute; nhiều khả năng được chẩn đo&aacute;n với điều kiện hơn những người kh&aacute;c. Những yếu tố l&agrave;m tăng nguy cơ bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đ&atilde; c&oacute; bệnh suyễn g&acirc;y ra bởi những thứ kh&aacute;c.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; sốt hay hoặc dị ứng kh&aacute;c.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; lịch sử gia đ&igrave;nh, chẳng hạn như cha mẹ hoặc anh chị em với bệnh hen suyễn. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sống trong một khu vực đ&ocirc; thị lớn, đặc biệt l&agrave; nội th&agrave;nh, c&oacute; thể tăng tiếp x&uacute;c với kh&ocirc;ng kh&iacute; &ocirc; nhiễm. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&uacute;t thuốc hoặc tiếp x&uacute;c với kh&oacute;i thuốc. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tiếp x&uacute;c với nghề nghiệp g&acirc;y ra, chẳng hạn như ho&aacute; chất sử dụng trong n&ocirc;ng nghiệp v&agrave; l&agrave;m t&oacute;c, trong sơn, th&eacute;p, nh���a v&agrave; sản xuất điện tử. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đang rất thừa c&acirc;n (b&eacute;o ph&igrave;).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tham gia c&aacute;c m&ocirc;n thể thao m&ugrave;a đ&ocirc;ng như trượt băng h&igrave;nh, kh&uacute;c c&ocirc;n cầu tr&ecirc;n băng, trượt tuyết.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">L&agrave; một đứa trẻ, trẻ em n&oacute;i chung l&agrave; t&iacute;ch cực hơn so với người lớn,</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hen suyễn bao gồm cả tập thể dục g&acirc;y ra hen suyễn, c&oacute; thể g&acirc;y ra một số biến chứng. Điều trị đ&uacute;ng c&aacute;ch c&oacute; thể gi&uacute;p tr&aacute;nh ch&uacute;ng. Biến chứng hen suyễn c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hoạt động thể thao ngh&egrave;o n&agrave;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thường thu hẹp đường h&ocirc; hấp (ống phế quản), g&acirc;y kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khẩn cấp v&agrave; nhập viện do tấn c&ocirc;ng bệnh suyễn trầm trọng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">B&aacute;c sĩ c&oacute; lẽ sẽ bắt đầu bằng c&aacute;ch thực hiện kh&aacute;m bao gồm tai, mũi, họng v&agrave; ngực v&agrave; c&oacute; thể y&ecirc;u cầu một số c&acirc;u hỏi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; thể cần một hoặc nhiều x&eacute;t nghiệm để xem những g&igrave; đang xảy ra, v&agrave; để đảm bảo rằng c&aacute;c triệu chứng kh&ocirc;ng được g&acirc;y ra bởi một c&aacute;i g&igrave; đ&oacute; kh&aacute;c với bệnh hen suyễn tập thể dục g&acirc;y ra. Nếu c&oacute; bệnh suyễn, b&aacute;c sĩ c&oacute; thể muốn l&agrave;m c&aacute;c x&eacute;t nghiệm để x&aacute;c định phổi đang l&agrave;m việc như thế n&agrave;o v&agrave; nếu c&oacute; điều g&igrave; kh&aacute;c hơn l&agrave; tập thể dục cũng g&acirc;y ra c&aacute;c triệu chứng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tuy nhi&ecirc;n, thử nghiệm rộng r&atilde;i kh&ocirc;ng phải lu&ocirc;n lu&ocirc;n cần thiết để chẩn đo&aacute;n hen suyễn tập thể dục g&acirc;y ra. B&aacute;c sĩ c&oacute; thể cho một ống thuốc để thử trước khi tập thể dục. Nếu n&oacute; hoạt động, c&oacute; thể c&oacute; bệnh suyễn. Đối với nhiều người bị bệnh hen suyễn tập thể dục g&acirc;y ra, d&ugrave;ng một hoặc hai nh&aacute;t albuterol hoặc thuốc h&iacute;t kh&aacute;c trước khi tập thể dục l&agrave; đủ để l&agrave;m giảm triệu chứng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Loại trừ c&aacute;c điều kiện kh&aacute;c </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu n&oacute; kh&ocirc;ng r&otilde; r&agrave;ng g&acirc;y ra c&aacute;c triệu chứng của bệnh hen suyễn, b&aacute;c sĩ c&oacute; thể l&agrave;m c&aacute;c x&eacute;t nghiệm để xem liệu c&aacute;c triệu chứng c&oacute; thể l&agrave; do c&aacute;i g&igrave; kh&aacute;c. C&aacute;c vấn đề sức khỏe m&agrave; c&oacute; thể bắt chước tập thể dục g&acirc;y ra bệnh hen suyễn bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh tim hoặc suy tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Phổi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Phản ứng phụ của thuốc nhất định.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tắc nghẽn đường h&ocirc; hấp.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">D&acirc;y thanh &acirc;m rối loạn chức năng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hay sốt hoặc dị ứng kh&aacute;c.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">X&eacute;t nghiệm chức năng phổi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Để xem phổi đang l&agrave;m việc như thế n&agrave;o, v&agrave; để xem liệu c&oacute; dấu hiệu của bệnh hen suyễn, c&oacute; thể cần x&eacute;t nghiệm chức năng phổi c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đo phế dung.</span></b><span style=\"font-family: Arial;\"> Kiểm tra n&agrave;y, c&aacute;c biện ph&aacute;p thở như thế n&agrave;o, l&agrave; thử nghiệm ưa th&iacute;ch để chẩn đo&aacute;n hen suyễn. Trong 10 &ndash; 15 ph&uacute;t để kiểm tra, h&iacute;t thở s&acirc;u v&agrave; thở ra một c&aacute;ch mạnh mẽ v&agrave;o một ống nối với m&aacute;y t&iacute;nh được gọi l&agrave; một phế dung kế. Nếu một số ph&eacute;p đo ch&iacute;nh l&agrave; dưới mức b&igrave;nh thường một người theo tuổi t&aacute;c v&agrave; giới t&iacute;nh, đường th&ocirc;ng kh&iacute; c&oacute; thể bị vi&ecirc;m (cản trở). Đ&acirc;y l&agrave; một dấu hiệu ch&iacute;nh của bệnh hen suyễn. B&aacute;c sĩ c&oacute; thể y&ecirc;u cầu<span style=\"\">&nbsp; </span>h&iacute;t một loại thuốc gi&atilde;n phế quản được sử dụng trong điều trị bệnh hen suyễn để mở đường h&ocirc; hấp. Sau đ&oacute;, l&agrave;m lại b&agrave;i kiểm tra đo phế dung. Nếu hơi thở cải thiện đ&aacute;ng kể, n&oacute; c&oacute; khả năng c&oacute; bệnh suyễn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tập thể dục th&aacute;ch thức.</span></b><span style=\"font-family: Arial;\"> Thử nghiệm n&agrave;y được sử dụng để xem như ảnh hưởng đến nỗ lực chức năng phổi thế n&agrave;o. Với thử nghiệm n&agrave;y, b&aacute;c sĩ c&oacute; thể l&agrave;m một thử nghiệm chức năng phổi trước v&agrave; sau khi tập thể dục. Tập thể dục thường được thực hiện s&aacute;u đến t&aacute;m ph&uacute;t tr&ecirc;n một m&aacute;y chạy bộ hay tập luyện c&aacute;c m&aacute;y văn ph&ograve;ng. Đối với c&aacute;c vận động vi&ecirc;n, thực hiện c&aacute;c b&agrave;i kiểm tra thử th&aacute;ch đ&ocirc;i khi được thực hiện trong m&ocirc;i trường thể thao.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đo lưu lượng đỉnh cao.</span></b><span style=\"font-family: Arial;\"> Để x&aacute;c định xem c&oacute; bệnh suyễn, v&agrave; phổi l&agrave;m việc như thế n&agrave;o, b&aacute;c sĩ c&oacute; thể y&ecirc;u cầu thực hiện đo lưu lượng đỉnh cao. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Nitric oxide thử nghiệm.</span></b><span style=\"font-family: Arial;\"> Thử nghiệm n&agrave;y được sử dụng để xem c&oacute; bao nhi&ecirc;u kh&iacute; oxit nitric trong hơi th . Cao của oxit nitric l&agrave; một dấu hiệu của bệnh hen suyễn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Methacholine thử nghiệm.</span></b><span style=\"font-family: Arial;\"> Trong thử nghiệm methacholine thử th&aacute;ch, h&iacute;t một lượng nhỏ sương methacholine để xem nếu n&oacute; g&acirc;y ra c&aacute;c triệu chứng hen suyễn. Chức năng phổi được kiểm tra trước v&agrave; sau khi methacholine, xem n&oacute; ảnh hưởng đến khả năng thở bao nhi&ecirc;u. Th&aacute;ch thức kiểm tra cũng c&oacute; thể được thực hiện bằng c&aacute;ch sử dụng kh&iacute; lạnh hoặc histamine. C&aacute;c x&eacute;t nghiệm n&agrave;y chỉ được thực hiện tại c&aacute;c ph&ograve;ng kh&aacute;m chuy&ecirc;n khoa.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">X&eacute;t nghiệm dị ứng da.</span></b><span style=\"font-family: Arial;\"> Trong một thử nghiệm tr&ecirc;n da, da ch&iacute;ch dị ứng với chất chiết xuất tinh khiết v&agrave; quan s&aacute;t những dấu hiệu của một phản ứng dị ứng. C&aacute;c x&eacute;t nghiệm n&agrave;y c&oacute; thể hữu &iacute;ch để xem liệu c&oacute; một phản ứng đối với những thứ kh&aacute;c hơn l&agrave; tập thể dục, chẳng hạn như l&ocirc;ng động vật, nấm mốc, mạt bụi hoặc cao su. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đối với nhiều người, một v&agrave;i nh&aacute;t từ một ống thuốc phải cứu trợ nhanh trước khi tập thể dục l&agrave; đủ để kiểm so&aacute;t c&aacute;c triệu chứng hen suyễn. C&aacute;c thuốc gi&atilde;n phế quản - albuterol, nhanh ch&oacute;ng mở đường h&ocirc; hấp v&agrave; c&oacute; thể gi&uacute;p kiểm so&aacute;t c&aacute;c triệu chứng trong nhiều giờ. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tuy nhi&ecirc;n, một số người cũng cần phải uống thuốc bổ sung để kiểm so&aacute;t c&aacute;c triệu chứng hen suyễn. C&oacute; thể cần kiểm so&aacute;t thuốc h&agrave;ng ng&agrave;y l&acirc;u d&agrave;i nếu c&oacute; c&aacute;c triệu chứng hen suyễn thường xuy&ecirc;n khi kh&ocirc;ng tập thể dục, hoặc nếu sử dụng một loại thuốc trước khi tập thể dục kh&ocirc;ng giữ c&aacute;c triệu chứng dưới sự kiểm so&aacute;t. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Kiểm so&aacute;t thuốc d&agrave;i hạn </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong hầu hết trường hợp, c&aacute;c thuốc n&agrave;y cần phải được thực hiện mỗi ng&agrave;y. C&aacute;c loại thuốc kiểm so&aacute;t d&agrave;i hạn bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">H&iacute;t corticosteroid</span></b><span style=\"font-family: Arial;\">. Những thuốc n&agrave;y bao gồm fluticasone (Flovent Diskus, Flovent HFA), budesonide (Pulmicort Flexhaler), mometasone (Asmanex), triamcinolone (Azmacort), flunisolide (Aerobid), beclomethasone (Qvar) v&agrave; những loại kh&aacute;c. N&oacute; l&agrave; những loại phổ biến nhất theo quy định của thuốc suyễn l&acirc;u d&agrave;i. C&oacute; thể cần phải sử dụng c&aacute;c loại thuốc n&agrave;y trong v&agrave;i ng&agrave;y đến v&agrave;i tuần trước khi họ đạt được lợi &iacute;ch tối đa của họ. Kh&ocirc;ng giống như corticosteroid uống, c&aacute;c thuốc corticosteroid c&oacute; nguy cơ tương đối thấp của c&aacute;c t&aacute;c dụng phụ v&agrave; n&oacute;i chung l&agrave; an to&agrave;n cho sử dụng l&acirc;u d&agrave;i.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Leukotriene sửa đổi.</span></b><span style=\"font-family: Arial;\"> C&aacute;c thuốc uống bao gồm montelukast (Singulair), zafirlukast (Accolate) v&agrave; zileuton. Ch&uacute;ng gi&uacute;p ngăn ngừa c&aacute;c triệu chứng hen suyễn đến 24 giờ. Trong trường hợp hiếm hoi, c&aacute;c thuốc n&agrave;y c&oacute; li&ecirc;n quan đến phản ứng t&acirc;m l&yacute;, chẳng hạn như k&iacute;ch động, g&acirc;y hấn, ảo gi&aacute;c, trầm cảm v&agrave; suy nghĩ tự tử. T&igrave;m kiếm sự tư vấn y tế ngay lập tức cho bất kỳ phản ứng bất thường.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Theophylline.</span></b><span style=\"font-family: Arial;\"> Đ&acirc;y l&agrave; một vi&ecirc;n thuốc h&agrave;ng ng&agrave;y để gi&uacute;p giữ cho đường thở mở (thuốc gi&atilde;n phế quản). Theophylline thư gi&atilde;n c&aacute;c cơ xung quanh đường dẫn kh&iacute; l&agrave;m cho việc thở dễ d&agrave;ng hơn. N&oacute; kh&ocirc;ng được sử dụng thường ng&agrave;y b&acirc;y giờ như trong những năm qua.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Beta agonist (LABAs).</span></b><span style=\"font-family: Arial;\"> Những loại thuốc h&iacute;t bao gồm salmeterol (Serevent Diskus) v&agrave; formoterol (Foradil Aerolizer). D&ugrave;ng 30 ph&uacute;t trước khi tập thể dục, t&aacute;c dụng k&eacute;o d&agrave;i thuốc chủ vận beta c&oacute; thể ngăn ngừa c&aacute;c triệu chứng của bệnh hen suyễn do gắng sức, cho đến 12 giờ. Tuy nhi&ecirc;n, N&oacute; đ&atilde; li&ecirc;n kết với c&aacute;c cơn suyễn nặng. LABAs lu&ocirc;n cần được thực hiện chỉ khi kết hợp với một corticosteroid h&iacute;t.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kết hợp h&iacute;t chẳng hạn như fluticasone v&agrave; salmeterol (Advair Diskus) v&agrave; budesonide v&agrave; formoterol (Symbicort). Những thuốc n&agrave;y c&oacute; chứa một LABA c&ugrave;ng với một corticosteroid. Giống như những thuốc LABA, c&aacute;c thuốc n&agrave;y c&oacute; thể l&agrave;m tăng nguy cơ của một cuộc tấn c&ocirc;ng bệnh suyễn trầm trọng v&agrave; cần phải được sử dụng cẩn thận.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thuốc giảm nhanh </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&ograve;n được gọi l&agrave; thuốc cứu hộ, thuốc cứu trợ nhanh được sử dụng khi cần thiết để nhanh ch&oacute;ng giảm triệu chứng ngắn hạn trong một cuộc tấn c&ocirc;ng bệnh suyễn - hoặc trước khi tập thể dục nếu b&aacute;c sĩ đề nghị. C&aacute;c loại thuốc giảm nhanh bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Dẫn xuất ngắn beta agonist.</span></b><span style=\"font-family: Arial;\"> Những thuốc n&agrave;y bao gồm c&aacute;c thuốc gi&atilde;n phế quản h&iacute;t albuterol (ProAir HFA, Ventolin HFA&hellip;), levalbuterol (Xopenex HFA) v&agrave; pirbuterol (Maxair Autohaler). Đối với hầu hết mọi người, một đến hai nh&aacute;t của albuterol hay một h&agrave;nh động ngắn h&iacute;t chủ vận beta 10 đến 15 ph&uacute;t trước khi tập thể dục l&agrave; đủ để ngăn ngừa c&aacute;c triệu chứng đến bốn giờ. Những loại thuốc gi&atilde;n phế quản h&iacute;t v&agrave;o c&oacute; thể nhanh ch&oacute;ng dễ d&agrave;ng giảm triệu chứng trong một vụ tấn c&ocirc;ng bệnh suyễn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ipratropium (Atrovent).</span></b><span style=\"font-family: Arial;\"> B&aacute;c sĩ c&oacute; thể k&ecirc; toa thuốc n&agrave;y h&iacute;t để cứu trợ ngay lập tức c&aacute;c triệu chứng. Giống như c&aacute;c thuốc gi&atilde;n phế quản kh&aacute;c, ipratropium thư gi&atilde;n đường h&ocirc; hấp, l&agrave;m cho n&oacute; dễ d&agrave;ng hơn để thở. Ipratropium l&agrave; chủ yếu d&ugrave;ng cho vi&ecirc;m phế quản m&atilde;n t&iacute;nh v&agrave; bệnh kh&iacute; thũng, nhưng n&oacute; đ&ocirc;i khi được d&ugrave;ng để điều trị cơn suyễn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Uống v&agrave; c&aacute;c corticosteroid ti&ecirc;m tĩnh mạch.</span></b><span style=\"font-family: Arial;\"> Những thuốc n&agrave;y l&agrave;m giảm vi&ecirc;m đường h&ocirc; hấp g&acirc;y ra bởi bệnh hen suyễn nặng. V&iacute; dụ như prednisone v&agrave; methylprednisolone. N&oacute; c&oacute; thể g&acirc;y ra t&aacute;c dụng phụ nghi&ecirc;m trọng khi được sử dụng l&acirc;u d&agrave;i, do đ&oacute; ch&uacute;ng chỉ được sử dụng để điều trị triệu chứng hen suyễn nghi&ecirc;m trọng tr&ecirc;n cơ sở ngắn hạn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Điều trị dị ứng g&acirc;y ra bệnh hen suyễn </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu bệnh suyễn được k&iacute;ch hoạt hoặc trở n&ecirc;n tồi tệ do dị ứng, c&oacute; thể hưởng lợi từ điều trị dị ứng. Dị ứng điều trị bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Omalizumab (Xolair).</span></b><span style=\"font-family: Arial;\"> Thuốc n&agrave;y đặc biệt cho những người c&oacute; bệnh dị ứng v&agrave; hen suyễn. N&oacute; l&agrave;m giảm phản ứng của hệ miễn dịch g&acirc;y dị ứng, chẳng hạn như phấn hoa, mạt bụi hoặc l&ocirc;ng vật nu&ocirc;i. Xolair được ph&acirc;n phối bằng c&aacute;ch ti&ecirc;m mỗi 2 - 4 tuần.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc dị ứng.</span></b><span style=\"font-family: Arial;\"> Ch&uacute;ng bao gồm thuốc kh&aacute;ng histamin đường uống v&agrave; xịt mũi v&agrave; thuốc th&ocirc;ng mũi cũng như cromolyn, corticosteroid v&agrave; thuốc xịt mũi ipratropium.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Miễn dịch liệu ph&aacute;p.</span></b><span style=\"font-family: Arial;\"> Miễn dịch liệu ph&aacute;p thường được ti&ecirc;m cho một tuần một lần cho một v&agrave;i th&aacute;ng, sau đ&oacute; mỗi th&aacute;ng một lần trong thời gian 3 - 5 năm. Theo thời gian, dần dần giảm hệ thống miễn dịch phản ứng với dị nguy&ecirc;n cụ thể.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Kh&ocirc;ng chỉ dựa v&agrave;o thuốc giảm nhanh </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu đ&atilde; được quy định thuốc kiểm so&aacute;t hen suyễn l&acirc;u d&agrave;i - như corticosteroid dạng h&iacute;t,<span style=\"\">&nbsp; </span>kh&ocirc;ng n&ecirc;n cần phải sử dụng nhanh cứu trợ h&iacute;t thường xuy&ecirc;n hơn b&aacute;c sĩ đề nghị. Ghi lại bao nhi&ecirc;u nh&aacute;t sử dụng mỗi tuần. Nếu thường xuy&ecirc;n cần phải sử dụng nhanh cứu trợ<span style=\"\">&nbsp; </span>h&iacute;t, gặp b&aacute;c sĩ. C&oacute; lẽ cần phải điều chỉnh thuốc kiểm so&aacute;t d&agrave;i hạn. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Ph&ograve;ng chống</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o để tr&aacute;nh tập thể dục g&acirc;y ra hen suyễn. Nhưng c&oacute; thể thực hiện c&aacute;c bước để giữ cho c&aacute;c triệu chứng: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">L&agrave;m ấm l&ecirc;n 15 ph&uacute;t trước khi tập luyện vất vả. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">L&agrave;m tốt nhất để tr&aacute;nh cảm lạnh v&agrave; c&aacute;c bệnh nhiễm tr&ugrave;ng đường h&ocirc; hấp. Kh&ocirc;ng l&agrave;m b&agrave;i tập vất vả khi c&oacute; lạnh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Chọn một m&ocirc;i trường ẩm ướt cho tập luyện, chẳng hạn như một đường m&ograve;n b&ecirc;n cạnh hồ nước hay ph&ograve;ng tập thể dục với một hồ bơi trong nh&agrave;. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tr&aacute;nh c&aacute;c chất g&acirc;y dị ứng khi tập thể dục nếu n&oacute; l&agrave;m trầm trọng th&ecirc;m c&aacute;c triệu chứng. V&iacute; dụ như &ocirc; nhiễm kh&ocirc;ng kh&iacute;, phấn hoa hoặc l&ocirc;ng vật nu&ocirc;i.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&atilde;y học c&aacute;ch h&iacute;t thở bằng mũi để sưởi ấm kh&ocirc;ng kh&iacute; trước khi n&oacute; đi v&agrave;o phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Giữ cho miệng v&agrave; mũi phủ trong khi tập luyện trong thời tiết lạnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng n&ecirc;n tr&aacute;nh tập thể dục bởi v&igrave; c&oacute; bệnh suyễn. Ở trong t&igrave;nh trạng tốt c&oacute; thể giảm triệu chứng hen suyễn trong thời gian d&agrave;i.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: right;\"><b style=\"\"><i style=\"\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(128, 0, 0);\"></span></span></i></b></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">&nbsp;</span></p></span>\n', 1);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(6, 'Ho mãn tính', '\n   <h1 class=\"title\">Ho mãn tính</h1>   <span class=\"des\">Ho mãn tính! Được gọi khi tám tuần hoặc lâu hơn, co thể gây ra: Sổ mũi hay nghẹt mũi, thở khò khè, ợ nóng hoặc chua trong miệng, ho máu trong trường hợp hiếm hoi.</span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho m&atilde;n t&iacute;nh - được gọi khi k&eacute;o d&agrave;i t&aacute;m tuần hoặc l&acirc;u hơn. Ngo&agrave;i việc thể chất rối loạn, ho kinh ni&ecirc;n c&oacute; thể xa l&aacute;nh gia đ&igrave;nh v&agrave; đồng nghiệp, l&agrave;m hỏng giấc ngủ v&agrave; để lại cảm gi&aacute;c tức giận v&agrave; thất vọng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đ&ocirc;i khi c&oacute; thể kh&oacute; x&aacute;c định được vấn đề g&acirc;y ra ho m&atilde;n t&iacute;nh, những nguy&ecirc;n nh&acirc;n phổ biến nhất của ho m&atilde;n t&iacute;nh l&agrave; chảy nước mũi sau, hen suyễn v&agrave; acid tr&agrave;o ngược - một triệu chứng thường xuy&ecirc;n của bệnh tr&agrave;o ngược dạ d&agrave;y (GERD). Ho m&atilde;n t&iacute;nh thường biến mất khi c&aacute;c vấn đề cơ bản được xử l&yacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho mạn t&iacute;nh c&oacute; thể xảy ra với c&aacute;c dấu hiệu v&agrave; triệu chứng kh&aacute;c, c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sổ mũi hay nghẹt mũi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một cảm gi&aacute;c của chất lỏng chạy xuống ph&iacute;a sau cổ họng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thở kh&ograve; kh&egrave; v&agrave; kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ợ n&oacute;ng hoặc một hương vị chua trong miệng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong trường hợp hiếm hoi c&oacute; ho ra m&aacute;u. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho m&atilde;n t&iacute;nh thường được định nghĩa l&agrave; ho k&eacute;o d&agrave;i t&aacute;m tuần hoặc nhiều hơn, nhưng bất cứ ho dai dẳng n&agrave;o cũng c&oacute; thể ph&aacute; vỡ cuộc sống. Đi kh&aacute;m b&aacute;c sĩ nếu ho k&eacute;o d&agrave;i, đặc biệt l&agrave; ho c&oacute; đờm đờm hoặc m&aacute;u, l&agrave;m nhiễu loạn giấc ngủ, hoặc ảnh hưởng đến c&ocirc;ng việc hay c&aacute;c mối quan hệ. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho bắt đầu khi acid dạ d&agrave;y g&acirc;y k&iacute;ch ứng, chất nhầy, xịt t&oacute;c, nước hoa, thậm ch&iacute; nhiều gia vị thực phẩm k&iacute;ch th&iacute;ch d&acirc;y thần kinh trong đường h&ocirc; hấp. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho thường xuy&ecirc;n l&agrave; điều b&igrave;nh thường, n&oacute; gi&uacute;p l&agrave;m sạch c&aacute;c chất ngoại lai v&agrave; tiết ra từ phổi v&agrave; ngăn ngừa nhiễm tr&ugrave;ng. Tuy nhi&ecirc;n, ho thời gian k&eacute;od&agrave;i thường l&agrave; kết quả của một vấn đề tiềm ẩn. V&iacute; dụ bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Chảy nước mũi sau</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mỗi ng&agrave;y, tuyến ở mũi v&agrave; xoang sản xuất chất nhầy v&agrave;o cổ họng, l&agrave;m sạch v&agrave; ẩm đoạn mũi. Th&ocirc;ng thường nuốt c&aacute;c chất lỏng m&agrave; kh&ocirc;ng biết n&oacute;, nhưng khi c&oacute; nhiều hơn b&igrave;nh thường từ dị ứng, nhiễm tr&ugrave;ng lạnh hoặc xoang c&oacute; thể cảm thấy n&oacute; t&iacute;ch lũy ở mặt sau của cổ họng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Chất nhờn dư thừa n&agrave;y, c&oacute; thể g&acirc;y dị ứng v&agrave; vi&ecirc;m g&acirc;y ra phản xạ ho. Nếu chảy nước mũi sau l&agrave; m&atilde;n t&iacute;nh, ho c&oacute; thể trở th&agrave;nh m&atilde;n t&iacute;nh. Mặc d&ugrave; chảy nước mũi sau thường sạch, c&oacute; thể kh&ocirc;ng bao giờ c&oacute; triệu chứng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Hen phế quản</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đ&acirc;y l&agrave; một nguy&ecirc;n nh&acirc;n phổ biến của ho m&atilde;n t&iacute;nh ở người lớn v&agrave; nguy&ecirc;n nh&acirc;n h&agrave;ng đầu ở trẻ em. Th&ocirc;ng thường, ho xảy ra với thở kh&ograve; kh&egrave; v&agrave; kh&oacute; thở, nhưng trong loại bệnh hen, ho chỉ l&agrave; triệu chứng. Ho c&oacute; li&ecirc;n quan đến c&aacute;c m&ugrave;a, xuất hiện sau một nhiễm tr&ugrave;ng đường h&ocirc; hấp tr&ecirc; , hoặc trở n&ecirc;n tồi tệ hơn khi đang tiếp x&uacute;c với kh&ocirc;ng kh&iacute; lạnh hoặc h&oacute;a chất n&agrave;o đ&oacute; hoặc nước hoa. Đ&acirc;y l&agrave; loại bệnh hen thường xuy&ecirc;n được gọi l&agrave; &quot; bệnh đường h&ocirc; hấp hiếu động &quot;. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tr&agrave;o ngược dạ d&agrave;y (GERD)</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong điều kiện th&ocirc;ng thường, acid dạ d&agrave;y chảy ngược trở lại v&agrave;o ống kết nối dạ d&agrave;y v&agrave; cổ họng (thực quản). Việc k&iacute;ch th&iacute;ch li&ecirc;n tục trong thực quản, cổ họng v&agrave; thậm ch&iacute; phổi c&oacute; thể dẫn đến ho m&atilde;n t&iacute;nh. Tr&agrave;o ngược acid thường g&acirc;y ra chứng ợ n&oacute;ng v&agrave; hương vị chua, nhưng gần một nửa những người c&oacute; ho l&agrave; do tr&agrave;o ngược kh&ocirc;ng c&oacute; triệu chứng kh&aacute;c. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nhiễm tr&ugrave;ng đường h&ocirc; hấp</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho c&oacute; thể<span style=\"\">&nbsp; </span>k&eacute;o d&agrave;i l&acirc;u sau khi hầu hết c&aacute;c triệu chứng của cảm lạnh, cảm c&uacute;m, vi&ecirc;m phổi hoặc nhiễm tr&ugrave;ng kh&aacute;c của đường h&ocirc; hấp tr&ecirc;n đ&atilde; đi xa. Trong một số trường hợp, điều n&agrave;y c&oacute; thể xảy ra do bệnh k&eacute;o d&agrave;i. Đ&ocirc;i khi, ngay cả khi nhiễm tr&ugrave;ng đ&atilde; biến mất, đường thở c&oacute; thể vẫn c&ograve;n bị vi&ecirc;m v&agrave; do đ&oacute; đặc biệt nhạy cảm với chất k&iacute;ch th&iacute;ch. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thuốc huyết &aacute;p</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Angiotensin - ức chế men chuyển (ACE ), thường được quy định đối với bệnh cao huyết &aacute;p v&agrave; suy tim, được biết l&agrave; g&acirc;y ho m&atilde;n t&iacute;nh trong khoảng 20 phần trăm của những người d&ugrave;ng thuốc. Th&ocirc;ng thường, ho bắt đầu trong v&ograve;ng một tuần sau khi bắt đầu điều trị, nhưng đ&ocirc;i khi n&oacute; c&oacute; thể kh&ocirc;ng ph&aacute;t triển l&ecirc;n đến s&aacute;u th&aacute;ng. V&agrave; mặc d&ugrave; ho thường k&eacute;o d&agrave;i v&agrave;i ng&agrave;y sau khi thuốc được dừng lại, n&oacute; cũng c&oacute; thể k&eacute;o d&agrave;i tới một th&aacute;ng hoặc hơn. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Vi&ecirc;m phế quản m&atilde;n t&iacute;nh</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m l&acirc;u d&agrave;i của đường h&ocirc; hấp ch&iacute;nh (ống phế quản) c&oacute; thể g&acirc;y ra tắc nghẽn, kh&oacute; thở, thở kh&ograve; kh&egrave;, ho v&agrave; đổi m&agrave;u đờm. Bởi v&igrave; hầu hết mọi người với vi&ecirc;m phế quản m&atilde;n t&iacute;nh l&agrave; hiện tại hoặc trước đ&acirc;y h&uacute;t thuốc, ho thường l&agrave; một dấu hiệu của tổn hại tới phổi v&agrave; đường h&ocirc; hấp. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Gi&atilde;n phế quản</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đ&acirc;y l&agrave; một t&igrave;nh trạng nghi&ecirc;m trọng về phổi m&atilde;n t&iacute;nh, trong đ&oacute; bất thường mở rộng của ống phế quản ảnh hưởng đến khả năng l&agrave;m sạch chất nhờn từ phổi. N&oacute; gần như lu&ocirc;n vi&ecirc;m phổi, mặc d&ugrave; c&oacute; thể kh&ocirc;ng đủ nghi&ecirc;m trọng. C&aacute;c dấu hiệu v&agrave; triệu chứng bao gồm ho v&agrave; đổi m&agrave;u đờm hoặc m&aacute;u, kh&oacute; thở v&agrave; mệt mỏi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ung thư phổi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Chỉ c&oacute; một tỷ lệ phần trăm nhỏ của những người bị ho m&atilde;n t&iacute;nh bị ung thư phổi, v&agrave; nhất l&agrave; hiện tại hoặc trước đ&acirc;y h&uacute;t thuốc. Nếu đờm c&oacute; chứa m&aacute;u, h&atilde;y gặp b&aacute;c sỹ. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bất cứ ai cũng c&oacute; thể ph&aacute;t triển ho m&atilde;n t&iacute;nh, nhưng những yếu tố n&agrave;y l&agrave;m dễ bị: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">H&uacute;t thuốc l&aacute;</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hiện tại hoặc trước đ&acirc;y h&uacute;t thuốc l&agrave; một trong những yếu tố nguy cơ h&agrave;ng đầu cho ho m&atilde;n t&iacute;nh. Thường xuy&ecirc;n tiếp x&uacute;c với kh&oacute;i thuốc l&aacute; cũng c&oacute; thể dẫn đến ho v&agrave; hư hại phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Giới t&iacute;nh</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bởi v&igrave; phụ nữ thường c&oacute; phản xạ ho nhạy cảm hơn, họ đang c&oacute; nhiều khả năng để ph&aacute;t triển ho m&atilde;n t&iacute;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một cơn ho dai dẳng c&oacute; thể hết. C&aacute;c h&agrave;nh động vật l&yacute; của ho l&agrave;m cạn kiệt dự trữ năng lượng v&agrave; ph&aacute; vỡ giấc ngủ. Ho mạn t&iacute;nh cũng c&oacute; thể g&acirc;y ra: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhức đầu.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ch&oacute;ng mặt.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Qu&aacute; nhiều mồ h&ocirc;i.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tiết niệu kh&ocirc;ng th&ecirc;̉ giư được. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bị g&atilde;y xương sườn, đặc biệt l&agrave; ở phụ nữ với xương dễ vỡ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ba nguy&ecirc;n nh&acirc;n phổ biến h&agrave;ng đầu của ho m&atilde;n t&iacute;nh - chảy nước mũi sau, hen suyễn v&agrave; acid tr&agrave;o ngược, c&aacute;c b&aacute;c sĩ thường c&oacute; thể x&aacute;c định được vấn đề cơ bản th&ocirc;ng qua phản ứng để điều trị hơn l&agrave; bởi c&aacute;c kiểm tra. Nếu ho với điều trị cho một vấn đề cụ thể, chẩn đo&aacute;n được x&aacute;c nhận. Điều trị bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thuốc kh&aacute;ng histamine v&agrave; thuốc th&ocirc;ng mũi cho chảy nước mũi sau.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thuốc h&iacute;t hoặc thuốc xịt mũi để chữa hen. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thuốc giảm acid tr&agrave;o ngược.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu phương ph&aacute;p n&agrave;y trở th&agrave;nh thất vọng với qu&aacute; tr&igrave;nh thử nghiệm, c&oacute; thể cần một hoặc một số c&aacute;c x&eacute;t nghiệm sau đ&acirc;y. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">X - quang ngực</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">X - ray kh&ocirc;ng tiết lộ những l&yacute; do phổ biến nhất cho ho, n&oacute; c&oacute; thể được sử dụng để kiểm tra ung thư phổi v&agrave; c&aacute;c bệnh phổi kh&aacute;c.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Chụp cắt lớp vi t&iacute;nh (CT scan )</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một CT</span><span style=\"font-family: Arial;\"> scan c&oacute; X -quang từ nhiều g&oacute;c độ kh&aacute;c nhau v&agrave; sau đ&oacute; kết hợp ch&uacute;ng lại để tạo th&agrave;nh h&igrave;nh ảnh mặt cắt ngang. Kỹ thuật n&agrave;y c&oacute; thể cung cấp chi tiết hơn về phổi. CT scan cũng c&oacute; thể được sử dụng để kiểm tra s&acirc;u răng, xoang nhiễm tr&ugrave;ng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">X&eacute;t nghiệm chức năng phổi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đơn giản, kh&ocirc;ng x&acirc;m lấn. Đ&ocirc;i khi cũng c&oacute; thể c&oacute; một b&agrave;i kiểm tra thử th&aacute;ch hen, trong đ&oacute; kiểm tra h&iacute;t thở như thế n&agrave;o c&oacute; thể trước v&agrave; sau khi h&iacute;t một loại thuốc gọi l&agrave; methacholine. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nội soi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c x&eacute;t nghiệm n&agrave;y sử dụng một ống mỏng, linh hoạt, trang bị với một &aacute;nh s&aacute;ng v&agrave; camera để h&igrave;nh dung cấu tr&uacute;c b&ecirc;n trong cơ thể. Thủ tục n&agrave;y lu&ocirc;n được phun mũi hay cổ họng với một thuốc t&ecirc; như lidocain. C&oacute; thể được cho thuốc an thần hoặc thuốc giảm đau để l&agrave;m thủ tục &iacute;t kh&oacute; chịu. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Nội soi mũi.</span></b><span style=\"font-family: Arial;\"> Thử nghiệm n&agrave;y bao gồm việc ch&egrave;n một c&aacute;p quang nhỏ v&agrave;o lỗ mũi để đ&aacute;nh gi&aacute; tốt hơn t&igrave;nh trạng của ni&ecirc;m mạc mũi v&agrave; c&aacute;c lỗ đến xoang. Một CT scan xoang thường được thực hiện đầu ti&ecirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Nội soi dạ d&agrave;y thực quản.</span></b><span style=\"font-family: Arial;\"> Trong thử nghiệm n&agrave;y, ống soi được th&ocirc;ng qua xuống cổ họng v&agrave;o thực quản để kiểm tra c&aacute;c dấu hiệu của tr&agrave;o ngược axit trong dạ d&agrave;y v&agrave; thực quản.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Soi phế quản.</span></b><span style=\"font-family: Arial;\"> Trong thử nghiệm n&agrave;y, ống soi được th&ocirc;ng qua xuống kh&iacute; quản để kiểm tra c&aacute;c ống phế quản cho c&aacute;c dấu hiệu nhiễm tr&ugrave;ng hoặc tắc nghẽn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều trị ho m&atilde;n t&iacute;nh với một nguy&ecirc;n nh&acirc;n thường l&agrave; đơn giản. Khi g&acirc;y ra kh&ocirc;ng thể được x&aacute;c định, điều trị trở n&ecirc;n kh&oacute; giải quyết. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thuốc kh&aacute;ng histamine v&agrave; thuốc th&ocirc;ng mũi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"\">&nbsp;</span>Thuốc kh&aacute;ng histamine v&agrave; thuốc th&ocirc;ng mũi thường được kết hợp như ti&ecirc;u chuẩn điều trị cho c&aacute;c bệnh dị ứng v&agrave; chảy nước mũi sau. Thuốc kh&aacute;ng histamine thế hệ mới c&oacute; thể hiệu quả trong điều trị ho hơn thế hệ cũ m&agrave; kh&ocirc;ng l&agrave;m buồn ngủ. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">H&iacute;t corticosteroids </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c thuốc chống vi&ecirc;m được điều trị hiệu quả nhất cho bệnh hen v&agrave; bệnh hen c&oacute; li&ecirc;n quan đến ho, nhưng việc sử dụng thuốc gi&atilde;n phế quản h&iacute;t v&agrave;o cũng c&oacute; thể được y&ecirc;u cầu. Sử dụng d&agrave;i hạn corticosteroid c&oacute; thể l&agrave;m tăng nguy cơ da mỏng đi, vết bầm, lo&atilde;ng xương v&agrave; đục thủy tinh thể. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thuốc để điều trị acid tr&agrave;o ngược</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi thay đổi lối sống kh&ocirc;ng kết quả, c&oacute; thể được điều trị bằng một thuốc ức chế bơm proton, ngăn chặn acid sản xuất v&agrave; cho ph&eacute;p thời gian để chữa l&agrave;nh c&aacute;c m&ocirc; thực quản. Toa chất ức chế bơm proton sức mạnh bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Esomeprazole (Nexium).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Lansoprazole (Prevacid).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Omeprazole (Prilosec).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Pantoprazole (Protonix).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Rabeprazole (Aciphex).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi l&yacute; do ho kh&ocirc;ng được biết, b&aacute;c sĩ c&oacute; thể k&ecirc; một giảm ho triệu chứng hoặc một loại thuốc l&agrave;m thư gi&atilde;n c&aacute;c đoạn th&ocirc;ng kh&iacute; trong phổi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phong c&aacute;ch sống v&agrave; biện ph&aacute;p khắc phục</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">H&uacute;t thuốc l&aacute;</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhiều loại ho m&atilde;n t&iacute;nh g&acirc;y ra hoặc trở n&ecirc;n tồi tệ bởi h&uacute;t thuốc l&aacute;. Bỏ thuốc l&aacute; c&oacute; thể gi&uacute;p đỡ đ&aacute;ng kể, như c&oacute; thể tr&aacute;nh kh&oacute;i thuốc. N&oacute;i chuyện với b&aacute;c sĩ về một chương tr&igrave;nh ngừng h&uacute;t thuốc l&aacute; to&agrave;n diện bao gồm:<span style=\"color: red;\"> </span></span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hỗ trợ x&atilde; hội.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tư vấn dinh dưỡng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nicotin kẹo cao su .</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thuốc men.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Acid tr&agrave;o ngược</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho do acid tr&agrave;o ngược thường c&oacute; thể được điều trị bằng thay đổi lối sống. Ch&uacute;ng bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Duy tr&igrave; một trọng lượng khỏe mạnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ăn bữa nhỏ hơn v&agrave; thường xuy&ecirc;n hơn ăn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tr&aacute;nh g&acirc;y ợ n&oacute;ng như rượu, chocolate, bạc h&agrave; v&agrave; c&aacute;c loại thực phẩm chi&ecirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Chờ 3 - 4 giờ sau khi một bữa ăn trước khi nằm xuống.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">N&acirc;ng cao đầu giường.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Giảm ho</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một số sản phẩm mua kh&ocirc;ng cần toa c&oacute; thể gi&uacute;p kiểm so&aacute;t ho, nhưng kh&ocirc;ng t&aacute;c dụng g&igrave; cho c&aacute;c nguy&ecirc;n nh&acirc;n. V&iacute; dụ bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ch&agrave; ngực với chất c&oacute; chứa long n&atilde;o hoặc tinh dầu bạc h&agrave;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Xir&ocirc; ho, đặc biệt l&agrave; c&oacute; chứa dextromethorphan.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thu&ocirc;́c ho.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mật ong, đặc biệt l&agrave; trong nước n&oacute;ng hoặc tr&agrave;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: right;\"><b style=\"\"><i style=\"\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(128, 0, 0);\"></span></span></i></b></p>\r\n<p class=\"MsoNormal\" style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">&nbsp;</span></p></span>\n', 1),
(7, 'Hội chứng hô hấp cấp tính nặng (SARS)', '\n   <h1 class=\"title\">Hội chứng hô hấp cấp tính nặng (SARS)</h1>   <span class=\"des\">SARS thường bắt đầu có dấu hiệu giống như cúm và các triệu chứng - bị sốt, ớn lạnh, đau nhức bắp thịt và tiêu chảy thường xuyên. </span>\n   <span><p style=\"text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Hội chứng h&ocirc; hấp cấp t&iacute;nh nặng (SARS) l&agrave; một bệnh h&ocirc; hấp truyền nhiễm v&agrave; đ&ocirc;i khi g&acirc;y tử vong. SARS đầu ti&ecirc;n xuất hiện tại Trung Quốc v&agrave;o th&aacute;ng Mười năm 2002. Trong v&ograve;ng một v&agrave;i th&aacute;ng, SARS l&acirc;y lan tr&ecirc;n to&agrave;n thế giới, mang theo với du kh&aacute;ch kh&ocirc;ng nghi ngờ.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">SARS đ&atilde; cho thấy c&aacute;ch nhanh ch&oacute;ng l&acirc;y nhiễm c&oacute; thể l&acirc;y lan trong một thế giới di động cao v&agrave; kết nối với nhau. Dịch SARS cũng đ&atilde; chứng minh rằng hợp t&aacute;c quốc tế giữa c&aacute;c chuy&ecirc;n gia y tế c&oacute; hiệu quả c&oacute; thể với sự l&acirc;y lan của bệnh. Từ năm 2004, được biết đến của SARS l&acirc;y truyền đ&atilde; giảm xuống bằng kh&ocirc;ng tr&ecirc;n to&agrave;n thế giới.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">SARS thường bắt đầu c&oacute; dấu hiệu giống như c&uacute;m v&agrave; c&aacute;c triệu chứng - bị sốt, ớn lạnh, đau nhức bắp thịt v&agrave; ti&ecirc;u chảy thường xuy&ecirc;n. Sau một tuần, dấu hiệu v&agrave; triệu chứng bao gồm:</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Sốt 100,4 F (38 độ C) hoặc cao hơn.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Ho khan.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Kh&oacute; thở.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Đến gặp b&aacute;c sĩ khi</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Nếu nghĩ rằng đ&atilde; bị nhiễm hoặc bị nhiễm SARS, gặp b&aacute;c sĩ ngay lập tức. SARS l&agrave; một căn bệnh nghi&ecirc;m trọng c&oacute; thể dẫn đến tử vong.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">SARS được g&acirc;y ra bởi một d&ograve;ng coronavirus, c&ugrave;ng một gia đ&igrave;nh của virus g&acirc;y cảm lạnh th&ocirc;ng thường. Cho đến nay, những loại virus n&agrave;y chưa bao giờ đặc biệt nguy hiểm ở người, mặc d&ugrave; ch&uacute;ng c&oacute; thể g&acirc;y bệnh nặng ở động vật. V&igrave; l&yacute; do đ&oacute;, c&aacute;c nh&agrave; khoa học ban đầu cho rằng virus SARS c&oacute; thể đ&atilde; vượt qua từ động vật sang người. N&oacute; b&acirc;y giờ c&oacute; khả năng đ&atilde; tiến h&oacute;a từ một hoặc nhiều virus động vật v&agrave;o một d&ograve;ng ho&agrave;n to&agrave;n mới.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">L&acirc;y lan SARS thế n&agrave;o</span><span lang=\"EN-US\" style=\"font-family: Arial;\"><br>\r\nHầu hết c&aacute;c bệnh đường h&ocirc; hấp, bao gồm cả SARS, l&acirc;y lan qua c&aacute;c giọt nhỏ nhập v&agrave;o kh&ocirc;ng kh&iacute; khi một ai đ&oacute; với bệnh ho, hắt hơi, n&oacute;i chuyện. Hầu hết c&aacute;c chuy&ecirc;n gia cho rằng SARS l&acirc;y lan chủ yếu qua mặt đối mặt, nhưng virus cũng c&oacute; thể l&acirc;y lan tr&ecirc;n c&aacute;c đối tượng bị &ocirc; nhiễm - chẳng hạn như tay nắm cửa, điện thoại v&agrave; c&aacute;c n&uacute;t thang m&aacute;y.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">N&oacute;i chung, người c&oacute; nguy cơ lớn nhất của SARS đ&atilde; c&oacute; chỉ đạo, tiếp x&uacute;c gần với người bị nhiễm bệnh, chẳng hạn như th&agrave;nh vi&ecirc;n gia đ&igrave;nh v&agrave; nh&acirc;n vi&ecirc;n y tế. </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Hầu hết mọi người với SARS ph&aacute;t triển vi&ecirc;m phổi. Vấn đề về thở c&oacute; thể trở n&ecirc;n nghi&ecirc;m trọng m&agrave; một nạ kh&iacute; l&agrave; cần thiết. SARS g&acirc;y tử vong trong một số trường hợp, thường do suy h&ocirc; hấp. C&aacute;c biến chứng kh&aacute;c c&oacute; thể bao gồm tim v&agrave; suy gan.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Người lớn tuổi hơn so với tuổi 60 - đặc biệt l&agrave; những người c&oacute; điều kiện cơ bản như bệnh tiểu đường hoặc bệnh vi&ecirc;m gan - c&oacute; nguy cơ cao bị biến chứng nghi&ecirc;m trọng. </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Khi SARS đầu ti&ecirc;n, kh&ocirc;ng c&oacute; x&eacute;t nghiệm cụ thể c&oacute; sẵn để gi&uacute;p b&aacute;c sĩ chẩn đo&aacute;n bệnh. Một số x&eacute;t nghiệm c&oacute; thể gi&uacute;p ph&aacute;t hiện virus. Nhưng kh&ocirc;ng biết đến SARS đ&atilde; xảy ra bất cứ nơi n&agrave;o tr&ecirc;n thế giới từ năm 2004.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Mặc d&ugrave; c&oacute; một nỗ lực to&agrave;n cầu phối hợp, c&aacute;c nh&agrave; khoa học vẫn chưa t&igrave;m thấy điều trị hiệu quả đối với bệnh SARS. Thuốc kh&aacute;ng sinh kh&ocirc;ng c&oacute; t&aacute;c dụng chống lại virus v&agrave; c&aacute;c loại thuốc kh&aacute;ng vi-r&uacute;t đ&atilde; kh&ocirc;ng thể được hưởng lợi nhiều.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Ph&ograve;ng chống</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">C&aacute;c nh&agrave; nghi&ecirc;n cứu đang l&agrave;m việc tr&ecirc;n một số loại vắc xin cho SARS, nhưng kh&ocirc;ng ai đ&atilde; được thử nghiệm. Nếu SARS tiếp tục l&acirc;y nhiễm, theo c&aacute;c hướng dẫn an to&agrave;n nếu đang chăm s&oacute;c cho người bị nhiễm bệnh:</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Rửa tay.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Rửa tay thường xuy&ecirc;n bằng x&agrave; ph&ograve;ng v&agrave; nước n&oacute;ng hoặc sử dụng dựa tr&ecirc;n ch&agrave; x&aacute;t rượu b&agrave;n tay c&oacute; chứa rượu &iacute;t nhất 60 phần trăm.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Mang bao tay d&ugrave;ng một lần.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Nếu c&oacute; tiếp x&uacute;c với chất lỏng của cơ thể hoặc ph&acirc;n người, đeo găng tay d&ugrave;ng một lần. Vứt bỏ găng tay ra ngay lập tức sau khi sử dụng v&agrave; rửa tay kỹ lưỡng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Đeo khẩu trang phẫu thuật.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Khi đang ở trong c&ugrave;ng một ph&ograve;ng một người bị SARS, che miệng v&agrave; mũi với một mặt nạ phẫu thuật. Đeo k&iacute;nh mắt cũng c&oacute; thể cung cấp một số bảo vệ.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Rửa dụng cụ c&aacute; nh&acirc;n.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Sử dụng x&agrave; ph&ograve;ng v&agrave; nước n&oacute;ng để rửa c&aacute;c dụng cụ, khăn, bộ đồ giường v&agrave; quần &aacute;o của một ai đ&oacute; với SARS.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Khử tr&ugrave;ng c&aacute;c bề mặt.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Sử dụng thuốc khử tr&ugrave;ng để lau ch&ugrave;i c&aacute;c bề mặt c&oacute; thể đ&atilde; bị &ocirc; nhiễm với mồ h&ocirc;i, nước bọt, dịch nhầy, chất &oacute;i mửa, ph&acirc;n hay nước tiểu. </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Thực hiện theo tất cả c&aacute;c biện ph&aacute;p ph&ograve;ng ngừa &iacute;t nhất 10 ng&agrave;y kể từ ng&agrave;y c&oacute; dấu hiệu v&agrave; triệu chứng đ&atilde; biến mất. Kh&ocirc;ng cho trẻ từ trường về nh&agrave; nếu bị sốt hoặc c&oacute; triệu chứng đường h&ocirc; hấp trong thời hạn 10 ng&agrave;y kể từ ng&agrave;y tiếp x&uacute;c với một ai đ&oacute; với SARS. Trẻ em c&oacute; thể trở lại trường học nếu c&aacute;c dấu hiệu v&agrave; triệu chứng biến mất sau ba ng&agrave;y.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: right;\"><b style=\"\"><i style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\"><span style=\"color: rgb(128, 0, 0);\"></span></span></i></b></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\">&nbsp;</p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">&nbsp;</span></p></span>\n', 1);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(8, 'Khí phế thũng', '\n   <h1 class=\"title\">Khí phế thũng</h1>   <span class=\"des\">Khí phế thũng! Có thể: Khó thở, thở khò khè, tức ngực, giảm khả năng hoạt động thể chất, ho mãn tính, ăn kém và gầy, mệt mỏi...Gặp bác sỹ khi: Mệt nhanh chóng, khó thở khi hoạt động vừa phải.</span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&iacute; phế thũng l&agrave; một yếu tố trong sự tiến triển của bệnh nghẽn phổi m&atilde;n t&iacute;nh (COPD), một điều kiện hạn chế d&ograve;ng chảy của kh&iacute; khi thở ra. Kh&iacute; phế thũng xảy ra khi c&aacute;c t&uacute;i kh&iacute; ở cuối của đường dẫn kh&iacute; nhỏ nhất (tiểu phế quản) đang dần bị ph&aacute; hủy. H&uacute;t thuốc l&agrave; nguy&ecirc;n nh&acirc;n h&agrave;ng đầu của kh&iacute; phế thũng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nặng hơn, kh&iacute; phế thủng t&uacute;i kh&iacute; h&oacute;a c&aacute;c h&igrave;nh cầu - tập hợp giống như ch&ugrave;m nho, t&uacute;i kh&ocirc;ng đều c&oacute; lỗ hổng trong c&aacute;c bức th&agrave;nh b&ecirc;n trong của n&oacute;. Điều n&agrave;y l&agrave;m giảm số lượng c&aacute;c t&uacute;i kh&iacute; v&agrave; giữ &ocirc;xy từ phổi đến m&aacute;u. Ngo&agrave;i ra, c&aacute;c sợi đ&agrave;n hồi để giữ mở c&aacute;c đường dẫn kh&iacute; nhỏ dẫn đến c&aacute;c t&uacute;i kh&iacute; (phế nang) đang dần bị ph&aacute; hủy, để họ sụp khi thở ra. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tắc nghẽn đường thở, một t&iacute;nh năng của COPD g&oacute;p phần v&agrave;o kh&iacute; phế thũng. Sự kết hợp của bệnh kh&iacute; thũng v&agrave; đường h&ocirc; hấp bị cản trở l&agrave;m cho hơi thở ng&agrave;y c&agrave;ng kh&oacute; khăn. Điều trị thường chậm lại, nhưng kh&ocirc;ng đảo ngược qu&aacute; tr&igrave;nh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Triệu chứng kh&iacute; phế thũng bắt đầu nhẹ với bệnh tiến triển đều đặn c&agrave;ng xấu hơn. C&aacute;c triệu chứng bệnh kh&iacute; thũng ch&iacute;nh l&agrave;: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thở kh&ograve; kh&egrave;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tức ngực.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Giảm khả năng hoạt động thể chất.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho m&atilde;n t&iacute;nh, m&agrave; cũng c&oacute; thể cho biết vi&ecirc;m phế quản m&atilde;n t&iacute;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ch&aacute;n ăn v&agrave; mất trọng lượng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mệt mỏi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Gặp b&aacute;c sĩ khi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mệt một c&aacute;ch nhanh ch&oacute;ng, hoặc c&oacute; thể kh&ocirc;ng thể l&agrave;m những việc dễ d&agrave;ng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng thể thở khi chịu đựng mức độ thực hiện ngay cả vừa phải.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; khăn hơi thở khi bị cảm lạnh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">M&ocirc;i hoặc m&oacute;ng tay c&oacute; m&agrave;u xanh hoặc m&agrave;u x&aacute;m, chỉ ra oxy thấp trong m&aacute;u.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thường xuy&ecirc;n ho ra đờm m&agrave;u v&agrave;ng hoặc m&agrave;u xanh lục.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&uacute;i xuống để buộc gi&agrave;y l&agrave;m cho kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đang giảm c&acirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những dấu hiệu v&agrave; triệu chứng kh&ocirc;ng nhất thiết c&oacute; nghĩa l&agrave; c&oacute; ph&ugrave; thủng, nhưng ch&uacute;ng cho thấy rằng phổi kh&ocirc;ng hoạt động đ&uacute;ng v&agrave; cần được đ&aacute;nh gi&aacute; bởi b&aacute;c sĩ c&agrave;ng sớm c&agrave;ng tốt. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c nguy&ecirc;n nh&acirc;n g&acirc;y bệnh kh&iacute; thũng bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">H&uacute;t thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&uacute;t thuốc l&aacute; l&agrave; nguy&ecirc;n nh&acirc;n phổ biến nhất của bệnh kh&iacute; thũng. C&oacute; hơn 4.000 h&oacute;a chất trong kh&oacute;i thuốc l&aacute;. Những chất k&iacute;ch th&iacute;ch h&oacute;a học ph&aacute; hủy từ từ đường h&ocirc; hấp ngoại vi nhỏ, t&uacute;i kh&iacute; - đ&agrave;n hồi v&agrave; hỗ trợ của c&aacute;c sợi đ&agrave;n hồi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thiếu hụt Protein </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khoảng 1 - 2 % những người c&oacute; kh&iacute; phế thũng c&oacute; thừa kế thiếu một protein gọi l&agrave; AAT, để bảo vệ c&aacute;c cấu tr&uacute;c đ&agrave;n hồi trong phổi. Nếu kh&ocirc;ng c&oacute; protein n&agrave;y, c&aacute;c enzyme c&oacute; thể g&acirc;y tổn thương phổi tiến triển, cuối c&ugrave;ng dẫn đến bệnh kh&iacute; thũng. Nếu l&agrave; một người h&uacute;t thuốc với thiếu AAT, kh&iacute; phế thũng c&oacute; thể bắt đầu ở độ tuổi 30 v&agrave; 40. Những tiến triển v&agrave; mức độ nghi&ecirc;m trọng của căn bệnh n&agrave;y l&agrave; rất nhanh do h&uacute;t thuốc l&aacute;. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Yếu tố nguy cơ bệnh kh&iacute; thũng bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">H&uacute;t thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&iacute; phế thũng rất c&oacute; thể ph&aacute;t triển ở người h&uacute;t thuốc l&aacute;, x&igrave; g&agrave; v&agrave; ống h&uacute;t thuốc cũng l&agrave; nhạy cảm v&agrave; nguy cơ cho tất cả những người h&uacute;t thuốc tăng với số năm v&agrave; số lượng thuốc l&aacute; h&uacute;t.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tuổi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mặc d&ugrave; những tổn thương phổi xảy ra trong kh&iacute; phế thũng ph&aacute;t triển dần dần, hầu hết những người c&oacute; li&ecirc;n quan đến kh&iacute; phế thũng thuốc l&aacute; bắt đầu c&oacute; những triệu chứng của bệnh trong độ tuổi từ 40 v&agrave; 60.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tiếp x&uacute;c với kh&oacute;i thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&uacute;t thuốc gi&aacute;n tiếp, cũng gọi l&agrave; kh&oacute;i thuốc l&aacute; thụ động, kh&ocirc;ng h&uacute;t thuốc m&agrave; v&ocirc; t&igrave;nh h&iacute;t v&agrave;o từ người kh&aacute;c, thuốc l&aacute; hoặc x&igrave; g&agrave;. Kh&oacute;i thuốc xung quanh l&agrave;m tăng nguy cơ bệnh kh&iacute; thũng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nghề nghiệp tiếp x&uacute;c với kh&oacute;i, bụi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu h&iacute;t thở kh&oacute;i từ một số h&oacute;a chất, bụi từ ngũ cốc, gỗ, b&ocirc;ng hoặc c&aacute;c sản phẩm khai th&aacute;c mỏ, c&oacute; nhiều khả năng ph&aacute;t triển bệnh kh&iacute; thũng. Nguy cơ n&agrave;y thậm ch&iacute; c&ograve;n lớn hơn nếu h&uacute;t thuốc. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tiếp x&uacute;c với &ocirc; nhiễm trong nh&agrave; v&agrave; ngo&agrave;i trời</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thở c&aacute;c chất &ocirc; nhiễm trong nh&agrave;, chẳng hạn như kh&iacute; thải từ nhi&ecirc;n liệu sưởi ấm, cũng như c&aacute;c chất g&acirc;y &ocirc; nhiễm ngo&agrave;i trời, v&iacute; dụ kh&oacute;i xe, tăng nguy cơ bệnh kh&iacute; thũng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">L&acirc;y nhiễm HIV</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những người h&uacute;t thuốc sống với HIV c&oacute; nguy cơ lớn hơn kh&iacute; phế thũng hơn l&agrave; người h&uacute;t thuốc l&aacute; kh&ocirc;ng c&oacute; nhiễm HIV.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Rối loạn m&ocirc; li&ecirc;n kết</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một số điều kiện c&oacute; ảnh hưởng đến c&aacute;c m&ocirc; li&ecirc;n kết - c&aacute;c sợi cung cấp khu&ocirc;n v&agrave; hỗ trợ cho cơ thể được kết hợp với kh&iacute; phế thũng. C&aacute;c điều kiện n&agrave;y bao gồm laxa - da, một căn bệnh hiếm gặp g&acirc;y ra l&atilde;o h&oacute;a sớm v&agrave; hội chứng Marfan, một rối loạn c&oacute; ảnh hưởng đến nhiều cơ quan kh&aacute;c nhau, đặc biệt l&agrave; tim, mắt, xương v&agrave; phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&iacute; phế thũng c&oacute; thể l&agrave;m tăng mức độ nghi&ecirc;m trọng của c&aacute;c điều kiện m&atilde;n t&iacute;nh kh&aacute;c, như bệnh tiểu đường v&agrave; suy tim. Nếu c&oacute; kh&iacute; phế thũng, &ocirc; nhiễm kh&ocirc;ng kh&iacute; hoặc nhiễm tr&ugrave;ng đường h&ocirc; hấp c&oacute; thể dẫn đến một đợt cấp COPD, với độ kh&oacute; thở v&agrave; nồng độ oxy thấp nguy hiểm. C&oacute; thể cần nhập một đơn v��� chăm s&oacute;c t&iacute;ch cực v&agrave; hỗ trợ tạm thời từ một m&aacute;y thở nh&acirc;n tạo cho đến khi nhiễm tr&ugrave;ng x&oacute;a hết. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Để x&aacute;c định xem c&oacute; ph&ugrave; thủng, b&aacute;c sĩ c&oacute; thể khuy&ecirc;n n&ecirc;n thử nghiệm nhất định, bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Đo phế dung v&agrave; x&eacute;t nghiệm chức năng phổi kh&aacute;c (PFTs)</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c x&eacute;t nghiệm kh&ocirc;ng x&acirc;m lấn c&oacute; thể ph&aacute;t hiện kh&iacute; phế thũng trước khi c&oacute; triệu chứng. Đ&aacute;nh gi&aacute; bao nhi&ecirc;u kh&ocirc;ng kh&iacute; phổi c&oacute; thể giữ v&agrave; d&ograve;ng chảy của kh&ocirc;ng kh&iacute; v&agrave;o v&agrave; ra khỏi phổi. Cũng c&oacute; thể đo lường phổi cung cấp &ocirc;xy cho m&aacute;u như thế n&agrave;o. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong đo phế dung, thường y&ecirc;u cầu thổi v&agrave;o một dụng cụ đơn giản gọi l&agrave; phế dung kế. PFTs c&oacute; thể được thực hiện trước v&agrave; sau khi sử dụng thuốc h&iacute;t để kiểm tra phản ứng<span style=\"\">&nbsp; </span>đối với n&oacute;. Nếu l&agrave; một người nghiện thuốc l&aacute; hoặc người h&uacute;t thuốc cũ, h&atilde;y hỏi b&aacute;c sĩ về việc tham gia c&aacute;c thử nghiệm n&agrave;y, ngay cả khi kh&ocirc;ng c&oacute; triệu chứng của bệnh kh&iacute; thũng hay COPD. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ph&acirc;n t&iacute;ch kh&iacute; m&aacute;u động mạch</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c x&eacute;t nghiệm m&aacute;u đo lường phổi chuyển oxy v&agrave;o như thế n&agrave;o v&agrave; loại bỏ kh&iacute; cacbonic từ m&aacute;u.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p đo oxy xung</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thử nghiệm n&agrave;y bao gồm việc sử dụng một thiết bị nhỏ gắn v&agrave;o ng&oacute;n tay. C&aacute;c biện ph&aacute;p đo lượng &ocirc;xy trong m&aacute;u kh&aacute;c với c&aacute;ch được đo bằng một ph&acirc;n t&iacute;ch kh&iacute; m&aacute;u. Để gi&uacute;p x&aacute;c định xem cần bổ sung oxy, thử nghiệm c&oacute; thể được thực hiện ở phần c&ograve;n lại, trong khi tập luyện v&agrave; qua đ&ecirc;m.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Chụp X - quang</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Chụp X - quang c&oacute; thể gi&uacute;p x&aacute;c định chẩn đo&aacute;n bệnh kh&iacute; thũng v&agrave; loại trừ nguy&ecirc;n nh&acirc;n kh&aacute;c g&acirc;y kh&oacute; thở, nhưng X - quang l&agrave; kh&ocirc;ng đủ để l&agrave;m một chẩn đo&aacute;n ch&iacute;nh x&aacute;c. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Kiểm tra đờm</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ph&acirc;n t&iacute;ch của c&aacute;c tế b&agrave;o ho ra (đờm) c&oacute; thể gi&uacute;p x&aacute;c định nguy&ecirc;n nh&acirc;n của một số vấn đề về phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">M&aacute;y vi t&iacute;nh cắt lớp (CT scan). CT scan c&oacute; thể cho ph&eacute;p b&aacute;c sĩ xem cơ quan nội tạng, nơi sẽ t&igrave;m c&aacute;c đặc trưng hoặc b&oacute;ng nước m&agrave; do kh&iacute; phế thũng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bước quan trọng nhất trong bất kỳ kế hoạch điều trị cho người h&uacute;t thuốc với bệnh kh&iacute; thũng l&agrave; ngừng h&uacute;t thuốc, đ&oacute; l&agrave; c&aacute;ch duy nhất để ngăn chặn c&aacute;c thiệt hại cho phổi<span style=\"\">&nbsp; </span>trở n&ecirc;n tồi tệ hơn. Nhưng bỏ l&agrave; kh&ocirc;ng bao giờ dễ d&agrave;ng, v&agrave; người ta thường cần sự gi&uacute;p đỡ của một kế hoạch to&agrave;n diện ngừng h&uacute;t thuốc l&aacute;, c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một ng&agrave;y mục ti&ecirc;u để bỏ thuốc l&aacute;. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ph&ograve;ng, chống t&aacute;i nghiện. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Lời khuy&ecirc;n d&agrave;nh cho c&aacute;c thay đổi lối sống l&agrave;nh mạnh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hệ thống hỗ trợ x&atilde; hội.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thuốc men, kẹo cao su nicotine như hay loại thay thế v&agrave; thuốc theo toa hydrochloride, bupropion v&agrave; varenicline.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Phương ph&aacute;p điều trị bệnh kh&iacute; thũng kh&aacute;c tập trung v&agrave;o việc gi&uacute;p cảm thấy tốt hơn, trở lại hoạt động nhiều hơn v&agrave; l&agrave;m chậm sự tiến triển của bệnh kh&iacute; thũng. Ch&uacute;ng bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thuốc gi&atilde;n phế quản</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c thuốc n&agrave;y c&oacute; thể gi&uacute;p l&agrave;m giảm ho, kh&oacute; thở v&agrave; kh&oacute; thở do thư gi&atilde;n đường thở hẹp, nhưng ch&uacute;ng kh&ocirc;ng c&oacute; hiệu quả trong điều trị bệnh kh&iacute; thũng khi đang ở trong điều trị bệnh hen suyễn hay vi&ecirc;m phế quản m&atilde;n t&iacute;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">H&iacute;t steroid</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thuốc corticosteroid h&iacute;t như thuốc xịt aerosol c&oacute; thể l&agrave;m giảm triệu chứng của bệnh kh&iacute; thũng kết hợp với bệnh hen suyễn v&agrave; vi&ecirc;m phế quản. Mặc d&ugrave; h&iacute;t steroid c&oacute; t&aacute;c dụng phụ &iacute;t hơn steroid đường uống, sử dụng l&acirc;u d&agrave;i c&oacute; thể l&agrave;m yếu xương v&agrave; tăng nguy cơ cao huyết &aacute;p, đục thủy tinh thể v&agrave; b���nh tiểu đường.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Điều trị GERD </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Triệu chứng bệnh tr&agrave;o ngược dạ d&agrave;y thực quản (GERD) trầm trọng th&ecirc;m c&aacute;c bệnh đường thở ở nhiều người, v&igrave; thế b&aacute;c sĩ c&oacute; thể khuy&ecirc;n n&ecirc;n d&ugrave;ng thuốc cũng như thay đổi lối sống để điều trị n&oacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Bổ sung oxy</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; kh&iacute; phế thũng nặng với nồng độ &ocirc;xy trong m&aacute;u thấp, sử dụng oxy thường xuy&ecirc;n ở nh&agrave; v&agrave; khi tập thể dục c&oacute; thể cung cấp một số cứu trợ. Nhiều người sử dụng oxy 24 giờ một ng&agrave;y. Nhiều h&igrave;nh thức oxy c&oacute; sẵn cũng như c&aacute;c thiết bị kh&aacute;c nhau để cung cấp &ocirc;xy cho phổi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Chương tr&igrave;nh phục hồi chức năng phổi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một phần quan trọng của việc điều trị bao gồm một chương tr&igrave;nh phục hồi chức năng phổi, kết hợp gi&aacute;o dục, đ&agrave;o tạo, tập thể dục v&agrave; can thiệp h&agrave;nh vi để gi&uacute;p trở lại hoạt động v&agrave; cải thiện sức khỏe v&agrave; chất lượng cuộc sống. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Trợ gi&uacute;p cai nghiện thuốc l&aacute; v&agrave; nhu cầu dinh dưỡng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; thể t&igrave;m hiểu kỹ thuật h&iacute;t thở đặc biệt v&agrave; c&aacute;ch thức để bảo tồn năng lượng. Bởi v&igrave; tập thể dục c&oacute; thể gi&uacute;p l&agrave;m chậm sự suy giảm chức năng phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thuốc kh&aacute;ng sinh</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu ph&aacute;t triển nhiễm khuẩn như vi&ecirc;m phế quản hoặc vi&ecirc;m phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ti&ecirc;m ph&ograve;ng chống c&uacute;m v&agrave; vi&ecirc;m phổi </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; kh&iacute; phế thũng hoặc c&aacute;c h&igrave;nh thức kh&aacute;c của bệnh COPD, c&aacute;c chuy&ecirc;n gia khuy&ecirc;n n&ecirc;n ph&ograve;ng bệnh c&uacute;m (flu) h&agrave;ng năm v&agrave; vi&ecirc;m phổi mỗi năm năm.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Phẫu thuật</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong một quy tr&igrave;nh thử nghiệm được gọi l&agrave; phẫu thuật giảm thể t&iacute;ch phổi (LVRS), b&aacute;c sĩ phẫu thuật loại bỏ n&ecirc;m nhỏ của m&ocirc; phổi bị hư hỏng. Loại bỏ c&aacute;c m&ocirc; bệnh gi&uacute;p phổi l&agrave;m việc hiệu quả hơn v&agrave; gi&uacute;p cải thiện hơi thở. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong phẫu thuật kh&aacute;c, gọi l&agrave; bullectomy, c&aacute;c b&aacute;c sĩ loại bỏ một hoặc nhiều c&aacute;c b&oacute;ng nước đ&atilde; h&igrave;nh th&agrave;nh khi c&aacute;c t&uacute;i kh&iacute; nhỏ bị ph&aacute; hủy. Thủ tục n&agrave;y c&oacute; thể cải thiện hơi thở. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Cấy gh&eacute;p</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Cấy gh&eacute;p phổi l&agrave; một lựa chọn nếu c&oacute; kh&iacute; phế thũng nặng v&agrave; c&aacute;c t&ugrave;y chọn kh&aacute;c đ&atilde; thất bại. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phong c&aacute;ch sống v&agrave; biện ph&aacute;p khắc phục</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một số b&agrave;i tập đơn giản c&oacute; thể cải thiện hơi thở nếu c&oacute; bệnh kh&iacute; thũng phổi hoặc rối loạn m&atilde;n t&iacute;nh kh&aacute;c. Ch&uacute;ng gi&uacute;p kiểm so&aacute;t phổi bằng c&aacute;ch sử dụng c&aacute;c cơ bụng 2 - 4 lần / ng&agrave;y. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thở bằng cơ ho&agrave;nh</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Để thực hiện kiểu thở tập thể dục, c&oacute; c&aacute;c bước sau: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nằm ngửa với đầu gối được hỗ trợ bởi gối. Bắt đầu bằng c&aacute;ch thở v&agrave;o v&agrave; ra từ từ v&agrave; nhẹ nh&agrave;ng trong một m&ocirc; h&igrave;nh nhịp điệu - Thư gi&atilde;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đặt ng&oacute;n tay l&ecirc;n bụng, ngay dưới khung xương sườn. Khi h&iacute;t v&agrave;o từ từ, sẽ cảm thấy cơ ho&agrave;nh n&acirc;ng b&agrave;n tay.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thực h&agrave;nh đẩy bụng chống lại b&agrave;n tay như ngực trở n&ecirc;n tr&agrave;n đầy kh&ocirc;ng kh&iacute;. H&atilde;y chắc chắn rằng ngực vẫn bất động. H&atilde;y thử điều n&agrave;y trong khi h&iacute;t qua miệng v&agrave; đếm chậm đến ba. Sau đ&oacute; thở ra bằng miệng m&iacute;m m&ocirc;i trong khi đếm từ từ đến s&aacute;u.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thực h&agrave;nh thở bằng cơ ho&agrave;nh nắm ngửa cho đến 10 - 15 hơi thở li&ecirc;n tiếp m&agrave; kh&ocirc;ng mệt mỏi. Sau đ&oacute;, thực h&agrave;nh n&oacute; trong khi đang &uacute;p v&agrave; nghi&ecirc;ng. Tiến độ để l&agrave;m c&aacute;c b&agrave;i tập trong khi ngồi thẳng tr&ecirc;n ghế, đứng l&ecirc;n, đi bộ v&agrave; cuối c&ugrave;ng leo cầu thang. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thở m&iacute;m m&ocirc;i</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&atilde;y thử b&agrave;i tập h&iacute;t thở bằng cơ ho&agrave;nh với m&iacute;m m&ocirc;i khi thở ra. H&iacute;t s&acirc;u qua miệng thở ra. Lặp lại 10 lần mỗi kỳ. Thở ra đối với m&ocirc;i m&iacute;m l&agrave;m tăng &aacute;p suất kh&ocirc;ng kh&iacute; b&ecirc;n trong đường h&ocirc; hấp, bao gồm cả đường h&ocirc; hấp rất nhỏ, giảm thiểu sụp đổ đường dẫn kh&iacute;. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">H&iacute;t thở s&acirc;u</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong khi ngồi hoặc đứng, k&eacute;o khuỷu tay chắc chắn khi h&iacute;t s&acirc;u. Giữ hơi thở trong với ngực cong đến đến năm v&agrave; sau đ&oacute; thở ra bằng c&aacute;ch hợp đồng cơ bụng v&agrave; để khuỷu tay<span style=\"\">&nbsp; </span>trở về vị tr&iacute; bắt đầu. Lại b&agrave;i tập 10 lần. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">C&aacute;c bước c&oacute; thể </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; kh&iacute; phế thũng, c&oacute; thể mất một số bước để ngăn chặn sự tiến triển của n&oacute; v&agrave; để bảo vệ m&igrave;nh khỏi c&aacute;c biến chứng: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ngưng h&uacute;t thuốc l&aacute;.</span></b><span style=\"font-family: Arial;\"> Đ&acirc;y l&agrave; biện ph&aacute;p quan trọng nhất c&oacute; thể gi&uacute;p sức khỏe tổng thể v&agrave; một trong những biện ph&aacute;p c&oacute; thể ngăn chặn sự tiến triển của bệnh kh&iacute; thũng. Tham gia một chương tr&igrave;nh cai thuốc nếu cần trợ gi&uacute;p bỏ h&uacute;t thuốc. C&agrave;ng nhiều c&agrave;ng tốt tr&aacute;nh kh&oacute;i thuốc. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tr&aacute;nh c&aacute;c chất k&iacute;ch th&iacute;ch đường h&ocirc; hấp kh&aacute;c.</span></b><span style=\"font-family: Arial;\"> Ch&uacute;ng bao gồm sơn v&agrave; kh&oacute;i từ ống xả xe hơi, một số m&ugrave;i nấu ăn, nước hoa n&agrave;o đ&oacute;, thậm ch&iacute; đốt nến v&agrave; hương trầm. Thay đổi l&ograve; sưởi v&agrave; điều h&ograve;a kh&ocirc;ng kh&iacute; c&aacute;c bộ lọc thường xuy&ecirc;n để hạn chế &ocirc; nhiễm.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tập thể dục thường xuy&ecirc;n.</span></b><span style=\"font-family: Arial;\"> Cố gắng kh&ocirc;ng để cho vấn đề thở ảnh hưởng tập thể dục thường xuy&ecirc;n, trong đ&oacute; c&oacute; thể tăng dung lượng phổi đ&aacute;ng kể.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Th&ocirc;ng tho&aacute;ng đường h&ocirc; hấp.</span></b><span style=\"font-family: Arial;\"> Nếu c&oacute; vi&ecirc;m phế quản m&atilde;n t&iacute;nh, chất nhờn c&oacute; xu hướng thu thập trong đường dẫn kh&iacute; v&agrave; c&oacute; thể kh&oacute; ho khạc. Để giữ cho chất tiết lo&atilde;ng v&agrave; dễ d&agrave;ng để tho&aacute;t, uống nhiều chất lỏng kh&ocirc;ng cồn mỗi ng&agrave;y.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tự bảo vệ m&igrave;nh khỏi kh&ocirc;ng kh&iacute; lạnh.</span></b><span style=\"font-family: Arial;\"> Kh&ocirc;ng kh&iacute; lạnh c&oacute; thể g&acirc;y ra co thắt c&aacute;c đoạn phế quản, thậm ch&iacute; l&agrave;m cho n&oacute; kh&oacute; khăn hơn để thở. Khi thời tiết lạnh, mặc một chiếc khăn mềm hoặc khẩu trang c&oacute; sẵn từ nh&agrave; thuốc - miệng v&agrave; mũi trước khi đi ra ngo&agrave;i để l&agrave;m ấm kh&ocirc;ng kh&iacute; v&agrave;o phổi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tr&aacute;nh nhiễm tr&ugrave;ng đường h&ocirc; hấp.</span></b><span style=\"font-family: Arial;\"> Được chủng ngừa vi&ecirc;m phổi như lời của b&aacute;c sĩ. Cũng nhận được chủng ngừa c&uacute;m h&agrave;ng năm. L&agrave;m tốt nhất<span style=\"\">&nbsp; </span>để tr&aacute;nh tiếp x&uacute;c trực tiếp với những người bị cảm lạnh hoặc c&uacute;m. Nếu phải h&ograve;a nhập với c&aacute;c nh&oacute;m lớn của người trong m&ugrave;a lạnh v&agrave; c&uacute;m, đeo khẩu trang, rửa tay thường xuy&ecirc;n v&agrave; mang theo một lọ thuốc nhỏ rửa tay trong t&uacute;i hoặc v&iacute;. Cố gắng tr&aacute;nh chạm v&agrave;o b&ecirc;n trong mũi hoặc dụi mắt, c&aacute;ch l&acirc;y nhiễm virus nhất.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Duy tr&igrave; tốt chế độ dinh dưỡng.</span></b><span style=\"font-family: Arial;\"> C&acirc;n bằng chế độ ăn uống cho cơ thể c&aacute;c chất dinh dưỡng cần thiết cho năng lượng, x&acirc;y dựng v&agrave; duy tr&igrave; c&aacute;c tế b&agrave;o v&agrave; cho điều chỉnh c&aacute;c qu&aacute; tr&igrave;nh cơ thể. C&ocirc;ng t&aacute;c hướng tới v&agrave; duy tr&igrave; một trọng lượng cơ thể mong muốn. Cơ thể thừa c&acirc;n cần nhiều &ocirc;xy hơn v&agrave; c&oacute; thể can thiệp v&agrave;o hơi thở. Nếu đang thiếu c&acirc;n, đạt trọng lượng khỏe mạnh c&oacute; thể l&agrave;m tăng sức mạnh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi ăn, c&oacute; thể cần ăn bữa ăn nhỏ thường xuy&ecirc;n hơn. Ăn bữa ăn lớn trong ng&agrave;y v&agrave; tr&aacute;nh nằm xuống sau bữa ăn. Ăn thức ăn mềm, dễ ti&ecirc;u h&oacute;a, chẳng hạn như sữa chua, gạo, khoai t&acirc;y nướng, g&agrave; kho hay c&aacute; c&oacute; thể dễ chịu hơn so với thịt đỏ v&agrave; c&aacute;c m&oacute;n ăn nặng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Đối ph&oacute; v&agrave; hỗ trợ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những đề nghị n&agrave;y c&oacute; thể gi&uacute;p đối ph&oacute; với kh&iacute; phế thũng: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">B&agrave;y tỏ cảm x&uacute;c</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&iacute; phế thũng c&oacute; thể giới hạn một số hoạt động v&agrave; ảnh hưởng đến kế hoạch v&agrave; th&oacute;i quen của gia đ&igrave;nh theo những c&aacute;ch c&oacute; thể kh&ocirc;ng phải lu&ocirc;n lu&ocirc;n dự đo&aacute;n. Nếu bản th&acirc;n v&agrave; gia đ&igrave;nh c&oacute; thể n&oacute;i chuyện cởi mở về những nhu cầu của nhau, sẽ tốt hơn c&oacute; thể đ&aacute;p ứng c&aacute;c th&aacute;ch thức về sống chung với bệnh n&agrave;y. H&atilde;y cảnh gi&aacute;c với những thay đổi trong t&acirc;m trạng v&agrave; c&aacute;c mối quan hệ với người kh&aacute;c v&agrave; kh&ocirc;ng ngại t&igrave;m kiếm sự tư vấn. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Xem x&eacute;t một nh&oacute;m hỗ trợ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Cũng c&oacute; thể xem x&eacute;t tham gia một nh&oacute;m hỗ trợ cho những người bị bệnh kh&iacute; thũng. Mặc d&ugrave; c&aacute;c nh&oacute;m hỗ trợ kh&ocirc;ng phải cho tất cả mọi người, họ c&oacute; thể l&agrave; một nguồn cung cấp th&ocirc;ng tin về phương ph&aacute;p điều trị mới v&agrave; chiến lược đối ph&oacute;. V&agrave; n&oacute; c&oacute; thể được khuyến kh&iacute;ch để d&agrave;nh thời gian với những người kh&aacute;c trong ho&agrave;n cảnh tương tự. Nếu đang quan t&acirc;m đến một nh&oacute;m hỗ trợ, n&oacute;i chuyện với b&aacute;c sĩ. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Ph&ograve;ng chống</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hầu hết c&aacute;c trường hợp c&oacute; thể ngăn ngừa kh&iacute; phế thũng. Để ngăn ngừa bệnh kh&iacute; thũng: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng h&uacute;t thuốc. Nếu h&uacute;t thuốc, t&igrave;m sự gi&uacute;p đỡ để bỏ thuốc l&aacute; c&agrave;ng sớm c&agrave;ng tốt.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tr&aacute;nh tiếp x&uacute;c với kh&oacute;i thuốc.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đeo khẩu trang để bảo vệ phổi nếu l&agrave;m việc với h&oacute;a chất, bụi kh&oacute;i.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tăng hoạt động thể chất hoặc tập thể dục thường xuy&ecirc;n nếu đ&atilde; h&uacute;t thuốc trong qu&aacute; khứ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: right;\"><b style=\"\"><i style=\"\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(128, 0, 0);\"></span></span></i></b></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">&nbsp;</span></p></span>\n', 1);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(9, 'Ngưng thở khi ngủ trung ương', '\n   <h1 class=\"title\">Ngưng thở khi ngủ trung ương</h1>   <span class=\"des\"> Ngưng thở khi ngủ trung ương xảy ra khi não không truyền tín hiệu đến các cơ hô hấp. Ngưng thở khi ngủ trung ương có thể được gây ra bởi một số điều kiện ảnh hưởng đến khả năng của thân não. </span>\n   <span><p style=\"text-align: justify; \"><span style=\"font-family: Arial; font-size: 18pt; line-height: 150%; \">Định nghĩa</span></p>\r\n<p style=\"margin-top:6.0pt;mso-margin-bottom-alt:auto;line-height:150%\"><span style=\"font-family:Arial\">Ngưng thở khi ngủ l&agrave; một rối loạn trong đ&oacute; thở li&ecirc;n tục dừng lại v&agrave; bắt đầu trong khi ngủ. Ngưng thở khi ngủ trung ương xảy ra v&igrave; n&atilde;o kh&ocirc;ng gửi t&iacute;n hiệu th&iacute;ch hợp đến c&aacute;c cơ kiểm so&aacute;t hơi thở - kh&ocirc;ng giống như ngưng thở khi ngủ do tắc nghẽn, trong đ&oacute;, thở kh&ocirc;ng thể b&igrave;nh thường do tắc nghẽn đường h&ocirc; hấp tr&ecirc;n. Ngưng thở khi ngủ trung ương &iacute;t phổ biến hơn, chiếm &iacute;t hơn 5% c&aacute;c trường hợp ngưng thở khi ngủ.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Ngưng thở khi ngủ trung ương c&oacute; thể xảy ra như l&agrave; kết quả của c&aacute;c điều kiện kh&aacute;c, chẳng hạn như suy tim v&agrave; đột quỵ. Ngủ ở một độ cao cũng c&oacute; thể g&acirc;y ngưng thở khi ngủ trung ương.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Phương ph&aacute;p điều trị ngưng thở khi ngủ trung ương c&oacute; thể li&ecirc;n quan đến việc giải quyết điều kiện tạo n&ecirc;n, bằng c&aacute;ch sử dụng một thiết bị hỗ trợ thở hoặc bổ sung oxy. <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-size:18.0pt;line-height:150%;font-family:Arial\">C&aacute;c triệu chứng<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">C&aacute;c dấu hiệu thường gặp v&agrave; c&aacute;c triệu chứng ngưng thở khi ngủ trung ương bao gồm: <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Ngừng thở hoặc c&aacute;c mẫu hơi thở bất thường trong khi ngủ. <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Thức giấc đột ngột k&egrave;m theo kh&oacute; thở. <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Kh&oacute; thở m&agrave; thuy&ecirc;n giảm bằng c&aacute;ch ngồi l&ecirc;n. <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Mất ngủ.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Buồn ngủ qu&aacute; mức v&agrave;o ban ng&agrave;y (hypersomnia).<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Kh&oacute; tập trung. <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Đau đầu buổi s&aacute;ng.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Ng&aacute;y. <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Mặc d&ugrave; chứng ng&aacute;y cho thấy mức độ tắc nghẽn l&agrave;m tăng luồng kh&ocirc;ng kh&iacute;, ng&aacute;y ngủ cũng c&oacute; thể được nghe thấy trong sự hiện diện của ngưng thở khi ngủ trung ương. Tuy nhi&ecirc;n, ng&aacute;y ngủ c&oacute; thể kh&ocirc;ng nổi bật với ngưng thở khi ngủ trung ương v&igrave; n&oacute; l&agrave; ngừng thở khi ngủ.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Hỏi &yacute; kiến một chuy&ecirc;n gia y tế nếu trải nghiệm, hoặc nếu đối t&aacute;c quan s&aacute;t, bất kỳ dấu hiệu hoặc triệu chứng ngưng thở khi ngủ trung ương, đặc biệt sau đ&acirc;y:<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Kh&oacute; thở đ&aacute;nh thức giấc ngủ. <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Li&ecirc;n tục tạm dừng hơi thở trong khi ngủ. <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Kh&oacute; ngủ.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Buồn ngủ ban ng&agrave;y qu&aacute; nhiều, c&oacute; thể khiến rơi v&agrave;o giấc ngủ trong khi đang l&agrave;m việc, xem truyền h&igrave;nh hoặc thậm ch&iacute; l&aacute;i xe. <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">H&atilde;y hỏi b&aacute;c sĩ về bất kỳ vấn đề về giấc ngủ l&agrave;m mệt mỏi kinh ni&ecirc;n, buồn ngủ v&agrave; c&aacute;u kỉnh. Buồn ngủ ban ng&agrave;y qu&aacute; mức (hypersomnia) c&oacute; thể l&agrave; do rối loạn kh&aacute;c, chẳng hạn như chứng ngủ rũ hoặc ngưng thở khi ngủ tắc nghẽn.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-size:18.0pt;line-height:150%;font-family:Arial\">Nguy&ecirc;n nh&acirc;n<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Ngưng thở khi ngủ trung ương xảy ra khi n&atilde;o kh&ocirc;ng truyền t&iacute;n hiệu đến c&aacute;c cơ h&ocirc; hấp. Ngưng thở khi ngủ trung ương c&oacute; thể được g&acirc;y ra bởi một số điều kiện ảnh hưởng đến khả năng của th&acirc;n n&atilde;o - li&ecirc;n kết bộ n&atilde;o v&agrave;o cột sống v&agrave; điều khiển nhiều chức năng như nhịp tim v&agrave; hơi thở để kiểm so&aacute;t hơi thở. Nguy&ecirc;n nh&acirc;n kh&aacute;c nhau với c&aacute;c loại h&igrave;nh ngưng thở khi ngủ trung ương. C&aacute;c loại bao gồm:</span><span style=\"font-size:18.0pt;line-height:150%;\r\nfont-family:Arial\"><o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">V&ocirc; căn.</span></b><span style=\"font-family:Arial\"> Nguy&ecirc;n nh&acirc;n của chứng ngưng thở khi ngủ trung ương kh&ocirc;ng r&otilde;. <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Cheyne - Stokes.</span></b><span style=\"font-family:Arial\"> Đ&acirc;y l&agrave; loại ngưng thở khi ngủ trung ương thường kết hợp với suy tim sung huyết hoặc đột quỵ v&agrave; đặc trưng bởi chu kỳ tăng nhịp điệu dần dần, v&agrave; sau đ&oacute; giảm thở. Trong nỗ lực thở yếu nhất, thiếu luồng kh&ocirc;ng kh&iacute; (ngưng thở khi ngủ trung ương) c&oacute; thể xảy ra.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">T&igrave;nh trạng y tế g&acirc;y ra t&igrave;nh trạng ngưng thở khi ngủ.</span></b><span style=\"font-family:\r\nArial\"> Ngo&agrave;i suy tim sung huyết v&agrave; đột quỵ, một số điều kiện y tế c&oacute; thể dẫn đến ngưng thở khi ngủ trung ương. Thiệt hại bất kỳ th&acirc;n n&atilde;o kiểm so&aacute;t thở - c&oacute; thể l&agrave;m giảm qu&aacute; tr&igrave;nh h&ocirc; hấp b&igrave;nh thường.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Thuốc g&acirc;y ra ngưng thở.</span></b><span style=\"font-family:Arial\"> Thuốc nhất định như thuốc phiện, morphine, oxycodone hoặc codeine c&oacute; thể g&acirc;y ra thở kh&ocirc;ng đều, tăng, giảm trong một m&ocirc; h&igrave;nh thường xuy&ecirc;n, hoặc ngừng ho&agrave;n to&agrave;n.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Độ cao.</span></b><span style=\"font-family:Arial\"> Cheyne - Stokes c&oacute; thể xảy ra nếu tiếp x&uacute;c độ cao đủ cao, chẳng hạn như độ cao lớn hơn 15.000 feet (khoảng 4.500 m&eacute;t). Sự thay đổi kh&iacute; oxy trong độ cao l&agrave; l&yacute; do thở nhanh (tăng th&ocirc;ng kh&iacute;) v&agrave; kh&oacute; thở.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Ngưng thở khi ngủ phức tạp.</span></b><span style=\"font-family:Arial\"> Một số người bị ngưng thở khi ngủ tắc nghẽn ph&aacute;t triển ngưng thở khi ngủ trung ương trong khi điều trị với &aacute;p suất đường thở dương li&ecirc;n tục (CPAP). Điều n&agrave;y được gọi l&agrave; ngưng thở khi ngủ phức tạp bởi v&igrave; n&oacute; l&agrave; một sự kết hợp của ngưng thở khi ngủ tắc nghẽn v&agrave; trung ương.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-size:18.0pt;line-height:150%;font-family:Arial\">C&aacute;c yếu tố nguy cơ<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Yếu tố tăng nguy cơ ngưng thở khi ngủ trung t&acirc;m: <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Giới t&iacute;nh.</span></b><span style=\"font-family:Arial\"> Con trai nhiều khả năng ph&aacute;t triển ngưng thở khi ngủ trung ương hơn l&agrave; nữ.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Tuổi.</span></b><span style=\"font-family:Arial\"> Ngưng thở khi ngủ trung ương phổ biến hơn ở người lớn tuổi, c&oacute; thể bởi v&igrave; c&ugrave;ng tồn tại điều kiện y tế hoặc c&aacute;c m&ocirc; h&igrave;nh giấc ng��� c&oacute; thể g&acirc;y ra ngưng thở khi ngủ trung ương.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Rối loạn tim.</span></b><span style=\"font-family:Arial\"> Những người bị rung nhĩ hoặc suy tim sung huyết c&oacute; nguy cơ ngưng thở khi ngủ trung ương. Ngưng thở khi ngủ trung ương c&oacute; thể c&oacute; mặt l&ecirc;n đến 40% những người bị suy tim sung huyết.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Tai biến mạch m&aacute;u n&atilde;o hoặc u n&atilde;o.</span></b><span style=\"font-family:Arial\"> C&aacute;c điều kiện n&agrave;y c&oacute; thể l&agrave;m giảm khả năng của n&atilde;o điều chỉnh hơi thở.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Độ cao.</span></b><span style=\"font-family:Arial\"> Ngủ ở độ cao chưa quen c&oacute; thể l&agrave;m tăng nguy cơ ngưng thở khi ngủ. Ngưng thở khi ngủ độ cao kh&ocirc;ng c&ograve;n l&agrave; một vấn đề khi quay trở lại với độ cao thấp hơn.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Opioid.</span></b><span style=\"font-family:Arial\"> Sử dụng opioid như morphine, oxycodone v&agrave; codein, l&agrave;m tăng nguy cơ ngưng thở khi ngủ trung ương.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">CPAP.</span></b><span style=\"font-family:Arial\"> Một số người bị ngưng thở khi ngủ tắc nghẽn ph&aacute;t triển ngưng thở khi ngủ trung ương trong khi điều trị bằng CPAP (thở &aacute;p lực dương li&ecirc;n tục). Điều n&agrave;y được gọi l&agrave; ngưng thở khi ngủ phức tạp bởi v&igrave; n&oacute; l&agrave; sự kết hợp của ngưng thở khi ngủ tắc nghẽn v&agrave; trung ương. Đối với một số người, ngưng thở khi ngủ phức tạp tiếp tục sử dụng CPAP. Những người kh&aacute;c c&oacute; thể được điều trị bằng thở &aacute;p lực dương (PAP).<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-size:18.0pt;line-height:150%;font-family:Arial\">C&aacute;c biến chứng<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Ngưng thở khi ngủ trung ương l&agrave; một điều kiện y tế nghi&ecirc;m trọng. Một số biến chứng bao gồm:<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Mệt mỏi.</span></b><span style=\"font-family:Arial\"> Thức giấc lặp đi lặp lại li&ecirc;n quan với chứng ngưng thở khi ngủ l&agrave;m cho kh&ocirc;ng thể phục hồi từ giấc ngủ b&igrave;nh thường. Những người ngưng thở khi ngủ trung ương thường trải nghiệm buồn ngủ ban ng&agrave;y nghi&ecirc;m trọng, mệt mỏi v&agrave; kh&oacute; chịu. C&oacute; thể gặp kh&oacute; trong việc tập trung v&agrave; rơi v&agrave;o giấc ngủ tại nơi l&agrave;m việc, trong khi xem TV hay ngay cả khi l&aacute;i xe.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Vấn đề tim mạch.</span></b><span style=\"font-family:Arial\"> Giảm đột ngột nồng độ &ocirc;xy trong m&aacute;u xảy ra trong thời gian ngưng thở khi ngủ trung ương c&oacute; thể ảnh hưởng đến sức khỏe tim mạch. Nếu c&oacute; bệnh tim cơ bản, lặp đi lặp lại nhiều lần oxy trong m&aacute;u thấp (t&igrave;nh trạng thiếu oxy hoặc thiếu oxy) l&agrave;m xấu đi ti&ecirc;n lượng v&agrave; tăng nguy cơ nhịp tim bất thường.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-size:18.0pt;line-height:150%;font-family:Arial\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">B&aacute;c sĩ c&oacute; thể thực hiện đ&aacute;nh gi&aacute; dựa tr&ecirc;n c&aacute;c dấu hiệu v&agrave; triệu chứng hoặc c&oacute; thể giới thiệu đến một trung t&acirc;m rối loạn giấc ngủ. Ở đ&oacute;, chuy&ecirc;n gia về giấc ngủ c&oacute; thể gi&uacute;p quyết định về nhu cầu đ&aacute;nh gi&aacute; th&ecirc;m. Đ&aacute;nh gi&aacute; như vậy thường li&ecirc;n quan đến việc gi&aacute;m s&aacute;t hơi thở qua đ&ecirc;m v&agrave; c&aacute;c chức năng cơ thể kh&aacute;c trong khi ngủ bằng polysomnography.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Trong thử nghiệm polysomnography, kết nối với thiết bị gi&aacute;m s&aacute;t tim, phổi v&agrave; hoạt động của n&atilde;o bộ, h&iacute;t thở c&aacute;c m&ocirc; h&igrave;nh, c&aacute;nh tay v&agrave; di chuyển ch&acirc;n, nồng độ oxy trong m&aacute;u trong khi ngủ.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Đ&aacute;nh gi&aacute; của một chuy&ecirc;n gia tim (b&aacute;c sĩ tim mạch) hoặc một b&aacute;c sĩ chuy&ecirc;n về hệ thống thần kinh (thần kinh học) cũng c&oacute; thể l&agrave; cần thiết để t&igrave;m nguy&ecirc;n nh&acirc;n của chứng ngưng thở khi ngủ trung ương. <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-size:18.0pt;line-height:150%;font-family:Arial\">Phương ph&aacute;p điều trị v&agrave; thuốc<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Phương ph&aacute;p điều trị ngưng thở khi ngủ trung ương c&oacute; thể bao gồm: <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Giải quyết c&aacute;c vấn đề li&ecirc;n quan đến y tế.</span></b><span style=\"font-family:Arial\"> Nguy&ecirc;n nh&acirc;n c&oacute; thể ngưng thở khi ngủ trung ương bao gồm c&aacute;c rối loạn kh&aacute;c, v&agrave; điều trị những điều kiện c&oacute; thể gi&uacute;p ngưng thở khi ngủ trung ương. V&iacute; dụ, điều trị th&iacute;ch hợp suy tim c&oacute; thể loại bỏ ngưng thở khi ngủ trung ương.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Giảm c&aacute;c thuốc opioid.</span></b><span style=\"font-family:Arial\"> Nếu thuốc opioid g&acirc;y ngưng thở khi ngủ trung ương, b&aacute;c sĩ c&oacute; thể dần dần giảm liều của những thuốc n&agrave;y.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">&Aacute;p suất đường thở dương li&ecirc;n tục (CPAP).</span></b><span style=\"font-family:Arial\"> Phương ph&aacute;p n&agrave;y, cũng được sử dụng để điều trị ngưng thở khi ngủ do tắc nghẽn, li&ecirc;n quan đến việc đeo mast tr&ecirc;n mũi trong khi ngủ. Mặt nạ n&agrave;y được gắn v&agrave;o m&aacute;y bơm nhỏ cung cấp kh&iacute; n&eacute;n để giữ mở đường h&ocirc; hấp tr&ecirc;n. CPAP c&oacute; thể ngăn chặn việc đ&oacute;ng đường h&ocirc; hấp c&oacute; thể g&acirc;y ngưng thở khi ngủ trung ương. Cũng như ngừng thở khi ngủ, điều quan trọng l&agrave; sử dụng thiết bị duy nhất theo chỉ dẫn. Nếu mặt nạ kh&oacute; chịu hoặc cảm thấy &aacute;p lực qu&aacute; mạnh, n&oacute;i chuyện với b&aacute;c sĩ để điều chỉnh được thực hiện.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Thở &aacute;p lực dương (BPAP).</span></b><span style=\"font-family:Arial\"> Kh&ocirc;ng giống như CPAP, nguồn cung cấp &aacute;p lực ổn định li&ecirc;n tục v&agrave;o đường h&ocirc; hấp tr&ecirc;n khi thở v&agrave;o v&agrave; thở ra, BPAP x&acirc;y dựng &aacute;p suất cao hơn khi h&iacute;t v&agrave;o v&agrave; giảm &aacute;p suất thấp hơn khi thở ra. Mục ti&ecirc;u của điều trị n&agrave;y l&agrave; để th&uacute;c đẩy m&ocirc; h&igrave;nh thở yếu của ngưng thở khi ngủ trung ương. Một số thiết bị BPAP c&oacute; thể được thiết lập để tự động mang một hơi thở nếu thiết bị ph&aacute;t hiện kh&ocirc;ng lấy một hơi thở sau khi một số nhất định của gi&acirc;y.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Hỗ trợ th&ocirc;ng kh&iacute; (ASV).</span></b><span style=\"font-family:Arial\"> Một số nghi&ecirc;n cứu đ&atilde; cho thấy thiết bị luồng kh&iacute; n&agrave;y c&oacute; hiệu quả hơn CPAP hoặc BPAP để điều trị ngưng thở khi ngủ trung ương. ASV được thiết kế để điều trị ngưng thở khi ngủ trung ương v&agrave; ngưng thở khi ngủ phức tạp bằng c&aacute;ch theo d&otilde;i m&ocirc; h&igrave;nh thở b&igrave;nh thường v&agrave; lưu trữ c&aacute;c th&ocirc;ng tin trong m&aacute;y t&iacute;nh được x&acirc;y dựng. Sau khi rơi v&agrave;o giấc ngủ, m&aacute;y sử dụng &aacute;p lực để điều chỉnh m&ocirc; h&igrave;nh thở v&agrave; ngăn chặn tạm dừng thở.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Bổ sung oxy.</span></b><span style=\"font-family:Arial\"> Sử dụng oxy bổ sung trong khi ngủ c&oacute; thể gi&uacute;p đỡ nếu c&oacute; ngưng thở khi ngủ trung ương. C&aacute;c h&igrave;nh thức kh&aacute;c nhau của oxy c&oacute; sẵn cũng như c&aacute;c thiết bị kh&aacute;c nhau để cung cấp oxy cho phổi.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Thuốc.</span></b><span style=\"font-family:Arial\"> Một số loại thuốc đ&atilde; được sử dụng để k&iacute;ch th&iacute;ch h&ocirc; hấp ở những người bị ngưng thở khi ngủ trung ương. V&iacute; dụ, một số b&aacute;c sĩ k&ecirc; toa acetazolamide để ngăn chặn ngưng thở khi ngủ trung ương ở độ cao.</span><span lang=\"EN-US\" style=\"font-family:Arial;mso-ansi-language:EN-US\"><o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: right; \"><b><i><span lang=\"EN-US\" style=\"font-family:Arial;mso-ansi-language:EN-US\"><span style=\"color: rgb(128, 0, 0); \"></span><o:p></o:p></span></i></b></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">&nbsp;</span></p></span>\n', 1);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(10, 'Ngưng thở tắc nghẽn khi ngủ', '\n   <h1 class=\"title\">Ngưng thở tắc nghẽn khi ngủ</h1>   <span class=\"des\">Điều trị tắc nghẽn ngưng thở khi ngủ có thể liên quan đến cách sử dụng một thiết bị để giữ cho đường thở mở hoặc trải qua một thủ tục để loại bỏ các tế bào từ miệng, mũi hay cổ họng.</span>\n   <span><p style=\"text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Tắc nghẽn ngưng thở khi ngủ l&agrave; rối loạn giấc ngủ nghi&ecirc;m trọng, trong đ&oacute; thở nhiều lần dừng lại v&agrave; bắt đầu trong khi ngủ. Một v&agrave;i loại chứng ngưng thở khi ngủ tồn tại, nhưng loại phổ biến nhất l&agrave; ngưng thở khi ngủ tắc nghẽn, xảy ra khi cơ cổ họng li&ecirc;n tục thư gi&atilde;n v&agrave; ngăn chặn đường thở trong khi ngủ. Dấu hiệu đ&aacute;ng ch&uacute; &yacute; nhất của ngưng thở khi ngủ tắc nghẽn l&agrave; ng&aacute;y, mặc d&ugrave; kh&ocirc;ng phải tất cả những người đ&atilde; ng&aacute;y ngưng thở khi ngủ tắc nghẽn.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Bất cứ ai cũng c&oacute; thể ph&aacute;t triển ngưng thở khi ngủ tắc nghẽn, mặc d&ugrave; n&oacute; thường ảnh hưởng đến người lớn tuổi. N&oacute; cũng đặc biệt phổ biến ở những người thừa c&acirc;n. Điều trị tắc nghẽn ngưng thở khi ngủ c&oacute; thể li&ecirc;n quan đến c&aacute;ch sử dụng một thiết bị để giữ cho đường thở mở hoặc trải qua một thủ tục để loại bỏ c&aacute;c tế b&agrave;o từ miệng, mũi hay cổ họng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">C&aacute;c dấu hiệu v&agrave; triệu chứng ngưng thở khi ngủ tắc nghẽn bao gồm: </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Buồn ngủ qu&aacute; mức v&agrave;o ban ng&agrave;y (hypersomnia).</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Tiếng ng&aacute;y.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Quan s&aacute;t tập ngừng thở trong khi ngủ.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Thức giấc đột ngột k&egrave;m theo kh&oacute; thở.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Tỉnh thức với kh&ocirc; miệng hoặc đau họng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Đau đầu buổi s&aacute;ng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Thường xuy&ecirc;n đi tiểu v&agrave;o ban đ&ecirc;m.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Mất ngủ.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Đến gặp b&aacute;c sĩ khi</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Tham khảo &yacute; kiến chuy&ecirc;n gia y tế nếu c&oacute; kinh nghiệm, hoặc nếu đối t&aacute;c quan s&aacute;t, sau đ&acirc;y: </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Ng&aacute;y to, đủ để l&agrave;m phiền giấc ngủ hay của người kh&aacute;c.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Kh&oacute; thở đ&aacute;nh thức giấc ngủ.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Li&ecirc;n tục tạm dừng hơi thở trong khi ngủ.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Buồn ngủ qu&aacute; mức v&agrave;o ban ng&agrave;y, c&oacute; thể khiến rơi v&agrave;o giấc ngủ, trong khi đang l&agrave;m việc, xem truyền h&igrave;nh hoặc thậm ch&iacute; l&aacute;i xe.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Nhiều người kh&ocirc;ng nghĩ đến ng&aacute;y như l&agrave; một dấu hiệu của c&aacute;i g&igrave; đ&oacute; nghi&ecirc;m trọng, v&agrave; tất cả mọi người kh&ocirc;ng phải người đ&atilde; ngưng thở khi ngủ ng&aacute;y. Nhưng h&atilde;y chắc chắn để n&oacute;i chuyện với b&aacute;c sĩ nếu c&oacute; trải nghiệm tiếng ng&aacute;y, đặc biệt ng&aacute;y l&agrave; khoảng thời gian ngắt qu&atilde;ng bởi sự im lặng. Với ngưng thở khi ngủ, ng&aacute;y thường l&agrave; to nhất khi ngủ nằm ngửa, v&agrave; n&oacute; y&ecirc;n lặng khi quay nghi&ecirc;ng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">H&atilde;y hỏi b&aacute;c sĩ về bất kỳ vấn đề giấc ngủ kinh ni&ecirc;n mệt mỏi, buồn ngủ v&agrave; c&aacute;u kỉnh. Buồn ngủ qu&aacute; mức v&agrave;o ban ng&agrave;y (hypersomnia) c&oacute; thể l&agrave; do rối loạn kh&aacute;c, chẳng hạn như chứng ngủ rũ.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Ngưng thở khi ngủ tắc nghẽn xảy ra khi c&aacute;c cơ ở mặt sau của cổ họng thư gi&atilde;n qu&aacute; nhiều để cho ph&eacute;p thở b&igrave;nh thường. Những cơ bắp hỗ trợ c&aacute;c cấu tr&uacute;c bao gồm c&aacute;c v&ograve;m miệng, kh&iacute; quản - một mảnh m&ocirc; h&igrave;nh tam gi&aacute;c treo từ v&ograve;m miệng, c&aacute;c amiđan v&agrave; lưỡi.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Khi c&aacute;c cơ thư gi&atilde;n, thu hẹp đường thở hoặc đ&oacute;ng khi h&iacute;t v&agrave;o v&agrave; thở c&oacute; thể kh&ocirc;ng đủ cho 10-20 gi&acirc;y. Điều n&agrave;y c&oacute; thể mức độ oxy trong m&aacute;u thấp hơn. Gi&aacute;c quan n&atilde;o n&agrave;y kh&ocirc;ng c&oacute; khả năng thở v&agrave; đ&aacute;nh thức thời gian ngắn từ giấc ngủ để c&oacute; thể mở lại đường th&ocirc;ng kh&iacute;. Điều n&agrave;y thường thức tỉnh ngắn v&agrave; kh&ocirc;ng nhớ n&oacute;.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">C&oacute; thể đ&aacute;nh thức với một tho&aacute;ng kh&oacute; thở m&agrave; sửa chữa n&oacute; một c&aacute;ch nhanh ch&oacute;ng, trong v&ograve;ng một hoặc hai hơi thở s&acirc;u, mặc d&ugrave; tr&igrave;nh tự n&agrave;y l&agrave; hiếm. C&oacute; thể c&oacute; &acirc;m thanh h&iacute;t, nghẹt thở hay thở hổn hển. M&ocirc; h&igrave;nh n&agrave;y c&oacute; thể lặp lại ch&iacute;nh n&oacute; từ năm đến 30 lần hoặc hơn mỗi giờ, cả đ&ecirc;m. Những sự gi&aacute;n đoạn l&agrave;m giảm khả năng để đạt được mong muốn s&acirc;u, giai đoạn của giấc ngủ y&ecirc;n tĩnh, v&agrave; c&oacute; thể sẽ cảm thấy buồn ngủ trong giờ thức giấc.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Những người bị chứng ngưng thở khi ngủ c&oacute; thể kh&ocirc;ng biết rằng giấc ngủ của họ bị gi&aacute;n đoạn. Trong thực tế, nhiều người với loại h&igrave;nh n&agrave;y, ngưng thở khi ngủ nghĩ rằng họ ngủ ngon cả đ&ecirc;m.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Bất cứ ai cũng c&oacute; thể ph&aacute;t triển chứng ngưng thở khi ngủ. Tuy nhi&ecirc;n, c&aacute;c yếu tố nhất định c&oacute; nguy cơ cao:</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Vượt qu&aacute; trọng lượng.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Hơn một nửa số những người bị ngưng thở khi ngủ tắc nghẽn l&agrave; thừa c&acirc;n. Chất b&eacute;o tr&ecirc;n đường h&ocirc; hấp tr&ecirc;n c&oacute; thể cản trở hơi thở. Tuy nhi&ecirc;n, kh&ocirc;ng phải tất cả những người đ&atilde; ngưng thở khi ngủ l&agrave; thừa c&acirc;n v&agrave; ngược lại. Người gầy c&oacute; thể ph&aacute;t triển c&aacute;c rối loạn.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Hạ eo.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> K&iacute;ch thước của cổ c&oacute; thể cho thấy c&oacute; hay kh&ocirc;ng c&oacute; nguy cơ ngưng thở khi ngủ. Đ&oacute; l&agrave; bởi v&igrave; một c&aacute;i cổ d&agrave;y c&oacute; thể thu hẹp đường thở v&agrave; c&oacute; thể l&agrave; một dấu hiệu cho thấy trọng lượng dư thừa. Một chu vi v&ograve;ng cổ lớn hơn 17 inch (43 cm) đối với nam v&agrave; 15 inch (38 cm) cho phụ nữ được kết hợp với tăng nguy cơ ngưng thở khi ngủ tắc nghẽn.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Cao huyết &aacute;p.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Nghẽn ngưng thở khi ngủ tương đối phổ biến ở những người bị cao huyết &aacute;p.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Một đường th&ocirc;ng kh&iacute; thu hẹp.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> C&oacute; thể kế thừa thu hẹp cổ họng tự nhi&ecirc;n. Hoặc amidan v&ograve;m họng hoặc c&oacute; thể trở th&agrave;nh mở rộng, m&agrave; c&oacute; thể chặn đường thở.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Nghẹt mũi kinh ni&ecirc;n.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Nghẽn xảy ra ngưng thở khi ngủ thường xuy&ecirc;n ở những người c&oacute; ph&ugrave; hợp nghẹt mũi v&agrave;o ban đ&ecirc;m hai lần ở người nghẹt mũi kinh ni&ecirc;n, bất kể nguy&ecirc;n nh&acirc;n. Điều n&agrave;y c&oacute; thể l&agrave; do đường h&ocirc; hấp thu hẹp.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Bệnh tiểu đường.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Nghẽn ngưng thở khi ngủ l&agrave; ba lần nhiều hơn ở những người c&oacute; bệnh tiểu đường.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">L&agrave; nam giới.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Nh&igrave;n chung, đ&agrave;n &ocirc;ng gấp hai lần khả năng c&oacute; ngưng thở khi ngủ.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">L&agrave; m&agrave;u đen, gốc T&acirc;y Ban Nha hoặc một đảo Th&aacute;i B&igrave;nh Dương.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Trong số những người dưới 35 tuổi, ngưng thở khi ngủ tắc nghẽn phổ biến hơn ở người da đen, gốc T&acirc;y Ban Nha v&agrave; Th&aacute;i B&igrave;nh Dương.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Lớn tuổi.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Ngủ ngưng thở khi xảy ra 2-3 lần thường xuy&ecirc;n hơn ở người lớn tr&ecirc;n 65 tuổi.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Thời kỳ m&atilde;n kinh.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Một người phụ nữ xuất hiện nguy cơ tăng sau khi m&atilde;n kinh.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Lịch sử gia đ&igrave;nh ngưng thở khi ngủ.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Nếu c&oacute; c&aacute;c th&agrave;nh vi&ecirc;n gia đ&igrave;nh với ngưng thở khi ngủ, c&oacute; thể c&oacute; nguy cơ tăng l&ecirc;n.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Sử dụng rượu, thuốc an thần.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Những chất thư gi&atilde;n c&aacute;c cơ ở cổ họng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">H&uacute;t thuốc l&aacute;.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> H&uacute;t thuốc l&agrave; gần gấp ba lần nhiều khả năng c&oacute; cơn ngưng thở khi ngủ.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Ngưng thở khi ngủ được coi l&agrave; một điều kiện y tế nghi&ecirc;m trọng. C&aacute;c biến chứng c&oacute; thể bao gồm:</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">C&aacute;c vấn đề tim mạch.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Đột ngột giảm nồng độ &ocirc;xy trong m&aacute;u xảy ra trong qu&aacute; tr&igrave;nh tăng huyết &aacute;p ngưng thở khi ngủ v&agrave; căng thẳng hệ tim mạch. Khoảng một nửa số người bị chứng ngưng thở khi ngủ bị cao huyết &aacute;p, điều n&agrave;y l&agrave;m tăng nguy cơ suy tim v&agrave; đột quỵ. C&aacute;c tắc nghẽn nghi&ecirc;m trọng hơn giấc ngủ ngưng thở, c&agrave;ng c&oacute; nhiều nguy cơ huyết &aacute;p cao. Bệnh nh&acirc;n ngưng thở khi ngủ rất c&oacute; khả năng ph&aacute;t triển nhịp tim bất thường như rung t&acirc;m nhĩ. Nếu c&oacute; bệnh tim tiềm ẩn, những lặp đi lặp lại nhiều hồi của oxy trong m&aacute;u thấp (thiếu oxy) c&oacute; thể dẫn đến c&aacute;i chết bất ngờ từ một sự kiện tim.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Ban ng&agrave;y mệt mỏi.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> C&aacute;c thức giấc nhiều lần kết hợp với ngưng thở khi ngủ l&agrave;m b&igrave;nh thường, phục hồi giấc ngủ kh&ocirc;ng thể. Những người bị ngưng thở khi ngủ thường trải nghiệm nghi&ecirc;m trọng buồn ngủ ban ng&agrave;y, mệt mỏi v&agrave; kh&oacute; chịu. Họ c&oacute; thể c&oacute; kh&oacute; tập trung v&agrave; thấy m&igrave;nh rơi v&agrave;o giấc ngủ tại nơi l&agrave;m việc, trong khi xem TV hay ngay cả khi l&aacute;i xe. Trẻ em v&agrave; thanh thiếu ni&ecirc;n với ngưng thở khi ngủ c&oacute; thể k&eacute;m ở trường, l&agrave;m giảm sự ph&aacute;t triển t&acirc;m thần hoặc c&oacute; vấn đề về h&agrave;nh vi. Điều trị chứng ngưng thở khi ngủ c&oacute; thể cải thiện c&aacute;c triệu chứng n&agrave;y, kh&ocirc;i phục lại sự tỉnh t&aacute;o v&agrave; cải thiện chất lượng cuộc sống.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">C&aacute;c biến chứng với thuốc v&agrave; phẫu thuật.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Nghẽn ngưng thở khi ngủ cũng l&agrave; một mối quan t&acirc;m với một số thuốc v&agrave; g&acirc;y m&ecirc; to&agrave;n th&acirc;n. Những người bị chứng ngưng thở khi ngủ c&oacute; thể nhiều khả năng biến chứng sau phẫu thuật lớn bởi v&igrave; ch&uacute;ng dễ bị kh&oacute; thở, đặc biệt l&agrave; khi được g&acirc;y m&ecirc; v&agrave; nằm ngửa. Trước khi c&oacute; phẫu thuật, n&oacute;i cho b&aacute;c sĩ đ&atilde; ngưng thở khi ngủ. Kh&ocirc;ng được chẩn đo&aacute;n ngưng thở khi ngủ l&agrave; đặc biệt nguy hiểm trong t&igrave;nh huống n&agrave;y.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Mất ngủ đối t&aacute;c.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Ng&aacute;y c&oacute; thể giữ cho những người xung quanh nhận được từ nghỉ ngơi tốt v&agrave; cuối c&ugrave;ng ph&aacute; vỡ c&aacute;c mối quan hệ. N&oacute; kh&ocirc;ng phải l&agrave; kh&ocirc;ng phổ biến cho một đối t&aacute;c lựa chọn để ngủ trong một ph&ograve;ng kh&aacute;c. Nhiều đối t&aacute;c của những người ng&aacute;y l&agrave; mất ngủ.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Những người bị chứng ngưng thở khi ngủ tắc nghẽn cũng c&oacute; quyền khiếu nại c&aacute;c vấn đề về tr&iacute; nhớ, đau đầu buổi s&aacute;ng, thay đổi t&acirc;m trạng hay cảm x&uacute;c của bệnh trầm cảm, v&agrave; cần phải đi tiểu thường xuy&ecirc;n v&agrave;o ban đ&ecirc;m (nocturia). </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">B&aacute;c sĩ c&oacute; thể thực hiện đ&aacute;nh gi&aacute; dựa tr&ecirc;n c&aacute;c dấu hiệu v&agrave; triệu chứng hoặc c&oacute; thể giới thiệu đến một trung t&acirc;m rối loạn giấc ngủ. Chuy&ecirc;n gia giấc ngủ c&oacute; thể gi&uacute;p quyết định xem cần đ&aacute;nh gi&aacute; th&ecirc;m. Việc đ&aacute;nh gi&aacute; c&oacute; thể bao gồm gi&aacute;m s&aacute;t qua đ&ecirc;m của hơi thở v&agrave; chức năng cơ thể kh&aacute;c trong khi ngủ. X&eacute;t nghiệm ph&aacute;t hiện ngưng thở khi ngủ bao gồm:</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Polysomnography đ&ecirc;m.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Trong thời gian thử nghiệm n&agrave;y, nối với thiết bị m&agrave;n h&igrave;nh phổi tim, v&agrave; hoạt động của n&atilde;o, m&ocirc; h&igrave;nh thở, c&aacute;nh tay v&agrave; cử động ch&acirc;n, v&agrave; nồng độ &ocirc;xy trong m&aacute;u trong khi ngủ. Điều n&agrave;y c&oacute; thể gi&uacute;p b&aacute;c sĩ ra c&aacute;c điều kiện kh&aacute;c - chẳng hạn như chi định kỳ hay chứng ngủ rũ - cũng c&oacute; thể g&acirc;y buồn ngủ qu&aacute; mức v&agrave;o ban ng&agrave;y, nhưng cần điều trị kh&aacute;c nhau.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Phương ph&aacute;p đo oxy.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Điều n&agrave;y li&ecirc;n quan đến việc sử dụng phương ph&aacute;p s&agrave;ng lọc, một m&aacute;y nhỏ gi&aacute;m s&aacute;t v&agrave; hồ sơ cấp oxy trong m&aacute;u trong khi đang ngủ. Một tay &aacute;o đơn giản, ph&ugrave; hợp với kh&ocirc;ng đau đớn tr&ecirc;n một trong những ng&oacute;n tay để thu thập th&ocirc;ng tin qua đ&ecirc;m ở nh&agrave;. Nếu đ&atilde; ngưng thở khi ngủ, kết quả của thử nghiệm n&agrave;y thường sẽ chỉ giảm mức độ oxy trong m&aacute;u trong thời gian ngưng thở v&agrave; tăng tiếp theo với thức giấc. Nếu kết quả l&agrave; bất thường, b&aacute;c sĩ c&oacute; thể c&oacute; trải qua polysomnography để x&aacute;c định chẩn đo&aacute;n. Phương ph&aacute;p đo oxy kh&ocirc;ng ph&aacute;t hiện tất cả c&aacute;c trường hợp ngưng thở khi ngủ, do đ&oacute;, b&aacute;c sĩ vẫn c&oacute; thể đề nghị polysomnogram ngay cả khi c&aacute;c kết quả phương ph&aacute;p đo oxy l&agrave; b&igrave;nh thường.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Portable cardiorespiratory thử nghiệm.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Trong những ho&agrave;n cảnh nhất định, b&aacute;c sĩ c&oacute; thể cung cấp c&aacute;c b&agrave;i kiểm tra ở nh&agrave; để chẩn đo&aacute;n ngưng thở khi ngủ. C&aacute;c x&eacute;t nghiệm n&agrave;y thường li&ecirc;n quan đến phương ph&aacute;p đo oxy, đo lưu lượng kh&iacute; v&agrave; đo lường của c&aacute;c mẫu hơi thở.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">B&aacute;c sĩ cũng c&oacute; thể giới thiệu đến b&aacute;c sĩ mũi, tai v&agrave; họng (otolaryngologist) để loại trừ bất kỳ sự tắc nghẽn giải phẫu trong mũi hay cổ họng. </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Đối với trường hợp nhẹ hơn của chứng ngưng thở khi ngủ tắc nghẽn, b&aacute;c sĩ c&oacute; thể khuy&ecirc;n n&ecirc;n thay đổi lối sống, chẳng hạn như giảm trọng lượng hoặc bỏ h&uacute;t thuốc. Nếu những biện ph&aacute;p n&agrave;y kh&ocirc;ng cải thiện c&aacute;c dấu hiệu v&agrave; triệu chứng hoặc nếu ngưng thở trung b&igrave;nh đến nặng, một số phương ph&aacute;p điều trị kh&aacute;c c&oacute; sẵn. Một số thiết bị c&oacute; thể gi&uacute;p mở đường th&ocirc;ng kh&iacute; bị chặn. Trong trường hợp kh&aacute;c, phẫu thuật c&oacute; thể l&agrave; cần thiết.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">&Aacute;p lực t&iacute;ch cực đường thở.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Nếu c&oacute; ngưng thở khi ngủ trung b&igrave;nh đến nặng, c&oacute; thể hưởng lợi từ một m&aacute;y cung cấp &aacute;p suất kh&ocirc;ng kh&iacute; th&ocirc;ng qua một mặt nạ được đặt tr&ecirc;n mũi trong khi ngủ. C&aacute;c loại phổ biến nhất được gọi l&agrave; th&ocirc;ng kh&iacute; &aacute;p lực dương li&ecirc;n tục, hoặc CPAP (SEE-pap). Với điều trị n&agrave;y, &aacute;p lực của kh&ocirc;ng kh&iacute; thở l&agrave; li&ecirc;n tục v&agrave; c&oacute; phần lớn hơn của kh&ocirc;ng kh&iacute; xung quanh, chỉ l&agrave; đủ để giữ cho đường h&ocirc; hấp tr&ecirc;n mở. Điều n&agrave;y ngăn ngưng thở v&agrave; ng&aacute;y.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Mặc d&ugrave; CPAP lu&ocirc;n th&agrave;nh c&ocirc;ng v&agrave; hầu hết thường được sử dụng phương ph&aacute;p điều trị ngưng thở khi ngủ, một số người thấy n&oacute; cồng kềnh v&agrave; kh&oacute; chịu. Với thực tế đ&oacute;, hầu hết mọi người t&igrave;m hiểu để điều chỉnh mặt nạ để c&oacute; được một sự ph&ugrave; hợp thoải m&aacute;i v&agrave; an to&agrave;n. C&oacute; thể cần phải thử c&aacute;c loại kh&aacute;c nhau để t&igrave;m mặt nạ th&iacute;ch hợp. Nếu gặp kh&oacute; khăn đặc biệt &aacute;p lực, c&oacute; m&aacute;y c&oacute; chức năng &aacute;p lực th&iacute;ch ứng đặc biệt để cải thiện thoải m&aacute;i. Một số người cũng được hưởng lợi từ việc sử dụng m&aacute;y tạo độ ẩm c&ugrave;ng với hệ thống CPAP.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Kh&ocirc;ng ngừng sử dụng m&aacute;y CPAP nếu gặp vấn đề.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Kiểm tra với b&aacute;c sĩ để xem những g&igrave; điều chỉnh c&oacute; thể l&agrave;m để cải thiện sự thoải m&aacute;i. Ngo&agrave;i ra, li&ecirc;n hệ với b&aacute;c sĩ nếu vẫn ng&aacute;y d&ugrave; đ&atilde; điều trị hoặc bắt đầu ng&aacute;y nữa. Nếu thay đổi trọng lượng, b&aacute;c sĩ c&oacute; thể cần phải điều chỉnh c&agrave;i đặt &aacute;p lực.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Thiết bị bằng miệng.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Một t&ugrave;y chọn kh&aacute;c l&agrave; mặc một c&aacute;i loa được thiết kế để giữ cho họng mở. Trong khi &aacute;p lực đường h&agrave;ng kh&ocirc;ng t&iacute;ch cực gần như lu&ocirc;n lu&ocirc;n điều trị hiệu quả, c&aacute;c dụng cụ bằng miệng l&agrave; một thay thế th&agrave;nh c&ocirc;ng cho một số bệnh nh&acirc;n. Một số được thiết kế để mở cổ họng bằng c&aacute;ch đưa h&agrave;m ra, đ&ocirc;i khi c&oacute; thể l&agrave;m giảm ng&aacute;y v&agrave; ngưng thở khi ngủ tắc nghẽn nhẹ. Những người kh&aacute;c giữ lưỡi ở một vị tr&iacute; kh&aacute;c nhau. Nếu bạn v&agrave; b&aacute;c sĩ quyết định để kh&aacute;m ph&aacute; t&ugrave;y chọn n&agrave;y, sẽ cần phải gặp nha sĩ c&oacute; kinh nghiệm trong c&aacute;c thiết bị y nha khoa cho giấc ngủ v&agrave; theo d&otilde;i điều trị ph&ugrave; hợp.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Một số thiết bị c&oacute; sẵn từ nha sĩ.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> C&oacute; thể cần phải thử c&aacute;c thiết bị kh&aacute;c nhau trước khi t&igrave;m một trong đ&oacute; l&agrave;m việc. Bởi v&igrave; c&aacute;c thiết bị bằng miệng kh&ocirc;ng được thống nhất c&oacute; hiệu quả như CPAP, theo d&otilde;i l&agrave; cần thiết để đảm bảo điều trị th&agrave;nh c&ocirc;ng của chứng ngưng thở khi ngủ.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Phẫu thuật hoặc c&aacute;c thủ tục kh&aacute;c</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Mục đ&iacute;ch của phẫu thuật cho ngưng thở khi ngủ l&agrave; để loại bỏ m&ocirc; dư thừa từ mũi hay cổ họng c&oacute; thể sẽ rung v&agrave; l&agrave;m ng&aacute;y, hoặc c&oacute; thể được ngăn chặn lối đi kh&ocirc;ng kh&iacute; ph&iacute;a tr&ecirc;n v&agrave; g&acirc;y ngưng thở khi ngủ. Phẫu thuật lựa chọn c&oacute; thể bao gồm:</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Phẫu thuật cắt bỏ m&ocirc;.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Uvulopalatopharyngoplasty (UPPP) l&agrave; một thủ tục trong đ&oacute; b&aacute;c sĩ lấy m&ocirc; từ ph&iacute;a sau của miệng v&agrave; cổ họng. Amidal v&ograve;m họng thường được loại bỏ. Phẫu thuật n&agrave;y c&oacute; thể th&agrave;nh c&ocirc;ng trong việc ngăn chặn c&aacute;c cấu tr&uacute;c cổ họng dao động v&agrave; g&acirc;y ra ng&aacute;y. UPPP thường được thực hiện tại bệnh viện v&agrave; y&ecirc;u cầu phải c&oacute; g&acirc;y m&ecirc;.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Chỉnh h&agrave;m.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Trong tiến tr&igrave;nh n&agrave;y, được gọi l&agrave; maxillomandibular, phần thấp hơn tr&ecirc;n của xương h&agrave;m được chuyển về ph&iacute;a trước từ phần c&ograve;n lại của xương mặt. Điều n&agrave;y ph&oacute;ng to kh&ocirc;ng gian ph&iacute;a sau lưỡi v&agrave; v&ograve;m miệng, l&agrave;m tắc nghẽn &iacute;t c&oacute; khả năng. Thủ tục n&agrave;y c&oacute; thể y&ecirc;u cầu một b&aacute;c sĩ phẫu thuật răng miệng v&agrave; chỉnh h&igrave;nh răng mặt, c&oacute; thể được kết hợp với c&aacute;c thủ tục kh&aacute;c để cải thiện khả năng th&agrave;nh c&ocirc;ng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Phẫu thuật mở ở cổ.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> C&oacute; thể cần h&igrave;nh thức phẫu thuật nếu phương ph&aacute;p điều trị kh&aacute;c đ&atilde; thất bại v&agrave; c&oacute; nghi&ecirc;m trọng, đe dọa ngưng thở khi ngủ trong cuộc sống. Trong tiến tr&igrave;nh n&agrave;y, được gọi l&agrave; tracheostomy, b&aacute;c sĩ phẫu thuật l&agrave;m một lỗ tr&ecirc;n cổ v&agrave; ch&egrave;n một ống kim loại hoặc nhựa th&ocirc;ng qua đ&oacute; để h&iacute;t thở. Giữ cho mở được trong ng&agrave;y. Nhưng v&agrave;o ban đ&ecirc;m ph&aacute;t hiện ra n&oacute; để cho ph&eacute;p kh&ocirc;ng kh&iacute; đi v&agrave;o v&agrave; ra khỏi phổi, bỏ qua kh&ocirc;ng kh&iacute; đi qua nơi bị chặn trong cổ họng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Cấy gh&eacute;p.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> C&aacute;c thủ tục l&agrave; một điều trị x&acirc;m lấn tối thiểu c&oacute; li&ecirc;n quan đến vị tr&iacute; của ba thanh polyester nhỏ trong v&ograve;m miệng. Ch&egrave;n cứng lại v&agrave; hỗ trợ c&aacute;c m&ocirc; của v&ograve;m miệng v&agrave; giảm sự sụp đổ đường h&ocirc; hấp tr&ecirc;n v&agrave; ng&aacute;y. Điều trị n&agrave;y được khuyến c&aacute;o chỉ cho những người tắc nghẽn ngưng thở khi ngủ nhẹ đến trung b&igrave;nh.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Loại bỏ c&aacute;c m&ocirc; ở mặt sau của cổ họng với laser (laser hỗ trợ uvulopalatoplasty) hoặc với tần số v&ocirc; tuyến năng lượng (tần số v&ocirc; tuyến cắt bỏ) l&agrave; những thủ tục m&agrave; c&aacute;c b&aacute;c sĩ đ&ocirc;i khi được sử dụng để điều trị chứng ng&aacute;y. Tuy nhi&ecirc;n, c&aacute;c thủ tục n&agrave;y kh&ocirc;ng n&ecirc;n d&ugrave;ng để điều trị chứng ngưng thở khi ngủ.<a name=\"http:__www.dieutri.vn_HoHap_10-1-2012_Ngung-tho-tac-nghen-khi-ngu.htm\"></a></span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">C&aacute;c loại phẫu thuật c&oacute; thể gi&uacute;p giảm chứng ng&aacute;y v&agrave; ngưng thở khi ngủ bằng c&aacute;ch x&oacute;a hoặc mở rộng đường dẫn kh&iacute;: </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Mũi phẫu thuật cắt bỏ khối u hoặc lệch v&aacute;ch ngăn mũi.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Phẫu thuật cắt bỏ amiđan mở rộng hoặc v&ograve;m họng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phong c&aacute;ch sống v&agrave; biện ph&aacute;p khắc phục</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Trong nhiều trường hợp, tự chăm s&oacute;c c&oacute; thể l&agrave; c&aacute;ch th&iacute;ch hợp nhất cho để đối ph&oacute; với chứng ngưng thở khi ngủ. H&atilde;y thử những lời khuy&ecirc;n n&agrave;y:</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Mất trọng lượng.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Ngay cả một sự mất m&aacute;t nhẹ c&acirc;n qu&aacute; mức c&oacute; thể gi&uacute;p l&agrave;m giảm co thắt đường thở.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Tr&aacute;nh uống rượu v&agrave; thuốc an thần như thuốc ngủ.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Những thư gi&atilde;n c&aacute;c cơ ở mặt sau của cổ họng, can thiệp v&agrave;o hơi thở.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Ngủ nghi&ecirc;ng hay hơn l&agrave; nằm ngửa.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Ngủ ngửa c&oacute; thể l&agrave;m cho lưỡi v&agrave; v&ograve;m miệng nghỉ ngơi đối với mặt sau của cổ họng v&agrave; ngăn chặn đường thở. Để ngăn chặn ngủngửa, h&atilde;y thử may một quả b&oacute;ng ở ph&iacute;a sau đầu Pajama.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">H&atilde;y mở đường mũi v&agrave;o ban đ&ecirc;m.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Nếu c&oacute; tắc nghẽn, sử dụng phun nước muối để gi&uacute;p giữ cho đường mũi mở. N&oacute;i chuyện với b&aacute;c sĩ về c&aacute;ch sử dụng thuốc th&ocirc;ng mũi hoặc kh&aacute;ng histamin, bởi v&igrave;, kh&ocirc;ng giống như thuốc xịt nước muối, c&aacute;c loại thuốc n&agrave;y thường được khuyến c&aacute;o chỉ cho hạn sử dụng ngắn.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: right;\"><b style=\"\"><i style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\"><span style=\"color: rgb(128, 0, 0);\"></span></span></i></b></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\">&nbsp;</p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">&nbsp;</span></p></span>\n', 1);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(11, 'Phù phổi', '\n   <h1 class=\"title\">Phù phổi</h1>   <span class=\"des\">Phù phổi mà phát triển đột ngột (cấp tính) là một trường hợp khẩn cấp cần chăm sóc y tế ngay lập tức. Mặc dù đôi khi có thể phù phổi gây tử vong, triển vọng có thể tốt khi được điều trị kịp thời </span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ph&ugrave; phổi l&agrave; một điều kiện g&acirc;y ra bởi chất lỏng dư thừa trong phổi. Chất lỏng n&agrave;y thu thập trong nhiều t&uacute;i kh&iacute; trong phổi, l&agrave;m cho n&oacute; kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong hầu hết trường hợp, vấn đề về tim g&acirc;y ph&ugrave; phổi. Nhưng chất lỏng c&oacute; thể t&iacute;ch lũy cho c&aacute;c l&yacute; do kh&aacute;c, bao gồm vi&ecirc;m phổi, tiếp x&uacute;c với độc tố nhất định, thuốc men v&agrave; tập thể dục, sinh sống ở độ cao.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ph&ugrave; phổi m&agrave; ph&aacute;t triển đột ngột (cấp t&iacute;nh) l&agrave; một trường hợp khẩn cấp cần chăm s&oacute;c y tế ngay lập tức. Mặc d&ugrave; đ&ocirc;i khi c&oacute; thể ph&ugrave; phổi g&acirc;y tử vong, triển vọng c&oacute; thể tốt khi được điều trị kịp thời cho ph&ugrave; phổi c&ugrave;ng với điều trị cho c&aacute;c vấn đề cơ bản. Điều trị ph&ugrave; phổi thay đổi t&ugrave;y theo nguy&ecirc;n nh&acirc;n, nhưng n&oacute;i chung bao gồm bổ sung oxy v&agrave; thuốc.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">T&ugrave;y thuộc v&agrave;o nguy&ecirc;n nh&acirc;n, triệu chứng ph&ugrave; phổi c&oacute; thể xuất hiện bất ngờ hoặc ph&aacute;t triển chậm. </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c dấu hiệu v&agrave; triệu chứng xuất hiện đột ngột c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở c&ugrave;ng cực hoặc kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Cảm gi&aacute;c nghẹt thở hay chết đuối.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thở kh&ograve; kh&egrave; hoặc thở hổn hển.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Lo lắng, bồn chồn hoặc cảm gi&aacute;c e sợ.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho ra đờm bọt c&oacute; thể nhuốm m&aacute;u.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ra mồ h&ocirc;i qu&aacute; nhiều.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Da nhợt nhạt.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau ngực, nếu ph&ugrave; phổi l&agrave; do bệnh tim.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhịp tim nhanh bất thường (đ&aacute;nh trống ngực).</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu ph&aacute;t triển bất cứ dấu hiệu hoặc triệu chứng, h&atilde;y gọi số khẩn cấp trợ gi&uacute;p y tế ngay lập tức. Ph&ugrave; phổi c&oacute; thể g&acirc;y tử vong nếu kh&ocirc;ng được điều trị.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c dấu hiệu v&agrave; triệu chứng ph&aacute;t triển th&ecirc;m dần dần, thường l&agrave; do suy tim, bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; nhiều kh&oacute; thở hơn b&igrave;nh thường khi đang vận động cơ thể.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở với gắng sức, thường l&agrave; khi đang nằm thẳng.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thức tỉnh v&agrave;o ban đ&ecirc;m với một cảm gi&aacute;c kh&oacute; thở, c&oacute; thể được thuy&ecirc;n giảm bằng c&aacute;ch ngồi l&ecirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhanh ch&oacute;ng tăng c&acirc;n khi ph&ugrave; phổi ph&aacute;t triển như l&agrave; kết quả của suy tim sung huyết, một t&igrave;nh trạng m&agrave; tim bơm m&aacute;u qu&aacute; &iacute;t để đ&aacute;p ứng nhu cầu của cơ thể. Việc tăng c&acirc;n l&agrave; từ sự t&iacute;ch tụ của chất lỏng trong cơ thể, đặc biệt l&agrave; ở ch&acirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ăn mất ngon.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mệt mỏi.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c dấu hiệu v&agrave; triệu chứng của ph&ugrave; phổi do độ cao lớn thường bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhức đầu.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mất ngủ.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ứ dịch.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Đến gặp b&aacute;c sĩ khi</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ph&ugrave; phổi đến đột ngột (cấp t&iacute;nh) l&agrave; đe dọa t&iacute;nh mạng. Nhận được hỗ trợ khẩn cấp nếu c&oacute; bất kỳ dấu hiệu v&agrave; triệu chứng cấp t&iacute;nh như sau:</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở hay cảm gi&aacute;c nghẹt thở.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">S&ugrave;i bọt, thở kh&ograve; kh&egrave; hoặc thở hổn hển, &acirc;m thanh khi thở.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bọt hồng khi ho đờm.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở nặng c&ugrave;ng với mồ h&ocirc;i.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">M&agrave;u xanh hoặc m&agrave;u x&aacute;m để l&agrave;n da.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sự sụt giảm nghi&ecirc;m trọng về huyết &aacute;p.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bất ngờ xấu đi của bất cứ triệu chứng li&ecirc;n quan đến ph&ugrave; phổi m&atilde;n t&iacute;nh hoặc cao độ ph&ugrave; phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đừng cố gắng tự m&igrave;nh l&aacute;i xe đến bệnh viện. Thay v&agrave;o đ&oacute;, h&atilde;y gọi số khẩn cấp chăm s&oacute;c y tế v&agrave; chờ đợi để được gi&uacute;p đỡ.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Phổi c&oacute; chứa nhiều nhỏ, t&uacute;i kh&iacute; đ&agrave;n hồi gọi l&agrave; phế nang. Với mỗi hơi thở, c&aacute;c t&uacute;i kh&iacute; c&oacute; oxy v&agrave; carbon dioxide ph&aacute;t h&agrave;nh. Th&ocirc;ng thường, việc trao đổi kh&iacute; diễn ra m&agrave; kh&ocirc;ng c&oacute; vấn đề.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhưng trong trường hợp nhất định, c&aacute;c phế nang đầy chất lỏng thay v&igrave; kh&ocirc;ng kh&iacute;, ngăn ngừa oxy hấp thu v&agrave;o m&aacute;u. Một số điều c&oacute; thể g&acirc;y ra dịch t&iacute;ch tụ trong phổi, nhưng hầu hết với tr&aacute;i tim ph&ugrave; phổi. Hiểu biết về mối quan hệ giữa tim v&agrave; phổi c&oacute; thể gi&uacute;p giải th&iacute;ch tại sao.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tim hoạt động thế n&agrave;o</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tr&aacute;i tim gồm c&oacute; hai phần tr&ecirc;n v&agrave; hai thấp. C&aacute;c ngăn tr&ecirc;n (t&acirc;m nhĩ phải v&agrave; tr&aacute;i) nhận được m&aacute;u đến v&agrave; bơm n&oacute; v&agrave;o ngăn dưới. C&aacute;c ngăn dưới, t&acirc;m thất phải v&agrave; t&acirc;m thất tr&aacute;i, bơm m&aacute;u của tim. C&aacute;c van tim - giữ cho m&aacute;u chảy theo hướng ch&iacute;nh x&aacute;c.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Th&ocirc;ng thường, m&aacute;u đ&atilde; loại &ocirc; xy từ khắp nơi tr&ecirc;n cơ thể v&agrave;o t&acirc;m nhĩ phải v&agrave; chảy v&agrave;o t&acirc;m thất phải, nơi m&agrave; n&oacute; được bơm qua mạch m&aacute;u lớn (động mạch phổi) cho phổi. Ở đ&oacute;, m&aacute;u giải ph&oacute;ng đi&ocirc;x&iacute;t cacbon v&agrave; lấy oxy. C&aacute;c m&aacute;u gi&agrave;u oxy sau đ&oacute; trở về t&acirc;m nhĩ tr&aacute;i th&ocirc;ng qua c&aacute;c tĩnh mạch phổi, chảy qua c&aacute;c van hai l&aacute; v&agrave;o t&acirc;m thất tr&aacute;i, v&agrave; cuối c&ugrave;ng l&aacute; tr&aacute;i tim th&ocirc;ng qua một động mạch lớn, động mạch chủ. C&aacute;c van động mạch chủ ở cơ sở của động mạch chủ giữ cho m&aacute;u kh&ocirc;ng chảy ngược v&agrave;o tr&aacute;i tim. Từ động mạch chủ, m&aacute;u đi đến phần c&ograve;n lại của cơ thể.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Những g&igrave; sai</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ph&ugrave; phổi tim - c&ograve;n được gọi l&agrave; suy tim sung huyết - xảy ra khi t&acirc;m thất tr&aacute;i bị bệnh hoặc l&agrave;m việc qu&aacute; sức kh&ocirc;ng thể bơm đủ lượng m&aacute;u nhận được từ phổi. Kết quả l&agrave;, tăng &aacute;p lực b&ecirc;n trong t&acirc;m nhĩ tr&aacute;i v&agrave; sau đ&oacute; trong c&aacute;c tĩnh mạch v&agrave; mao mạch phổi, g&acirc;y ra dịch sẽ được đẩy qua th&agrave;nh mao mạch v&agrave;o c&aacute;c t&uacute;i kh&iacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Suy tim xung huyết cũng c&oacute; thể xảy ra khi t&acirc;m thất phải kh&ocirc;ng thể vượt qua &aacute;p lực gia tăng trong động mạch phổi, thường l&agrave; kết quả của suy tim tr&aacute;i, bệnh phổi m&atilde;n t&iacute;nh hoặc huyết &aacute;p cao trong c&aacute;c động mạch phổi (phổi tăng huyết &aacute;p). </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c điều kiện y tế c&oacute; thể g&acirc;y ra t&acirc;m thất tr&aacute;i yếu v&agrave; cuối c&ugrave;ng kh&ocirc;ng bơm đủ m&aacute;u bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Bệnh động mạch v&agrave;nh.</span></b><span style=\"font-family: Arial;\"> Qua thời gian, c&aacute;c động mạch cung cấp m&aacute;u cho tim c&oacute; thể trở n&ecirc;n thu hẹp từ mảng chất b&eacute;o. Một cơn đau tim xảy ra khi một cục m&aacute;u đ&ocirc;ng trong c&aacute;c động mạch bị thu hẹp, chặn d&ograve;ng chảy của m&aacute;u v&agrave; l&agrave;m hư hại một phần của cơ tim được cung cấp bởi động mạch n&agrave;y. Kết quả l&agrave; cơ tim bị hư hỏng kh&ocirc;ng thể bơm như n&oacute; phải l&agrave;m.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mặc d&ugrave; nghỉ ngơi sẽ cố gắng để b&ugrave; đắp cho sự mất m&aacute;t n&agrave;y, hoặc l&agrave; n&oacute; kh&ocirc;ng thể l&agrave;m c&oacute; hiệu quả hoặc n&oacute; bị suy yếu do khối lượng c&ocirc;ng việc th&ecirc;m. Khi hoạt động bơm của tim yếu đi, m&aacute;u tr&agrave;n v&agrave;o phổi, buộc chất lỏng trong m&aacute;u đi qua th&agrave;nh mao mạch v&agrave;o c&aacute;c t&uacute;i kh&iacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Bệnh cơ tim.</span></b><span style=\"font-family: Arial;\"> Khi cơ tim bị hư hỏng do nguy&ecirc;n nh&acirc;n kh&aacute;c ngo&agrave;i vấn đề lưu lượng m&aacute;u, điều kiện được gọi l&agrave; bệnh cơ tim. Th&ocirc;ng thường, bệnh cơ tim kh&ocirc;ng r&otilde; nguy&ecirc;n nh&acirc;n, mặc d&ugrave; đ&ocirc;i khi n&oacute; c&oacute; yếu tố trong gia đ&igrave;nh. Nguy&ecirc;n nh&acirc;n &iacute;t phổ biến bao gồm nhiễm virus (vi&ecirc;m cơ tim), lạm dụng rượu v&agrave; c&aacute;c hiệu ứng độc hại của c&aacute;c loại thuốc như heroin v&agrave; một số loại h&oacute;a trị.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bởi v&igrave; bệnh cơ tim suy yếu t&acirc;m thất tr&aacute;i - bơm ch&iacute;nh, tim c&oacute; thể kh&ocirc;ng c&oacute; khả năng đ&aacute;p ứng với điều kiện phải c&oacute; để l&agrave;m việc chăm chỉ hơn, chẳng hạn như tăng huyết &aacute;p, nhịp tim nhanh hơn với mức ti&ecirc;u thụ muối gắng sức hoặc qu&aacute; mức l&agrave; nguy&ecirc;n nh&acirc;n g&acirc;y giữ nước hoặc nhiễm tr&ugrave;ng. Khi t&acirc;m thất tr&aacute;i kh&ocirc;ng thể theo kịp với nhu cầu được đặt tr&ecirc;n đ&oacute;, chất lỏng tr&agrave;n v&agrave;o phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Vấn đề về tim van.</span></b><span style=\"font-family: Arial;\"> Trong bệnh van hai l&aacute;, bệnh van động mạch chủ, c&aacute;c van điều tiết lưu lượng m&aacute;u ở ph&iacute;a b&ecirc;n tr&aacute;i của tim, hoặc kh&ocirc;ng đủ mở rộng (hẹp) hoặc kh&ocirc;ng đ&oacute;ng cửa ho&agrave;n to&agrave;n (hở). Điều n&agrave;y cho ph&eacute;p m&aacute;u chảy ngược qua van. Khi c&aacute;c van bị hẹp, m&aacute;u kh&ocirc;ng thể lưu chuyển tự do trong t&acirc;m thất tr&aacute;i x&acirc;y dựng l&ecirc;n, l&agrave;m cho t&acirc;m thất tr&aacute;i l&agrave;m việc kh&oacute; khăn hơn với từng cơn co thắt. T&acirc;m thất tr&aacute;i nở ra để cho ph&eacute;p lưu lượng m&aacute;u nhiều hơn, nhưng điều n&agrave;y l&agrave;m cho h&agrave;nh động của t&acirc;m thất tr&aacute;i bơm &iacute;t hiệu quả. Bởi v&igrave; n&oacute; l&agrave;m việc kh&oacute; khăn hơn rất nhiều, t&acirc;m thất tr&aacute;i cuối c&ugrave;ng d&agrave;y, trong đ&oacute; đặt &aacute;p lực lớn hơn v&agrave;o c&aacute;c động mạch v&agrave;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">&Aacute;p lực gia tăng k&eacute;o d&agrave;i đến t&acirc;m nhĩ tr&aacute;i v&agrave; sau đ&oacute; đến c&aacute;c tĩnh mạch phổi, g&acirc;y ra dịch t&iacute;ch tụ trong phổi. Mặt kh&aacute;c, nếu r&ograve; rỉ van hai l&aacute;, một số m&aacute;u xo&aacute;y ngược đối với phổi mỗi khi tim bơm m&aacute;u. Nếu r&ograve; rỉ ph&aacute;t triển đột ngột, c&oacute; thể ph&aacute;t triển bất ngờ v&agrave; ph&ugrave; phổi nặng.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Cao huyết &aacute;p.</span></b><span style=\"font-family: Arial;\"> Nếu kh&ocirc;ng điều trị hoặc kh&ocirc;ng kiểm so&aacute;t được huyết &aacute;p cao g&acirc;y d&agrave;y l&ecirc;n của cơ t&acirc;m thất tr&aacute;i, v&agrave; l&agrave;m trầm trọng th&ecirc;m bệnh động mạch v&agrave;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ph&ugrave; phổi kh&ocirc;ng do tim</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng phải tất cả ph&ugrave; phổi l&agrave; kết quả của bệnh tim. Chất lỏng cũng c&oacute; thể bị r&ograve; rỉ từ c&aacute;c mao mạch trong t&uacute;i kh&iacute; phổi bởi v&igrave; bản th&acirc;n c&aacute;c mao mạch trở n&ecirc;n thấm hoặc r&ograve; rỉ, thậm ch&iacute; kh&ocirc;ng c&oacute; sự t&iacute;ch tụ của c&aacute;c &aacute;p lực trở lại từ tr&aacute;i tim. Trong trường hợp đ&oacute;, t&igrave;nh trạng n&agrave;y được gọi l&agrave; ph&ugrave; phổi noncardiac bởi v&igrave; tr&aacute;i tim kh&ocirc;ng phải l&agrave; nguy&ecirc;n nh&acirc;n của vấn đề. Một số yếu tố c&oacute; thể g&acirc;y ph&ugrave; phổi noncardiac l&agrave;:</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Nhiễm tr&ugrave;ng phổi.</span></b><span style=\"font-family: Arial;\"> Khi kết quả ph&ugrave; phổi nhiễm tr&ugrave;ng phổi, như vi&ecirc;m phổi, ph&ugrave; xảy ra chỉ trong một phần của phổi vi&ecirc;m.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Độc tố nhất định.</span></b><span style=\"font-family: Arial;\"> Bao gồm c&aacute;c độc tố h&iacute;t v&agrave;o - chẳng hạn như clo hoặc amoniac - cũng như những loại c&oacute; thể tự lưu h&agrave;nh trong cơ thể, v&iacute; dụ, nếu h&iacute;t v&agrave;o một số dịch dạ d&agrave;y khi bị n&ocirc;n mửa.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Bệnh thận.</span></b><span style=\"font-family: Arial;\"> Khi thận kh&ocirc;ng thể loại bỏ chất thải hiệu quả, chất lỏng dư thừa c&oacute; thể x&acirc;y dựng l&ecirc;n, g&acirc;y ph&ugrave; phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">H&iacute;t phải kh&oacute;i.</span></b><span style=\"font-family: Arial;\"> Kh&oacute;i từ đ&aacute;m ch&aacute;y c&oacute; chứa h&oacute;a chất thiệt hại m&agrave;ng giữa c&aacute;c t&uacute;i kh&iacute; v&agrave; c&aacute;c mao mạch, cho ph&eacute;p chất lỏng v&agrave;o phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Phản ứng bất lợi thuốc.</span></b><span style=\"font-family: Arial;\"> Rất nhiều loại thuốc - từ thuốc bất hợp ph&aacute;p như heroin v&agrave; cocaine v&agrave; thuốc h&oacute;a trị aspirin - được biết l&agrave; g&acirc;y noncardiac ph&ugrave; phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Hội chứng suy h&ocirc; hấp cấp (ARDS).</span></b><span style=\"font-family: Arial;\"> Chứng rối loạn nghi&ecirc;m trọng xảy ra khi phổi đột nhi&ecirc;n đầy vi&ecirc;m tế b&agrave;o m&aacute;u trắng v&agrave; chất lỏng. Nhiều điều kiện c&oacute; thể g&acirc;y ARDS, bao gồm cả thương t&iacute;ch nặng (chấn thương), nhiễm tr&ugrave;ng hệ thống (nhiễm tr&ugrave;ng huyết), vi&ecirc;m phổi v&agrave; sốc.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Cao độ.</span></b><span style=\"font-family: Arial;\"> Nh&agrave; leo n&uacute;i v&agrave; những người sống trong hoặc đi du lịch đến vị tr&iacute; độ cao c&oacute; nguy cơ ph&aacute;t triển cao độ ph&ugrave; phổi (HAPE). T&igrave;nh trạng n&agrave;y - thường xảy ra ở độ cao tr&ecirc;n 8.000 feet (khoảng 2.400 m&eacute;t) - cũng c&oacute; thể ảnh hưởng đến người đi bộ đường d&agrave;i hoặc trượt tuyết, người bắt đầu thực hiện ở độ cao cao m&agrave; kh&ocirc;ng trở th&agrave;nh th&iacute;ch nghi. Nhưng ngay cả những người hiked hoặc skied ở độ cao lớn trong qu&aacute; khứ kh&ocirc;ng c&oacute; miễn dịch.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mặc d&ugrave; nguy&ecirc;n nh&acirc;n ch&iacute;nh x&aacute;c l&agrave; kh&ocirc;ng ho&agrave;n to&agrave;n hiểu, HAPE dường như ph&aacute;t triển như l&agrave; kết quả của &aacute;p lực tăng từ co thắt của c&aacute;c mao mạch phổi. Nếu kh&ocirc;ng c&oacute; chăm s&oacute;c th&iacute;ch hợp, HAPE c&oacute; thể g&acirc;y tử vong.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu ph&ugrave; phổi vẫn tiếp tục, n&oacute; c&oacute; thể l&agrave;m tăng &aacute;p lực động mạch phổi v&agrave; cuối c&ugrave;ng l&agrave; t&acirc;m thất phải bắt đầu suy. T&acirc;m thất phải c&oacute; th&agrave;nh mỏng hơn nhiều so với ph&iacute;a b&ecirc;n tr&aacute;i. &Aacute;p lực tăng tr&agrave;n v&agrave;o t&acirc;m nhĩ phải v&agrave; sau đ&oacute; v&agrave;o bộ phận kh&aacute;c nhau của cơ thể, nơi n&oacute; c&oacute; thể g&acirc;y ra:</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sưng ch&acirc;n (ph&ugrave; nề).</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sưng bụng (cổ trướng).</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sự t&iacute;ch tụ của chất lỏng trong m&agrave;ng bao quanh phổi (tr&agrave;n dịch m&agrave;ng phổi).</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tắc nghẽn v&agrave; sưng gan.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi kh&ocirc;ng được điều trị, ph&ugrave; phổi cấp t&iacute;nh c&oacute; thể g&acirc;y tử vong. Trong một số trường hợp n&oacute; c&oacute; thể g&acirc;y tử vong ngay cả khi được điều trị.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bởi v&igrave; ph&ugrave; phổi cần điều trị nhanh ch&oacute;ng, sẽ được chẩn đo&aacute;n ban đầu tr&ecirc;n cơ sở c&aacute;c triệu chứng v&agrave; một kỳ kiểm tra l&acirc;m s&agrave;ng v&agrave; tia X ngực. Cũng c&oacute; thể c&oacute; m&aacute;u r&uacute;t ra - thường l&agrave; từ một động mạch ở cổ tay - để n&oacute; c&oacute; thể được kiểm tra lượng oxy v&agrave; carbon dioxide (nồng độ kh&iacute; m&aacute;u động mạch). M&aacute;u cũng sẽ được kiểm tra mức độ của một chất gọi l&agrave; B-type natriuretic peptide (BNP). Mức tăng của BNP c&oacute; thể cho thấy ph&ugrave; phổi l&agrave; do vấn đề về tim. X&eacute;t nghiệm m&aacute;u kh&aacute;c thường sẽ được thực hiện, bao gồm cả c&aacute;c x&eacute;t nghiệm chức năng thận, số lượng m&aacute;u, cũng như c&aacute;c x&eacute;t nghiệm để loại trừ một cơn đau tim l&agrave; nguy&ecirc;n nh&acirc;n g&acirc;y ph&ugrave; phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi t&igrave;nh trạng ổn định hơn, b&aacute;c sĩ sẽ hỏi về bệnh sử, đặc biệt l&agrave; đ&atilde; từng c&oacute; bệnh tim mạch hoặc phổi. </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c x&eacute;t nghiệm c&oacute; thể được thực hiện để chẩn đo&aacute;n ph&ugrave; phổi hoặc để x&aacute;c định l&yacute; do tại sao ph&aacute;t triển chất lỏng trong phổi bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">X-ray.</span></b><span style=\"font-family: Arial;\"> Chụp X-ray c&oacute; thể sẽ l&agrave; b&agrave;i kiểm tra đầu ti&ecirc;n phải thực hiện để x&aacute;c định chẩn đo&aacute;n của ph&ugrave; phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Điện tim (ECG).</span></b><span style=\"font-family: Arial;\"> Điều thử nghiệm n&agrave;y kh&ocirc;ng x&acirc;m lấn c&oacute; thể tiết lộ một loạt c&aacute;c th&ocirc;ng tin về tr&aacute;i tim. Trong một điện t&acirc;m đồ, c&aacute;c bản v&aacute; nhận được xung điện từ tr&aacute;i tim. Ghi ở dạng đồ thị s&oacute;ng tr&ecirc;n giấy hoặc m&agrave;n h&igrave;nh. C&aacute;c mẫu s&oacute;ng hiển thị nhịp tim v&agrave; tốc độ, v&agrave; liệu c&aacute;c khu vực của tr&aacute;i tim hiển thị giảm lưu lượng m&aacute;u.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Si&ecirc;u &acirc;m tim (chẩn đo&aacute;n si&ecirc;u &acirc;m tim).</span></b><span style=\"font-family: Arial;\"> Một thử nghiệm kh&ocirc;ng x&acirc;m lấn, si&ecirc;u &acirc;m tim sử dụng một thiết bị gọi l&agrave; đầu d&ograve; chuyển đổi để tạo ra s&oacute;ng &acirc;m thanh tần số cao được phản &aacute;nh từ c&aacute;c m&ocirc; của tim. C&aacute;c s&oacute;ng &acirc;m thanh n&agrave;y sau đ&oacute; được gửi đến một m&aacute;y sử dụng ch&uacute;ng để soạn h&igrave;nh ảnh của tr&aacute;i tim tr&ecirc;n m&agrave;n h&igrave;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c thử nghiệm c&oacute; thể gi&uacute;p chẩn đo&aacute;n một số vấn đề về tim, bao gồm cả vấn đề van, bất thường chuyển động của c&aacute;c bức th&agrave;nh thất, dịch xung quanh tim (m&agrave;ng ngo&agrave;i tim tr&agrave;n dịch) v&agrave; dị tật tim bẩm sinh. N&oacute; cũng ch&iacute;nh x&aacute;c đo lượng m&aacute;u t&acirc;m thất tr&aacute;i đẩy với mỗi nhịp đập của tim (ph&acirc;n số tống m&aacute;u, hoặc EF). Mặc d&ugrave; một EF thấp thường chỉ ra một nguy&ecirc;n nh&acirc;n tim cho ph&ugrave; phổi, c&oacute; thể c&oacute; ph&ugrave; phổi tim với EF b&igrave;nh thường.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Transesophageal si&ecirc;u &acirc;m tim (TEE).</span></b><span style=\"font-family: Arial;\"> Trong một b&agrave;i kiểm tra si&ecirc;u &acirc;m tim truyền thống, bộ chuyển đổi n&agrave;y vẫn c&ograve;n b&ecirc;n ngo&agrave;i cơ thể tr&ecirc;n ngực. Nhưng trong TEE, ống linh hoạt mềm với một đầu d&ograve; đặc biệt được đưa qua miệng v&agrave; v&agrave;o thực quản - th&ocirc;ng qua dẫn đến dạ d&agrave;y. Thực quản nằm ngay ph&iacute;a sau tr&aacute;i tim, cho ph&eacute;p cho một h&igrave;nh ảnh ch&iacute;nh x&aacute;c của tim v&agrave; động mạch phổi. Sẽ nhận được một thuốc an thần để l&agrave;m cho thoải m&aacute;i hơn. C&oacute; thể c&oacute; đau họng trong một v&agrave;i ng&agrave;y sau khi thủ tục, v&agrave; c&oacute; một nguy cơ nhỏ của thủng hoặc chảy m&aacute;u thực quản.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đặt ống th&ocirc;ng động mạch phổi.</span></b><span style=\"font-family: Arial;\"> Nếu c&aacute;c x&eacute;t nghiệm kh&aacute;c kh&ocirc;ng tiết lộ l&yacute; do ph&ugrave; phổi, b&aacute;c sĩ c&oacute; thể đề xuất một thủ tục để đo &aacute;p suất trong mao mạch phổi. Trong thử nghiệm n&agrave;y, một b&oacute;ng nhỏ - ống th&ocirc;ng được đưa qua một tĩnh mạch ở ch&acirc;n hay c&aacute;nh tay v&agrave;o một động mạch phổi. Ống th&ocirc;ng n&agrave;y c&oacute; hai lỗ kết nối với đầu d&ograve; &aacute;p lực. B&oacute;ng được thổi phồng v&agrave; sau đ&oacute; x&igrave; hơi đo &aacute;p lực.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đặt ống th&ocirc;ng tim.</span></b><span style=\"font-family: Arial;\"> Nếu c&aacute;c x&eacute;t nghiệm như l&agrave; điện t&acirc;m đồ hoặc si&ecirc;u &acirc;m tim kh&ocirc;ng ph&aacute;t hiện ra nguy&ecirc;n nh&acirc;n g&acirc;y ra ph&ugrave; phổi, hoặc cũng c&oacute; đau ngực, b&aacute;c sĩ c&oacute; thể gợi &yacute; đặt ống th&ocirc;ng tim. Trong thời gian đặt ống th&ocirc;ng tim, một ống th&ocirc;ng được lắp v&agrave;o một động mạch hoặc tĩnh mạch ở cổ, h&aacute;ng hay c&aacute;nh tay v&agrave; luồn qua mạch m&aacute;u đến tr&aacute;i tim. Nếu thuốc nhuộm được ti&ecirc;m trong thời gian thử nghiệm, n&oacute; được gọi l&agrave; chụp động mạch v&agrave;nh. Trong thủ tục n&agrave;y, khắc phục h&agrave;nh động như mở một động mạch bị chặn c&oacute; thể được thực hiện, nhanh ch&oacute;ng c&oacute; thể cải thiện hoạt động bơm của t&acirc;m thất tr&aacute;i. Đặt ống th&ocirc;ng tim cũng c&oacute; thể được sử dụng để đo &aacute;p suất trong buồng tim, đ&aacute;nh gi&aacute; c&aacute;c van tim, v&agrave; t&igrave;m nguy&ecirc;n nh&acirc;n g&acirc;y ph&ugrave; phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Cho oxy l&agrave; bước đầu ti&ecirc;n trong việc điều trị cho ph&ugrave; phổi. Thường nhận được oxy qua mặt nạ hoặc ống th&ocirc;ng mũi - một ống nhựa mềm với hai lỗ cung cấp oxy cho mỗi lỗ mũi. Điều n&agrave;y sẽ giảm bớt một số triệu chứng. Đ&ocirc;i khi n&oacute; c&oacute; thể l&agrave; cần thiết để hỗ trợ thở bằng m&aacute;y.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">T&ugrave;y theo t&igrave;nh trạng v&agrave; l&yacute; do ph&ugrave; phổi, cũng c&oacute; thể nhận được một hoặc nhiều loại thuốc sau đ&acirc;y: </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Giảm sức tải.</span></b><span style=\"font-family: Arial;\"> C&aacute;c b&aacute;c sĩ thường sử dụng nitroglycerin v&agrave; c&aacute;c thuốc lợi tiểu, chẳng hạn như furosemide (Lasix), để điều trị ph&ugrave; phổi. Những thuốc n&agrave;y l&agrave;m gi&atilde;n c&aacute;c tĩnh mạch trong phổi v&agrave; c&aacute;c nơi kh&aacute;c trong cơ thể, gi&uacute;p giảm &aacute;p lực dịch đi v&agrave;o tim v&agrave; phổi. Thuốc lợi tiểu c&oacute; thể l&agrave;m cho đi tiểu qu&aacute; nhiều ban đầu m&agrave; tạm thời c&oacute; thể cần một ống th&ocirc;ng tiểu trong khi đang ở trong bệnh viện.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Morphine (Astramorph, Roxanol).</span></b><span style=\"font-family: Arial;\"> Điều n&agrave;y c&oacute; thể được sử dụng ma t&uacute;y để l&agrave;m giảm kh&oacute; thở v&agrave; lo lắng. Nhưng một số b&aacute;c sĩ tin rằng những rủi ro của morphine c&oacute; thể lớn hơn những lợi &iacute;ch v&agrave; c&oacute; nhiều khuynh hướng sử dụng kh&aacute;c, hiệu quả hơn, ma t&uacute;y.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Giảm hậu g&aacute;nh.</span></b><span style=\"font-family: Arial;\"> C&aacute;c thuốc n&agrave;y l&agrave;m gi&atilde;n mạch ngoại vi v&agrave; giảm &aacute;p lực tải t&acirc;m thất tr&aacute;i. Một số v&iacute; dụ về c&aacute;c thuốc giảm hậu g&aacute;nh bao gồm nitroprusside (Nitropress), enalapril (Vasotec) v&agrave; captopril (Capoten).</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc điều chỉnh &aacute;p lực m&aacute;u.</span></b><span style=\"font-family: Arial;\"> Nếu bị cao huyết &aacute;p khi ph&aacute;t triển ph&ugrave; phổi, sẽ được cấp thuốc để kiểm so&aacute;t n&oacute;. Mặt kh&aacute;c, nếu huyết &aacute;p qu&aacute; thấp, c&oacute; thể bị ti&ecirc;m thuốc để n&acirc;ng cao n&oacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Điều trị ph&ugrave; phổi độ cao (HAPE)</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu đang leo n&uacute;i hoặc đi du lịch ở độ cao v&agrave; trải nghiệm triệu chứng nhẹ của HAPE, xuống một v&agrave;i ngh&igrave;n feet (khoảng 600-900 m) n&ecirc;n l&agrave;m để giảm triệu chứng. Oxy cũng l&agrave; hữu &iacute;ch. Khi triệu chứng nặng hơn, sẽ c&oacute; thể cần gi&uacute;p đỡ. Một m&aacute;y bay trực thăng cứu hộ c&oacute; thể cần thiết cho c&aacute;c trường hợp nghi&ecirc;m trọng nhất, bởi v&igrave; HAPE c&oacute; thể đe dọa t&iacute;nh mạng.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một số nh&agrave; leo n&uacute;i đi theo với thuốc theo toa acetazolamide (Diamox) để gi&uacute;p chữa trị hoặc ngăn ngừa c&aacute;c triệu chứng của HAPE. Để ngăn chặn HAPE, acetazolamide bắt đầu ba ng&agrave;y trước khi đi l&ecirc;n. Acetazolamide đ&ocirc;i khi c&oacute; thể c&oacute; t&aacute;c dụng phụ - bao gồm ngứa ran hoặc n&oacute;ng tay v&agrave; b&agrave;n ch&acirc;n, rối loạn ti&ecirc;u chảy, buồn n&ocirc;n, ch&aacute;n ăn, v&agrave; c&aacute;c vấn đề n&oacute;i.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phong c&aacute;ch sống v&agrave; biện ph&aacute;p khắc phục</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những gợi &yacute; sau đ&acirc;y c&oacute; thể gi&uacute;p tốc độ phục hồi từ ph&ugrave; phổi tim v&agrave; gi&uacute;p ngăn ngừa một sự t&aacute;i ph&aacute;t: </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tự c&acirc;n h&agrave;ng ng&agrave;y.</span></b><span style=\"font-family: Arial;\"> L&agrave;m như vậy v&agrave;o buổi s&aacute;ng trước khi ăn s&aacute;ng v&agrave; lưu giữ hồ sơ của trọng lượng h&agrave;ng ng&agrave;y. Gọi b&aacute;c sĩ nếu đạt được 2-3 bảng Anh (khoảng 1 đến 1,4 kg) trong một ng&agrave;y.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thực hiện theo hướng dẫn của b&aacute;c sĩ về chế độ ăn uống.</span></b><span style=\"font-family: Arial;\"> Hầu hết mọi người với ph&ugrave; phổi sẽ cần phải theo một chế độ ăn &iacute;t muối. Y&ecirc;u cầu để được giới thiệu đến chuy&ecirc;n vi&ecirc;n dinh dưỡng nếu cần trợ gi&uacute;p đ&aacute;nh gi&aacute; h&agrave;m lượng muối trong thực phẩm. </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Nếu c&oacute; huyết &aacute;p cao, thực hiện c&aacute;c bước để kiểm so&aacute;t n&oacute;.</span></b><span style=\"font-family: Arial;\"> C&aacute;ch tốt nhất để l&agrave;m điều n&agrave;y l&agrave; để kiểm tra huyết &aacute;p &iacute;t nhất một lần một ng&agrave;y. H&atilde;y hỏi b&aacute;c sĩ để hướng dẫn li&ecirc;n quan đến huyết &aacute;p tối ưu.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Lắng nghe lời khuy&ecirc;n y tế.</span></b><span style=\"font-family: Arial;\"> Thực hiện theo lời khuy&ecirc;n của b&aacute;c sĩ về việc kiểm so&aacute;t vấn đề sức khỏe tiềm ẩn bất kỳ, bao gồm tư vấn về trọng lượng, chế độ ăn uống v&agrave; tập thể dục.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Nhận được rất nhiều của giấc ngủ mỗi đ&ecirc;m.</span></b><span style=\"font-family: Arial;\"> Một giấc ngủ ngắn trong ng&agrave;y nếu cảm thấy mệt mỏi. N&oacute; c&oacute; thể mất đến 3-6 th&aacute;ng trước khi trở về chức năng phổi b&igrave;nh thường.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu đ&atilde; c&oacute; trải nghiệm ph&ugrave; phổi kh&ocirc;ng do tim - bao gồm cả một số h&igrave;nh thức ARDS - chăm s&oacute;c để giảm thiểu thiệt hại th&ecirc;m cho phổi, v&agrave; c&agrave;ng nhiều c&agrave;ng tốt tr&aacute;nh những nguy&ecirc;n nh&acirc;n g&acirc;y ra t&igrave;nh trạng, chẳng hạn như ma t&uacute;y, chất g&acirc;y dị ứng hoặc độ cao lớn. </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Ph&ograve;ng chống</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ph&ugrave; phổi thường kh&ocirc;ng ph&ograve;ng ngừa, nhưng c&aacute;c biện ph&aacute;p n&agrave;y c&oacute; thể gi&uacute;p giảm nguy cơ. </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ngăn ngừa bệnh tim mạch</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh tim mạch l&agrave; nguy&ecirc;n nh&acirc;n h&agrave;ng đầu của ph&ugrave; phổi. C&oacute; thể l&agrave;m giảm nguy cơ của nhiều loại bệnh tim bằng c&aacute;ch l&agrave;m theo những gợi &yacute;:</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Kiểm so&aacute;t huyết &aacute;p.</span></b><span style=\"font-family: Arial;\"> Cao huyết &aacute;p (huyết &aacute;p cao) c&oacute; thể dẫn đến c&aacute;c điều kiện nghi&ecirc;m trọng như bệnh tim mạch, đột quỵ v&agrave; suy thận. Hầu hết người lớn cần phải c&oacute; kiểm tra huyết &aacute;p &iacute;t nhất một lần mỗi hai năm. Đ&acirc;y l&agrave; một thủ tục kh&ocirc;ng x&acirc;m lấn v&agrave; kh&ocirc;ng đau đớn bằng c&aacute;ch sử dụng một đai m&agrave; quấn quanh c&aacute;nh tay tr&ecirc;n. X&eacute;t nghiệm n&agrave;y chỉ mất v&agrave;i ph&uacute;t.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Huyết &aacute;p nghỉ ngơi đọc dưới 120/80 milim&eacute;t thuỷ ng&acirc;n (mm Hg) được xem l&agrave; b&igrave;nh thường. Nếu nghỉ ngơi huyết &aacute;p lu&ocirc;n 140/90 mm Hg hoặc cao hơn, c&oacute; huyết &aacute;p cao. Giữa c&aacute;c cấp c&aacute;c thể l&agrave; prehypertensive.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong nhiều trường hợp, c&oacute; thể hạ thấp huyết &aacute;p hoặc duy tr&igrave; một mức độ l&agrave;nh mạnh bằng c&aacute;ch tập thể dục thường xuy&ecirc;n, ăn một chế độ ăn nhiều tr&aacute;i c&acirc;y tươi, rau v&agrave; c&aacute;c sản phẩm bơ sữa &iacute;t chất b&eacute;o, v&agrave; hạn chế muối v&agrave; rượu. </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Xem cholesterol trong m&aacute;u.</span></b><span style=\"font-family: Arial;\"> Cholesterol l&agrave; một trong một số loại chất b&eacute;o cần thiết cho sức khỏe. Nhưng qu&aacute; nhiều cholesterol c&oacute; thể vượt qu&aacute; điều tốt. Cao hơn mức cholesterol b&igrave;nh thường c&oacute; thể g&acirc;y ra mảng chất b&eacute;o h&igrave;nh th&agrave;nh trong động mạch, cản trở lưu th&ocirc;ng m&aacute;u v&agrave; tăng nguy cơ bệnh mạch m&aacute;u. Nhưng thay đổi lối sống thường c&oacute; thể giữ mức cholesterol thấp. Điều n&agrave;y bao gồm hạn chế chất b&eacute;o - đặc biệt l&agrave; chất b&eacute;o b&atilde;o h&ograve;a - ăn nhiều chất xơ, c&aacute;, tr&aacute;i c&acirc;y tươi v&agrave; rau quả, tập thể dục thường xuy&ecirc;n, ngừng h&uacute;t thuốc v&agrave; uống điều độ.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Kh&ocirc;ng h&uacute;t thuốc.</span></b><span style=\"font-family: Arial;\"> Nếu h&uacute;t thuốc, điều quan trọng nhất duy nhất c&oacute; thể l&agrave;m cho tim v&agrave; sức khỏe của phổi l&agrave; ngăn chặn. Tiếp tục h&uacute;t thuốc l&aacute; l&agrave;m tăng nguy cơ đau tim thứ hai hoặc li&ecirc;n quan đến c&aacute;i chết tr&aacute;i tim v&agrave; cũng l&agrave;m tăng nguy cơ ung thư phổi v&agrave; bệnh về phổi kh&aacute;c như kh&iacute; phế thũng. Hơn nữa, c&oacute; nguy cơ ngay cả khi kh&ocirc;ng h&uacute;t thuốc nhưng sống hoặc l&agrave;m việc với một người n&agrave;o đ&oacute;. Tiếp x&uacute;c với kh&oacute;i thuốc l&agrave; một yếu tố g&oacute;p phần v&agrave;o bệnh động mạch v&agrave;nh. Nếu kh&ocirc;ng thể tự ngừng h&uacute;t thuốc, h&atilde;y hỏi b&aacute;c sĩ quy định một kế hoạch điều trị để gi&uacute;p bỏ thuốc l&aacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Chế độ ăn uống cho tr&aacute;i tim khỏe mạnh.</span></b><span style=\"font-family: Arial;\"> Ăn c&aacute; l&agrave; một trong những nền tảng của một chế độ ăn uống cho sức khỏe tim - n&oacute; c&oacute; chứa omega-3 acid b&eacute;o, gi&uacute;p cải thiện mức cholesterol trong m&aacute;u v&agrave; ngăn ngừa cục m&aacute;u đ&ocirc;ng. N&oacute; cũng quan trọng l&agrave; ăn nhiều tr&aacute;i c&acirc;y v&agrave; rau quả, c&oacute; chứa chất chống oxy h&oacute;a, vitamin v&agrave; kho&aacute;ng chất gi&uacute;p ngăn ngừa mảng b&aacute;m v&agrave;o động mạch v&agrave;nh. Ăn &iacute;t chất b&eacute;o, đặc biệt l&agrave; động vật (b&atilde;o h&ograve;a) v&agrave; chất b&eacute;o trans (hydro h&oacute;a dầu).</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Hạn chế muối.</span></b><span style=\"font-family: Arial;\"> Điều n&agrave;y đặc biệt quan trọng để sử dụng &iacute;t muối (sodium) nếu c&oacute; bệnh tim hoặc huyết &aacute;p cao. Ở một số người bị thiệt hại chức năng t&acirc;m thất tr&aacute;i khi d&ugrave;ng muối vượt qu&aacute; - ngay cả trong một bữa ăn đơn lẻ hoặc một t&uacute;i khoai t&acirc;y chi&ecirc;n - c&oacute; thể đủ để g&acirc;y ra suy tim sung huyết. Nếu đang c&oacute; một thời gian kh&oacute; cắt giảm muối, c&oacute; thể gi&uacute;p đỡ để n&oacute;i chuyện với chuy&ecirc;n vi&ecirc;n dinh dưỡng. Người đ&oacute; c&oacute; thể gi&uacute;p chỉ ra c&aacute;c loại thực phẩm &iacute;t muối cũng như c&aacute;c thủ thuật cung cấp cho việc thực hiện một chế độ ăn &iacute;t muối tốt.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tập thể dục thường xuy&ecirc;n.</span></b><span style=\"font-family: Arial;\"> Tập thể dục l&agrave; quan trọng đối với một tr&aacute;i tim khỏe mạnh. Tập thể dục aerobic thường - khoảng 30 ph&uacute;t mỗi ng&agrave;y - gi&uacute;p kiểm so&aacute;t huyết &aacute;p v&agrave; mức cholesterol, v&agrave; duy tr&igrave; một trọng lượng khỏe mạnh. Nếu kh&ocirc;ng tập thể dục, bắt đầu từ từ v&agrave; x&acirc;y dựng dần dần.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Duy tr&igrave; một trọng lượng khỏe mạnh.</span></b><span style=\"font-family: Arial;\"> Thừa c&acirc;n tăng nhẹ nguy cơ mắc bệnh tim mạch. Mặt kh&aacute;c, thậm ch&iacute; mất đi một lượng nhỏ trọng lượng c&oacute; thể giảm cholesterol trong m&aacute;u &aacute;p lực, v&agrave; giảm nguy cơ bệnh tiểu đường.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Xem x&eacute;t uống aspirin.</span></b><span style=\"font-family: Arial;\"> Thảo luận với b&aacute;c sĩ về những ưu v&agrave; khuyết điểm uống một vi&ecirc;n aspirin liều nhỏ (81 mg) mỗi ng&agrave;y.<a name=\"http:__www.dieutri.vn_HoHap_6-12-2011_S1826_Phu-phoi.htm\"></a></span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Quản l&yacute; căng thẳng.</span></b><span style=\"font-family: Arial;\"> Để giảm nguy cơ bệnh tim, cố gắng giảm bớt mức độ căng thẳng. Suy nghĩ lại th&oacute;i quen nghiện l&agrave;m việc v&agrave; t&igrave;m c&aacute;ch l&agrave;nh mạnh để giảm thiểu hoặc đối ph&oacute; với c&aacute;c sự kiện căng thẳng trong cuộc sống.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ngăn chặn HAPE</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu đi du lịch hoặc l&ecirc;n độ cao, th&iacute;ch nghi cho m&igrave;nh từ từ. Mặc d&ugrave; c&aacute;c khuyến nghị kh&aacute;c nhau, hầu hết c&aacute;c chuy&ecirc;n gia tư vấn cho tăng dần kh&ocirc;ng qu&aacute; 1.000 hoặc 2.000 feet (300-600 m&eacute;t) một ng&agrave;y khi tới 8.000 feet (khoảng 2.400 m&eacute;t). Ngo&agrave;i ra, điều quan trọng l&agrave; uống nhiều nước. L&ecirc;n cao hơn thở nhanh hơn, c&oacute; nghĩa l&agrave; bị mất số lớn hơn nước trong kh&ocirc;ng kh&iacute; thở ra từ phổi. Cuối c&ugrave;ng, mặc d&ugrave; sức khỏe tốt sẽ kh&ocirc;ng nhất thiết phải ngăn chặn HAPE, mọi người trong t&igrave;nh trạng tốt c&oacute; xu hướng &iacute;t căng thẳng hơn ở độ cao lớn. 12-72 giờ trước khi đi du lịch đến một độ cao, thuốc acetazolamide (Diamox) c&oacute; thể gi&uacute;p ngăn ngừa HAPE. H&atilde;y xem x&eacute;t tiếp tục d&ugrave;ng thuốc cho một v&agrave;i ng&agrave;y bổ sung nếu c&oacute; dấu hiệu của bệnh độ cao, đặc biệt l&agrave; đau đầu hay mất ngủ xảy ra.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: right;\"><b style=\"\"><i style=\"\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(128, 0, 0);\"></span></span></i></b></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">&nbsp;</span></p></span>\n', 1);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(12, 'Tạo đờm đường hô hấp do virus', '\n   <h1 class=\"title\">Tạo đờm đường hô hấp do virus</h1>   <span class=\"des\">Virus tạo đờm đường hô hấp (RSV) là một loại vi trùng gây nhiễm trùng ở phổi và đường hô hấp. Nó rất phổ biến mà hầu hết trẻ em được 2 tuổi đã bị nhiễm vi-rút. Virus hô hấp tạo đàm cũng có thể lây nhiễm ở người lớn.</span>\n   <span><p style=\"text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Virus tạo đờm đường h&ocirc; hấp (RSV) l&agrave; một loại vi tr&ugrave;ng g&acirc;y nhiễm tr&ugrave;ng ở phổi v&agrave; đường h&ocirc; hấp. N&oacute; rất phổ biến m&agrave; hầu hết trẻ em được 2 tuổi đ&atilde; bị nhiễm vi-r&uacute;t. Virus h&ocirc; hấp tạo đ&agrave;m cũng c&oacute; thể l&acirc;y nhiễm ở người lớn.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Ở người lớn tuổi v&agrave; trẻ em khỏe mạnh, c&aacute;c triệu chứng của virus đường h&ocirc; hấp tạo đ&agrave;m đều nhẹ v&agrave; thường bắt chước cảm lạnh th&ocirc;ng thường. C&aacute;c biện ph&aacute;p tự chăm s&oacute;c thường tất cả l&agrave; cần thiết để l&agrave;m giảm sự kh&oacute; chịu.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Nhiễm virus đường h&ocirc; hấp c&oacute; thể tạo đ&agrave;m nghi&ecirc;m trọng ở một số trường hợp, đặc biệt l&agrave; ở trẻ sinh non v&agrave; trẻ sơ sinh với điều kiện sức khỏe tiềm ẩn. RSV cũng c&oacute; thể trở th&agrave;nh nghi&ecirc;m trọng ở người lớn tuổi v&agrave; người lớn bị bệnh tim v&agrave; phổi.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Phổ biến, &yacute; thức ph&ograve;ng ngừa c&oacute; thể gi&uacute;p ngăn ngừa sự l&acirc;y lan của virus đường h&ocirc; hấp tạo đ&agrave;m. </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">C&aacute;c dấu hiệu v&agrave; triệu chứng của nhiễm tr&ugrave;ng virus đường h&ocirc; hấp tạo đ&agrave;m thường xuất hiện khoảng 4-6 ng&agrave;y sau khi tiếp x&uacute;c với virus. Ở người lớn v&agrave; trẻ lớn hơn, thường g&acirc;y ra dấu hiệu RSV nhẹ giống như cảm lạnh v&agrave; c&aacute;c triệu chứng. Ch&uacute;ng bao gồm:</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Tắc nghẽn hoặc chảy nước mũi.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Ho khan.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Sốt nhẹ.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Đau họng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Đau đầu nhẹ.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Cảm gi&aacute;c chung kh&oacute; chịu.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Trong trường hợp nghi&ecirc;m trọng</span><span lang=\"EN-US\" style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Virus tạo đờm đường h&ocirc; hấp c&oacute; thể dẫn đến bệnh đường h&ocirc; hấp dưới như vi&ecirc;m phổi hoặc vi&ecirc;m tiểu phế quản - một t&igrave;nh trạng vi&ecirc;m của c&aacute;c đoạn đường dẫn kh&iacute; nhỏ v&agrave;o phổi. C&aacute;c dấu hiệu v&agrave; triệu chứng c&oacute; thể bao gồm:</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Sốt cao.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Ho nặng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Thở kh&ograve; kh&egrave; - một tiếng the th&eacute; thường nghe hơi thở ra (thở ra). </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Thở nhanh hoặc kh&oacute; thở, c&oacute; thể l&agrave;m cho trẻ th&iacute;ch ngồi l&ecirc;n hơn l&agrave; nằm xuống.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">M&agrave;u xanh của da do thiếu oxy (xanh t&iacute;m).</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Trẻ sơ sinh l&agrave; nghi&ecirc;m trọng nhất ảnh hưởng bởi RSV. R&otilde; rệt co k&eacute;o cơ ngực của v&agrave; da giữa xương sườn, chỉ ra rằng đang gặp kh&oacute; khăn khi thở, v&agrave; hơi thở c&oacute; thể ngắn, n&ocirc;ng v&agrave; nhanh ch&oacute;ng. C&oacute; thể ho hoặc c&oacute; thể hiển thị &iacute;t, nếu c&oacute;, dấu hiệu của nhiễm tr&ugrave;ng đường h&ocirc; hấp, sẽ ăn k&eacute;m v&agrave; bất thường h&ocirc;n m&ecirc; v&agrave; c&aacute;u kỉnh.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Hầu hết trẻ em v&agrave; người lớn phục hồi từ c&aacute;c bệnh trong t&aacute;m đến 15 ng&agrave;y. Nhưng ở trẻ sơ sinh, trẻ sinh non hoặc người lớn c&oacute; vấn đề tim hoặc phổi m&atilde;n t&iacute;nh, virus c&oacute; thể g&acirc;y ra nghi&ecirc;m trọng hơn - đ&ograve;i hỏi phải nhập viện, đ&ocirc;i khi đe dọa t&iacute;nh mạng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Đến gặp b&aacute;c sĩ khi</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Hầu hết c&aacute;c trường hợp nhiễm virus đường h&ocirc; hấp tạo đ&agrave;m kh&ocirc;ng đe dọa t&iacute;nh mạng. Tuy nhi&ecirc;n, t&igrave;m tư vấn y tế cho trẻ em, người cao ni&ecirc;n hoặc người kh&aacute;c c&oacute; nguy cơ cao, những người trải nghiệm bất kỳ dấu hiệu v&agrave; triệu chứng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">T&igrave;m kiếm sự chăm s&oacute;c y tế ngay lập tức nếu những trải nghiệm kh&oacute; thở, sốt cao hoặc chuyển da m&agrave;u xanh t&iacute;m. </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Virus h&ocirc; hấp tạo đ&agrave;m v&agrave;o cơ thể th&ocirc;ng qua mũi, mắt hoặc miệng. N&oacute; l&acirc;y lan dễ d&agrave;ng khi truyền nhiễm tiết đường h&ocirc; hấp - chẳng hạn như từ ho hoặc hắt hơi - được h&iacute;t v&agrave;o hay l&acirc;y sang người kh&aacute;c qua tiếp x&uacute;c trực tiếp, chẳng hạn như bắt tay. C&aacute;c virus cũng c&oacute; thể sống h&agrave;ng giờ tr&ecirc;n c&aacute;c đối tượng như mặt b&agrave;n v&agrave; đồ chơi. Miệng, mũi hoặc mắt sau khi chạm v&agrave;o một đối tượng bị &ocirc; nhiễm, v&agrave; c&oacute; thể c&oacute; virus.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Một người bị nhiễm bệnh nhất l&acirc;y nhiễm trong v&agrave;i ng&agrave;y đầu sau khi nhiễm bệnh, nhưng h&ocirc; hấp tạo đ&agrave;m virus c&oacute; thể l&acirc;y lan cho đến một v&agrave;i tuần sau khi bắt đầu nhiễm tr&ugrave;ng. </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Ở 2 tuổi, hầu hết trẻ em sẽ bị nhiễm virus tạo đ&agrave;m hệ h&ocirc; hấp. Trẻ em ở c&aacute;c trung t&acirc;m chăm s&oacute;c trẻ em hoặc những người c&oacute; anh chị em đi học c&oacute; nguy cơ phơi nhiễm cao hơn. V&igrave; vậy, trẻ sơ sinh tiếp x&uacute;c với mức độ cao của &ocirc; nhiễm kh&ocirc;ng kh&iacute; hoặc kh&oacute;i thuốc l&aacute;. T&iacute;nh nhạy cảm cũng lớn hơn trong m&ugrave;a cao điểm RSV, thường bắt đầu v&agrave;o m&ugrave;a thu v&agrave; kết th&uacute;c v&agrave;o m&ugrave;a xu&acirc;n.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Những người c&oacute; nguy cơ nghi&ecirc;m trọng - đ&ocirc;i khi đe dọa t&iacute;nh mạng - nhiễm tr&ugrave;ng bao gồm: </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Trẻ em dưới 6 th&aacute;ng tuổi.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Trẻ em, đặc biệt l&agrave; dưới 1 năm tuổi, những người bị sinh non hoặc những người c&oacute; điều kiện cơ bản, chẳng hạn như tim bẩm sinh hoặc bệnh phổi.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Trẻ em c&oacute; hệ thống miễn dịch yếu, chẳng hạn như những h&oacute;a trị trải qua hoặc cấy gh&eacute;p.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Người lớn tuổi.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Người lớn bị suy tim sung huyết hay bệnh phổi tắc nghẽn.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Những người bị suy giảm miễn dịch, bao gồm cả những người c&oacute; HIV / AIDS.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">C&aacute;c biến chứng của virus tạo đ&agrave;m về đường h&ocirc; hấp bao gồm: </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Nhập viện.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Khi bị nhiễm tr&ugrave;ng virus đường h&ocirc; hấp tạo đ&agrave;m g&acirc;y bệnh nặng, c&oacute; thể phải nhập viện để b&aacute;c sĩ c&oacute; thể theo d&otilde;i v&agrave; điều trị c&aacute;c vấn đ��� về h&ocirc; hấp v&agrave; cho ti&ecirc;m tĩnh mạch (IV). Hầu hết nguy cơ nhập viện l&agrave; trẻ em dưới 6 th&aacute;ng tuổi, em b&eacute; sinh non, v&agrave; trẻ sơ sinh c&oacute; tim bẩm sinh hoặc bệnh phổi.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Vi&ecirc;m phổi hay vi&ecirc;m tiểu phế quản.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Khi di chuyển virus đường h&ocirc; hấp tạo đ&agrave;m từ đường h&ocirc; hấp tr&ecirc;n đến đường h&ocirc; hấp dưới, vi&ecirc;m phổi hoặc vi&ecirc;m tiểu phế quản c&oacute; thể kết quả. Biến chứng n&agrave;y c&oacute; thể kh&aacute; nghi&ecirc;m trọng ở trẻ sơ sinh, trẻ nhỏ, c&aacute;c c&aacute; nh&acirc;n suy giảm miễn dịch hoặc những người c&oacute; bệnh tim m&atilde;n t&iacute;nh hoặc bệnh phổi.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Vi&ecirc;m tai giữa.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Khi c&aacute;c vi sinh vật x&acirc;m nhập v&agrave;o kh&ocirc;ng gian ph&iacute;a sau m&agrave;ng nhĩ, nhiễm tr&ugrave;ng tai giữa (vi&ecirc;m tai giữa) c&oacute; thể kết quả. Điều n&agrave;y xảy ra thường xuy&ecirc;n nhất ở trẻ sơ sinh v&agrave; trẻ nhỏ.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Suyễn.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> C&oacute; thể c&oacute; li&ecirc;n kết giữa virus đường h&ocirc; hấp tạo đ&agrave;m nặng v&agrave; cơ hội ph&aacute;t triển bệnh hen suyễn sau n&agrave;y.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Định kỳ nhiễm tr&ugrave;ng.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Khi đ&atilde; bị nhiễm virus, l&agrave; phổ biến trải nghiệm để thỉnh thoảng t&aacute;i ph&aacute;t RSV trong suốt cuộc đời, thường l&agrave; dưới h&igrave;nh thức cảm lạnh th&ocirc;ng thường. Mặc d&ugrave; nhiễm tr&ugrave;ng sau đ&oacute; thường l&agrave; kh&ocirc;ng nặng, c&oacute; thể nghi&ecirc;m trọng ở người lớn tuổi hoặc những người c&oacute; tr&aacute;i tim m&atilde;n t&iacute;nh hoặc bệnh phổi.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">B&aacute;c sĩ c&oacute; thể nghi ngờ virus đường h&ocirc; hấp tạo đ&agrave;m dựa tr&ecirc;n kiểm tra thể chất v&agrave; thời gian của nhiễm tr&ugrave;ng. Trong suốt kỳ kiểm tra, c&oacute; thể nghe phổi với ống nghe để kiểm tra thở kh&ograve; kh&egrave; hoặc c&aacute;c &acirc;m thanh bất thường kh&aacute;c.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">B&aacute;c sĩ cũng c&oacute; thể sử dụng: </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Phương ph&aacute;p đo xung oxy để kiểm tra xem mức độ oxy c&oacute; sẵn trong m&aacute;u thấp hơn b&igrave;nh thường.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">X&eacute;t nghiệm m&aacute;u để kiểm tra số lượng tế b&agrave;o m&agrave;u trắng hoặc để t&igrave;m sự hiện diện của virus, vi khuẩn hoặc c&aacute;c sinh vật kh&aacute;c.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Chụp X - quang để kiểm tra vi&ecirc;m phổi.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">X&eacute;t nghiệm c&aacute;c chất tiết đường h&ocirc; hấp từ mũi để kiểm tra virus.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Thuốc kh&aacute;ng sinh m&agrave; b&aacute;c sĩ k&ecirc; toa để điều trị nhiễm tr&ugrave;ng do vi khuẩn, l&agrave; kh&ocirc;ng c&oacute; sử dụng chống lại virus tạo đ&agrave;m về đường h&ocirc; hấp v&igrave; đ&acirc;y l&agrave; nhiễm virus. Tuy nhi&ecirc;n, b&aacute;c sĩ c&oacute; thể k&ecirc; toa thuốc kh&aacute;ng sinh nếu c&oacute; một biến chứng của vi khuẩn, chẳng hạn như nhiễm tr&ugrave;ng tai giữa hoặc vi&ecirc;m phổi do vi khuẩn.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Nếu kh&ocirc;ng, c&oacute; thể đề nghị một loại thuốc kh&ocirc;ng k&ecirc; toa, chẳng hạn như acetaminophen (Tylenol, những loại kh&aacute;c) hoặc ibuprofen (Advil, Motrin, những loại kh&aacute;c), t&ugrave;y thuộc v&agrave;o độ tuổi của trẻ. Những loại thuốc giảm sốt nhưng sẽ kh&ocirc;ng chữa trị c&aacute;c nhiễm tr&ugrave;ng hoặc l&agrave;m n&oacute; biến mất sớm hơn.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Điều trị trường hợp nặng</span><span lang=\"EN-US\" style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Trong trường hợp nhiễm tr&ugrave;ng nặng, nhập viện c&oacute; thể cần thiết để cung cấp đường tĩnh mạch (IV) chất lỏng v&agrave; &ocirc;xy ẩm. Trẻ sơ sinh v&agrave; trẻ em nhập viện cũng c&oacute; thể được nối với th&ocirc;ng kh&iacute; cơ học - một m&aacute;y thở - để dễ thở.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Trong một số trường hợp nặng, thuốc gi&atilde;n phế quản kh&iacute; dung như albuterol (Proventil, Ventolin) c&oacute; thể được sử dụng để l&agrave;m giảm thở kh&ograve; kh&egrave;. Thuốc n&agrave;y sẽ mở đường dẫn kh&iacute; trong phổi. Kh&iacute; dung c&oacute; nghĩa l&agrave; n&oacute; được quản l&yacute; như một m&agrave;n sương. Đ&ocirc;i khi, một dạng kh&iacute; dung ribavirin (Rebetol), một t&aacute;c nh&acirc;n kh&aacute;ng vi-r&uacute;t, c&oacute; thể được sử dụng. B&aacute;c sĩ cũng c&oacute; thể đề nghị ti&ecirc;m epinephrine hoặc h&igrave;nh thức của epinephrine c&oacute; thể được h&iacute;t v&agrave;o th&ocirc;ng qua một m&aacute;y phun sương (racemic epinephrine) để l&agrave;m giảm triệu chứng của nhiễm RSV.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phong c&aacute;ch sống v&agrave; biện ph&aacute;p khắc phục</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">C&oacute; thể kh&ocirc;ng c&oacute; khả năng r&uacute;t ngắn thời gian bị nhiễm virus đường h&ocirc; hấp tạo đ&agrave;m, nhưng c&oacute; thể thử để l&agrave;m giảm một số dấu hiệu v&agrave; triệu chứng. </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Nếu c&oacute; nhiễm tr&ugrave;ng, l&agrave;m tốt nhất để an ủi hoặc ph&acirc;n t&acirc;m người đ&oacute; - &ocirc;m ấp, đọc một cuốn s&aacute;ch hay chơi một tr&ograve; chơi y&ecirc;n tĩnh. Thủ thuật kh&aacute;c để l&agrave;m giảm triệu chứng bao gồm:</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Tạo kh&ocirc;ng kh&iacute; ẩm để thở.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Giữ ph&ograve;ng của đứa trẻ ấm nhưng kh&ocirc;ng qu&aacute; n&oacute;ng. Nếu kh&ocirc;ng kh&iacute; kh&ocirc;, sương ẩm m&aacute;t hoặc vaporizer c&oacute; thể l&agrave;m ẩm kh&ocirc;ng kh&iacute; v&agrave; gi&uacute;p giảm bớt tắc nghẽn v&agrave; ho. H&atilde;y chắc chắn để giữ cho độ ẩm trong sạch, để ngăn chặn sự ph&aacute;t triển của vi khuẩn v&agrave; nấm mốc. Một độ ẩm trong nh&agrave; l&yacute; tưởng l&agrave; khoảng 50 phần trăm.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Ngồi trong một vị tr&iacute; thẳng đứng.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Để dễ d&agrave;ng trẻ sơ sinh thở, một chỗ ngồi tương tự hoặc thẳng đứng. Như thường lệ, em b&eacute; nằm ngửa v&agrave;o nệm để ngủ, nhưng đặt khối người, đứng đầu của nệm để n&acirc;ng cao n&oacute; khoảng 3 inch (7,6 cm).</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Uống nước.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Nước ấm, như một m&oacute;n s&uacute;p y&ecirc;u th&iacute;ch, c&oacute; thể nhẹ nh&agrave;ng v&agrave; gi&uacute;p nới lỏng tiết d&agrave;y. Giữ cho con b&uacute; hoặc chai nu&ocirc;i trẻ sơ sinh như b&igrave;nh thường. Dinh dưỡng l&agrave; rất quan trọng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">H&atilde;y thử giọt nước muối mũi.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Để giảm bớt tắc nghẽn, ngay cả đối với trẻ nhỏ. Nhỏ một v&agrave;i giọt để nới lỏng nhầy mũi cứng, sau đ&oacute; ngay lập tức h&uacute;t lỗ mũi, bằng c&aacute;ch sử dụng một ống h&uacute;t tr&ograve;n. Lặp lại qu&aacute; tr&igrave;nh trong lỗ mũi kh&aacute;c. Thời gian tốt để l&agrave;m điều n&agrave;y l&agrave; trước khi ăn v&agrave; trước khi cho b&eacute; nằm xuống ngủ.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Sử dụng thuốc giảm đau đối ứng qu&aacute; mức.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Thuốc giảm đau OTC như acetaminophen (Tylenol, những loại kh&aacute;c) c&oacute; thể gi&uacute;p giảm sốt, giảm đau họng v&agrave; cải thiện khả năng để uống nước. Kiểm tra với b&aacute;c sĩ về sử dụng ph&ugrave; hợp với lứa tuổi v&agrave; liều lượng của thuốc đ&oacute;. Kh&ocirc;ng sử dụng ch&uacute;ng để đ&agrave;n &aacute;p sốt dưới 100,9 F (38,3 C).</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Loại bỏ phơi nhiễm với kh&oacute;i thuốc l&aacute;.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Tr&aacute;nh xa kh&oacute;i thuốc l&aacute; v&igrave; n&oacute; c&oacute; thể l&agrave;m nặng th&ecirc;m c&aacute;c triệu chứng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Ph&ograve;ng chống</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Kh&ocirc;ng c&oacute; vắc-xin cho virus tạo đ&agrave;m hệ h&ocirc; hấp. Nhưng th&ocirc;ng thường, &yacute; thức ph&ograve;ng ngừa c&oacute; thể gi&uacute;p ngăn ngừa sự l&acirc;y lan của bệnh n&agrave;y:</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Rửa tay thường xuy&ecirc;n.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> L&agrave;m như vậy đặc biệt l&agrave; trước khi chạm v&agrave;o em b&eacute;, v&agrave; dạy cho con c&aacute;i quan trọng của việc rửa tay.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Tr&aacute;nh tiếp x&uacute;c.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Hạn chế cho trẻ sơ sinh li&ecirc;n hệ với những người c&oacute; sốt hoặc cảm lạnh. Điều n&agrave;y đặc biệt quan trọng ở trẻ sinh non v&agrave; tất cả trẻ sơ sinh trong những th&aacute;ng đầu ti&ecirc;n 2 năm của cuộc sống.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Giữ mọi thứ sạch sẽ.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> L&agrave;m sạch b&agrave;n trong nh&agrave; bếp v&agrave; ph&ograve;ng tắm, đặc biệt l&agrave; khi ai đ&oacute; trong gia đ&igrave;nh c&oacute; cảm lạnh.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Kh&ocirc;ng uống chia sẻ với người kh&aacute;c.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Sử dụng k&iacute;nh ri&ecirc;ng hoặc ly d&ugrave;ng một lần khi hoặc ai đ&oacute; đang bị bệnh.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Kh&ocirc;ng h&uacute;t thuốc.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Trẻ sơ sinh tiếp x&uacute;c với kh&oacute;i thuốc l&aacute; c&oacute; nguy cơ cao nhiễm RSV v&agrave; c&aacute;c triệu chứng nặng hơn c&oacute; khả năng. Nếu kh&ocirc;ng h&uacute;t thuốc, kh&ocirc;ng bao giờ l&agrave;m như vậy trong nh&agrave; hoặc xe hơi.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Rửa đồ chơi thường xuy&ecirc;n.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> L&agrave;m điều n&agrave;y đặc biệt l&agrave; khi con hoặc bạn c&ugrave;ng một bệnh.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thuốc bảo vệ</span><span lang=\"EN-US\" style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Ngo&agrave;i ra, palivizumab (Synagis) c&oacute; thể gi&uacute;p bảo vệ trẻ em dưới 2 tuổi c&oacute; nguy cơ cao bị biến chứng nghi&ecirc;m trọng khi nhận được RSV, như sinh non hoặc với tim bẩm sinh hoặc bệnh phổi. </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Synagis hoạt động bằng c&aacute;ch cung cấp c&aacute;c kh&aacute;ng thể cần thiết để bảo vệ chống lại virus tạo đ&agrave;m hệ h&ocirc; hấp. N&oacute; đ&ograve;i hỏi ti&ecirc;m h&agrave;ng th&aacute;ng v&agrave;o c&aacute;c m&ocirc; của cơ đ&ugrave;i trong m&ugrave;a RSV cao điểm, bắt đầu v&agrave;o m&ugrave;a thu v&agrave; tiếp tục cho khoảng năm th&aacute;ng (thường l&agrave; th&aacute;ng mười một đến th&aacute;ng tư). Ti&ecirc;m lặp đi lặp lại h&agrave;ng năm cho đến khi trẻ kh&ocirc;ng c&ograve;n c&oacute; nguy cơ cao. C&aacute;c thuốc kh&ocirc;ng can thiệp với vắc-xin ở trẻ em.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Sử dụng điều trị n&agrave;y l&agrave;m giảm tần suất v&agrave; thời gian nằm viện cho bệnh nhiễm tr&ugrave;ng đường h&ocirc; hấp virus tạo đ&agrave;m. Nhưng chi ph&iacute; cao của thuốc giới hạn sử dụng của n&oacute; cho những người c&oacute; nguy cơ cao bị biến chứng từ nhiễm RSV. Thuốc kh&ocirc;ng phải l&agrave; hữu &iacute;ch trong điều trị nhiễm tr&ugrave;ng virus đường h&ocirc; hấp tạo đ&agrave;m khi n&oacute; đ&atilde; ph&aacute;t triển. N&oacute;i chuyện với b&aacute;c sĩ nếu nghĩ rằng c&oacute; thể hội đủ điều kiện điều trị n&agrave;y.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">C&aacute;c nh&agrave; khoa học đang l&agrave;m việc để t&igrave;m một loại vắc xin chống lại virus kh&ocirc;ng chỉ tạo đ&agrave;m về đường h&ocirc; hấp ở trẻ sơ sinh m&agrave; c&ograve;n ở người lớn tuổi v&agrave; người lớn c&oacute; nguy cơ cao.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: right;\"><b style=\"\"><i style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\"><span style=\"color: rgb(128, 0, 0);\"> </span></span></i></b></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\">&nbsp;</p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">&nbsp;</span></p></span>\n', 1);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(13, 'Tràn khí màng phổi', '\n   <h1 class=\"title\">Tràn khí màng phổi</h1>   <span class=\"des\">Tràn khí màng phổi có thể được gây ra bởi chấn thương ngực, một số thủ tục y tế liên quan đến phổi, bệnh phổi, hoặc nó có thể xảy ra không có lý do rõ ràng.</span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tr&agrave;n kh&iacute; m&agrave;ng phổi (một thuật ngữ cho phổi bị sụp đổ) xảy ra khi r&ograve; rỉ kh&ocirc;ng kh&iacute; v&agrave;o kh&ocirc;ng gian giữa phổi v&agrave; lồng ngực, tạo ra &aacute;p lực đối với phổi. T&ugrave;y thuộc v&agrave;o nguy&ecirc;n nh&acirc;n g&acirc;y tr&agrave;n kh&iacute; m&agrave;ng phổi n&agrave;y, phổi c&oacute; thể chỉ một phần sụp đổ, hoặc n&oacute; c&oacute; thể sụp đổ ho&agrave;n to&agrave;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tr&agrave;n kh&iacute; m&agrave;ng phổi c&oacute; thể được g&acirc;y ra bởi chấn thương ngực, một số thủ tục y tế li&ecirc;n quan đến phổi, bệnh phổi, hoặc n&oacute; c&oacute; thể xảy ra kh&ocirc;ng c&oacute; l&yacute; do r&otilde; r&agrave;ng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tr&agrave;n kh&iacute; m&agrave;ng phổi nhỏ, kh&ocirc;ng biến chứng nhanh ch&oacute;ng c&oacute; thể tự chữa l&agrave;nh, nhưng khi tr&agrave;n kh&iacute; m&agrave;ng phổi lớn hơn, kh&ocirc;ng kh&iacute; vượt qu&aacute; thường gỡ bỏ bằng c&aacute;ch ch&egrave;n một ống hoặc kim giữa c&aacute;c xương sườn v&agrave; từ từ loại bỏ kh&ocirc;ng kh&iacute; trong một v&agrave;i ng&agrave;y. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c dấu hiệu v&agrave; triệu chứng tr&agrave;n kh&iacute; m&agrave;ng phổi thường bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đột ngột, đau ngực sắc n&eacute;t tr&ecirc;n c&ugrave;ng một b&ecirc;n phổi bị ảnh hưởng - đau n&agrave;y kh&ocirc;ng xảy ra ở trung t&acirc;m của ngực, dưới xương v&uacute;. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở, c&oacute; thể nhiều hơn hoặc &iacute;t nghi&ecirc;m trọng, t&ugrave;y thuộc v&agrave;o bao nhi&ecirc;u phổi bị sụp đổ. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một cảm gi&aacute;c tức ngực. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một nhịp tim nhanh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu chỉ một lượng nhỏ kh&ocirc;ng kh&iacute; đi v&agrave;o kh&ocirc;ng gian giữa phổi v&agrave; lồng ngực (kh&ocirc;ng gian m&agrave;ng phổi), c&oacute; thể c&oacute; v&agrave;i dấu hiệu hoặc triệu chứng. Tuy nhi&ecirc;n, ngay cả phổi hơi bị sụp đổ c&oacute; thể g&acirc;y ra một số đau ngực v&agrave; kh&oacute; thở, một số từ từ cải thiện trong một v&agrave;i giờ đến một ng&agrave;y hoặc l&acirc;u hơn, thậm ch&iacute; nếu kh&ocirc;ng c&oacute; giảm k&iacute;ch thước của sự sụp đổ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Đến gặp b&aacute;c sĩ khi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đi kh&aacute;m b&aacute;c sĩ ngay nếu c&oacute; đau ngực v&agrave; kh&oacute; thở đột ngột. Nhiều điều kiện kh&aacute;c với tr&agrave;n kh&iacute; m&agrave;ng phổi c&oacute; thể g&acirc;y ra những triệu chứng n&agrave;y, v&agrave; hầu hết y&ecirc;u cầu một chẩn đo&aacute;n ch&iacute;nh x&aacute;c v&agrave; điều trị kịp thời. Nếu cơn đau ngực l&agrave; nặng hay hơi thở trở n&ecirc;n ng&agrave;y c&agrave;ng kh&oacute; khăn, được chăm s&oacute;c cấp cứu ngay lập tức.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Phổi v&agrave; lồng ngực l&agrave; đ&agrave;n hồi. Khi h&iacute;t v&agrave;o v&agrave; thở ra, kh&iacute; v&agrave;o trong phổi trong khi ngực mở rộng ra ngo&agrave;i. Hai lực đối lập tạo ra một &aacute;p suất &acirc;m trong kh&ocirc;ng gian giữa c&aacute;c khung sườn v&agrave; phổi. Khi kh&ocirc;ng kh&iacute; đi v&agrave;o, hoặc l&agrave; từ b&ecirc;n trong hoặc b&ecirc;n ngo&agrave;i phổi, &aacute;p lực m&agrave; n&oacute; t&aacute;c động c&oacute; thể g&acirc;y ra tất cả hoặc một phần của phổi bị ảnh hưởng đến sự sụp đổ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; một số loại tr&agrave;n kh&iacute; m&agrave;ng phổi, được x&aacute;c định theo những g&igrave; g&acirc;y ra: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tự ph&aacute;t tr&agrave;n kh&iacute; m&agrave;ng phổi nhỏ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tr&agrave;n kh&iacute; m&agrave;ng phổi tự ph&aacute;t được cho l&agrave; ph&aacute;t triển khi một vỉ kh&ocirc;ng kh&iacute; nhỏ (bọt) tr&ecirc;n đầu của phổi vỡ. L&agrave; do sự yếu k&eacute;m trong m&ocirc; phổi v&agrave; c&oacute; thể vỡ ra từ những thay đổi &aacute;p suất kh&ocirc;ng kh&iacute; khi đang lặn biển, bay, leo n&uacute;i hay, theo một số b&aacute;o c&aacute;o - nghe nhạc cực kỳ lớn. Ngo&agrave;i ra, tr&agrave;n kh&iacute; m&agrave;ng phổi tự ph&aacute;t c&oacute; thể xảy ra trong khi h&uacute;t cần sa, sau khi h&iacute;t s&acirc;u, sau đ&oacute; thở ra chậm với một phần đ&oacute;ng m&ocirc;i. Nhưng phổ biến nhất, vỡ kh&ocirc;ng c&oacute; l&yacute; do r&otilde; r&agrave;ng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Yếu tố di truyền c&oacute; thể đ&oacute;ng một vai tr&ograve; trong tr&agrave;n kh&iacute; m&agrave;ng phổi tự ph&aacute;t ch&iacute;nh v&igrave; điều kiện n&agrave;y c&oacute; thể trong gia đ&igrave;nh. Tr&agrave;n kh&iacute; m&agrave;ng phổi tự ph&aacute;t ch&iacute;nh thường l&agrave; nhẹ v&igrave; &aacute;p lực từ c&aacute;c phần của phổi bị sụp đổ lần lượt theo th&aacute;ng năm.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tr&agrave;n kh&iacute; m&agrave;ng phổi tự ph&aacute;t thứ cấp</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ph&aacute;t triển ở những người đ&atilde; c&oacute; một chứng rối loạn phổi, đặc biệt l&agrave; kh&iacute; phế thũng, thiệt hại dần dần phổi. C&aacute;c điều kiện kh&aacute;c c&oacute; thể dẫn đến tr&agrave;n kh&iacute; m&agrave;ng phổi tự ph&aacute;t thứ cấp bao gồm c&aacute;c bệnh lao, vi&ecirc;m phổi, xơ h&oacute;a nang v&agrave; bệnh ung thư phổi. Trong những trường hợp n&agrave;y, tr&agrave;n kh&iacute; m&agrave;ng phổi xảy ra v&igrave; c&aacute;c m&ocirc; phổi bệnh b&ecirc;n cạnh kh&ocirc;ng gian m&agrave;ng phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tự ph&aacute;t tr&agrave;n kh&iacute; m&agrave;ng phổi c&oacute; thể nặng hơn v&agrave; thậm ch&iacute; đe dọa t&iacute;nh mạng v&igrave; m&ocirc; bệnh c&oacute; thể mở một lỗ rộng hơn, cho ph&eacute;p kh&ocirc;ng kh&iacute; nhiều hơn v&agrave;o kh&ocirc;ng gian m&agrave;ng phổi hơn so với kh&ocirc;ng một vỡ nhỏ. Ngo&agrave;i ra, người bị bệnh phổi đ&atilde; giảm dự trữ phổi, l&agrave;m suy giảm chức năng phổi bất kỳ nghi&ecirc;m trọng hơn. Tr&agrave;n kh&iacute; m&agrave;ng phổi tự ph&aacute;t hầu như lu&ocirc;n lu&ocirc;n đ&ograve;i hỏi phải c&oacute; ống dẫn lưu ngực để điều trị.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tr&agrave;n kh&iacute; m&agrave;ng phổi sau chấn thương </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bất kỳ vết thương hay chấn thương ngực c&oacute; thể g&acirc;y ra sự sụp đổ phổi. Dao v&agrave; những vết thương do đạn bắn, một đ&ograve;n đ&aacute;nh v&agrave;o ngực, ngay cả một t&uacute;i kh&iacute; được triển khai c&oacute; thể g&acirc;y ra tr&agrave;n kh&iacute; m&agrave;ng phổi. V&igrave; vậy, c&oacute; thể bị thương v&ocirc; t&igrave;nh xảy ra trong thủ tục y tế nhất định, chẳng hạn như ống lồng ngực, hồi sức tim phổi (CPR), v&agrave; sinh thiết phổi hoặc gan.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tr&agrave;n kh&iacute; m&agrave;ng phổi căng </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c loại nghi&ecirc;m trọng nhất của tr&agrave;n kh&iacute; m&agrave;ng phổi, điều n&agrave;y xảy ra khi &aacute;p suất trong kh&ocirc;ng gian m&agrave;ng phổi lớn hơn &aacute;p suất kh&iacute; quyển, hoặc v&igrave; kh&ocirc;ng kh&iacute; bị giữ lại trong kh&ocirc;ng gian m&agrave;ng phổi hoặc v&igrave; kh&ocirc;ng kh&iacute; đi v&agrave;o từ một &aacute;p lực dương t&iacute;nh th&ocirc;ng kh&iacute; cơ học. C&aacute;c lực của kh&ocirc;ng kh&iacute; c&oacute; thể g&acirc;y ra ảnh hưởng đến phổi sụp đổ ho&agrave;n to&agrave;n. N&oacute; cũng c&oacute; thể đẩy về ph&iacute;a tim phổi kh&ocirc;ng xẹp. Tr&agrave;n kh&iacute; m&agrave;ng phổi căng thẳng đến đột ngột, tiến triển nhanh ch&oacute;ng v&agrave; g&acirc;y tử vong nếu kh&ocirc;ng được điều trị nhanh ch&oacute;ng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Yếu tố nguy cơ tr&agrave;n kh&iacute; m&agrave;ng phổi bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Giới t&iacute;nh.</span></b><span style=\"font-family: Arial;\"> N&oacute;i chung, đ&agrave;n &ocirc;ng nhiều khả năng c&oacute; tr&agrave;n kh&iacute; m&agrave;ng phổi hơn so với phụ nữ, mặc d&ugrave; phụ nữ c&oacute; thể ph&aacute;t triển một dạng hiếm của tr&agrave;n kh&iacute; m&agrave;ng phổi (catamenial tr&agrave;n kh&iacute; m&agrave;ng phổi) li&ecirc;n quan đến chu kỳ kinh nguyệt.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">H&uacute;t thuốc.</span></b><span style=\"font-family: Arial;\"> Đ&acirc;y l&agrave; yếu tố nguy cơ h&agrave;ng đầu cho tr&agrave;n kh&iacute; m&agrave;ng phổi tự ph&aacute;t ch&iacute;nh. Việc l&agrave;m tăng nguy cơ với độ d&agrave;i của thời gian v&agrave; số lượng thuốc l&aacute; h&uacute;t.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tuổi.</span></b><span style=\"font-family: Arial;\"> Tr&agrave;n kh&iacute; m&agrave;ng phổi tự ph&aacute;t c&oacute; thể xảy ra ở những người từ 20 đến 40 tuổi, đặc biệt l&agrave; nếu người đ&oacute; rất cao v&agrave; nhẹ c&acirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Bệnh phổi.</span></b><span style=\"font-family: Arial;\"> C&oacute; một bệnh phổi, đặc biệt l&agrave; kh&iacute; phế thũng, l&agrave;m cho phổi dễ bị sụp đổ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Lịch sử tr&agrave;n kh&iacute; m&agrave;ng phổi.</span></b><span style=\"font-family: Arial;\"> Nếu đ&atilde; c&oacute; một tr&agrave;n kh&iacute; m&agrave;ng phổi, c&oacute; nguy cơ gia tăng lần kh&aacute;c, thường l&agrave; trong v&ograve;ng 1 - 2 năm đầu ti&ecirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Biến chứng c&oacute; thể từ tr&agrave;n kh&iacute; m&agrave;ng phổi tự ph&aacute;t hoặc sau chấn thương bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">T&aacute;i ph&aacute;t.</span></b><span style=\"font-family: Arial;\"> Gần một nửa những người đ&atilde; c&oacute; tr&agrave;n kh&iacute; m&agrave;ng phổi c&oacute; t&aacute;i ph&aacute;t, thường l&agrave; trong v&ograve;ng ba năm đầu ti&ecirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Kh&ocirc;ng kh&iacute; li&ecirc;n tục bị r&ograve; rỉ.</span></b><span style=\"font-family: Arial;\"> Mặc d&ugrave; ống dẫn lưu được đưa v&agrave;o để h&uacute;t kh&ocirc;ng kh&iacute; ra, đ&ocirc;i khi kh&ocirc;ng kh&iacute; c&oacute; thể tiếp tục bị r&ograve; rỉ nếu mở trong phổi kh&ocirc;ng đ&oacute;ng. Sau v&agrave;i ng&agrave;y đến một tuần hoặc l&acirc;u hơn, n&oacute; c&oacute; thể cần phải phẫu thuật đ&oacute;ng r&ograve; rỉ kh&iacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c biến chứng của một căng tr&agrave;n kh&iacute; m&agrave;ng phổi nghi&ecirc;m trọng hơn v&agrave; bao gồm:</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Nồng độ &ocirc;xy trong m&aacute;u thấp (thiếu oxy).</span></b><span style=\"font-family: Arial;\"> Bởi v&igrave; một nguy&ecirc;n nh&acirc;n g&acirc;y tr&agrave;n kh&iacute; m&agrave;ng phổi g&acirc;y căng hoặc sụp đổ v&agrave; c&oacute; thể n&eacute;n phổi, kh&ocirc;ng kh&iacute; c&oacute; trong phổ &iacute;t hơn v&agrave; &iacute;t oxy đi v&agrave;o m&aacute;u. Kết quả l&agrave; oxy m&aacute;u thấp hơn so với oxy trong m&aacute;u b&igrave;nh thường. Thiếu oxy c&oacute; thể ph&aacute; vỡ hoạt động cơ thể cơ bản, v&agrave; ở mức độ thấp nghi&ecirc;m trọng c&oacute; thể đe dọa t&iacute;nh mạng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ch&egrave;n &eacute;p tim.</span></b><span style=\"font-family: Arial;\"> Nếu tiếp tục x&acirc;y dựng, &aacute;p lực ng&agrave;y c&agrave;ng tăng c&oacute; thể đẩy tim v&agrave; mạch m&aacute;u, n&eacute;n cả hai phổi khỏe mạnh v&agrave; tim. Tr&agrave;n kh&iacute; m&agrave;ng phổi căng c&oacute; thể ảnh hưởng đến sự trở lại của m&aacute;u đến tim v&agrave; dẫn đến một sự mất đột ngột của chức năng tim. Ch&egrave;n &eacute;p tim l&agrave; tử vong nếu kh&ocirc;ng được điều trị ngay lập tức.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Suy h&ocirc; hấp.</span></b><span style=\"font-family: Arial;\"> Điều n&agrave;y xảy ra khi mức độ oxy trong m&aacute;u giảm qu&aacute; thấp, v&agrave; mức độ của carbon dioxide trở th&agrave;nh qu&aacute; cao. Thấp oxy m&aacute;u nặng c&oacute; thể dẫn đến rối loạn nhịp tim v&agrave; bất tỉnh, v&agrave; mức carbon dioxide cao g&acirc;y lẫn lộn, buồn ngủ v&agrave; h&ocirc;n m&ecirc;. Cuối c&ugrave;ng, suy h&ocirc; hấp c&oacute; thể g&acirc;y tử vong.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Shock.</span></b><span style=\"font-family: Arial;\"> T&igrave;nh trạng n&agrave;y nguy kịch xảy ra khi huyết &aacute;p giảm xuống rất thấp v&agrave; c&aacute;c cơ quan quan trọng của cơ thể bị tước oxy v&agrave; chất dinh dưỡng. Shock l&agrave; một cấp cứu y tế lớn v&agrave; đ&ograve;i hỏi phải chăm s&oacute;c ngay lập tức.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tr&agrave;n kh&iacute; m&agrave;ng phổi thường được chẩn đo&aacute;n bằng chụp X-quang. C&aacute;c x&eacute;t nghiệm kh&aacute;c đ&ocirc;i khi được thực hiện, bao gồm:</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">M&aacute;y vi t&iacute;nh cắt lớp (CT scan).</span></b><span style=\"font-family: Arial;\"> CT l&agrave; một kỹ thuật X-ray sản xuất c&aacute;c h&igrave;nh ảnh chi tiết hơn X-quang th&ocirc;ng thường. Điều n&agrave;y c&oacute; thể được thực hiện nếu b&aacute;c sĩ nghi ngờ tr&agrave;n kh&iacute; m&agrave;ng phổi sau một thủ tục bụng hoặc ngực. CT scan c&oacute; thể gi&uacute;p x&aacute;c định xem liệu một bệnh tiềm ẩn c&oacute; thể g&acirc;y ra phổi sụp đổ - một c&aacute;i g&igrave; đ&oacute; m&agrave; c&oacute; thể kh&ocirc;ng hiển thị tr&ecirc;n một X-quang th&ocirc;ng thường.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">X&eacute;t nghiệm m&aacute;u.</span></b><span style=\"font-family: Arial;\"> C&oacute; thể được sử dụng để đo lường mức độ oxy trong m&aacute;u động mạch.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mục ti&ecirc;u trong điều trị tr&agrave;n kh&iacute; m&agrave;ng phổi l&agrave; để l&agrave;m giảm &aacute;p lực l&ecirc;n phổi, cho ph&eacute;p n&oacute; lại mở rộng, v&agrave; để ngăn ngừa t&aacute;i ph&aacute;t. Phương ph&aacute;p tốt nhất để đạt được điều n&agrave;y phụ thuộc v&agrave;o mức độ nghi&ecirc;m trọng của sự sụp đổ phổi v&agrave; đ&ocirc;i khi về sức khỏe tổng thể:</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Quan s&aacute;t.</span></b><span style=\"font-family: Arial;\"> Nếu phổi &iacute;t hơn 20 phần trăm bị sụp đổ, b&aacute;c sĩ chỉ đơn giản c&oacute; thể theo d&otilde;i t&igrave;nh trạng với một loạt c&aacute;c ngực X-quang cho đến khi kh&ocirc;ng kh&iacute; được hấp thu ho&agrave;n to&agrave;n v&agrave; phổi đ&atilde; t&aacute;i mở rộng. Bởi v&igrave; n&oacute; c&oacute; thể mất h&agrave;ng tuần cho tr&agrave;n kh&iacute; m&agrave;ng phổi để tự chữa bệnh, tuy nhi&ecirc;n, một ống kim ti&ecirc;m ngực c&oacute; thể được sử dụng để loại bỏ kh&ocirc;ng kh&iacute;, ngay cả khi tr&agrave;n kh&iacute; m&agrave;ng phổi l&agrave; nhỏ v&agrave; kh&ocirc;ng đe dọa.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ống kim hoặc ch&egrave;n ngực.</span></b><span style=\"font-family: Arial;\"> Khi phổi đ&atilde; bị sụp đổ hơn 20 phần trăm, b&aacute;c sĩ c&oacute; thể loại bỏ kh&ocirc;ng kh&iacute; bằng c&aacute;ch ch&egrave;n một c&acirc;y kim, ống rỗng v&agrave;o kh&ocirc;ng gian giữa phổi v&agrave; lồng ngực. Ống kim thường được gắn v&agrave;o một thiết bị h&uacute;t li&ecirc;n tục loại bỏ kh&ocirc;ng kh&iacute; từ khoang ngực v&agrave; c&oacute; thể được để lại tại chỗ trong v&agrave;i giờ đến v&agrave;i ng&agrave;y.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Phương ph&aacute;p điều trị tr&agrave;n kh&iacute; m&agrave;ng phổi kh&aacute;c.</span></b><span style=\"font-family: Arial;\"> Nếu đ&atilde; c&oacute; nhiều hơn một lần tr&agrave;n kh&iacute; m&agrave;ng phổi, c&oacute; thể c&oacute; phương ph&aacute;p điều trị để ngăn ngừa t&aacute;i ph&aacute;t nữa.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một thủ tục phẫu thuật th&ocirc;ng thường được gọi l&agrave; thoracoscopy video hỗ trợ, trong đ&oacute; sử dụng vết mổ nhỏ v&agrave; một video camera nhỏ để hướng dẫn phẫu thuật. Trong phần n&agrave;y, hai hoặc ba ống được đặt giữa c&aacute;c xương sườn trong khi trong g&acirc;y m&ecirc; to&agrave;n th&acirc;n. Th&ocirc;ng qua một trong c&aacute;c ống, c&aacute;c b&aacute;c sĩ phẫu thuật c&oacute; thể quan s&aacute;t với fiberscope, trong khi th&ocirc;ng qua c&aacute;c ống kh&aacute;c, b&aacute;c sĩ phẫu thuật cố gắng để đ&oacute;ng r&ograve; rỉ kh&ocirc;ng kh&iacute; với c&aacute;c dụng cụ phẫu thuật. Hiếm khi, khi điều n&agrave;y kh&ocirc;ng l&agrave;m việc, tiến h&agrave;nh phẫu thuật với một vết mổ l&agrave; cần thiết.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c ống dẫn vẫn c&ograve;n trong bao l&acirc;u cần thiết cho đến khi kh&ocirc;ng kh&iacute; trong kh&ocirc;ng gian m&agrave;ng phổi đ&atilde; biến mất v&agrave; kh&ocirc;ng t&aacute;i diễn khi c&aacute;c ống ngực được kẹp v&agrave; kiểm tra với một tia-X. Video hỗ trợ thoracoscopy dẫn tới đau &iacute;t hơn v&agrave; hồi phục một thời gian ngắn hơn so với c&aacute;c loại h&igrave;nh phẫu thuật v&igrave; khoang ngực c&oacute; thể được truy cập m&agrave; kh&ocirc;ng vi phạm bất kỳ xương sườn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Ph&ograve;ng chống</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mặc d&ugrave; thường kh&ocirc;ng thể ngăn chặn tr&agrave;n kh&iacute; m&agrave;ng phổi, ngừng h&uacute;t thuốc l&aacute; l&agrave; một c&aacute;ch quan trọng để giảm nguy cơ của một tr&agrave;n kh&iacute; m&agrave;ng phổi đầu ti&ecirc;n v&agrave; tr&aacute;nh t&aacute;i ph&aacute;t. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: right;\"><b style=\"\"><i style=\"\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(128, 0, 0);\"></span></span></i></b></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">&nbsp;</span></p></span>\n', 1);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(14, 'Ung thư phổi', '\n   <h1 class=\"title\">Ung thư phổi</h1>   <span class=\"des\">Ung thư phổi! Ung thư phổi là nguyên nhân gây tử vong hàng đầu ung thư tại Hoa Kỳ, trong cả nam giới và phụ nữ. Ung thư phổi tử vong mỗi năm hơn so với đại tràng, tuyến tiền liệt, buồng trứng, bạch huyết và ung thư vú.  </span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ung thư phổi l&agrave; một loại ung thư bắt đầu trong phổi. Phổi l&agrave; bộ phận cơ thể xốp ở ngực c&oacute; oxy khi h&iacute;t v&agrave;o v&agrave; lượng kh&iacute; carbon dioxide ph&aacute;t h&agrave;nh khi thở ra. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ung thư phổi l&agrave; nguy&ecirc;n nh&acirc;n g&acirc;y tử vong h&agrave;ng đầu ung thư tại Hoa Kỳ, trong cả nam giới v&agrave; phụ nữ. Ung thư phổi tử vong mỗi năm hơn so với đại tr&agrave;ng, tuyến tiền liệt, buồng trứng, bạch huyết v&agrave; ung thư v&uacute;. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những người h&uacute;t thuốc c&oacute; nguy cơ lớn nhất của ung thư phổi. Nguy cơ gia tăng ung thư phổi với độ d&agrave;i của thời gian v&agrave; số lượng thuốc l&aacute; h&uacute;t thuốc. Nếu bỏ h&uacute;t thuốc, ngay cả sau khi h&uacute;t thuốc l&aacute; trong nhiều năm, c&oacute; thể l&agrave;m giảm đ&aacute;ng kể nguy cơ ph&aacute;t triển ung thư phổi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ung thư phổi thường kh&ocirc;ng g&acirc;y ra dấu hiệu v&agrave; triệu chứng ở giai đoạn sớm nhất. C&aacute;c dấu hiệu v&agrave; triệu chứng của ung thư phổi thường chỉ xảy ra khi bệnh được n&acirc;ng cao. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c dấu hiệu v&agrave; triệu chứng của ung thư phổi c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho mới m&agrave; kh&ocirc;ng hết.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thay đổi trong ho m&atilde;n t&iacute;nh hay &quot;ho của người h&uacute;t thuốc&quot;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho ra m&aacute;u, thậm ch&iacute; một lượng nhỏ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau ngực.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thở kh&ograve; kh&egrave;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khan tiếng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Giảm c&acirc;n m&agrave; kh&ocirc;ng cố gắng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau xương.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhức đầu.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Đến gặp b&aacute;c sĩ khi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Lấy hẹn với b&aacute;c sĩ nếu c&oacute; bất cứ dấu hiệu hay triệu chứng lo lắng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu h&uacute;t thuốc v&agrave; muốn dừng lại để giảm nguy cơ ung thư phổi, l&agrave;m cho một cuộc hẹn với b&aacute;c sĩ. B&aacute;c sĩ c&oacute; thể khuy&ecirc;n chiến lược, chẳng hạn như tư vấn, thuốc men v&agrave; c&aacute;c sản phẩm thay thế nicotine. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&uacute;t thuốc g&acirc;y ra phần lớn c&aacute;c bệnh ung thư phổi - cả người h&uacute;t thuốc l&aacute; v&agrave; ở những người tiếp x&uacute;c với kh&oacute;i thuốc. Nhưng ung thư phổi cũng xảy ra ở những người kh&ocirc;ng bao giờ h&uacute;t thuốc v&agrave; ở những người chưa bao giờ tiếp x&uacute;c k&eacute;o d&agrave;i với kh&oacute;i thuốc. Trong những trường hợp n&agrave;y, c&oacute; thể kh&ocirc;ng c&oacute; nguy&ecirc;n nh&acirc;n r&otilde; r&agrave;ng của bệnh ung thư phổi. C&aacute;c b&aacute;c sĩ đ&atilde; x&aacute;c định được yếu tố c&oacute; thể l&agrave;m tăng nguy cơ. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">L&agrave;m thế n&agrave;o? H&uacute;t thuốc l&aacute; g&acirc;y ung thư phổi:</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c b&aacute;c sĩ tin rằng nguy&ecirc;n nh&acirc;n g&acirc;y ung thư phổi do h&uacute;t thuốc l&aacute; g&acirc;y tổn hại c&aacute;c tế b&agrave;o l&oacute;t phổi. Khi h&iacute;t v&agrave;o kh&oacute;i thuốc l&aacute;, trong đ&oacute; c&oacute; đầy đủ c&aacute;c chất g&acirc;y ung thư, những thay đổi trong m&ocirc; phổi bắt đầu gần như ngay lập tức. L&uacute;c đầu, cơ thể c&oacute; thể sửa chữa thiệt hại n&agrave;y. Nhưng với từng tiếp x&uacute;c lặp đi lặp lại, c&aacute;c tế b&agrave;o b&igrave;nh thường ng&agrave;y c&agrave;ng bị hư hại. Theo thời gian, thiệt hại g&acirc;y ra c&aacute;c tế b&agrave;o h&agrave;nh động bất thường v&agrave; cuối c&ugrave;ng l&agrave; ung thư c&oacute; thể ph&aacute;t triển. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c loại ung thư phổi </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c b&aacute;c sĩ chia bệnh ung thư phổi l&agrave;m hai loại ch&iacute;nh dựa tr&ecirc;n sự xuất hiện của c&aacute;c tế b&agrave;o ung thư phổi dưới k&iacute;nh hiển vi. B&aacute;c sĩ quyết định điều trị dựa tr&ecirc;n ch&iacute;nh loại ung thư phổi c&oacute;. Hai loại bệnh ung thư phổi bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ung thư phổi tế b&agrave;o nhỏ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Xảy ra hầu như chỉ ở người nghiện thuốc l&aacute; v&agrave; &iacute;t phổ biến hơn so với ung thư phổi kh&ocirc;ng phải tế b&agrave;o nhỏ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ung thư phổi kh&ocirc;ng phải tế b&agrave;o nhỏ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">L&agrave; một thuật ngữ chung cho một số loại ung thư phổi theo c&aacute;ch tương tự. Bao gồm ung thư biểu m&ocirc; tế b&agrave;o vảy, adenocarcinoma v&agrave; ung thư biểu m&ocirc; tế b&agrave;o lớn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một số yếu tố c&oacute; thể l&agrave;m tăng nguy cơ ung thư phổi. Một số yếu tố nguy cơ c&oacute; thể được kiểm so&aacute;t, chẳng hạn, bằng c&aacute;ch bỏ h&uacute;t thuốc. V&agrave; yếu tố kh&aacute;c kh&ocirc;ng thể kiểm so&aacute;t, chẳng hạn như giới t&iacute;nh. Yếu tố nguy cơ ung thư phổi bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">H&uacute;t thuốc </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&uacute;t thuốc vẫn l&agrave; yếu tố nguy cơ lớn nhất đối với ung thư phổi. Nguy cơ gia tăng ung thư phổi với số lượng thuốc l&aacute;<span style=\"\">&nbsp; </span>h&uacute;t mỗi ng&agrave;y v&agrave; số năm c&oacute; h&uacute;t thuốc. Bỏ ở mọi lứa tuổi c&oacute; thể l&agrave;m giảm đ&aacute;ng kể nguy cơ ph&aacute;t triển ung thư phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tiếp x&uacute;c với kh&oacute;i thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ngay cả khi kh&ocirc;ng h&uacute;t thuốc, nguy cơ gia tăng ung thư phổi nếu đang tiếp x&uacute;c với kh&oacute;i thuốc.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tiếp x&uacute;c với kh&iacute; radon</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Radon l&agrave; sản phẩm của sự ph&acirc;n hủy tự nhi&ecirc;n của urani trong đ&aacute;, đất v&agrave; nước m&agrave; cuối c&ugrave;ng trở th&agrave;nh một phần của kh&ocirc;ng kh&iacute; h&iacute;t thở. Kh&ocirc;ng an to&agrave;n mức độ kh&iacute; radon c&oacute; thể t&iacute;ch lũy trong việc x&acirc;y dựng bất kỳ, bao gồm cả nh&agrave;. Radon thử nghiệm c&oacute; thể x&aacute;c định mức độ an to&agrave;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tiếp x&uacute;c với amiang v&agrave; c&aacute;c h&oacute;a chất kh&aacute;c</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nơi l&agrave;m việc tiếp x&uacute;c với amiang v&agrave; c&aacute;c chất c&oacute; thể g&acirc;y ung thư - chẳng hạn như asen, niken, crom v&agrave; nhựa đường - cũng c&oacute; thể l&agrave;m tăng nguy cơ ung thư phổi ph&aacute;t triển, đặc biệt l&agrave; nếu l&agrave; một người h&uacute;t thuốc.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Lịch sử gia đ&igrave;nh mắc bệnh ung thư phổi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những người c&oacute; cha mẹ, anh chị em ruột hoặc người th&acirc;n với ung thư phổi c&oacute; tăng nguy cơ của bệnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Sử dụng qu&aacute; nhiều rượu</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Uống nhiều hơn một lượng rượu vừa phải - kh&ocirc;ng uống nhiều hơn một ly một ng&agrave;y cho phụ nữ hoặc hai ly một ng&agrave;y đối với nam giới - c&oacute; thể l&agrave;m tăng nguy cơ ung thư phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Một số bệnh phổi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những người bị bệnh phổi nhất định, chẳng hạn như bệnh nghẽn phổi m&atilde;n t&iacute;nh, c&oacute; thể c&oacute; tăng nguy cơ ung thư phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ung thư phổi c&oacute; thể g&acirc;y biến chứng, như: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Kh&oacute; thở</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những người bị ung thư phổi c&oacute; thể trải nghiệm kh&oacute; thở nếu ung thư ph&aacute;t triển để ngăn chặn c&aacute;c đường dẫn kh&iacute; ch&iacute;nh. Ung thư phổi cũng c&oacute; thể g&acirc;y ra dịch t&iacute;ch tụ quanh phổi, l&agrave;m kh&oacute; khăn hơn cho phổi mở rộng đầy đủ khi h&iacute;t v&agrave;o.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ho ra m&aacute;u</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ung thư phổi c&oacute; thể g&acirc;y chảy m&aacute;u trong đường th&ocirc;ng kh&iacute;, m&agrave; c&oacute; thể l&agrave;m bị ho ra m&aacute;u (ho ra m&aacute;u). Đ&ocirc;i khi chảy m&aacute;u c&oacute; thể trở n&ecirc;n nghi&ecirc;m trọng. Phương ph&aacute;p điều trị c&oacute; sẵn để kiểm so&aacute;t chảy m&aacute;u.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Đau</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ung thư phổi l&acirc;y lan đến lớp ni&ecirc;m mạc của phổi hoặc một v&ugrave;ng kh&aacute;c của cơ thể c&oacute; thể g&acirc;y ra đau đớn. B&aacute;o cho b&aacute;c sĩ nếu bị đau. Đau đầu c&oacute; thể nhẹ v&agrave; li&ecirc;n tục, nhưng c&oacute; thể trở th&agrave;nh kh&ocirc;ng đổi. Thuốc men, xạ trị v&agrave; phương ph&aacute;p điều trị kh&aacute;c c&oacute; thể gi&uacute;p l&agrave;m cho thoải m&aacute;i hơn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Chất lỏng trong ngực (tr&agrave;n dịch m&agrave;ng phổi)</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ung thư phổi c&oacute; thể g&acirc;y ra dịch t&iacute;ch tụ trong kh&ocirc;ng gian bao quanh phổi trong khoang ngực (m&agrave;ng phổi kh&ocirc;ng gian). Tr&agrave;n dịch m&agrave;ng phổi c&oacute; thể g&acirc;y l&acirc;y lan bệnh ung thư ngo&agrave;i phổi hoặc trong phản ứng đối với ung thư phổi ở trong phổi. Chất lỏng t&iacute;ch tụ trong lồng ngực c&oacute; thể g&acirc;y kh&oacute; thở. Phương ph&aacute;p điều trị c&oacute; sẵn để r&aacute;o nước dịch từ ngực<span style=\"\">&nbsp; </span>v&agrave; giảm nguy cơ sẽ xảy ra tr&agrave;n dịch m&agrave;ng phổi một lần nữa.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ung thư l&acirc;y lan sang c&aacute;c bộ phận kh&aacute;c của cơ thể (di căn) </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ung thư phổi thường lan (di căn) tới c&aacute;c bộ phận kh&aacute;c của cơ thể - thường phổi b&ecirc;n, n&atilde;o, xương, gan v&agrave; tuyến thượng thận. Ung thư l&acirc;y lan c&oacute; thể g&acirc;y đau, buồn n&ocirc;n, đau đầu hoặc c&aacute;c dấu hiệu v&agrave; triệu chứng phụ thuộc v&agrave;o cơ quan bị ảnh hưởng. Trong một số trường hợp, điều trị c&oacute; sẵn di căn biệt lập, nhưng trong nhiều trường hợp, mục ti&ecirc;u của điều trị di căn chỉ để l&agrave;m giảm c&aacute;c dấu hiệu v&agrave; triệu chứng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">C&aacute;i chết</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thật kh&ocirc;ng may, tỷ lệ sống cho người được chẩn đo&aacute;n mắc bệnh ung thư phổi đang rất thấp. Trong hầu hết trường hợp, bệnh g&acirc;y tử vong. Người được chẩn đo&aacute;n ở giai đoạn sớm nhất c&oacute; cơ hội lớn nhất cho chữa bệnh. B&aacute;c sĩ c&oacute; thể thảo luận về cơ hội cho sự sống c&ograve;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Kiểm tra sức khỏe người bệnh ung thư phổi </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c b&aacute;c sĩ kh&ocirc;ng chắc chắn liệu những người kh&ocirc;ng c&oacute; dấu hiệu hoặc triệu chứng của ung thư phổi cần phải trải qua kiểm tra cho bệnh. Thậm ch&iacute; nếu c&oacute; tăng nguy cơ ung thư phổi - v&iacute; dụ, nếu l&agrave; một người h&uacute;t thuốc - kh&ocirc;ng r&otilde; r&agrave;ng l&agrave; một X - quang ngực hoặc chụp cắt lớp vi t&iacute;nh (CT scan) c&oacute; thể được lợi. Một số nghi&ecirc;n cứu cho thấy rằng những thử nghiệm n&agrave;y c&oacute; thể t&igrave;m thấy ung thư trước đ&oacute;, khi n&oacute; c&oacute; thể được điều trị th&agrave;nh c&ocirc;ng hơn. Nhưng c&aacute;c nghi&ecirc;n cứu kh&aacute;c t&igrave;m thấy rằng những thử nghiệm n&agrave;y thường l&agrave;nh t&iacute;nh, tiết lộ th&ecirc;m điều kiện y&ecirc;u cầu x&eacute;t nghiệm x&acirc;m lấn v&agrave; tiếp x&uacute;c với mọi người những rủi ro kh&ocirc;ng cần thiết. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tầm so&aacute;t ung thư phổi l&agrave; g&acirc;y tranh c&atilde;i giữa c&aacute;c b&aacute;c sĩ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c nghi&ecirc;n cứu đang được tiến h&agrave;nh để x&aacute;c định những loại x&eacute;t nghiệm c&oacute; thể hữu &iacute;ch v&agrave; những người sẽ được hưởng lợi từ s&agrave;ng lọc ung thư phổi. Trong khi đ&oacute;, n&oacute;i chuyện với b&aacute;c sĩ nếu lo ngại về nguy cơ ung thư phổi. C&ugrave;ng c&oacute; thể x&aacute;c định chiến lược để giảm nguy cơ v&agrave; quyết định c&oacute; x&eacute;t nghiệm s&agrave;ng lọc th&iacute;ch hợp. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm để chẩn đo&aacute;n ung thư phổi </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; l&yacute; do để nghĩ rằng c&oacute; thể bị ung thư phổi, b&aacute;c sĩ c&oacute; thể đặt một số x&eacute;t nghiệm để t&igrave;m tế b&agrave;o ung thư v&agrave; để loại trừ c&aacute;c điều kiện kh&aacute;c. Để chẩn đo&aacute;n ung thư phổi, b&aacute;c sĩ c&oacute; thể khuy&ecirc;n n&ecirc;n: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">H&igrave;nh ảnh kiểm tra</span></b><span style=\"font-family: Arial;\">. Một h&igrave;nh ảnh X - quang phổi c&oacute; thể tiết lộ một khối bất thường hoặc hạch. Một CT scan c&oacute; thể ph&aacute;t hiện c&aacute;c tổn thương nhỏ trong phổi<span style=\"\">&nbsp; </span>m&agrave; c&oacute; thể kh&ocirc;ng được ph&aacute;t hiện tr&ecirc;n một chụp X - quang.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tế b&agrave;o học đờm</span></b><span style=\"font-family: Arial;\">. Nếu bị ho v&agrave; đang sản xuất đờm, nh&igrave;n v&agrave;o đờm dưới k&iacute;nh hiển vi đ&ocirc;i khi c&oacute; thể tiết lộ sự hiện diện của c&aacute;c tế b&agrave;o ung thư phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Mẫu m&ocirc; (sinh thiết)</span></b><span style=\"font-family: Arial;\">. Một mẫu tế b&agrave;o bất thường c&oacute; thể được loại bỏ trong một thủ tục gọi l&agrave; sinh thiết để chẩn đo&aacute;n ung thư phổi. B&aacute;c sĩ c&oacute; thể thực hiện sinh thiết trong một số c&aacute;ch, bao gồm cả phế quản, b&aacute;c sĩ xem x&eacute;t c&aacute;c khu vực bất thường của phổi<span style=\"\">&nbsp; </span>bằng c&aacute;ch sử dụng một ống &aacute;nh s&aacute;ng th&ocirc;ng qua xuống cổ họng v&agrave; v&agrave;o phổi; nội soi trung thất (mediastinoscopy), trong đ&oacute; vết mổ được đưa ra tại cơ sở của cổ v&agrave; c&aacute;c c&ocirc;ng cụ phẫu thuật được đưa v&agrave;o ph&iacute;a sau xương ức để lấy mẫu m&ocirc; từ c&aacute;c hạch bạch huyết, b&aacute;c sĩ sử dụng X - quang hay CT h&igrave;nh ảnh để hướng dẫn một kim th&ocirc;ng qua ngực v&agrave; th&agrave;nh một khối u đ&aacute;ng ngờ hoặc hạch để thu thập c&aacute;c tế b&agrave;o. Một mẫu sinh thiết cũng c&oacute; thể được lấy từ c&aacute;c hạch bạch huyết hoặc c&aacute;c khu vực kh&aacute;c nơi ung thư đ&atilde; lan rộng, chẳng hạn như gan. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Giai đoạn ung thư </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi ung thư phổi đ&atilde; được chẩn đo&aacute;n, b&aacute;c sĩ sẽ l&agrave;m việc để x&aacute;c định mức độ, hoặc giai đoạn của bệnh ung thư. giai đoạn ung thư gi&uacute;p bạn v&agrave; b&aacute;c sĩ điều trị quyết định những g&igrave; l&agrave; th&iacute;ch hợp nhất. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">D&agrave;n dựng c&aacute;c b&agrave;i kiểm tra c&oacute; thể bao gồm c&aacute;c thủ tục h&igrave;nh ảnh cho ph&eacute;p b&aacute;c sĩ t&igrave;m bằng chứng cho thấy ung thư đ&atilde; lan rộng ra khỏi phổi. C&aacute;c x&eacute;t nghiệm n&agrave;y bao gồm chụp CT, chụp cộng hưởng từ (MRI), chụp cắt lớp ph&aacute;t xạ positron (PET) v&agrave; qu&eacute;t xương. Loại kiểm tra l&agrave; th&iacute;ch hợp cho mỗi người, n&oacute;i chuyện với b&aacute;c sĩ về c&aacute;c thủ tục th&iacute;ch hợp. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">C&aacute;c giai đoạn của ung thư phổi kh&ocirc;ng phải tế b&agrave;o nhỏ </span></b></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Giai</span></b><b style=\"\"><span style=\"font-family: Arial;\"> đoạn I.</span></b><span style=\"font-family: Arial;\"> Ung thư ở giai đoạn n&agrave;y đ&atilde; x&acirc;m chiếm c&aacute;c m&ocirc; phổi nằm b&ecirc;n dưới nhưng kh&ocirc;ng l&acirc;y lan đến c&aacute;c hạch bạch huyết.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Giai đoạn II.</span></b><span style=\"font-family: Arial;\"> Điều n&agrave;y, giai đoạn ung thư đ&atilde; lan đến hạch bạch huyết l&acirc;n cận hoặc x&acirc;m chiếm th&agrave;nh ngực hoặc cấu tr&uacute;c l&acirc;n cận kh&aacute;c. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Giai đoạn IIIA.</span></b><span style=\"font-family: Arial;\"> Ở giai đoạn n&agrave;y, ung thư đ&atilde; lan rộng từ phổi đến c&aacute;c hạch bạch huyết ở trung t&acirc;m của ngực. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Giai đoạn IIIB.</span></b><span style=\"font-family: Arial;\"> Ung thư đ&atilde; lan truyền tại địa phương đến c&aacute;c khu vực như tim, mạch m&aacute;u, kh&iacute; quản v&agrave; thực quản - tất cả trong lồng ngực - hoặc đến c&aacute;c hạch bạch huyết trong lĩnh vực xương đ&ograve;n hoặc đến c&aacute;c m&ocirc; bao quanh phổi trong lồng ngực (m&agrave;ng phổi).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Giai đoạn IV.</span></b><span style=\"font-family: Arial;\"> Ung thư đ&atilde; lan ra c&aacute;c phần kh&aacute;c của cơ thể, chẳng hạn như, gan, xương hay n&atilde;o.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">C&aacute;c giai đoạn của ung thư phổi </span></b></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Hạn chế.</span></b><span style=\"font-family: Arial;\"> Ung thư giới hạn ở một phổi v&agrave; c&aacute;c hạch bạch huyết l&acirc;n cận.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Mở rộng.</span></b><span style=\"font-family: Arial;\"> Ung thư đ&atilde; lan tr&agrave;n ra ngo&agrave;i một phổi v&agrave; c&aacute;c hạch bạch huyết gần đ&oacute;, v&agrave; c&oacute; thể đ&atilde; x&acirc;m chiếm cả hai phổi, hạch bạch huyết nhiều, từ xa, hoặc c&aacute;c cơ quan kh&aacute;c, chẳng hạn như gan hay n&atilde;o.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">B&aacute;c sĩ chọn một ph&aacute;c đồ điều trị ung thư dựa tr&ecirc;n một số yếu tố, chẳng hạn như sức khỏe tổng thể, loại v&agrave; giai đoạn của bệnh ung thư v&agrave; sở th&iacute;ch. T&ugrave;y chọn n&agrave;y thường bao gồm một hoặc nhiều phương ph&aacute;p điều trị, bao gồm phẫu thuật, h&oacute;a trị, xạ trị hoặc điều trị bằng thuốc nhắm mục ti&ecirc;u. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong một số trường hợp, c&oacute; thể chọn kh&ocirc;ng phải trải qua điều trị. V&iacute; dụ, c&oacute; thể cảm thấy rằng c&aacute;c t&aacute;c dụng phụ của việc điều trị sẽ cao hơn lợi &iacute;ch tiềm năng. Khi đ&oacute; l&agrave; trường hợp, b&aacute;c sĩ c&oacute; thể gợi &yacute; chăm s&oacute;c an ủi duy nhất để điều trị c&aacute;c triệu chứng của bệnh ung thư g&acirc;y ra, như đau đớn. </span></p>\r\n<table width=\"100%\" cellspacing=\"0\" cellpadding=\"0\" border=\"1\" class=\"MsoNormalTable\" style=\"width: 100%; border-collapse: collapse; border: medium none;\">\r\n    <tbody>\r\n        <tr style=\"\">\r\n            <td colspan=\"2\" style=\"border-width: medium medium 1.5pt; border-style: none none solid; border-color: -moz-use-text-color -moz-use-text-color rgb(112, 172, 47); background: none repeat scroll 0% 0% rgb(133, 191, 70); padding: 3.75pt 7.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Lựa chọn điều trị ung thư phổi kh&ocirc;ng phải tế b&agrave;o   nhỏ </span></b></p>\r\n            </td>\r\n        </tr>\r\n        <tr style=\"\">\r\n            <td valign=\"top\" style=\"border-width: medium 1pt 1pt; border-style: none solid solid; border-color: -moz-use-text-color white rgb(218, 218, 218); padding: 3.75pt 7.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Giai đoạn </span></p>\r\n            </td>\r\n            <td valign=\"top\" style=\"border-width: medium 1pt 1pt medium; border-style: none solid solid none; border-color: -moz-use-text-color white rgb(218, 218, 218) -moz-use-text-color; padding: 3.75pt 7.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c t&ugrave;y chọn phổ biến </span></p>\r\n            </td>\r\n        </tr>\r\n        <tr style=\"\">\r\n            <td valign=\"top\" style=\"border-width: medium 1pt 1pt; border-style: none solid solid; border-color: -moz-use-text-color white rgb(218, 218, 218); padding: 3.75pt 7.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">I</span></p>\r\n            </td>\r\n            <td valign=\"top\" style=\"border-width: medium 1pt 1pt medium; border-style: none solid solid none; border-color: -moz-use-text-color white rgb(218, 218, 218) -moz-use-text-color; padding: 3.75pt 7.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><i style=\"\"><span style=\"font-family: Arial;\">Phẫu thuật, đ&ocirc;i khi h&oacute;a trị</span></i></p>\r\n            </td>\r\n        </tr>\r\n        <tr style=\"\">\r\n            <td valign=\"top\" style=\"border-width: medium 1pt 1pt; border-style: none solid solid; border-color: -moz-use-text-color white rgb(218, 218, 218); padding: 3.75pt 7.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">II </span></p>\r\n            </td>\r\n            <td valign=\"top\" style=\"border-width: medium 1pt 1pt medium; border-style: none solid solid none; border-color: -moz-use-text-color white rgb(218, 218, 218) -moz-use-text-color; padding: 3.75pt 7.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><i style=\"\"><span style=\"font-family: Arial;\">Phẫu thuật, h&oacute;a trị, xạ </span></i></p>\r\n            </td>\r\n        </tr>\r\n        <tr style=\"\">\r\n            <td valign=\"top\" style=\"border-width: medium 1pt 1pt; border-style: none solid solid; border-color: -moz-use-text-color white rgb(218, 218, 218); padding: 3.75pt 7.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">IIIA </span></p>\r\n            </td>\r\n            <td valign=\"top\" style=\"border-width: medium 1pt 1pt medium; border-style: none solid solid none; border-color: -moz-use-text-color white rgb(218, 218, 218) -moz-use-text-color; padding: 3.75pt 7.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><i style=\"\"><span style=\"font-family: Arial;\">Kết hợp h&oacute;a trị v&agrave; xạ, đ&ocirc;i khi phẫu thuật dựa tr&ecirc;n   kết quả điều trị </span></i></p>\r\n            </td>\r\n        </tr>\r\n        <tr style=\"\">\r\n            <td valign=\"top\" style=\"border-width: medium 1pt 1pt; border-style: none solid solid; border-color: -moz-use-text-color white rgb(218, 218, 218); padding: 3.75pt 7.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">IIIB </span></p>\r\n            </td>\r\n            <td valign=\"top\" style=\"border-width: medium 1pt 1pt medium; border-style: none solid solid none; border-color: -moz-use-text-color white rgb(218, 218, 218) -moz-use-text-color; padding: 3.75pt 7.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><i style=\"\"><span style=\"font-family: Arial;\">H&oacute;a trị liệu, đ&ocirc;i khi bức xạ </span></i></p>\r\n            </td>\r\n        </tr>\r\n        <tr style=\"\">\r\n            <td valign=\"top\" style=\"border-width: medium 1pt 1pt; border-style: none solid solid; border-color: -moz-use-text-color white rgb(218, 218, 218); padding: 3.75pt 7.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">IV </span></p>\r\n            </td>\r\n            <td valign=\"top\" style=\"border-width: medium 1pt 1pt medium; border-style: none solid solid none; border-color: -moz-use-text-color white rgb(218, 218, 218) -moz-use-text-color; padding: 3.75pt 7.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><i style=\"\"><span style=\"font-family: Arial;\">H&oacute;a trị liệu, thuốc theo mục ti&ecirc;u điều trị, thử   nghiệm l&acirc;m s&agrave;ng, hỗ trợ chăm s&oacute;c </span></i></p>\r\n            </td>\r\n        </tr>\r\n        <tr style=\"\">\r\n            <td colspan=\"2\" style=\"border-width: medium medium 1.5pt; border-style: none none solid; border-color: -moz-use-text-color -moz-use-text-color rgb(112, 172, 47); background: none repeat scroll 0% 0% rgb(133, 191, 70); padding: 3.75pt 7.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Lựa chọn điều trị ung thư phổi tế b&agrave;o nhỏ </span></b></p>\r\n            </td>\r\n        </tr>\r\n        <tr style=\"\">\r\n            <td valign=\"top\" style=\"border-width: medium 1pt 1pt; border-style: none solid solid; border-color: -moz-use-text-color white rgb(218, 218, 218); padding: 3.75pt 7.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Giai đoạn </span></p>\r\n            </td>\r\n            <td valign=\"top\" style=\"border-width: medium 1pt 1pt medium; border-style: none solid solid none; border-color: -moz-use-text-color white rgb(218, 218, 218) -moz-use-text-color; padding: 3.75pt 7.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c t&ugrave;y chọn </span></p>\r\n            </td>\r\n        </tr>\r\n        <tr style=\"\">\r\n            <td valign=\"top\" style=\"border-width: medium 1pt 1pt; border-style: none solid solid; border-color: -moz-use-text-color white rgb(218, 218, 218); padding: 3.75pt 7.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hạn chế </span></p>\r\n            </td>\r\n            <td valign=\"top\" style=\"border-width: medium 1pt 1pt medium; border-style: none solid solid none; border-color: -moz-use-text-color white rgb(218, 218, 218) -moz-use-text-color; padding: 3.75pt 7.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><i style=\"\"><span style=\"font-family: Arial;\">Kết hợp h&oacute;a trị v&agrave; xạ, đ&ocirc;i khi phẫu thuật </span></i></p>\r\n            </td>\r\n        </tr>\r\n        <tr style=\"\">\r\n            <td valign=\"top\" style=\"border-width: medium 1pt 1pt; border-style: none solid solid; border-color: -moz-use-text-color white rgb(218, 218, 218); padding: 3.75pt 7.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mở rộng </span></p>\r\n            </td>\r\n            <td valign=\"top\" style=\"border-width: medium 1pt 1pt medium; border-style: none solid solid none; border-color: -moz-use-text-color white rgb(218, 218, 218) -moz-use-text-color; padding: 3.75pt 7.5pt;\">\r\n            <p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><i style=\"\"><span style=\"font-family: Arial;\">H&oacute;a trị, thử nghiệm l&acirc;m s&agrave;ng, hỗ trợ chăm s&oacute;c </span></i></p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Phẫu thuật </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong khi phẫu thuật, b&aacute;c sĩ phẫu thuật hoạt động để loại bỏ bệnh ung thư phổi v&agrave; lề m&ocirc; khỏe mạnh. Thủ tục để loại bỏ ung thư phổi bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Cắt bỏ để loại bỏ một phần nhỏ của phổi c&oacute; khối u c&ugrave;ng với một bi&ecirc;n độ của m&ocirc; khỏe mạnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ph&acirc;n đoạn cắt bỏ để loại bỏ một phần lớn của phổi, nhưng kh&ocirc;ng phải l&agrave; to&agrave;n bộ th&ugrave;y.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Lobectomy để loại bỏ to&agrave;n bộ một th&ugrave;y phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Pneumonectomy để loại bỏ to&agrave;n bộ một phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu trải qua phẫu thuật, b&aacute;c sĩ phẫu thuật cũng c&oacute; thể loại bỏ c&aacute;c hạch bạch huyết từ ngực để kiểm tra c&aacute;c dấu hiệu của bệnh ung thư. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ung thư phổi phẫu thuật mang rủi ro, bao gồm chảy m&aacute;u v&agrave; nhiễm tr&ugrave;ng. Sẽ cảm thấy hụt hơi sau khi phẫu thuật phổi. M&ocirc; phổi sẽ mở rộng theo thời gian v&agrave; l&agrave;m cho n&oacute; dễ d&agrave;ng hơn để thở. B&aacute;c sĩ c&oacute; thể đề nghị một b&aacute;c sĩ chuy&ecirc;n khoa h&ocirc; hấp c&oacute; thể hướng dẫn qua c&aacute;c b&agrave;i tập thở để hỗ trợ phục hồi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">H&oacute;a trị </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&oacute;a trị d&ugrave;ng thuốc để diệt tế b&agrave;o ung thư. Một hoặc nhiều loại thuốc h&oacute;a trị c&oacute; thể được quản l&yacute; th&ocirc;ng qua một tĩnh mạch ở c&aacute;nh tay<span style=\"\">&nbsp; </span>(tĩnh mạch) hoặc uống. Một sự kết hợp của c&aacute;c loại thuốc thường được đưa ra trong một loạt c&aacute;c phương ph&aacute;p điều trị trong khoảng thời gian v&agrave;i tuần hoặc v&agrave;i th&aacute;ng, với đứt g&atilde;y ở giữa để cơ thể c&oacute; thể phục hồi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&oacute;a trị c&oacute; thể được sử dụng như một d&ograve;ng đầu ti&ecirc;n cho điều trị ung thư phổi hoặc điều trị bổ sung sau khi phẫu thuật. Trong một số trường hợp, h&oacute;a trị liệu c&oacute; thể được sử dụng để l&agrave;m giảm t&aacute;c dụng phụ của bệnh ung thư. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Xạ trị </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Xạ trị sử dụng cung cấp năng lượng cao dầm, như X - quang, để ti&ecirc;u diệt tế b&agrave;o ung thư. Liệu ph&aacute;p bức xạ c&oacute; thể được hướng v&agrave;o bệnh ung thư phổi từ b&ecirc;n ngo&agrave;i cơ thể (bức xạ tia b&ecirc;n ngo&agrave;i) hoặc n&oacute; c&oacute; thể được đặt b&ecirc;n trong kim, hạt hoặc c&aacute;c ống th&ocirc;ng v&agrave; đặt b&ecirc;n trong cơ thể gần bệnh ung thư (brachytheraphy). </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Liệu ph&aacute;p bức xạ c&oacute; thể được sử dụng một m&igrave;nh hoặc với phương ph&aacute;p điều trị ung thư phổi kh&aacute;c. Đ&ocirc;i l&uacute;c đ&oacute; l&agrave; quản l&yacute; đồng thời h&oacute;a trị. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Mục ti&ecirc;u điều trị bằng thuốc </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Liệu ph&aacute;p mục ti&ecirc;u l&agrave; phương ph&aacute;p điều trị ung thư mới l&agrave;m việc bằng c&aacute;ch nhắm mục ti&ecirc;u bất thường cụ thể trong c&aacute;c tế b&agrave;o ung thư. Mục ti&ecirc;u điều trị t&ugrave;y chọn để điều trị ung thư phổi bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Bevacizumab (Avastin).</span></b><span style=\"font-family: Arial;\"> Bevacizumab dừng một khối u từ việc tạo ra một nguồn cung cấp m&aacute;u mới. Mạch m&aacute;u kết nối với c&aacute;c khối u c&oacute; thể cung cấp oxy v&agrave; chất dinh dưỡng đến khối u, cho ph&eacute;p n&oacute; ph&aacute;t triển. Bevacizumab thường được sử dụng kết hợp với h&oacute;a trị liệu v&agrave; được ph&ecirc; chuẩn khi n&acirc;ng cao v&agrave; t&aacute;i ph&aacute;t ung thư phổi kh&ocirc;ng phải tế b&agrave;o nhỏ. Bevacizumab mang một nguy cơ chảy m&aacute;u, đ&ocirc;ng m&aacute;u v&agrave; huyết &aacute;p cao.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Erlotinib (Tarceva).</span></b><span style=\"font-family: Arial;\"> Erlotinib, khối chất h&oacute;a học t&iacute;n hiệu tế b&agrave;o ung thư ph&aacute;t triển v&agrave; ph&acirc;n chia. Erlotinib được chấp thuận cho những người mới v&agrave; t&aacute;i ph&aacute;t ung thư phổi kh&ocirc;ng tế b&agrave;o nhỏ m&agrave; kh&ocirc;ng được sự gi&uacute;p đỡ của h&oacute;a trị. Erlotinib t&aacute;c dụng phụ bao gồm ph&aacute;t ban tr&ecirc;n da v&agrave; ti&ecirc;u chảy.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thử nghiệm l&acirc;m s&agrave;ng </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thử nghiệm l&acirc;m s&agrave;ng đang nghi&ecirc;n cứu c&aacute;c phương ph&aacute;p điều trị ung thư phổi thử nghiệm. C&oacute; thể quan t&acirc;m đăng k&yacute; v&agrave;o một thử nghiệm l&acirc;m s&agrave;ng điều trị ung thư phổi nếu đ&atilde; điều trị kh&ocirc;ng tiến triển hoặc nếu điều trị t&ugrave;y chọn c&oacute; giới hạn. C&aacute;c phương ph&aacute;p điều trị nghi&ecirc;n cứu trong một thử nghiệm l&acirc;m s&agrave;ng c&oacute; thể l&agrave; ph&aacute;t kiến mới nhất, nhưng ch&uacute;ng kh&ocirc;ng đảm bảo một chữa bệnh. Cẩn thận c&acirc;n nhắc lựa chọn điều trị với b&aacute;c sĩ. Sự tham gia trong một thử nghiệm l&acirc;m s&agrave;ng c&oacute; thể gi&uacute;p c&aacute;c b&aacute;c sĩ hiểu r&otilde; hơn về l&agrave;m thế n&agrave;o để điều trị ung thư phổi trong tương lai. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Hỗ trợ chăm s&oacute;c </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi điều trị cung cấp rất &iacute;t cơ hội chữa khỏi bệnh, b&aacute;c sĩ c&oacute; thể khuy&ecirc;n n&ecirc;n tr&aacute;nh điều trị khắc nghiệt v&agrave; chăm s&oacute;c hỗ trợ lựa chọn thay thế. Nếu nhận được chăm s&oacute;c hỗ trợ, b&aacute;c sĩ c&oacute; thể điều trị c&aacute;c dấu hiệu v&agrave; triệu chứng để l&agrave;m cho cảm thấy thoải m&aacute;i hơn, nhưng sẽ kh&ocirc;ng được điều trị nhằm ngăn chặn bệnh ung thư. Hỗ trợ chăm s&oacute;c cho ph&eacute;p<span style=\"\">&nbsp; </span>tận dụng tối đa của tuần cuối c&ugrave;ng hoặc th&aacute;ng<span style=\"\">&nbsp; </span>m&agrave; kh&ocirc;ng cần điều trị l&acirc;u d&agrave;i t&aacute;c dụng phụ ti&ecirc;u cực c&oacute; thể t&aacute;c động đến chất lượng sống. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phong c&aacute;ch sống v&agrave; biện ph&aacute;p khắc phục</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đối ph&oacute; với kh&oacute; thở.</span><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\"> </span><span style=\"font-family: Arial;\">Nhiều người c&oacute; trải nghiệm ung thư phổi kh&oacute; thở tại một số điểm trong qu&aacute; tr&igrave;nh của bệnh. Phương ph&aacute;p điều trị v&agrave; thuốc c&oacute; sẵn để gi&uacute;p cảm thấy thoải m&aacute;i hơn, nhưng kh&ocirc;ng phải lu&ocirc;n lu&ocirc;n đủ. Để đối ph&oacute; với kh&oacute; thở, n&oacute; c&oacute; thể gi&uacute;p để: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">H&atilde;y thử để thư gi&atilde;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Cảm thấy kh&oacute; thở c&oacute; thể l&agrave; đ&aacute;ng sợ. Nhưng nỗi sợ h&atilde;i v&agrave; lo lắng chỉ l&agrave;m cho kh&oacute; hơn để thở. Khi bắt đầu cảm thấy kh&oacute; thở, h&atilde;y cố gắng để chống lại sự sợ h&atilde;i bằng c&aacute;ch chọn một hoạt động gi&uacute;p thư gi&atilde;n. Nghe nhạc, h&atilde;y tưởng tượng kỳ nghỉ tại chỗ ưa th&iacute;ch trong t&acirc;m tr&iacute;, ngồi thiền hay n&oacute;i một lời cầu nguyện.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">T&igrave;m một vị tr&iacute; thoải m&aacute;i</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">N&oacute; c&oacute; thể gi&uacute;p đỡ để dựa về ph&iacute;a trước khi cảm thấy hụt hơi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tập trung v&agrave;o hơi thở</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi cảm thấy hụt hơi, tập trung t&acirc;m tr&iacute; v&agrave;o hơi thở. Thay v&igrave; cố gắng để điền v&agrave;o phổi với kh&ocirc;ng kh&iacute;, tập trung v&agrave;o việc di chuyển c&aacute;c cơ điều khiển cơ ho&agrave;nh. Cố gắng h&iacute;t thở th&ocirc;ng qua m&ocirc;i m&iacute;m v&agrave; nhịp thở với c&aacute;c hoạt động.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tiết kiệm năng lượng cho những g&igrave; quan trọng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu đang hụt hơi, c&oacute; thể trở n&ecirc;n mệt mỏi một c&aacute;ch dễ d&agrave;ng. Cắt ra c&aacute;c nhiệm vụ kh&ocirc;ng cần thiết từ ng&agrave;y để<span style=\"\">&nbsp; </span>c&oacute; thể tiết kiệm năng lượng cho những g&igrave; cần phải được thực hiện.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Hạ nhiệt độ ph&ograve;ng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một căn ph&ograve;ng lạnh c&oacute; thể l&agrave;m cho n&oacute; cảm thấy dễ d&agrave;ng hơn để thở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">H&atilde;y ngồi gần cửa sổ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ngồi hoặc nằm trong một c&aacute;ch m&agrave; cho ph&eacute;p nh&igrave;n ra cửa sổ c&oacute; thể gi&uacute;p cảm thấy &iacute;t bị giới hạn khi đang cảm thấy hụt hơi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Quạt m&aacute;t</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Quạt thổi tr&ecirc;n khu&ocirc;n mặt c&oacute; thể l&agrave;m cho n&oacute; cảm thấy dễ d&agrave;ng hơn để thở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">B&aacute;o cho b&aacute;c sĩ nếu gặp kh&oacute; thở hoặc nếu c&aacute;c triệu chứng nặng hơn. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Thay thế thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu b&aacute;c sĩ đ&atilde; n&oacute;i rằng bệnh ung thư phổi kh&ocirc;ng thể chữa khỏi, c&oacute; thể muốn chuyển sang thuốc bổ sung v&agrave; thay thế cho c&acirc;u trả lời. Bổ sung v&agrave; thay thế phương ph&aacute;p điều trị ung thư phổi kh&ocirc;ng thể chữa khỏi bệnh ung thư. Nhưng phương ph&aacute;p điều trị bổ sung v&agrave; thay thế thường c&oacute; thể được kết hợp với chăm s&oacute;c của b&aacute;c sĩ để gi&uacute;p l&agrave;m giảm c&aacute;c dấu hiệu v&agrave; triệu chứng c&oacute; thể gặp. B&aacute;c sĩ c&oacute; thể gi&uacute;p c&acirc;n nhắc những lợi &iacute;ch v&agrave; rủi ro của phương ph&aacute;p điều trị bổ sung v&agrave; thay thế. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">B&aacute;c sĩ xem x&eacute;t lại phương ph&aacute;p điều trị ngực c&oacute; bổ sung v&agrave; thay thế v&agrave; t&igrave;m thấy một số liệu ph&aacute;p c&oacute; thể hữu &iacute;ch cho những người bị ung thư phổi, bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ch&acirc;m cứu</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong một buổi ch&acirc;m cứu, một học vi&ecirc;n được đ&agrave;o tạo ch&egrave;n nhỏ kim v&agrave;o điểm ch&iacute;nh x&aacute;c tr&ecirc;n cơ thể. Ch&acirc;m cứu c&oacute; thể l&agrave;m giảm đau v&agrave; giảm t&aacute;c dụng điều trị ung thư phụ như buồn n&ocirc;n, n&ocirc;n, miệng kh&ocirc;, nhưng kh&ocirc;ng c&oacute; bằng chứng cho thấy ch&acirc;m cứu c&oacute; bất kỳ t&aacute;c dụng tr&ecirc;n bệnh ung thư. Ch&acirc;m cứu c&oacute; thể được an to&agrave;n khi được thực hiện bởi một học vi&ecirc;n được chứng nhận. H&atilde;y hỏi b&aacute;c sĩ để giới thiệu một ai đ&oacute; trong cộng đồng. Tuy nhi&ecirc;n, ch&acirc;m cứu l&agrave; kh&ocirc;ng an to&agrave;n nếu c&oacute; số lượng m&aacute;u thấp hoặc d&ugrave;ng chất l&agrave;m lo&atilde;ng m&aacute;u.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Th&ocirc;i mi&ecirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Th&ocirc;i mi&ecirc;n l&agrave; một loại điều trị đặt trong trạng th&aacute;i lịm - như thế c&oacute; thể được thư gi&atilde;n. Th&ocirc;i mi&ecirc;n thường được thực hiện bởi một nh&agrave; trị liệu th&ocirc;ng qua c&aacute;c b&agrave;i tập thư gi&atilde;n v&agrave; y&ecirc;u cầu phải suy nghĩ l&agrave;m h&agrave;i l&ograve;ng v&agrave; suy nghĩ t&iacute;ch cực. Th&ocirc;i mi&ecirc;n c&oacute; thể l&agrave;m giảm lo &acirc;u, buồn n&ocirc;n v&agrave; đau ở những người bị bệnh ung thư.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Massage</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong qu&aacute; tr&igrave;nh massage, một liệu ph&aacute;p massage sử dụng b&agrave;n tay của m&igrave;nh để &aacute;p dụng &aacute;p lực cho da v&agrave; cơ bắp . Massage c&oacute; thể gi&uacute;p giảm lo lắng, căng thẳng, mệt mỏi v&agrave; đau ở những người bị bệnh ung thư. Một số chuy&ecirc;n gia trị liệu massage được huấn luyện đặc biệt để l&agrave;m việc với những người c&oacute; bệnh ung thư. H&atilde;y hỏi b&aacute;c sĩ cho t&ecirc;n của trị liệu massage trong cộng đồng. Massage kh&ocirc;ng n&ecirc;n l&agrave;m tổn thương. Liệu ph&aacute;p massage kh&ocirc;ng n&ecirc;n g&acirc;y &aacute;p lực bất cứ nơi n&agrave;o gần khối u hoặc bất kỳ vết thương phẫu thuật. Tr&aacute;nh việc c&oacute; massage nếu m&aacute;u thấp hoặc nếu đang d&ugrave;ng chất l&agrave;m lo&atilde;ng m&aacute;u.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thiền</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thiền l&agrave; một thời gian y&ecirc;n tĩnh phản chiếu trong đ&oacute; tập trung t&acirc;m tr&iacute; v&agrave;o c&aacute;i g&igrave;, như một h&igrave;nh ảnh, &yacute; tưởng hoặc &acirc;m thanh. Thiền c&oacute; thể l&agrave;m giảm căng thẳng v&agrave; cải thiện chất lượng cuộc sống ở những người bị bệnh ung thư. Thiền c&oacute; thể được tự thực hiện, hoặc c&oacute; thể c&oacute; hướng dẫn trong cộng đồng. Y&ecirc;u cầu c&aacute;c khuyến nghị từ nh&oacute;m chăm s&oacute;c sức khỏe hoặc bạn b&egrave; v&agrave; gia đ&igrave;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Yoga</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Yoga kết hợp chuyển động nhẹ nh&agrave;ng k&eacute;o d&agrave;i với h&iacute;t thở s&acirc;u v&agrave; thiền định. Yoga c&oacute; thể gi&uacute;p những người bị ung thư ngủ tốt hơn. Yoga n&oacute;i chung l&agrave; an to&agrave;n khi được giảng dạy bởi một giảng vi&ecirc;n được đ&agrave;o tạo, nhưng kh&ocirc;ng l&agrave;m bất cứ động th&aacute;i l&agrave;m tổn thương hoặc kh&ocirc;ng cảm thấy đ&uacute;ng. Nhiều trung t&acirc;m thể dục cung cấp c&aacute;c lớp học yoga. H&atilde;y hỏi bạn b&egrave; v&agrave; gia đ&igrave;nh cho &yacute; kiến về c&aacute;c lớp học yoga họ đ&atilde; thực hiện.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Đối ph&oacute; v&agrave; hỗ trợ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Chẩn đo&aacute;n ung thư phổi l&agrave; t&agrave;n ph&aacute;. N&oacute; c&oacute; thể mất một thời gian để đến với c&aacute;c điều khoản với cảm x&uacute;c. Khi đ&atilde; sẵn s&agrave;ng, c&oacute; thể thực hiện c&aacute;c bước để kiểm so&aacute;t t&igrave;nh h&igrave;nh. Một vai tr&ograve; t&iacute;ch cực trong việc chăm s&oacute;c sức khỏe c&oacute; thể l&agrave;m cho<span style=\"\">&nbsp; </span>cảm thấy được trao quyền đối ph&oacute; với ung thư phổi. H&atilde;y cố gắng: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">T&igrave;m hiểu tất cả c&oacute; thể về ung thư phổi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">T&igrave;m hiểu tất cả mọi thứ c&oacute; thể về bệnh ung thư phổi - c&aacute;c loại, diễn biến, lựa chọn điều trị v&agrave; ảnh hưởng của ch&uacute;ng.<span style=\"\">&nbsp; </span>C&agrave;ng biết, c&agrave;ng c&oacute; nhiều hoạt động c&oacute; thể được trong việc chăm s&oacute;c của ri&ecirc;ng. H&atilde;y viết ra những c&acirc;u hỏi v&agrave; y&ecirc;u cầu họ ở cuộc hẹn tiếp theo. H&atilde;y hỏi nh&oacute;m chăm s&oacute;c sức khỏe về c&aacute;c nguồn bổ sung th&ocirc;ng tin. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tham dự một vai tr&ograve; t&iacute;ch cực trong điều trị</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mặc d&ugrave; c&oacute; thể cảm thấy mệt mỏi v&agrave; ch&aacute;n nản, kh&ocirc;ng để cho những người kh&aacute;c - kể cả gia đ&igrave;nh hoặc b&aacute;c sĩ<span style=\"\">&nbsp; </span>- những quyết định quan trọng. Tham dự một vai tr&ograve; t&iacute;ch cực trong điều trị v&agrave; l&agrave;m việc với b&aacute;c sĩ để đưa ra quyết định chăm s&oacute;c sức khỏe.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">X&acirc;y dựng một hệ thống hỗ trợ mạnh mẽ </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một hệ thống hỗ trợ mạnh mẽ gi&uacute;p<span style=\"\">&nbsp; </span>đối ph&oacute; với những kh&oacute; khăn h&agrave;ng ng&agrave;y, chẳng hạn như mệt mỏi v&agrave; đau đớn. Bạn b&egrave; v&agrave; gia đ&igrave;nh đang lo lắng v&agrave; muốn gi&uacute;p đỡ, để học c&aacute;ch chấp nhận sự gi&uacute;p đỡ khi cần. Duy tr&igrave; kết nối với bạn b&egrave; v&agrave; gia đ&igrave;nh gi&uacute;p họ đối ph&oacute; với bệnh tật, v&agrave; n&oacute; sẽ cho một cơ hội để n&oacute;i về hy vọng v&agrave; sợ h&atilde;i. Đ&ocirc;i khi sẽ cảm thấy như bạn b&egrave; v&agrave; gia đ&igrave;nh kh&ocirc;ng thể hiểu được cảm x&uacute;c nếu họ kh&ocirc;ng bao giờ bị ung thư. Trong những trường hợp n&agrave;y, c&aacute;c nh&oacute;m hỗ trợ - cả hai trong cộng đồng<span style=\"\">&nbsp; </span>v&agrave; tr&ecirc;n Internet - c&oacute; thể l&agrave; một nguồn th&ocirc;ng tin thiết thực v&agrave; hỗ trợ. Cũng c&oacute; thể thấy rằng ph&aacute;t triển li&ecirc;n kết s&acirc;u sắc v&agrave; l&acirc;u d&agrave;i với những người đang trải qua những điều tương tự.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Đặt mục ti&ecirc;u hợp l&yacute;</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; mục ti&ecirc;u gi&uacute;p cảm thấy kiểm so&aacute;t v&agrave; c&oacute; thể cung cấp cho một cảm gi&aacute;c mục đ&iacute;ch. Nhưng kh&ocirc;ng chọn mục ti&ecirc;u m&agrave; kh&ocirc;ng thể đạt được. C&oacute; thể kh&ocirc;ng thể l&agrave;m việc một tuần 40 giờ, v&iacute; dụ, nhưng c&oacute; thể l&agrave;m việc &iacute;t nhất một nửa thời gian. Trong thực tế, nhiều người thấy rằng tiếp tục l&agrave;m việc c&oacute; thể hữu &iacute;ch.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">H&atilde;y d&agrave;nh thời gian cho ch&iacute;nh m&igrave;nh</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ăn uống tốt, thư gi&atilde;n v&agrave; nhận được nghỉ ngơi đầy đủ c&oacute; thể gi&uacute;p chống lại sự căng thẳng v&agrave; mệt mỏi của bệnh ung thư. Ngo&agrave;i ra, kế hoạch trước cho c&aacute;c thời gian nghỉ khi c&oacute; thể cần nghỉ ngơi nhiều hơn hoặc hạn chế những g&igrave; l&agrave;m.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Vẫn hoạt động</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Chẩn đo&aacute;n ung thư kh&ocirc;ng c&oacute; nghĩa l&agrave; phải ngừng l&agrave;m những điều th&iacute;ch. Phần lớn, nếu cảm thấy cũng đủ để l&agrave;m một c&aacute;i g&igrave; đ&oacute;, đi trước v&agrave; l&agrave;m điều đ&oacute;. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Ph&ograve;ng chống</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o chắc chắn để ngăn ngừa bệnh ung thư phổi, nhưng c&oacute; thể l&agrave;m giảm nguy cơ nếu : </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Kh&ocirc;ng h&uacute;t thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu chưa bao giờ h&uacute;t thuốc, kh&ocirc;ng bắt đầu. N&oacute;i chuyện với c&aacute;c em về việc kh&ocirc;ng h&uacute;t thuốc để họ c&oacute; thể hiểu l&agrave;m thế n&agrave;o để tr&aacute;nh yếu tố nguy cơ ch&iacute;nh đối với ung thư phổi. Nhiều người h&uacute;t thuốc bắt đầu h&uacute;t thuốc l&aacute; hiện nay ở tuổi thiếu ni&ecirc;n của họ. Bắt đầu cuộc hội thoại về sự nguy hiểm của việc h&uacute;t thuốc với trẻ em sớm để họ biết l&agrave;m thế n&agrave;o để phản ứng với &aacute;p lực đồng đẳng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ngưng h&uacute;t thuốc l&aacute;</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ngừng h&uacute;t thuốc ngay b&acirc;y giờ. L&agrave;m giảm nguy cơ ung thư phổi, ngay cả khi đ&atilde; h&uacute;t thuốc trong nhiều năm. N&oacute;i chuyện với b&aacute;c sĩ về c&aacute;c chiến lược v&agrave; ngừng h&uacute;t thuốc viện trợ c&oacute; thể gi&uacute;p bỏ thuốc l&aacute;. T&ugrave;y chọn bao gồm c&aacute;c sản phẩm thay thế nicotine, thuốc men v&agrave; c&aacute;c nh&oacute;m hỗ trợ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tr&aacute;nh kh&oacute;i thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu sống hoặc l&agrave;m việc với người h&uacute;t thuốc, đ&ocirc;n đốc người đ&oacute; bỏ thuốc l&aacute;. &Iacute;t nhất, h&atilde;y hỏi anh ấy hoặc c&ocirc; ấy h&uacute;t thuốc b&ecirc;n ngo&agrave;i. Tr&aacute;nh những nơi người h&uacute;t thuốc l&aacute;, chẳng hạn như qu&aacute;n bar v&agrave; nh&agrave; h&agrave;ng, v&agrave; t&igrave;m ra c&aacute;c t&ugrave;y chọn kh&oacute;i thuốc.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Kiểm tra nh&agrave; cho radon</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; mức độ kh&iacute; radon trong nh&agrave; kiểm tra, đặc biệt l&agrave; nếu sống trong một khu vực radon được biết đến l&agrave; một vấn đề. radon cao cấp c&oacute; thể được khắc phục để l&agrave;m cho nh&agrave; an to&agrave;n hơn. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tr&aacute;nh c&aacute;c chất g&acirc;y ung thư tại nơi l&agrave;m việc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Biện ph&aacute;p ph&ograve;ng ngừa để bảo vệ m&igrave;nh từ việc tiếp x&uacute;c với h&oacute;a chất độc hại tại nơi l&agrave;m việc. Tại Hoa Kỳ, chủ nh&acirc;n phải cho biết nếu đang tiếp x&uacute;c với ho&aacute; chất nguy hiểm tại nơi l&agrave;m việc . Thực hiện theo biện ph&aacute;p ph&ograve;ng ngừa của c&ocirc;ng ty. V&iacute; dụ, nếu đưa ra một mặt nạ bảo vệ, lu&ocirc;n lu&ocirc;n mặc n&oacute;. H&atilde;y hỏi b&aacute;c sĩ cho nhiều hơn những g&igrave; c&oacute; thể l&agrave;m để bảo vệ bản th&acirc;n trong c&ocirc;ng việc. Nguy cơ thiệt hại phổi từ những chất g&acirc;y ung thư tăng nếu h&uacute;t thuốc.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ăn một chế độ ăn uống đầy đủ c&aacute;c loại tr&aacute;i c&acirc;y v&agrave; rau quả</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Chọn một chế độ ăn uống l&agrave;nh mạnh với nhiều loại tr&aacute;i c&acirc;y v&agrave; rau quả. Những nguồn thực phẩm vitamin v&agrave; chất dinh dưỡng tốt nhất. Tr&aacute;nh d&ugrave;ng liều lớn vitamin dưới dạng thuốc vi&ecirc;n, c&oacute; thể c&oacute; hại. V&iacute; dụ, c&aacute;c nh&agrave; nghi&ecirc;n cứu hy vọng sẽ l&agrave;m giảm nguy cơ ung thư phổi ở người h&uacute;t thuốc l&aacute; nặng cho họ bổ sung beta carotene. Kết quả cho thấy c&aacute;c chất bổ sung thực sự l&agrave;m tăng nguy cơ ung thư ở người h&uacute;t thuốc.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Uống rượu vừa phải, nếu uống</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Giới hạn m&igrave;nh một ly mỗi ng&agrave;y nếu l&agrave; một người phụ nữ hoặc hai ly một ng&agrave;y nếu l&agrave; một người đ&agrave;n &ocirc;ng. Bất cứ ai 65 tuổi trở l&ecirc;n n&ecirc;n kh&ocirc;ng uống nhiều hơn một ly một ng&agrave;y.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tập thể dục</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mục đ&iacute;ch để đạt được &iacute;t nhất 30 ph&uacute;t tập luyện tr&ecirc;n hầu hết c&aacute;c ng&agrave;y trong tuần. Kiểm tra với b&aacute;c sĩ đầu ti&ecirc;n nếu chưa thực hiện thường xuy&ecirc;n. Bắt đầu từ từ v&agrave; tiếp tục hoạt động th&ecirc;m nhiều hơn nữa. Đi xe đạp, bơi lội v&agrave; đi bộ l&agrave; lựa chọn tốt. Th&ecirc;m tập thể dục trong ng&agrave;y - từ c&ocirc;ng việc v&agrave; đi bộ phần c&ograve;n lại của đường hoặc đi cầu thang thay v&igrave; thang m&aacute;y.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: right;\"><span style=\"color: rgb(128, 0, 0);\"><b style=\"\"><i style=\"\"><span style=\"font-family: Arial;\"></span></i></b></span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><i style=\"\"><span style=\"font-family: Arial;\">&nbsp;</span></i></b><span style=\"font-family: Arial;\"><br>\r\n</span></p></span>\n', 1);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(15, 'Viêm màng phổi (pleuritis)', '\n   <h1 class=\"title\">Viêm màng phổi (pleuritis)</h1>   <span class=\"des\">Viêm màng phổi xảy ra như là một biến chứng của một loạt các điều kiện cơ bản. Làm giảm viêm màng phổi liên quan đến việc xử lý các điều kiện cơ bản nếu nó được biết đến, và dùng thuốc giảm đau.</span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m m&agrave;ng phổi xảy ra khi c&aacute;c lớp m&agrave;ng k&eacute;p (m&agrave;ng phổi) khoang ngực v&agrave; bao quanh mỗi phổi bị vi&ecirc;m. C&ograve;n được gọi l&agrave; pleuritis, vi&ecirc;m m&agrave;ng phổi thường g&acirc;y đau nh&oacute;i, hầu như lu&ocirc;n lu&ocirc;n khi c&oacute; hơi thở.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m m&agrave;ng phổi xảy ra như l&agrave; một biến chứng của một loạt c&aacute;c điều kiện cơ bản. L&agrave;m giảm vi&ecirc;m m&agrave;ng phổi li&ecirc;n quan đến việc xử l&yacute; c&aacute;c điều kiện cơ bản nếu n&oacute; được biết đến, v&agrave; d&ugrave;ng thuốc giảm đau.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c dấu hiệu v&agrave; triệu chứng của vi&ecirc;m m&agrave;ng phổi c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau ngực khi h&iacute;t v&agrave;o v&agrave; thở ra (giữa hơi thở, cảm thấy hầu như kh&ocirc;ng đau).</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho khan. </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sốt v&agrave; ớn lạnh, t&ugrave;y thuộc v&agrave;o nguy&ecirc;n nh&acirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c sắc th&aacute;i đau ngực l&agrave; nguy&ecirc;n nh&acirc;n g&acirc;y vi&ecirc;m m&agrave;ng phổi, n&ecirc;n tồi tệ hơn khi ho, hắt hơi, di chuyển v&agrave; h&iacute;t thở s&acirc;u. Trong một số trường hợp, cơn đau c&oacute; thể k&eacute;o d&agrave;i từ ngực v&agrave;o vai. C&oacute; thể t&igrave;m thấy giảm đau khi giữ hơi thở hoặc khi &aacute;p dụng &aacute;p lực l&ecirc;n v&ugrave;ng đau đớn.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi sự t&iacute;ch tụ c&aacute;c chất dịch (tr&agrave;n dịch m&agrave;ng phổi) được kết hợp với vi&ecirc;m m&agrave;ng phổi, đau thường biến mất bởi v&igrave; chất lỏng phục vụ như một chất b&ocirc;i trơn. Tuy nhi&ecirc;n, nếu đủ chất lỏng t&iacute;ch tụ, n&oacute; đặt &aacute;p lực l&ecirc;n phổi v&agrave; cản trở chức năng b&igrave;nh thường, g&acirc;y ra kh&oacute; thở. Nếu chất lỏng bị nhiễm tr&ugrave;ng, c&aacute;c dấu hiệu v&agrave; triệu chứng của bệnh sốt kh&ocirc;, ho v&agrave; ớn lạnh c&oacute; thể xuất hiện. Tr&agrave;n dịch m&agrave;ng phổi bị nhiễm được gọi l&agrave; empyema.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Đến gặp b&aacute;c sĩ khi</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Gọi b&aacute;c sĩ nếu c&oacute; trải nghiệm trong c&aacute;c c&aacute;ch sau: </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Dữ dội, đau ngực dai dẳng.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sốt cao.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những dấu hiệu v&agrave; triệu chứng chỉ ra một vấn đề với phổi hoặc m&agrave;ng phổi, v&agrave; cũng c&oacute; thể chỉ ra một căn bệnh tiềm ẩn cần chăm s&oacute;c y tế kịp thời. </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một lớp m&agrave;ng k&eacute;p gọi l&agrave; m&agrave;ng phổi ri&ecirc;ng biệt phổi v&agrave; th&agrave;nh ngực. Một lớp m&agrave;ng phổi che phủ trong mỗi phổi. Lớp kh&aacute;c phủ th&agrave;nh ngực b&ecirc;n trong. C&aacute;c lớp cũng giống như hai mảnh satin mịn x&aacute;t với nhau với ma s&aacute;t gần như kh&ocirc;ng c&oacute;, cho ph&eacute;p phổi mở rộng v&agrave; hợp đồng khi thở m&agrave; kh&ocirc;ng cần bất kỳ sự kh&aacute;ng cự của c&aacute;c lớp l&oacute;t th&agrave;nh ngực.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi bị vi&ecirc;m, hai lớp m&agrave;ng m&agrave;ng phổi ở b&ecirc;n bị ảnh hưởng của ngực xoa với nhau như hai miếng giấy nh&aacute;m, sản xuất c&aacute;c cơn đau của vi&ecirc;m m&agrave;ng phổi khi h&iacute;t v&agrave;o v&agrave; thở ra. </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c điều kiện y tế cơ bản c&oacute; thể g&acirc;y vi&ecirc;m m&agrave;ng phổi l&agrave; rất nhiều. Vi&ecirc;m m&agrave;ng phổi, nguy&ecirc;n nh&acirc;n bao gồm:</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bị nhiễm tr&ugrave;ng cấp t&iacute;nh do virus, chẳng hạn như c&uacute;m.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m phổi, trong những trường hợp trong đ&oacute; c&aacute;c phần của phổi bị nhiễm bệnh li&ecirc;n quan đến bề mặt của m&agrave;ng phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều kiện tự miễn như lupus, vi&ecirc;m khớp dạng thấp v&agrave; vi&ecirc;m gan tự miễn.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Lao v&agrave; bệnh nhiễm tr&ugrave;ng kh&aacute;c.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một cục m&aacute;u đ&ocirc;ng trong động mạch của phổi (tắc mạch phổi).</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m m&agrave;ng phổi cũng c&oacute; thể xảy ra như l&agrave; kết quả của chấn thương ở ngực hoặc sau khi phẫu thuật tim. Xương sườn g&atilde;y cũng c&oacute; thể g&acirc;y vi&ecirc;m m&agrave;ng phổi. C&oacute; thể g&atilde;y một xương sườn trong trường hợp kh&ocirc;ng chấn thương, chẳng hạn như từ một cơn ho nặng. Trong một số trường hợp, nguy&ecirc;n nh&acirc;n của vi&ecirc;m m&agrave;ng phổi l&agrave; kh&ocirc;ng biết (v&ocirc; căn).</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ung thư trong phổi hiếm khi g&acirc;y vi&ecirc;m m&agrave;ng phổi. Kh&ocirc;ng c&oacute; mối quan hệ giữa h&uacute;t thuốc v&agrave; vi&ecirc;m m&agrave;ng phổi, nhưng một &quot;người h&uacute;t thuốc ho&quot; sẽ l&agrave;m trầm trọng th&ecirc;m nỗi đau của t&igrave;nh trạng n&agrave;y.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">B&aacute;c sĩ c&oacute; thể thực hiện một chẩn đo&aacute;n dựa tr&ecirc;n dấu hiệu v&agrave; triệu chứng. Khi kiểm tra v&agrave; nghe ngực, b&aacute;c sĩ c&oacute; thể nghe thấy &quot;ch&agrave; ma s&aacute;t&quot; c&oacute; thể &acirc;m thanh như tiếng lạo xạo đi bộ tr&ecirc;n tuyết rất kh&ocirc;.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">B&aacute;c sĩ cũng c&oacute; thể sử dụng c&aacute;c thủ tục sau đ&acirc;y để x&aacute;c định nguy&ecirc;n nh&acirc;n cơ bản của vi&ecirc;m m&agrave;ng phổi: </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">H&igrave;nh ảnh.</span></b><span style=\"font-family: Arial;\"> Chụp X-quang c&oacute; thể hiển thị một khu vực của vi&ecirc;m phổi cho biết vi&ecirc;m phổi. B&aacute;c sĩ sẽ điều tra bất thường kh&ocirc;ng giải th&iacute;ch được nh&igrave;n thấy tr&ecirc;n X-quang với h&igrave;nh ảnh bổ sung, thường bắt đầu bằng chụp cắt lớp vi t&iacute;nh (CT scan). Trong thủ tục CT, một m&aacute;y t&iacute;nh dịch th&ocirc;ng tin từ X-quang v&agrave;o h&igrave;nh ảnh của c&aacute;c bộ phận mỏng (l&aacute;t) của ngực. CT sản xuất chi tiết h&igrave;nh ảnh nhiều hơn của c&aacute;c cơ quan nội bộ hơn so với X-quang học th&ocirc;ng thường. Đ&ocirc;i khi b&aacute;c sĩ muốn c&oacute; một kiểu đặc biệt của X-quang ngực, trong đ&oacute; nằm nghi&ecirc;ng, nơi m&agrave;ng phổi bị vi&ecirc;m để xem nếu c&oacute; bất kỳ chất lỏng m&agrave; kh&ocirc;ng xuất hiện tr&ecirc;n một X-quang ngực ti&ecirc;u chuẩn. Đ&acirc;y l&agrave; loại tia X được gọi l&agrave; ngực decubitus X-ray. B&aacute;c sĩ cũng c&oacute; thể sử dụng si&ecirc;u &acirc;m để x&aacute;c định xem liệu c&oacute; tr&agrave;n dịch m&agrave;ng phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">X&eacute;t nghiệm m&aacute;u.</span></b><span style=\"font-family: Arial;\"> X&eacute;t nghiệm m&aacute;u c&oacute; thể n&oacute;i với b&aacute;c sĩ nếu c&oacute; nhiễm tr&ugrave;ng, nếu c&oacute;, những loại nhiễm tr&ugrave;ng. X&eacute;t nghiệm m&aacute;u kh&aacute;c cũng c&oacute; thể ph&aacute;t hiện một rối loạn tự miễn như vi&ecirc;m khớp dạng thấp hoặc lupus, trong đ&oacute; c&aacute;c dấu hiệu ban đầu l&agrave; vi&ecirc;m m&agrave;ng phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thoracentesis.</span></b><span style=\"font-family: Arial;\"> Để loại bỏ chất lỏng để ph&acirc;n t&iacute;ch trong ph&ograve;ng th&iacute; nghiệm, b&aacute;c sĩ c&oacute; thể đề xuất một quy tr&igrave;nh gọi l&agrave; thoracentesis. Trong th�� thuật n&agrave;y, b&aacute;c sĩ đầu ti&ecirc;n ti&ecirc;m g&acirc;y t&ecirc; cục bộ, sau đ&oacute; ch&egrave;n một c&acirc;y kim qua th&agrave;nh ngực giữa c&aacute;c xương sườn để loại bỏ chất lỏng. Ngo&agrave;i ra, một mẫu m&ocirc; (m&agrave;ng phổi sinh thiết) để ph&acirc;n t&iacute;ch k&iacute;nh hiển vi c&oacute; thể c&oacute; được nếu b&aacute;c sĩ lo ngại rằng việc thu chất lỏng c&oacute; thể được g&acirc;y ra bởi bệnh lao phổi hoặc ung thư. Nếu chỉ một lượng nhỏ chất lỏng hiện nay, b&aacute;c sĩ c&oacute; thể ch&egrave;n c&aacute;c kim với sự trợ gi&uacute;p của hướng dẫn si&ecirc;u &acirc;m.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thoracoscopy.</span></b><span style=\"font-family: Arial;\"> Thủ tục n&agrave;y cho ph&eacute;p một b&aacute;c sĩ phẫu thuật xem b&ecirc;n trong ngực v&agrave; c&oacute; được một mẫu m&ocirc; m&agrave;ng phổi. Đầu ti&ecirc;n, b&aacute;c sĩ phẫu thuật l&agrave;m một hoặc nhiều vết mổ nhỏ giữa c&aacute;c xương sườn. Một ống với một m&aacute;y quay phim nhỏ sau đ&oacute; được đưa v&agrave;o khoang ngực - một thủ tục đ&ocirc;i khi được gọi video hỗ trợ phẫu thuật thoracoscopic (VATS). C&aacute;c c&ocirc;ng cụ phẫu thuật đặc biệt cho ph&eacute;p b&aacute;c sĩ phẫu thuật cắt bỏ m&ocirc; để thử nghiệm.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mục ti&ecirc;u quan trọng nhất trong việc l&agrave;m giảm vi&ecirc;m m&agrave;ng phổi hoặc tr&agrave;n dịch m&agrave;ng phổi l&agrave; điều trị t&igrave;nh trạng cơ bản g&acirc;y ra c&aacute;c dấu hiệu v&agrave; triệu chứng. V&iacute; dụ, nếu nguy&ecirc;n nh&acirc;n của vi&ecirc;m m&agrave;ng phổi l&agrave; một nhiễm tr&ugrave;ng phổi (vi&ecirc;m phổi), một kh&aacute;ng sinh c&oacute; thể kiểm so&aacute;t sự l&acirc;y nhiễm. Tuy nhi&ecirc;n, đối với vi&ecirc;m m&agrave;ng phổi do nhiễm si&ecirc;u vi, kh&aacute;ng sinh sẽ kh&ocirc;ng hiệu quả. Hầu hết nhiễm virus tự cải thiện m&agrave; kh&ocirc;ng cần điều trị.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thuốc chống vi&ecirc;m kh&ocirc;ng steroid (NSAID) c&oacute; thể gi&uacute;p giảm một số trong những dấu hiệu v&agrave; triệu chứng của vi&ecirc;m m&agrave;ng phổi. Prescription codein c&oacute; thể gi&uacute;p kiểm so&aacute;t ho cũng như cơn đau. Nếu c&oacute; sự t&iacute;ch tụ lớn c&aacute;c chất lỏng, c&oacute; thể cần ở lại bệnh viện để tho&aacute;t nước trong khoảng thời gian v&agrave;i ng&agrave;y qua một ống đưa v&agrave;o ngực.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kết quả của điều trị vi&ecirc;m m&agrave;ng phổi c&oacute; thể phụ thuộc v&agrave;o mức độ nghi&ecirc;m trọng của bệnh. </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: right;\"><span style=\"color: rgb(128, 0, 0);\"><b style=\"\"><i style=\"\"><span style=\"font-family: Arial;\"></span></i></b></span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">&nbsp;</span></p></span>\n', 1);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(16, 'Viêm phế quản', '\n   <h1 class=\"title\">Viêm phế quản</h1>   <span class=\"des\">Viêm phế quản là tình trạng viêm của lớp niêm mạc các ống phế quản, ống mang không khí đến và đi từ phổi. Viêm phế quản có thể là cấp tính hoặc mãn tính.</span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa<img width=\"1\" height=\"1\" alt=\"\" src=\"file:///C:/Users/MyPhuong/AppData/Local/Temp/msohtml1/01/clip_image001.gif\"></span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m phế quản l&agrave; t&igrave;nh trạng vi&ecirc;m của lớp ni&ecirc;m mạc c&aacute;c ống phế quản, ống mang kh&ocirc;ng kh&iacute; đến v&agrave; đi từ phổi. Vi&ecirc;m phế quản c&oacute; thể l&agrave; cấp t&iacute;nh hoặc m&atilde;n t&iacute;nh.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một điều kiện th&ocirc;ng thường, vi&ecirc;m phế quản cấp t&iacute;nh thường ph&aacute;t triển từ một nhiễm tr&ugrave;ng đường h&ocirc; hấp do lạnh hay kh&aacute;c. Vi&ecirc;m phế quản m&atilde;n t&iacute;nh, một t&igrave;nh trạng nghi&ecirc;m trọng hơn, l&agrave; một k&iacute;ch th&iacute;ch thường xuy&ecirc;n hoặc vi&ecirc;m ni&ecirc;m mạc của c&aacute;c ống phế quản, thường l&agrave; do h&uacute;t thuốc l&aacute;.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m phế quản cấp t&iacute;nh thường được cải thiện trong v&ograve;ng v&agrave;i ng&agrave;y m&agrave; kh&ocirc;ng l&acirc;u d&agrave;i, mặc d&ugrave; c&oacute; thể tiếp tục ho tới cả tuần. Tuy nhi&ecirc;n, nếu c&oacute; lặp đi lặp lại cơn của vi&ecirc;m phế quản, c&oacute; thể c&oacute; vi&ecirc;m phế quản m&atilde;n t&iacute;nh v&agrave; đ&ograve;i hỏi chăm s&oacute;c y tế. Vi&ecirc;m phế quản m&atilde;n t&iacute;nh l&agrave; một trong những điều kiện đưa đến bệnh phổi tắc nghẽn phổi m&atilde;n t&iacute;nh (COPD).</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều trị vi&ecirc;m phế quản tập trung v&agrave;o l&agrave;m giảm c&aacute;c triệu chứng v&agrave; giảm bớt hơi thở kh&oacute;.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đối với vi&ecirc;m phế quản cấp t&iacute;nh hoặc vi&ecirc;m phế quản m&atilde;n t&iacute;nh, dấu hiệu v&agrave; triệu chứng c&oacute; thể bao gồm:</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sản xuất đờm, hoặc trong hoặc m&agrave;u trắng hoặc m&agrave;u x&aacute;m v&agrave;ng hoặc m&agrave;u xanh lục.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở, l&agrave;m tồi tệ hơn bởi gắng sức nhẹ.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thở kh&ograve; kh&egrave;.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mệt mỏi.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sốt v&agrave; ớn lạnh.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tức ngực.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; vi&ecirc;m phế quản cấp t&iacute;nh, c&oacute; thể c&oacute; ho dai dẳng k&eacute;o d&agrave;i trong v&agrave;i tuần sau khi vi&ecirc;m phế quản được giải quyết. Tuy nhi&ecirc;n, c&aacute;c triệu chứng vi&ecirc;m phế quản c&oacute; thể được pha tạp. Kh&ocirc;ng lu&ocirc;n lu&ocirc;n tạo ra đờm khi c&oacute; vi&ecirc;m phế quản v&agrave; trẻ em thường nuốt đờm, do đ&oacute; cha mẹ c&oacute; thể kh&ocirc;ng biết bị nhiễm tr&ugrave;ng thứ cấp. C&oacute; thể ph&aacute;t triển vi&ecirc;m phế quản m&atilde;n m&agrave; kh&ocirc;ng cần đầu ti&ecirc;n ph&aacute;t triển vi&ecirc;m phế quản cấp t&iacute;nh. V&agrave; nhiều người h&uacute;t thuốc, cổ họng của họ mỗi buổi s&aacute;ng khi thức dậy c&oacute; đờm, trong đ&oacute; nếu n&oacute; tiếp tục trong hơn ba th&aacute;ng, c&oacute; thể bị vi&ecirc;m phế quản m&atilde;n t&iacute;nh.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c triệu chứng của vi&ecirc;m phế quản m&atilde;n t&iacute;nh:</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; vi&ecirc;m phế quản m&atilde;n, vi&ecirc;m l&acirc;u d&agrave;i dẫn đến sẹo của c&aacute;c ống phế quản, sản xuất chất nhờn qu&aacute; mức. Theo thời gian, những lớp m&agrave;ng d&agrave;y của ống phế quản v&agrave; đường h&ocirc; hấp cuối c&ugrave;ng c&oacute; thể trở th&agrave;nh sẹo. C&aacute;c dấu hiệu v&agrave; triệu chứng của vi&ecirc;m phế quản m&atilde;n t&iacute;nh cũng c&oacute; thể bao gồm:</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho l&agrave; tồi tệ hơn v&agrave;o buổi s&aacute;ng v&agrave; trong thời tiết ẩm ướt.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thường xuy&ecirc;n bị nhiễm tr&ugrave;ng đường h&ocirc; hấp (như cảm lạnh hoặc c&uacute;m ) với ho xấu đi.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; vi&ecirc;m phế quản m&atilde;n t&iacute;nh, c&oacute; thể c&oacute; thời kỳ c&aacute;c dấu hiệu v&agrave; triệu chứng xấu đi. V&agrave;o những thời điểm c&oacute; thể c&oacute; vi&ecirc;m phế quản cấp t&iacute;nh chồng l&ecirc;n hoặc do virus hoặc vi khuẩn ngo&agrave;i vi&ecirc;m phế quản m&atilde;n t&iacute;nh.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m phế quản cấp t&iacute;nh c&oacute; thể tự giải quyết trong một v&agrave;i ng&agrave;y. Đi kh&aacute;m b&aacute;c sĩ nếu:</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho l&agrave; nghi&ecirc;m trọng hoặc ngăn kh&ocirc;ng cho ngủ. B&aacute;c sĩ c&oacute; thể khuy&ecirc;n n&ecirc;n dung toa ngăn triệu chứng ho để gi&uacute;p nghỉ ngơi.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; sốt nhẹ vẫn c&ograve;n hơn ba ng&agrave;y hoặc sốt cao hơn 38,3<sup>0</sup>C, đang kh&oacute; thở hoặc ho ra m&aacute;u hoặc chất nhầy m&agrave;u v&agrave;ng hoặc m&agrave;u xanh l&aacute; c&acirc;y - c&oacute; thể đ&atilde; vi&ecirc;m phổi. Đổi m&agrave;u chất nhờn thường chỉ bị nhiễm vi khuẩn, c&oacute; thể đ&aacute;p ứng với kh&aacute;ng sinh. Cũng c&oacute; thể đ&atilde; ph&aacute;t triển một vi khuẩn vi&ecirc;m xoang.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho k&eacute;o d&agrave;i hơn ba tuần. C&aacute;c vi&ecirc;m nhiễm m&atilde;n t&iacute;nh khi bị nhiễm c&oacute; thể dẫn đến bệnh suyễn ở một số người.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; bệnh tim phổi m&atilde;n t&iacute;nh hay c&aacute;c vấn đề bao gồm cả bệnh hen suyễn, kh&iacute; phế thũng hoặc suy tim sung huyết, v&agrave; nghĩ rằng c&oacute; thể đ&atilde; ph&aacute;t triển vi&ecirc;m phế quản. C&aacute;c điều kiện n&agrave;y c&oacute; nguy cơ ph&aacute;t triển c&aacute;c biến chứng nhiễm tr&ugrave;ng phế quản.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đ&atilde; lặp đi lặp lại cơn vi&ecirc;m phế quản. C&oacute; thể c&oacute; vi&ecirc;m phế quản mạn t&iacute;nh, t&igrave;nh trạng sức khỏe nghi&ecirc;m trọng kh&aacute;c như bệnh suyễn hoặc chứng gi&atilde;n phế quản.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m phế quản cấp. C&ugrave;ng một virus g&acirc;y cảm lạnh thường g&acirc;y vi&ecirc;m phế quản cấp t&iacute;nh. Nhưng cũng c&oacute; thể ph&aacute;t triển vi&ecirc;m phế quản kh&ocirc;ng truyền nhiễm khi tiếp x&uacute;c với ai đ&oacute; hoặc của ri&ecirc;ng bản th&acirc;n h&uacute;t thuốc l&aacute; v&agrave; từ c&aacute;c chất &ocirc; nhiễm như chất tẩy rửa gia dụng v&agrave; sương kh&oacute;i.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m phế quản cũng c&oacute; thể xảy ra khi axit từ dạ d&agrave;y lu&ocirc;n tr&agrave;o ngược v&agrave;o ống thực quản v&agrave; một v&agrave;i giọt đi v&agrave;o đường h&ocirc; hấp tr&ecirc;n, một điều kiện được gọi l&agrave; bệnh tr&agrave;o ngược dạ d&agrave;y (GERD). V&agrave; c&ocirc;ng nh&acirc;n tiếp x&uacute;c với kh&oacute;i bụi nhất định c&oacute; thể ph&aacute;t triển vi&ecirc;m phế quản nghề nghiệp - một bệnh cấp t&iacute;nh, n&oacute;i chung l&ecirc;n ngừng khi tiếp x&uacute;c với c&aacute;c điểm k&iacute;ch ứng.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m phế quản m&atilde;n t&iacute;nh. Đ&ocirc;i khi vi&ecirc;m v&agrave; d&agrave;y l&ecirc;n của m&agrave;ng ống phế quản trở th&agrave;nh vĩnh viễn - một điều kiện được gọi l&agrave; vi&ecirc;m phế quản m&atilde;n t&iacute;nh. Thường được coi l&agrave; c&oacute; vi&ecirc;m phế quản m&atilde;n, nếu ho hầu hết c&aacute;c ng&agrave;y trong &iacute;t nhất ba th&aacute;ng một năm trong hai năm li&ecirc;n tiếp. Th&ocirc;ng thường, tuy nhi&ecirc;n, người h&uacute;t thuốc bị ho do vi&ecirc;m phế quản m&atilde;n hầu như mỗi ng&agrave;y.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng giống như vi&ecirc;m phế quản cấp t&iacute;nh, vi&ecirc;m phế quản m&atilde;n t&iacute;nh l&agrave; một bệnh nghi&ecirc;m trọng đang diễn ra. H&uacute;t thuốc l&aacute; l&agrave; nguy&ecirc;n nh&acirc;n ch&iacute;nh, nhưng kh&ocirc;ng kh&iacute; &ocirc; nhiễm v&agrave; bụi hoặc kh&iacute; độc trong m&ocirc;i trường hoặc nơi l&agrave;m việc cũng c&oacute; thể g&oacute;p phần v&agrave;o t&igrave;nh trạng n&agrave;y.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những yếu tố l&agrave;m tăng nguy cơ vi&ecirc;m phế quản bao gồm:</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Kh&oacute;i thuốc l&aacute;.</span></b><span style=\"font-family: Arial;\"> Những người h&uacute;t thuốc hoặc những người sống với người h&uacute;t thuốc l&agrave; nguy cơ lớn nhất của cả hai vi&ecirc;m phế quản cấp t&iacute;nh v&agrave; vi&ecirc;m phế quản m&atilde;n. Trẻ em trong c&aacute;c hộ gia đ&igrave;nh c&oacute; người h&uacute;t thuốc l&aacute; cũng dễ bị vi&ecirc;m phế quản, cũng như hen suyễn, vi&ecirc;m phổi, cảm lạnh v&agrave; vi&ecirc;m tai giữa.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đề kh&aacute;ng thấp.</span></b><span style=\"font-family: Arial;\"> Điều n&agrave;y c&oacute; thể l&agrave; kết quả của một bệnh cấp t&iacute;nh, chẳng hạn như cảm lạnh hoặc từ một t&igrave;nh trạng m&atilde;n t&iacute;nh thỏa hiệp hệ thống miễn dịch. Người lớn tuổi, trẻ sơ sinh v&agrave; trẻ nhỏ c&oacute; lỗ hổng lớn đến nhiễm tr&ugrave;ng.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tr&agrave;o ngược dạ d&agrave;y thực quản (GERD).</span></b><span style=\"font-family: Arial;\"> Axit dạ d&agrave;y m&agrave; li&ecirc;n tục tr&agrave;o ngược v&agrave;o thực quản c&oacute; thể g&acirc;y ra ho m&atilde;n t&iacute;nh.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tiếp x&uacute;c với chất k&iacute;ch th&iacute;ch trong c&ocirc;ng việc.</span></b><span style=\"font-family: Arial;\"> C&aacute;c nguy cơ ph&aacute;t triển bệnh vi&ecirc;m phế quản nghề nghiệp nếu m&ocirc;i trường l&agrave;m việc xung quanh g&acirc;y k&iacute;ch ứng nhất định, chẳng hạn như hạt hoặc h&agrave;ng dệt, hoặc<span style=\"\">&nbsp; </span>tiếp x&uacute;c với kh&oacute;i ho&aacute; chất từ amoniac, axit mạnh, clo, sulfua hydro, kh&iacute; lưu huỳnh hoặc br&ocirc;m. Ho li&ecirc;n quan đến vi&ecirc;m phế quản nghề nghiệp c&oacute; thể bị kh&ocirc;. Vi&ecirc;m phế quản nghề nghiệp thường sẽ tự hết khi kh&ocirc;ng c&ograve;n tiếp x&uacute;c với c&aacute;c chất n&agrave;y. Đ&acirc;y l&agrave; loại vi&ecirc;m phế quản kh&ocirc;ng li&ecirc;n quan đến bất kỳ t&aacute;c nh&acirc;n g&acirc;y bệnh, nhưng k&iacute;ch ứng của đường h&ocirc; hấp l&agrave;m cho dễ nhận một nhiễm tr&ugrave;ng.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mặc d&ugrave; vi&ecirc;m phế quản thường kh&ocirc;ng phải l&agrave; mối lo lớn, n&oacute; c&oacute; thể dẫn đến vi&ecirc;m phổi ở một số người. Người lớn tuổi, trẻ sơ sinh, những người h&uacute;t thuốc v&agrave; những người c&oacute; rối loạn m&atilde;n t&iacute;nh về đường h&ocirc; hấp hoặc tim, c&aacute;c vấn đề c&oacute; nguy cơ cao mắc bệnh vi&ecirc;m phổi.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Lặp đi lặp lại của cơn vi&ecirc;m phế quản nghi&ecirc;m trọng. C&oacute; thể t&iacute;n hiệu:</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m phế quản m&atilde;n t&iacute;nh.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hen.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c rối loạn phổi.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ngo&agrave;i ra, nếu c&oacute; vi&ecirc;m phế quản m&atilde;n t&iacute;nh v&agrave; tiếp tục h&uacute;t thuốc l&aacute;, nguy cơ ung thư phổi tăng vượt ra ngo&agrave;i b&igrave;nh thường m&agrave; người h&uacute;t thuốc c&oacute; nguy cơ phải đối mặt.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Những chuẩn bị cho việc kh&aacute;m bệnh</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; vi&ecirc;m phế quản m&atilde;n t&iacute;nh, c&oacute; thể gọi một b&aacute;c sỹ chuy&ecirc;n khoa phổi.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Để c&oacute; được tất cả c&aacute;c th&ocirc;ng tin cần thiết từ b&aacute;c sĩ, rất tốt để được chuẩn bị cho buổi kh&aacute;m. Đ&acirc;y l&agrave; c&aacute;ch l&agrave;m.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Những g&igrave; c&oacute; thể l&agrave;m</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Viết ra bất kỳ triệu chứng, Ngay cả khi n&oacute; c&oacute; vẻ kh&ocirc;ng li&ecirc;n quan đến l&yacute; do dự kiến kh&aacute;m, v&agrave; đ&atilde; c&oacute; triệu chứng bao l&acirc;u.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ghi th&ocirc;ng tin c&aacute; nh&acirc;n ch&iacute;nh, bao gồm bất kỳ cuộc sống thay đổi gần đ&acirc;y hoặc tiếp x&uacute;c với chất k&iacute;ch th&iacute;ch trong nh&agrave; hoặc nơi l&agrave;m việc.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tạo một danh s&aacute;ch tất cả thuốc men, vitamin v&agrave; bổ sung đang d&ugrave;ng.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">B&aacute;o cho b&aacute;c sĩ cho d&ugrave; nhận được mũi ch&iacute;ch ngừa c&uacute;m h&agrave;ng năm v&agrave; cho d&ugrave; đ&atilde; c&oacute; một mũi ph&ograve;ng vi&ecirc;m phổi.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">B&aacute;o cho b&aacute;c sĩ nếu h&uacute;t thuốc, ngay cả khi đ&atilde; cắt giảm đến hai hoặc ba điếu thuốc mỗi ng&agrave;y. H&uacute;t hai hoặc ba điếu thuốc mỗi ng&agrave;y c&oacute; thể kh&ocirc;ng c&oacute; vẻ nhiều, nhưng n&oacute; c&oacute; thể đủ để duy tr&igrave; vi&ecirc;m phế quản.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đi với một th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh nếu c&oacute; thể. Một người n&agrave;o đ&oacute; đi c&ugrave;ng c&oacute; thể nhớ một c&aacute;i g&igrave; đ&oacute; m&agrave; bị mất hoặc qu&ecirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu đ&atilde; từng gặp một b&aacute;c sỹ kh&aacute;c, cho b&aacute;c sĩ hiện tại biết những g&igrave; c&aacute;c x&eacute;t nghiệm đ&atilde; được thực hiện, v&agrave; nếu c&oacute; thể mang c&aacute;c kết quả, bao gồm kết quả của x - quang v&agrave; thử nghiệm chức năng phổi.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">B&aacute;o cho b&aacute;c sĩ về bất cứ loại thuốc theo toa đ&atilde; d&ugrave;ng.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Chuẩn bị một danh s&aacute;ch c&aacute;c c&acirc;u hỏi trước sẽ gi&uacute;p tận dụng tối đa thời gian với b&aacute;c sĩ. Danh s&aacute;ch c&aacute;c c&acirc;u hỏi từ quan trọng nhất đến &iacute;t quan trọng. Đối với vi&ecirc;m phế quản, một số c&acirc;u hỏi cơ bản để y&ecirc;u cầu b&aacute;c sĩ bao gồm:</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những g&igrave; c&oacute; thể g&acirc;y ra triệu chứng hoặc t&igrave;nh trạng n&agrave;y?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; thể bị dị ứng với con vật nu&ocirc;i kh&ocirc;ng?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&aacute;c hơn l&agrave; nguy&ecirc;n nh&acirc;n rất c&oacute; thể, nguy&ecirc;n nh&acirc;n kh&aacute;c c&oacute; thể c&oacute; c&aacute;c triệu chứng hoặc t&igrave;nh trạng n&agrave;y l&agrave; g&igrave;?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những loại kiểm tra cần?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c triệu chứng của t&ocirc;i c&oacute; thể tạm thời hoặc m&atilde;n t&iacute;nh?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c lựa chọn thay thế cho phương ph&aacute;p đang đề xuất l&agrave; g&igrave;?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; điều kiện y tế n&agrave;y kh&aacute;c. L&agrave;m thế n&agrave;o c&oacute; thể quản l&yacute; ch&uacute;ng với nhau?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; bất kỳ hạn chế cần phải l&agrave;m theo?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu t&ocirc;i c&oacute; GERD, sẽ xử l&yacute; n&oacute; gi&uacute;p c&ugrave;ng vi&ecirc;m phế quản kh&ocirc;ng?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều g&igrave; sẽ l&agrave; chi ph&iacute; v&agrave; bảo hiểm?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; bất kỳ t&agrave;i liệu quảng c&aacute;o hay t&agrave;i liệu in kh&aacute;c m&agrave; thể mang về?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;ch tốt nhất để bỏ h&uacute;t thuốc l&aacute; l&agrave; g&igrave;?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ngo&agrave;i c&aacute;c c&acirc;u hỏi qu&yacute; vị chuẩn bị, kh&ocirc;ng ngần ngại đặt c&acirc;u hỏi bất cứ l&uacute;c n&agrave;o m&agrave; kh&ocirc;ng hiểu điều g&igrave; đ&oacute;.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">B&aacute;c sĩ c&oacute; thể sẽ hỏi một số c&acirc;u hỏi, chẳng hạn như</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Gần đ&acirc;y c&oacute; bị cảm lạnh hoặc cảm c&uacute;m?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đ&atilde; bao giờ đ&atilde; c&oacute; bệnh vi&ecirc;m phổi? ( Nếu c&oacute;, người đ&oacute; c&oacute; thể muốn X - quang chẩn đo&aacute;n vi&ecirc;m phổi)</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">L&agrave; một người h&uacute;t thuốc hoặc xung quanh c&oacute; người h&uacute;t thuốc hoặc c&aacute;c chất &ocirc; nhiễm kh&aacute;c hoặc kh&oacute;i ?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi n&agrave;o bắt đầu trải qua những triệu chứng đầu ti&ecirc;n?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; c&aacute;c triệu chứng li&ecirc;n tục hoặc thỉnh thoảng?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi n&agrave;o triệu chứng nặng?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; tập thể dục? C&oacute; thể leo l&ecirc;n cầu thang m&agrave; kh&ocirc;ng gặp kh&oacute; khăn? C&oacute; thể đi bộ nhanh?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; đau ngực ?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều g&igrave;, nếu bất cứ điều g&igrave; dường như để cải thiện c&aacute;c triệu chứng?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; ng&aacute;y to v&agrave;o ban đ&ecirc;m?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thức dậy v&agrave;o buổi s&aacute;ng cảm gi&aacute;c như đ&atilde; kh&ocirc;ng nhận được ngủ nhiều?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều g&igrave;, nếu bất cứ điều g&igrave; xuất hiện c&aacute;c triệu chứng xấu đi?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng kh&iacute; lạnh l&agrave;m phiền ?</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Những g&igrave; c&oacute; thể l&agrave;m trong khi chờ đợi, c&oacute; thể gi&uacute;p giảm bớt triệu chứng</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&atilde;y thử c&oacute; được giấc ngủ d&agrave;i một đ&ecirc;m.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Uống nhiều chất lỏng.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tr&aacute;nh tiếp x&uacute;c với kh&oacute;i thuốc l&aacute;.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Giữ cho kh&ocirc;ng kh&iacute; trong nh&agrave; ấm &aacute;p v&agrave; ẩm.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n<img width=\"1\" height=\"1\" alt=\"\" src=\"file:///C:/Users/MyPhuong/AppData/Local/Temp/msohtml1/01/clip_image001.gif\"></span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Để chẩn đo&aacute;n vi&ecirc;m phế quản, b&aacute;c sĩ c&oacute; thể đi theo c&aacute;ch tiếp cận sau đ&acirc;y:</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sử dụng một ống nghe để nghe thở kh&ograve; kh&egrave; v&agrave; c&aacute;c &acirc;m thanh kh&aacute;c bất thường ở phổi.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">X - quang ngực.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đ&atilde; trải qua x&eacute;t nghiệm m&aacute;u.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ph&acirc;n t&iacute;ch đờm - một thử nghiệm kiểm tra sự hiện diện của vi khuẩn trong đờm khi ho ra.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đ&atilde; tham gia một thử nghiệm chức năng phổi (PFT), để loại trừ c&aacute;c nguy&ecirc;n nh&acirc;n kh&aacute;c cho c&aacute;c triệu chứng.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thử nghiệm chức năng phổi: </span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thử nghiệm n&agrave;y cho c&aacute;c dấu hiệu của bệnh hen hay bệnh kh&iacute; thũng. Trong thử nghiệm chức năng phổi, thổi v&agrave;o một thiết bị gọi l&agrave; phế dung kế, m&agrave; c&aacute;c biện ph&aacute;p khối lượng kh&ocirc;ng kh&iacute; trong phổi sau khi đ&atilde; lấy một hơi thở s&acirc;u v&agrave; thổi n&oacute; ra. Phế dung kế n&agrave;y cũng cho thấy c&aacute;ch nhanh ch&oacute;ng c&oacute; thể nhận được kh&ocirc;ng kh&iacute; ra khỏi phổi.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kiểm tra l&agrave; kh&ocirc;ng đau v&agrave; chỉ mất v&agrave;i ph&uacute;t. Nếu c&oacute; lặp đi lặp lại cơn vi&ecirc;m phế quản v&agrave; b&aacute;c sĩ kh&ocirc;ng đề xuất một thử nghiệm chức năng phổi, h&atilde;y y&ecirc;u cầu để thực hiện.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mục ti&ecirc;u của điều trị cho vi&ecirc;m phế quản l&agrave; để giảm triệu chứng v&agrave; dễ thở. Đ&ocirc;i khi, tất cả c&oacute; thể cần phải phục hồi từ bệnh vi&ecirc;m phế quản cấp t&iacute;nh, c&oacute; thể l&agrave;:</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Uống nhiều chất lỏng.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thở trong kh&ocirc;ng kh&iacute; ấm v&agrave; ẩm.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một toa thuốc giảm triệu chứng ho v&agrave; acetaminophen hoặc aspirin.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong một số trường hợp, b&aacute;c sĩ c&oacute; thể k&ecirc; toa cho thuốc:</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc kh&aacute;ng sinh.</span></b><span style=\"font-family: Arial;\"> Vi&ecirc;m phế quản thường l&agrave; kết quả sau khi bị nhiễm virus, do đ&oacute; kh&aacute;ng sinh kh&ocirc;ng hiệu quả. Tuy nhi&ecirc;n, b&aacute;c sĩ c&oacute; thể k&ecirc; toa thuốc kh&aacute;ng sinh nếu anh ta hoặc c&ocirc; ta nghi ngờ rằng c&oacute; một nhiễm tr&ugrave;ng vi khuẩn. Nếu c&oacute; một rối loạn phổi m&atilde;n t&iacute;nh hoặc nếu h&uacute;t thuốc, b&aacute;c sĩ c&oacute; thể k&ecirc; đơn thuốc kh&aacute;ng sinh để giảm nguy cơ nhiễm tr&ugrave;ng nghi&ecirc;m trọng thứ cấp.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc ho.</span></b><span style=\"font-family: Arial;\"> Tốt nhất l&agrave; kh&ocirc;ng giảm ho v&igrave; nước nhầy ngược trở về v&agrave; bởi v&igrave; ho gi&uacute;p loại bỏ c&aacute;c chất k&iacute;ch th&iacute;ch từ phổi v&agrave; những đoạn kh&ocirc;ng kh&iacute;. Nếu ho từ giấc ngủ, sử dụng thuốc ho OTC đủ để c&oacute; thể nghỉ ngơi, nhưng kh&ocirc;ng đủ để ngăn chặn ho ho&agrave;n to&agrave;n. Nếu ho tước đoạt giấc ngủ, b&aacute;c sĩ c&oacute; thể đề nghị một thuốc giảm ho theo toa.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">C&aacute;c thuốc kh&aacute;c.</span></b><span style=\"font-family: Arial;\"> Nếu c&oacute; bệnh hen hoặc bệnh phổi tắc nghẽn m&atilde;n t&iacute;nh (COPD), b&aacute;c sĩ c&oacute; thể khuy&ecirc;n một ống thuốc xịt v&agrave; c&aacute;c thuốc kh&aacute;c để giảm vi&ecirc;m v&agrave; mở đoạn ống thu hẹp trong phổi.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Liệu Ph&aacute;p.</span></b><span style=\"font-family: Arial;\"> Nếu c&oacute; vi&ecirc;m phế quản m&atilde;n t&iacute;nh, n&oacute;i chuyện với b&aacute;c sĩ về phục hồi chức năng phổi. Phục hồi chức năng phổi l&agrave; một chương tr&igrave;nh tập luyện thở, l&agrave;m việc với một b&aacute;c sĩ chuy&ecirc;n khoa h&ocirc; hấp để gi&uacute;p học c&aacute;ch h&iacute;t thở dễ d&agrave;ng hơn v&agrave; tăng khả năng tập thể dục.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phong c&aacute;ch sống v&agrave; biện ph&aacute;p khắc phục </span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">B&ecirc;n cạnh c&aacute;c phương ph&aacute;p điều trị cơ bản, chất lỏng v&agrave; thuốc ho kh&ocirc;ng k&ecirc; toa, c&aacute;c đề xuất n&agrave;y c&oacute; thể gi&uacute;p l&agrave;m cho thoải m&aacute;i hơn, tốc độ hồi phục v&agrave; ngăn ngừa c&aacute;c biến chứng của vi&ecirc;m phế quản cấp t&iacute;nh v&agrave; gi&uacute;p kiểm so&aacute;t c&aacute;c triệu chứng của vi&ecirc;m phế quản m&atilde;n t&iacute;nh:</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tr&aacute;nh tiếp x&uacute;c với chất k&iacute;ch th&iacute;ch, chẳng hạn như kh&oacute;i thuốc l&aacute;. Kh&ocirc;ng h&uacute;t thuốc. Mang khẩu trang khi kh&ocirc;ng kh&iacute; bị &ocirc; nhiễm hoặc nếu đang tiếp x&uacute;c với chất k&iacute;ch th&iacute;ch, chẳng hạn như sơn hay chất tẩy rửa gia dụng với hơi mạnh.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sử dụng m&aacute;y tạo độ ẩm trong ph&ograve;ng. Ấm &aacute;p, ẩm kh&ocirc;ng kh&iacute; gi&uacute;p giảm ho v&agrave; l&agrave;m lỏng chất nhờn ở đường h&ocirc; hấp. Nhưng h&atilde;y chắc chắn l&agrave;m sạch độ ẩm theo khuyến nghị của nh&agrave; sản xuất để tr&aacute;nh sự ph&aacute;t triển của vi khuẩn v&agrave; nấm trong ngăn chứa nước.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sử dụng toa thuốc. Để giảm đau v&agrave; hạ sốt cao, acetaminophen v&agrave; ibuprofen c&oacute; thể gi&uacute;p đỡ.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Xem x&eacute;t một khẩu trang. Nếu kh&ocirc;ng kh&iacute; lạnh c&agrave;ng l&agrave;m trầm trọng ho v&agrave; kh&oacute; thở, d&ugrave;ng khẩu trang trước khi đi ra ngo&agrave;i.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&atilde;y thử thở m&iacute;m m&ocirc;i. Nếu c&oacute; vi&ecirc;m phế quản m&atilde;n t&iacute;nh, c&oacute; thể h&iacute;t thở qu&aacute; nhanh. Thở m&iacute;m m&ocirc;i gi&uacute;p l&agrave;m chậm hơi thở, v&agrave; c&oacute; thể l&agrave;m cảm thấy tốt hơn. H&atilde;y h&iacute;t thở s&acirc;u sau đ&oacute; từ từ thở ra qua miệng trong khi m&iacute;m m&ocirc;i. Lặp lại kỹ thuật n&agrave;y l&agrave;m tăng &aacute;p suất kh&ocirc;ng kh&iacute; trong đường h&ocirc; hấp.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Thuốc thay thế</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một số người tin rằng một số biện ph&aacute;p cứu trợ từ thảo dược cung cấp tốt cho vi&ecirc;m phế quản cấp t&iacute;nh.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thảo dược. Kh&ocirc;ng c&oacute; bằng chứng từ ngẫu nhi&ecirc;n, c&aacute;c nghi&ecirc;n cứu kiểm so&aacute;t để đề xuất việc sử dụng c&aacute;c loại dược thảo Trung Quốc cho vi&ecirc;m phế quản, v&agrave; sự an to&agrave;n của loại thảo mộc Trung Quốc l&agrave; kh&ocirc;ng r&otilde;.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&acirc;y thi&ecirc;n tr&uacute;c. Chi Mỏ hạc n&agrave;y của Nam Phi l&agrave; một thảo dược m&agrave; đ&atilde; thể hiện một số hiệu quả trong việc giảm triệu chứng của vi&ecirc;m phế quản cấp t&iacute;nh. Nghi&ecirc;n cứu th&ecirc;m l&agrave; cần thiết. Kiểm tra với b&aacute;c sĩ trước khi thử bất kỳ thảo dược n&agrave;o.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Ph&ograve;ng chống</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; thường xuy&ecirc;n lặp đi lặp lại c&aacute;c cuộc tấn c&ocirc;ng của vi&ecirc;m phế quản, thủ phạm c&oacute; thể l&agrave; một c&aacute;i g&igrave; đ&oacute; trong m&ocirc;i trường. Lạnh, địa điểm ẩm - đặc biệt l&agrave; kết hợp với &ocirc; nhiễm kh&ocirc;ng kh&iacute; hoặc kh&oacute;i thuốc l&aacute; c&oacute; thể l&agrave;m dễ bị vi&ecirc;m phế quản cấp t&iacute;nh. Khi vấn đề l&agrave; nghi&ecirc;m trọng, c&oacute; thể cần phải xem x&eacute;t thay đổi ở đ&acirc;u v&agrave; l&agrave;m thế n&agrave;o để sống v&agrave; l&agrave;m việc.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những biện ph&aacute;p n&agrave;y cũng c&oacute; thể gi&uacute;p giảm nguy cơ vi&ecirc;m phế quản v&agrave; bảo vệ phổi n&oacute;i chung:</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tr&aacute;nh h&uacute;t thuốc v&agrave; tiếp x&uacute;c với kh&oacute;i thuốc l&aacute;. Kh&oacute;i thuốc l&aacute; l&agrave;m tăng nguy cơ vi&ecirc;m phế quản m&atilde;n t&iacute;nh v&agrave; bệnh kh&iacute; thũng.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Cố gắng tr&aacute;nh những người c&oacute; cảm lạnh hoặc c&uacute;m - đang tiếp x&uacute;c với c&aacute;c vi r&uacute;t dẫn đến vi&ecirc;m phế quản, nguy cơ l&acirc;y nhiễm n&oacute; thấp hơn. Tr&aacute;nh đ&aacute;m đ&ocirc;ng trong m&ugrave;a c&uacute;m.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhận được một vắc-xin c&uacute;m h&agrave;ng năm. Nhiều trường hợp vi&ecirc;m phế quản cấp t&iacute;nh kết quả từ c&uacute;m - một virus. Một thuốc ngừa chủng c&uacute;m h&agrave;ng năm c&oacute; thể gi&uacute;p bảo vệ khỏi mắc bệnh c&uacute;m, do đ&oacute; c&oacute; thể giảm nguy cơ vi&ecirc;m phế quản.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&atilde;y hỏi b&aacute;c sĩ về mũi ti&ecirc;m ph&ograve;ng vi&ecirc;m phổi. Nếu lớn tuổi hơn 60 hoặc c&oacute; yếu tố nguy cơ như tiểu đường, bệnh tim v&agrave; bệnh kh&iacute; thũng, h&atilde;y xem x&eacute;t việc c&oacute; mũi ti&ecirc;m ph&ograve;ng vi&ecirc;m phổi. Ngo&agrave;i ra, một loại thuốc được biết đến như Prevnar c&oacute; thể gi&uacute;p bảo vệ trẻ em chống lại vi&ecirc;m phổi. Đ&oacute; l&agrave; khuyến c&aacute;o cho tất cả trẻ em dưới 2 tuổi v&agrave; cho trẻ em 2 - 5 tuổi c&oacute; nguy cơ đặc biệt của bệnh phế cầu khuẩn, chẳng hạn như những người c&oacute; thiếu một hệ miễn dịch, bệnh hen, bệnh tim mạch hoặc thiếu m&aacute;u tế b&agrave;o h&igrave;nh liềm. T&aacute;c dụng phụ của vắc-xin phế cầu khuẩn n&agrave;y thường nhỏ v&agrave; bao gồm đau nhẹ hoặc sưng tại chỗ ti&ecirc;m. Nếu đ&atilde; c&oacute; mũi ti&ecirc;m ngừa bệnh vi&ecirc;m phổi năm trước hoặc nhiều hơn năm trước, b&aacute;c sĩ c&oacute; thể khuy&ecirc;n n&ecirc;n nhận được một mũi kh&aacute;c .</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Rửa tay hoặc sử dụng thuốc rửa tay thường xuy&ecirc;n. Để giảm nguy cơ bị nhiễm si&ecirc;u vi, rửa tay thường xuy&ecirc;n v&agrave; c&oacute; th&oacute;i quen sử dụng thuốc rửa tay v&agrave; kh&ocirc;ng chạm v&agrave;o b&ecirc;n trong mũi hay ch&agrave; mắt.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đeo khẩu trang. Nếu phải ti&ecirc;u tốn rất nhiều thời gian gần những người kh&aacute;c, những người đang ho v&agrave; hắt hơi, l&agrave; một &yacute; tưởng tốt để mang khẩu trang che miệng v&agrave; mũi để giảm nguy cơ nhiễm tr&ugrave;ng.</span></p>\r\n<p style=\"margin: 12pt 0in; line-height: 150%; text-align: right;\"><b style=\"\"><i style=\"\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(128, 0, 0);\"></span></span></i></b></p>\r\n<p class=\"MsoNormal\" style=\"line-height: 150%; text-align: justify;\">&nbsp;</p></span>\n', 1);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(17, 'Viêm phổi', '\n   <h1 class=\"title\">Viêm phổi</h1>   <span class=\"des\">Viêm phổi có thể là ở mức độ từ nhẹ đến đe dọa tính mạng. Viêm phổi thường là biến chứng của bệnh khác, chẳng hạn như bệnh cúm</span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m phổi l&agrave; t&igrave;nh trạng vi&ecirc;m thường do nhiễm tr&ugrave;ng. Vi khuẩn, virus, nấm hay k&yacute; sinh tr&ugrave;ng c&oacute; thể g&acirc;y vi&ecirc;m phổi. Vi&ecirc;m phổi l&agrave; một quan t&acirc;m đặc biệt nếu tr&ecirc;n 65 tuổi hoặc c&oacute; bệnh m&atilde;n t&iacute;nh hoặc hệ thống miễn dịch k&eacute;m. N&oacute; cũng c&oacute; thể xảy ra ở trẻ, những người khỏe mạnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m phổi c&oacute; thể l&agrave; ở mức độ từ nhẹ đến đe dọa t&iacute;nh mạng. Vi&ecirc;m phổi thường l&agrave; biến chứng của bệnh kh&aacute;c, chẳng hạn như bệnh c&uacute;m. Kh&aacute;ng sinh c&oacute; thể xử l&yacute; c&aacute;c h&igrave;nh thức phổ biến nhất của vi khuẩn pneumonias, nhưng c&aacute;c chủng kh&aacute;ng thuốc l&agrave; một vấn đề ng&agrave;y c&agrave;ng tăng. C&aacute;ch tiếp cận tốt nhất l&agrave; cố gắng để ngăn chặn nhiễm tr&ugrave;ng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m phổi, triệu chứng c&oacute; thể kh&aacute;c nhau rất nhiều, t&ugrave;y thuộc v&agrave;o bất kỳ điều kiện cơ bản c&oacute; thể c&oacute; v&agrave; loại sinh vật g&acirc;y bệnh. Vi&ecirc;m phổi thường bắt chước c&aacute;c bệnh c&uacute;m, bắt đầu với một cơn ho v&agrave; sốt, v&igrave; vậy c&oacute; thể kh&ocirc;ng nhận ra rằng m&igrave;nh c&oacute; một t&igrave;nh trạng nghi&ecirc;m trọng hơn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Dấu hiệu thường gặp v&agrave; triệu chứng của vi&ecirc;m phổi c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sốt.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ra mồ h&ocirc;i.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ớn lạnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau ngực c&oacute; biến động với thở (vi&ecirc;m m&agrave;ng phổi).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhức đầu.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau cơ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mệt mỏi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trớ tr&ecirc;u thay, người trong nh&oacute;m nguy cơ cao dễ bị tổn thương như người cao ni&ecirc;n v&agrave; những người c&oacute; bệnh m&atilde;n t&iacute;nh hoặc suy yếu hệ thống miễn dịch c&oacute; thể c&oacute; c&aacute;c triệu chứng &iacute;t hơn hoặc nhẹ hơn. V&agrave; thay v&igrave; c&oacute; những cơn sốt cao, thư���ng đặc trưng vi&ecirc;m phổi, người gi&agrave; thậm ch&iacute; c&oacute; thể c&oacute; nhiệt độ thấp hơn b&igrave;nh thường.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Đến gặp b&aacute;c sĩ khi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bởi v&igrave; vi&ecirc;m phổi c&oacute; thể đe dọa t&iacute;nh mạng, gặp b&aacute;c sĩ c&agrave;ng sớm c&agrave;ng tốt nếu bị ho dai dẳng, kh&oacute; thở, đau ngực, sốt kh&ocirc;ng r&otilde; nguy&ecirc;n nh&acirc;n - đặc biệt l&agrave; sốt k&eacute;o d&agrave;i 38.9<sup>0</sup>C hoặc cao hơn với ớn lạnh v&agrave; ra mồ h&ocirc;i, hoặc nếu đột nhi&ecirc;n cảm thấy tồi tệ hơn sau khi bị cảm lạnh hoặc c&uacute;m. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&atilde;y đặc biệt nhắc về t&igrave;m kiếm chăm s&oacute;c y tế nếu l&agrave; một người cao ni&ecirc;n hoặc h&uacute;t thuốc, uống rượu qu&aacute; mức, c&oacute; một thương t&iacute;ch, đang trải qua h&oacute;a trị hoặc d&ugrave;ng thuốc như prednisone l&agrave; ngăn chặn hệ thống miễn dịch. Đối với một số người lớn tuổi v&agrave; những người c&oacute; bệnh tim hoặc phổi, vi&ecirc;m phổi c&oacute; thể nhanh ch&oacute;ng trở th&agrave;nh một t&igrave;nh trạng đe dọa t&iacute;nh mạng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Cơ thể c&oacute; nhiều c&aacute;ch để bảo vệ phổi bị l&acirc;y nhiễm. Trong thực tế, thường xuy&ecirc;n tiếp x&uacute;c với vi khuẩn v&agrave; virus c&oacute; thể g&acirc;y vi&ecirc;m phổi, nhưng cơ thể thường sử dụng một số ph&ograve;ng thủ, chẳng hạn như ho v&agrave; c&aacute;c vi sinh vật b&igrave;nh thường trong cơ thể, để ngăn chặn sinh vật g&acirc;y hại từ x&acirc;m nhập v&agrave; tổn hại h&ocirc; hấp. Tuy nhi&ecirc;n, điều kiện rất nhiều, bao gồm suy dinh dưỡng v&agrave; c&aacute;c bệnh hệ thống, c&oacute; thể bảo vệ thấp hơn v&agrave; cho ph&eacute;p sinh vật g&acirc;y hại qua ph&ograve;ng thủ của cơ thể v&agrave; v&agrave;o phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi c&aacute;c sinh vật x&acirc;m nhập được v&agrave;o phổi, c&aacute;c tế b&agrave;o m&aacute;u trắng - một phần quan trọng của hệ thống miễn dịch bắt đầu tấn c&ocirc;ng. Những kẻ x&acirc;m lược t&iacute;ch lũy, c&aacute;c tế b&agrave;o m&aacute;u trắng v&agrave; c&aacute;c protein của hệ miễn dịch g&acirc;y ra t&uacute;i phế nang bị vi&ecirc;m v&agrave; chứa đầy chất lỏng, dẫn đến kh&oacute; thở đặc trưng nhiều loại vi&ecirc;m phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ph&acirc;n loại vi&ecirc;m phổi</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m phổi đ&ocirc;i khi được ph&acirc;n loại theo nguy&ecirc;n nh&acirc;n g&acirc;y ra bệnh vi&ecirc;m phổi: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Vi&ecirc;m phổi ở cộng đồng.</span></b><span style=\"font-family: Arial;\"> Đề cập đến vi&ecirc;m phổi c&oacute; trong qu&aacute; tr&igrave;nh của cuộc sống h&agrave;ng ng&agrave;y - ở trường, l&agrave;m việc hoặc ph&ograve;ng tập thể dục, v&iacute; dụ. Nguy&ecirc;n nh&acirc;n phổ biến nhất l&agrave; vi khuẩn Streptococcus pneumoniae. &Iacute;t phổ biến g&acirc;y ra l&agrave; Mycoplasma pneumoniae, một sinh vật nhỏ b&eacute; thường sản xuất những dấu hiệu v&agrave; triệu chứng nhẹ hơn c&aacute;c loại kh&aacute;c của vi&ecirc;m phổi. Đi bộ vi&ecirc;m phổi, một thuật ngữ d&ugrave;ng để m&ocirc; tả kh&ocirc;ng phải l&agrave; vi&ecirc;m phổi nặng tới mức phải nằm tr&ecirc;n giường, c&oacute; thể do Mycoplasma pneumoniae.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Vi&ecirc;m phổi bệnh viện.</span></b><span style=\"font-family: Arial;\"> Nếu đang nằm viện, đang c&oacute; nguy cơ cao của bệnh vi&ecirc;m phổi, đặc biệt l&agrave; nếu đang h&iacute;t thở với sự gi&uacute;p đỡ của một th&ocirc;ng gi&oacute; cơ học, trong một đơn vị chăm s&oacute;c đặc biệt hoặc c&oacute; hệ thống miễn dịch suy yếu. Đ&acirc;y l&agrave; loại vi&ecirc;m phổi c&oacute; thể cực kỳ nghi&ecirc;m trọng, đặc biệt l&agrave; cho người gi&agrave;, trẻ nhỏ v&agrave; những người c&oacute; bệnh phổi tắc nghẽn mạn t&iacute;nh (COPD) hoặc HIV / AIDS.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m phổi bệnh viện ph&aacute;t triển &iacute;t nhất 48 giờ sau khi nhập viện. Thể loại n&agrave;y bao gồm vi&ecirc;m phổi sau phẫu thuật - phổ biến nhất ở người gi&agrave; hơn 70 tuổi những người đ&atilde; phẫu thuật ở bụng hoặc ngực - v&agrave; chăm s&oacute;c sức khỏe bệnh vi&ecirc;m phổi li&ecirc;n quan mắc phải tại c&aacute;c cơ sở chăm s&oacute;c d&agrave;i ng&agrave;y, c&aacute;c trung t&acirc;m, nơi thuốc n&agrave;y được d&ugrave;ng bằng c&aacute;ch nhỏ giọt tĩnh mạch (truyền dịch) v&agrave; lọc m&aacute;u thận.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một yếu tố phổ biến cho loại vi&ecirc;m phổi l&agrave; bệnh tr&agrave;o ngược dạ d&agrave;y thực quản (GERD). Điều n&agrave;y xảy ra khi một số nội dung của d&ograve;ng dạ d&agrave;y trở lại v&agrave;o thực quản. Từ đ&oacute;, c&aacute;c chất dạ d&agrave;y thực quản c&oacute; thể được h&iacute;t v&agrave;o kh&iacute; quản v&agrave; sau đ&oacute; v&agrave;o đường h&ocirc; hấp thấp hơn. Ngay cả số lượng nhỏ của tr&agrave;o ngược dạ d&agrave;y c&oacute; thể dẫn đến vi&ecirc;m phổi ở những người được nhập viện.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Vi&ecirc;m phổi sặc. </span></b><span style=\"font-family: Arial;\">Đ&acirc;y l&agrave; loại bệnh vi&ecirc;m phổi xảy ra chất ngoại lai v&agrave;o phổi - thường xuy&ecirc;n nhất khi c&aacute;c chất của dạ d&agrave;y nhập v&agrave;o phổi sau khi bị n&ocirc;n mửa. Điều n&agrave;y thường xảy ra khi một chấn thương n&atilde;o hoặc điều kiện kh&aacute;c ảnh hưởng đến phản xạ b&igrave;nh thường bịt miệng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một nguy&ecirc;n nh&acirc;n kh&aacute;c của vi&ecirc;m phổi sặc l&agrave; uống nhiều rượu. Sặc xảy ra khi người say rượu đi ra ngo&agrave;i v&agrave; sau đ&oacute; bị n&ocirc;n do ảnh hưởng của rượu đối với dạ d&agrave;y. Nếu ai đ&oacute; v&ocirc; thức, c&oacute; thể h&uacute;t c&aacute;c chất lỏng v&agrave; c&oacute; thể ăn thức ăn đặc từ dạ d&agrave;y v&agrave;o phổi, g&acirc;y vi&ecirc;m phổi h&iacute;t.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; nuốt, xảy ra với c&aacute;c bệnh như amyotrophia (ALS), bệnh Parkinson, đột quỵ, cũng c&oacute; thể dẫn đến vi&ecirc;m phổi h&iacute;t. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Vi&ecirc;m phổi g&acirc;y ra bởi sinh vật cơ hội.</span></b><span style=\"font-family: Arial;\"> Đ&acirc;y l&agrave; loại bệnh vi&ecirc;m phổi với người hệ thống miễn dịch suy yếu. C&aacute;c sinh vật kh&ocirc;ng c&oacute; hại cho những người khỏe mạnh c&oacute; thể nguy hiểm cho những người bị AIDS v&agrave; c&aacute;c điều kiện kh&aacute;c m&agrave; l&agrave;m suy yếu hệ miễn dịch, cũng như những người đ&atilde; c&oacute; một cấy gh&eacute;p nội tạng. Thuốc ức chế hệ thống miễn dịch, như corticosteroid hay h&oacute;a trị liệu, cũng c&oacute; thể đặt v&agrave;o nguy cơ vi&ecirc;m phổi cơ hội.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">T&aacute;c nh&acirc;n g&acirc;y bệnh kh&aacute;c.</span></b><span style=\"font-family: Arial;\"> Bộc ph&aacute;t của c&uacute;m H5N1 (c&uacute;m gia cầm) v&agrave; hội chứng h&ocirc; hấp cấp t&iacute;nh virus nặng (SARS) đ&atilde; g&acirc;y ra nghi&ecirc;m trọng, vi&ecirc;m phổi nhiễm tr&ugrave;ng chết người đ&ocirc;i khi, ngay cả trong người khỏe mạnh trước đ&oacute;. Mặc d&ugrave; bệnh than - hiếm, bệnh dịch h��ch v&agrave; bệnh thỏ cũng c&oacute; thể g&acirc;y vi&ecirc;m phổi. Một số h&igrave;nh thức nấm, khi h&iacute;t v&agrave;o c&oacute; thể g&acirc;y vi&ecirc;m phổi. Bệnh lao trong phổi cũng c&oacute; thể g&acirc;y vi&ecirc;m phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c yếu tố li&ecirc;n quan với tăng nguy cơ vi&ecirc;m phổi bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tuổi.</span></b><span style=\"font-family: Arial;\"> Nếu đang độ tuổi 65 trở l&ecirc;n, đặc biệt nếu c&oacute; điều kiện kh&aacute;c m&agrave; l&agrave;m cho dễ bị vi&ecirc;m phổi ph&aacute;t triển, đang c&oacute; nguy cơ vi&ecirc;m phổi. Trẻ nhỏ, c&oacute; hệ thống miễn dịch kh&ocirc;ng ph&aacute;t triển đầy đủ, cũng c&oacute; nguy cơ vi&ecirc;m phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Một số bệnh.</span></b><span style=\"font-family: Arial;\"> Ch&uacute;ng bao gồm c&aacute;c bệnh suy giảm miễn dịch như HIV / AIDS v&agrave; c&aacute;c bệnh m&atilde;n t&iacute;nh như bệnh tim mạch, kh&iacute; thũng v&agrave; c&aacute;c bệnh phổi kh&aacute;c, v&agrave; tiểu đường. cũng c&oacute; nguy cơ cao nếu hệ thống miễn dịch đ&atilde; bị suy giảm do h&oacute;a trị hoặc sử dụng l&acirc;u d&agrave;i của c&aacute;c loại thuốc ức chế miễn dịch.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">H&uacute;t thuốc, lạm dụng rượu.</span></b><span style=\"font-family: Arial;\"> H&agrave;ng triệu sợi l&ocirc;ng nhỏ bao gồm c&aacute;c bề mặt của c&aacute;c tế b&agrave;o l&oacute;t ống phế quản. C&aacute;c sợi l&ocirc;ng đ&aacute;nh bại để x&oacute;a đường thở từ c&aacute;c chất tiết b&igrave;nh thường, nhưng chất k&iacute;ch th&iacute;ch như kh&oacute;i thuốc l&aacute; l&agrave;m t&ecirc; liệt c&aacute;c l&ocirc;ng mao, g&acirc;y tiết t&iacute;ch lũy. Nếu những tiết chứa vi khuẩn, ch&uacute;ng c&oacute; thể ph&aacute;t triển th&agrave;nh vi&ecirc;m phổi. Rượu g&acirc;y trở ngại cho phản xạ b&igrave;nh thường cũng như với c&aacute;c h&agrave;nh động của c&aacute;c tế b&agrave;o m&aacute;u trắng chống nhiễm tr&ugrave;ng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Nhập viện trong một đơn vị chăm s&oacute;c đặc biệt.</span></b><span style=\"font-family: Arial;\"> Vi&ecirc;m phổi mắc phải ở bệnh viện c&oacute; xu hướng trầm trọng hơn c&aacute;c loại kh&aacute;c của vi&ecirc;m phổi. Những người cần thở m&aacute;y đặc biệt c&oacute; nguy cơ bởi v&igrave; c&aacute;c ống thở đi qua c&aacute;c ph&ograve;ng b&igrave;nh thường của đường h&ocirc; hấp tr&ecirc;n, ngăn ho, c&oacute; thể cho ph&eacute;p chất dạ d&agrave;y sao lưu v&agrave;o thực quản, nơi c&oacute; thể được h&iacute;t v&agrave;o (h&uacute;t), v&agrave; c&oacute; thể vi khuẩn v&agrave; c&aacute;c sinh vật c&oacute; hại.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">COPD v&agrave; c&aacute;ch sử dụng corticosteroid h&iacute;t trong hơn 24 tuần.</span></b><span style=\"font-family: Arial;\"> Nghi&ecirc;n cứu chỉ ra rằng điều n&agrave;y l&agrave;m tăng đ&aacute;ng kể nguy cơ ph&aacute;t triển bệnh vi&ecirc;m phổi, vi&ecirc;m phổi c&oacute; thể nghi&ecirc;m trọng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tiếp x&uacute;c với h&oacute;a chất hoặc c&aacute;c chất &ocirc; nhiễm.</span></b><span style=\"font-family: Arial;\"> Nguy cơ ph&aacute;t triển một số loại phổ biến của bệnh vi&ecirc;m phổi c&oacute; thể tăng l&ecirc;n nếu l&agrave;m việc trong n&ocirc;ng nghiệp, x&acirc;y dựng hoặc xung quanh h&oacute;a chất c&ocirc;ng nghiệp nhất định hay động vật. Tiếp x&uacute;c với kh&ocirc;ng kh&iacute; &ocirc; nhiễm hoặc kh&iacute; độc cũng c&oacute; thể đ&oacute;ng g&oacute;p đến vi&ecirc;m phổi, m&agrave; l&agrave;m cho n&oacute; kh&oacute; khăn hơn cho phổi tự l&agrave;m sạch.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Chấn thương.</span></b><span style=\"font-family: Arial;\"> Những người đ&atilde; c&oacute; phẫu thuật hay những người đang cố định từ một chấn thương, chấn thương t&acirc;m l&yacute; c&oacute; nguy cơ cao của bệnh vi&ecirc;m phổi do phẫu thuật hay thương t&iacute;ch nghi&ecirc;m trọng c&oacute; thể l&agrave;m giảm ho - gi&uacute;p phổi sạch kh&oacute; khăn hơn, v&agrave; nằm thẳng c&oacute; thể cho ph&eacute;p chất nhầy thu thập trong phổi, cung cấp một nơi sinh sản cho vi khuẩn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">D&acirc;n tộc.</span></b><span style=\"font-family: Arial;\"> Nếu l&agrave; d&acirc;n Alaska hoặc một số bộ lạc người Mỹ bản xứ, c&oacute; nguy cơ lớn hơn cho hợp đồng vi&ecirc;m phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m phổi nghi&ecirc;m trọng thế n&agrave;o thường phụ thuộc v&agrave;o sức khỏe tổng thể v&agrave; loại v&agrave; mức độ vi&ecirc;m phổi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&ograve;n trẻ v&agrave; khỏe mạnh, vi&ecirc;m phổi thường xuy&ecirc;n c&oacute; thể được điều trị th&agrave;nh c&ocirc;ng. Tuy nhi&ecirc;n, một số sinh vật g&acirc;y bệnh vi&ecirc;m phổi rất nguy hiểm m&agrave; &aacute;p đảo c&aacute;c cơ chế ph&ograve;ng thủ, ngay cả ở những người khỏe mạnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; suy tim hay bệnh phổi, đặc biệt l&agrave; nếu h&uacute;t thuốc, hoặc nếu lớn tuổi, vi&ecirc;m phổi c&oacute; thể rất kh&oacute; điều trị th&agrave;nh c&ocirc;ng. Cũng c&oacute; nhiều khả năng để ph&aacute;t triển c&aacute;c biến chứng, một số trong đ&oacute; c&oacute; thể đe dọa t&iacute;nh mạng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m phổi, biến chứng c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Vi khuẩn trong m&aacute;u.</span></b><span style=\"font-family: Arial;\"> Vi&ecirc;m phổi c&oacute; thể đe dọa t&iacute;nh mạng khi vi&ecirc;m nhiễm từ bệnh lấp đầy c&aacute;c phế nang trong phổi v&agrave; cản trở khả năng để thở. Trong một số trường hợp nhiễm tr&ugrave;ng c&oacute; thể x&acirc;m nhập v&agrave;o m&aacute;u. Sau đ&oacute; c&oacute; thể l&acirc;y lan nhanh ch&oacute;ng tới c&aacute;c cơ quan kh&aacute;c.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">T&iacute;ch luỹ v&agrave; nhiễm tr&ugrave;ng xung quanh.</span></b><span style=\"font-family: Arial;\"> Đ&ocirc;i khi dịch t&iacute;ch tụ giữa phổi &ndash; m&agrave;ng phổi, m&agrave;ng mỏng trong suốt (m&agrave;ng phổi) bao gồm phổi v&agrave; bề mặt b&ecirc;n trong của th&agrave;nh ngực - một t&igrave;nh trạng được gọi l&agrave; tr&agrave;n dịch m&agrave;ng phổi. Th&ocirc;ng thường, m&agrave;ng phổi mịn, cho ph&eacute;p phổi để dịch chuyển một c&aacute;ch dễ d&agrave;ng dọc theo th&agrave;nh ngực khi thở v&agrave;o v&agrave; ra. Nhưng khi m&agrave;ng phổi bị vi&ecirc;m (vi&ecirc;m m&agrave;ng phổi) - thường l&agrave; do vi&ecirc;m phổi - chất lỏng c&oacute; thể t&iacute;ch lũy v&agrave; c&oacute; thể bị nhiễm bệnh (empyema).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">&Aacute;p xe phổi.</span></b><span style=\"font-family: Arial;\"> Một khoang chứa mủ (&aacute;p xe) m&agrave; h&igrave;nh thức trong khu vực bị ảnh hưởng bởi bệnh vi&ecirc;m phổi l&agrave; một biến chứng tiềm năng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Hội chứng suy h&ocirc; hấp cấp (ARDS).</span></b><span style=\"font-family: Arial;\"> Vi&ecirc;m phổi n&agrave;y bao gồm hầu hết c&aacute;c v&ugrave;ng của cả hai phổi, l&agrave;m cho hơi thở kh&oacute; khăn v&agrave; lấy đi oxy cơ thể. Bệnh phổi cơ bản bất kỳ loại n&agrave;o, nhưng đặc biệt l&agrave; COPD, l&agrave;m cho dễ bị ARDS.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">B&aacute;c sĩ đầu ti&ecirc;n c&oacute; thể nghi ngờ vi&ecirc;m phổi dựa tr&ecirc;n lịch sử y tế v&agrave; một kỳ kiểm tra. C&oacute; thể trải qua một số hoặc tất cả c&aacute;c x&eacute;t nghiệm:</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Kh&aacute;m nghiệm vật l&yacute;.</span></b><span style=\"font-family: Arial;\"> Trong kỳ thi, b&aacute;c sĩ nghe phổi với ống nghe để kiểm tra tiếng bất thường bọt hoặc &acirc;m thanh tanh t&aacute;ch (rales) v&agrave; đối với nhiều bất ổn (rhonchi) c&oacute; t&iacute;n hiệu sự hiện diện của chất lỏng d&agrave;y.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Chụp X-quang.</span></b><span style=\"font-family: Arial;\"> X-quang c&oacute; thể x&aacute;c nhận sự hiện diện của vi&ecirc;m phổi v&agrave; x&aacute;c định mức độ v&agrave; vị tr&iacute; của nhiễm tr&ugrave;ng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">X&eacute;t nghiệm m&aacute;u v&agrave; chất nhầy.</span></b><span style=\"font-family: Arial;\"> C&oacute; thể c&oacute; x&eacute;t nghiệm m&aacute;u để đo số lượng tế b&agrave;o trắng v&agrave; t&igrave;m sự hiện diện của virus, vi khuẩn hoặc c&aacute;c sinh vật kh&aacute;c. B&aacute;c sĩ cũng c&oacute; thể kiểm tra một mẫu chất nhờn hoặc m&aacute;u để gi&uacute;p x&aacute;c định c&aacute;c vi sinh vật đặc biệt g&acirc;y ra bệnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Phương ph&aacute;p điều trị vi&ecirc;m phổi kh&aacute;c nhau, t&ugrave;y thuộc v&agrave;o mức độ nghi&ecirc;m trọng của c&aacute;c triệu chứng v&agrave; loại vi&ecirc;m phổi c&oacute;. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Vi khuẩn.</span></b><span style=\"font-family: Arial;\"> C&aacute;c b&aacute;c sĩ thường điều trị vi&ecirc;m phổi do vi khuẩn với kh&aacute;ng sinh. Mặc d&ugrave; c&oacute; thể bắt đầu cảm thấy tốt hơn ngay sau khi bắt đầu uống thuốc, h&atilde;y chắc chắn để ho&agrave;n th&agrave;nh to&agrave;n bộ liều của thuốc kh&aacute;ng sinh. Ngừng uống thuốc qu&aacute; sớm c&oacute; thể g&acirc;y vi&ecirc;m phổi trở lại. N&oacute; cũng gi&uacute;p tạo ra c&aacute;c chủng vi khuẩn kh&aacute;ng thuốc kh&aacute;ng sinh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Viral.</span></b><span style=\"font-family: Arial;\"> Kh&aacute;ng sinh kh&ocirc;ng hiệu quả đối với hầu hết c&aacute;c dạng virus vi&ecirc;m phổi. V&agrave; mặc d&ugrave; một v&agrave;i virus c&oacute; thể pneumonias được điều trị bằng thuốc kh&aacute;ng vi-r&uacute;t, c&aacute;c điều trị khuyến c&aacute;o thường l&agrave; nghỉ ngơi v&agrave; uống nhiều nước.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Mycoplasma.</span></b><span style=\"font-family: Arial;\"> Pneumonias Mycoplasma được điều trị bằng thuốc kh&aacute;ng sinh. Mặc d&ugrave; vậy, c&oacute; thể kh&ocirc;ng phục hồi ngay lập tức. Trong một số trường hợp mệt mỏi c&oacute; thể tiếp tục l&acirc;u d&agrave;i sau khi bệnh n&agrave;y đ&atilde; bị x&oacute;a. Nhiều trường hợp kh&ocirc;ng được chẩn đo&aacute;n vi&ecirc;m phổi mycoplasma v&agrave; kh&ocirc;ng được điều trị. C&aacute;c dấu hiệu v&agrave; triệu chứng bắt chước những người cảm lạnh, v&igrave; vậy một số người kh&ocirc;ng bao giờ t&igrave;m kiếm sự chăm s&oacute;c y tế. C&aacute;c triệu chứng thường tự biến mất.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Nấm.</span></b><span style=\"font-family: Arial;\"> Nếu vi&ecirc;m phổi l&agrave; do một loại nấm, c&oacute; thể được điều trị bằng thuốc kh&aacute;ng nấm.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Đối ph&oacute; với c&aacute;c triệu chứng</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ngo&agrave;i c&aacute;c phương ph&aacute;p điều trị, b&aacute;c sĩ c&oacute; thể khuy&ecirc;n n&ecirc;n thuốc kh&ocirc;ng k&ecirc; đơn để giảm sốt, chữa đau nhức, v&agrave; l&agrave;m dịu ho li&ecirc;n quan đến vi&ecirc;m phổi. kh&ocirc;ng muốn bỏ ho ho&agrave;n to&agrave;n, tuy nhi&ecirc;n, v&igrave; ho gi&uacute;p phổi sạch. Nếu phải sử dụng để giảm cơn ho, d&ugrave;ng liều thấp nhất gi&uacute;p c&oacute; được một số nghỉ ngơi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Khi nhập viện l&agrave; cần thiết</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; vi&ecirc;m phổi nặng, sẽ được nhập viện v&agrave; điều trị bằng thuốc kh&aacute;ng sinh ti&ecirc;m tĩnh mạch v&agrave; c&oacute; thể đưa v&agrave;o oxy. Nếu kh&ocirc;ng cần oxy, c&oacute; thể phục hồi một c&aacute;ch nhanh ch&oacute;ng ở nh&agrave; với kh&aacute;ng sinh uống như trong c&aacute;c bệnh viện, đặc biệt l&agrave; nếu c&oacute; đủ điều kiện tiếp cận với chăm s&oacute;c sức khỏe tại nh&agrave;. Đ&ocirc;i khi c&oacute; thể ba hoặc bốn ng&agrave;y tại bệnh viện tiếp nhận thuốc kh&aacute;ng sinh ti&ecirc;m tĩnh mạch v&agrave; sau đ&oacute; tiếp tục phục hồi ở nh&agrave; với thuốc uống.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Theo d&otilde;i điều trị</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">B&aacute;c sĩ rất c&oacute; thể một lịch tr&igrave;nh theo d&otilde;i X-ray v&agrave; kh&aacute;m lại sau khi chẩn đo&aacute;n v&agrave; điều trị ban đầu. V&agrave;o thời gian đ&oacute; nhiễm tr&ugrave;ng đ&atilde; x&oacute;a, nhưng điều quan trọng đối với b&aacute;c sĩ để xem, ngay cả khi đang cảm thấy tốt hơn. Theo d&otilde;i c&aacute;c cuộc hẹn v&agrave; X-quang l&agrave; đặc biệt quan trọng ở những người h&uacute;t thuốc.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu kh&ocirc;ng cảm thấy tốt hơn, tiếp theo chuyến thăm l&agrave; cơ hội để b&aacute;c sĩ x&aacute;c định xem liệu điều trị l&agrave; kh&ocirc;ng hiệu quả v&agrave; để thử nghiệm th&ecirc;m để c&oacute; th&ecirc;m th&ocirc;ng tin về t&igrave;nh trạng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phong c&aacute;ch sống v&agrave; biện ph&aacute;p khắc phục</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; vi&ecirc;m phổi, c&aacute;c biện ph&aacute;p sau đ&acirc;y c&oacute; thể gi&uacute;p phục hồi nhanh hơn v&agrave; giảm nguy cơ biến chứng: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Nghỉ ngơi nhiều.</span></b><span style=\"font-family: Arial;\"> Ngay cả khi bắt đầu cảm thấy tốt hơn, cẩn thận kh&ocirc;ng để lạm dụng n&oacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ở nh&agrave; cho đến khi trở về nhiệt độ b&igrave;nh thường v&agrave; ngừng ho ra chất nhầy. Lời khuy&ecirc;n n&agrave;y phụ thuộc một phần v&agrave;o c&aacute;ch đ&atilde; bị bệnh. Nếu kh&ocirc;ng chắc chắn, h&atilde;y hỏi b&aacute;c sĩ. Bởi v&igrave; bệnh vi&ecirc;m phổi c&oacute; thể t&aacute;i diễn trong v&ograve;ng một tuần hoặc l&acirc;u hơn, c&oacute; thể l&agrave; tốt hơn l&agrave;m một khối lượng c&ocirc;ng việc đầy đủ cho đến khi chắc chắn rằng đang tốt.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Uống nhiều nước.</span></b><span style=\"font-family: Arial;\"> Chất lỏng giữ cho khỏi bị mất nước v&agrave; gi&uacute;p nới lỏng chất nhầy trong phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">D&ugrave;ng to&agrave;n bộ liều của bất cứ loại thuốc quy định.</span></b><span style=\"font-family: Arial;\"> Ngừng thuốc qu&aacute; sớm c&oacute; thể g&acirc;y vi&ecirc;m phổi trở lại v&agrave; g&oacute;p phần v&agrave;o sự ph&aacute;t triển của vi khuẩn kh&aacute;ng kh&aacute;ng sinh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Giữ tất cả theo d&otilde;i c&aacute;c cuộc hẹn. </span></b><span style=\"font-family: Arial;\">Ngay cả khi cảm thấy tốt hơn, phổi vẫn c&oacute; thể bị nhiễm bệnh. Điều quan trọng l&agrave; c&oacute; b&aacute;c sĩ theo d&otilde;i tiến bộ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Ph&ograve;ng chống</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những gợi &yacute; sau đ&acirc;y c&oacute; thể gi&uacute;p giữ cho khỏe mạnh: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Được chủng ngừa</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bởi v&igrave; vi&ecirc;m phổi c&oacute; thể l&agrave; biến chứng của bệnh c&uacute;m, khi ti&ecirc;m ngừa bệnh c&uacute;m h&agrave;ng năm l&agrave; một c&aacute;ch tốt để ngăn ngừa vi&ecirc;m phổi do virus c&uacute;m, c&oacute; thể dẫn đến vi&ecirc;m phổi do vi khuẩn. Ngo&agrave;i ra, mặc d&ugrave; c&oacute; một số tranh c&atilde;i về hiệu quả của n&oacute;, đặc biệt l&agrave; ở người lớn tuổi, c&aacute;c b&aacute;c sĩ khuy&ecirc;n n&ecirc;n nhận được chủng ngừa phế cầu khuẩn vi&ecirc;m phổi &iacute;t nhất một lần sau tuổi 50, v&agrave; nếu c&oacute; bất kỳ yếu tố nguy cơ, mỗi năm năm sau đ&oacute;. B&aacute;c sĩ sẽ khuy&ecirc;n n&ecirc;n chủng ngừa vi&ecirc;m phổi ngay cả khi trẻ hơn 50 nếu l&agrave; một người h&uacute;t thuốc, nếu c&oacute; ung thư phổi, bệnh tim mạch, một số loại ung thư, tiểu đường hay bệnh thiếu m&aacute;u hồng cầu h&igrave;nh liềm, nếu hệ thống miễn dịch bị tổn thương, hoặc nếu đ&atilde; cắt l&aacute; l&aacute;ch của m&igrave;nh v&igrave; l&yacute; do n&agrave;o.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một vắc-xin được gọi l&agrave; vắc-xin li&ecirc;n hợp phế cầu khuẩn c&oacute; thể gi&uacute;p bảo vệ trẻ em chống lại vi&ecirc;m phổi. Đ&oacute; l&agrave; khuyến c&aacute;o cho tất cả trẻ em dưới 2 tuổi v&agrave; cho c&aacute;c lứa tuổi trẻ em từ 2 đến 5 năm c&oacute; nguy cơ đặc biệt của bệnh phế cầu khuẩn, chẳng hạn như những người c&oacute; thiếu hụt hệ thống miễn dịch, ung thư, bệnh tim mạch hoặc thiếu m&aacute;u hồng cầu h&igrave;nh liềm, hoặc những người tham dự nh&oacute;m Trung t&acirc;m chăm s&oacute;c ban ng&agrave;y. T&aacute;c dụng phụ của thuốc chủng ngừa phế cầu khuẩn thường nhẹ v&agrave; bao gồm đau nhẹ hoặc sưng tại chỗ ti&ecirc;m.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Rửa tay</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tay</span><span style=\"font-family: Arial;\"> li&ecirc;n tục li&ecirc;n hệ với hầu hết với c&aacute;c vi tr&ugrave;ng c&oacute; thể g&acirc;y vi&ecirc;m phổi. C&aacute;c vi tr&ugrave;ng n&agrave;y nhập v&agrave;o cơ thể khi chạm v&agrave;o đ&ocirc;i mắt hoặc ch&agrave; x&aacute;t b&ecirc;n trong mũi. Rửa tay thường xuy&ecirc;n v&agrave; kỹ lưỡng c&oacute; thể gi&uacute;p giảm nguy cơ. Khi rửa l&agrave; kh&ocirc;ng thể, sử dụng thuốc khử tr&ugrave;ng tay c&oacute; cồn, c&oacute; thể c&oacute; hiệu quả hơn x&agrave; ph&ograve;ng v&agrave; nước trong việc ti&ecirc;u diệt vi khuẩn v&agrave; virus g&acirc;y bệnh. Hơn nữa, vệ sinh tay với chất chứa c&aacute;c th&agrave;nh phần giữ ẩm da.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Kh&ocirc;ng h&uacute;t thuốc </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&uacute;t thuốc thường g&acirc;y thiệt hại cho ph&ograve;ng thủ chống lại c&aacute;c bệnh nhiễm tr&ugrave;ng hại về đường h&ocirc; hấp.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">H&atilde;y chăm s&oacute;c bản th&acirc;n m&igrave;nh</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nghỉ ngơi th&iacute;ch hợp v&agrave; chế độ ăn uống nhiều tr&aacute;i c&acirc;y, rau v&agrave; ngũ cốc c&ugrave;ng với tập thể dục vừa phải c&oacute; thể gi&uacute;p giữ cho hệ thống miễn dịch mạnh mẽ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Điều trị GERD </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều trị triệu chứng GERD, v&agrave; giảm c&acirc;n nếu đang thừa c&acirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Bảo vệ người kh&aacute;c khỏi bị nhiễm tr&ugrave;ng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; vi&ecirc;m phổi, cố gắng tr&aacute;nh xa bất cứ ai c&oacute; một hệ thống miễn dịch bị tổn thương. Khi điều đ&oacute; l&agrave; kh&ocirc;ng thể, c&oacute; thể gi&uacute;p bảo vệ người kh&aacute;c bằng c&aacute;ch đeo khẩu trang v&agrave; lu&ocirc;n lu&ocirc;n ho v&agrave;o khăn giấy.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: right;\"><span style=\"color: rgb(128, 0, 0);\"><b style=\"\"><i style=\"\"><span style=\"font-family: Arial;\"></span></i></b></span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">&nbsp;</span></p></span>\n', 1);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(18, 'Viêm phổi không do nhiễm trùng', '\n   <h1 class=\"title\">Viêm phổi không do nhiễm trùng</h1>   <span class=\"des\">Nếu viêm phổi không bị phát hiện hoặc không được chữa trị, dần dần có thể phát triển viêm phổi mãn tính.</span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m phổi l&agrave; một thuật ngữ chung m&agrave; t&igrave;nh trạng l&agrave; vi&ecirc;m của m&ocirc; phổi. Mặc d&ugrave; một loại g&acirc;y vi&ecirc;m phổi do c&aacute;c nguy&ecirc;n nh&acirc;n vi&ecirc;m nhiễm, hầu hết c&aacute;c b&aacute;c sĩ đang đề cập đến nguy&ecirc;n nh&acirc;n kh&aacute;c g&acirc;y vi&ecirc;m phổi khi họ sử dụng thuật ngữ &quot;vi&ecirc;m phổi.&quot;</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c yếu tố c&oacute; thể g&acirc;y vi&ecirc;m phổi bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nghề nghiệp tiếp x&uacute;c với c&aacute;c hạt trong kh&ocirc;ng kh&iacute;, như l&agrave; amiăng hoặc silica.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một số loại thuốc, đặc biệt l&agrave; loại thuốc h&oacute;a trị.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Xạ trị v&agrave;o ngực.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tiếp x&uacute;c với gia cầm, chim bồ c&acirc;u hoặc chim th&uacute; cưng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhiều loại nấm mốc .</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở - thường đi k&egrave;m với ho - l&agrave; triệu chứng thường gặp nhất của vi&ecirc;m phổi. Nhưng c&oacute; những vấn đề n&agrave;y kh&ocirc;ng c&oacute; nghĩa rằng c&oacute; vi&ecirc;m phổi. Kiểm tra chuy&ecirc;n ng&agrave;nh l&agrave; cần thiết để chẩn đo&aacute;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c dấu hiệu thường gặp nhất v&agrave; triệu chứng của vi&ecirc;m phổi l&agrave;: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một cơn sốt, c&oacute; thể cấp thấp.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Vi&ecirc;m phổi m&atilde;n t&iacute;nh</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu vi&ecirc;m phổi kh&ocirc;ng bị ph&aacute;t hiện hoặc kh&ocirc;ng được chữa trị, dần dần c&oacute; thể ph&aacute;t triển vi&ecirc;m phổi m&atilde;n t&iacute;nh. Điều n&agrave;y đặc biệt phổ biến trong c&aacute;c trường hợp vi&ecirc;m phổi qu&aacute; mẫn, trong đ&oacute; c&oacute; thể li&ecirc;n tục tiếp x&uacute;c với chất g&acirc;y dị ứng m&agrave; kh&ocirc;ng nhận ra. C&aacute;c dấu hiệu v&agrave; triệu chứng của vi&ecirc;m phổi m&atilde;n t&iacute;nh bao gồm:</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mệt mỏi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ch&aacute;n ăn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Giảm c&acirc;n kh&ocirc;ng chủ &yacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Gặp b&aacute;c sĩ khi </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&atilde;y gọi b&aacute;c sĩ một cuộc gọi bất cứ l&uacute;c n&agrave;o c&oacute; kh&oacute; thở, c&oacute; hoặc kh&ocirc;ng c&oacute; một ho - đặc biệt l&agrave; nếu c&aacute;c triệu chứng xuất hiện sau khi h&iacute;t bụi, h&oacute;a chất, hoặc nếu đang d&ugrave;ng một loại thuốc c&oacute; thể g&acirc;y ra kh&oacute; thở. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m phổi xảy ra khi một số chất k&iacute;ch th&iacute;ch c&aacute;c t&uacute;i kh&iacute; nhỏ (phế nang) trong phổi bị vi&ecirc;m. Vi&ecirc;m n&agrave;y c&oacute; thể cản trở việc trao đổi &ocirc;xy trong c&aacute;c t&uacute;i kh&iacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một loạt c&aacute;c chất k&iacute;ch th&iacute;ch, từ khu&ocirc;n mẫu kh&ocirc;ng kh&iacute; đến loại thuốc h&oacute;a trị, c&oacute; li&ecirc;n quan đến vi&ecirc;m phổi. Nhưng đối với hầu hết mọi người, c&aacute;c chất g&acirc;y vi&ecirc;m cụ thể l&agrave; kh&ocirc;ng bao giờ x&aacute;c định.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thuốc </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một loại thuốc d&ugrave;ng để điều trị bệnh kh&aacute;c c&oacute; thể g&acirc;y vi&ecirc;m phổi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc kh&aacute;ng sinh.</span></b><span style=\"font-family: Arial;\"> Nitrofurantoin, amphotericin B, minocycline, sulfasalazine v&agrave; sulfadiazine c&oacute; li&ecirc;n quan đến vi&ecirc;m phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc h&oacute;a trị.</span></b><span style=\"font-family: Arial;\"> C&aacute;c loại thuốc chống ung thư c&oacute; thể g&acirc;y ra vi&ecirc;m phổi bao gồm bleomycin, methotrexate, carmustine, busulfan v&agrave; cyclophosphamide.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc tim mạch. </span></b><span style=\"font-family: Arial;\">Dược phẩm để giữ cho nhịp tim thường xuy&ecirc;n, chẳng hạn như amiodarone, c&oacute; thể g&acirc;y vi&ecirc;m phổi ở một số người.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nấm v&agrave; chất g&acirc;y dị ứng kh&aacute;c</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c chất k&iacute;ch th&iacute;ch phổi c&oacute; li&ecirc;n quan bao gồm:</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mốc.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">L&ocirc;ng vũ hoặc ph&acirc;n chim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Xạ trị</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một số người trải qua liệu ph&aacute;p bức xạ ngực cho bệnh ung thư c&oacute; thể ph&aacute;t triển vi&ecirc;m phổi. Điều n&agrave;y rất c&oacute; thể xảy ra ở những người đang được điều trị cho:</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ung thư phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ung thư v&uacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ung thư bạch cầu.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Lymphoma.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nguy&ecirc;n nh&acirc;n thường gặp nhất của vi&ecirc;m phổi li&ecirc;n quan đến bức xạ, tuy nhi&ecirc;n, l&agrave; loại bức xạ to&agrave;n bộ cơ thể cần thiết để chuẩn bị một người để được gh&eacute;p tủy xương. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">H&iacute;t sặc </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&iacute;t sặc xảy ra nếu h&iacute;t dị vật v&agrave;o phổi.Th&agrave;nh phần trong dạ d&agrave;y, chẳng hạn như thực phẩm hoặc chất lỏng, đ&ocirc;i khi c&oacute; thể g&acirc;y vi&ecirc;m phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nghề nghiệp hoặc sở th&iacute;ch</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một số nghề nghiệp v&agrave; sở th&iacute;ch mang theo những rủi ro cao hơn của vi&ecirc;m phổi, bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">N&ocirc;ng nghiệp.</span></b><span style=\"font-family: Arial;\"> Nhiều loại hoạt động nu&ocirc;i trồng tiếp x&uacute;c sương kh&iacute; dung v&agrave; thuốc trừ s&acirc;u. H&iacute;t phải c&aacute;c hạt trong kh&ocirc;ng kh&iacute; từ mốc l&agrave; một trong những nguy&ecirc;n nh&acirc;n phổ biến nhất của vi&ecirc;m phổi do nghề nghiệp. Khu&ocirc;n hạt cũng c&oacute; thể được h&iacute;t v&agrave;o trong thời gian m&ugrave;a thu hoạch ngũ cốc v&agrave; cỏ kh&ocirc;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Vật nu&ocirc;i.</span></b><span style=\"font-family: Arial;\"> Gia cầm c&ocirc;ng nh&acirc;n v&agrave; những giống vật nu&ocirc;i giữ c&aacute;c lo&agrave;i chim - thường tiếp x&uacute;c với ph&acirc;n, l&ocirc;ng v&agrave; c&aacute;c vật liệu kh&aacute;c c&oacute; thể g&acirc;y vi&ecirc;m phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Bồn tắm v&agrave; độ ẩm.</span></b><span style=\"font-family: Arial;\"> Mốc trong bồn tắm n&oacute;ng c&oacute; thể g&acirc;y vi&ecirc;m phổi, bởi v&igrave; c&aacute;c h&agrave;nh động tạo bọt l&agrave;m cho sương m&ugrave; c&oacute; thể h&iacute;t v&agrave;o. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thủ tục y tế </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Phẫu thuật g&acirc;y m&ecirc;.</span></b><span style=\"font-family: Arial;\"> G&acirc;y m&ecirc; to&agrave;n th&acirc;n thư gi&atilde;n cơ cổ họng tăng l&ecirc;n v&agrave; nguy cơ h&iacute;t v&agrave;o trong dạ d&agrave;y. Đ&oacute; l&agrave; l&yacute; do tại sao cần dạ d&agrave;y rỗng cho một số giờ trước khi nhận được g&acirc;y m&ecirc; to&agrave;n th&acirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Điều trị ung thư.</span></b><span style=\"font-family: Arial;\"> Một số loại thuốc h&oacute;a trị c&oacute; thể g&acirc;y vi&ecirc;m phổi, như liệu ph&aacute;p xạ trị c&oacute; thể đến phổi. Sự kết hợp của hai l&agrave;m tăng nguy cơ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vi&ecirc;m phổi m&agrave; kh&ocirc;ng để &yacute; hoặc kh&ocirc;ng được điều trị c&oacute; thể g&acirc;y tổn thương phổi kh&ocirc;ng thể đảo ngược. Th&ocirc;ng thường, t&uacute;i kh&iacute; trong phổi căng ra v&agrave; thư gi&atilde;n với từng hơi thở. Vi&ecirc;m m&atilde;n t&iacute;nh của m&ocirc; mỏng l&oacute;t mỗi t&uacute;i kh&iacute; (phế nang) c&oacute; thể l&agrave;m cho t&uacute;i kh&iacute; trở th&agrave;nh vết sẹo kh&ocirc;ng linh hoạt - cứng như một miếng bọt biển kh&ocirc;. Điều n&agrave;y được gọi l&agrave; xơ h&oacute;a phổi. Trong trường hợp nặng, xơ h&oacute;a phổi c&oacute; thể g&acirc;y suy tim, suy h&ocirc; hấp v&agrave; tử vong.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Để ph&acirc;n biệt vi&ecirc;m phổi từ bệnh phổi kh&aacute;c, c&oacute; thể c&oacute; một hoặc nhiều c&aacute;c x&eacute;t nghiệm sau đ&acirc;y. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">H&igrave;nh ảnh kiểm tra</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong hầu hết trường hợp, vi&ecirc;m phổi ảnh hưởng đến chỉ một phần nhỏ nội phổi, trong khi c&aacute;c t&aacute;c động của vi&ecirc;m phổi thường lan rộng ra khắp cả năm th&ugrave;y của phổi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Chụp X-quang.</span></b><span style=\"font-family: Arial;\"> Kiểm tra n&agrave;y kh&ocirc;ng g&acirc;y đau đớn, g&acirc;y ra một số nhỏ của bức xạ đi qua ngực để tạo ra h&igrave;nh ảnh của phổi. X-quang chỉ mất v&agrave;i ph&uacute;t để thực hiện.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">M&aacute;y vi t&iacute;nh cắt lớp (CT scan).</span></b><span style=\"font-family: Arial;\"> Ảnh chụp cắt lớp X-quang kết hợp h&igrave;nh ảnh chụp từ nhiều g&oacute;c độ kh&aacute;c nhau v&agrave;o mặt cắt ngang h&igrave;nh ảnh chi tiết. Điều n&agrave;y li&ecirc;n quan đến việc thử nghiệm kh&ocirc;ng g&acirc;y đau đớn nằm tr&ecirc;n một c&aacute;i b&agrave;n hẹp c&oacute; thể trượt, m&aacute;y b&aacute;nh h&igrave;nh lớn. CT scan thường mất &iacute;t hơn 15 ph&uacute;t để thực hiện. Chụp cắt lớp vi t&iacute;nh cho nhiều chi tiết hơn những thay đổi trong phổi so với những g&igrave; chụp X-quang c&oacute; thể cung cấp.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">X&eacute;t nghiệm m&aacute;u</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">B&aacute;c sĩ c&oacute; thể y&ecirc;u cầu x&eacute;t nghiệm m&aacute;u kiểm tra t&igrave;nh trạng sức khỏe tổng thể. X&eacute;t nghiệm m&aacute;u cũng c&oacute; thể hữu &iacute;ch trong chẩn đo&aacute;n bệnh phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">X&eacute;t nghiệm chức năng phổi</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">X&eacute;t nghiệm được gọi l&agrave; c&aacute;c biện ph&aacute;p đo phế dung lượng kh&ocirc;ng kh&iacute; c&oacute; thể h&iacute;t v&agrave;o v&agrave; thở ra trong một thời gian nhất định. B&aacute;c sĩ cũng c&oacute; thể đo lường hiệu quả phổi chuyển kh&iacute; từ kh&ocirc;ng kh&iacute; v&agrave;o m&aacute;u trong khi tập luyện như thế n&agrave;o.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một c&aacute;ch kh&aacute;c để đ&aacute;nh gi&aacute; hiệu quả của phổi l&agrave; để đo độ b&atilde;o h&ograve;a oxy trong m&aacute;u với đo oxy - một thiết bị kh&ocirc;ng đau đớn kẹp v&agrave;o ng&oacute;n tay. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nội soi phế quản</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Soi phế quản l&agrave; một thủ tục sử dụng một ống linh hoạt luồng xuống cổ họng để xem đường h&ocirc; hấp v&agrave; thu thập c&aacute;c mẫu từ phổi. B&aacute;c sĩ sẽ xịt thuốc t&ecirc; ở mặt sau của miệng v&agrave; cổ họng v&agrave; sẽ cho thuốc qua tĩnh mạch ở c&aacute;nh tay để gi&uacute;p thư gi&atilde;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi ống đ&atilde; được diễn ra, b&aacute;c sĩ c&oacute; thể bơm một dung dịch nước muối để thu thập c&aacute;c tế b&agrave;o phổi v&agrave; c&aacute;c vật liệu kh&aacute;c. Điều n&agrave;y gọi l&agrave; rửa. B&aacute;c sĩ c&oacute; thể cũng th&ecirc;m một c&ocirc;ng cụ nhỏ th&ocirc;ng qua soi phế quản để l&agrave;m sinh thiết một mẫu tế b&agrave;o từ c&aacute;c m&ocirc; phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Phẫu thuật sinh thiết phổi</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong một số trường hợp, b&aacute;c sĩ c&oacute; thể muốn kiểm tra một mẫu lớn hơn của m&ocirc; từ một số địa điểm trong phổi m&agrave; kh&ocirc;ng thể đạt được th&ocirc;ng qua nội soi phế quản. Một thủ tục phẫu thuật để c&oacute; được c&aacute;c mẫu n&agrave;y c&oacute; thể cần thiết.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; qu&aacute; mẫn hoặc vi&ecirc;m phổi do h&oacute;a chất, tr&aacute;nh g&acirc;y dị ứng l&agrave; một phương ph&aacute;p phổ biến để điều trị. Bằng c&aacute;ch loại trừ tiếp x&uacute;c với c&aacute;c chất g&acirc;y dị ứng hoặc h&oacute;a chất g&acirc;y k&iacute;ch th&iacute;ch phổi, sẽ nhận thấy rằng c&aacute;c triệu chứng giảm bớt.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong trường hợp nặng của vi&ecirc;m phổi, điều trị c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Corticosteroid.</span></b><span style=\"font-family: Arial;\"> Thuốc giảm vi&ecirc;m, chẳng hạn như prednisone, c&oacute; thể gi&uacute;p l&agrave;m giảm c&aacute;c triệu chứng của vi&ecirc;m phổi. Corticosteroid thường được d&ugrave;ng như thuốc vi&ecirc;n. Những thuốc n&agrave;y l&agrave;m việc bằng c&aacute;ch ức chế hệ thống miễn dịch, giảm vi&ecirc;m trong phổi. Tuy nhi&ecirc;n, corticosteroid sử dụng cũng l&agrave;m tăng nguy cơ nhiễm tr&ugrave;ng ph&aacute;t triển v&agrave; được kết hợp với lo&atilde;ng xương.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc kh&aacute;ng sinh.</span></b><span style=\"font-family: Arial;\"> Nếu c&oacute; nhiễm tr&ugrave;ng do vi khuẩn trong phổi, b&aacute;c sĩ c&oacute; thể k&ecirc; toa thuốc kh&aacute;ng sinh, m&agrave; c&oacute; thể được th&ocirc;ng qua một tĩnh mạch ở c&aacute;nh tay hoặc thực hiện như l&agrave; một thuốc vi&ecirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">&Ocirc;xy liệu ph&aacute;p.</span></b><span style=\"font-family: Arial;\"> Nếu đang c&oacute; kh&oacute; thở nhiều, c&oacute; thể cần điều trị bằng oxy th&ocirc;ng qua mặt nạ hoặc ống nhựa với prongs ph&ugrave; hợp v&agrave;o lỗ mũi. Một số người cần điều trị bằng oxy li&ecirc;n tục, trong khi những người kh&aacute;c c&oacute; thể cần n&oacute; chỉ trong thời gian tập thể dục hoặc ngủ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phong c&aacute;ch sống v&agrave; biện ph&aacute;p khắc phục</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Chẩn đo&aacute;n vi&ecirc;m phổi c&oacute; thể c&oacute; nghĩa l&agrave; phải thay đổi lối sống để cải thiện sức khỏe. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">V&iacute; dụ, nếu nhiệm vụ c&ocirc;ng việc đặt v&agrave;o những chất g&acirc;y k&iacute;ch th&iacute;ch phổi, n&oacute;i chuyện với b&aacute;c sĩ v&agrave; gi&aacute;m s&aacute;t tại nơi l&agrave;m việc về c&aacute;c lựa chọn để bảo vệ m&igrave;nh, chẳng hạn như đeo mặt nạ thở bụi phấn hoa hoặc c&aacute; nh&acirc;n. Nếu một sở th&iacute;ch, chẳng hạn như sở hữu lo&agrave;i chim, l&agrave; g&acirc;y ra vấn đề, xem x&eacute;t từ bỏ n&oacute; ho&agrave;n to&agrave;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: right;\"><span style=\"color: rgb(128, 0, 0);\"><b style=\"\"><i style=\"\"><span style=\"font-family: Arial;\"></span></i></b></span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">&nbsp;</span></p></span>\n', 1),
(19, 'Viêm tiểu phế quản', '\n   <h1 class=\"title\">Viêm tiểu phế quản</h1>   <span class=\"des\">Viêm tiểu phế quản nặng có thể gây thở khó khăn đáng kể, da xanh (xanh tím) - một dấu hiệu oxy không đầy đủ. Điều này đòi hỏi chăm sóc y tế khẩn cấp.</span>\n   <span><p style=\"text-align: justify; \"><span style=\"font-family: Arial; font-size: 18pt; line-height: 150%; \">Định nghĩa</span></p>\r\n<p style=\"margin-top:6.0pt;mso-margin-bottom-alt:auto;line-height:150%\"><span style=\"font-family:Arial\">Vi&ecirc;m tiểu phế quản l&agrave; một bệnh phổi phổ biến, thường do virus g&acirc;y ra. N&oacute; thường xuy&ecirc;n xảy ra ở trẻ sơ sinh, trẻ dưới 6 th&aacute;ng tuổi. Th&ocirc;ng thường, vi&ecirc;m tiểu phế quản xảy ra trong những th&aacute;ng m&ugrave;a đ&ocirc;ng.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Vi&ecirc;m tiểu phế quản bắt đầu với c&aacute;c triệu chứng tương tự như những người bị cảm lạnh th&ocirc;ng thường, nhưng sau đ&oacute; ho v&agrave; thở kh&ograve; kh&egrave;. C&aacute;c triệu chứng của vi&ecirc;m tiểu phế quản thường k&eacute;o d&agrave;i một hoặc hai tuần v&agrave; sau đ&oacute; biến mất.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Trong một số trường hợp, đặc biệt l&agrave; nếu c&oacute; một vấn đề sức khỏe cơ bản hoặc trẻ sơ sinh đẻ non, vi&ecirc;m tiểu phế quản c&oacute; thể trở th&agrave;nh nghi&ecirc;m trọng v&agrave; phải nhập viện. <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-size:18.0pt;line-height:150%;font-family:Arial\">C&aacute;c triệu chứng<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-size:14.0pt;line-height:150%;font-family:Arial\">Trong v&agrave;i ng&agrave;y đầu ti&ecirc;n<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">C&aacute;c dấu hiệu v&agrave; triệu chứng của vi&ecirc;m tiểu phế quản tương tự như những người bị cảm lạnh th&ocirc;ng thường: <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Chảy nước mũi.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Nghẹt mũi.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Sốt nhẹ (kh&ocirc;ng phải lu&ocirc;n lu&ocirc;n hiện diện).<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-size:14.0pt;line-height:150%;font-family:Arial\">Sau đ&oacute;, c&oacute; thể l&ecirc;n đến một tuần<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Thở kh&ograve; kh&egrave; - thở c&oacute; vẻ kh&oacute; khăn hơn hoặc ồn &agrave;o khi thở ra.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Thở nhanh hoặc kh&oacute; khăn.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Nhịp tim nhanh.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Ở trẻ sơ sinh khỏe mạnh, nhiễm tr&ugrave;ng thường tự biến mất trong 1 - 2 tuần. Nếu trẻ được sinh ra sớm hoặc c&oacute; vấn đề sức khỏe cơ bản, chẳng hạn như bệnh tim, t&igrave;nh trạng phổi hoặc hệ thống miễn dịch suy yếu, nhiễm tr&ugrave;ng c&oacute; thể nặng hơn v&agrave; c&oacute; thể cần phải nhập viện.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Vi&ecirc;m tiểu phế quản nặng c&oacute; thể g&acirc;y thở kh&oacute; khăn đ&aacute;ng kể, da xanh (xanh t&iacute;m) - một dấu hiệu oxy kh&ocirc;ng đầy đủ. Điều n&agrave;y đ&ograve;i hỏi chăm s&oacute;c y tế khẩn cấp.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Nếu c&oacute; nhiều hơn c&aacute;c vấn đề h&ocirc; hấp nhỏ, nhỏ hơn 12 tuần tuổi hoặc c&oacute; yếu tố nguy cơ kh&aacute;c của vi&ecirc;m tiểu phế quản - bao gồm sinh non hoặc bệnh tim hoặc bệnh phổi, li&ecirc;n lạc với b&aacute;c sĩ.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Nếu gặp bất kỳ dấu hiệu v&agrave; triệu chứng sau đ&acirc;y, t&igrave;m kiếm nhanh ch&oacute;ng chăm s&oacute;c y t��: <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">&Oacute;i mửa.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Thở rất nhanh - hơn 40 hơi thở một ph&uacute;t v&agrave; n&ocirc;ng.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Da m&agrave;u xanh, đặc biệt l&agrave; xung quanh m&ocirc;i v&agrave; m&oacute;ng tay (xanh t&iacute;m).<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Kiệt sức cố gắng để thở hoặc sự cần thiết phải ngồi dậy để thở.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Thờ ơ.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Từ chối uống đủ chất lỏng, hoặc h&iacute;t thở qu&aacute; nhanh khi ăn hoặc uống.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Nghe &acirc;m thanh thở kh&ograve; kh&egrave;</span><span lang=\"EN-US\" style=\"font-family:Arial;mso-ansi-language:EN-US\">.</span><span style=\"font-family:Arial\"> <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-size:18.0pt;line-height:150%;font-family:Arial\">Nguy&ecirc;n nh&acirc;n<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Vi&ecirc;m tiểu phế quản xảy ra khi virus th&acirc;m nhập v&agrave;o hệ thống h&ocirc; hấp v&agrave; đến c&aacute;c tiểu phế quản, ống nhỏ nhất của đường h&ocirc; hấp từ ph&acirc;n nh&aacute;nh ra khỏi hai ống thở ch&iacute;nh (phế quản) trong phổi. Nhiễm virus l&agrave;m cho phế quản bị sưng ph&ugrave; v&agrave; bị vi&ecirc;m. Kết quả l&agrave;, chất nhầy thường thu thập trong c&aacute;c đường h&ocirc; hấp, c&oacute; thể l&agrave;m kh&oacute; cho kh&ocirc;ng kh&iacute; lưu th&ocirc;ng tự do qua phổi.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Ở trẻ em nhiều tuổi hơn v&agrave; người lớn, dấu hiệu v&agrave; triệu chứng thường nhẹ. Tuy nhi&ecirc;n, tiểu phế quản trẻ sơ sinh hẹp hơn nhiều hơn người lớn v&agrave; dễ bị tắc, dẫn đến kh&oacute; thở hơn.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Vi r&uacute;t hợp b&agrave;o h&ocirc; hấp (RSV), một loại virus phổ biến, g&acirc;y ra những trường hợp vi&ecirc;m tiểu phế quản ở trẻ em nhất. Phần c&ograve;n lại được g&acirc;y ra bởi c&aacute;c t&aacute;c nh&acirc;n nhiễm tr&ugrave;ng kh&aacute;c, bao gồm virus g&acirc;y bệnh c&uacute;m hoặc cảm lạnh th&ocirc;ng thường.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Vi&ecirc;m phế quản l&agrave; một t&igrave;nh trạng truyền nhiễm. Nhiễm virus sẽ giống như cảm lạnh hoặc c&uacute;m - qua c&aacute;c giọt trong kh&ocirc;ng kh&iacute; khi một người bị bệnh ho, hắt hơi hoặc n&oacute;i chuyện. Cũng c&oacute; thể mắc vi&ecirc;m tiểu phế quản bằng c&aacute;ch chạm v&agrave;o đối tượng chia sẻ, chẳng hạn như dụng cụ, khăn, đồ chơi v&agrave; sau đ&oacute; chạm v&agrave;o mắt, mũi hay miệng.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-size:18.0pt;line-height:150%;font-family:Arial\">C&aacute;c yếu tố nguy cơ<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Một trong những yếu tố nguy cơ lớn nhất đối với bị vi&ecirc;m tiểu phế quản trẻ dưới 6 th&aacute;ng tuổi, bởi v&igrave; phổi v&agrave; hệ thống miễn dịch chưa ph&aacute;t triển đầy đủ. Trai c&oacute; xu hướng vi&ecirc;m tiểu phế quản thường xuy&ecirc;n hơn hơn con g&aacute;i.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">C&aacute;c yếu tố kh&aacute;c c&oacute; li&ecirc;n quan với tăng nguy cơ vi&ecirc;m tiểu phế quản ở trẻ em bao gồm: <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Kh&ocirc;ng được b&uacute; sữa mẹ - trẻ sơ sinh b&uacute; sữa mẹ nhận được lợi &iacute;ch miễn dịch từ mẹ.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Sinh non.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Bệnh tim hoặc t&igrave;nh trạng phổi.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Hệ thống miễn dịch bị khuyết.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Tiếp x&uacute;c với kh&oacute;i thuốc l&aacute;.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">M&ocirc;i trường nhiều trẻ, chẳng hạn như trong một m&ocirc;i trường chăm s&oacute;c trẻ em.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Sống trong một m&ocirc;i trường đ&ocirc;ng đ&uacute;c.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">C&oacute; anh, chị, em ruột ở trường học hoặc chăm s&oacute;c trẻ v&agrave; mang lại sự l&acirc;y nhiễm.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-size:18.0pt;line-height:150%;font-family:Arial\">C&aacute;c biến chứng<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">C&aacute;c biến chứng của vi&ecirc;m tiểu phế quản nặng c&oacute; thể bao gồm: <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Kh&oacute; thở nặng l&ecirc;n.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">T&iacute;m t&aacute;i, t&igrave;nh trạng m&agrave; trong đ&oacute; da xuất xanh hoặc t&aacute;i m&eacute;t, đặc biệt l&agrave; xung quanh m&ocirc;i, g&acirc;y ra do thiếu oxy.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Mất nước.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Mệt mỏi.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Suy h&ocirc; hấp nặng.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Nếu xảy ra, c&oacute; thể cần nhập viện. Suy h&ocirc; hấp nặng c&oacute; thể y&ecirc;u cầu đặt nội kh&iacute; quản để hỗ trợ h&ocirc; hấp của đứa trẻ cho đến khi nhiễm tr&ugrave;ng đ&atilde; được dưới sự kiểm so&aacute;t.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Nếu trẻ sơ sinh đ&atilde; được sinh ra sớm, c&oacute; bệnh tim hoặc bệnh phổi, hoặc c&oacute; một hệ thống miễn dịch bị tổn hại, gi&aacute;m s&aacute;t chặt chẽ khi bắt đầu c&oacute; dấu hiệu của vi&ecirc;m tiểu phế quản. C&aacute;c nhiễm tr&ugrave;ng c&oacute; thể nhanh ch&oacute;ng trở n&ecirc;n nghi&ecirc;m trọng, v&agrave; c&aacute;c dấu hiệu v&agrave; triệu chứng của điều kiện cơ bản c&oacute; thể trở n&ecirc;n tồi tệ hơn. Trong những trường hợp như vậy, trẻ thường sẽ cần phải nhập viện để theo d&otilde;i sức khỏe v&agrave; cung cấp dịch vụ chăm s&oacute;c cần thiết.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Kh&ocirc;ng thường xuy&ecirc;n, vi&ecirc;m tiểu phế quản được đi k&egrave;m bởi một nhiễm tr&ugrave;ng phổi như vi&ecirc;m phổi do vi khuẩn. T&aacute;i l&acirc;y nhiễm với RSV sau khi bị bệnh đợt ban đ��u c&oacute; thể xảy ra nhưng thường l&agrave; kh&ocirc;ng nghi&ecirc;m trọng. Đợt bệnh lặp đi lặp lại của vi&ecirc;m tiểu phế quản c&oacute; thể đi trước sự ph&aacute;t triển của bệnh hen suyễn sau n&agrave;y trong đời, nhưng mối quan hệ giữa hai điều kiện l&agrave; kh&ocirc;ng r&otilde; r&agrave;ng.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-size:18.0pt;line-height:150%;font-family:Arial\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">B&aacute;c sĩ c&oacute; thể sẽ nghe phổi với một ống nghe để kiểm tra thở kh&ograve; kh&egrave; v&agrave; k&eacute;o d&agrave;i (thở ra). Đ&acirc;y c&oacute; thể chỉ ra luồng kh&ocirc;ng kh&iacute; bị cản trở trong c&aacute;c tiểu phế quản. B&aacute;c sĩ c&oacute; thể xem x&eacute;t c&aacute;c yếu tố nguy cơ cụ thể đối với vi&ecirc;m tiểu phế quản.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Đ&ocirc;i khi c&aacute;c x&eacute;t nghiệm kh&aacute;c được đề nghị, bao gồm: <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Chụp X ray.</span></b><span style=\"font-family:Arial\"> Trong trường hợp nặng hoặc kh&ocirc;ng chắc chắn, b&aacute;c sĩ c&oacute; thể y&ecirc;u cầu chụp ngực X ray kiểm tra dấu hiệu của vi&ecirc;m phổi. B&aacute;c sĩ cũng c&oacute; thể kiểm tra sự hiện diện của đối tượng ngoại lai, chẳng hạn như đậu phộng hoặc mảnh nhỏ của nhựa c&oacute; thể đ&atilde; h&iacute;t v&agrave;o.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Kiểm tra chất nhầy.</span></b><span style=\"font-family:Arial\"> Ngo&agrave;i ra, b&aacute;c sĩ c&oacute; thể thu thập một mẫu của chất nhờn, bằng c&aacute;ch sử dụng một miếng gạc mũi hay thanh quản để kiểm tra virus c&oacute; thể g&acirc;y ra vi&ecirc;m tiểu phế quản.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">X&eacute;t nghiệm m&aacute;u.</span></b><span style=\"font-family:Arial\"> Đ&ocirc;i khi, x&eacute;t nghiệm m&aacute;u c&oacute; thể được sử dụng để kiểm tra số lượng tế b&agrave;o bạch cầu. Sự gia tăng c&aacute;c tế b&agrave;o m&aacute;u trắng thường l&agrave; một dấu hiệu cho thấy cơ thể đang chiến đấu chống nhiễm tr&ugrave;ng. X&eacute;t nghiệm m&aacute;u cũng c&oacute; thể x&aacute;c định xem mức độ oxy giảm trong m&aacute;u của con qu&yacute; vị. Một thử nghiệm thay thế cho c&aacute;c mức độ oxy l&agrave; một đo oxy tr&ecirc;n ng&oacute;n tay. Oxygen l&agrave; cần thiết để c&aacute;c cơ quan của cơ thể hoạt động, bao gồm cả n&atilde;o.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">B&aacute;c sĩ cũng c&oacute; thể hỏi về c&aacute;c dấu hiệu mất nước, đặc biệt l&agrave; nếu từ chối uống hoặc ăn hoặc đ&atilde; n&ocirc;n mửa. Những dấu hiệu của mất nước bao gồm mắt trũng, miệng kh&ocirc; v&agrave; da nhăn, v&agrave; &iacute;t hoặc kh&ocirc;ng nước tiểu.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-size:18.0pt;line-height:150%;font-family:Arial\">Lối sống v&agrave; c&aacute;c biện ph&aacute;p khắc phục hậu quả<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Mặc d&ugrave; c&oacute; thể kh&ocirc;ng r&uacute;t ngắn thời gian bệnh, c&oacute; thể để l&agrave;m giảm một số triệu chứng v&agrave; l&agrave;m cho trẻ thoải m&aacute;i hơn. Dưới đ&acirc;y l&agrave; một số mẹo để xem x&eacute;t:</span><span style=\"font-size:18.0pt;line-height:150%;\r\nfont-family:Arial\"><o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Độ ẩm kh&ocirc;ng kh&iacute;.</span></b><span style=\"font-family:Arial\"> Nếu kh&ocirc;ng kh&iacute; trong ph&ograve;ng bị kh&ocirc;, độ ẩm m&aacute;t hoặc sương m&ugrave; c&oacute; thể l&agrave;m ẩm kh&ocirc;ng kh&iacute; v&agrave; tắc nghẽn một c&aacute;ch dễ d&agrave;ng v&agrave; ho. H&atilde;y chắc chắn giữ độ ẩm sạch sẽ để ngăn chặn sự ph&aacute;t triển của vi khuẩn v&agrave; nấm mốc. Giữ ph&ograve;ng ấm nhưng kh&ocirc;ng qu&aacute; n&oacute;ng, nhiệt qu&aacute; nhiều c&oacute; thể l&agrave;m cho kh&ocirc;ng kh&iacute; kh&ocirc; hơn. Một c&aacute;ch kh&aacute;c để độ ẩm kh&ocirc;ng kh&iacute; l&agrave; chạy một v&ograve;i sen nước n&oacute;ng hoặc tắm trong ph&ograve;ng tắm v&agrave; để bay hơi nước l&ecirc;n ph&ograve;ng. Ngồi trong ph&ograve;ng giữ trẻ trong khoảng 15 ph&uacute;t c&oacute; thể gi&uacute;p giảm bớt một cơn ho.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Giữ con thẳng đứng.</span></b><span style=\"font-family:Arial\"> Vị tr&iacute; thẳng đứng thường l&agrave;m cho thở dễ d&agrave;ng hơn. Nếu c&oacute; kế hoạch để trẻ trong một ghế an to&agrave;n cho một khoảng thời gian d&agrave;i, chẳng hạn như cho một giấc ngủ ngắn, chắc chắn đầu của đứa trẻ sẽ kh&ocirc;ng ngả về ph&iacute;a trước, c&oacute; thể ngăn ngừa ngạt thở.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Cho trẻ uống nước.</span></b><span style=\"font-family:Arial\"> Để ngăn ngừa mất nước, cung cấp cho rất nhiều chất lỏng để uống, chẳng hạn như nước tr&aacute;i c&acirc;y, nước hoặc nước gelatin. Trẻ c&oacute; thể uống chậm hơn b&igrave;nh thường, do tắc nghẽn.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">H&atilde;y thử nước muối mũi để giảm bớt tắc nghẽn.</span></b><span style=\"font-family:Arial\"> Thấm giọt v&agrave;o một lỗ mũi, sau đ&oacute; ngay lập tức sử dụng một dụng cụ h&uacute;t mũi với một ống nhỏ (nhưng kh&ocirc;ng đẩy b&oacute;ng qu&aacute; xa). Lặp lại trong lỗ mũi kh&aacute;c. Nếu trẻ đủ lớn, c&oacute; thể dạy để thổi mũi.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Sử dụng thuốc giảm đau OTC.</span></b><span style=\"font-family:Arial\"><o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Duy tr&igrave; m&ocirc;i trường kh&ocirc;ng kh&oacute;i thuốc. </span></b><span style=\"font-family:Arial\">H&uacute;t thuốc c&oacute; thể l&agrave;m nặng th&ecirc;m c&aacute;c triệu chứng vi&ecirc;m đường h&ocirc; hấp. Nếu một th&agrave;nh vi&ecirc;n gia đ&igrave;nh h&uacute;t thuốc, h&atilde;y h&uacute;t thuốc b&ecirc;n ngo&agrave;i nh&agrave; v&agrave; b&ecirc;n ngo&agrave;i của xe.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-size:18.0pt;line-height:150%;font-family:Arial\">Ph&ograve;ng chống<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Một em b&eacute; c&oacute; thể ph&aacute;t triển vi&ecirc;m tiểu phế quản sau khi nhiễm một loại virus từ một người lớn hoặc trẻ em c&oacute; cảm lạnh th&ocirc;ng thường. Khi bị cảm lạnh, rửa tay trước khi chạm v&agrave;o em b&eacute;, v&agrave; c&acirc;n nhắc việc đeo khẩu trang. Thường xuy&ecirc;n rửa tay l&agrave;m giảm sự l&acirc;y lan của virus g&acirc;y vi&ecirc;m tiểu phế quản. Nếu trẻ bị vi&ecirc;m tiểu phế quản, giữ ở nh&agrave; cho đến khi lui bệnh ho&agrave;n to&agrave;n để tr&aacute;nh l&acirc;y lan cho người kh&aacute;c.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Những c&aacute;ch đơn giản nhưng hiệu quả kh&aacute;c c&oacute; thể gi&uacute;p kiềm chế sự l&acirc;y lan của nhiễm tr&ugrave;ng: <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Hạn chế tiếp x&uacute;c với những người bị sốt hoặc cảm lạnh.</span></b><span style=\"font-family:Arial\"> Nếu trẻ sơ sinh, đặc biệt l&agrave; trẻ sơ sinh sớm, tr&aacute;nh tiếp x&uacute;c với người bị cảm lạnh trong hai th&aacute;ng đầu ti&ecirc;n của cuộc sống.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Giữ cho ph&ograve;ng tắm, nh&agrave; bếp, b&agrave;n trong nh&agrave; sạch sẽ.</span></b><span style=\"font-family:\r\nArial\"> H&atilde;y đặc biệt cẩn thận nếu một th&agrave;nh vi&ecirc;n kh&aacute;c trong gia đ&igrave;nh bị cảm lạnh. Để khử tr&ugrave;ng c&aacute;c khu vực, c&oacute; thể sử dụng một dung dịch thuốc tẩy v&agrave; nước được thực hiện với một muỗng canh thuốc tẩy cho mỗi gallon nước lạnh. Kh&ocirc;ng được trộn lẫn bất kỳ h&oacute;a chất kh&aacute;c, v&igrave; điều n&agrave;y c&oacute; thể tạo ra một phản ứng h&oacute;a học độc hại. Lu&ocirc;n lu&ocirc;n lưu trữ hỗn hợp tự chế trong bao b&igrave; c&oacute; nh&atilde;n ra khỏi tầm với của trẻ nhỏ.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Sử dụng vật dụng chỉ một lần.</span></b><span style=\"font-family:Arial\"> Bỏ khăn giấy đ&atilde; d&ugrave;ng kịp thời, sau đ&oacute; rửa tay hoặc sử dụng chất rửa tay.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Sử dụng ly uống ri&ecirc;ng.</span></b><span style=\"font-family:Arial\"> Kh&ocirc;ng d&ugrave;ng chung ly với những người kh&aacute;c.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">H&atilde;y chuẩn bị từ nh&agrave;.</span></b><span style=\"font-family:Arial\"> Chất rửa tay tiện dụng cho ch&iacute;nh m&igrave;nh v&agrave; cho trẻ khi đang xa nh&agrave;.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><span style=\"font-family:Arial\">Rửa tay.</span></b><span style=\"font-family:Arial\"> Thường xuy&ecirc;n rửa tay của cả người lớn v&agrave; trẻ.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-size:14.0pt;line-height:150%;font-family:Arial\">Kh&ocirc;ng c&oacute; vắc xin sẵn</span><span style=\"font-family:Arial\"> <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><span style=\"font-family:Arial\">Kh&ocirc;ng c&oacute; thuốc chủng ngừa vi&ecirc;m tiểu phế quản. Nhưng thuốc palivizumab (Synagis) c&oacute; thể gi&uacute;p giảm khả năng RSV g&acirc;y bệnh nhiễm tr&ugrave;ng ở trẻ sơ sinh, cũng như l&agrave;m giảm sự cần thiết phải nhập viện v&agrave; giới hạn mức độ nghi&ecirc;m trọng của bệnh. Palivizumab thường được cho th&ocirc;ng qua một liều ti&ecirc;m duy nhất v&agrave;o một cơ lớn, chẳng hạn như đ&ugrave;i, mỗi th&aacute;ng một lần trong m&ugrave;a RSV cao điểm từ th&aacute;ng mười đến th&aacute;ng ba.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: right; \"><span style=\"color: rgb(128, 0, 0); \"><strong><i><span lang=\"EN-US\" style=\"font-family: Arial; \"></span></i></strong></span><b><i><span lang=\"EN-US\" style=\"font-family:Arial;mso-ansi-language:EN-US\"><o:p></o:p></span></i></b></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify; \"><b><i><span lang=\"EN-US\" style=\"font-family:Arial;mso-ansi-language:EN-US\">&nbsp;</span></i></b></p></span>\n', 1);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(20, 'Xẹp phổi', '\n   <h1 class=\"title\">Xẹp phổi</h1>   <span class=\"des\">Xẹp phổi đáng kể có thể xảy ra khi đã nằm điều trị trong bệnh viện. Tuy nhiên, đến gặp bác sĩ ngay nếu có khó thở. Các điều kiện khác bên cạnh xẹp phổi có thể gây ra khó thở, và hầu hết yêu cầu chẩn đoán chính xác và điều trị kịp thời. </span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa </span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Xẹp phổi - sự sụp đổ ho&agrave;n to&agrave;n hoặc một phần của phổi l&agrave; một biến chứng c&oacute; thể của nhiều vấn đề h&ocirc; hấp. Chất nhầy trong đường h&ocirc; hấp sau khi phẫu thuật, xơ nang, h&iacute;t sặc từ ngo&agrave;i, hen suyễn nặng v&agrave; chấn thương ngực l&agrave; một trong những nguy&ecirc;n nh&acirc;n phổ biến của xẹp phổi.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng giống như tr&agrave;n kh&iacute; m&agrave;ng phổi, l&agrave; kh&ocirc;ng kh&iacute; giữa th&agrave;nh ngực v&agrave; phổi, xẹp phổi ph&aacute;t triển khi c&aacute;c t&uacute;i kh&iacute; nhỏ (phế nang) trong phổi x&igrave; hơi. </span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Lượng của m&ocirc; phổi li&ecirc;n quan đến xẹp phổi c&oacute; thể thay đổi, t&ugrave;y thuộc v&agrave;o nguy&ecirc;n nh&acirc;n. C&aacute;c dấu hiệu v&agrave; triệu chứng xẹp phổi cũng kh&aacute;c nhau với nguy&ecirc;n nh&acirc;n cơ bản v&agrave; mức độ sự tham gia của phổi. Xẹp phổi c&oacute; thể l&agrave; nghi&ecirc;m trọng bởi v&igrave; n&oacute; l&agrave;m suy yếu việc trao đổi oxy v&agrave; carbon dioxide trong phổi. Điều trị phụ thuộc v&agrave;o nguy&ecirc;n nh&acirc;n v&agrave; mức độ nghi&ecirc;m trọng của sự sụp đổ.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">T&ugrave;y thuộc v&agrave;o mức độ nghi&ecirc;m trọng của xẹp phổi, c&oacute; thể kh&ocirc;ng c&oacute; dấu hiệu hoặc triệu chứng r&otilde; r&agrave;ng. Nếu trải nghiệm c&aacute;c dấu hiệu v&agrave; triệu chứng, c&oacute; thể bao gồm:</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở. </span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thở nhanh n&ocirc;ng.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho. </span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sốt nhẹ.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Xẹp phổi đ&aacute;ng kể c&oacute; thể xảy ra khi đ&atilde; nằm điều trị trong bệnh viện. Tuy nhi&ecirc;n, đến gặp b&aacute;c sĩ ngay nếu c&oacute; kh&oacute; thở. C&aacute;c điều kiện kh&aacute;c b&ecirc;n cạnh xẹp phổi c&oacute; thể g&acirc;y ra kh&oacute; thở, v&agrave; hầu hết y&ecirc;u cầu chẩn đo&aacute;n ch&iacute;nh x&aacute;c v&agrave; điều trị kịp thời. Nếu thở trở n&ecirc;n ng&agrave;y c&agrave;ng kh&oacute; khăn, t&igrave;m kiếm sự chăm s&oacute;c khẩn cấp.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Xẹp phổi c&oacute; thể l&agrave; kết quả của đường dẫn kh&iacute; bị chặn (xẹp phổi tắc nghẽn) hoặc &aacute;p lực b&ecirc;n ngo&agrave;i phổi (nonobstructive xẹp phổi). Cũng giống như bề mặt chất lỏng một bong b&oacute;ng x&agrave; ph&ograve;ng giữ bong b&oacute;ng c&ograve;n nguy&ecirc;n vẹn, &aacute;o kho&aacute;c bề mặt mỗi t&uacute;i kh&iacute; nhỏ (phế nang) trong phổi để ch&uacute;ng kh&ocirc;ng bị đổ. Bất cứ điều g&igrave; l&agrave;m giảm bề mặt, chẳng hạn như &aacute;p lực l&ecirc;n phổi, g&acirc;y xẹp phổi.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tắc nghẽn trong đường dẫn kh&iacute; (ống phế quản) c&oacute; thể g&acirc;y xẹp phổi tắc nghẽn. Nguy&ecirc;n nh&acirc;n c&oacute; thể bị tắc nghẽn bao gồm:</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Chất nhầy.</span></b><span style=\"font-family: Arial;\"> Sự t&iacute;ch tụ của chất nhầy trong đường h&ocirc; hấp, thường xảy ra trong v&agrave; sau khi phẫu thuật, l&agrave; nguy&ecirc;n nh&acirc;n phổ biến nhất của xẹp phổi. Thuốc được d&ugrave;ng trong khi phẫu thuật l&agrave;m cho phổi gi&atilde;n nở đầy đủ &iacute;t hơn so với th&ocirc;ng thường, chất tiết b&igrave;nh thường thu thập trong đường h&ocirc; hấp. H&uacute;t dịch phổi trong qu&aacute; tr&igrave;nh phẫu thuật sẽ gi&uacute;p giảm chất tiết, nhưng c&oacute; thể tiếp tục tiết ra sau đ&oacute;. Đ&acirc;y l&agrave; l&yacute; do tại sao tập thở v&agrave; ho trong qu&aacute; tr&igrave;nh phục hồi. Mở rộng phổi với h&iacute;t kh&ocirc;ng kh&iacute; l&agrave;m cho dễ d&agrave;ng hơn để ho ra. Chất nhầy cũng l&agrave; phổ biến ở những người bị xơ nang v&agrave; trong c&aacute;c cơn cấp hen suyễn nặng.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Dị vật đường thở.</span></b><span style=\"font-family: Arial;\"> Trẻ em c&oacute; thể h&iacute;t một dị vật, phổ biến nhất l&agrave; đậu phộng v&agrave;o phổi.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thu hẹp chủ yếu đường h&ocirc; hấp.</span></b><span style=\"font-family: Arial;\"> Do bệnh lao v&agrave; c&aacute;c bệnh kh&aacute;c c&oacute; li&ecirc;n quan đến đường h&ocirc; hấp lớn c&oacute; thể g&acirc;y ra xẹp phổi.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Khối u trong đường thở lớn.</span></b><span style=\"font-family: Arial;\"> Một khối ung thư hoặc l&agrave;nh t&iacute;nh tăng trưởng c&oacute; thể thu hẹp đường h&ocirc; hấp.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Cục m&aacute;u đ&ocirc;ng.</span></b><span style=\"font-family: Arial;\"> Chỉ xảy ra nếu c&oacute; sự chảy m&aacute;u v&agrave;o phổi m&agrave; kh&ocirc;ng thể ho ra.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">&Aacute;p lực b&ecirc;n ngo&agrave;i của phổi c&oacute; thể g&acirc;y xẹp phổi kh&ocirc;ng tắc nghẽn. Nguy&ecirc;n nh&acirc;n c&oacute; thể bao gồm:</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Chấn thương ngực.</span></b><span style=\"font-family: Arial;\"> Chấn thương từ ng&atilde; hay một tai nạn xe hơi, c&oacute; thể g&acirc;y thiệt hại v&agrave; n&eacute;n phổi.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tr&agrave;n dịch m&agrave;ng phổi.</span></b><span style=\"font-family: Arial;\"> Đ&acirc;y l&agrave; một sự t&iacute;ch tụ của chất lỏng giữa c&aacute;c m&ocirc; (m&agrave;ng phổi) đường phổi v&agrave; b&ecirc;n trong của th&agrave;nh ngực.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Vi&ecirc;m phổi.</span></b><span style=\"font-family: Arial;\"> Loại kh&aacute;c nhau của vi&ecirc;m phổi, c&oacute; thể tạm thời g&acirc;y ra xẹp phổi cũng như xẹp phổi tắc nghẽn. Xẹp phổi với sụp đổ một v&agrave;i tuần hoặc hơn c&oacute; thể dẫn đến gi&atilde;n phế quản, một điều kiện trong đ&oacute; thiệt hại cho đường h&ocirc; hấp g&acirc;y ra mở rộng v&agrave; trở n&ecirc;n yếu v&agrave; c&oacute; sẹo.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tr&agrave;n kh&iacute; m&agrave;ng phổi.</span></b><span style=\"font-family: Arial;\"> Kh&iacute; r&ograve; rỉ v&agrave;o kh&ocirc;ng gian giữa phổi v&agrave; th&agrave;nh ngực, gi&aacute;n tiếp g&acirc;y ra một số hoặc tất cả phổi sụp đổ.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Sẹo của m&ocirc; phổi.</span></b><span style=\"font-family: Arial;\"> Sẹo c&oacute; thể được g&acirc;y ra bởi chấn thương, bệnh phổi hoặc phẫu thuật. Trong những trường hợp hiếm hoi, xẹp phổi l&agrave; nhỏ so với thiệt hại cho c&aacute;c m&ocirc; phổi từ c&aacute;c sẹo.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Khối u.</span></b><span style=\"font-family: Arial;\"> Một số khối u lớn c&oacute; thể g&acirc;y &aacute;p lực l&ecirc;n phổi, ngăn chặn c&aacute;c đường dẫn kh&iacute;.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c yếu tố nguy cơ</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những yếu tố l&agrave;m tăng nguy cơ của xẹp phổi bao gồm: </span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sinh non, nếu phổi kh&ocirc;ng ph&aacute;t triển đầy đủ.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bất kỳ điều kiện tự ph&aacute;t ho, ng&aacute;p v&agrave; thở d&agrave;i. </span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh phổi, chẳng hạn như hen suyễn, gi&atilde;n phế quản, xơ nang. </span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ngủ kh&ocirc;ng thường xuy&ecirc;n thay đổi vị tr&iacute;.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Phẫu thuật ngực hoặc bụng.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">G&acirc;y m&ecirc;. </span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thở n&ocirc;ng - V&iacute; dụ do đau bụng hoặc g&atilde;y xương sườn.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Yếu cơ h&ocirc; hấp do chứng loạn dưỡng cơ, tổn thương tủy sống hoặc bệnh thần kinh cơ. </span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">B&eacute;o ph&igrave;, c&oacute; thể n&acirc;ng cao cơ ho&agrave;nh v&agrave; cản trở khả năng để h&iacute;t đầy đủ.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c biến chứng sau đ&acirc;y c&oacute; thể dẫn đến xẹp phổi: </span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Oxy m&aacute;u thấp (thiếu oxy).</span></b><span style=\"font-family: Arial;\"> Xẹp phổi cản trở khả năng của phổi để với oxy phế nang đầy đủ.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Sẹo.</span></b><span style=\"font-family: Arial;\"> Một số thiệt hại hoặc vết sẹo c&oacute; thể vẫn c&ograve;n sau khi phổi t&aacute;i gi&aacute;n nở, kết quả trong gi&atilde;n phế quản.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Vi&ecirc;m phổi.</span></b><span style=\"font-family: Arial;\"> Ở nguy cơ ph&aacute;t triển bệnh vi&ecirc;m phổi cho đến khi xẹp phổi.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Suy h&ocirc; hấp.</span></b><span style=\"font-family: Arial;\"> Một khu vực nhỏ, xẹp phổi, đặc biệt l&agrave; ở người lớn, thường l&agrave; c&oacute; thể điều trị được. Tuy nhi&ecirc;n, một khu vực rộng lớn, đặc biệt l&agrave; ở trẻ sơ sinh hoặc một người n&agrave;o đ&oacute; bị bệnh phổi, c&oacute; thể đe dọa t&iacute;nh mạng.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">X-quang v&ugrave;ng ngực thường c&oacute; thể chẩn đo&aacute;n xẹp phổi. C&aacute;c triệu chứng của nhiễm tr&ugrave;ng h&ocirc; hấp, đặc biệt l&agrave; vi&ecirc;m phổi, X-quang ngực của một đứa trẻ c&oacute; thể chỉ ra dị vật, nguy&ecirc;n nh&acirc;n phổ biến nhất của xẹp phổi tắc nghẽn ở trẻ em.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Để x&aacute;c định nguy&ecirc;n nh&acirc;n cơ bản, b&aacute;c sĩ c&oacute; thể chỉ định c&aacute;c x&eacute;t nghiệm kh&aacute;c, bao gồm: </span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Chụp cắt lớp (CT) qu&eacute;t.</span></b><span style=\"font-family: Arial;\"> CT l&agrave; một kỹ thuật X-ray, sản xuất chi tiết h&igrave;nh ảnh X-quang. CT scan c&oacute; thể gi&uacute;p x&aacute;c định liệu một khối u c&oacute; thể đ&atilde; g&acirc;y ra phổi sụp đổ hay một c&aacute;i g&igrave; đ&oacute; c&oacute; thể kh&ocirc;ng hiển thị tr&ecirc;n một X-quang thường.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Oxy.</span></b><span style=\"font-family: Arial;\"> Thử nghiệm đơn giản n&agrave;y sử dụng một thiết bị nhỏ được đặt tr&ecirc;n một trong những ng&oacute;n tay để đo độ b&atilde;o h&ograve;a oxy trong m&aacute;u.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Nội soi phế quản.</span></b><span style=\"font-family: Arial;\"> Ống s&aacute;ng linh hoạt luồn xuống cổ họng cho ph&eacute;p b&aacute;c sĩ xem v&agrave; c&oacute; thể loại bỏ &iacute;t nhất một phần c&aacute;c vật trong đường h&ocirc; hấp, chẳng hạn như một plug chất nhầy, khối u hoặc dị vật.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều trị xẹp phổi phụ thuộc v&agrave;o nguy&ecirc;n nh&acirc;n. Xẹp phổi của một khu vực nhỏ của phổi c&oacute; thể kh&ocirc;ng cần điều trị. Nếu c&oacute; một điều kiện cơ bản, chẳng hạn như một khối u, điều trị c&oacute; thể li&ecirc;n quan đến việc loại bỏ hoặc thu nhỏ khối u bằng h&oacute;a trị, phẫu thuật hoặc xạ trị.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thuốc men</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong một số trường hợp, thuốc c&oacute; thể được sử dụng. Ch&uacute;ng bao gồm:</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Acetylcystein (Acetadote, Mucomyst), l&agrave;m lỏng chất nhầy v&agrave; l&agrave;m dễ d&agrave;ng loại bỏ khi ho. </span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thuốc h&iacute;t gi&atilde;n phế quản (Foradil, Maxair, Proventil, Serevent, Ventolin, Xopenex), mở c&aacute;c ống phế quản của phổi, thở dễ d&agrave;ng hơn. </span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">DNase (Dornase Alfa), được sử dụng để x&oacute;a n&uacute;t chất nhầy ở trẻ em bị xơ nang v&agrave; được chấp nhận l&agrave; một điều trị xẹp phổi cho những người kh&ocirc;ng c&oacute; bệnh xơ nang. </span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Liệu Ph&aacute;p</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một số liệu ph&aacute;p được gọi l&agrave; vật l&yacute; trị liệu ngực được sử dụng để điều trị xẹp phổi. Ch&uacute;ng bao gồm:</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vỗ tay tr&ecirc;n ngực tr&ecirc;n khu vực bị sụp đổ để l&agrave;m lỏng chất nhầy. </span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thực hiện c&aacute;c b&agrave;i tập thở s&acirc;u (khuyến kh&iacute;ch đo phế dung).</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Định vị cơ thể để đầu thấp hơn ngực (được gọi l&agrave; tư thế tho&aacute;t nước), cho ph&eacute;p chất nhầy tho&aacute;t tốt hơn. </span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bổ sung oxy, c&oacute; thể gi&uacute;p giảm bớt kh&oacute; thở. </span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thủ tục phẫu thuật</span><span style=\"font-family: Arial;\"> </span><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">hoặc c&aacute;c thủ thuật</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">B&aacute;c sĩ c&oacute; thể đề nghị loại bỏ c&aacute;c dị vật đường thở, c&oacute; thể được thực hiện bằng c&aacute;ch h&uacute;t chất nhầy hoặc nội soi phế quản. Nội soi phế quản sử dụng một ống luồn xuống cổ họng đến đường h&ocirc; hấp. Th&ocirc;ng thường, thực hiện c&aacute;c thủ tục (bronchoscopist) c&oacute; thể loại bỏ một phần khối u để mở đường th&ocirc;ng kh&iacute; v&agrave; tạm thời l&agrave;m giảm sự tắc nghẽn.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Ph&ograve;ng chống</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Để giảm nguy cơ xẹp phổi: </span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Bỏ qua những hạt quả.</span></b><span style=\"font-family: Arial;\"> Kh&ocirc;ng cho trẻ em c&aacute;c loại hạt cho đến khi ch&uacute;ng khoảng 3 năm, khi c&oacute; răng h&agrave;m nhai kỹ hơn c&aacute;c loại hạt.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ngưng h&uacute;t thuốc l&aacute;.</span></b><span style=\"font-family: Arial;\"> H&uacute;t thuốc gia tăng sản xuất chất nhầy v&agrave; c&aacute;c thiệt hại cấu tr&uacute;c nhỏ giống như t&oacute;c đường ống phế quản (l&ocirc;ng mi). Chuyển động s&oacute;ng của n&oacute; gi&uacute;p loại bỏ chất nhầy của đường h&ocirc; hấp.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">B&agrave;i tập thở s&acirc;u.</span></b><span style=\"font-family: Arial;\"> Sau khi phẫu thuật, theo hướng dẫn của b&aacute;c sĩ để ho thường xuy&ecirc;n v&agrave; l&agrave;m c&aacute;c b&agrave;i tập thở s&acirc;u.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu phải nằm tr&ecirc;n giường, thay đổi vị tr&iacute; thường xuy&ecirc;n. Định vị lại ch&iacute;nh m&igrave;nh. H&atilde;y đứng dậy v&agrave; đi bộ ngay sau khi c&oacute; thể.</span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: right;\"><b style=\"\"><i style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\"><span style=\"color: rgb(128, 0, 0);\"></span></span></i></b></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">&nbsp;</span></p></span>\n', 1);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(21, 'Xơ phổi', '\n   <h1 class=\"title\">Xơ phổi</h1>   <span class=\"des\">Các triệu chứng thường gặp nhất là chứng xơ phổi là khó thở, đặc biệt là trong hoặc sau khi hoạt động thể chất, và ho khan.</span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Xơ phổi l&agrave; một bệnh nghi&ecirc;m trọng g&acirc;y sẹo tiến triển của m&ocirc; phổi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những suy nghĩ hiện nay, chứng xơ phổi bắt đầu với chấn thương lặp đi lặp lại c&aacute;c m&ocirc; trong v&agrave; giữa c&aacute;c t&uacute;i kh&iacute; nhỏ (phế nang) trong phổi. Thiệt hại cuối c&ugrave;ng dẫn đến sẹo (xơ h&oacute;a), v&agrave; l&agrave;m cho hơi thở kh&oacute; khăn. C&aacute;c triệu chứng th&ocirc;ng thường nhất l&agrave; kh&oacute; thở v&agrave; ho khan.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Phương ph&aacute;p điều trị hiện tại của chứng xơ phổi bao gồm thuốc v&agrave; điều trị để cải thiện chức năng phổi v&agrave; chất lượng cuộc sống. Một số phương ph&aacute;p điều trị mới cho chứng xơ phổi trong c&aacute;c thử nghiệm l&acirc;m s&agrave;ng. Trong khi đ&oacute;, việc cấy gh&eacute;p phổi c&oacute; thể l&agrave; một lựa chọn cho một số người bị xơ phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c dấu hiệu v&agrave; triệu chứng của chứng xơ phổi bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho khan.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mệt mỏi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng giải th&iacute;ch được giảm c&acirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau nhức bắp thịt v&agrave; khớp.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c triệu chứng thường gặp nhất l&agrave; chứng xơ phổi l&agrave; kh&oacute; thở, đặc biệt l&agrave; trong hoặc sau khi hoạt động thể chất, v&agrave; ho khan. Thường kh&ocirc;ng xuất hiện cho đến khi bệnh được n&acirc;ng cao, v&agrave; kh&ocirc;ng thể đảo ngược tổn thương phổi đ&atilde; xảy ra. Thậm ch&iacute; sau đ&oacute;, c&oacute; thể giảm nhẹ triệu chứng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vấn đề hơi thở thường trở n&ecirc;n tồi tệ dần dần, v&agrave; cuối c&ugrave;ng c&oacute; thể nhận được trong c&aacute;c hoạt động thường xuy&ecirc;n - mặc quần &aacute;o, n&oacute;i chuyện tr&ecirc;n điện thoại, thậm ch&iacute; ăn uống. Tại thời điểm n&agrave;y, c&aacute;c triệu chứng kh&ocirc;ng thể bỏ qua.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c diễn biến của chứng xơ phổi v&agrave; mức độ nghi&ecirc;m trọng của triệu chứng c&oacute; thể thay đổi đ&aacute;ng kể từ người sang người. Một số người bị bệnh rất nhanh ch&oacute;ng với c&aacute;c bệnh nghi&ecirc;m trọng. Những người kh&aacute;c c&oacute; triệu chứng trung b&igrave;nh nhiều hơn m&agrave; ph&aacute;t triển tồi tệ hơn trong thời gian v&agrave;i th&aacute;ng hay một năm.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Qu&aacute; tr&igrave;nh h&ocirc; hấp</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mỗi khi h&iacute;t v&agrave;o, di chuyển kh&ocirc;ng kh&iacute; đến phổi th&ocirc;ng qua hai đường dẫn kh&iacute; lớn được gọi l&agrave; phế quản. B&ecirc;n trong phổi, chia nhỏ phế quản như c&aacute;c nh&aacute;nh của c&acirc;y th&agrave;nh một triệu đường h&ocirc; hấp nhỏ hơn (tiểu phế quản) m&agrave; cuối c&ugrave;ng kết th&uacute;c bằng cụm t&uacute;i kh&ocirc;ng kh&iacute; nhỏ (phế nang). C&oacute; khoảng 300 triệu phế nang trong phổi mỗi. Trong c&aacute;c bức th&agrave;nh của c&aacute;c t&uacute;i kh&iacute; l&agrave; c&aacute;c mạch m&aacute;u nhỏ (mao mạch), nơi lượng oxy được th&ecirc;m v&agrave;o m&aacute;u, v&agrave; carbon dioxide - một sản phẩm chất thải của sự trao đổi chất được lấy ra.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Điều g&igrave; xảy ra trong chứng xơ phổi</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong chứng xơ phổi, phế nang nhỏ thiệt hại do c&aacute;c nguy&ecirc;n nh&acirc;n g&acirc;y sẹo kh&ocirc;ng thể đảo ngược. Th&ocirc;ng thường, c&aacute;c t&uacute;i phế nang được đ&aacute;nh gi&aacute; đ&agrave;n hồi cao, mở rộng v&agrave; hợp đồng như bong b&oacute;ng nhỏ với từng hơi thở. Nhưng sẹo l&agrave;m cho c&aacute;c m&ocirc; kẽ cứng v&agrave; d&agrave;y v&agrave; kh&ocirc;ng kh&iacute; &iacute;t linh hoạt hơn. Thay v&igrave; được mềm mại v&agrave; đ&agrave;n hồi, c&aacute;c t&uacute;i kh&iacute; c&oacute; cấu tạo của một miếng bọt biển kh&ocirc; cứng, l&agrave;m cho hơi thở kh&oacute; khăn hơn nhiều.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều t&iacute;ch tụ trong m&ocirc; sẹo l&agrave; kh&ocirc;ng b&igrave;nh thường, cơ thể chỉ l&agrave;m cho m&ocirc; đủ để sửa chữa thiệt hại. Nhưng trong chứng xơ phổi, qu&aacute; tr&igrave;nh sửa chữa đi xi&ecirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đối với những người bị xơ phổi, chữa bệnh c&oacute; thể bị cản trở theo c&aacute;ch kh&aacute;c. Th&ocirc;ng thường, h&igrave;nh thức c&aacute;c mạch m&aacute;u cơ thể mới gi&uacute;p mang oxy v&agrave; chất dinh dưỡng cho c&aacute;c tế b&agrave;o bị thương. V&iacute; dụ, nếu cắt ng&oacute;n tay, cơ thể gửi chất dinh dưỡng đến c&aacute;c ng&oacute;n tay để gi&uacute;p th&uacute;c đẩy chữa bệnh. Nhưng ở những người bị chứng xơ phổi, một số c&aacute;c mạch m&aacute;u mới ph&aacute;t triển đi từ hoặc b&iacute;t ở m&ocirc; sẹo.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Điều g&igrave; g&acirc;y thiệt hại cho phổi?</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&agrave;ng trăm c&aacute;c yếu tố c&oacute; thể g&acirc;y ra c&aacute;c tổn thương phổi m&agrave; cuối c&ugrave;ng dẫn đến xơ h&oacute;a phổi. Một số phổ biến nhất bao gồm:</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Lao động v&agrave; m&ocirc;i trường </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">D&agrave;i hạn tiếp x&uacute;c với một số chất độc v&agrave; c&aacute;c chất &ocirc; nhiễm c&oacute; thể g&acirc;y hại phổi. Trong số đ&oacute; c&oacute; bụi silic v&agrave; sợi amiăng. Phơi nhiễm kinh ni&ecirc;n đối với một số chất hữu cơ, bao gồm cả bụi ngũ cốc, m&iacute;a đường, ph&acirc;n chim v&agrave; động vật, cũng c&oacute; thể g&acirc;y xơ h&oacute;a.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Bức xạ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một tỷ lệ nhỏ những người nhận được trị liệu bức xạ đối với phổi hoặc ung thư v&uacute; c&oacute; dấu hiệu tổn thương phổi của th&aacute;ng hoặc đ&ocirc;i khi năm sau khi điều trị ban đầu. C&aacute;c mức độ nghi&ecirc;m trọng của thiệt hại t&ugrave;y thuộc v&agrave;o phổi được tiếp x&uacute;c với bức xạ, tổng số lượng bức xạ trị, h&oacute;a trị liệu cũng được sử dụng v&agrave; sự hiện diện của bệnh phổi nằm b&ecirc;n dưới.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thuốc men </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhiều loại thuốc c&oacute; thể g&acirc;y hại phổi, đặc biệt l&agrave; c&aacute;c loại thuốc h&oacute;a trị (methotrexate, cyclophosphamide); thuốc d&ugrave;ng để điều trị rối loạn nhịp tim v&agrave; c&aacute;c vấn đề tim mạch kh&aacute;c (amiodarone, propranolol); một số thuốc t&acirc;m thần v&agrave; một số thuốc kh&aacute;ng sinh (nitrofurantoin, sulfasalazine).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tr&agrave;o ngược dạ d&agrave;y thực quản bệnh (GERD)</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Acid reflux, xảy ra axit dạ d&agrave;y trở lại th&agrave;nh ống thực quản, xuất hiện một vai tr&ograve; quan trọng trong chứng xơ phổi. Mặc d&ugrave; những người bị xơ phổi thường xuy&ecirc;n c&oacute; GERD, họ c&oacute; thể kh&ocirc;ng c&oacute; triệu chứng điển h&igrave;nh của GERD, chẳng hạn như chứng ợ n&oacute;ng v&agrave; ợ hơi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Y tế v&agrave; c&aacute;c điều kiện kh&aacute;c </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhiễm tr&ugrave;ng phổi nghi&ecirc;m trọng như bệnh lao v&agrave; vi&ecirc;m phổi c&oacute; thể g&acirc;y tổn thương phổi vĩnh viễn. V&igrave; vậy, c&oacute; thể rối loạn c&oacute; ảnh hưởng đến c&aacute;c m&ocirc; trong cơ thể, kh&ocirc;ng chỉ phổi, như bệnh lupus ban đỏ hệ thống, vi&ecirc;m khớp dạng thấp, dermatomyositis, polymyositis, hội chứng Sjogren v&agrave; sarcoidosis. Trong trường h��p hiếm hoi, xơ cứng b&igrave; li&ecirc;n kết với một h&igrave;nh thức đặc biệt nghi&ecirc;m trọng của chứng xơ ho&aacute; phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Xơ h&oacute;a phổi tự ph&aacute;t: Khi nguy&ecirc;n nh&acirc;n kh&ocirc;ng được biết đến.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Danh mục c&aacute;c chất v&agrave; c&aacute;c điều kiện c&oacute; thể dẫn đến chứng xơ phổi l&agrave; d&agrave;i. Mặc d&ugrave; vậy, trong nhiều trường hợp, nguy&ecirc;n nh&acirc;n kh&ocirc;ng bao giờ được t&igrave;m thấy. Xơ phổi kh&ocirc;ng r&otilde; nguy&ecirc;n nh&acirc;n được gọi l&agrave; xơ h&oacute;a phổi tự ph&aacute;t.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c nh&agrave; nghi&ecirc;n cứu c&oacute; một số l&yacute; thuyết về những g&igrave; c&oacute; thể g&acirc;y ra chứng xơ phổi tự ph&aacute;t, bao gồm virus v&agrave; phơi nhiễm với kh&oacute;i thuốc l&aacute;. V&agrave; bởi v&igrave; một loại xơ h&oacute;a phổi tự ph&aacute;t h&agrave;nh trong gia đ&igrave;nh, di truyền cũng được cho l&agrave; đ&oacute;ng vai tr&ograve;, ngay cả ở những người kh&ocirc;ng trực tiếp thừa hưởng căn bệnh n&agrave;y.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c yếu tố l&agrave;m cho dễ bị xơ h&oacute;a phổi bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tuổi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mặc d&ugrave; chứng xơ phổi đ&atilde; được chẩn đo&aacute;n ở trẻ em v&agrave; trẻ sơ sinh, rối loạn rất c&oacute; thể ảnh hưởng đến tuổi v&agrave; người cao ni&ecirc;n trung b&igrave;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Giới t&iacute;nh</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">N&oacute;i chung, đ&agrave;n &ocirc;ng c&oacute; nhiều khả năng c&oacute; chứng xơ phổi hơn so với phụ nữ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Lao động v&agrave; độc tố m&ocirc;i trường</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; nguy cơ ph&aacute;t triển chứng xơ phổi nếu l&agrave;m việc trong n&ocirc;ng nghiệp, khai th&aacute;c, x&acirc;y dựng hoặc đang tiếp x&uacute;c với c&aacute;c chất &ocirc; nhiễm được biết l&agrave; thiệt hại phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Bức xạ v&agrave; h&oacute;a trị</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; phương ph&aacute;p điều trị ph&oacute;ng xạ đến ngực hoặc sử dụng thuốc h&oacute;a trị liệu nhất định l&agrave;m cho dễ bị xơ h&oacute;a phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ xơ h&oacute;a phổi tự ph&aacute;t</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mặc d&ugrave; c&aacute;c nguy&ecirc;n nh&acirc;n g&acirc;y xơ h&oacute;a phổi tự ph&aacute;t kh&ocirc;ng được biết, c&aacute;c nh&agrave; nghi&ecirc;n cứu đ&atilde; x&aacute;c định một số yếu tố m&agrave; dường như l&agrave;m tăng nguy cơ: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">H&uacute;t thuốc l&aacute;.</span></b><span style=\"font-family: Arial;\"> Người h&uacute;t thuốc nhiều hơn v&agrave; h&uacute;t thuốc l&aacute; trước đ&acirc;y ph&aacute;t triển tự ph&aacute;t chứng xơ phổi hơn những người chưa bao giờ h&uacute;t thuốc.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Yếu tố di truyền.</span></b><span style=\"font-family: Arial;\"> Một loại hiếm của chứng xơ phổi tự ph&aacute;t chạy trong gia đ&igrave;nh. C&aacute;c nh&agrave; nghi&ecirc;n cứu vẫn chưa x&aacute;c định được những gen cụ thể li&ecirc;n quan, nhưng họ đ&atilde; ph&aacute;t hiện ra những thay đổi di truyền trong c&aacute;c protein trong đường h&ocirc; hấp v&agrave; t&uacute;i kh&iacute; của người d&acirc;n với c&aacute;c loại xơ h&oacute;a phổi tự ph&aacute;t.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Virus.</span></b><span style=\"font-family: Arial;\"> Nhiều người b&aacute;o c&aacute;o ph&aacute;t triển c&aacute;c triệu chứng của chứng xơ phổi sau khi một căn bệnh do virus, đặc biệt l&agrave; một g&acirc;y ra bởi virus chẳng hạn như herpes, Epstein-Barr, virus g&acirc;y bệnh bạch cầu đơn nh&acirc;n c&ugrave;ng. Kết quả, c&aacute;c nh&agrave; nghi&ecirc;n cứu đang điều tra c&aacute;c vi r&uacute;t c&oacute; thể đ&oacute;ng vai tr&ograve; trong bệnh phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c biến chứng của chứng xơ phổi c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Mức độ &ocirc;xy m&aacute;u thấp</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bởi v&igrave; phổi xơ l&agrave;m giảm lượng oxy đ&oacute; đi v&agrave;o m&aacute;u, c&oacute; khả năng ph&aacute;t triển oxy trong m&aacute;u thấp hơn b&igrave;nh thường. Thiếu oxy c&oacute; thể ph&aacute; vỡ cơ bản hoạt động cơ thể, v&agrave; ở mức độ thấp nghi&ecirc;m trọng c&oacute; thể đe dọa t&iacute;nh mạng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Cao huyết &aacute;p ở phổi </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng giống như cao huyết &aacute;p hệ thống, t&igrave;nh trạng n&agrave;y chỉ ảnh hưởng đến c&aacute;c động mạch trong phổi. N&oacute; bắt đầu khi c&aacute;c động mạch v&agrave; mao mạch nhỏ nhất được n&eacute;n bằng c&aacute;ch m&ocirc; sẹo, g&acirc;y ra sức kh&aacute;ng lưu lượng m&aacute;u trong phổi tăng. Điều n&agrave;y sẽ l&agrave;m tăng &aacute;p suất trong động mạch phổi. Tăng huyết &aacute;p động mạch phổi l&agrave; một bệnh nghi&ecirc;m trọng trở n&ecirc;n tồi tệ dần dần v&agrave; cuối c&ugrave;ng c&oacute; thể g&acirc;y tử vong.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Suy tim phải</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều n&agrave;y t&igrave;nh trạng nghi&ecirc;m trọng xảy ra khi t&acirc;m thất phải đ&atilde; phải bơm mạnh hơn hơn b&igrave;nh thường để di chuyển m&aacute;u qua động mạch phổi bị chặn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Suy h&ocirc; hấp</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều n&agrave;y thường l&agrave; giai đoạn cuối của bệnh phổi m&atilde;n t&iacute;nh. N&oacute; xảy ra khi mức độ oxy trong m&aacute;u giảm thấp nguy hiểm. Thấp oxy m&aacute;u cấp c&oacute; thể dẫn đến rối loạn nhịp tim v&agrave; bất tỉnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Chẩn đo&aacute;n chứng xơ phổi c&oacute; thể cực kỳ kh&oacute; khăn. Sự kh&aacute;c biệt giữa c&aacute;c h&igrave;nh thức tự ph&aacute;t v&agrave; kh&ocirc;ng tự ph&aacute;t của bệnh kh&ocirc;ng phải lu&ocirc;n lu&ocirc;n r&otilde; r&agrave;ng, v&agrave; c&aacute;c hệ thống đặt t&ecirc;n v&agrave; ph&acirc;n loại cho cả hai đều c&oacute; lịch sử đ&atilde; g&acirc;y nhầm lẫn v&agrave; tranh c&atilde;i. Ngo&agrave;i ra, nhiều điều kiện y tế, bao gồm cả bệnh nghẽn phổi m&atilde;n t&iacute;nh (COPD), hen suyễn v&agrave; thậm ch&iacute; suy tim, c&oacute; thể bắt chước chứng xơ phổi, do đ&oacute;, c&aacute;c b&aacute;c sĩ phải xem x&eacute;t trước khi đưa ra chẩn đo&aacute;n x&aacute;c định.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một lịch sử y tế, kh&aacute;m sức khỏe v&agrave; thậm ch&iacute; cả chụp X - quang l&agrave; kh&ocirc;ng đủ để chẩn đo&aacute;n chứng xơ phổi, nhưng c&oacute; thể gi&uacute;p loại trừ c&aacute;c điều kiện kh&aacute;c. V&igrave; l&yacute; do đ&oacute;, c&oacute; thể đ&atilde; thử nghiệm như:</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Chụp X - quang</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều n&agrave;y thường sẽ hiển thị c&aacute;c m&ocirc; sẹo điển h&igrave;nh của chứng xơ phổi v&agrave; rất hữu &iacute;ch cho sau qu&aacute; tr&igrave;nh bệnh v&agrave; điều trị. Thỉnh thoảng, ngực X-ray l&agrave; b&igrave;nh thường v&agrave; thử nghiệm th&ecirc;m được y&ecirc;u cầu phải giải th&iacute;ch nguy&ecirc;n nh&acirc;n kh&oacute; thở kh&ocirc;ng r&otilde;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Chụp cắt lớp vi t&iacute;nh (HRCT) qu&eacute;t độ ph&acirc;n giải cao</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều n&agrave;y cung cấp nhiều chi tiết h&igrave;nh ảnh sắc n&eacute;t của phổi hơn l&agrave; qu&eacute;t CT th&ocirc;ng thường hay X-quang ngực thường xuy&ecirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">X&eacute;t nghiệm chức năng phổi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c x&eacute;t nghiệm n&agrave;y x&aacute;c định phổi l&agrave;m việc chung như thế n&agrave;o. Đ&aacute;nh gi&aacute; bao nhi&ecirc;u kh&ocirc;ng kh&iacute; phổi c&oacute; thể nắm giữ, v&agrave; nhanh c&oacute; thể di chuyển kh&ocirc;ng kh&iacute; v&agrave;o v&agrave; ra khỏi phổi như thế n&agrave;o.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p đo oxy</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều thử nghiệm đơn giản n&agrave;y sử dụng một thiết bị nhỏ được đặt tr&ecirc;n một trong những ng&oacute;n tay để đo độ b&atilde;o h&ograve;a oxy trong m&aacute;u. Phương ph&aacute;p đo oxy c&oacute; thể phục vụ như l&agrave; một c&aacute;ch dễ d&agrave;ng để theo d&otilde;i qu&aacute; tr&igrave;nh của bệnh, đ&ocirc;i khi c&oacute; thể ch&iacute;nh x&aacute;c hơn l&agrave; chụp X - quang.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thử nghiệm tập thể dục căng thẳng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một thử nghiệm thực hiện tr&ecirc;n m&aacute;y chạy bộ hoặc xe đạp c&oacute; thể được sử dụng để theo d&otilde;i chức năng phổi khi đang hoạt động.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Th&ocirc;ng thường, mặc d&ugrave; xơ h&oacute;a phổi c&oacute; thể được chẩn đo&aacute;n dứt kho&aacute;t bằng c&aacute;ch kiểm tra một số lượng nhỏ m&ocirc; phổi (sinh thiết) trong ph&ograve;ng th&iacute; nghiệm. C&aacute;c mẫu m&ocirc; c&oacute; thể thu được trong một trong c&aacute;c c&aacute;ch sau:</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nội soi phế quản ( sinh thiết) </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong phần n&agrave;y, b&aacute;c sĩ loại bỏ c&aacute;c mẫu m&ocirc; rất nhỏ - sử dụng ống linh hoạt, ống nhỏ (soi phế quản) đi qua mũi hoặc miệng v&agrave;o phổi. Những rủi ro của phế quản thường l&agrave; trẻ vị th&agrave;nh ni&ecirc;n - thường bị đau họng v&agrave; khan tiếng tạm thời - nhưng c&aacute;c mẫu m&ocirc; đ&ocirc;i khi qu&aacute; nhỏ cho một chẩn đo&aacute;n ch&iacute;nh x&aacute;c.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Rửa phế quản</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong thủ thuật n&agrave;y, b&aacute;c sĩ ti&ecirc;m nước muối qua soi phế quản, v&agrave; sau đ&oacute; ngay lập tức h&uacute;t n&oacute; ra. C&aacute;c giải ph&aacute;p thu hồi chất c&oacute; chứa c&aacute;c tế b&agrave;o từ t&uacute;i kh&iacute;. Mặc d&ugrave; mẫu rửa phế quản - khu vực lớn hơn của phổi so với c&aacute;c thủ tục kh&aacute;c, n&oacute; c&oacute; thể kh&ocirc;ng cung cấp đủ th&ocirc;ng tin để chẩn đo&aacute;n bệnh xơ h&oacute;a phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Phẫu thuật sinh thiết</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong một số trường hợp, b&aacute;c sĩ c&oacute; thể khuy&ecirc;n phẫu thuật sinh thiết. Mặc d&ugrave; đ&acirc;y l&agrave; một thủ thuật x&acirc;m lấn nhiều hơn với c&aacute;c biến chứng tiềm năng, n&oacute; thường l&agrave; c&aacute;ch duy nhất để c&oacute; được một mẫu m&ocirc; đủ lớn để l&agrave;m chẩn đo&aacute;n ch&iacute;nh x&aacute;c. Trong thủ tục, dụng cụ phẫu thuật v&agrave; một camera nhỏ được ch&egrave;n v&agrave;o th&ocirc;ng qua hai hoặc ba vết mổ nhỏ giữa c&aacute;c xương sườn. Camera cho ph&eacute;p b&aacute;c sĩ phẫu thuật để xem phổi tr&ecirc;n một m&agrave;n h&igrave;nh video trong khi loại bỏ c&aacute;c mẫu m&ocirc; từ phổi. Do hỗ trợ video thoracoscopic phẫu thuật kh&ocirc;ng cần cắt qua xương sườn, c&oacute; khả năng bị đau &iacute;t hơn v&agrave; để chữa l&agrave;nh nhanh hơn với phẫu thuật mở phổi truyền thống</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c vết sẹo phổi xảy ra trong chứng xơ phổi kh&ocirc;ng thể đảo ngược, v&agrave; kh&ocirc;ng điều trị hiện nay đ&atilde; chứng minh hiệu quả trong việc ngăn chặn sự ph&aacute;t triển cuối c&ugrave;ng của bệnh. Một số phư��ng ph&aacute;p điều trị, mặc d&ugrave;, c&oacute; thể cải thiện triệu chứng tạm thời hoặc l&agrave;m chậm sự tiến triển của bệnh. Những loại kh&aacute;c gi&uacute;p cải thiện chất lượng cuộc sống.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thuốc men</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhiều người chẩn đo&aacute;n mắc chứng xơ phổi được điều trị ban đầu với một (prednisone) corticosteroid, đ&ocirc;i khi kết hợp với c&aacute;c thuốc ức chế hệ thống miễn dịch kh&aacute;c, như methotrexate hay cyclosporin. Những kết hợp n&agrave;y đ&atilde; kh&ocirc;ng chứng minh rất hiệu quả. Th&ecirc;m N-acetylcystein, một dẫn xuất của một acid amin tự nhi&ecirc;n, để prednisone c&oacute; thể l&agrave;m giảm bệnh ở một số người. V&agrave; trong c&aacute;c thử nghiệm l&acirc;m s&agrave;ng, c&aacute;c pirfenidone thuốc đ&atilde; được hiển thị để cải thiện chức năng phổi v&agrave; ngăn chặn ph&aacute; hủy c&aacute;c m&ocirc; phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Prednisone v&agrave; thuốc ức chế miễn dịch kh&aacute;c cũng c&oacute; thể g&acirc;y ra t&aacute;c dụng phụ nghi&ecirc;m trọng, trong đ&oacute; c&oacute; bệnh tiểu đường, bệnh tăng nh&atilde;n &aacute;p, giảm sản xuất tế b&agrave;o hồng cầu, ung thư da v&agrave; ung thư hạch. V&igrave; l&yacute; do đ&oacute;, thường l&agrave; ngưng điều trị nếu kh&ocirc;ng c&oacute; cải thiện sau s&aacute;u th&aacute;ng. Mặc d&ugrave; một số người cải thiện tạm thời với c&aacute;c loại thuốc ức chế miễn dịch, kh&ocirc;ng r&otilde; l&yacute; do tại sao một số người phản ứng v&agrave; những người kh&aacute;c th&igrave; kh&ocirc;ng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Cấy gh&eacute;p phổi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Cấy gh&eacute;p phổi c&oacute; thể l&agrave; một lựa chọn cuối c&ugrave;ng cho những người trẻ tuổi bị xơ phổi nghi&ecirc;m trọng đ&atilde; kh&ocirc;ng được hưởng lợi từ c&aacute;c lựa chọn điều trị kh&aacute;c. Để được xem x&eacute;t để cấy gh&eacute;p, phải đồng &yacute; bỏ thuốc nếu h&uacute;t thuốc, khỏe mạnh đủ để trải qua phẫu thuật v&agrave; điều trị sau gh&eacute;p, phải sẵn s&agrave;ng v&agrave; c&oacute; thể thực hiện theo c&aacute;c chương tr&igrave;nh y tế đưa ra bởi nh&oacute;m phục hồi chức năng v&agrave; cấy gh&eacute;p, c&oacute; ki&ecirc;n nhẫn v&agrave; sức mạnh tinh thần v&agrave; hỗ trợ để trải qua sự chờ đợi cho một cơ quan t&agrave;i trợ. Y&ecirc;u cầu cuối c&ugrave;ng l&agrave; đặc biệt quan trọng bởi v&igrave; c&aacute;c cơ quan từ nh&agrave; t&agrave;i trợ được cung cấp đủ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">C&aacute;c phương ph&aacute;p điều trị</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c phương ph&aacute;p điều trị chứng xơ phổi tập trung n&acirc;ng cao chất lượng cuộc sống. Ch&uacute;ng bao gồm:</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">&Ocirc;xy liệu ph&aacute;p.</span></b><span style=\"font-family: Arial;\"> Sử dụng oxy kh&ocirc;ng thể dừng lại tổn thương phổi, nhưng n&oacute; c&oacute; thể l&agrave;m cho hơi thở v&agrave; tập thể dục dễ d&agrave;ng hơn, ngăn ngừa hoặc l&agrave;m giảm c&aacute;c biến chứng từ oxy thấp trong m&aacute;u, cải thiện giấc ngủ v&agrave; cảm gi&aacute;c được tốt. N&oacute; cũng c&oacute; thể l&agrave;m giảm huyết &aacute;p ở ph&iacute;a b&ecirc;n tim phải. C&oacute; nhiều khả năng nhận được oxy khi ngủ hoặc tập thể dục, mặc d&ugrave; một số người c&oacute; thể sử dụng n&oacute; suốt ng&agrave;y đ&ecirc;m.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Phục hồi chức năng phổi.</span></b><span style=\"font-family: Arial;\"> Đ&acirc;y l&agrave; một chương tr&igrave;nh ch&iacute;nh thức cho những người bị bệnh phổi m&atilde;n t&iacute;nh. Mục đ&iacute;ch của phục hồi chức năng phổi kh&ocirc;ng chỉ để điều trị một bệnh hoặc thậm ch&iacute; cải thiện hoạt động h&agrave;ng ng&agrave;y, m&agrave; c&ograve;n để gi&uacute;p đỡ những người bị xơ phổi sống đầy đủ, đ&aacute;p ứng cuộc sống. Cuối c&ugrave;ng, chương tr&igrave;nh phục hồi chức năng phổi tập trung v&agrave;o việc tập thể dục, về giảng dạy l&agrave;m thế n&agrave;o để h&iacute;t thở hiệu quả hơn, về gi&aacute;o dục, hỗ trợ tinh thần v&agrave; tư vấn dinh dưỡng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Th&ocirc;ng thường, c&aacute;ch tiếp cận đa diện đ&ograve;i hỏi một đội ngũ c&aacute;c chuy&ecirc;n gia chăm s&oacute;c sức khỏe m&agrave; c&oacute; thể bao gồm b&aacute;c sĩ, y t&aacute;, chuy&ecirc;n vi&ecirc;n phục hồi chức năng, chuy&ecirc;n vi&ecirc;n dinh dưỡng v&agrave; nh&acirc;n vi&ecirc;n x&atilde; hội. Chương tr&igrave;nh c&oacute; thể rất kh&aacute;c nhau, tuy nhi&ecirc;n. B&aacute;c sĩ thường c&oacute; thể cho biết về c&aacute;c chương tr&igrave;nh phục hồi chức năng phổi trong khu vực. Hoặc li&ecirc;n lạc với Hiệp hội Phổi cho biết th&ecirc;m th&ocirc;ng tin.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p trị liệu điều tra</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một số phương ph&aacute;p điều trị chứng xơ phổi đang được ph&aacute;t triển hoặc đang trong thử nghiệm l&acirc;m s&agrave;ng. c&oacute; thể t&igrave;m thấy một danh s&aacute;ch rộng r&atilde;i của c&aacute;c thử nghiệm l&acirc;m s&agrave;ng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phong c&aacute;ch sống v&agrave; biện ph&aacute;p khắc phục</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">T&iacute;ch cực tham gia điều trị v&agrave; nghỉ ngơi c&agrave;ng khỏe mạnh c&agrave;ng tốt l&agrave; rất cần thiết để sống chung với chứng xơ phổi. V&igrave; l&yacute; do đ&oacute;, điều quan trọng l&agrave;:</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ngừng h&uacute;t thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; bệnh phổi, điều tốt nhất c&oacute; thể l&agrave;m cho ch&iacute;nh m&igrave;nh l&agrave; dừng h&uacute;t thuốc. N&oacute;i chuyện với b&aacute;c sĩ về c&aacute;c t&ugrave;y chọn cho việc dừng, c&oacute; sử dụng nhiều kỹ thuật đ&atilde; được chứng minh để gi&uacute;p bỏ thuốc l&aacute;. V&agrave; bởi v&igrave; kh&oacute;i thuốc c&oacute; thể g&acirc;y hại cho phổi, t&ocirc;i kh&ocirc;ng cho ph&eacute;p người kh&aacute;c h&uacute;t thuốc xung quanh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tập thể dục thường xuy&ecirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tập thể dục l&agrave; một thanh gươm hai lưỡi đối với người bị bệnh phổi. N&oacute; c&oacute; thể l&agrave;m cho kh&oacute; thở tạm thời, nhưng n&oacute; gi&uacute;p duy tr&igrave; chức năng phổi, giảm căng thẳng v&agrave; cải thiện sức khỏe tổng thể v&agrave; hạnh ph&uacute;c. Nếu đ&atilde; thực hiện, kh&ocirc;ng dừng lại. V&agrave; nếu hiện kh&ocirc;ng hoạt động thể chất, n&oacute;i chuyện với b&aacute;c sĩ về một chương tr&igrave;nh tập thể dục m&agrave; c&oacute; thể l&agrave; tốt nhất.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ăn uống tốt</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những người bị bệnh phổi c&oacute; thể bị mất trọng lượng bởi v&igrave; n&oacute; kh&ocirc;ng thoải m&aacute;i để ăn v&agrave; v&igrave; năng lượng th&ecirc;m cần để h&iacute;t thở. Tuy nhi&ecirc;n, một chế độ ăn uống gi&agrave;u dinh dưỡng c&oacute; chứa đủ lượng calo cần thiết. Một chuy&ecirc;n vi&ecirc;n dinh dưỡng c&oacute; thể cung cấp hướng dẫn th&ecirc;m cho việc ăn uống khỏe mạnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nghỉ ngơi nhiều</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">&Iacute;t nhất t&aacute;m giờ nghỉ ngơi chất lượng tốt mỗi đ&ecirc;m c&oacute; thể tăng cường hệ miễn dịch v&agrave; cảm gi&aacute;c hạnh ph&uacute;c.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Kiểm so&aacute;t GERD</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu đ&atilde; được chẩn đo&aacute;n GERD, cần thiết để giữ cho n&oacute; được kiểm so&aacute;t. C&oacute; thể gi&uacute;p l&agrave;m giảm acid reflux, chờ đợi &iacute;t nhất 3 - 4 giờ sau khi ăn trước khi nằm xuống, ăn bữa ăn nhỏ, tr&aacute;nh bất kỳ loại thực phẩm g&acirc;y ra c&aacute;c triệu chứng v&agrave; duy tr&igrave; một trọng lượng khỏe mạnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: right;\"><span style=\"color: rgb(128, 0, 0);\"><b style=\"\"><i style=\"\"><span style=\"font-family: Arial;\"></span></i></b></span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">&nbsp;</span></p></span>\n', 1);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(22, 'Bệnh cơ tim phì đại', '\n   <h1 class=\"title\">Bệnh cơ tim phì đại</h1>   <span class=\"des\">Bệnh cơ tim phì đại! Bệnh cơ tim phì đại thường được gây ra bởi đột biến gen. Những đột biến này làm cho cơ tim để phát triển dày bất thường.</span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh cơ tim ph&igrave; đại (HCM) l&agrave; một căn bệnh trong đ&oacute; cơ tim sẽ trở th&agrave;nh d&agrave;y bất thường hoặc nở to (hypertrophied). Điều n&agrave;y cơ tim d&agrave;y l&ecirc;n c&oacute; thể l&agrave;m kh&oacute; cho tim bơm m&aacute;u. Bệnh cơ tim ph&igrave; đại cũng c&oacute; thể ảnh hưởng đến hệ thống điện của tim. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh cơ tim ph&igrave; đại thường kh&ocirc;ng được chẩn đo&aacute;n bởi v&igrave; nhiều những người c&oacute; bệnh cơ tim ph&igrave; đại c&oacute; rất &iacute;t c&aacute;c triệu chứng, nếu c&oacute;. Trong một số &iacute;t người c&oacute; điều kiện n&agrave;y, cơ tim d&agrave;y l&ecirc;n c&oacute; thể g&acirc;y ra c&aacute;c dấu hiệu v&agrave; triệu chứng như kh&oacute; thở v&agrave; c&aacute;c vấn đề trong hệ thống điện của tim dẫn đến nhịp tim bất thường đe dọa t&iacute;nh mạng (loạn nhịp tim). </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">May mắn thay, hầu hết những người bị bệnh cơ tim ph&igrave; đại cuộc sống b&igrave;nh thường kh&ocirc;ng c&oacute; vấn đề đ&aacute;ng kể. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\"><img width=\"1\" height=\"1\" alt=\"\" src=\"file:///C:/Users/MyPhuong/AppData/Local/Temp/msohtml1/01/clip_image001.gif\">C&oacute; thể c&oacute; c&aacute;c dấu hiệu v&agrave; triệu chứng của bệnh cơ tim ph&igrave; đại bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở, đặc biệt l&agrave; trong thời gian tập thể dục hay gắng sức.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau ngực, đặc biệt l&agrave; trong thời gian tập thể dục hay gắng sức.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ngất xỉu, đặc biệt l&agrave; trong thời gian tập thể dục hay gắng sức.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ch&oacute;ng mặt.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mệt mỏi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tim đập nhanh - cảm gi&aacute;c tim đập nhanh, rung hoặc nghiền.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh cơ tim ph&igrave; đại thường được g&acirc;y ra bởi đột biến gen. Những đột biến n&agrave;y l&agrave;m cho cơ tim để ph&aacute;t triển d&agrave;y bất thường. Những người c&oacute; bệnh cơ tim ph&igrave; đại cũng c&oacute; một sự sắp xếp bất thường của c&aacute;c sợi cơ tim. C&aacute;c tế b&agrave;o cơ tim trở n&ecirc;n lộn xộn, được gọi l&agrave; rối loạn myofiber. T&igrave;nh trạng lộn xộn n&agrave;y c&oacute; thể đ&oacute;ng g&oacute;p v&agrave;o một nhịp tim kh&ocirc;ng đều (loạn nhịp tim) ở một số người. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c mức độ nghi&ecirc;m trọng của bệnh cơ tim ph&igrave; đại rất kh&aacute;c nhau. Hầu hết những người c&oacute; cơ tim ph&igrave; đại c&oacute; một h&igrave;nh thức của bệnh m&agrave; trong đ&oacute; c&aacute;c bức th&agrave;nh (v&aacute;ch) giữa hai buồng dưới của tim (t&acirc;m thất) trở n&ecirc;n mở rộng v&agrave; cản trở lưu th&ocirc;ng m&aacute;u. Điều n&agrave;y đ&ocirc;i khi được gọi l&agrave; bệnh cơ tim ph&igrave; đại tắc nghẽn. Khoảng 70 phần trăm những người c&oacute; bệnh cơ tim ph&igrave; đại c&oacute; một số h&igrave;nh thức, cản trở. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đ&ocirc;i khi bệnh cơ tim ph&igrave; đại xảy ra m&agrave; kh&ocirc;ng g&acirc;y cản trở đ&aacute;ng kể lưu lượng m&aacute;u. Tuy nhi&ecirc;n, ch&iacute;nh c&aacute;c buồng tim bơm m&aacute;u của tim (t&acirc;m thất tr&aacute;i) c&oacute; thể trở n&ecirc;n cứng, l&agrave;m giảm lượng m&aacute;u t&acirc;m thất c&oacute; thể giữ v&agrave; c&oacute; bao nhi&ecirc;u m&aacute;u được bơm ra ngo&agrave;i cơ thể với từng cơn co thắt. C&aacute;c b&aacute;c sĩ đ&ocirc;i khi gọi đ&acirc;y l&agrave; bệnh cơ tim ph&igrave; đại kh&ocirc;ng g&acirc;y cản trở hoặc cơ tim ph&igrave; đại nonobstructive</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh cơ tim ph&igrave; đại thường di truyền. C&oacute; 50 phần trăm cơ hội m&agrave; c&aacute;c con c&aacute;i của những người c&oacute; bệnh cơ tim ph&igrave; đại sẽ thừa hưởng đột biến di truyền cho rối loạn. Anh chị em của những người c&oacute; bệnh cơ tim ph&igrave; cũng c&oacute; nguy cơ. Kết quả l&agrave;, họ h&agrave;ng gần gũi với người bệnh cơ tim ph&igrave; đại được khuyến kh&iacute;ch để n&oacute;i chuyện với b&aacute;c sĩ về việc s&agrave;ng lọc bệnh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh cơ tim ph&igrave; đại xảy ra trong khoảng một trong mỗi 500 người v&agrave; ảnh hưởng đến nam v&agrave; nữ bằng nhau. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong nhiều người, bệnh cơ tim ph&igrave; đại kh&ocirc;ng g&acirc;y ra vấn đề sức khỏe đ&aacute;ng kể. Tuy nhi&ecirc;n, ở một số người, bệnh cơ tim ph&igrave; đại c&oacute; thể g&acirc;y ra c&aacute;c dấu hiệu v&agrave; triệu chứng nặng như kh&oacute; thở, đau ngực hay ngất xỉu. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những người c&oacute; bệnh cơ tim ph&igrave; đại c&oacute; nguy cơ của nhịp tim nguy hiểm bất thường (chứng loạn nhịp tim), chẳng hạn như nhịp nhanh thất hoặc rung thất. Những nhịp tim bất thường c&oacute; thể g&acirc;y ra đột tử do tim. Bệnh cơ tim ph&igrave; đại l&agrave; nguy&ecirc;n nh&acirc;n g&acirc;y tử vong h&agrave;ng đầu li&ecirc;n quan đến tim đột ngột ở những người dưới 30 tuổi. May mắn thay, c&aacute;i chết như vậy l&agrave; rất hiếm. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Biến chứng c&oacute; thể xảy ra của bệnh cơ tim ph&igrave; đại bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Loạn nhịp tim.</span></b><span style=\"font-family: Arial;\"> D&agrave;y cơ tim, cũng như cấu tr&uacute;c bất thường của c&aacute;c tế b&agrave;o tim (rối loạn), c&oacute; thể l&agrave;m gi&aacute;n đoạn hoạt động b&igrave;nh thường của hệ thống điện của tim, dẫn đến tim đập nhanh hay kh&ocirc;ng đều. Rung t&acirc;m nhĩ, nhịp nhanh thất v&agrave; rung t&acirc;m thất nằm trong số c&aacute;c rối loạn nhịp tim c&oacute; thể được g&acirc;y ra bởi bệnh cơ tim ph&igrave; đại. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c biến chứng đ&aacute;ng sợ nhất của cơ tim ph&igrave; đại l&agrave; tử vong đột ngột do nhịp nhanh thất hoặc rung thất. Thật kh&ocirc;ng may, n&oacute; c&oacute; thể kh&oacute; để dự đo&aacute;n m&agrave; những người c&oacute; bệnh cơ tim ph&igrave; đại c&oacute; nhiều nguy cơ đe dọa mạng sống, nhịp tim bất thường. Nếu gặp cơn ngất xỉu, ch&oacute;ng mặt nặng hoặc đ&aacute;nh trống ngực k&eacute;o d&agrave;i,<span style=\"\">&nbsp; </span>n&ecirc;n t&igrave;m kiếm sự chăm s&oacute;c y tế ngay lập tức. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Cản trở d&ograve;ng chảy m&aacute;u.</span></b><span style=\"font-family: Arial;\"> Trong nhiều người, c&aacute;c cơ tim d&agrave;y l&ecirc;n g&acirc;y cản trở lưu lượng m&aacute;u ra khỏi tim. Điều n&agrave;y c&oacute; thể dẫn đến kh&oacute; thở với gắng sức, đau ngực, ch&oacute;ng mặt v&agrave; ngất.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Van hai l&aacute; vấn đề.</span></b><span style=\"font-family: Arial;\"> C&aacute;c cơ tim d&agrave;y l&ecirc;n c&oacute; thể để lại một kh&ocirc;ng gian nhỏ hơn cho m&aacute;u lưu th&ocirc;ng, n&oacute; sẽ g&acirc;y ra m&aacute;u vội v&agrave;ng th&ocirc;ng qua c&aacute;c van tim nhanh hơn v&agrave; mạnh mẽ hơn. Lực lượng n&agrave;y tăng l&ecirc;n c&oacute; thể ngăn chặn van hai l&aacute; - van giữa t&acirc;m nhĩ tr&aacute;i của tim v&agrave; t&acirc;m thất tr&aacute;i đ&oacute;ng đ&uacute;ng c&aacute;ch. Kết quả l&agrave;, m&aacute;u c&oacute; thể bị r&ograve; rỉ ngược v&agrave;o trong t&acirc;m nhĩ tr&aacute;i. Điều n&agrave;y được gọi l&agrave; hở van hai l&aacute;. Van hai l&aacute; hở c&oacute; thể dẫn đến c&aacute;c biến chứng kh&aacute;c như suy tim hay chứng loạn nhịp tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Suy tim.</span></b><span style=\"font-family: Arial;\"> Suy tim l&agrave; tim kh&ocirc;ng thể bơm đủ m&aacute;u để đ&aacute;p ứng nhu cầu của cơ thể. C&aacute;c cơ tim d&agrave;y l&ecirc;n của cơ tim ph&igrave; đại cuối c&ugrave;ng c&oacute; thể trở n&ecirc;n qu&aacute; cứng để l&agrave;m hiệu quả v&agrave; c&oacute; thể dẫn đến kh&oacute; thở v&agrave; suy tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Bệnh cơ tim gi&atilde;n nở.</span></b><span style=\"font-family: Arial;\"> Theo thời gian, d&agrave;y l&ecirc;n cơ tim c&oacute; thể trở n&ecirc;n yếu k&eacute;m v&agrave; kh&ocirc;ng hiệu quả v&agrave; t&acirc;m thất trở n&ecirc;n mở rộng (d&atilde;n) v&agrave; khả năng bơm trở n&ecirc;n mạnh mẽ hơn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">B&aacute;c sĩ đầu ti&ecirc;n c&oacute; thể nghi ngờ bệnh cơ tim ph&igrave; đại, nếu anh ta hoặc c&ocirc; nghe thấy một tiếng thổi tim trong khi lắng nghe tr&aacute;i tim. Một tiếng thổi tim c&oacute; thể chỉ ra rằng cơ tim d&agrave;y l&ecirc;n g&acirc;y ra chảy m&aacute;u bất thường th&ocirc;ng qua tr&aacute;i tim. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Si&ecirc;u &acirc;m tim l&agrave; thử nghiệm phổ biến nhất để chẩn đo&aacute;n bệnh cơ tim ph&igrave; đại. Sử dụng h&igrave;nh ảnh si&ecirc;u &acirc;m tim, b&aacute;c sĩ c&oacute; thể thấy độ d&agrave;y của cơ tim, c&oacute; d&ograve;ng m&aacute;u cản trở v&agrave; nếu van tim đang di chuyển b&igrave;nh thường. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Si&ecirc;u &acirc;m tim sử dụng s&oacute;ng &acirc;m thanh để tạo ra h&igrave;nh ảnh của tim. Si&ecirc;u &acirc;m tim cho ph&eacute;p b&aacute;c sĩ nh&igrave;n thấy sự chuyển động phức tạp của trung t&acirc;m trong chuyển động - t&acirc;m thất b&oacute;p v&agrave; thư gi&atilde;n, v&agrave; van đ&oacute;ng mở trong nhịp điệu với nhịp đập của tim. C&aacute;c b&aacute;c sĩ c&oacute; thể sử dụng những h&igrave;nh ảnh n&agrave;y để x&aacute;c định c&aacute;c bất thường trong cơ tim v&agrave; van. C&aacute;c loại echocardiograms bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Si&ecirc;u &acirc;m tim qua th&agrave;nh ngực.</span></b><span style=\"font-family: Arial;\"> Đ&acirc;y l&agrave; một si&ecirc;u &acirc;m tim ti&ecirc;u chuẩn. Một kỹ thuật (sonographer) lan gel v&agrave;o ngực v&agrave; sau đ&oacute; ấn v&agrave;o một thiết bị được gọi l&agrave; bộ chuyển đổi tr&ecirc;n l&agrave;n da, nhằm một ch&ugrave;m tia si&ecirc;u &acirc;m th&ocirc;ng qua ngực đến tr&aacute;i tim. Đầu d&ograve; cũng ph&aacute;t hiện s&oacute;ng &acirc;m vang phản &aacute;nh bởi c&aacute;c cấu tr&uacute;c nội bộ. Một m&aacute;y t&iacute;nh chuyển đổi vang v&agrave;o h&igrave;nh ảnh chuyển động tr&ecirc;n m&agrave;n h&igrave;nh. Nếu phổi hoặc xương sườn che khuất tầm nh&igrave;n, một lượng nhỏ thuốc nhuộm tĩnh mạch c&oacute; thể được sử dụng để cải thiện h&igrave;nh ảnh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Transesophageal si&ecirc;u &acirc;m tim (thực quản).</span></b><span style=\"font-family: Arial;\"> Trong loại si&ecirc;u &acirc;m tim, một ống linh hoạt c&oacute; chứa một bộ chuyển đổi, được hướng dẫn xuống cổ họng v&agrave; v&agrave;o thực quản - kết nối miệng dạ d&agrave;y. Từ đ&oacute;, c&aacute;c bộ chuyển đổi c&oacute; thể nhận được h&igrave;nh ảnh chi tiết hơn của tr&aacute;i tim. B&aacute;c sĩ c&oacute; thể chỉ định si&ecirc;u &acirc;m tim transesophageal nếu n&oacute; kh&oacute; khăn để c&oacute; được một h&igrave;nh ảnh r&otilde; r&agrave;ng của tr&aacute;i tim<span style=\"\">&nbsp; </span>với một si&ecirc;u &acirc;m tim ti&ecirc;u chuẩn hoặc nếu người đ&oacute; muốn xem x&eacute;t th&ecirc;m van hai l&aacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Th&iacute; nghiệm bổ sung c&oacute; thể được thực hiện để gi&uacute;p t&igrave;m c&aacute;c hiệu ứng kh&aacute;c của bệnh cơ tim ph&igrave; đại v&agrave; gi&uacute;p x&aacute;c định những loại điều trị c&oacute; thể l&agrave; cần thiết. C&aacute;c x&eacute;t nghiệm bổ sung bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Điện t&acirc;m đồ (ECG).</span></b><span style=\"font-family: Arial;\"> Thử nghiệm n&agrave;y ghi lại hoạt động điện của tim. N&oacute; được thực hiện để ph&aacute;t hiện c&aacute;c t&iacute;n hiệu điện bất thường c&oacute; thể l&agrave; kết quả của c&aacute;c cơ tim d&agrave;y l&ecirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Holter theo d&otilde;i.</span></b><span style=\"font-family: Arial;\"> Đ&acirc;y l&agrave; một điện t&acirc;m đồ cầm tay c&oacute; ghi điện li&ecirc;n tục trong tr&aacute;i tim, thường l&agrave; trong suốt một đến hai ng&agrave;y. N&oacute; được sử dụng để ph&aacute;t hiện nhịp tim bất thường.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đặt ống th&ocirc;ng tim.</span></b><span style=\"font-family: Arial;\"> Đ&ocirc;i khi b&aacute;c sĩ c&oacute; thể sử dụng thử nghiệm để đo &aacute;p lực của d&ograve;ng m&aacute;u trong tim. Một ống th&ocirc;ng được đưa v&agrave;o động mạch, bắt đầu từ v&ugrave;ng h&aacute;ng. Sau đ&oacute; cẩn thận luồng đến ngăn tr&aacute;i tim theo hướng dẫn của một m&aacute;y X - quang cho thấy thời gian thực h&igrave;nh ảnh của cơ thể. Chất m&agrave;u được ti&ecirc;m qua ống th&ocirc;ng, v&agrave; c&aacute;c m&aacute;y X - quang l&agrave;m cho h&igrave;nh ảnh (angiograms) của tim v&agrave; mạch m&aacute;u.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">MRI.</span></b><span style=\"font-family: Arial;\"> H&igrave;nh ảnh cộng hưởng từ (MRI) l&agrave; một kỹ thuật h&igrave;nh ảnh c&oacute; sử dụng từ trường v&agrave; s&oacute;ng v&ocirc; tuyến để tạo ra h&igrave;nh ảnh của tr&aacute;i tim. MRI tim thường được sử dụng ngo&agrave;i việc si&ecirc;u &acirc;m tim, đặc biệt nếu những h&igrave;nh ảnh echocardiographic kh&ocirc;ng kết luận.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thử nghiệm c&aacute;c c&aacute;ch tiếp cận đối với th&acirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">X&eacute;t nghiệm di truyền c&oacute; sẵn, c&oacute; thể c&oacute; thể gi&uacute;p b&aacute;c sĩ chẩn đo&aacute;n bệnh cơ tim ph&igrave; đại. Tuy nhi&ecirc;n, nguy&ecirc;n nh&acirc;n di truyền của bệnh cơ tim ph&igrave; đại kh&ocirc;ng phải ho&agrave;n to&agrave;n hiểu r&otilde;. Hiện c&oacute; hơn 10 gen được x&aacute;c định cho đến nay, c&oacute; thể l&agrave;m cho dễ bị bệnh cơ tim ph&igrave; đại. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Do t&iacute;nh chất phức tạp của c&aacute;c gen tương t&aacute;c như thế n&agrave;o, c&aacute;c x&eacute;t nghiệm di truyền thường kh&ocirc;ng đưa ra một c&acirc;u trả lời dứt kho&aacute;t. Ngo&agrave;i ra, c&aacute;c c&ocirc;ng ty bảo hiểm kh&ocirc;ng bao gồm c&aacute;c thử nghiệm. Thảo luận với b&aacute;c sĩ về việc thử nghiệm di truyền c&oacute; thể l&agrave; một lựa chọn. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; một mức độ tương đối - cha mẹ, anh chị em ruột hoặc con với bệnh cơ tim ph&igrave; đại, c&aacute;c chuy&ecirc;n gia khuy&ecirc;n n&ecirc;n thường xuy&ecirc;n kiểm tra để t&igrave;m dấu hiệu của t&igrave;nh trạng n&agrave;y. Đối với trẻ em, khuyến c&aacute;o l&agrave; phải c&oacute; một si&ecirc;u &acirc;m tim v&agrave; điện mỗi năm một lần cho đến khi tuổi dậy th&igrave; hay tuổi 18. Nếu kh&ocirc;ng c&oacute; bằng chứng của bệnh cơ tim ph&igrave; đại được t&igrave;m thấy bởi thời gian đạt đến tuổi trưởng th&agrave;nh, b&aacute;c sĩ c&oacute; thể khuy&ecirc;n n&ecirc;n điều chỉnh lịch tr&igrave;nh chiếu năm năm một lần. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c mục ti&ecirc;u của điều trị cho bệnh cơ tim ph&igrave; đại l&agrave; để l&agrave;m giảm triệu chứng v&agrave; ngăn ngừa đột tử do tim ở những người c&oacute; nguy cơ cao. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều trị t&ugrave;y chọn cho cơ tim ph&igrave; đại bao gồm thuốc, phẫu thuật hoặc c&aacute;c phương ph&aacute;p kh&aacute;c để ti&ecirc;u diệt m&ocirc; tim tắc nghẽn hoặc cấy c&aacute;c thiết bị để gi&uacute;p kiểm so&aacute;t nhịp tim. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc điều trị.</span></b><span style=\"font-family: Arial;\"> Đ&acirc;y l&agrave; t&ugrave;y chọn tốt nhất cho hầu hết mọi người. Nhiều thuốc c&oacute; thể gi&uacute;p thư gi&atilde;n c&aacute;c cơ bắp v&agrave; l&agrave;m chậm tốc độ sao cho tim c&oacute; thể bơm hiệu quả hơn. Một số loại thuốc b&aacute;c sĩ c&oacute; thể đề nghị bao gồm c&aacute;c thuốc chẹn beta, thuốc chẹn k&ecirc;nh canxi hoặc thuốc disopyramide chống arrhythmic hoặc amiodaron.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Cắt một phần cơ v&aacute;ch ngăn (myectomy).</span></b><span style=\"font-family: Arial;\"> Đ&acirc;y l&agrave; một hoạt động mở tr&aacute;i tim, trong đ&oacute; b&aacute;c sĩ phẫu thuật loại bỏ một phần của cơ d&agrave;y l&ecirc;n th&agrave;nh cơ tim, v&aacute;ch ngăn c&aacute;ch hai buồng tim dưới (t&acirc;m thất). Loại bỏ một phần của cơ n&agrave;y cải thiện lưu th&ocirc;ng m&aacute;u v&agrave; l&agrave;m giảm hở van hai l&aacute;. Myectomy được sử dụng nếu thuốc kh&ocirc;ng l&agrave;m giảm triệu chứng. Hầu hết những người c&oacute; c&aacute;c triệu chứng v&agrave; trải qua myectomy kh&ocirc;ng c&oacute; triệu chứng hơn nữa. Phẫu thuật n&agrave;y chỉ c&oacute; sẵn ở c&aacute;c trung t&acirc;m y tế chuy&ecirc;n điều trị c&aacute;c bệnh cơ tim ph&igrave; đại.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Cắt bỏ v&aacute;ch ngăn.</span></b><span style=\"font-family: Arial;\"> Đ&acirc;y l&agrave; một điều trị mới, trong đ&oacute; một phần nhỏ của cơ tim d&agrave;y l&ecirc;n bị ph&aacute; hủy bằng c&aacute;ch ti&ecirc;m cồn qua một ống th&ocirc;ng v&agrave;o động mạch cung cấp m&aacute;u cho n&oacute;. C&oacute; những biến chứng c&oacute; thể với quy tr&igrave;nh n&agrave;y, bao gồm cả sự gi&aacute;n đoạn của hệ thống điện của tim - đ&ograve;i hỏi phải cấy m&aacute;y tạo nhịp tim. Sự th&agrave;nh c&ocirc;ng l&acirc;u d&agrave;i của thủ tục n&agrave;y chưa được biết đến, nhưng n&oacute; ng&agrave;y c&agrave;ng trở n&ecirc;n th&ocirc;ng dụng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Cấy m&aacute;y tạo nhịp tim.</span></b><span style=\"font-family: Arial;\"> M&aacute;y tạo nhịp tim l&agrave; một thiết bị điện tử nhỏ ch&egrave;n v&agrave;o dưới da sẽ gửi t&iacute;n hiệu điện đến tr&aacute;i tim để gi&aacute;m s&aacute;t v&agrave; điều tiết nhịp tim. Phẫu thuật cấy gh&eacute;p m&aacute;y tạo nhịp tim thường được thực hiện dưới g&acirc;y t&ecirc; tại chỗ v&agrave; thường mất &iacute;t hơn ba giờ. Cấy m&aacute;y tạo nhịp tim thường kh&ocirc;ng c&oacute; hiệu quả như lựa chọn phẫu thuật, nhưng n&oacute; đ&ocirc;i khi được sử dụng ở người lớn tuổi, những người muốn tr&aacute;nh nhiều thủ tục x&acirc;m lấn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">M&aacute;y khử rung tim c���y dưới da (ICD).</span></b><span style=\"font-family: Arial;\"> Đ&acirc;y l&agrave; một thiết bị m&aacute;y cấy v&agrave;o ngực<span style=\"\">&nbsp; </span>như m&aacute;y tạo nhịp tim. ICD li&ecirc;n tục gi&aacute;m s&aacute;t nhịp tim. Nếu một chứng loạn nhịp tim đe dọa t&iacute;nh mạng xảy ra, ICD cung cấp ch&iacute;nh x&aacute;c hiệu chuẩn c&aacute;c c&uacute; sốc điện để phục hồi nhịp tim b&igrave;nh thường. Một số nhỏ những người c&oacute; bệnh cơ tim ph&igrave; đại c&oacute; nguy cơ tử vong đột ngột v&igrave; nhịp tim bất thường. Trong những c&aacute; nh&acirc;n n&agrave;y c&oacute; nguy cơ cao, nhiều b&aacute;c sĩ đề nghị cấy một ICD. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những người c&oacute; bệnh cơ tim ph&igrave; đại n&agrave;y c&oacute; thể cấy ICD bao gồm những người c&oacute;: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đ&atilde; c&oacute; ngừng tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một hoặc nhiều th&agrave;nh vi&ecirc;n gia đ&igrave;nh với c&aacute;i chết đột ngột do bệnh cơ tim ph&igrave; đại.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng r&otilde; nguy&ecirc;n nh&acirc;n ngất xỉu.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhịp tim đập nhanh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Huyết &aacute;p kh&ocirc;ng tăng trong khi luyện tập thử nghiệm.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Si&ecirc;u &acirc;m tim để ph&aacute;t hiện độ d&agrave;y cực của th&agrave;nh thất tr&aacute;i.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Đối ph&oacute; v&agrave; hỗ trợ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Được chẩn đo&aacute;n bệnh cơ tim ph&igrave; đại c&oacute; thể g&acirc;y ra một loạt những cảm x&uacute;c kh&oacute; khăn v&agrave; sợ h&atilde;i. Giống như nhiều người với t&igrave;nh trạng n&agrave;y, c&oacute; khả năng đấu tranh với những cảm x&uacute;c của sự sợ h&atilde;i, đau buồn v&agrave; tức giận. Đ&acirc;y l&agrave; những phản ứng th&iacute;ch hợp của con người với những thay đổi nghi&ecirc;m trọng đi k&egrave;m với chẩn đo&aacute;n, bao gồm cả hạn chế tập thể dục, một sự phụ thuộc v&agrave;o thuốc suốt đời, sợ chết v&agrave; sợ h&atilde;i truyền c&aacute;c điều kiện về cho con c&aacute;i. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng ngạc nhi&ecirc;n, những người c&oacute; bệnh cơ tim ph&igrave; đại c&oacute; nguy cơ rối loạn sức khỏe t&acirc;m thần, bao gồm trầm cảm, lo lắng v&agrave; c&aacute;c vấn đề lạm dụng thuốc. N&oacute;i chuyện với b&aacute;c sĩ nếu cảm thấy tuyệt vọng, hoảng sợ hoặc kh&ocirc;ng thể đối ph&oacute;. Trong một số trường hợp, c&oacute; thể hưởng lợi từ điều trị y tế cho những điều kiện sức khỏe t&acirc;m thần. Trong trường hợp kh&aacute;c, sức khỏe t&acirc;m thần c&oacute; thể hưởng lợi nhiều nhất từ n&oacute;i chuyện với c&aacute;c chuy&ecirc;n gia y tế, chẳng hạn như nh&oacute;m chăm s&oacute;c của y tế hoặc nh&acirc;n vi&ecirc;n tư vấn di truyền, những người c&oacute; thể gi&uacute;p hiểu được những rủi ro v&agrave; t&igrave;m c&aacute;ch đối ph&oacute; c&oacute; hiệu quả. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Ph&ograve;ng chống</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bởi v&igrave; bệnh cơ tim ph&igrave; đại được kế thừa, n&oacute; kh&ocirc;ng thể được ngăn chặn. Tuy nhi&ecirc;n, c&aacute;c b&aacute;c sĩ v&agrave; c&aacute;c nh&agrave; khoa học đang học th&ecirc;m về c&aacute;c đột biến di truyền g&acirc;y ra c&aacute;c rối loạn. Mặc d&ugrave; điều kiện tự n&oacute; kh&ocirc;ng thể được ngăn chặn, điều quan trọng để x&aacute;c định t&igrave;nh trạng n&agrave;y c&agrave;ng sớm c&agrave;ng tốt để hướng dẫn điều trị v&agrave; ngăn ngừa biến chứng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ph&ograve;ng ngừa đột tử </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Việc sử dụng m&aacute;y khử rung tim cấy gh&eacute;p đ&atilde; được hiển thị để gi&uacute;p ngăn ngừa đột tử do tim, hiếm khi xuất hiện ở những người c&oacute; bệnh cơ tim ph&igrave; đại. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thật kh&ocirc;ng may, v&igrave; nhiều người bị bệnh cơ tim ph&igrave; đại kh&ocirc;ng nhận ra họ đ&atilde; c&oacute; n&oacute;, c&oacute; những trường hợp c&aacute;c dấu hiệu đầu ti&ecirc;n của một vấn đề l&agrave; tử vong đột ngột. Những trường hợp n&agrave;y c&oacute; thể xảy ra ở những người trẻ tuổi c&oacute; vẻ khỏe mạnh, bao gồm cả vận động vi&ecirc;n, người lớn kh&aacute;c đang hoạt động. Tin tức của c&aacute;c loại tử vong tạo ra sự ch&uacute; &yacute; dễ hiểu bởi v&igrave; ch&uacute;ng rất bất ngờ, nhưng cha mẹ n&ecirc;n biết những c&aacute;i chết n&agrave;y l&agrave; kh&ocirc;ng cao. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tuy nhi&ecirc;n, c&aacute;c chuy&ecirc;n gia tr&aacute;i tim bất thường thường khuy&ecirc;n những người c&oacute; bệnh cơ tim ph&igrave; đại kh&ocirc;ng tham gia trong hầu hết c&aacute;c m&ocirc;n thể thao cạnh tranh, ngoại trừ c&oacute; thể c&oacute; của một số m&ocirc;n thể thao cường độ thấp. N&ecirc;n n&oacute;i chuyện với b&aacute;c sĩ tim mạch<span style=\"\">&nbsp; </span>về c&aacute;c khuyến nghị cụ thể. Việc sử dụng m&aacute;y khử rung tim cấy gh&eacute;p kh&ocirc;ng n&ecirc;n được xem như l&agrave; một thay thế cho những khuyến nghị n&agrave;y. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: right;\"><b style=\"\"><i style=\"\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(128, 0, 0);\"></span></span></i></b></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">&nbsp;</span></p></span>\n', 2);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(23, 'Bệnh động mạch ngoại biên (PAD)', '\n   <h1 class=\"title\">Bệnh động mạch ngoại biên (PAD)</h1>   <span class=\"des\">Bệnh động mạch ngoại vi thường là do xơ vữa động mạch. Trong xơ vữa động mạch, mảng tích tụ trong thành động mạch và giảm lưu lượng máu.</span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh động mạch ngoại bi&ecirc;n l&agrave; một vấn đề tuần ho&agrave;n phổ biến trong đ&oacute; thu hẹp động mạch l&agrave;m giảm lưu lượng m&aacute;u đến tay ch&acirc;n. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi ph&aacute;t triển bệnh động mạch ngoại bi&ecirc;n (PAD), tứ chi - thường l&agrave; đ&ocirc;i ch&acirc;n, kh&ocirc;ng nhận được lưu lượng m&aacute;u đủ để theo kịp với nhu cầu. Điều n&agrave;y g&acirc;y ra c&aacute;c triệu chứng, đặc biệt l&agrave; ch&acirc;n đau khi đi bộ (claudication).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh động mạch ngoại bi&ecirc;n cũng c&oacute; khả năng l&agrave; dấu hiệu của một sự t&iacute;ch tụ hơn chất b&eacute;o trong động mạch (xơ vữa động mạch). T&igrave;nh trạng n&agrave;y c&oacute; thể l&agrave;m giảm lưu lượng m&aacute;u đến tim v&agrave; n&atilde;o, cũng như đ&ocirc;i ch&acirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Th&ocirc;ng thường, c&aacute;c th&agrave;nh c&ocirc;ng c&oacute; thể điều trị bệnh động mạch ngoại vi do bỏ thuốc l&aacute;, tập thể dục v&agrave; ăn một chế độ ăn uống khỏe mạnh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong khi nhiều người bị bệnh động mạch ngoại vi c&oacute; triệu chứng nhẹ hoặc kh&ocirc;ng c&oacute;, một số người bị đau ch&acirc;n khi đi bộ (claudication). </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Li&ecirc;n tục c&aacute;c triệu chứng bao gồm đau cơ hoặc chuột r&uacute;t ở ch&acirc;n hoặc c&aacute;nh tay k&iacute;ch hoạt bởi c&aacute;c hoạt động, như đi bộ, nhưng biến mất sau một v&agrave;i ph&uacute;t nghỉ ngơi. C&aacute;c vị tr&iacute; của cơn đau phụ thuộc v&agrave;o vị tr&iacute; của c&aacute;c động mạch bị tắc hay hẹp. Đau khối cơ chắc l&agrave; phổ biến nhất.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c mức độ nghi&ecirc;m trọng của claudication li&ecirc;n tục rất kh&aacute;c nhau, từ kh&oacute; chịu nhẹ đến đau suy nhược. Claudication nặng li&ecirc;n tục c&oacute; thể l&agrave;m cho kh&oacute; khăn cho c&aacute;ch đi bộ hoặc c&aacute;c loại hoạt động thể chất.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Triệu chứng bệnh động mạch ngoại bi&ecirc;n bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau chuột r&uacute;t ở đ&ugrave;i, h&ocirc;ng hoặc cơ bắp ch&acirc;n sau khi hoạt động, như đi bộ hoặc leo cầu thang (claudication).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ch&acirc;n bị t&ecirc; hoặc yếu.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Lạnh ở ch&acirc;n thấp hoặc b&agrave;n ch&acirc;n, đặc biệt l&agrave; khi so s&aacute;nh với ch&acirc;n kh&aacute;c.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau tr&ecirc;n ng&oacute;n ch&acirc;n, b&agrave;n ch&acirc;n hoặc vết thương ch&acirc;n kh&ocirc;ng sẽ chữa l&agrave;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sự thay đổi m&agrave;u sắc của ch&acirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Rụng t&oacute;c hoặc t&oacute;c tăng trưởng chậm hơn tr&ecirc;n đ&ocirc;i ch&acirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Chậm ph&aacute;t triển của m&oacute;ng ch&acirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Da s&aacute;ng b&oacute;ng tr&ecirc;n đ&ocirc;i ch&acirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng c&oacute; mạch hoặc mạch yếu ở ch&acirc;n hoặc b&agrave;n ch&acirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Rối loạn cương dương ở nam giới.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu bệnh động mạch ngoại vi tiến triển, đau đớn thậm ch&iacute; c&oacute; thể xảy ra khi đang nghỉ hoặc khi nằm xuống (thiếu m&aacute;u cục đau đớn khi nghỉ). N&oacute; c&oacute; thể đủ mạnh để ph&aacute; vỡ giấc ngủ. Treo ch&acirc;n tr&ecirc;n cạnh giường hoặc đi bộ quanh ph&ograve;ng c&oacute; thể tạm thời l&agrave;m giảm cơn đau.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Đến gặp b&aacute;c sĩ khi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; đau ch&acirc;n, t&ecirc; hay c&aacute;c triệu chứng kh&aacute;c, kh&ocirc;ng bỏ ch&uacute;ng như l&agrave; một phần b&igrave;nh thường của tuổi gi&agrave;. Gọi cho b&aacute;c sĩ v&agrave; l&agrave;m một cuộc hẹn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ngay cả khi kh&ocirc;ng c&oacute; triệu chứng của bệnh động mạch ngoại vi, c&oacute; thể cần phải được kiểm tra nếu l&agrave;: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tr&ecirc;n 70 tuổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tr&ecirc;n 50 tuổi v&agrave; c&oacute; tiền sử bệnh tiểu đường hoặc h&uacute;t thuốc l&aacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Dưới 50 tuổi nhưng bị bệnh tiểu đường v&agrave; c&aacute;c nguy cơ yếu tố bệnh động mạch kh&aacute;c, chẳng hạn như b&eacute;o ph&igrave; hoặc cao huyết &aacute;p.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh động mạch ngoại vi thường l&agrave; do xơ vữa động mạch. Trong xơ vữa động mạch, mảng t&iacute;ch tụ trong th&agrave;nh động mạch v&agrave; giảm lưu lượng m&aacute;u.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mặc d&ugrave; tim thường l&agrave; trọng t&acirc;m của cuộc thảo luận của xơ vữa động mạch, bệnh n&agrave;y c&oacute; thể v&agrave; thường kh&ocirc;ng ảnh hưởng đến động mạch khắp cơ thể. Khi n&oacute; xảy ra ở c&aacute;c động mạch cung cấp m&aacute;u cho tay ch&acirc;n, n&oacute; g&acirc;y ra bệnh động mạch ngoại vi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">&Iacute;t gặp hơn, nguy&ecirc;n nh&acirc;n của PAD c&oacute; thể l&agrave; vi&ecirc;m mạch m&aacute;u, tổn thương đến tay ch&acirc;n, giải phẫu học bất thường của d&acirc;y chằng hoặc cơ bắp, hoặc tiếp x&uacute;c với bức xạ. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những yếu tố l&agrave;m tăng nguy cơ ph&aacute;t triển bệnh động mạch ngoại vi bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&uacute;t thuốc.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh tiểu đường.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">B&eacute;o ph&igrave; (chỉ số khối cơ thể một trong 30).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Huyết &aacute;p cao (140/90 milim&eacute;t thuỷ ng&acirc;n hoặc cao hơn).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Cholesterol cao (tổng số cholesterol trong m&aacute;u hơn 240 mg / dL, hoặc 6,2 millimoles / l&iacute;t).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Lớn tuổi, đặc biệt l&agrave; sau khi đạt 50 tuổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Lịch sử gia đ&igrave;nh mắc bệnh động mạch ngoại bi&ecirc;n, bệnh tim, hay đột quỵ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Vượt qu&aacute; mức độ homocysteine, một th&agrave;nh phần protein gi&uacute;p x&acirc;y dựng v&agrave; duy tr&igrave; m&ocirc;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Những người h&uacute;t thuốc hoặc bị bệnh tiểu đường c&oacute; nguy cơ cao mắc bệnh động mạch ngoại vi do giảm lưu lượng m&aacute;u.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu bệnh động mạch ngoại vi l&agrave; do một sự t&iacute;ch tụ c&aacute;c mảng b&aacute;m trong mạch m&aacute;u (xơ vữa động mạch), cũng c&oacute; nguy cơ ph&aacute;t triển: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Chi thiếu m&aacute;u cục bộ.</span></b><span style=\"font-family: Arial;\"> T&igrave;nh trạng n&agrave;y bắt đầu như lở lo&eacute;t kh&ocirc;ng l&agrave;nh, một chấn thương hoặc nhiễm tr&ugrave;ng ch&acirc;n hay tay. Thiếu m&aacute;u cục bộ chi quan trọng (CLI) xảy ra khi bị thương hoặc tiến độ nhiễm tr&ugrave;ng v&agrave; c&oacute; thể g&acirc;y ra chết tế b&agrave;o (hoại tử), đ&ocirc;i khi đ&ograve;i hỏi phải cắt cụt c&aacute;c chi bị ảnh hưởng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đột quỵ v&agrave; đau tim.</span></b><span style=\"font-family: Arial;\"> Xơ vữa động mạch l&agrave; nguy&ecirc;n nh&acirc;n g&acirc;y c&aacute;c dấu hiệu v&agrave; triệu chứng của bệnh động mạch ngoại bi&ecirc;n l&agrave; kh&ocirc;ng giới hạn đếnch&acirc;n. Mảng chất b&eacute;o cũng tụ trong động mạch cung cấp cho tim v&agrave; n&atilde;o.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một số thử nghiệm, b&aacute;c sĩ c&oacute; thể dựa v&agrave;o để chẩn đo&aacute;n bệnh động mạch ngoại bi&ecirc;n l&agrave;: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Kh&aacute;m nghiệm.</span></b><span style=\"font-family: Arial;\"> B&aacute;c sĩ c&oacute; thể t&igrave;m thấy dấu hiệu của PAD trong một cuộc kiểm tra thể chất, chẳng hạn như vắng mặt hoặc mạch yếu dưới một khu vực hẹp của động mạch, &acirc;m thanh (bruits) trong động mạch c&oacute; thể được nghe bằng ống nghe, bằng chứng của vết thương k&eacute;m chữa l&agrave;nh trong khu vực, nơi lưu lượng m&aacute;u bị hạn chế, v&agrave; giảm &aacute;p lực m&aacute;u ở chi bị ảnh hưởng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Mắt c&aacute; ch&acirc;n, c&aacute;nh tay chỉ mục (ABI).</span></b><span style=\"font-family: Arial;\"> Đ&acirc;y l&agrave; một x&eacute;t nghiệm thường được sử dụng để chẩn đo&aacute;n PAD. N&oacute; so s&aacute;nh huyết &aacute;p ở mắt c&aacute; ch&acirc;n với những &aacute;p lực m&aacute;u ở c&aacute;nh tay. Để c&oacute; được huyết &aacute;p, b&aacute;c sĩ sử dụng một đai &aacute;p suất m&aacute;u thường xuy&ecirc;n v&agrave; một thiết bị si&ecirc;u &acirc;m đặc biệt để đ&aacute;nh gi&aacute; huyết &aacute;p v&agrave; d&ograve;ng chảy. C&oacute; thể đi bộ tr&ecirc;n m&aacute;y chạy bộ v&agrave; đọc trước v&agrave; ngay sau khi tập thể dục để nắm bắt được mức độ nghi&ecirc;m trọng của c&aacute;c động mạch bị thu hẹp trong thời gian đi bộ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Si&ecirc;u &acirc;m.</span></b><span style=\"font-family: Arial;\"> Đặc biệt si&ecirc;u &acirc;m, chẳng hạn như si&ecirc;u &acirc;m Doppler c&oacute; thể gi&uacute;p b&aacute;c sĩ đ&aacute;nh gi&aacute; lưu lượng m&aacute;u qua c&aacute;c mạch m&aacute;u v&agrave; x&aacute;c định thu hẹp c&aacute;c động mạch hoặc bị chặn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Chụp động mạch.</span></b><span style=\"font-family: Arial;\"> Bằng c&aacute;ch ti&ecirc;m một chất nhuộm m&agrave;u v&agrave;o mạch m&aacute;u, thử nghiệm n&agrave;y cho ph&eacute;p b&aacute;c sĩ xem lưu lượng m&aacute;u qua động mạch v&igrave; n&oacute; sẽ xảy ra. B&aacute;c sĩ c&oacute; thể theo d&otilde;i d&ograve;ng chảy của c&aacute;c vật liệu tương phản, sử dụng kỹ thuật h&igrave;nh ảnh như X-ray hoặc c&aacute;c thủ tục được gọi l&agrave; chụp cộng hưởng từ (MRA) hoặc chụp cắt lớp vi t&iacute;nh (CTA). Ống th&ocirc;ng chụp động mạch l&agrave; một thủ tục x&acirc;m lấn nhiều hơn, li&ecirc;n quan đến hướng dẫn một ống th&ocirc;ng qua động mạch ở h&aacute;ng đến khu vực bị ảnh hưởng v&agrave; ti&ecirc;m chất nhuộm m&agrave;u theo c&aacute;ch đ&oacute;. Mặc d&ugrave; x&acirc;m hại, loại h&igrave;nh n&agrave;y cho ph&eacute;p chụp động mạch đồng thời chẩn đo&aacute;n v&agrave; điều trị - việc t&igrave;m kiếm diện t&iacute;ch thu hẹp c&aacute;c mạch m&aacute;u v&agrave; sau đ&oacute; mở rộng n&oacute; với một thủ thuật nong mạch, quản l&yacute; thuốc để cải thiện lưu lượng m&aacute;u.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">X&eacute;t nghiệm m&aacute;u.</span></b><span style=\"font-family: Arial;\"> Một mẫu m&aacute;u c&oacute; thể được sử dụng để đo chất b&eacute;o trung t&iacute;nh, cholesterol v&agrave; để kiểm tra bệnh tiểu đường.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều trị bệnh động mạch ngoại vi c&oacute; hai mục ti&ecirc;u ch&iacute;nh. Đầu ti&ecirc;n l&agrave; để quản l&yacute; c&aacute;c triệu chứng, như đau ch&acirc;n, để c&oacute; thể tiếp tục c&aacute;c hoạt động thể chất. Thứ hai l&agrave; để ngăn chặn sự tiến triển của xơ vữa động mạch khắp cơ thể để giảm nguy cơ đau tim v&agrave; đột quỵ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; thể ho&agrave;n th&agrave;nh c&aacute;c mục ti&ecirc;u n&agrave;y với thay đổi lối sống. Nếu h&uacute;t thuốc, bỏ h&uacute;t thuốc l&agrave; điều quan trọng nhất c&oacute; thể l&agrave;m để giảm nguy cơ biến chứng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu thay đổi lối sống l&agrave; kh&ocirc;ng đủ, cần bổ sung điều trị y tế. B&aacute;c sĩ c&oacute; thể k&ecirc; toa thuốc để ngăn ngừa đ&ocirc;ng m&aacute;u, hạ huyết &aacute;p v&agrave; cholesterol, giảm đau v&agrave; c&aacute;c triệu chứng kh&aacute;c.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thuốc men </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc hạ cholesterol.</span></b><span style=\"font-family: Arial;\"> C&oacute; thể d&ugrave;ng thuốc hạ cholesterol được gọi l&agrave; statin để giảm yếu tố nguy cơ đau tim v&agrave; đột quỵ. Mục ti&ecirc;u cho những người c&oacute; bệnh động mạch ngoại bi&ecirc;n l&agrave; giảm mật độ lipoprotein thấp (LDL), cholesterol &quot;xấu&quot; &iacute;t hơn 100 mg / dL (mg / dL), hoặc 2,6 millimoles / l&iacute;t (mmol / L) . Mục đ&iacute;ch thậm ch&iacute; thấp hơn nếu c&oacute; yếu tố nguy cơ chủ yếu bổ sung cho c&aacute;c cơn đau tim v&agrave; đột quỵ, đặc biệt l&agrave; bệnh tiểu đường hoặc h&uacute;t thuốc l&aacute; vẫn tiếp tục.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc điều trị tăng huyết &aacute;p.</span></b><span style=\"font-family: Arial;\"> Nếu cũng c&oacute; huyết &aacute;p cao, b&aacute;c sĩ c&oacute; thể k&ecirc; toa thuốc để hạ thấp n&oacute;. Mục ti&ecirc;u của liệu ph&aacute;p n&agrave;y l&agrave; giảm huyết &aacute;p t&acirc;m thu tới 140 milim&eacute;t thuỷ ng&acirc;n (mm Hg) hoặc thấp hơn v&agrave; &aacute;p lực m&aacute;u t&acirc;m trương (số dưới) đến 90 mm Hg hoặc thấp hơn. Nếu bị tiểu đường, huyết &aacute;p mục ti&ecirc;u l&agrave; dưới 130/80 mm Hg.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc để kiểm so&aacute;t lượng đường trong m&aacute;u.</span></b><span style=\"font-family: Arial;\"> Nếu cũng bị tiểu đường, n&oacute; trở n&ecirc;n quan trọng hơn để kiểm so&aacute;t lượng đường trong m&aacute;u (glucose). N&oacute;i chuyện với b&aacute;c sĩ về những mục ti&ecirc;u lượng đường trong m&aacute;u v&agrave; những bước cần thực hiện để đạt được c&aacute;c mục ti&ecirc;u n&agrave;y.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">C&aacute;c loại thuốc để ngăn ngừa cục m&aacute;u đ&ocirc;ng.</span></b><span style=\"font-family: Arial;\"> Bởi v&igrave; bệnh động mạch ngoại vi c&oacute; li&ecirc;n quan đến lưu lượng m&aacute;u giảm xuống ch&acirc;n tay, điều quan trọng để giảm nguy cơ đ&ocirc;ng m&aacute;u. Một cục m&aacute;u đ&ocirc;ng ho&agrave;n to&agrave;n c&oacute; thể chặn nơi thu hẹp mạch m&aacute;u v&agrave; g&acirc;y ra c&aacute;i chết m&ocirc;. B&aacute;c sĩ c&oacute; thể k&ecirc; đơn điều trị bằng aspirin h&agrave;ng ng&agrave;y hoặc loại thuốc kh&aacute;c gi&uacute;p ngăn ngừa cục m&aacute;u đ&ocirc;ng, chẳng hạn như clopidogrel (Plavix).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc giảm triệu chứng.</span></b><span style=\"font-family: Arial;\"> C&aacute;c cilostazol (Pletal) tăng lưu lượng m&aacute;u đến ch&acirc;n tay, cả hai bằng c&aacute;ch ngăn ngừa cục m&aacute;u đ&ocirc;ng v&agrave; mở rộng c&aacute;c mạch m&aacute;u. N&oacute; đặc biệt gi&uacute;p c&aacute;c triệu chứng claudication, ch&acirc;n đau, cho những người c&oacute; bệnh động mạch ngoại vi. T&aacute;c dụng phụ thường gặp của thuốc n&agrave;y bao gồm đau đầu v&agrave; ti&ecirc;u chảy. Thay thế cho cilostazol l&agrave; pentoxifylline (Trental), tuy nhi&ecirc;n, thường &iacute;t hiệu quả. Nhưng t&aacute;c dụng phụ hiếm gặp với thuốc n&agrave;y.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nong mạch v&agrave; phẫu thuật </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong một số trường hợp, nong mạch hoặc phẫu thuật c&oacute; thể l&agrave; cần thiết để điều trị bệnh động mạch ngoại vi g&acirc;y claudication li&ecirc;n tục: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Nong mạch.</span></b><span style=\"font-family: Arial;\"> Trong phần n&agrave;y, một ống rỗng nhỏ (catheter) được luồn qua mạch m&aacute;u v&agrave;o động mạch bị ảnh hưởng. Ở đ&oacute;, một quả b&oacute;ng nhỏ tr&ecirc;n đầu của ống th&ocirc;ng sẽ tăng cao để mở lại c&aacute;c động mạch v&agrave; l&agrave;m phẳng tắc nghẽn v&agrave;o th&agrave;nh động mạch, trong khi c&ugrave;ng một thời gian k&eacute;o d&agrave;i cho động mạch mở để tăng lưu lượng m&aacute;u. B&aacute;c sĩ c&oacute; thể ch&egrave;n một khung lưới gọi l&agrave; stent v&agrave;o động mạch để giữ cho n&oacute; mở. Đ&acirc;y l&agrave; c&ugrave;ng một thủ tục sử dụng để mở c&aacute;c động mạch tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Phẫu thuật.</span></b><span style=\"font-family: Arial;\"> B&aacute;c sĩ c&oacute; thể tạo ra một v&ograve;ng cầu bằng c&aacute;ch sử dụng một mạch từ một phần kh&aacute;c của cơ thể hoặc một mạch m&aacute;u l&agrave;m bằng vải tổng hợp. Kỹ thuật n&agrave;y cho ph&eacute;p m&aacute;u chảy xung quanh - hoặc bỏ qua thu hẹp động mạch bị chặn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Điều trị tan huyết khối.</span></b><span style=\"font-family: Arial;\"> Nếu c&oacute; một cục m&aacute;u chặn động mạch, b&aacute;c sĩ c&oacute; thể ti&ecirc;m một loại thuốc h&ograve;a tan cục m&aacute;u đ&ocirc;ng trong động mạch tại c&aacute;c điểm của c&aacute;c cục m&aacute;u đ&ocirc;ng để ph&aacute; vỡ n&oacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Gi&aacute;m s&aacute;t chương tr&igrave;nh tập luyện</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ngo&agrave;i việc d&ugrave;ng thuốc hoặc phẫu thuật, b&aacute;c sĩ c&oacute; thể quy định một chương tr&igrave;nh tập thể dục đ&agrave;o tạo gi&aacute;m s&aacute;t để gia tăng khoảng c&aacute;ch c&oacute; thể đi bộ. Thường xuy&ecirc;n tập thể dục cải thiện c&aacute;c triệu chứng của PAD bằng một số phương ph&aacute;p, bao gồm việc gi&uacute;p đỡ sử dụng oxy cơ thể hiệu quả hơn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phong c&aacute;ch sống v&agrave; biện ph&aacute;p khắc phục</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhiều người c&oacute; thể quản l&yacute; c&aacute;c triệu chứng của bệnh động mạch ngoại vi v&agrave; ngăn chặn sự tiến triển của bệnh th&ocirc;ng qua việc thay đổi lối sống, đặc biệt l&agrave; bỏ h&uacute;t thuốc. Để ổn định hoặc cải thiện PAD:</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ngưng h&uacute;t thuốc l&aacute;.</span></b><span style=\"font-family: Arial;\"> H&uacute;t thuốc g&oacute;p phần thắt v&agrave; thiệt hại c&aacute;c động mạch v&agrave; l&agrave; một yếu tố nguy cơ quan trọng cho sự ph&aacute;t triển v&agrave; ng&agrave;y c&agrave;ng tồi tệ của PAD. Nếu h&uacute;t thuốc, bỏ h&uacute;t thuốc l&agrave; điều quan trọng nhất c&oacute; thể l&agrave;m để giảm nguy cơ biến chứng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tập thể dục.</span></b><span style=\"font-family: Arial;\"> Đ&acirc;y l&agrave; một th&agrave;nh phần quan trọng. Th&agrave;nh c&ocirc;ng trong điều trị của PAD thường được đo lường bằng c&aacute;ch xa c&oacute; thể đi bộ m&agrave; kh&ocirc;ng đau đớn. Th&iacute;ch hợp của tập thể dục gi&uacute;p cơ bắp sử dụng oxy hiệu quả hơn. B&aacute;c sĩ c&oacute; thể gi&uacute;p ph&aacute;t triển một kế hoạch tập thể dục th&iacute;ch hợp. C&oacute; thể giới thiệu qu&yacute; vị thực hiện chương tr&igrave;nh phục hồi claudication.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ăn một chế độ ăn uống khỏe mạnh.</span></b><span style=\"font-family: Arial;\"> Một chế độ ăn uống cho sức khỏe tim &iacute;t chất b&eacute;o b&atilde;o h&ograve;a c&oacute; thể gi&uacute;p kiểm so&aacute;t huyết &aacute;p v&agrave; mức cholesterol, g&oacute;p phần xơ vữa động mạch. Chế độ ăn uống gi&agrave;u chất dinh dưỡng - như vitamin A, B-6, C v&agrave; E, folate, chất xơ; v&agrave; axit b&eacute;o omega 3 - li&ecirc;n kết với một tỷ lệ thấp hơn của bệnh động mạch ngoại vi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Một số thuốc tr&aacute;nh lạnh.</span></b><span style=\"font-family: Arial;\"> Biện ph&aacute;p khắc phục lạnh c&oacute; chứa pseudoephedrin (Advil lạnh v&agrave; Sinus, Aleve, Claritin-D, Sudafed, Tylenol lạnh, Zyrtec-D, những loại kh&aacute;c) teo mạch m&aacute;u v&agrave; c&oacute; thể l&agrave;m tăng triệu chứng PAD.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Cẩn thận chăm s&oacute;c b&agrave;n ch&acirc;n </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ngo&agrave;i c&aacute;c đề xuất tr&ecirc;n, chăm s&oacute;c tốt cho đ&ocirc;i ch&acirc;n. Những người bị bệnh động mạch ngoại vi, đặc biệt l&agrave; những người cũng bị bệnh tiểu đường, c&oacute; nguy cơ bị c&aacute;c vết lo&eacute;t tr&ecirc;n cẳng ch&acirc;n v&agrave; b&agrave;n ch&acirc;n k&eacute;m l&agrave;nh. Ngh&egrave;o m&aacute;u lưu th&ocirc;ng c&oacute; thể tr&igrave; ho&atilde;n hoặc ngăn ngừa chữa bệnh ph&ugrave; hợp v&agrave; tăng nguy cơ nhiễm tr&ugrave;ng. Thực hiện theo lời khuy&ecirc;n n&agrave;y để chăm s&oacute;c cho đ&ocirc;i ch&acirc;n:</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Rửa ch&acirc;n h&agrave;ng ng&agrave;y, lau kh&ocirc; kỹ lưỡng v&agrave; dưỡng ẩm thường xuy&ecirc;n để ngăn chặn vết nứt c&oacute; thể dẫn đến nhiễm tr&ugrave;ng. Kh&ocirc;ng dưỡng ẩm giữa c&aacute;c ng&oacute;n ch&acirc;n, tuy nhi&ecirc;n, v&igrave; điều n&agrave;y c&oacute; thể khuyến kh&iacute;ch tăng trưởng nấm.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mang gi&agrave;y vừa vặn v&agrave; tất d&agrave;y kh&ocirc;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kịp thời điều trị bất kỳ bệnh nhiễm nấm b&agrave;n ch&acirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&atilde;y cẩn thận khi cắt tỉa m&oacute;ng tay.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tr&aacute;nh đi ch&acirc;n trần.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; một b&aacute;c sĩ (podiatrist) điều trị bunions, ng&ocirc; hoặc vết chai.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đi kh&aacute;m b&aacute;c sĩ khi c&aacute;c dấu hiệu đầu ti&ecirc;n của một cơn đau hoặc tổn thương da.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Thay thế thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c t&aacute;c dụng l&agrave;m lo&atilde;ng m&aacute;u của bạch quả c&oacute; thể cho ph&eacute;p những người c&oacute; claudication li&ecirc;n tục đi bộ khoảng c&aacute;ch xa với nỗi đau &iacute;t hơn. Tuy nhi&ecirc;n, thảo dược n&agrave;y c&oacute; thể g&acirc;y chảy m&aacute;u khi d&ugrave;ng liều cao, v&agrave; n&oacute; c&oacute; thể nguy hiểm nếu kết hợp với thuốc chống tiểu cầu, bao gồm aspirin, thường quy định với PAD. N&oacute;i chuyện với b&aacute;c sĩ trước khi xem x&eacute;t việc d&ugrave;ng bạch quả để giảm đau ch&acirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Đối ph&oacute; v&agrave; hỗ trợ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh động mạch ngoại bi&ecirc;n c&oacute; thể rất bực bội, đặc biệt l&agrave; khi tập thể dục sẽ c&oacute; đau đớn. Đừng nản l&ograve;ng, tuy nhi&ecirc;n. Khi tiếp tục tập thể dục, sẽ gia tăng khoảng c&aacute;ch c&oacute; thể đi bộ m&agrave; kh&ocirc;ng đau đớn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; thể t&igrave;m thấy hữu &iacute;ch để n&acirc;ng cao đầu giường bằng c&aacute;ch 10 - 15 cm, bởi v&igrave; việc giữ ch&acirc;n dưới mức của tim thường l&agrave;m giảm đau. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một mẹo để giảm triệu chứng l&agrave; để tr&aacute;nh nhiệt độ lạnh c&agrave;ng nhiều c&agrave;ng tốt. Nếu kh&ocirc;ng thể tr&aacute;nh được lạnh, h&atilde;y mặc quần &aacute;o ấm.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một số người thấy n&oacute; hữu &iacute;ch để n&oacute;i chuyện với những người kh&aacute;c đang trong t&igrave;nh cảnh tương tự. C&aacute;c nh&oacute;m hỗ trợ c&oacute; thể cung cấp động vi&ecirc;n, tư vấn v&agrave; hiểu biết. Hội Tim mạch cung cấp diễn đ&agrave;n trực tuyến cho với PAD. cũng c&oacute; thể hỏi b&aacute;c sĩ nếu người đ&oacute; biết bất kỳ nh&oacute;m hỗ trợ trong khu vực.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Ph&ograve;ng chống</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;ch tốt nhất để ngăn chặn claudication l&agrave; duy tr&igrave; một lối sống l&agrave;nh mạnh. Điều đ&oacute; c&oacute; nghĩa l&agrave;:</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bỏ h&uacute;t thuốc l&aacute; nếu l&agrave; một người h&uacute;t thuốc.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu bị tiểu đường, giữ đường huyết trong kiểm so&aacute;t tốt.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tập thể dục thường xuy&ecirc;n. Mục ti&ecirc;u trong 30 ph&uacute;t &iacute;t nhất ba lần một tuần sau khi đ&atilde; nhận được b&aacute;c sĩ OK.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hạ cholesterol v&agrave; mức huyết &aacute;p, nếu cần thiết.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ăn thức ăn c&oacute; &iacute;t chất b&eacute;o b&atilde;o h&ograve;a.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Duy tr&igrave; một trọng lượng khỏe mạnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: right;\"><b style=\"\"><i style=\"\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(128, 0, 0);\"></span></span></i></b></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">&nbsp;</span></p></span>\n', 2);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(24, 'Bệnh động mạch vành', '\n   <h1 class=\"title\">Bệnh động mạch vành</h1>   <span class=\"des\">Bệnh động mạch vành! Đau ngực, khó thở, đau tim...Ngay lập tức gọi cấp cứu. Nếu tăng huyết áp, cholesterol máu cao, tiểu đường hay béo phì...có những yếu tố nguy cơ bệnh động mạch vành này, hãy gặp bác sỹ...</span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh động mạch v&agrave;nh ph&aacute;t triển khi động mạch v&agrave;nh - c&aacute;c mạch m&aacute;u lớn l&agrave; nguồn cung cấp m&aacute;u cho tim, &ocirc;xy v&agrave; chất dinh dưỡng trở n&ecirc;n hư hỏng hoặc bị bệnh. Mảng b&aacute;m c&oacute; chứa cholesterol v&agrave;o động mạch thường bị đổ lỗi l&agrave; nguy&ecirc;n nh&acirc;n g&acirc;y bệnh mạch v&agrave;nh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi mảng b&aacute;m ph&aacute;t triển n&oacute; thu hẹp động mạch v&agrave;nh, tim nhận m&aacute;u &iacute;t hơn. Cuối c&ugrave;ng, giảm lưu lượng m&aacute;u c&oacute; thể g&acirc;y đau ngực (đau thắt ngực), kh&oacute; thở hoặc c&aacute;c triệu chứng bệnh động mạch v&agrave;nh kh&aacute;c. Một tắc nghẽn ho&agrave;n to&agrave;n c&oacute; thể g&acirc;y ra một cơn đau tim. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bởi v&igrave; bệnh động mạch v&agrave;nh thường ph&aacute;t triển qua nhiều thập kỷ, n&oacute; c&oacute; thể hầu như kh&ocirc;ng được ch&uacute; &yacute; cho đến khi n&oacute; tạo ra một cơn đau tim. Nhưng c&oacute; rất nhiều việc c&oacute; thể l&agrave;m để ngăn ngừa v&agrave; điều trị bệnh ��ộng mạch v&agrave;nh. Bắt đầu bằng việc cam kết một lối sống l&agrave;nh mạnh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu động mạch v&agrave;nh trở n&ecirc;n thu hẹp, n&oacute; kh&ocirc;ng thể cung cấp đủ oxy, m&aacute;u tới tim - đặc biệt khi gắng sức, chẳng hạn như trong hoạt động thể chất. L&uacute;c đầu, d&ograve;ng m&aacute;u bị hạn chế c&oacute; thể kh&ocirc;ng g&acirc;y ra bất kỳ triệu chứng bệnh động mạch v&agrave;nh. Tuy nhi&ecirc;n, khi mảng b&aacute;m tiếp tục t&iacute;ch tụ trong động mạch v&agrave;nh, c&oacute; thể ph&aacute;t triển triệu chứng bệnh động mạch v&agrave;nh, bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đau ngực (đau thắt ngực).</span></b><span style=\"font-family: Arial;\"> C&oacute; thể cảm thấy &aacute;p lực hoặc tức ngực như c&oacute; ai đ&oacute; đứng tr&ecirc;n ngực. Những đau đớn, gọi l&agrave; đau thắt ngực thường được k&iacute;ch hoạt bởi căng thẳng về thể chất hoặc cảm x&uacute;c. N&oacute; thường biến mất trong v&ograve;ng v&agrave;i ph&uacute;t sau khi ngừng hoạt động căng thẳng. Ở một số người, đặc biệt l&agrave; phụ nữ, đau đớn n&agrave;y c&oacute; thể tho&aacute;ng qua hoặc sắc n&eacute;t v&agrave; nhận thấy ở v&ugrave;ng bụng, lưng hoặc c&aacute;nh tay.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Kh&oacute; thở.</span></b><span style=\"font-family: Arial;\"> Nếu tim kh&ocirc;ng thể bơm đủ m&aacute;u để đ&aacute;p ứng nhu cầu cơ thể, c&oacute; thể ph&aacute;t triển kh&oacute; thở hoặc mệt mỏi c&ugrave;ng cực với gắng sức.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đau tim.</span></b><span style=\"font-family: Arial;\"> Nếu một động mạch v&agrave;nh trở n&ecirc;n ho&agrave;n to&agrave;n bị chặn, c&oacute; thể c&oacute; một cơn đau tim. C&aacute;c dấu hiệu v&agrave; triệu chứng kinh điển của một cơn đau tim bao gồm &aacute;p lực nghiền v&agrave;o ngực v&agrave; đau lan vai hoặc tay, đ&ocirc;i khi kh&oacute; thở v&agrave; ra mồ h&ocirc;i. Phụ nữ c&oacute; hơi nhiều khả năng hơn nam giới l&agrave; trải nghiệm &iacute;t dấu hiệu điển h&igrave;nh v&agrave; triệu chứng của một cơn đau tim, bao gồm buồn n&ocirc;n v&agrave; đau lưng hay quai h&agrave;m. Đ&ocirc;i khi một cơn đau tim xảy ra m&agrave; kh&ocirc;ng c&oacute; bất kỳ dấu hiệu r&otilde; r&agrave;ng hoặc c&aacute;c triệu chứng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu nghi ngờ đang bị đau tim, ngay lập tức gọi cấp cứu khẩn cấp địa phương. Nếu kh&ocirc;ng, c&oacute; ai đ&oacute; đưa tới bệnh viện gần nhất. Tự m&igrave;nh l&aacute;i xe chỉ như l&agrave; một phương s&aacute;ch cuối c&ugrave;ng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; yếu tố nguy cơ bệnh động mạch v&agrave;nh, chẳng hạn như huyết &aacute;p cao, cholesterol cao, tiểu đường hay b&eacute;o ph&igrave;, h&atilde;y n&oacute;i chuyện với b&aacute;c sĩ. B&aacute;c sỹ c&oacute; thể muốn thử nghiệm cho t&igrave;nh trạng n&agrave;y, đặc biệt l&agrave; nếu c&oacute; dấu hiệu hoặc triệu chứng của động mạch bị hẹp. Thậm ch&iacute; nếu kh&ocirc;ng c&oacute; bằng chứng của bệnh động mạch v&agrave;nh, b&aacute;c sĩ c&oacute; thể khuy&ecirc;n n&ecirc;n điều trị t&iacute;ch cực của c&aacute;c yếu tố nguy cơ. Chẩn đo&aacute;n v&agrave; điều trị c&oacute; thể ngăn chặn tiến triển của bệnh động mạch v&agrave;nh v&agrave; gi&uacute;p ngăn ngừa một cơn đau tim. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh động mạch v&agrave;nh được cho l&agrave; bắt đầu với những thiệt hại hoặc tổn thương lớp b&ecirc;n trong của một động mạch v&agrave;nh, đ&ocirc;i khi ngay từ thời thơ ấu. Thiệt hại c&oacute; thể được g&acirc;y ra bởi nhiều nh&acirc;n tố, bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&uacute;t thuốc l&aacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Cao huyết &aacute;p.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Cholesterol cao.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tiểu đường.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bức xạ trị liệu đến ngực, như được sử dụng cho một số loại ung thư.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một khi c&aacute;c th&agrave;nh b&ecirc;n trong của động mạch bị hư hỏng, mảng b&aacute;m l&agrave;m bằng chất thải cholesterol v&agrave; c&aacute;c sản phẩm di động kh&aacute;c c&oacute; xu hướng t&iacute;ch tụ tại điểm thương t&iacute;ch, qu&aacute; tr&igrave;nh gọi l&agrave; xơ vữa động mạch. Nếu bề mặt của c&aacute;c mảng b&aacute;m vỡ, c&aacute;c tế b&agrave;o m&aacute;u gọi l&agrave; tiểu cầu sẽ ngưng tụ h&igrave;nh th&agrave;nh cục m&aacute;u đ&ocirc;ng tại đ&oacute; để cố gắng sửa chữa c&aacute;c động mạch. Khối m&aacute;u n&agrave;y c&oacute; thể chặn c&aacute;c động mạch, dẫn đến một cơn đau tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c yếu tố rủi ro cho bệnh mạch v&agrave;nh bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tuổi.</span></b><span style=\"font-family: Arial;\"> Tuổi c&agrave;ng cao c&agrave;ng tăng nguy cơ bị hư hỏng v&agrave; thu hẹp động mạch.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Giới.</span></b><span style=\"font-family: Arial;\"> Đ&agrave;n &ocirc;ng n&oacute;i chung l&agrave; c&oacute; nguy cơ bệnh mạch v&agrave;nh. Tuy nhi&ecirc;n, nguy cơ cho phụ nữ tăng l&ecirc;n sau khi m&atilde;n kinh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Lịch sử gia đ&igrave;nh.</span></b><span style=\"font-family: Arial;\"> Một sử gia đ&igrave;nh mắc bệnh tim l&agrave; li&ecirc;n kết với một nguy cơ cao của bệnh động mạch v&agrave;nh, đặc biệt l&agrave; nếu một người th&acirc;n ph&aacute;t triển bệnh tim ở tuổi trẻ. Nguy cơ<span style=\"\">&nbsp; </span>l&agrave; cao nhất nếu cha hoặc anh em đ&atilde; được chẩn đo&aacute;n bị bệnh tim trước tuổi 55, hoặc mẹ hoặc em g&aacute;i ph&aacute;t triển n&oacute; trước khi 65 tuổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">H&uacute;t thuốc l&aacute;.</span></b><span style=\"font-family: Arial;\"> Nicotin co mạch m&aacute;u, v&agrave; kh&iacute; carbon monoxide c&oacute; thể l&agrave;m hỏng lớp l&oacute;t b&ecirc;n trong, l&agrave;m cho ch&uacute;ng dễ bị xơ vữa động mạch. Tỷ lệ mắc bệnh tim ở những phụ nữ h&uacute;t thuốc &iacute;t nhất 20 điếu thuốc mỗi ng&agrave;y l&agrave; s&aacute;u lần so với những phụ nữ kh&ocirc;ng bao giờ h&uacute;t thuốc. Đối với những người đ&agrave;n &ocirc;ng h&uacute;t thuốc l&aacute;, tỷ lệ n&agrave;y l&agrave; ba của người kh&ocirc;ng h&uacute;t thuốc.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Cao huyết &aacute;p.</span></b><span style=\"font-family: Arial;\"> Kh&ocirc;ng kiểm so&aacute;t được huyết &aacute;p cao c&oacute; thể dẫn đến xơ cứng v&agrave; d&agrave;y động mạch, thu hẹp mạch m&aacute;u.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Cholesterol trong m&aacute;u cao.</span></b><span style=\"font-family: Arial;\"> Nồng độ cao của cholesterol trong m&aacute;u c&oacute; thể l&agrave;m tăng nguy cơ h&igrave;nh th&agrave;nh c&aacute;c mảng b&aacute;m v&agrave; xơ vữa động mạch. Cholesterol cao c&oacute; thể được g&acirc;y ra bởi một cấp độ cao lipoprotein mật độ thấp (LDL), được gọi l&agrave; cholesterol &quot;xấu&quot;. Mức thấp của lipoprotein mật độ cao (HDL), được gọi l&agrave; &quot; cholesterol &quot;tốt, cũng c&oacute; thể th&uacute;c đẩy xơ vữa động mạch.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Bệnh tiểu đường.</span></b><span style=\"font-family: Arial;\"> Tiểu đường l&agrave; li&ecirc;n kết với tăng nguy cơ bệnh mạch v&agrave;nh. Cả hai điều kiện chia sẻ c&aacute;c yếu tố nguy cơ tương tự, chẳng hạn như b&eacute;o ph&igrave; v&agrave; huyết &aacute;p cao.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Bệnh b&eacute;o ph&igrave;.</span></b><span style=\"font-family: Arial;\"> Vượt qu&aacute; trọng lượng thường nặng hơn yếu tố nguy cơ kh&aacute;c.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Kh&ocirc;ng hoạt động.</span></b><span style=\"font-family: Arial;\"> Thiếu tập thể dục cũng c&oacute; li&ecirc;n quan với bệnh động mạch v&agrave;nh v&agrave; một số yếu tố nguy cơ của n&oacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Căng thẳng.</span></b><span style=\"font-family: Arial;\"> Căng thẳng trong cuộc sống c&oacute; thể thiệt hại động mạch cũng như xấu đi yếu tố nguy cơ kh&aacute;c đối với bệnh mạch v&agrave;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c yếu tố rủi ro thường xảy ra trong li&ecirc;n kết v&agrave; c&oacute; thể x&acirc;y dựng tr&ecirc;n nhau, chẳng hạn như b&eacute;o ph&igrave; dẫn đến bệnh tiểu đường v&agrave; cao huyết &aacute;p. Khi nh&oacute;m lại với nhau, c&aacute;c yếu tố nguy cơ nhất định đặt ở một nguy cơ to lớn hơn nữa của bệnh động mạch v&agrave;nh. V&iacute; dụ, hội chứng chuyển ho&aacute;, một nh&oacute;m c&aacute;c điều kiện đ&oacute; bao gồm huyết &aacute;p cao, chất b&eacute;o trung t&iacute;nh cao, mức insulin cao v&agrave; cơ thể dư thừa chất b&eacute;o xung quanh thắt lưng l&agrave;m tăng nguy cơ bệnh mạch v&agrave;nh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đ&ocirc;i khi bệnh mạch v&agrave;nh ph&aacute;t triển m&agrave; kh&ocirc;ng c&oacute; bất kỳ yếu tố nguy cơ cổ điển. C&aacute;c nh&agrave; nghi&ecirc;n cứu đang nghi&ecirc;n cứu c&aacute;c yếu tố kh&aacute;c c&oacute; thể, bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ngưng thở khi ngủ.<i style=\"\"> </i></span></b><span style=\"font-family: Arial;\">Rối loạn n&agrave;y l&agrave;m cho nhiều lần dừng lại v&agrave; bắt đầu thở trong khi đang ngủ. Đột ngột mức oxy m&aacute;u giảm xuống xảy ra trong thời gian ngưng thở khi ngủ l&agrave;m &aacute;p lực m&aacute;u tăng v&agrave; căng thẳng hệ tim mạch, c&oacute; thể dẫn đến bệnh mạch v&agrave;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">C - reactive protein.</span></b><span style=\"font-family: Arial;\"> C - reactive protein (CRP) l&agrave; một protein b&igrave;nh thường xuất hiện cao hơn khi c&oacute; sưng một nơi n&agrave;o đ&oacute; trong cơ thể. CRP cao c&oacute; thể l&agrave; một yếu tố nguy cơ bệnh tim. Nghĩ rằng, cũng như động mạch v&agrave;nh hẹp, sẽ c&oacute; nhiều CRP trong m&aacute;u.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Homocysteine.</span></b><span style=\"font-family: Arial;\"> Homocysteine l&agrave; một axit amin cơ thể sử dụng để l&agrave;m cho protein v&agrave; để x&acirc;y dựng v&agrave; duy tr&igrave; m&ocirc;. Nhưng qu&aacute; mức homocysteine c&oacute; thể l&agrave;m tăng nguy cơ bệnh mạch v&agrave;nh v&agrave; tim mạch, CRP v&agrave; c&aacute;c điều kiện kh&aacute;c.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Fibrinogen.</span></b><span style=\"font-family: Arial;\"> L&agrave; một protein trong m&aacute;u đ&oacute;ng một vai tr&ograve; trung t&acirc;m trong đ&ocirc;ng m&aacute;u. Nhưng qu&aacute; nhiều c&oacute; thể l&agrave;m tăng kết d&iacute;nh của tiểu cầu, c&aacute;c loại tế b&agrave;o m&aacute;u chủ yếu chịu tr&aacute;ch nhiệm về đ&ocirc;ng m&aacute;u. Điều đ&oacute; c&oacute; thể g&acirc;y ra một cục m&aacute;u đ&ocirc;ng h&igrave;nh th&agrave;nh trong động mạch, dẫn đến một cơn đau tim hoặc đột quỵ. Fibrinogen cũng c&oacute; thể l&agrave; một chỉ b&aacute;o của vi&ecirc;m đi k&egrave;m với xơ vữa động mạch.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Lipoprotein (a).</span></b><span style=\"font-family: Arial;\"> Chất n&agrave;y h&igrave;nh th&agrave;nh khi một lipoprotein mật độ thấp (LDL) gắn v&agrave;o một protein cụ thể. Lipoprotein (a) c&oacute; thể l&agrave;m gi&aacute;n đoạn khả năng của cơ thể để h&ograve;a tan cục m&aacute;u đ&ocirc;ng. Lipoprotein (a) cao c&oacute; thể được li&ecirc;n kết với tăng nguy cơ bệnh tim mạch, bao gồm bệnh động mạch v&agrave;nh v&agrave; đau tim. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh động mạch v&agrave;nh c&oacute; thể dẫn đến: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đau ngực (đau thắt ngực).</span></b><span style=\"font-family: Arial;\"> Khi thu hẹp động mạch v&agrave;nh, tim c&oacute; thể kh&ocirc;ng nhận đủ m&aacute;u khi nhu cầu l&agrave; lớn nhất, đặc biệt l&agrave; trong hoạt động thể chất. Điều n&agrave;y c&oacute; thể g&acirc;y ra đau ngực (đau thắt ngực) hoặc kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đau tim.</span></b><span style=\"font-family: Arial;\"> Nếu vỡ mảng b&aacute;m cholesterol v&agrave; một h&igrave;nh thức đ&ocirc;ng m&aacute;u tắc nghẽn ho&agrave;n to&agrave;n động mạch tim c&oacute; thể k&iacute;ch hoạt một cơn đau tim. Việc thiếu lưu lượng m&aacute;u tới tim<span style=\"\">&nbsp; </span>c&oacute; thể thiệt hại cho cơ tim. Số thiệt hại phụ thuộc một phần v&agrave;o nhận được điều trị nhanh như thế n&agrave;o.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Suy tim.</span></b><span style=\"font-family: Arial;\"> Nếu một số khu vực của tim kinh ni&ecirc;n bị tước đoạt &ocirc;xy v&agrave; chất dinh dưỡng v&igrave; lưu lượng m&aacute;u giảm, hoặc nếu tr&aacute;i tim đ&atilde; bị hư hại bởi một cơn đau tim, tim c&oacute; thể trở n&ecirc;n qu&aacute; yếu để bơm đủ m&aacute;u để đ&aacute;p ứng nhu cầu cơ thể. T&igrave;nh trạng n&agrave;y được gọi l&agrave; suy tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Nhịp tim bất thường (loạn nhịp).</span></b><span style=\"font-family: Arial;\"> Thiếu m&aacute;u cung cấp cho tim hoặc thiệt hại cho c&aacute;c m&ocirc; tim c&oacute; thể can thiệp với xung điện tim, g&acirc;y nhịp tim bất thường.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">B&aacute;c sĩ sẽ đặt c&acirc;u hỏi về bệnh sử, l&agrave;m một b&agrave;i kiểm tra thể lực v&agrave; x&eacute;t nghiệm m&aacute;u th&ocirc;ng thường. C&oacute; thể đề xuất một hoặc nhiều x&eacute;t nghiệm chẩn đo&aacute;n, bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Điện t&acirc;m đồ (ECG).</span></b><span style=\"font-family: Arial;\"> Một bản ghi điện t&acirc;m đồ t&iacute;n hiệu điện khi ch&uacute;ng đi qua tr&aacute;i tim. ECG thường c&oacute; thể tiết lộ bằng chứng của một cơn đau tim trước đ&oacute; hoặc trong tiến tr&igrave;nh. Trong trường hợp kh&aacute;c, theo d&otilde;i Holter c&oacute; thể được khuyến kh&iacute;ch.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Si&ecirc;u &acirc;m tim.</span></b><span style=\"font-family: Arial;\"> Si&ecirc;u &acirc;m tim sử dụng s&oacute;ng &acirc;m để tạo ra h&igrave;nh ảnh của tr&aacute;i tim. Trong si&ecirc;u &acirc;m tim, b&aacute;c sĩ c&oacute; thể x&aacute;c định xem liệu tất cả c&aacute;c phần của bức th&agrave;nh tim đang đ&oacute;ng g&oacute;p cho hoạt động bơm b&igrave;nh thường?. C&aacute;c bộ phận chuyển động yếu ớt c&oacute; thể đ&atilde; bị hư hại trong một cơn đau tim hoặc nhận được qu&aacute; &iacute;t oxy. Điều n&agrave;y c&oacute; thể chỉ ra c&aacute;c bệnh động mạch v&agrave;nh hay c&aacute;c điều kiện kh&aacute;c nhau.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Stress thử nghiệm.</span></b><span style=\"font-family: Arial;\"> Nếu c&aacute;c dấu hiệu v&agrave; triệu chứng xảy ra thường xuy&ecirc;n nhất trong thời gian tập thể dục, b&aacute;c sĩ c&oacute; thể y&ecirc;u cầu đi bộ tr&ecirc;n m&aacute;y chạy bộ hay đi xe đạp văn ph&ograve;ng với một ECG. Điều n&agrave;y được biết đến như một thử nghiệm gắng sức tập thể dục. Trong một số trường hợp, thuốc k&iacute;ch th&iacute;ch tim c&oacute; thể được sử dụng thay v&igrave; tập thể dục. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một số x&eacute;t nghiệm stress được thực hiện bằng c&aacute;ch sử dụng si&ecirc;u &acirc;m tim. V&iacute; dụ, b&aacute;c sĩ c&oacute; thể l&agrave;m một si&ecirc;u &acirc;m trước v&agrave; sau khi tập thể dục tr&ecirc;n một guồng quay hay xe đạp. Hoặc b&aacute;c sĩ c&oacute; thể sử dụng thuốc để k&iacute;ch th&iacute;ch tim trong si&ecirc;u &acirc;m tim. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một thử nghiệm gắng sức được biết đến như một b&agrave;i kiểm tra hạt nh&acirc;n. N&oacute; tương tự như một b&agrave;i kiểm tra gắng sức, nhưng với h&igrave;nh ảnh, th&ecirc;m v&agrave;o một điện t&acirc;m đồ. Chất ph&oacute;ng xạ, như tali hoặc một hợp chất được biết đến như sestamibi được ti&ecirc;m v&agrave;o mạch m&aacute;u. M&aacute;y ảnh đặc biệt c&oacute; thể ph&aacute;t hiện c&aacute;c khu vực trong tim nhận được lưu lượng m&aacute;u &iacute;t hơn. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đặt ống th&ocirc;ng mạch v&agrave;nh.</span></b><span style=\"font-family: Arial;\"> Để xem lưu lượng m&aacute;u qua tim, b&aacute;c sĩ c&oacute; thể ti&ecirc;m một chất nhuộm m&agrave;u đặc biệt v&agrave;o c&aacute;c động mạch (tĩnh mạch). Điều n&agrave;y được biết đến như một angiogram. Thuốc nhuộm được ti&ecirc;m v&agrave;o c&aacute;c động mạch của tim th&ocirc;ng qua một ống th&ocirc;ng c&oacute; nghĩa l&agrave; luồng th&ocirc;ng qua động mạch, thường l&agrave; ở ch&acirc;n đến c&aacute;c động mạch trong tim. Thủ tục n&agrave;y được gọi l&agrave; đặt ống th&ocirc;ng tim. Thuốc nhuộm ph&aacute;c thảo c&aacute;c điểm thu hẹp v&agrave; tắc nghẽn tr&ecirc;n c&aacute;c h&igrave;nh ảnh X - quang. Nếu c&oacute; một tắc nghẽn m&agrave; y&ecirc;u cầu điều trị, kh&iacute; cầu c&oacute; thể được đẩy qua ống th&ocirc;ng v&agrave; bơm căng để cải thiện lưu lượng m&aacute;u ở động mạch v&agrave;nh. Một ống lưới (stent) sau đ&oacute; c&oacute; thể được sử dụng để giữ cho c&aacute;c động mạch gi&atilde;n mở ra.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">CT scan.</span></b><span style=\"font-family: Arial;\"> Chụp cắt lớp vi t&iacute;nh (CT), chẳng hạn như chụp cắt lớp vi t&iacute;nh ch&ugrave;m electron (EBCT) hoặc một angiogram CT mạch v&agrave;nh c&oacute; thể gi&uacute;p b&aacute;c sĩ h&igrave;nh dung c&aacute;c động mạch. EBCT, cũng gọi l&agrave; CT scan cực nhanh c&oacute; thể ph&aacute;t hiện canxi trong mảng b&aacute;m l&agrave;m hẹp động mạch v&agrave;nh. Nếu một số lượng đ&aacute;ng kể canxi được ph&aacute;t hiện, bệnh động mạch v&agrave;nh c&oacute; thể l&agrave; khả năng. Một   CT mạch v&agrave;nh angiogram, trong đ&oacute; nhận được một chất nhuộm m&agrave;u tương phản ti&ecirc;m tĩnh mạch trong một m&aacute;y qu&eacute;t CT, cũng c&oacute; thể tạo ra h&igrave;nh ảnh của động mạch tim .</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Angiogram cộng hưởng từ (MRA).</span></b><span style=\"font-family: Arial;\"> Thủ tục n&agrave;y sử dụng c&ocirc;ng nghệ MRI, thường kết hợp với một chất nhuộm m&agrave;u tương phản ti&ecirc;m để kiểm tra c&aacute;c khu vực thu hẹp hoặc bị tắc, mặc d&ugrave; c&aacute;c chi tiết c&oacute; thể kh&ocirc;ng r&otilde; r&agrave;ng như những người đặt ống th&ocirc;ng mạch v&agrave;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều trị bệnh động mạch v&agrave;nh thường li&ecirc;n quan đến việc thay đổi lối sống v&agrave; nếu cần thiết thuốc v&agrave; c&aacute;c thủ tục y tế nhất định sẽ được sử dụng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thay đổi lối sống </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thực hiện một cam kết với c&aacute;c thay đổi lối sống l&agrave;nh mạnh sau đ&acirc;y c&oacute; thể đi một chặng đường d&agrave;i về hướng th&uacute;c đẩy c&aacute;c động mạch khỏe mạnh: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bỏ h&uacute;t thuốc l&aacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ăn c&aacute;c loại thực phẩm l&agrave;nh mạnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tập thể dục thường xuy&ecirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Giảm c&acirc;n vượt qu&aacute; trọng lượng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Giảm căng thẳng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bỏ ma t&uacute;y.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c loại thuốc kh&aacute;c nhau c&oacute; thể được d&ugrave;ng để điều trị bệnh động mạch v&agrave;nh, bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc hạ Cholesterol.</span></b><span style=\"font-family: Arial;\"> Bằng c&aacute;ch giảm lượng cholesterol trong m&aacute;u, đặc biệt l&agrave; mật độ lipoprotein thấp (LDL) cholesterol, c&aacute;c thuốc n&agrave;y l&agrave;m giảm vật liệu ch&iacute;nh h&igrave;nh th&agrave;nh mảng b&aacute;m v&agrave;o c&aacute;c động mạch v&agrave;nh. Th&uacute;c đẩy lipoprotein mật độ cao (HDL) cholesterol, c&oacute; thể gi&uacute;p đỡ. B&aacute;c sĩ c&oacute; thể chọn từ một loạt c&aacute;c loại thuốc bao gồm statins, niacin, fibrate v&agrave; sequestrants acid mật.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Aspirin.</span></b><span style=\"font-family: Arial;\"> B&aacute;c sĩ c&oacute; thể khuy&ecirc;n n&ecirc;n d&ugrave;ng aspirin h&agrave;ng ng&agrave;y. Điều n&agrave;y c&oacute; thể l&agrave;m giảm xu hướng của m&aacute;u h&igrave;nh th&agrave;nh cục m&aacute;u đ&ocirc;ng, c&oacute; thể gi&uacute;p ngăn ngừa tắc nghẽn động mạch v&agrave;nh. Nếu đ&atilde; c&oacute; một cơn đau tim, aspirin c&oacute; thể gi&uacute;p ngăn ngừa c&aacute;c cuộc tấn c&ocirc;ng trong tương lai. C&oacute; một số trường hợp aspirin kh&ocirc;ng th&iacute;ch hợp, chẳng hạn như nếu c&oacute; một rối loạn chảy m&aacute;u, do đ&oacute;, h&atilde;y hỏi b&aacute;c sĩ trước khi bắt đầu d&ugrave;ng thuốc aspirin.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Beta blockers.</span></b><span style=\"font-family: Arial;\"> C&aacute;c thuốc n&agrave;y l&agrave;m chậm nhịp tim v&agrave; giảm huyết &aacute;p, m&agrave; giảm nhu cầu &ocirc; xy cho tim. Nếu đ&atilde; c&oacute; một cơn đau tim, thuốc chẹn beta l&agrave;m giảm nguy cơ c&aacute;c cuộc tấn c&ocirc;ng trong tương lai.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Nitroglycerin.</span></b><span style=\"font-family: Arial;\"> Nitroglycerin vi&ecirc;n n&eacute;n, thuốc xịt v&agrave; c&aacute;c miếng d&aacute;n c&oacute; thể kiểm so&aacute;t đau ngực bằng c&aacute;ch mở c&aacute;c động mạch v&agrave;nh v&agrave; l&agrave;m giảm nhu cầu m&aacute;u cho tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Angiotensin - men chuyển (ACE) v&agrave; ức chế chặn thụ thể angiotensin (ARBS).</span></b><span style=\"font-family: Arial;\"> C&aacute;c thuốc n&agrave;y tương tự như giảm huyết &aacute;p v&agrave; c&oacute; thể gi&uacute;p ngăn chặn tiến triển của bệnh động mạch v&agrave;nh. Nếu đ&atilde; c&oacute; một cơn đau tim, chất ức chế ACE l&agrave;m giảm nguy cơ c&aacute;c cuộc tấn c&ocirc;ng trong tương lai.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Chẹn k&ecirc;nh canxi.</span></b><span style=\"font-family: Arial;\"> Những thuốc thư gi&atilde;n bắp thịt bao quanh động mạch v&agrave;nh v&agrave; g&acirc;y ra gi&atilde;n mạch, tăng lưu lượng m&aacute;u tới tim. N&oacute; cũng kiểm so&aacute;t huyết &aacute;p cao.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thủ tục để phục hồi v&agrave; cải thiện lưu lượng m&aacute;u</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đ&ocirc;i khi điều trị t&iacute;ch cực hơn l&agrave; cần thiết. Dưới đ&acirc;y l&agrave; một v&agrave;i lựa chọn: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nong mạch v&agrave; đặt stent (revascularization mạch v&agrave;nh qua da). Trong thủ tục n&agrave;y, ch&egrave;n ống th&ocirc;ng v&agrave;o một phần thu hẹp của động mạch. Một d&acirc;y với một quả b&oacute;ng x&igrave; hơi được truyền qua ống th&ocirc;ng v&agrave;o v&ugrave;ng thu hẹp, kh&iacute; cầu sau đ&oacute; được thổi phồng, n&eacute;n mảng b&aacute;m đối với th&agrave;nh động mạch. Stent thường c&ograve;n lại trong động mạch để gi&uacute;p giữ cho động mạch mở. Một số stents ph&aacute;t thuốc chậm để gi&uacute;p giữ cho động mạch mở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Phẫu thuật động mạch v&agrave;nh</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\"><span style=\"\">&nbsp;</span>B&aacute;c sĩ phẫu thuật tạo ra một mảnh gh&eacute;p để bỏ qua động mạch v&agrave;nh bị chặn bằng c&aacute;ch sử dụng một mạch từ một phần kh&aacute;c của cơ thể. Điều n&agrave;y cho ph&eacute;p m&aacute;u chảy xung quanh v&ugrave;ng bị cấm hoặc thu hẹp động mạch v&agrave;nh. Bởi v&igrave; điều n&agrave;y đ&ograve;i hỏi phẫu thuật tim mở, thường xuy&ecirc;n nhất d&agrave;nh cho trường hợp nhiều thu hẹp động mạch v&agrave;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thuốc thay thế</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Omega - 3 fatty acid l&agrave; một loại acid b&eacute;o kh&ocirc;ng b&atilde;o h&ograve;a để giảm vi&ecirc;m khắp cơ thể. Omega - 3 fatty acid c&oacute; thể gi&uacute;p hạ huyết &aacute;p v&agrave; c&oacute; thể giảm nguy cơ đau tim. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute; v&agrave; dầu c&aacute; l&agrave; nguồn hiệu quả nhất của c&aacute;c axit omega - 3 fatty. c&aacute; b&eacute;o như c&aacute; hồi, c&aacute; tr&iacute;ch v&agrave; c&aacute; ngừ v&agrave;o một phạm vi nhỏ hơn, chứa hầu hết c&aacute;c omega - 3 fatty acid v&agrave; do đ&oacute; lợi &iacute;ch nhất. Bổ sung dầu c&aacute; c&oacute; thể cung cấp lợi &iacute;ch, nhưng bằng chứng l&agrave; mạnh nhất cho nguồn thực phẩm.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Lanh v&agrave; dầu hạt lanh, mặc d&ugrave; c&aacute;c nghi&ecirc;n cứu đ&atilde; kh&ocirc;ng t&igrave;m thấy những nguồn n&agrave;y sẽ được hiệu quả như c&aacute;. Vỏ nguy&ecirc;n hạt lanh cũng c&oacute; chứa chất xơ h&ograve;a tan, m&agrave; c&oacute; thể gi&uacute;p cholesterol m&aacute;u thấp hơn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c chế độ ăn uống nguồn axit omega - 3 fatty bao gồm hạt &oacute;c ch&oacute;, dầu canola, đậu n&agrave;nh v&agrave; dầu đậu tương. Những thực phẩm n&agrave;y chứa một lượng nhỏ c&aacute;c axit b&eacute;o omega - 3 hơn l&agrave;m c&aacute; v&agrave; dầu c&aacute;, v&agrave; bằng chứng cho lợi &iacute;ch cho sức khoẻ tim mạch kh&ocirc;ng phải l&agrave; mạnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phong c&aacute;ch sống v&agrave; biện ph&aacute;p khắc phục</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thay đổi lối sống c&oacute; thể gi&uacute;p ngăn ngừa hoặc l&agrave;m chậm sự tiến triển của bệnh động mạch v&agrave;nh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ngưng h&uacute;t thuốc l&aacute;.</span></b><span style=\"font-family: Arial;\"> H&uacute;t thuốc l&agrave; một yếu tố nguy cơ ch&iacute;nh đối với bệnh mạch v&agrave;nh. Nicotin l&agrave;m lưu lượng mạch m&aacute;u v&agrave; tr&aacute;i tim l&agrave;m việc kh&oacute; hơn, v&agrave; carbon monoxide l&agrave;m giảm &ocirc;xy trong m&aacute;u v&agrave; tổn thương ni&ecirc;m mạc của c&aacute;c mạch m&aacute;u. Nếu h&uacute;t thuốc, bỏ thuốc l&agrave; một trong những c&aacute;ch tốt nhất để giảm nguy cơ đau tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Kiểm so&aacute;t huyết &aacute;p.</span></b><span style=\"font-family: Arial;\"> H&atilde;y đo huyết &aacute;p &iacute;t nhất mỗi hai năm. Người đ&oacute; c&oacute; thể khuy&ecirc;n n&ecirc;n đo thường xuy&ecirc;n hơn nếu huyết &aacute;p cao hơn b&igrave;nh thường hoặc c&oacute; tiền sử bệnh tim. Huyết &aacute;p l&yacute; tưởng l&agrave; dưới 120 &aacute;p t&acirc;m thu v&agrave; t&acirc;m trương 80, khi đo bằng milim&eacute;t thuỷ ng&acirc;n (mm Hg ).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Kiểm tra cholesterol.</span></b><span style=\"font-family: Arial;\"> H&atilde;y kiểm tra cholesterol cơ sở khi đang ở độ tuổi 20 v&agrave; sau đ&oacute; &iacute;t nhất mỗi năm năm. Nếu kết quả thử nghiệm kh&ocirc;ng phải l&agrave; trong phạm vi mong muốn, b&aacute;c sĩ c&oacute; thể khuy&ecirc;n n&ecirc;n đo thường xuy&ecirc;n hơn. Hầu hết mọi người phải nhắm cho một mức độ LDL dưới 130 mg / dL (mg / dL), hoặc 3,4 millimoles / l&iacute;t (mmol / L). Nếu c&oacute; yếu tố nguy cơ kh&aacute;c đối với bệnh tim, mục ti&ecirc;u c&oacute; thể LDL dưới 100 mg / dL (2.6 mmol / L).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Giữ bệnh tiểu đường được kiểm so&aacute;t.</span></b><span style=\"font-family: Arial;\"> Nếu bị tiểu đường, kiểm so&aacute;t chặt chẽ lượng đường trong m&aacute;u c&oacute; thể gi&uacute;p giảm nguy cơ bệnh tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tập thể dục gi&uacute;p đạt được v&agrave; duy tr&igrave; một trọng lượng khỏe mạnh v&agrave; kiểm so&aacute;t bệnh tiểu đường, cholesterol cao v&agrave; huyết &aacute;p cao, tất cả c&aacute;c yếu tố nguy cơ bệnh mạch v&agrave;nh. Với mục ti&ecirc;u của b&aacute;c sĩ, OK để 30 - 60 ph&uacute;t hoạt động thể chất năm ng&agrave;y hoặc tất cả c&aacute;c ng&agrave;y trong tuần.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ăn c&aacute;c loại thực phẩm l&agrave;nh mạnh.</span></b><span style=\"font-family: Arial;\"> Một chế độ ăn uống cho sức khỏe tim dựa v&agrave;o c&aacute;c loại tr&aacute;i c&acirc;y, rau v&agrave; ngũ cốc - v&agrave; &iacute;t chất b&eacute;o b&atilde;o h&ograve;a, cholesterol v&agrave; natri c&oacute; thể gi&uacute;p kiểm so&aacute;t c&acirc;n nặng, huyết &aacute;p v&agrave; cholesterol. Ăn một hoặc hai khẩu phần ăn của c&aacute; một tuần cũng c&oacute; lợi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Duy tr&igrave; một trọng lượng khỏe mạnh.</span></b><span style=\"font-family: Arial;\"> Khi l&ecirc;n c&acirc;n nguy cơ bệnh mạch v&agrave;nh. Trọng lượng giảm l&agrave; đặc biệt quan trọng cho những người c&oacute; số đo v&ograve;ng bụng lớn - hơn 102 cm d&agrave;nh cho nam giới v&agrave; hơn 89 cm cho phụ nữ - bởi v&igrave; những người c&oacute; cơ thể h&igrave;nh dạng n&agrave;y c&oacute; nhiều khả năng ph&aacute;t triển bệnh tiểu đường v&agrave; bệnh tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Quản l&yacute; căng thẳng.</span></b><span style=\"font-family: Arial;\"> Giảm căng thẳng c&agrave;ng nhiều c&agrave;ng tốt. Thực h&agrave;nh kỹ thuật l&agrave;nh mạnh cho quản l&yacute; căng thẳng, chẳng hạn như thư gi&atilde;n cơ bắp v&agrave; thở s&acirc;u.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ngo&agrave;i việc thay đổi lối sống l&agrave;nh mạnh, h&atilde;y nhớ tầm quan trọng của kiểm tra y tế thường xuy&ecirc;n. Một số c&aacute;c yếu tố nguy cơ ch&iacute;nh của bệnh động mạch v&agrave;nh - cholesterol cao, huyết &aacute;p cao v&agrave; bệnh tiểu đường - kh&ocirc;ng c&oacute; triệu chứng trong giai đoạn đầu. Ph&aacute;t hiện sớm v&agrave; điều trị c&oacute; thể thiết lập giai đoạn cho đời sống với sức khỏe tim tốt hơn. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Cũng hỏi b&aacute;c sĩ về thuốc chủng ngừa c&uacute;m h&agrave;ng năm. Bệnh động mạch v&agrave;nh v&agrave; rối loạn tim mạch kh&aacute;c l&agrave;m tăng nguy cơ biến chứng từ bệnh c&uacute;m.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Ph&ograve;ng chống</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c phong c&aacute;ch sống c&ugrave;ng một th&oacute;i quen c&oacute; thể gi&uacute;p điều trị bệnh động mạch v&agrave;nh cũng c&oacute; thể gi&uacute;p ngăn kh&ocirc;ng cho n&oacute; ph&aacute;t triển ở nơi đầu ti&ecirc;n. Dẫn đầu một lối sống l&agrave;nh mạnh c&oacute; thể gi&uacute;p giữ cho động mạch mạnh mẽ, đ&agrave;n hồi v&agrave; mịn m&agrave;ng, v&agrave; cho ph&eacute;p lưu lượng m&aacute;u tối đa. Th&oacute;i quen l&agrave;nh mạnh cho tim bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng h&uacute;t thuốc l&aacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kiểm so&aacute;t c&aacute;c điều kiện như huyết &aacute;p cao, cholesterol cao v&agrave; bệnh tiểu đường.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Duy tr&igrave; hoạt động thể chất.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ăn c&aacute;c loại thực phẩm l&agrave;nh mạnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Duy tr&igrave; một trọng lượng khỏe mạnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Giảm v&agrave; quản l&yacute; căng thẳng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: right;\"><b style=\"\"><i style=\"\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(128, 0, 0);\"></span></span></i></b></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\" class=\"MsoNormal\">&nbsp;</p></span>\n', 2);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(25, 'Bệnh Raynaud', '\n   <h1 class=\"title\">Bệnh Raynaud</h1>   <span class=\"des\">Điều trị bệnh Raynaud phụ thuộc vào mức độ nghiêm trọng của nó và sự hiện diện của các điều kiện liên quan. Đối với hầu hết mọi người, bệnh Raynaud là một mối phiền toái nhiều hơn khuyết tật.</span>\n   <span><p style=\"text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Bệnh Raynaud l&agrave; một điều kiện m&agrave; nguy&ecirc;n nh&acirc;n một số khu vực của cơ thể - chẳng hạn như ng&oacute;n tay, ng&oacute;n ch&acirc;n, ch&oacute;p mũi v&agrave; tai - cảm thấy t&ecirc; v&agrave; m&aacute;t để đ&aacute;p ứng với nhiệt độ lạnh hoặc căng thẳng. Trong bệnh Raynaud, c&aacute;c động mạch nhỏ cung cấp m&aacute;u cho da bị hẹp, hạn chế lưu th&ocirc;ng m&aacute;u đến c&aacute;c khu vực bị ảnh hưởng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Phụ nữ c&oacute; nhiều khả năng bị bệnh Raynaud. N&oacute; cũng phổ biến hơn ở những người sống ở v&ugrave;ng kh&iacute; hậu lạnh hơn.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Điều trị bệnh Raynaud phụ thuộc v&agrave;o mức độ nghi&ecirc;m trọng của n&oacute; v&agrave; sự hiện diện của c&aacute;c điều kiện li&ecirc;n quan. Đối với hầu hết mọi người, bệnh Raynaud l&agrave; một mối phiền to&aacute;i nhiều hơn khuyết tật.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Nhiều người bệnh Raynaud chỉ đơn giản l&agrave; c&oacute; b&agrave;n tay lạnh v&agrave; b&agrave;n ch&acirc;n lạnh, v&agrave; n&oacute; kh&ocirc;ng giống như bị t&ecirc; c&oacute;ng. C&aacute;c dấu hiệu v&agrave; triệu chứng của bệnh Raynaud phụ thuộc v&agrave;o thời gian, tần suất v&agrave; mức độ nghi&ecirc;m trọng của sự co thắt mạch m&aacute;u l&agrave;m nền tảng cho rối loạn. Triệu chứng của bệnh Raynaud bao gồm:</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Lạnh ng&oacute;n tay v&agrave; ng&oacute;n ch&acirc;n.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Tr&igrave;nh tự thay đổi m&agrave;u sắc tr&ecirc;n da phản ứng với stress hoặc lạnh.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">T&ecirc;, gai cảm gi&aacute;c hoặc đau nhức khi n&oacute;ng l&ecirc;n hoặc giảm căng thẳng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Trong một cuộc tấn c&ocirc;ng của Raynaud, c&aacute;c khu vực bị ảnh hưởng của l&agrave;n da đầu ti&ecirc;n thường chuyển sang m&agrave;u trắng. Sau đ&oacute;, c&aacute;c khu vực bị ảnh hưởng thường chuyển m&agrave;u xanh, cảm thấy lạnh, t&ecirc; liệt, v&agrave; cảm gi&aacute;c nhận thức k&eacute;m. Khi lưu th&ocirc;ng được cải thiện, c&aacute;c khu vực bị ảnh hưởng c&oacute; thể chuyển sang m&agrave;u đỏ, rung hay sưng l&ecirc;n. Thứ tự của c&aacute;c thay đổi về m&agrave;u sắc kh&ocirc;ng giống nhau cho tất cả mọi người, v&agrave; kh&ocirc;ng phải tất cả mọi người trải nghiệm cả ba m&agrave;u.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Thỉnh thoảng, một cuộc tấn c&ocirc;ng ảnh hưởng đến chỉ một hoặc hai ng&oacute;n tay hoặc ng&oacute;n ch&acirc;n. C&aacute;c cuộc tấn c&ocirc;ng kh&ocirc;ng nhất thiết l&uacute;c n&agrave;o cũng ảnh hưởng tương tự. Mặc d&ugrave; Raynaud phổ biến nhất ảnh hưởng đến c&aacute;c ng&oacute;n tay v&agrave; ng&oacute;n ch&acirc;n, điều kiện cũng c&oacute; thể ảnh hưởng đến c&aacute;c khu vực kh&aacute;c của cơ thể, chẳng hạn như mũi, m&ocirc;i, tai v&agrave; thậm ch&iacute; cả n&uacute;m v&uacute;. Một cuộc tấn c&ocirc;ng c&oacute; thể k&eacute;o d&agrave;i &iacute;t hơn một ph&uacute;t đến v&agrave;i giờ.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Những người c&oacute; Raynaud k&egrave;m theo một bệnh kh&aacute;c c&oacute; thể sẽ c&ograve;n c&oacute; c&aacute;c dấu hiệu v&agrave; triệu chứng li&ecirc;n quan đến điều kiện cơ bản của họ. </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Đến gặp b&aacute;c sĩ khi</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Đi kh&aacute;m b&aacute;c sĩ ngay nếu c&oacute; lịch sử nặng Raynaud v&agrave; ph&aacute;t triển lo&eacute;t hoặc nhiễm tr&ugrave;ng ở một trong những ng&oacute;n tay hay ng&oacute;n ch&acirc;n bị ảnh hưởng. </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">B&aacute;c sĩ kh&ocirc;ng ho&agrave;n to&agrave;n hiểu được nguy&ecirc;n nh&acirc;n của cuộc tấn c&ocirc;ng Raynaud, nhưng mạch m&aacute;u ở b&agrave;n tay v&agrave; b&agrave;n ch&acirc;n xuất hiện để phản ứng th&aacute;i qu&aacute; với nhiệt độ lạnh hoặc stress: </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Nhiệt độ lạnh.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Khi cơ thể tiếp x&uacute;c với nhiệt độ lạnh, tứ chi bị mất nhiệt. Cơ thể l&agrave;m m&aacute;u cung cấp tới c&aacute;c ng&oacute;n ch&acirc;n ng&oacute;n tay chậm xuống để bảo vệ của nhiệt độ nội tạng cơ thể. Cơ thể đặc biệt l&agrave;m giảm lưu lượng m&aacute;u bằng c&aacute;ch thu hẹp c&aacute;c động mạch nhỏ dưới da của tứ chi. Ở người bị Raynaud, phản ứng n&agrave;y thường bị qu&aacute; mức.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Căng thẳng.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Stress g&acirc;y ra một phản ứng tương tự như cảm lạnh trong cơ thể, v&agrave; tương tự phản ứng của cơ thể c&oacute; thể bị qu&aacute; mức ở những người bị Raynaud.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Co thắt mạch m&aacute;u</span><span lang=\"EN-US\" style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Với Raynaud, c&aacute;c động mạch c&aacute;c ng&oacute;n tay v&agrave; ng&oacute;n ch&acirc;n c&oacute; t&igrave;nh trạng gọi l&agrave; vasospasm. Điều n&agrave;y thu hẹp c&aacute;c mạch đ&aacute;ng kể v&agrave; tạm thời hạn chế nguồn cung cấp m&aacute;u. Theo thời gian, c&aacute;c động mạch nhỏ tương tự c&oacute; thể cũng d&agrave;y l&ecirc;n một ch&uacute;t, hơn nữa hạn chế lưu lượng m&aacute;u. Kết quả l&agrave; da bị ảnh hưởng biến m&agrave;u nhạt v&agrave; n&acirc;u sẫm do sự thiếu lưu lượng m&aacute;u đến khu vực n&agrave;y. Khi co thắt tho&aacute;i lui v&agrave; m&aacute;u trở về khu vực, c&aacute;c m&ocirc; c&oacute; thể chuyển sang m&agrave;u đỏ trước khi trở lại m&agrave;u b&igrave;nh thường.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Nhiệt độ lạnh c&oacute; nhiều khả năng k&iacute;ch hoạt một cuộc tấn c&ocirc;ng. Tiếp x&uacute;c với lạnh c&oacute; thể đơn giản như đặt b&agrave;n tay theo một v&ograve;i nước lạnh, lấy một c&aacute;i g&igrave; đ&oacute; trong tủ đ&ocirc;ng lạnh hoặc tiếp x&uacute;c với kh&ocirc;ng kh&iacute; lạnh. Đối với một số người, tiếp x&uacute;c với nhiệt độ lạnh l&agrave; kh&ocirc;ng cần thiết. Cảm x&uacute;c căng thẳng c&oacute; thể g&acirc;y ra khởi ph&aacute;t Raynaud.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Raynaud c&oacute; thể l&agrave; một phần của một rối loạn di truyền. </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Raynaud xảy ra trong hai loại ch&iacute;nh</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Raynaud ti&ecirc;n ph&aacute;t.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Đ&acirc;y l&agrave; Raynaud kh&ocirc;ng c&oacute; bệnh tiềm ẩn hoặc vấn đề y tế li&ecirc;n quan c&oacute; thể g&acirc;y vasospasm. C&ograve;n được gọi l&agrave; bệnh Raynaud, đ&oacute; l&agrave; h&igrave;nh thức phổ biến nhất của chứng rối loạn.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Raynaud thứ ph&aacute;t.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> C&ograve;n được gọi l&agrave; hiện tượng Raynaud\'s, mẫu đơn n&agrave;y l&agrave; do một vấn đề cơ bản. Mặc d&ugrave; Raynaud thứ ph&aacute;t kh&ocirc;ng phổ biến bằng c&aacute;c h&igrave;nh thức ti&ecirc;n ph&aacute;t, n&oacute; c&oacute; xu hướng l&agrave; một rối loạn nghi&ecirc;m trọng hơn. C&aacute;c dấu hiệu v&agrave; triệu chứng của Raynaud thứ ph&aacute;t thường đầu ti&ecirc;n xuất hiện ở độ tuổi sau n&agrave;y - khoảng 40 - hơn l&agrave; l&agrave;m cho người với h&igrave;nh thức Raynaud ti&ecirc;n ph&aacute;t.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n của thứ Raynaud bao gồm</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Hiện tượng Scleroderma.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Raynaud xảy ra trong đa số những người bị xơ cứng b&igrave; - một căn bệnh hiếm dẫn đến xơ cứng v&agrave; sẹo tr&ecirc;n da. Xơ cứng b&igrave;, một loại bệnh m&ocirc; li&ecirc;n kết, kết quả Raynaud l&agrave; v&igrave; căn bệnh n&agrave;y l&agrave;m giảm lưu lượng m&aacute;u đến c&aacute;c chi.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Lupus.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Raynaud cũng l&agrave; một vấn đề phổ biến cho người với lupus ban đỏ - một bệnh tự miễn dịch m&agrave; c&oacute; thể ảnh hưởng đến nhiều bộ phận của cơ thể, bao gồm c&aacute;c khớp xương, da, c&aacute;c cơ quan v&agrave; mạch m&aacute;u. Một bệnh tự miễn dịch trong đ&oacute; hệ miễn dịch tấn c&ocirc;ng c&aacute;c m&ocirc; khỏe mạnh.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Vi&ecirc;m khớp khớp dạng thấp.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Raynaud c&oacute; thể l&agrave; một dấu hiệu ban đầu của bệnh vi&ecirc;m khớp dạng thấp - một t&igrave;nh trạng vi&ecirc;m g&acirc;y đau v&agrave; cứng khớp ở c&aacute;c khớp, thường bao gồm cả b&agrave;n tay v&agrave; b&agrave;n ch&acirc;n.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Hội chứng Sjogren.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Hiện tượng Raynaud cũng c&oacute; thể xảy ra ở những người c&oacute; của hội chứng Sjogren - một rối loạn tự miễn dịch m&agrave; c&oacute; thể đi k&egrave;m với xơ cứng b&igrave;, lupus hay vi&ecirc;m khớp dạng thấp.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">C&aacute;c bệnh của động mạch.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Hiện tượng Raynaud c&oacute; thể được li&ecirc;n kết với c&aacute;c bệnh kh&aacute;c nhau c&oacute; ảnh hưởng đến động mạch, chẳng hạn như xơ vữa động mạch, đ&oacute; l&agrave; sự t&iacute;ch tụ dần dần của mảng b&aacute;m trong mạch m&aacute;u nu&ocirc;i tim (động mạch v&agrave;nh), hoặc của bệnh Buerger, một rối loạn trong đ&oacute; mạch m&aacute;u của b&agrave;n tay v&agrave; b&agrave;n ch&acirc;n bị vi&ecirc;m. Tăng huyết &aacute;p động mạch phổi, một loại cao huyết &aacute;p ảnh hưởng đến động mạch của phổi, c&oacute; thể được kết hợp với Raynaud.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Hội chứng ống cổ tay.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> C&aacute;c ống cổ tay l&agrave; một lối đi hẹp ở cổ tay để bảo vệ d&acirc;y thần kinh lớn cho b&agrave;n tay. Hội chứng ống cổ tay l&agrave; một t&igrave;nh trạng m&agrave; trong đ&oacute; &aacute;p suất đặt l&ecirc;n d&acirc;y thần kinh n&agrave;y, t&ecirc; v&agrave; đau ở tay bị ảnh hưởng. B&agrave;n tay bị ảnh hưởng c&oacute; thể trở n&ecirc;n nhạy cảm với nhiệt độ lạnh v&agrave; của Raynaud.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Lặp đi lặp lại chấn thương.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Raynaud cũng c&oacute; thể được g&acirc;y ra bởi chấn thương d&acirc;y thần kinh lặp đi lặp lại, thiệt hại cho c&aacute;c mạch m&aacute;u ở b&agrave;n tay v&agrave; b&agrave;n ch&acirc;n. Một số người chơi piano mạnh hoặc một thời gian d&agrave;i c&oacute; thể dễ bị Raynaud. C&ocirc;ng nh&acirc;n vận h&agrave;nh c&aacute;c c&ocirc;ng cụ rung c&oacute; thể ph&aacute;t triển một loại của hiện tượng Raynaud được gọi l&agrave; rung động g&acirc;y ra tay trắng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">H&uacute;t thuốc l&aacute;.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> H&uacute;t thuốc co thắt mạch m&aacute;u v&agrave; l&agrave; một nguy&ecirc;n nh&acirc;n tiềm năng của Raynaud.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Thương t&iacute;ch.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Trước khi bị chấn thương b&agrave;n tay hoặc b&agrave;n ch&acirc;n, chẳng hạn như phẫu thuật g&atilde;y xương, cổ tay hay t&ecirc; c&oacute;ng, c&oacute; thể dẫn đến hiện tượng Raynaud.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Một số loại thuốc.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Một số loại thuốc - kể cả thuốc chẹn beta, được sử dụng để điều trị huyết &aacute;p cao, thuốc đau nửa đầu c&oacute; chứa ergotamin, c&aacute;c thuốc c&oacute; chứa estrogen, chất h&oacute;a trị liệu nhất định v&agrave; c&aacute;c loại thuốc g&acirc;y ra c&aacute;c mạch m&aacute;u thu hẹp, chẳng hạn như một số toa- thuốc trị cảm lạnh - c&oacute; li&ecirc;n quan đến Raynaud.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">H&oacute;a chất.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Những người tiếp x&uacute;c với clorua vinyl, chẳng hạn như những người l&agrave;m việc trong ng&agrave;nh c&ocirc;ng nghiệp nhựa, c&oacute; thể ph&aacute;t triển một căn bệnh tương tự như xơ cứng b&igrave;. Raynaud c&oacute; thể l&agrave; một phần của căn bệnh đ&oacute;.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">C&aacute;c nguy&ecirc;n nh&acirc;n kh&aacute;c.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Raynaud cũng đ&atilde; được li&ecirc;n kết với c&aacute;c rối loạn tuyến gi&aacute;p.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Yếu tố nguy cơ của Raynaud ti&ecirc;n ph&aacute;t bao gồm: </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Giới t&iacute;nh.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Raynaud ti&ecirc;n ph&aacute;t ảnh hưởng đến phụ nữ nhiều hơn nam giới.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Tuổi.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Mặc d&ugrave; ai cũng c&oacute; thể ph&aacute;t triển c&aacute;c điều kiện, ch&iacute;nh Raynaud thường bắt đầu trong độ tuổi từ 15 v&agrave; 30.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Nơi sống.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Rối loạn n&agrave;y cũng phổ biến hơn ở những người sống ở v&ugrave;ng kh&iacute; hậu lạnh hơn.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Tiền sử gia đ&igrave;nh.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Ngo&agrave;i ra, lịch sử gia đ&igrave;nh c&oacute; vẻ như l&agrave;m tăng nguy cơ của Raynaud. Khoảng một phần ba người với Raynaud ti&ecirc;n ph&aacute;t c&oacute; tương đối - cha mẹ, anh chị em ruột hoặc con - với rối loạn.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Yếu tố nguy cơ Raynaud thứ ph&aacute;t bao gồm: </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Bệnh li&ecirc;n quan.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Ch&uacute;ng bao gồm c&aacute;c điều kiện như xơ cứng b&igrave; v&agrave; lupus.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Một số ng&agrave;nh nghề.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Những người trong nghề nghiệp g&acirc;y ra chấn thương lặp đi lặp lại, chẳng hạn như c&ocirc;ng nh&acirc;n vận h&agrave;nh c&aacute;c c&ocirc;ng cụ rung động, cũng c&oacute; thể dễ bị Raynaud.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Tiếp x&uacute;c với c&aacute;c chất nhất định.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> H&uacute;t thuốc, thuốc c&oacute; ảnh hưởng đến c&aacute;c mạch m&aacute;u v&agrave; tiếp x&uacute;c với h&oacute;a chất như clorua vinyl được kết hợp với tăng nguy cơ Raynaud.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Nếu Raynaud l&agrave; nghi&ecirc;m trọng - đ&oacute; l&agrave; hiếm - m&aacute;u lưu th&ocirc;ng đến c&aacute;c ng&oacute;n tay hoặc ng&oacute;n ch&acirc;n thường xuy&ecirc;n c&oacute; thể giảm, g&acirc;y ra dị tật c&aacute;c ng&oacute;n tay hay ng&oacute;n ch&acirc;n. </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Nếu một động mạch đến khu vực bị ảnh hưởng bị tắc nghẽn ho&agrave;n to&agrave;n, vết lo&eacute;t (lo&eacute;t da) hoặc m&ocirc; chết (hoại tử) c&oacute; thể ph&aacute;t triển. Lo&eacute;t v&agrave; hoại tử c&oacute; thể kh&oacute; điều trị.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Để chẩn đo&aacute;n bệnh Raynaud, b&aacute;c sĩ sẽ đặt c&acirc;u hỏi chi tiết về c&aacute;c triệu chứng v&agrave; lịch sử y tế v&agrave; tiến h&agrave;nh một cuộc kiểm tra thể chất. B&aacute;c sĩ cũng c&oacute; thể chạy c&aacute;c x&eacute;t nghiệm để loại trừ vấn đề y tế kh&aacute;c c&oacute; thể g&acirc;y ra c&aacute;c dấu hiệu v&agrave; triệu chứng tương tự, chẳng hạn như d&acirc;y thần kinh bị ch&egrave;n &eacute;p.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">B&aacute;c sĩ c&oacute; thể thực hiện một thử nghiệm đơn giản gọi l&agrave; một thử nghiệm lạnh k&iacute;ch th&iacute;ch trong thời gian đến văn ph&ograve;ng. X&eacute;t nghiệm n&agrave;y c&oacute; thể bao gồm việc đặt tay trong nước lạnh hoặc tiếp x&uacute;c kh&ocirc;ng kh&iacute; lạnh để k&iacute;ch hoạt Raynaud.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ph&acirc;n loại Raynaud</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Để ph&acirc;n biệt giữa Raynaud ti&ecirc;n hay thứ ph&aacute;t, b&aacute;c sĩ c&oacute; thể thực hiện một thử nghiệm gọi l&agrave; m&oacute;ng tay capillaroscopy. Trong thời gian thử nghiệm, b&aacute;c sĩ kiểm tra m&oacute;ng tay - da ở ch&acirc;n m&oacute;ng tay dưới k&iacute;nh hiển vi. Mao mạch gần c&aacute;c m&oacute;ng tay mở rộng hoặc biến dạng c&oacute; thể chỉ ra một bệnh tiềm ẩn. Tuy nhi&ecirc;n, một số bệnh phụ c&oacute; thể kh&ocirc;ng được ph&aacute;t hiện bởi thử nghiệm n&agrave;y.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Nếu b&aacute;c sĩ nghi ngờ một điều kiện, chẳng hạn như một bệnh tự miễn dịch hay m&ocirc; li&ecirc;n kết, nguy&ecirc;n nh&acirc;n cơ bản của Raynaud, c&oacute; thể y&ecirc;u cầu x&eacute;t nghiệm m&aacute;u, chẳng hạn như: </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Antinuclear thử nghiệm kh&aacute;ng thể.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Một x&eacute;t nghiệm dương t&iacute;nh với sự hiện diện của c&aacute;c kh&aacute;ng thể n&agrave;y - được sản xuất bởi hệ thống miễn dịch - chỉ ra hệ thống miễn dịch k&iacute;ch th&iacute;ch v&agrave; thường gặp ở những người c&oacute; bệnh m&ocirc; li&ecirc;n kết hay rối loạn tự miễn kh&aacute;c.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Tỷ lệ hồng cầu lắng.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Nhanh hơn so với tỷ lệ b&igrave;nh thường c&oacute; thể b&aacute;o hiệu một bệnh tự miễn dịch vi&ecirc;m hoặc nằm b&ecirc;n dưới. C&aacute;c bệnh tự miễn thường li&ecirc;n kết với Raynaud thứ ph&aacute;t.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Kh&ocirc;ng c&oacute; x&eacute;t nghiệm m&aacute;u duy nhất để chẩn đo&aacute;n bệnh Raynaud. B&aacute;c sĩ c&oacute; thể y&ecirc;u cầu c&aacute;c x&eacute;t nghiệm kh&aacute;c, chẳng hạn như loại trừ c&aacute;c bệnh của động mạch, gi&uacute;p x&aacute;c định một bệnh hay điều kiện c&oacute; thể được kết hợp với Raynaud.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Tự chăm s&oacute;c v&agrave; ph&ograve;ng ngừa, c&aacute;c bước th&ocirc;ng thường c&oacute; hiệu quả trong đối ph&oacute; với c&aacute;c triệu chứng nhẹ của Raynaud. Nếu đ&acirc;y kh&ocirc;ng phải l&agrave; đầy đủ, tuy nhi&ecirc;n, thuốc c&oacute; sẵn để điều trị nhiều h&igrave;nh thức nghi&ecirc;m trọng của t&igrave;nh trạng n&agrave;y. C&aacute;c mục ti&ecirc;u của điều trị l&agrave;:</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Giảm số lượng v&agrave; mức độ nghi&ecirc;m trọng của vụ tấn c&ocirc;ng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Ngăn ngừa tổn thương m&ocirc;.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Điều trị bất kỳ bệnh hay điều kiện cơ bản.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Thuốc men</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">T&ugrave;y thuộc v&agrave;o nguy&ecirc;n nh&acirc;n g&acirc;y ra c&aacute;c triệu chứng, thuốc c&oacute; thể chứng minh hiệu quả điều trị Raynaud. Để mở rộng mạch m&aacute;u (gi&atilde;n ra) v&agrave; th&uacute;c đẩy lưu th&ocirc;ng, b&aacute;c sĩ c&oacute; thể k&ecirc; toa:</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Chẹn k&ecirc;nh canxi.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> C&aacute;c thuốc n&agrave;y v&agrave; mở thư gi&atilde;n c&aacute;c mạch m&aacute;u nhỏ ở b&agrave;n tay v&agrave; b&agrave;n ch&acirc;n. L&agrave;m giảm tần suất v&agrave; mức độ nghi&ecirc;m trọng của vụ tấn c&ocirc;ng ở hầu hết mọi người với Raynaud. C&aacute;c thuốc n&agrave;y cũng c&oacute; thể gi&uacute;p chữa l&agrave;nh vết lo&eacute;t da ở c&aacute;c ng&oacute;n tay hay ng&oacute;n ch&acirc;n. V&iacute; dụ như nifedipine (Adalat CC, Afeditab CR, Procardia), amlodipin (Norvasc) v&agrave; felodipin (Plendil).</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Alpha blockers.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Một số người t&igrave;m cứu trợ với c&aacute;c thuốc gọi l&agrave; thuốc chẹn alpha, chống lại c&aacute;c h&agrave;nh động của norepinephrine, một hormone g&acirc;y co thắt mạch m&aacute;u. V&iacute; dụ như prazosin (Minipress) v&agrave; doxazosin (Cardura).</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Thuốc gi&atilde;n mạch.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Một số b&aacute;c sĩ k&ecirc; toa một thuốc gi&atilde;n mạch - một loại thuốc gi&uacute;p thư gi&atilde;n c&aacute;c mạch m&aacute;u - như kem nitroglycerin để gi&uacute;p chữa l&agrave;nh vết lo&eacute;t da. B&aacute;c sĩ cũng c&oacute; thể k&ecirc; toa thuốc gi&atilde;n mạch c&oacute; sử dụng để điều trị c&aacute;c điều kiện kh&aacute;c, nhưng hiệu quả c&oacute; thể l&agrave;m giảm c&aacute;c triệu chứng của bệnh Raynaud. C&aacute;c thuốc n&agrave;y bao gồm c&aacute;c loại thuốc cao huyết &aacute;p losartan (COZAAR), c&aacute;c rối loạn chức năng cương cứng sildenafil (Viagra), thuốc chống trầm cảm của fluoxetin (Prozac), v&agrave; thuốc gọi l&agrave; prostaglandin.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">C&oacute; thể thấy rằng một loại thuốc c&oacute; t&aacute;c dụng tốt hơn nữa. Một số loại thuốc d&ugrave;ng để điều trị Raynaud c&oacute; t&aacute;c dụng phụ c&oacute; thể y&ecirc;u cầu ngưng uống thuốc. Một loại thuốc cũng c&oacute; thể mất hiệu quả theo thời gian. L&agrave;m việc với b&aacute;c sĩ để t&igrave;m thấy những g&igrave; tốt nhất cho m&igrave;nh.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Một số loại thuốc thực sự c&oacute; thể l&agrave;m nặng th&ecirc;m Raynaud bằng c&aacute;ch dẫn đến tăng co thắt mạch m&aacute;u. B&aacute;c sĩ c&oacute; thể khuy&ecirc;n n&ecirc;n tr&aacute;nh d&ugrave;ng:</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Một số toa (OTC) loại thuốc cảm lạnh.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> V&iacute; dụ bao gồm c&aacute;c loại thuốc c&oacute; chứa pseudoephedrin (Actifed, Chlor-Trimeton, Sudafed).</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Beta blockers.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Loại thuốc n&agrave;y, được sử dụng để điều trị huyết &aacute;p cao v&agrave; bệnh tim mạch, bao gồm metoprolol (Lopressor, Toprol), nadolol (Corgard) v&agrave; propranolol (Inderal, Innopran XL).</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Thuốc tr&aacute;nh thai.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Nếu sử dụng thuốc tr&aacute;nh thai, c&oacute; thể chuyển sang một phương ph&aacute;p ngừa thai kh&aacute;c bởi v&igrave; c&aacute;c thuốc n&agrave;y ảnh hưởng đến lưu th&ocirc;ng v&agrave; c&oacute; thể l&agrave;m cho dễ bị tấn c&ocirc;ng. N&oacute;i chuyện với b&aacute;c sĩ trước khi ngừng thuốc.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Nếu c&oacute; thắc mắc về c&aacute;ch tốt nhất để quản l&yacute; Raynaud, li&ecirc;n hệ với b&aacute;c sĩ. Ch&iacute;nh b&aacute;c sĩ chăm s&oacute;c c&oacute; thể giới thiệu đến một b&aacute;c sĩ chuy&ecirc;n về điều trị Raynaud.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">C&aacute;c phương ph&aacute;p điều trị</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Đ&ocirc;i khi trong trường hợp Raynaud nghi&ecirc;m trọng, c&aacute;ch tiếp cận kh&aacute;c so với thuốc c&oacute; thể l&agrave; một lựa chọn điều trị: </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Phẫu thuật thần kinh.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> D&acirc;y thần kinh được gọi l&agrave; d&acirc;y thần kinh giao cảm ở ch&acirc;n tay kiểm so&aacute;t việc mở v&agrave; thu hẹp c&aacute;c mạch m&aacute;u trong da. Đ&ocirc;i khi n&oacute; l&agrave; cần thiết trong trường hợp nghi&ecirc;m trọng Raynaud để cắt c&aacute;c d&acirc;y thần kinh g&acirc;y cản trở phản ứng qu&aacute; mức. Th&ocirc;ng qua c&aacute;c vết mổ nhỏ trong tay bị ảnh hưởng hoặc b&agrave;n ch&acirc;n, b&aacute;c sĩ dải những d&acirc;y thần kinh nhỏ quanh c&aacute;c mạch m&aacute;u. Phẫu thuật gọi l&agrave; sympathectomy, c&oacute; thể l&agrave;m giảm tần suất v&agrave; thời gian của c&aacute;c cuộc tấn c&ocirc;ng, nhưng n&oacute; kh&ocirc;ng phải l&uacute;c n&agrave;o cũng th&agrave;nh c&ocirc;ng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Ti&ecirc;m h&oacute;a chất.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> B&aacute;c sĩ c&oacute; thể ti&ecirc;m h&oacute;a chất để ngăn chặn c&aacute;c d&acirc;y thần kinh giao cảm trong tay hoặc ch&acirc;n bị ảnh hưởng. C&oacute; thể cần phải c&oacute; c&aacute;c thủ tục lặp đi lặp lại nếu c&aacute;c triệu chứng trở lại hoặc k&eacute;o d&agrave;i.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Cắt cụt.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Đ&ocirc;i khi, c&aacute;c b&aacute;c sĩ cần phải loại bỏ c&aacute;c m&ocirc; bị hư hỏng do thiếu nguồn cung cấp m&aacute;u. Điều n&agrave;y c&oacute; thể bao gồm cắt cụt một ng&oacute;n tay hoặc ng&oacute;n ch&acirc;n bị ảnh hưởng bởi Raynaud\'s, trong đ&oacute; việc cung cấp m&aacute;u đ&atilde; ho&agrave;n to&agrave;n bị chặn v&agrave; c&aacute;c m&ocirc; đ&atilde; ph&aacute;t triển hoại tử. Nhưng điều n&agrave;y l&agrave; rất hiếm.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phong c&aacute;ch sống v&agrave; biện ph&aacute;p khắc phục</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Một loạt c&aacute;c bước c&oacute; thể l&agrave;m giảm c&aacute;c cuộc tấn c&ocirc;ng của Raynaud v&agrave; gi&uacute;p cảm thấy tổng thể tốt hơn: </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Kh&ocirc;ng h&uacute;t thuốc.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> H&uacute;t thuốc g&acirc;y ra co thắt mạch m&aacute;u giảm nhiệt độ da, c&oacute; thể dẫn đến một cuộc tấn c&ocirc;ng. H&iacute;t kh&oacute;i thuốc l&aacute; cũng c&oacute; thể l&agrave;m nặng th&ecirc;m Raynaud.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Tập thể dục.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> B&aacute;c sĩ c&oacute; thể khuy&ecirc;n n&ecirc;n tập thể dục thường xuy&ecirc;n, đặc biệt nếu c&oacute; Raynaud ti&ecirc;n ph&aacute;t. Tập thể dục c&oacute; thể l&agrave;m tăng lưu th&ocirc;ng, trong số những lợi &iacute;ch sức khỏe kh&aacute;c.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Kiểm so&aacute;t căng thẳng.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Bởi v&igrave; stress c&oacute; thể g&acirc;y ra một cuộc tấn c&ocirc;ng, học tập để nhận ra v&agrave; tr&aacute;nh những t&igrave;nh huống căng thẳng c&oacute; thể gi&uacute;p kiểm so&aacute;t số lượng c&aacute;c cuộc tấn c&ocirc;ng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Tr&aacute;nh chất caffeine.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Caffeine l&agrave;m mạch m&aacute;u thu hẹp v&agrave; c&oacute; thể l&agrave;m tăng c&aacute;c dấu hiệu v&agrave; triệu chứng của bệnh Raynaud.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">H&atilde;y chăm s&oacute;c đ&ocirc;i tay v&agrave; đ&ocirc;i ch&acirc;n.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Nếu c&oacute; Raynaud, bảo vệ b&agrave;n ch&acirc;n b&agrave;n tay v&agrave; chấn thương. Đừng đi ch&acirc;n đất. H&atilde;y chăm s&oacute;c m&oacute;ng tay để tr&aacute;nh bị thương ng&oacute;n ch&acirc;n v&agrave; ng&oacute;n tay nhạy cảm. Ngo&agrave;i ra, tr&aacute;nh mặc bất cứ thứ g&igrave; n&eacute;n c&aacute;c mạch m&aacute;u ở b&agrave;n tay hoặc b&agrave;n ch&acirc;n, chẳng hạn như d&acirc;y đeo cổ tay chặt, nhẫn hoặc gi&agrave;y d&eacute;p.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Tr&aacute;nh nơi l&agrave;m việc g&acirc;y n&ecirc;n.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Tr&aacute;nh c&aacute;c c&ocirc;ng cụ rung tay c&oacute; thể l&agrave;m giảm tần suất c&aacute;c cuộc tấn c&ocirc;ng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Trong một cuộc tấn c&ocirc;ng: n&ecirc;n l&agrave;m g&igrave;?</span><span lang=\"EN-US\" style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">N&ecirc;n l&agrave;m g&igrave; nếu đang trải qua một cuộc tấn c&ocirc;ng của Raynaud? C&aacute;c h&agrave;nh động đầu ti&ecirc;n v&agrave; quan trọng nhất l&agrave; sưởi ấm b&agrave;n tay hoặc b&agrave;n ch&acirc;n hoặc bất kỳ khu vực bị ảnh hưởng kh&aacute;c của da. C&aacute;c bước sau đ&acirc;y c&oacute; thể gi&uacute;p nhẹ nh&agrave;ng ấm c&aacute;c ng&oacute;n tay v&agrave; ng&oacute;n ch&acirc;n:</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Di chuyển đến một khu vực ấm hơn. </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Đặt tay dưới n&aacute;ch.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Sưởi ấm ng&oacute;n tay v&agrave; ng&oacute;n ch&acirc;n.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Xoay c&aacute;nh tay.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Ấm nhưng kh&ocirc;ng n&oacute;ng - nước tr&ecirc;n c&aacute;c ng&oacute;n tay v&agrave; ng&oacute;n ch&acirc;n.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Massage tay v&agrave; b&agrave;n ch&acirc;n.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Nếu một t&igrave;nh huống căng thẳng g&acirc;y ra một cuộc tấn c&ocirc;ng, c&oacute; thể gi&uacute;p ngăn chặn c&aacute;c cuộc tấn c&ocirc;ng bằng c&aacute;ch ra khỏi t&igrave;nh trạng căng thẳng v&agrave; thư gi&atilde;n. Nếu đang huấn luyện phản hồi sinh học, c&oacute; thể sử dụng kỹ thuật n&agrave;y c&ugrave;ng với sự n&oacute;ng tay hoặc ch&acirc;n trong nước để gi&uacute;p giảm bớt c&aacute;c cuộc tấn c&ocirc;ng.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Thay thế thuốc</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Phong c&aacute;ch sống thay đổi v&agrave; bổ sung khuyến kh&iacute;ch lưu th&ocirc;ng tốt hơn c&oacute; thể được lựa chọn thay thế hiệu quả cho quản l&yacute; Raynaud. Nếu quan t&acirc;m, n&oacute;i chuyện với b&aacute;c sĩ về:</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Phản hồi sinh học.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Biofeedback - sử dụng t&acirc;m tr&iacute; để kiểm so&aacute;t nhiệt độ cơ thể - c&oacute; thể gi&uacute;p giảm mức độ nghi&ecirc;m trọng v&agrave; tần suất c&aacute;c cuộc tấn c&ocirc;ng. Phản hồi sinh học bao gồm h&igrave;nh ảnh hướng dẫn để l&agrave;m tăng nhiệt độ của b&agrave;n tay v&agrave; b&agrave;n ch&acirc;n, h&iacute;t thở s&acirc;u v&agrave; b&agrave;i tập thư gi&atilde;n kh&aacute;c. B&aacute;c sĩ c&oacute; thể đề nghị một liệu ph&aacute;p c&oacute; thể gi&uacute;p t&igrave;m hiểu c&aacute;c kỹ thuật phản hồi sinh học. S&aacute;ch v&agrave; băng đĩa cũng c&oacute; sẵn.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Niacin.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Niacin, c&ograve;n được gọi l&agrave; vitamin B-3, nguy&ecirc;n nh&acirc;n l&agrave;m gi&atilde;n mạch m&aacute;u, tăng lưu lượng m&aacute;u đến da. Niacin bổ sung c&oacute; thể hữu &iacute;ch trong điều trị Raynaud, mặc d&ugrave; bổ sung niacin c&oacute; thể c&oacute; t&aacute;c dụng phụ.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Đối ph&oacute; với những căng thẳng v&agrave; phiền to&aacute;i của Raynaud mất ki&ecirc;n nhẫn v&agrave; nỗ lực. L&agrave;m việc với b&aacute;c sĩ để quản l&yacute; t&igrave;nh trạng v&agrave; duy tr&igrave; một th&aacute;i độ t&iacute;ch cực. Đa số những người bị Raynaud đ&aacute;p ứng với điều trị.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Ph&ograve;ng chống</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Raynaud l&agrave; một điều kiện c&oacute; thể cần phải quản l&yacute; cho cuộc sống khi n&oacute; ph&aacute;t triển. Nhưng c&oacute; những c&aacute;ch để gi&uacute;p ngăn chặn c&aacute;c cuộc tấn c&ocirc;ng:</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Mặc ấm ngo&agrave;i trời.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> V&agrave;o m&ugrave;a đ&ocirc;ng, đội mũ, khăn, tất v&agrave; khởi động, găng tay hoặc bao tay khi đi ra ngo&agrave;i. Đặt ch&uacute;ng trước khi đi ra ngo&agrave;i. Mũ l&agrave; quan trọng bởi v&igrave; bị mất một lượng lớn nhiệt cơ thể qua đầu. Mặc một chiếc găng ngăn chặn kh&ocirc;ng kh&iacute; lạnh đạt đến b&agrave;n tay. Mang che tai v&agrave; mặt nạ nếu đầu mũi v&agrave; d&aacute;i tai rất nhạy cảm với lạnh. Khởi n&oacute;ng xe cho một v&agrave;i ph&uacute;t trước khi l&aacute;i xe trong thời tiết lạnh.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">H&atilde;y đề ph&ograve;ng trong nh&agrave;.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Mang vớ. Khi d&ugrave;ng thực phẩm trong tủ lạnh hoặc tủ đ&aacute;, đeo găng tay hoặc găng tay bao l&ograve;. Một số người thấy n&oacute; hữu &iacute;ch để mang găng tay v&agrave; tất ngủ trong m&ugrave;a đ&ocirc;ng. Bởi v&igrave; điều h&ograve;a nhiệt độ c&oacute; thể g&acirc;y ra c&aacute;c cuộc tấn c&ocirc;ng, thiết lập điều h&ograve;a kh&ocirc;ng kh&iacute; tới một nhiệt độ ấm hơn c&oacute; thể gi&uacute;p ngăn ngừa c&aacute;c cuộc tấn c&ocirc;ng. </span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\">Xem x&eacute;t việc chuyển đến một địa điểm c&oacute; kh&iacute; hậu &ocirc;n h&ograve;a hơn.</span></b><span lang=\"EN-US\" style=\"font-family: Arial;\"> Di chuyển đến kh&iacute; hậu ấm hơn c&oacute; thể gi&uacute;p những người nặng Raynaud. Tuy nhi&ecirc;n, Raynaud c&oacute; thể xảy ra ngay cả trong v&ugrave;ng kh&iacute; hậu ấm &aacute;p hơn khi giảm nhiệt độ.</span></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: right;\"><b style=\"\"><i style=\"\"><span lang=\"EN-US\" style=\"font-family: Arial;\"><span style=\"color: rgb(128, 0, 0);\"></span></span></i></b></p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\">&nbsp;</p>\r\n<p style=\"margin: 6pt 0cm 0.0001pt; line-height: 150%; text-align: justify;\"><span lang=\"EN-US\" style=\"font-family: Arial;\">&nbsp;</span></p></span>\n', 2);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(26, 'Bệnh tim', '\n   <h1 class=\"title\">Bệnh tim</h1>   <span class=\"des\">Bệnh tim! Bệnh tim là kẻ giết người số 1 trên toàn thế giới của đàn ông và phụ nữ, kể cả ở Hoa Kỳ. Ví dụ, bệnh tim chịu trách nhiệm cho 40 phần trăm của tất cả các ca tử vong tại Hoa Kỳ.</span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh tim l&agrave; một kh&aacute;i niệm rộng được sử dụng để m&ocirc; tả một loạt c&aacute;c bệnh c&oacute; ảnh hưởng đến tim, v&agrave; trong một số trường hợp c&aacute;c mạch m&aacute;u. C&aacute;c bệnh kh&aacute;c nhau nằm dưới sự bảo trợ của bệnh tim bao gồm bệnh của c&aacute;c mạch m&aacute;u, chẳng hạn như bệnh động mạch v&agrave;nh; vấn đề về nhịp tim (loạn nhịp tim) v&agrave; khuyết tật tim khi sinh (dị tật tim bẩm sinh). </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thuật ngữ &quot;bệnh tim&quot; thường được d&ugrave;ng lẫn lộn với &quot;bệnh tim mạch&quot; - một thuật ngữ thường d&ugrave;ng để chỉ c&aacute;c điều kiện li&ecirc;n quan đến việc thu hẹp hoặc tắc mạch m&aacute;u c&oacute; thể dẫn đến một cơn đau tim, đau ngực (đau thắt ngực) hoặc đột quỵ. C&aacute;c trung t&acirc;m điều kiện, chẳng hạn như nhiễm tr&ugrave;ng v&agrave; c&aacute;c điều kiện ảnh hưởng đến cơ tim, van hay nhịp đập cũng được coi l&agrave; h&igrave;nh thức của bệnh tim. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh tim l&agrave; kẻ giết người số 1 tr&ecirc;n to&agrave;n thế giới của đ&agrave;n &ocirc;ng v&agrave; phụ nữ, kể cả ở Hoa Kỳ. V&iacute; dụ, bệnh tim chịu tr&aacute;ch nhiệm cho 40 phần trăm của tất cả c&aacute;c ca tử vong tại Hoa Kỳ, hơn tất cả c&aacute;c dạng bệnh ung thư cộng lại. Nhiều dạng bệnh tim c&oacute; thể ngăn ngừa hoặc điều trị với sự lựa chọn lối sống l&agrave;nh mạnh v&agrave; chế độ ăn uống v&agrave; tập thể dục. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tr&aacute;i tim c&oacute; triệu chứng bệnh kh&aacute;c nhau, t&ugrave;y thuộc v&agrave;o loại bệnh tim c&oacute;. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng của bệnh tim c&oacute; triệu chứng mạch m&aacute;u (Bệnh tim mạch)</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh tim mạch l&agrave; do mạch m&aacute;u bị hẹp, bị chặn hoặc cứng l&agrave;m cho tim, n&atilde;o<span style=\"\">&nbsp; </span>hoặc c&aacute;c bộ phận kh&aacute;c của cơ thể kh&ocirc;ng nhận đủ m&aacute;u. Triệu chứng bệnh tim mạch c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau ngực (đau thắt ngực).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau, t&ecirc;, yếu hoặc lạnh ở ch&acirc;n hoặc c&aacute;nh tay, nếu c&aacute;c mạch m&aacute;u ở những bộ phận của cơ thể thu hẹp.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; thể kh&ocirc;ng được chẩn đo&aacute;n mắc bệnh tim mạch cho đến khi nặng hơn t&igrave;nh trạng<span style=\"\">&nbsp; </span>đến mức c&oacute; một cơn đau tim, đau ngực (đau thắt ngực), đột quỵ, suy tim hoặc tử vong đột ngột. Điều quan trọng l&agrave; để xem c&aacute;c triệu chứng tim mạch v&agrave; thảo luận về bất kỳ vấn đề với b&aacute;c sĩ. Bệnh tim mạch đ&ocirc;i khi c&oacute; thể được t&igrave;m thấy với c&aacute;c chuyến thăm kh&aacute;m thường xuy&ecirc;n với b&aacute;c sĩ . </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Bệnh tim c&oacute; triệu chứng g&acirc;y ra do nhịp tim bất thường (loạn nhịp tim) </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Rối loạn nhịp tim l&agrave; một nhịp tim bất thường. Tr&aacute;i tim c&oacute; thể đập qu&aacute; nhanh, qu&aacute; chậm, hoặc đột xuất nếu c&oacute; một chứng loạn nhịp tim. Triệu chứng loạn nhịp tim c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một rung cảm trong lồng ngực.<span style=\"\">&nbsp; </span></span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một nhịp tim đua (nhịp tim nhanh).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một nhịp tim chậm (nhịp tim chậm).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau ngực. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hoa mắt. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ch&oacute;ng mặt. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ngất xỉu (ngất) hoặc gần ngất.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Bệnh tim c&oacute; triệu chứng g&acirc;y ra do khuyết tật tim </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khuyết tật tim bẩm sinh nghi&ecirc;m trọng - một khiếm khuyết khi sinh - thường trở n&ecirc;n r&otilde; r&agrave;ng trong v&agrave;i giờ đầu ti&ecirc;n, ng&agrave;y, tuần v&agrave; th&aacute;ng của cuộc sống. Triệu chứng tim khiếm khuyết c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Da m&agrave;u nhạt x&aacute;m hoặc xanh (xanh t&iacute;m).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ph&ugrave; ch&acirc;n, bụng, v&ugrave;ng quanh mắt.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở trong khi ăn, dẫn đến giảm c&acirc;n. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c khuyết tật tim bẩm sinh &iacute;t nghi&ecirc;m trọng thường kh&ocirc;ng được chẩn đo&aacute;n cho đến khi trong thời thơ ấu sau n&agrave;y, hoặc thậm ch&iacute; cả tuổi trưởng th&agrave;nh. C&aacute;c dấu hiệu v&agrave; triệu chứng của dị tật tim bẩm sinh thường kh&ocirc;ng phải l&agrave; ngay lập tức đe dọa t&iacute;nh mạng bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Dễ d&agrave;ng trở th&agrave;nh hụt hơi trong khi luyện tập hoặc hoạt động. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Dễ d&agrave;ng mệt mỏi trong khi luyện tập hoặc hoạt động. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">T&iacute;ch tụ chất lỏng trong tim hoặc phổi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ph&ugrave; ở tay, mắt c&aacute; ch&acirc;n hoặc b&agrave;n ch&acirc;n. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tr&aacute;i tim c&oacute; triệu chứng bệnh g&acirc;y ra do cơ tim d&agrave;y (bệnh cơ tim) </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh cơ tim l&agrave; d&agrave;y v&agrave; cứng cơ tim. Trong giai đoạn đầu của bệnh cơ tim, c&oacute; thể kh&ocirc;ng c&oacute; triệu chứng. Khi t&igrave;nh trạng bệnh nặng hơn, c&aacute;c triệu chứng bệnh cơ tim bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở với gắng sức hoặc thậm ch&iacute; nghỉ ngơi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ph&ugrave; ch&acirc;n, mắt c&aacute; ch&acirc;n v&agrave; b&agrave;n ch&acirc;n. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đầy hơi (chướng) bụng với chất lỏng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mệt mỏi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng thường xuy&ecirc;n cảm thấy tim đập nhanh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ch&oacute;ng mặt, hoa mắt v&agrave; ngất xỉu. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Bệnh tim c&oacute; triệu chứng g&acirc;y ra bởi nhiễm tr&ugrave;ng tim </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; ba loại bệnh tim: Bệnh vi&ecirc;m m&agrave;ng ngo&agrave;i tim, c&oacute; ảnh hưởng đến c&aacute;c m&ocirc; xung quanh tr&aacute;i tim (m&agrave;ng ngo&agrave;i tim); Bệnh vi&ecirc;m cơ tim, ảnh hưởng tới lớp cơ ở giữa những bức th&agrave;nh của tr&aacute;i tim (cơ tim); v&agrave; vi&ecirc;m nội t&acirc;m mạc, ảnh hưởng đến c&aacute;c m&agrave;ng tế b&agrave;o b&ecirc;n trong ph&acirc;n c&aacute;ch c&aacute;c buồng v&agrave; van tim (m&agrave;ng trong tim). Thay đổi một ch&uacute;t với từng loại nhiễm tr&ugrave;ng, triệu chứng nhiễm tr&ugrave;ng c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sốt.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điểm yếu hay mệt mỏi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ph&ugrave; ở ch&acirc;n hoặc bụng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thay đổi nhịp tim. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ho khan hoặc ho dai dẳng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Da ph&aacute;t ban hoặc c&aacute;c điểm bất thường.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Tr&aacute;i tim c&oacute; triệu chứng bệnh g&acirc;y ra bởi bệnh van tim </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tim c&oacute; bốn van - van động mạch chủ, van hai l&aacute;, van động mạch phổi v&agrave; van ba l&aacute; - mở v&agrave; đ&oacute;ng chảy m&aacute;u trực tiếp th&ocirc;ng qua tr&aacute;i tim. Van c&oacute; thể bị hư hại bởi một loạt c&aacute;c điều kiện dẫn đến thu hẹp (hẹp), bị r&ograve; rỉ (hở) hoặc đ&oacute;ng kh&ocirc;ng đ&uacute;ng c&aacute;ch (sa). T&ugrave;y thuộc v&agrave;o van hoạt động kh&ocirc;ng đ&uacute;ng, van tim c&oacute; triệu chứng bệnh th&ocirc;ng thường bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mệt mỏi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhịp tim bất thường hoặc tiếng thổi.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ph&ugrave; ch&acirc;n hoặc mắt c&aacute; ch&acirc;n. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau ngực.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ngất xỉu (syncope).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Đến gặp b&aacute;c sĩ khi</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">T&igrave;m kiếm sự chăm s&oacute;c y tế khẩn cấp nếu c&oacute; những triệu chứng bệnh tim: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau ngực.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bất tỉnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh tim l&agrave; dễ d&agrave;ng để điều trị khi n&oacute; được ph&aacute;t hiện sớm, n&oacute;i chuyện với b&aacute;c sĩ về bất kỳ mối quan t&acirc;m của qu&yacute; vị về sức khỏe tim. Nếu kh&ocirc;ng c&oacute; bệnh tim, nhưng c&oacute; quan t&acirc;m về ph&aacute;t triển bệnh tim, h&atilde;y n&oacute;i chuyện với b&aacute;c sĩ về c&aacute;c bước c&oacute; thể l&agrave;m để giảm nguy cơ bệnh tim. Điều n&agrave;y đặc biệt đ&uacute;ng nếu c&oacute; tiền sử gia đ&igrave;nh mắc bệnh tim. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu nghĩ rằng c&oacute; thể c&oacute; bệnh tim mạch dựa tr&ecirc;n dấu hiệu mới hoặc triệu chứng<span style=\"\">&nbsp; </span>đ&atilde; c&oacute;, l&agrave;m một cuộc hẹn để gặp b&aacute;c sĩ. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Để hiểu được bệnh tim, biết bao c&ocirc;ng tr&igrave;nh về tim c&oacute; thể gi&uacute;p. Tr&aacute;i tim l&agrave; một m&aacute;y bơm. Đ&oacute; l&agrave; một bộ phận cơ về k&iacute;ch thước của b&agrave;n tay v&agrave; nằm hơi tr&aacute;i của trung t&acirc;m trong lồng ngực. Tr&aacute;i tim được chia th&agrave;nh, ph&iacute;a b&ecirc;n tr&aacute;i - bộ phận bảo vệ m&aacute;u gi&agrave;u oxy từ trộn với oxy với m&aacute;u ngh&egrave;o oxy. M&aacute;u ngh&egrave;o oxy, &quot;m&aacute;u m&agrave;u xanh,&quot; trở về tim sau khi lưu th&ocirc;ng qua cơ thể. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ph&iacute;a b&ecirc;n phải của tim, bao gồm t&acirc;m nhĩ phải v&agrave; t&acirc;m thất, thu thập v&agrave; bơm m&aacute;u l&ecirc;n phổi qua động mạch phổi. Phổi l&agrave;m mới m&aacute;u với một nguồn cung cấp mới của oxy, l&agrave;m cho n&oacute; chuyển sang m&agrave;u đỏ. M&aacute;u gi&agrave;u oxy sau đ&oacute; đi v&agrave;o ph&iacute;a b&ecirc;n tr&aacute;i của tim, bao gồm c&aacute;c t&acirc;m nhĩ tr&aacute;i v&agrave; t&acirc;m thất, v&agrave; được bơm qua c&aacute;c động mạch chủ cho cơ thể để cung cấp cho c&aacute;c m&ocirc; trong cơ thể với oxy v&agrave; chất dinh dưỡng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bốn van trong tim<span style=\"\">&nbsp; </span>giữ cho m&aacute;u di chuyển đ&uacute;ng c&aacute;ch. C&aacute;c van ba l&aacute;, hai l&aacute;, phổi v&agrave; động mạch chủ l&agrave;m việc giống như cửa v&agrave;o h&agrave;ng r&agrave;o. Chỉ c&oacute; một c&aacute;ch mở v&agrave; chỉ khi bị đẩy v&agrave;o. Mỗi van mở v&agrave; đ&oacute;ng một lần cho mỗi nhịp đập của tim - hay khoảng một lần mỗi gi&acirc;y, trong khi đ&oacute;ng ở phần c&ograve;n lại. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một tr&aacute;i tim đập hợp đồng v&agrave; thư gi&atilde;n. Co được gọi l&agrave; t&acirc;m thu, v&agrave; thư gi&atilde;n được gọi l&agrave; t&acirc;m trương. Trong thời gian t&acirc;m thu, t&acirc;m thất hợp đồng, bơm m&aacute;u v&agrave;o c&aacute;c mạch m&aacute;u phổi v&agrave; cơ thể. Hợp đồng t&acirc;m thất b&ecirc;n phải một ch&uacute;t trước khi t&acirc;m thất tr&aacute;i kh&ocirc;ng. T&acirc;m thất sau đ&oacute; thư gi&atilde;n trong thời gian t&acirc;m trương v&agrave; đầy m&aacute;u đến từ c&aacute;c ngăn tr&ecirc;n, t&acirc;m nhĩ tr&aacute;i v&agrave; phải. Sau đ&oacute; bắt đầu chu kỳ tr&ecirc;n một lần nữa. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tr&aacute;i tim cũng c&oacute; hệ thống d&acirc;y điện giữ n&oacute; đập. Xung điện bắt đầu cao trong t&acirc;m nhĩ phải v&agrave; đi qua c&aacute;c con đường chuy&ecirc;n ng&agrave;nh đến c&aacute;c t&acirc;m thất, cung cấp t&iacute;n hiệu cho bơm. Hệ thống dẫn tim đập trong một nhịp điệu phối hợp v&agrave; b&igrave;nh thường giữ cho m&aacute;u lưu th&ocirc;ng. Việc trao đổi li&ecirc;n tục của m&aacute;u gi&agrave;u oxy m&aacute;u ngh&egrave;o oxy gi&uacute;p cho cơ thể sống. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c nguy&ecirc;n nh&acirc;n g&acirc;y bệnh tim kh&aacute;c nhau t&ugrave;y theo loại bệnh tim. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n của bệnh tim mạch </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong khi bệnh tim mạch c&oacute; thể tham khảo nhiều loại kh&aacute;c nhau của c&aacute;c vấn đề tim hay mạch m&aacute;u, thuật ngữ n&agrave;y thường được sử dụng c&oacute; nghĩa l&agrave; thiệt hại cho tim hay mạch m&aacute;u của xơ vữa động mạch, một sự t&iacute;ch tụ của mảng b&aacute;m chất b&eacute;o trong động mạch. Đ&acirc;y l&agrave; một bệnh ảnh hưởng đến động mạch. Động mạch l&agrave; mạch m&aacute;u mang oxy v&agrave; chất dinh dưỡng từ tr&aacute;i tim với phần c&ograve;n lại của cơ thể. Động mạch khỏe mạnh linh hoạt v&agrave; mạnh mẽ. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Theo thời gian, tuy nhi&ecirc;n, qu&aacute; nhiều &aacute;p lực trong động mạch c&oacute; thể l&agrave;m cho c&aacute;c bức th&agrave;nh d&agrave;y v&agrave; cứng - đ&ocirc;i khi hạn chế lưu lượng m&aacute;u đến c&aacute;c bộ phận v&agrave; c&aacute;c m&ocirc;. Qu&aacute; tr&igrave;nh n&agrave;y được gọi l&agrave; xơ cứng động mạch. Xơ vữa động mạch l&agrave; dạng phổ biến nhất của chứng rối loạn n&agrave;y. Xơ vữa động mạch cũng l&agrave; nguy&ecirc;n nh&acirc;n phổ biến nhất của bệnh tim mạch, v&agrave; n&oacute; g&acirc;y ra bởi một chế độ ăn uống kh&ocirc;ng l&agrave;nh mạnh, thiếu tập thể dục, thừa c&acirc;n v&agrave; h&uacute;t thuốc. Tất cả những yếu tố nguy cơ ch&iacute;nh cho ph&aacute;t triển v&agrave; xơ vữa động mạch, bệnh tim mạch. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n của rối loạn nhịp tim </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nguy&ecirc;n nh&acirc;n thường gặp của nhịp tim bất thường (chứng loạn nhịp tim), hoặc c&aacute;c điều kiện c&oacute; thể dẫn đến chứng loạn nhịp tim bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khuyết tật tim khi sinh ra (dị tật tim bẩm sinh). </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh động mạch v&agrave;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Cao huyết &aacute;p.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh tiểu đường. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">H&uacute;t thuốc.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Sử dụng qu&aacute; nhiều rượu hoặc c&agrave; ph&ecirc;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Lạm dụng ma t&uacute;y.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Stress. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một số toa thuốc, bổ sung chế độ ăn uống v&agrave; c&aacute;c biện ph&aacute;p thảo dược.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Van tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một người khỏe mạnh với một tr&aacute;i tim khỏe mạnh b&igrave;nh thường, một chứng loạn nhịp tim g&acirc;y tử vong ph&aacute;t triển m&agrave; kh&ocirc;ng c&oacute; một số k&iacute;ch hoạt ở b&ecirc;n ngo&agrave;i, chẳng hạn như l&agrave; một c&uacute; sốc điện hoặc sử dụng c&aacute;c loại thuốc bất hợp ph&aacute;p. Chủ yếu bởi v&igrave; một người tim khỏe mạnh l&agrave; miễn từ bất kỳ điều kiện bất thường g&acirc;y ra chứng loạn nhịp tim, c&oacute; lẽ l&agrave; một khu vực của m&ocirc; sẹo. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tuy nhi&ecirc;n, một tr&aacute;i tim của bệnh hoặc biến dạng, c&aacute;c xung điện của tim c&oacute; thể kh&ocirc;ng được bắt đầu hoặc đi qua tim, l&agrave;m rối loạn nhịp tim nhiều khả năng để ph&aacute;t triển. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n của khuyết tật tim </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tr&aacute;i tim lỗi thường ph&aacute;t triển trong khi một em b&eacute; vẫn c&ograve;n trong bụng mẹ. Khoảng một th&aacute;ng sau khi thụ thai, tim bắt đầu ph&aacute;t triển. Đ&oacute; l&agrave; v&agrave;o thời điểm dị tật tim c&oacute; thể bắt đầu h&igrave;nh th&agrave;nh. C&aacute;c nh&agrave; nghi&ecirc;n cứu kh&ocirc;ng chắc chắn ch&iacute;nh x&aacute;c những g&igrave; g&acirc;y ra lỗi bắt đầu, nhưng họ nghĩ rằng một số điều kiện y tế, thuốc men v&agrave; di truyền học c&oacute; thể đ&oacute;ng một vai tr&ograve;. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n của bệnh cơ tim </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nguy&ecirc;n nh&acirc;n ch&iacute;nh x&aacute;c của bệnh cơ tim, d&agrave;y l&ecirc;n hoặc mở rộng của cơ tim kh&ocirc;ng r&otilde;. C&oacute; ba loại bệnh cơ tim: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Bệnh cơ tim gi&atilde;n nở.</span></b><span style=\"font-family: Arial;\"> Đ&acirc;y l&agrave; loại phổ biến nhất của bệnh cơ tim. Trong rối loạn n&agrave;y, buồng bơm ch&iacute;nh - t&acirc;m thất tr&aacute;i to ra (gi&atilde;n ra), khả năng bơm trở n&ecirc;n kh&ocirc;ng mạnh mẽ hơn, v&agrave; m&aacute;u kh&ocirc;ng chảy một c&aacute;ch dễ d&agrave;ng th&ocirc;ng qua tr&aacute;i tim. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Bệnh cơ tim ph&igrave; đại.</span></b><span style=\"font-family: Arial;\"> Loại n&agrave;y bao gồm việc ph&aacute;t triển bất thường hoặc sự d&agrave;y l&ecirc;n của cơ tim, đặc biệt ảnh hưởng đến c&aacute;c cơ bắp của buồng tr&aacute;i tim bơm ch&iacute;nh. D&agrave;y xảy ra, tr&aacute;i tim c&oacute; xu hướng cứng lại v&agrave; k&iacute;ch thước của c&aacute;c khoang bơm c&oacute; thể co lại, cản trở khả năng của tim để cung cấp m&aacute;u cho cơ thể. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Bệnh cơ tim hạn chế.</span></b><span style=\"font-family: Arial;\"> Cơ tim ở những người bị bệnh cơ tim hạn chế trở n&ecirc;n cứng v&agrave; k&eacute;m đ&agrave;n hồi, c&oacute; nghĩa l&agrave; tim c&oacute; thể kh&ocirc;ng được mở rộng v&agrave; đầy m&aacute;u giữa c&aacute;c nhịp tim. Đ&acirc;y cũng l&agrave; loại phổ biến nhất của bệnh cơ tim v&agrave; c&oacute; thể xảy ra kh&ocirc;ng c&oacute; l&yacute; do được biết đến. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n của bệnh vi&ecirc;m tim </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhiễm tr&ugrave;ng tim, chẳng hạn như vi&ecirc;m m&agrave;ng ngo&agrave;i tim, vi&ecirc;m nội t&acirc;m mạc v&agrave; vi&ecirc;m cơ tim, g&acirc;y ra khi một chất k&iacute;ch th&iacute;ch, chẳng hạn như virus, vi khuẩn, h&oacute;a học đến cơ tim. Những nguy&ecirc;n nh&acirc;n phổ biến nhất của bệnh tim bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Vi khuẩn.</span></b><span style=\"font-family: Arial;\"> Vi&ecirc;m nội t&acirc;m mạc c&oacute; thể được g&acirc;y ra bởi một số vi khuẩn v&agrave;o m&aacute;u. C&aacute;c vi khuẩn c&oacute; thể nhập v&agrave;o d&ograve;ng m&aacute;u th&ocirc;ng qua c&aacute;c hoạt động h&agrave;ng ng&agrave;y, như ăn uống hay đ&aacute;nh răng, đặc biệt l&agrave; nếu c&oacute; sức khỏe răng miệng k&eacute;m. Vi&ecirc;m cơ tim cũng c&oacute; thể được g&acirc;y ra bởi một loại vi khuẩn truyền qua đ&aacute;nh dấu v&agrave;o đ&oacute; l&agrave; chịu tr&aacute;ch nhiệm về bệnh Lyme.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Virus.</span></b><span style=\"font-family: Arial;\"> Nhiễm tr&ugrave;ng tim c&oacute; thể được g&acirc;y ra bởi virus, trong đ&oacute; một số nguy&ecirc;n nh&acirc;n c&uacute;m (coxsackievirus B v&agrave; adenovirus), ph&aacute;t ban parvovirus B19, nhiễm tr&ugrave;ng đường ti&ecirc;u h&oacute;a (echovirus), bạch cầu đơn nh&acirc;n (Epstein-Barr virus) v&agrave; bệnh sởi (rubella). C&aacute;c virus được kết hợp với nhiễm tr&ugrave;ng qua đường t&igrave;nh dục cũng c&oacute; thể đi du lịch đến cơ tim v&agrave; g&acirc;y nhiễm tr&ugrave;ng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">K&yacute; sinh tr&ugrave;ng.</span></b><span style=\"font-family: Arial;\"> Trong số c&aacute;c k&yacute; sinh tr&ugrave;ng c&oacute; thể g&acirc;y nhiễm tr&ugrave;ng tim Trypanosoma cruzi, toxoplasma v&agrave; một số được truyền bởi c&ocirc;n tr&ugrave;ng v&agrave; c&oacute; thể g&acirc;y ra một t&igrave;nh trạng gọi l&agrave; Chagas. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc men c&oacute; thể g&acirc;y ra một phản ứng dị ứng hoặc độc hại.</span></b><span style=\"font-family: Arial;\"> Ch&uacute;ng bao gồm thuốc kh&aacute;ng sinh, chẳng hạn như thuốc penicillin v&agrave; sulfonamide, cũng như một số chất bất hợp ph&aacute;p, chẳng hạn như cocaine. C&aacute;c kim được sử dụng để quản l&yacute; thuốc, thuốc bất hợp ph&aacute;p cũng c&oacute; thể truyền virus hoặc vi khuẩn c&oacute; thể g&acirc;y nhiễm tr&ugrave;ng tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">C&aacute;c bệnh kh&aacute;c.</span></b><span style=\"font-family: Arial;\"> Ch&uacute;ng bao gồm lupus, c&aacute;c bệnh m&ocirc; li&ecirc;n kết, vi&ecirc;m mạch m&aacute;u (vi&ecirc;m mạch), v&agrave; t&igrave;nh trạng vi&ecirc;m hiếm, chẳng hạn như u hạt Wegener\'s.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n của bệnh van tim </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; nhiều nguy&ecirc;n nh&acirc;n g&acirc;y bệnh van tim. Bốn van trong tim <span style=\"\">&nbsp;</span>giữ cho m&aacute;u chảy đ&uacute;ng hướng. C&oacute; thể được sinh ra với bệnh van tim, hoặc c&aacute;c van c&oacute; thể bị hỏng bởi c&aacute;c điều kiện như sốt thấp khớp, nhiễm tr&ugrave;ng (nhiễm tr&ugrave;ng vi&ecirc;m nội t&acirc;m mạc), rối loạn m&ocirc; li&ecirc;n kết, v&agrave; một số loại thuốc hoặc phương ph&aacute;p điều trị bức xạ đối với bệnh ung thư. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Yếu tố nguy cơ bệnh tim bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tuổi.</span></b><span style=\"font-family: Arial;\"> Đơn giản chỉ cần nhận nguy cơ bị hư hại cơ tim v&agrave; hẹp động mạch v&agrave; l&agrave;m suy yếu hoặc d&agrave;y l&ecirc;n, c&oacute; đ&oacute;ng g&oacute;p cho bệnh tim. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Giới t&iacute;nh.</span></b><span style=\"font-family: Arial;\"> Đ&agrave;n &ocirc;ng thường c&oacute; nguy cơ mắc bệnh tim. Tuy nhi&ecirc;n, nguy cơ gia tăng phụ nữ sau khi m&atilde;n kinh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Lịch sử gia đ&igrave;nh.</span></b><span style=\"font-family: Arial;\"> Lịch sử gia đ&igrave;nh mắc bệnh tim tăng nguy cơ bệnh mạch v&agrave;nh, đặc biệt l&agrave; nếu cha mẹ ph&aacute;t triển n&oacute; ở độ tuổi sớm (trước tuổi 55 với người anh em nam, người cha v&agrave; 65 cho một phụ nữ như mẹ, chị g&aacute;i). </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">H&uacute;t thuốc.</span></b><span style=\"font-family: Arial;\"> Nicotine l&agrave;m co thắt mạch m&aacute;u, v&agrave; carbon monoxide c&oacute; thể l&agrave;m hỏng lớp l&oacute;t b&ecirc;n trong, l&agrave;m cho ch&uacute;ng dễ bị xơ vữa động mạch. Tim tấn c&ocirc;ng phổ biến hơn ở những người h&uacute;t thuốc so với người kh&ocirc;ng h&uacute;t thuốc. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Chế độ ăn uống ngh&egrave;o.</span></b><span style=\"font-family: Arial;\"> Một chế độ ăn uống liều lượng cao với muối, chất b&eacute;o v&agrave; cholesterol c&oacute; thể đ&oacute;ng g&oacute;p v&agrave;o sự ph&aacute;t triển của bệnh tim. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Cao huyết &aacute;p.</span></b><span style=\"font-family: Arial;\"> Kh&ocirc;ng kiểm so&aacute;t được huyết &aacute;p cao c&oacute; thể dẫn đến xơ cứng v&agrave; d&agrave;y của c&aacute;c động mạch, thu hẹp c&aacute;c mạch m&aacute;u. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Cholesterol trong m&aacute;u cao.</span></b><span style=\"font-family: Arial;\"> Mức cholesterol trong m&aacute;u cao c&oacute; thể l&agrave;m tăng nguy cơ h&igrave;nh th&agrave;nh c&aacute;c mảng xơ vữa động mạch. Mảng c&oacute; thể được g&acirc;y ra bởi một cấp độ cao lipoprotein mật độ thấp (LDLs), được gọi l&agrave; cholesterol &quot;xấu&quot;, hoặc l&agrave; mức thấp lipoprotein mật độ cao (HDLs), được gọi l&agrave; cholesterol &quot;tốt&quot;. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Bệnh tiểu đường.</span></b><span style=\"font-family: Arial;\"> Bệnh tiểu đường l&agrave;m tăng nguy cơ bệnh tim. Cả hai điều kiện chia sẻ c&aacute;c yếu tố nguy cơ tương tự, chẳng hạn như b&eacute;o ph&igrave; v&agrave; huyết &aacute;p cao. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Bệnh b&eacute;o ph&igrave;.</span></b><span style=\"font-family: Arial;\"> Vượt qu&aacute; trọng lượng thường nặng hơn yếu tố nguy cơ kh&aacute;c. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Kh&ocirc;ng hoạt động vật l&yacute;.</span></b><span style=\"font-family: Arial;\"> Thiếu tập thể dục cũng được kết hợp với nhiều h&igrave;nh thức của bệnh tim v&agrave; một số c&aacute;c yếu tố nguy cơ kh&aacute;c của n&oacute;. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Căng thẳng cao độ.</span></b><span style=\"font-family: Arial;\"> Kh&ocirc;ng giải tỏa được căng thẳng trong cuộc sống c&oacute; thể l&agrave;m hỏng c&aacute;c động mạch cũng như l&agrave;m trầm trọng th&ecirc;m c&aacute;c yếu tố nguy cơ kh&aacute;c của bệnh tim. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Vệ sinh ngh&egrave;o.</span></b><span style=\"font-family: Arial;\"> Kh&ocirc;ng thường xuy&ecirc;n rửa tay v&agrave; th&oacute;i quen kh&aacute;c c&oacute; thể gi&uacute;p ngăn ngừa nhiễm virus hoặc vi khuẩn c&oacute; thể đặt v&agrave;o nguy cơ nhiễm tr&ugrave;ng tim, đặc biệt l&agrave; nếu đ&atilde; c&oacute; một bệnh tim tiềm ẩn. C&aacute;c nh&agrave; nghi&ecirc;n cứu cũng tin rằng sức khỏe răng miệng k&eacute;m c&oacute; thể g&oacute;p phần v&agrave;o bệnh tim. Mầm bệnh về răng v&agrave; nướu răng c&oacute; thể đi từ miệng đến tr&aacute;i tim m&igrave;nh, c&oacute; khả năng xấu đi bệnh động mạch v&agrave;nh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một trong những biến chứng thường gặp nhất của bệnh tim l&agrave; ngất. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Suy tim.</span></b><span style=\"font-family: Arial;\"> Suy tim xảy ra khi tim kh&ocirc;ng thể bơm đủ m&aacute;u để đ&aacute;p ứng nhu cầu của cơ thể. Theo thời gian, tr&aacute;i tim kh&ocirc;ng c&ograve;n c&oacute; thể theo kịp với nhu cầu b&igrave;nh thường được đặt tr&ecirc;n đ&oacute;. T&acirc;m thất c&oacute; thể trở n&ecirc;n cứng v&agrave; kh&ocirc;ng hoạt động đ&uacute;ng giữa c&aacute;c nhịp đập. Ngo&agrave;i ra, cơ tim c&oacute; thể suy yếu, v&agrave; k&eacute;o d&agrave;i c&aacute;c t&acirc;m thất (gi&atilde;n ra) đến điểm tr&aacute;i tim kh&ocirc;ng thể bơm m&aacute;u hiệu quả khắp cơ thể . Suy tim c&oacute; thể l&agrave; kết quả của nhiều dạng bệnh tim, bao gồm cả c&aacute;c khuyết tật tim, bệnh tim mạch, bệnh van tim, nhiễm tr&ugrave;ng tim hoặc bệnh cơ tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c biến chứng kh&aacute;c của bệnh tim bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đau tim (tim tấn c&ocirc;ng).</span></b><span style=\"font-family: Arial;\"> Bệnh động mạch v&agrave;nh c&oacute; thể g&acirc;y ra một cơn đau tim. Tim tấn c&ocirc;ng thường xảy ra khi một khối m&aacute;u đ&ocirc;ng chặn d&ograve;ng chảy của m&aacute;u qua động mạch v&agrave;nh - một mạch m&aacute;u nu&ocirc;i m&aacute;u đến một phần của cơ tim. Bị gi&aacute;n đoạn lưu lượng m&aacute;u tới tim c&oacute; thể thiệt hại hoặc ph&aacute; hủy một phần của cơ tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đột quỵ.</span></b><span style=\"font-family: Arial;\"> Bệnh tim mạch c&oacute; thể g&acirc;y ra một cơn đột quỵ thiếu m&aacute;u cục bộ, trong đ&oacute; sẽ xảy ra khi c&aacute;c động mạch l&ecirc;n n&atilde;o bị hẹp hay tắc v&agrave; qu&aacute; &iacute;t m&aacute;u đến bộ n&atilde;o. Đột quỵ l&agrave; một cấp cứu y tế - m&ocirc; n&atilde;o bắt đầu chết chỉ trong v&ograve;ng v&agrave;i ph&uacute;t của một cơn đột quỵ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ph&igrave;nh mạch.</span></b><span style=\"font-family: Arial;\"> Bệnh tim mạch cũng c&oacute; thể g&acirc;y ra chứng ph&igrave;nh động mạch, một biến chứng nghi&ecirc;m trọng c&oacute; thể xảy ra bất cứ nơi n&agrave;o trong cơ thể Ph&igrave;nh mạch l&agrave; một phần lồi ra ở th&agrave;nh động mạch. Nếu vỡ ph&igrave;nh mạch, c&oacute; thể phải đối mặt với chảy m&aacute;u đe dọa t&iacute;nh mạng. Mặc d&ugrave; điều n&agrave;y thường l&agrave; một sự kiện thảm họa bất ngờ, một sự r&ograve; rỉ chậm l&agrave; c&oacute; thể. Nếu một cục m&aacute;u đ&ocirc;ng trong một ra khỏi ph&igrave;nh mạch, n&oacute; c&oacute; thể chặn một động mạch ở một điểm kh&aacute;c.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Bệnh động mạch ngoại bi&ecirc;n.</span></b><span style=\"font-family: Arial;\"> C&aacute;c xơ vữa động mạch tương tự c&oacute; thể dẫn đến bệnh động mạch v&agrave;nh cũng c&oacute; thể dẫn đến bệnh động mạch ngoại vi. Khi ph&aacute;t triển bệnh động mạch ngoại bi&ecirc;n (PAD), tứ chi<span style=\"\">&nbsp; </span>- thường l&agrave; đ&ocirc;i ch&acirc;n - kh&ocirc;ng nhận được lưu lượng m&aacute;u đủ để theo kịp với nhu cầu. Điều n&agrave;y g&acirc;y ra c&aacute;c triệu chứng, đau ch&acirc;n đ&aacute;ng ch&uacute; &yacute; nhất khi đi bộ (claudication).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ngừng tim đột ngột.</span></b><span style=\"font-family: Arial;\"> Ngừng tim đột ngột mất bất ngờ đột ngột thở v&agrave; tim đập, chức năng v&agrave; &yacute; thức. Ngừng tim đột ngột thường l&agrave; kết quả của sự nhiễu loạn điện trong tr&aacute;i tim l&agrave;m gi&aacute;n đoạn h&agrave;nh động bơm v&agrave; l&agrave; nguy&ecirc;n nh&acirc;n ngăn chặn m&aacute;u với c&aacute;c phần c&ograve;n lại của cơ thể. Ngừng tim đột ngột gần như lu&ocirc;n lu&ocirc;n xảy ra trong bối cảnh của c&aacute;c vấn đề tim mạch kh&aacute;c cơ bản, đặc biệt l&agrave; bệnh động mạch v&agrave;nh. Ngừng tim đột ngột l&agrave; một cấp cứu y tế. Nếu kh&ocirc;ng được điều trị ngay lập tức, n&oacute; l&agrave; nghi&ecirc;m trọng dẫn đến tử vong đột ngột.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c x&eacute;t nghiệm sẽ cần để chẩn đo&aacute;n bệnh tim phụ thuộc v&agrave;o những điều kiện b&aacute;c sĩ nghĩ rằng c&oacute; thể c&oacute;. Kh&ocirc;ng c&oacute; vấn đề g&igrave; loại bệnh tim, b&aacute;c sĩ c&oacute; thể sẽ thực hiện kh&aacute;m thực thể v&agrave; hỏi về lịch sử y tế gia đ&igrave;nh v&agrave; c&aacute; nh&acirc;n trước khi l&agrave;m bất kỳ x&eacute;t nghiệm kh&aacute;c. C&aacute;c x&eacute;t nghiệm để chẩn đo&aacute;n bệnh tim c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">X&eacute;t nghiệm m&aacute;u.</span></b><span style=\"font-family: Arial;\"> C&oacute; thể cần phải c&oacute; m&aacute;u r&uacute;t ra v&agrave; thử nghiệm c&aacute;c chất trong m&aacute;u m&agrave; c&oacute; thể cho biết bị bệnh tim. B&aacute;c sĩ c&oacute; thể kiểm tra mức cholesterol v&agrave; chất b&eacute;o trung t&iacute;nh, số lượng tế b&agrave;o m&aacute;u, hoặc x&eacute;t nghiệm m&aacute;u kh&aacute;c m&agrave; c&oacute; thể cho thấy c&oacute; hại cho tim . </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Chụp X - quang.</span></b><span style=\"font-family: Arial;\"> Một h&igrave;nh ảnh được tạo ra bởi X - quang ở ngực. M&aacute;y X - quang tạo ra một vụ nổ nhỏ của bức xạ đi qua cơ thể v&agrave; tạo ra một h&igrave;nh ảnh tr&ecirc;n phim, tấm ảnh kỹ thuật số. Chụp X - quang cho thấy một h&igrave;nh ảnh của tr&aacute;i tim m&igrave;nh, phổi v&agrave; mạch m&aacute;u. N&oacute; c&oacute; thể tiết lộ nếu tr&aacute;i tim được mở rộng, một dấu hiệu của một số dạng bệnh tim. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Điện t&acirc;m đồ (ECG).</span></b><span style=\"font-family: Arial;\"> Trong thử nghiệm kh&ocirc;ng x&acirc;m lấn, một kỹ thuật vi&ecirc;n sẽ đặt đầu d&ograve; tr&ecirc;n ngực ghi lại c&aacute;c xung điện l&agrave;m cho tim đập. ECG hồ sơ c&aacute;c t&iacute;n hiệu điện v&agrave; c&oacute; thể gi&uacute;p b&aacute;c sĩ ph&aacute;t hiện bất thường trong nhịp tim v&agrave; cấu tr&uacute;c. C&oacute; thể c&oacute; một điện t&acirc;m đồ trong khi đang nghỉ, hoặc trong khi stress.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Holter theo d&otilde;i.</span></b><span style=\"font-family: Arial;\"> Một m&agrave;n h&igrave;nh Holter l&agrave; một thiết bị di động m&agrave;<span style=\"\">&nbsp; </span>mặc để ghi lại một ECG li&ecirc;n tục, thường l&agrave; từ 24 đến 72 giờ. theo d&otilde;i Holter được sử dụng để ph&aacute;t hiện nhịp tim bất thường đ&ocirc;i khi kh&ocirc;ng t&igrave;m thấy trong kiểm tra điện t&acirc;m đồ thường xuy&ecirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Si&ecirc;u &acirc;m tim.</span></b><span style=\"font-family: Arial;\"> Kh&ocirc;ng x&acirc;m lấn, trong đ&oacute; bao gồm si&ecirc;u &acirc;m ngực, cho thấy h&igrave;nh ảnh chi tiết của cấu tr&uacute;c tim v&agrave; chức năng. S&oacute;ng si&ecirc;u &acirc;m được truyền đi, v&agrave; tiếng vang của họ được ghi lại với một thiết bị gọi l&agrave; bộ chuyển đổi tổ chức b&ecirc;n ngo&agrave;i cơ thể. Một m&aacute;y t&iacute;nh sử dụng th&ocirc;ng tin từ c&aacute;c bộ chuyển đổi để tạo ra h&igrave;nh ảnh chuyển động tr&ecirc;n một m&agrave;n h&igrave;nh video. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong một số trường hợp trong đ&oacute; c&aacute;c h&igrave;nh ảnh từ một si&ecirc;u &acirc;m tim thường kh&ocirc;ng r&otilde; r&agrave;ng, b&aacute;c sĩ c&oacute; thể đề nghị một si&ecirc;u &acirc;m transesophageal. Trong kiểm tra n&agrave;y,<span style=\"\">&nbsp; </span>nuốt một ống linh hoạt c&oacute; chứa một bộ chuyển đổi nhỏ về k&iacute;ch thước của ng&oacute;n tay trỏ, hướng dẫn xuống cổ họng. C&aacute;c bộ chuyển đổi sẽ truyền tải h&igrave;nh ảnh của tr&aacute;i tim đến một m&agrave;n h&igrave;nh m&aacute;y t&iacute;nh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đặt ống th&ocirc;ng tim.</span></b><span style=\"font-family: Arial;\"> Trong thử nghiệm n&agrave;y, một ống ngắn (vỏ bọc) được đưa v&agrave;o tĩnh mạch hoặc động mạch ở ch&acirc;n (h&aacute;ng) hoặc c&aacute;nh tay. Một ống rỗng, linh hoạt (hướng dẫn ống th&ocirc;ng) sau đ&oacute; đưa v&agrave;o vỏ bọc n&agrave;y. Nhờ c&aacute;c h&igrave;nh ảnh X - ray tr&ecirc;n m&agrave;n h&igrave;nh, b&aacute;c sĩ hướng dẫn qua c&aacute;c ống th&ocirc;ng c&oacute; động mạch cho đến khi n&oacute; đạt đến tr&aacute;i tim. Những &aacute;p lực trong buồng tim c&oacute; thể đo được, v&agrave; chất nhuộm c&oacute; thể được ti&ecirc;m. Thuốc nhuộm c&oacute; thể được nh&igrave;n thấy tr&ecirc;n một tia X, gi&uacute;p b&aacute;c sĩ nh&igrave;n thấy lưu lượng m&aacute;u qua tim, mạch m&aacute;u v&agrave; c&aacute;c van để kiểm tra bất thường.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Sinh thiết.</span></b><span style=\"font-family: Arial;\"> Đ&ocirc;i khi, sinh thiết tim sẽ được thực hiện như một phần của th&ocirc;ng tim, đặc biệt l&agrave; nếu b&aacute;c sĩ nghi ngờ c&oacute; vi&ecirc;m tim v&agrave; đ&atilde; kh&ocirc;ng thể x&aacute;c nhận với c&aacute;c x&eacute;t nghiệm kh&aacute;c. Trong sinh thiết tim, một mẫu nhỏ của m&ocirc; tim được lấy ra qua ống th&ocirc;ng v&agrave; được gửi đến một ph&ograve;ng th&iacute; nghiệm để thử nghiệm.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Chụp cắt lớp vi t&iacute;nh tim (CT scan).</span></b><span style=\"font-family: Arial;\"> X&eacute;t nghiệm n&agrave;y thường được sử dụng để kiểm tra c&aacute;c rối loạn nhịp tim hoặc tim. Trong một CT scan tim, nằm tr&ecirc;n một bảng b&ecirc;n trong một m&aacute;y c&oacute; h&igrave;nh chiếc b&aacute;nh r&aacute;n. Một ống X - quang b&ecirc;n trong m&aacute;y quay xung quanh cơ thể v&agrave; thu thập h&igrave;nh ảnh của tim v&agrave; ngực. Một số t&igrave;m sự t&iacute;ch tụ can-xi trong động mạch, m&agrave; c&oacute; thể cho thấy đang c&oacute; nguy cơ bị đau tim. Tuy nhi&ecirc;n, qu&eacute;t l&agrave; kh&ocirc;ng n&ecirc;n d&ugrave;ng cho hầu hết mọi người, như c&aacute;c th&ocirc;ng tin m&agrave; họ cung cấp thường kh&ocirc;ng hữu &iacute;ch.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">H&igrave;nh ảnh cộng hưởng từ (MRI).</span></b><span style=\"font-family: Arial;\"> Trong MRI tim, nằm tr&ecirc;n một bảng b&ecirc;n trong một m&aacute;y giống như ống d&agrave;i m&agrave; tạo ra một từ trường. Từ trường gắn c&aacute;c hạt nguy&ecirc;n tử trong một số của c&aacute;c tế b&agrave;o. Khi s&oacute;ng radio được ph&aacute;t s&oacute;ng đối với c&aacute;c hạt li&ecirc;n kết, sản xuất ra t&iacute;n hiệu thay đổi t&ugrave;y theo loại m&ocirc; của ch&uacute;ng. H&igrave;nh ảnh của tr&aacute;i tim được tạo ra từ c&aacute;c t&iacute;n hiệu n&agrave;y, trong đ&oacute; b&aacute;c sĩ sẽ xem x&eacute;t để gi&uacute;p x&aacute;c định nguy&ecirc;n nh&acirc;n của t&igrave;nh trạng tr&aacute;i tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Phương ph&aacute;p điều trị bệnh tim kh&aacute;c nhau. C&oacute; thể cần thay đổi lối sống, thuốc men, phẫu thuật hay thủ thuật y khoa kh&aacute;c như l&agrave; một phần của điều trị. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Điều trị bệnh tim mạch </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mục ti&ecirc;u trong điều trị c&aacute;c bệnh về động mạch (bệnh tim mạch) thường mở động mạch bị thu hẹp g&acirc;y ra c&aacute;c triệu chứng. T&ugrave;y thuộc v&agrave;o mức độ nghi&ecirc;m trọng c&aacute;c tắc nghẽn trong động mạch, điều trị c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thay đổi lối sống.</span></b><span style=\"font-family: Arial;\"> Cho d&ugrave; bệnh tim l&agrave; nhẹ hay nặng, rất c&oacute; thể b&aacute;c sĩ sẽ khuy&ecirc;n n&ecirc;n thay đổi lối sống như một phần của điều trị. Thay đổi lối sống bao gồm ăn &iacute;t chất b&eacute;o, thấp natri, &iacute;t nhất 30 ph&uacute;t tập luyện vừa phải tr&ecirc;n hầu hết c&aacute;c ng&agrave;y trong tuần, bỏ h&uacute;t thuốc v&agrave; hạn chế lượng rượu uống.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc.</span></b><span style=\"font-family: Arial;\"> Nếu thay đổi lối sống một m&igrave;nh l&agrave; kh&ocirc;ng đủ, b&aacute;c sĩ c&oacute; thể k&ecirc; toa cho thuốc để kiểm so&aacute;t bệnh tim. Điều n&agrave;y c&oacute; thể bao gồm c&aacute;c thuốc hạ huyết &aacute;p, chẳng hạn như thuốc lợi tiểu, c&aacute;c chất ức chế men chuyển angiotensin (ACE) hoặc thuốc chẹn beta, thuốc l&agrave;m lo&atilde;ng m&aacute;u, chẳng hạn như liệu ph&aacute;p aspirin h&agrave;ng ng&agrave;y, hoặc thuốc hạ cholesterol như statins hay fibrates.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thủ tục y tế hoặc phẫu thuật.</span></b><span style=\"font-family: Arial;\"> Nếu thuốc kh&ocirc;ng đủ, c&oacute; thể b&aacute;c sĩ sẽ khuy&ecirc;n<span style=\"\">&nbsp; </span>n&ecirc;n thủ tục cụ thể hoặc phẫu thuật để x&oacute;a tắc nghẽn trong tr&aacute;i tim. Một thủ thuật phổ biến l&agrave; nong mạch v&agrave;nh, được thực hiện bằng c&aacute;ch đặt một ống th&ocirc;ng trong một động mạch ở c&aacute;nh tay hay h&aacute;ng v&agrave; luồng một quả b&oacute;ng nhỏ đến động mạch bị chặn v&agrave; bơm n&oacute; để mở lại c&aacute;c động mạch. Một cuộn d&acirc;y kim loại nhỏ gọi l&agrave; ống đỡ động mạch thường được đặt trong động mạch trong nong mạch tim. Ống đỡ động mạch n&agrave;y gi&uacute;p giữ cho động mạch mở. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đ&ocirc;i khi, một thủ thuật x&acirc;m lấn hơn, phẫu thuật bắc cầu động mạch v&agrave;nh l&agrave; cần thiết. Trong thủ tục n&agrave;y, phần bị động mạch được lấy ra v&agrave; tĩnh mạch từ một phần kh&aacute;c của cơ thể - thường l&agrave; ch&acirc;n - thay thế c&aacute;c phần bị bệnh của động mạch. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Điều trị rối loạn nhịp tim </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tuỳ theo mức độ của t&igrave;nh trạng, b&aacute;c sĩ chỉ đơn giản c&oacute; thể đề nghị cơ động hoặc thuốc để điều chỉnh nhịp tim kh&ocirc;ng đều. Cũng c&oacute; thể sẽ cần một thiết bị y tế hoặc phẫu thuật nếu t&igrave;nh trạng l&agrave; nghi&ecirc;m trọng hơn. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Diễn tập d&acirc;y phế vị.</span></b><span style=\"font-family: Arial;\"> C&oacute; thể ngăn chặn một số rối loạn nhịp tim bằng c&aacute;ch sử dụng diễn tập cụ thể, trong đ&oacute; bao gồm giữ hơi thở v&agrave; căng thẳng, ng&acirc;m khu&ocirc;n mặt trong nước đ&aacute;, hoặc ho. B&aacute;c sĩ c&oacute; thể khuy&ecirc;n n&ecirc;n cuộc diễn tập kh&aacute;c để l&agrave;m chậm nhịp tim nhanh. C&aacute;c cuộc diễn tập ảnh hưởng đến hệ thống thần kinh điều khiển nhịp tim (d&acirc;y thần kinh vagal), thường g&acirc;y ra nhịp tim chậm. Kh&ocirc;ng t&igrave;m bất kỳ thao t&aacute;c m&agrave; kh&ocirc;ng n&oacute;i chuyện với b&aacute;c sĩ đầu ti&ecirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc.</span></b><span style=\"font-family: Arial;\"> Những người c&oacute; nhịp tim đập nhanh c&oacute; thể đ&aacute;p ứng tốt với thuốc chống arrhythmic. Mặc d&ugrave; kh&ocirc;ng chữa được vấn đề, c&oacute; thể l&agrave;m giảm cơn đau tim đập nhanh hoặc chậm xuống khi xảy ra. Một số thuốc c&oacute; thể l&agrave;m chậm tim rất nhiều m&agrave; v&agrave; c&oacute; thể cần một m&aacute;y tạo nhịp tim. Điều quan trọng để thực hiện bất kỳ thuốc chống arrhythmic ch&iacute;nh x&aacute;c theo chỉ dẫn của b&aacute;c sĩ để tr&aacute;nh biến chứng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thủ tục y tế.</span></b><span style=\"font-family: Arial;\"> Hai phổ biến c&aacute;c thủ tục để điều trị rối loạn nhịp tim nhịp tim v&agrave; cắt bỏ. Trong loạn nhịp tim, sốc điện được sử dụng để thiết lập lại nhịp tim thường xuy&ecirc;n của m&igrave;nh. Th&ocirc;ng thư���ng n&agrave;y được thực hiện đặt tr&ecirc;n ngực, c&oacute; thể cung cấp một c&uacute; sốc điện thiết lập theo d&otilde;i. Cho d&ugrave;ng thuốc an thần trong qu&aacute; tr&igrave;nh thủ tục, n&ecirc;n kh&ocirc;ng li&ecirc;n quan đau. Trong cắt bỏ, một hoặc một ống th&ocirc;ng được luồn qua mạch m&aacute;u đến tr&aacute;i tim b&ecirc;n trong. Đặt tr&ecirc;n c&aacute;c khu vực tr&aacute;i tim được x&aacute;c định bởi b&aacute;c sĩ, g&acirc;y ra chứng loạn nhịp tim. C&aacute;c điện cực ti&ecirc;u diệt (cắt bỏ) một điểm nhỏ m&ocirc; tim v&agrave; tạo ra một khối điện dọc theo con đường g&acirc;y ra chứng loạn nhịp tim.<br>\r\n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">M&aacute;y tạo nhịp tim hoặc m&aacute;y khử rung tim được cấy dưới da (ICD).</span></b><span style=\"font-family: Arial;\"> Trong một số trường hợp, b&aacute;c sĩ c&oacute; thể khuy&ecirc;n n&ecirc;n c&oacute; một m&aacute;y tạo nhịp tim hoặc ICD cấy gh&eacute;p để điều chỉnh nhịp tim. M&aacute;y tạo nhịp ph&aacute;t ra xung điện để l&agrave;m nhanh nhịp tim nếu n&oacute; trở n&ecirc;n qu&aacute; chậm, v&agrave; ICD c&oacute; thể đ&uacute;ng một nhịp tim nhanh hoặc hỗn loạn bằng c&aacute;ch sử dụng một loại tương tự của xung điện sử dụng trong nhịp tim. C&aacute;c phẫu thuật để cấy gh&eacute;p mỗi thiết bị l&agrave; tương đối nhỏ, v&agrave; thường chỉ đ&ograve;i hỏi một v&agrave;i ng&agrave;y hồi phục.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Phẫu thuật.</span></b><span style=\"font-family: Arial;\"> Đối với chứng loạn nhịp tim nặng, hoặc cho những người c&oacute; một nguy&ecirc;n nh&acirc;n tiềm ẩn như một khuyết tật tim, phẫu thuật c&oacute; thể l&agrave; một lựa chọn. Bởi v&igrave; c&aacute;c ca phẫu thuật để sửa chữa rối loạn nhịp tim l&agrave; tim mở đ&ocirc;i khi cần nhiều th&aacute;ng để phục hồi, phẫu thuật thường l&agrave; một điều trị cuối c&ugrave;ng lựa chọn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Điều trị dị tật tim </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một số khuyết tật tim l&agrave; nhỏ v&agrave; kh&ocirc;ng cần điều trị, trong khi những người kh&aacute;c c&oacute; thể y&ecirc;u cầu kiểm tra thường xuy&ecirc;n, ngay cả khi d&ugrave;ng thuốc hoặc phẫu thuật. T&ugrave;y thuộc v&agrave;o những g&igrave; tr&aacute;i tim khiếm khuyết c&oacute; v&agrave; mức độ nghi&ecirc;m trọng, điều trị c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc.</span></b><span style=\"font-family: Arial;\"> Một số khuyết tật tim bẩm sinh nhẹ, đặc biệt l&agrave; những người t&igrave;m thấy sau n&agrave;y trong thời thơ ấu hay tuổi trưởng th&agrave;nh, c&oacute; thể được điều trị bằng thuốc c&oacute; thể trợ gi&uacute;p c&aacute;c hoạt động tr&aacute;i tim hiệu quả hơn. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thủ tục đặc biệt bằng c&aacute;ch sử dụng ống th&ocirc;ng</span></b><span style=\"font-family: Arial;\">. Một số người đ&atilde; c&oacute; dị tật bẩm sinh tim sửa chữa bằng c&aacute;ch sử dụng kỹ thuật đặt ống th&ocirc;ng, cho ph&eacute;p sửa chữa được thực hiện m&agrave; kh&ocirc;ng cần phẫu thuật mở lồng ngực v&agrave; tim. Trong thủ tục c&oacute; thể được thực hiện bằng c&aacute;ch sử dụng th&ocirc;ng, c&aacute;c b&aacute;c sĩ đưa một ống nhỏ (catheter) v&agrave;o mạch m&aacute;u ở ch&acirc;n v&agrave; hướng dẫn n&oacute; v&agrave;o trung t&acirc;m với sự gi&uacute;p đỡ của c&aacute;c h&igrave;nh ảnh X - quang. Một khi ống th&ocirc;ng được đặt tại nơi của lỗi n&agrave;y, c&aacute;c c&ocirc;ng cụ nhỏ được luồn qua ống th&ocirc;ng đến trung t&acirc;m để sửa chữa c&aacute;c khiếm khuyết. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Phẫu thuật tim mở.</span></b><span style=\"font-family: Arial;\"> Trong một số trường hợp, b&aacute;c sĩ c&oacute; thể thực hiện phẫu thuật tim mở để cố gắng sửa chữa khuyết tật tim. Những ca phẫu thuật l&agrave; những thủ tục y tế v&agrave; đ&ocirc;i khi đ&ograve;i hỏi phải c&oacute; thời gian phục hồi l&acirc;u d&agrave;i. C&oacute; thể sẽ cần nhiều ca phẫu thuật trong v&agrave;i năm để sửa lỗi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tr&aacute;i tim cấy gh&eacute;p.</span></b><span style=\"font-family: Arial;\"> Nếu một khiếm khuyết tim nghi&ecirc;m trọng c&oacute; thể kh&ocirc;ng được sửa chữa, gh&eacute;p tim c&oacute; thể l&agrave; một lựa chọn. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Điều trị bệnh cơ tim </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều trị bệnh cơ tim kh&aacute;c nhau, t&ugrave;y thuộc v&agrave;o loại bệnh cơ tim c&oacute; v&agrave; l&agrave;m thế n&agrave;o l&agrave; nghi&ecirc;m trọng. Phương ph&aacute;p điều trị c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc.</span></b><span style=\"font-family: Arial;\"> B&aacute;c sĩ c&oacute; thể k&ecirc; toa cho thuốc c&oacute; thể cải thiện khả năng bơm của tim, chẳng hạn như chất ức chế ACE hoặc chẹn thụ thể angiotensin II. Beta blockers, l&agrave;m cho tim đập chậm hơn v&agrave; k&eacute;m mạnh mẽ, gi&uacute;p giảm căng thẳng tr&ecirc;n đ&oacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thiết bị y tế.</span></b><span style=\"font-family: Arial;\"> Nếu c&oacute; bệnh cơ tim gi&atilde;n nở, điều trị c&oacute; thể bao gồm một m&aacute;y tạo nhịp tim đặc biệt tọa độ c&aacute;c cơn co thắt giữa t&acirc;m thất tr&aacute;i v&agrave; phải của tr&aacute;i tim, n&acirc;ng cao khả năng bơm của tim. Nếu c&oacute; nguy cơ loạn nhịp tim nghi&ecirc;m trọng, một m&aacute;y khử rung tim được cấy dưới da (ICD) c&oacute; thể l&agrave; một lựa chọn. ICD l&agrave; c&aacute;c thiết bị nhỏ cấy v&agrave;o ngực để li&ecirc;n tục theo d&otilde;i nhịp tim v&agrave; cung cấp những c&uacute; sốc điện khi cần thiết để kiểm so&aacute;t bất thường, tim đập nhanh. C&aacute;c thiết bị cũng c&oacute; thể l&agrave;m việc như m&aacute;y tạo nhịp.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tr&aacute;i tim cấy gh&eacute;p.</span></b><span style=\"font-family: Arial;\"> Nếu c&oacute; bệnh tim nghi&ecirc;m trọng v&agrave; thuốc kh&ocirc;ng thể kiểm so&aacute;t c&aacute;c triệu chứng, cấy gh&eacute;p tim c&oacute; thể cần thiết.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Điều trị c&aacute;c bệnh nhiễm tr&ugrave;ng tim </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Việc điều trị c&aacute;c bệnh nhiễm tr&ugrave;ng tim như vi&ecirc;m nội t&acirc;m mạc, vi&ecirc;m m&agrave;ng ngo&agrave;i tim hoặc vi&ecirc;m cơ tim thường l&agrave; thuốc men, c&oacute; thể bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc kh&aacute;ng sinh.</span></b><span style=\"font-family: Arial;\"> Nếu t&igrave;nh trạng l&agrave; do một loại vi khuẩn, b&aacute;c sĩ sẽ k&ecirc; toa thuốc kh&aacute;ng sinh. Thuốc kh&aacute;ng sinh được đưa ra bởi một d&ograve;ng (IV) ti&ecirc;m tĩnh mạch cho 2 - 6 tuần, t&ugrave;y thuộc v&agrave;o nhiễm nặng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">C&aacute;c loại thuốc để điều chỉnh nhịp tim.</span></b><span style=\"font-family: Arial;\"> Nếu sự l&acirc;y nhiễm đ&atilde; ảnh hưởng đến nhịp tim, b&aacute;c sĩ c&oacute; thể k&ecirc; toa thuốc như thuốc ức chế chuyển đổi angiotensin-men hoặc thuốc chẹn beta để gi&uacute;p b&igrave;nh thường h&oacute;a nhịp tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu bệnh tim thiệt hại nghi&ecirc;m trọng, c&oacute; thể cần phải phẫu thuật để sửa chữa phần hư hỏng của tr&aacute;i tim. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Điều trị bệnh van tim </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mặc d&ugrave; phương ph&aacute;p điều trị cho c&aacute;c bệnh van tim c&oacute; thể thay đổi t&ugrave;y thuộc v&agrave;o những g&igrave; van bị ảnh hưởng v&agrave; mức độ nghi&ecirc;m trọng t&igrave;nh trạng, lựa chọn điều trị thường bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuốc.</span></b><span style=\"font-family: Arial;\"> C&oacute; thể bệnh van tim nếu nhẹ, c&oacute; thể được quản l&yacute; chỉ bằng thuốc. Th&ocirc;ng thường thuốc được quy định cho c&aacute;c bệnh van tim bao gồm thuốc để mở c&aacute;c mạch m&aacute;u (thuốc gi&atilde;n mạch), thuốc hạ cholesterol (statin), thuốc l&agrave;m giảm khả năng giữ nước (lợi tiểu), v&agrave; thuốc l&agrave;m lo&atilde;ng m&aacute;u (thuốc chống đ&ocirc;ng m&aacute;u).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Balloon valvuloplasty.</span></b><span style=\"font-family: Arial;\"> Thủ tục n&agrave;y đ&ocirc;i khi được d&ugrave;ng như l&agrave; thủ tục điều trị hẹp van. Trong thủ tục n&agrave;y, một ống nhỏ th&ocirc;ng qua một tĩnh mạch ở ch&acirc;n v&agrave; đến tr&aacute;i tim. Một b&oacute;ng uninflated được đặt th&ocirc;ng qua việc mở van phổi thu hẹp. B&aacute;c sĩ sau đ&oacute; bơm quả b&oacute;ng, mở cửa thu hẹp van động mạch phổi v&agrave; tăng diện t&iacute;ch d&agrave;nh cho lưu lượng m&aacute;u.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Van sửa chữa hoặc thay thế.</span></b><span style=\"font-family: Arial;\"> Nếu t&igrave;nh trạng l&agrave; nghi&ecirc;m trọng, c&oacute; thể cần phải phẫu thuật để sửa chữa t&igrave;nh trạng. B&aacute;c sĩ c&oacute; thể sửa chữa van. Nếu van kh&ocirc;ng thể được sửa chữa, n&oacute; c&oacute; thể được thay thế bằng một van thay thế l&agrave;m bằng vật liệu tổng hợp. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Phong c&aacute;ch sống v&agrave; biện ph&aacute;p khắc phục</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bệnh tim c&oacute; thể được cải thiện - hoặc thậm ch&iacute; ngăn chặn - bằng c&aacute;ch thay đổi lối sống nhất định. Những thay đổi sau đ&acirc;y c&oacute; thể gi&uacute;p đỡ bất cứ ai muốn cải thiện sức khỏe tim của họ: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ngưng h&uacute;t thuốc l&aacute;.</span></b><span style=\"font-family: Arial;\"> H&uacute;t thuốc l&aacute; l&agrave; một yếu tố nguy cơ ch&iacute;nh đối với bệnh tim, đặc biệt l&agrave; xơ vữa động mạch. Nicotine co thắt mạch m&aacute;u v&agrave; lực tim l&agrave;m việc chăm chỉ hơn, v&agrave; carbon monoxide l&agrave;m giảm &ocirc;xy trong m&aacute;u v&agrave; tổn thương ni&ecirc;m mạc của c&aacute;c mạch m&aacute;u. Nếu h&uacute;t thuốc, bỏ thuốc l&agrave; c&aacute;ch tốt nhất để giảm nguy cơ bệnh tim v&agrave; biến chứng của n&oacute;. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Kiểm so&aacute;t huyết &aacute;p.</span></b><span style=\"font-family: Arial;\"> H&atilde;y hỏi b&aacute;c sĩ cho một ph&eacute;p đo huyết &aacute;p &iacute;t nhất mỗi hai năm. B&aacute;c sĩ c&oacute; thể khuy&ecirc;n n&ecirc;n đo thường xuy&ecirc;n hơn nếu huyết &aacute;p cao hơn b&igrave;nh thường hoặc c&oacute; tiền sử bệnh tim. Huyết &aacute;p tốt nhất l&agrave; dưới 120 t&acirc;m thu v&agrave; t&acirc;m trương 80, được đo bằng milimet thủy ng&acirc;n (mm Hg).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Kiểm tra cholesterol.</span></b><span style=\"font-family: Arial;\"> H&atilde;y hỏi b&aacute;c sĩ cho một b&agrave;i kiểm tra cholesterol cơ bản khi<span style=\"\">&nbsp; </span>đang ở độ tuổi 20 v&agrave; sau đ&oacute; &iacute;t nhất mỗi năm năm. Nếu kết quả thử nghiệm kh&ocirc;ng thuộc phạm vi mong muốn, b&aacute;c sĩ c&oacute; thể khuy&ecirc;n n&ecirc;n đo thường xuy&ecirc;n hơn. Hầu hết mọi người phải nhắm v&agrave;o một mức độ LDL dưới 130 mg / dL (mg / dL), hoặc 3,4 millimoles / l&iacute;t (mmol / L). Nếu c&oacute; c&aacute;c yếu tố nguy cơ kh&aacute;c của bệnh tim, LDL mục ti&ecirc;u c&oacute; thể dưới 100 mg / dL (2.6 mmol / L). Nếu c&oacute; nguy cơ rất cao mắc bệnh tim - nếu đ&atilde; c&oacute; một cơn đau tim hoặc tiểu đường - mục ti&ecirc;u l&agrave; mức độ LDL dưới 70 mg / dL (1,8 mmol / L). </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Giữ bệnh tiểu đường được kiểm so&aacute;t</span></b><span style=\"font-family: Arial;\">. Nếu bị tiểu đường, kiểm so&aacute;t chặt chẽ lượng đường trong m&aacute;u c&oacute; thể gi&uacute;p giảm nguy cơ bệnh tim. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">C&oacute; di chuyển.</span></b><span style=\"font-family: Arial;\"> Nếu c&oacute; bệnh tim, tập thể dục gi&uacute;p đạt được v&agrave; duy tr&igrave; một trọng lượng khỏe mạnh v&agrave; kiểm so&aacute;t bệnh tiểu đường, cholesterol cao v&agrave; huyết &aacute;p cao - tất cả c&aacute;c yếu tố nguy cơ bệnh tim. Nếu c&oacute; một khuyết tật tim loạn nhịp tim hoặc tim, c&oacute; thể c&oacute; một số hạn chế về c&aacute;c hoạt động c&oacute; thể l&agrave;m, như vậy chắc chắn để n&oacute;i chuyện với b&aacute;c sĩ đầu ti&ecirc;n. Với mục đ&iacute;ch của b&aacute;c sĩ, OK cho 30 đến 60 ph&uacute;t hoạt động thể chất một ng&agrave;y v&agrave; nhiều nhất c&aacute;c ng&agrave;y trong tuần. Ngay cả khi kh&ocirc;ng thể d&agrave;nh thời gian cho trong 30 - 60 ph&uacute;t cho buổi tập thể dục, vẫn c&oacute; thể hưởng lợi từ ph&aacute; vỡ hoạt động v&agrave;o một số buổi 10 ph&uacute;t. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ăn thực phẩm l&agrave;nh mạnh.</span></b><span style=\"font-family: Arial;\"> Một chế độ ăn uống cho sức khỏe tim dựa tr&ecirc;n c&aacute;c loại tr&aacute;i c&acirc;y, rau v&agrave; ngũ cốc - v&agrave; &iacute;t cholesterol b&atilde;o h&ograve;a, chất b&eacute;o v&agrave; natri - c&oacute; thể gi&uacute;p<span style=\"\">&nbsp; </span>kiểm so&aacute;t c&acirc;n nặng, huyết &aacute;p v&agrave; cholesterol. Ăn uống một hoặc hai bữa c&aacute; một tuần cũng c&oacute; lợi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Duy tr&igrave; một trọng lượng khỏe mạnh.</span></b><span style=\"font-family: Arial;\"> Thừa c&acirc;n l&agrave;m tăng nguy cơ mắc bệnh tim. Giảm c&acirc;n đặc biệt quan trọng cho những người c&oacute; số đo v&ograve;ng eo lớn hơn 101,6 cm d&agrave;nh cho nam giới v&agrave; hơn 88,9 cm cho phụ nữ - bởi v&igrave; mọi người với h&igrave;nh dạng cơ thể c&oacute; nhiều khả năng ph&aacute;t triển bệnh tiểu đường v&agrave; bệnh tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Quản l&yacute; căng thẳng.</span></b><span style=\"font-family: Arial;\"> Giảm căng thẳng c&agrave;ng nhiều c&agrave;ng tốt. Thực h&agrave;nh kỹ thuật l&agrave;nh mạnh cho sự quản l&yacute; căng thẳng, chẳng hạn như thư gi&atilde;n cơ bắp v&agrave; h&iacute;t thở s&acirc;u.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Th&oacute;i quen thực h&agrave;nh tốt vệ sinh. </span></b><span style=\"font-family: Arial;\">Tr&aacute;nh xa những người kh&aacute;c khi họ bị bệnh v&agrave; thường xuy&ecirc;n rửa tay c&oacute; thể kh&ocirc;ng chỉ ngăn ngừa nhiễm tr&ugrave;ng tim, cũng c&oacute; thể gi&uacute;p ngăn ngừa nhiễm virus hoặc vi khuẩn c&oacute; thể đặt căng thẳng v&agrave;o tr&aacute;i tim nếu đ&atilde; c&oacute; bệnh tim. Ngo&agrave;i ra, đ&aacute;nh răng v&agrave; d&ugrave;ng chỉ nha khoa răng thường xuy&ecirc;n c&oacute; thể ngăn ngừa vi tr&ugrave;ng trong miệng đến những mảng trong tr&aacute;i tim, m&agrave; c&oacute; thể l&agrave;m tăng bệnh tim mạch.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ti&ecirc;m c&uacute;m.</span></b><span style=\"font-family: Arial;\"> Nếu c&oacute; bệnh tim mạch, đang ở một nguy cơ lớn hơn của việc c&oacute; một cơn đau tim n&ecirc;n mắc c&uacute;m. Ti&ecirc;m ngừa c&uacute;m l&agrave;m giảm nguy cơ n&agrave;y. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ngo&agrave;i việc thay đổi lối sống l&agrave;nh mạnh, h&atilde;y nhớ tầm quan trọng của kiểm tra y tế thường xuy&ecirc;n. Ph&aacute;t hiện sớm v&agrave; điều trị c&oacute; thể chuẩn bị cho một cuộc đời của sức khỏe tim tốt hơn. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Thay thế thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; một số loại thuốc thay thế c&oacute; thể c&oacute; hiệu quả trong việc hạ thấp cholesterol v&agrave; ngăn ngừa một số loại bệnh tim mạch, bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&acirc;y m&atilde; đề bọ ch&eacute;t.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Coenzyme Q - 10.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hạt lanh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Yến mạch v&agrave; c&aacute;m yến mạch.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Omega - 3 fatty acid.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Stanols v&agrave; sterol thực vật, chẳng hạn như beta - sitosterol v&agrave; sitostanol.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Như với bất kỳ thuốc thay thế, h&atilde;y n&oacute;i chuyện với b&aacute;c sĩ trước khi th&ecirc;m bất kỳ bổ sung mới cho chế độ điều trị. Ngay cả c&aacute;c loại thuốc thảo dược tự nhi&ecirc;n, bổ sung c&oacute; thể tương t&aacute;c với c&aacute;c thuốc đang d&ugrave;ng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Đối ph&oacute; v&agrave; hỗ trợ</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; thể cảm thấy thất vọng, buồn b&atilde; hay bị &aacute;p đảo sau khi biết bạn hoặc người th&acirc;n c&oacute; bệnh tim. May mắn thay, c&oacute; nhiều c&aacute;ch để gi&uacute;p đối ph&oacute; với bệnh tim hoặc cải thiện t&igrave;nh trạng. Ch&uacute;ng bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Phục hồi chức năng tim.</span></b><span style=\"font-family: Arial;\"> Đối với những người c&oacute; bệnh tim mạch g&acirc;y ra một cơn đau tim hoặc c&oacute; y&ecirc;u cầu phẫu thuật để sửa chữa, phục hồi chức năng tim thường được khuyến c&aacute;o như l&agrave; một c&aacute;ch để cải thiện điều trị v&agrave; phục hồi tốc độ. Phục hồi chức năng tim l&agrave; một chương tr&igrave;nh thường được chia th&agrave;nh c&aacute;c giai đoạn c&oacute; li&ecirc;n quan đến c&aacute;c cấp thực hiện gi&aacute;m s&aacute;t, tư vấn dinh dưỡng, hỗ trợ tinh thần, hỗ trợ v&agrave; gi&aacute;o dục về thay đổi lối sống để giảm thiểu rủi ro về vấn đề tim mạch. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Hỗ trợ nh&oacute;m.</span></b><span style=\"font-family: Arial;\"> T&igrave;m ra hoặc một người th&acirc;n c&oacute; bệnh tim c&oacute; thể l&agrave; điều đ&aacute;ng sợ. Chuyển đến bạn b&egrave; v&agrave; gia đ&igrave;nh để được hỗ trợ l&agrave; cần thiết, nhưng nếu t&igrave;m thấy<span style=\"\">&nbsp; </span>cần gi&uacute;p đỡ nhiều hơn, n&oacute;i chuyện với b&aacute;c sĩ về tham gia một nh&oacute;m hỗ trợ. C&oacute; thể n&oacute;i về mối quan t&acirc;m với những người kh&aacute;c đang trải qua những kh&oacute; khăn tương tự c&oacute; thể gi&uacute;p đỡ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tiếp tục kiểm tra y tế.</span></b><span style=\"font-family: Arial;\"> Nếu c&oacute; một bệnh tim t&aacute;i ph&aacute;t hay m&atilde;n t&iacute;nh, một &yacute; tưởng tốt để thường xuy&ecirc;n kiểm tra ở với b&aacute;c sĩ để đảm bảo rằng đ&uacute;ng c&aacute;ch quản l&yacute; t&igrave;nh trạng tim. Kiểm tra thường xuy&ecirc;n c&oacute; thể gi&uacute;p b&aacute;c sĩ quyết định xem cần thay đổi điều trị, v&agrave; c&oacute; thể gi&uacute;p bắt được những vấn đề mới nếu ch&uacute;ng xảy ra. Nếu l&agrave; cha mẹ của một đứa trẻ bị bệnh tim, đ&oacute; l&agrave; một &yacute; tưởng tốt để khuyến kh&iacute;ch con<span style=\"\">&nbsp; </span>thường xuy&ecirc;n gh&eacute; thăm b&aacute;c sĩ của m&igrave;nh để theo d&otilde;i bệnh tim ở tuổi trưởng th&agrave;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Ph&ograve;ng chống</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một số loại bệnh tim, chẳng hạn như dị tật tim, kh&ocirc;ng thể được ngăn chặn. Tuy nhi&ecirc;n, c&oacute; thể gi&uacute;p ngăn ngừa nhiều loại kh&aacute;c của bệnh tim bằng c&aacute;ch l&agrave;m thay đổi lối sống c&oacute; thể cải thiện bệnh tim, chẳng hạn như bằng c&aacute;ch: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng h&uacute;t thuốc l&aacute; .</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kiểm so&aacute;t như huyết &aacute;p cao, cholesterol cao v&agrave; bệnh tiểu đường. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Duy tr&igrave; hoạt động thể chất. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ăn thực phẩm l&agrave;nh mạnh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Duy tr&igrave; c&acirc;n nặng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Giảm v&agrave; quản l&yacute; căng thẳng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thực h&agrave;nh vệ sinh tốt.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: right;\"><span style=\"color: rgb(128, 0, 0);\"><b style=\"\"><i style=\"\"><span style=\"font-family: Arial;\"></span></i></b></span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">&nbsp;</span></p></span>\n', 2);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(27, 'Bệnh viêm động mạch Takayasu', '\n   <h1 class=\"title\">Bệnh viêm động mạch Takayasu</h1>   <span class=\"des\">Bệnh viêm động mạch Takayasu chủ yếu ảnh hưởng đến trẻ em gái v�� phụ nữ dưới 40 tuổi. Nguyên nhân chính xác của bệnh chưa được biết.</span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-family: Arial; font-size: 18pt; line-height: 150%;\">Định nghĩa</span></p>\r\n<p style=\"margin-top:6.0pt;mso-margin-bottom-alt:auto;line-height:150%\"><span lang=\"VI\" style=\"font-family:Arial\">Bệnh vi&ecirc;m động mạch Takayasu l&agrave; một loại hiếm gặp của vi&ecirc;m mạch, một nh&oacute;m c&aacute;c rối loạn g&acirc;y vi&ecirc;m mạch m&aacute;u. Trong bệnh vi&ecirc;m động mạch Takayasu, vi&ecirc;m tổn thương động mạch chủ - động mạch lớn mang m&aacute;u từ tim với phần c&ograve;n lại của cơ thể - v&agrave; c&aacute;c nh&aacute;nh ch&iacute;nh của động mạch chủ. Bệnh n&agrave;y c&oacute; thể dẫn đến tắc nghẽn hoặc hẹp động mạch, hoặc động mạch gi&atilde;n nở bất thường, gọi l&agrave; chứng ph&igrave;nh động mạch. Bệnh vi&ecirc;m động mạch Takayasu cũng c&oacute; thể dẫn đến </span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">đau </span><span lang=\"VI\" style=\"font-family:Arial\">c&aacute;nh tay hoặc đau ngực, huyết &aacute;p cao, v&agrave; cuối c&ugrave;ng l&agrave; suy tim hoặc đột quỵ.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Bệnh vi&ecirc;m động mạch Takayasu chủ yếu ảnh hưởng đến trẻ em g&aacute;i v&agrave; phụ nữ dưới 40 tuổi. Nguy&ecirc;n nh&acirc;n ch&iacute;nh x&aacute;c của bệnh chưa được biết.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Mục ti&ecirc;u của điều trị l&agrave; l&agrave;m giảm vi&ecirc;m trong động mạch v&agrave; ngăn ngừa c&aacute;c biến chứng tiềm năng. Ngay cả với ph&aacute;t hiện sớm v&agrave; điều trị, tuy nhi&ecirc;n, bệnh vi&ecirc;m động mạch Takayasu c&oacute; thể </span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">l&agrave;</span><span lang=\"VI\" style=\"font-family:Arial\"> th&aacute;ch thức để quản l&yacute;.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-size:18.0pt;line-height:150%;font-family:Arial\">C&aacute;c triệu chứng</span><span style=\"font-family:Arial;mso-ansi-language:EN-US\"><o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-size:14.0pt;line-height:150%;font-family:Arial\">C&aacute;c triệu chứng giai đoạn đầu ti&ecirc;n</span><span style=\"font-size:14.0pt;line-height:150%;\r\nfont-family:Arial;mso-ansi-language:EN-US\"><o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Bệnh vi&ecirc;m động mạch Takayasu thường xảy ra trong hai giai đoạn. Trong giai đoạn đầu ti&ecirc;n, c&oacute; thể cảm thấy kh&ocirc;ng khỏe, với:</span><span lang=\"VI\" style=\"font-size:18.0pt;line-height:150%;font-family:Arial\"><o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Mệt mỏi</span><span style=\"font-family:Arial;\r\nmso-ansi-language:EN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Nhanh ch&oacute;ng, ngo&agrave;i &yacute; muốn giảm c&acirc;n</span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Cơ bắp hoặc đau khớp</span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Sốt thấp</span><span style=\"font-family:Arial;\r\nmso-ansi-language:EN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Kh&ocirc;ng phải tất cả mọi người c&oacute; những triệu chứng ban đầu, tuy nhi&ecirc;n, v&agrave; n&oacute; c&oacute; thể bị vi&ecirc;m hay tổn thương động mạch trong nhiều năm trước khi vấn đề xuất hiện. <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-size:14.0pt;line-height:150%;font-family:Arial\">C&aacute;c triệu chứng giai đoạn thứ hai</span><span style=\"font-size:14.0pt;line-height:150%;\r\nfont-family:Arial;mso-ansi-language:EN-US\"><o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Triệu chứng giai đoạn thứ hai bắt đầu ph&aacute;t triển đ&atilde; g&acirc;y ra khi vi&ecirc;m động mạch thu hẹp, việc giảm số lượng m&aacute;u v&agrave; oxy, chất dinh dưỡng đến c&aacute;c cơ quan v&agrave; c&aacute;c m&ocirc; nhất định. Những dấu hiệu v&agrave; triệu chứng c&oacute; thể bao gồm:</span><span style=\"font-family:Arial;mso-ansi-language:EN-US\"><o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">C&aacute;nh tay hoặc ch&acirc;n yếu hoặc đau (claudication)</span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Hoa mắt, ch&oacute;ng mặt, ngất, đau đầu</span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Kh&oacute; khăn trong suy nghĩ v&agrave; ghi nhớ</span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Rối loạn thị</span><span style=\"font-family:\r\nArial;mso-ansi-language:EN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family:Arial;mso-ansi-language:EN-US\">Tăng</span><span lang=\"VI\" style=\"font-family:Arial\"> huyết &aacute;p</span><span style=\"font-family:Arial;\r\nmso-ansi-language:EN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Sự kh&aacute;c biệt huyết &aacute;p giữa hai c&aacute;nh tay</span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family:Arial;mso-ansi-language:EN-US\">Mạch giảm</span><span lang=\"VI\" style=\"font-family:Arial\"> hoặc vắng mặt</span><span style=\"font-family:\r\nArial;mso-ansi-language:EN-US\"> ở</span><span lang=\"VI\" style=\"font-family:Arial\"> cổ tay, vi&ecirc;m động mạch Takayasu đ&ocirc;i khi gọi l&agrave; bệnh </span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">mạch yếu </span><span lang=\"VI\" style=\"font-family:Arial\">v&igrave; động mạch thu hẹp c&oacute; thể l&agrave;m </span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">bắt mạch </span><span lang=\"VI\" style=\"font-family:Arial\">kh&oacute; hoặc kh&ocirc;ng thể ph&aacute;t hiện</span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Thiếu m&aacute;u, c&oacute; thể l&agrave;m cho cảm thấy mệt mỏi hay yếu</span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Đau ngực</span><span style=\"font-family:Arial;\r\nmso-ansi-language:EN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Ở một số người, huyết &aacute;p cao trong c&aacute;c động mạch trong phổi (tăng &aacute;p động mạch phổi) dẫn đến kh&oacute; thở v&agrave; mệt mỏi</span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Nếu c&oacute; c&aacute;c triệu chứng c&oacute; thể cho thấy bệnh vi&ecirc;m động mạch Takayasu, </span><span style=\"font-family:Arial;mso-ansi-language:\r\nEN-US\">gặp</span><span lang=\"VI\" style=\"font-family:Arial\"> b&aacute;c sĩ. Nhiều dấu hiệu v&agrave; triệu chứng của bệnh vi&ecirc;m động mạch Takayasu tương tự như những điều kiện kh&aacute;c, c&oacute; thể l&agrave;m cho chẩn đo&aacute;n đầy th&aacute;ch thức. Tuy nhi&ecirc;n, ph&aacute;t hiện sớm bệnh l&agrave; quan trọng để c&oacute; được lợi &iacute;ch nhiều nhất từ điều trị v&agrave; ngăn ngừa biến chứng.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Nếu đ&atilde; được chẩn đo&aacute;n bệnh vi&ecirc;m động mạch Takayasu, h&atilde;y ghi nhớ rằng c&aacute;c triệu chứng của một bệnh b&ugrave;ng ph&aacute;t (t&aacute;i ph&aacute;t) thường tương tự như đ&atilde; xảy ra khi căn bệnh đầu ti&ecirc;n bắt đầu. Ngo&agrave;i ra ch&uacute; &yacute; đến bất kỳ dấu hiệu hoặc triệu chứng mới. Đ&acirc;y c&oacute; thể chỉ ra </span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">dấu hiệu b&ugrave;ng ph&aacute;t </span><span lang=\"VI\" style=\"font-family:Arial\">bệnh tật hoặc biến chứng của điều trị.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-size:18.0pt;line-height:150%;font-family:Arial\">Nguy&ecirc;n nh&acirc;n<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Trong bệnh vi&ecirc;m động mạch Takayasu</span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">, </span><span lang=\"VI\" style=\"font-family:Arial\">động mạch chủ v&agrave; động mạch lớn kh&aacute;c, bao gồm cả những </span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">mạch</span><span lang=\"VI\" style=\"font-family:Arial\"> dẫn </span><span style=\"font-family:Arial;\r\nmso-ansi-language:EN-US\">l&ecirc;n </span><span lang=\"VI\" style=\"font-family:Arial\">đầu v&agrave; thận bị vi&ecirc;m. Theo thời gian, t&igrave;nh trạng vi&ecirc;m g&acirc;y ra những thay đổi trong c&aacute;c động mạch, bao gồm d&agrave;y l&ecirc;n, thu hẹp v&agrave; sẹo. Kết quả l&agrave; giảm lưu lượng m&aacute;u đến c&aacute;c m&ocirc; v&agrave; cơ quan quan trọng, c&oacute; thể dẫn đến biến chứng nghi&ecirc;m trọng v&agrave; thậm ch&iacute; tử vong. Đ&ocirc;i khi động mạch trở n&ecirc;n bất thường gi&atilde;n nở, dẫn đến chứng ph&igrave;nh động mạch c&oacute; thể c&oacute; vỡ.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Những g&igrave; g&acirc;y ra vi&ecirc;m trong bệnh vi&ecirc;m động mạch Takayasu ban đầu kh&ocirc;ng được biết. Khả năng bệnh vi&ecirc;m động mạch Takayasu l&agrave; một bệnh tự miễn, trong đ&oacute; trục trặc hệ thống miễn dịch v&agrave; tấn c&ocirc;ng động mạch như thể </span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">n&oacute;</span><span lang=\"VI\" style=\"font-family:Arial\"> l&agrave; chất </span><span style=\"font-family:\r\nArial;mso-ansi-language:EN-US\">ngoại lai.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-size:18.0pt;line-height:150%;font-family:Arial\">C&aacute;c yếu tố nguy cơ<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Bệnh vi&ecirc;m động mạch Takayasu chủ yếu ảnh hưởng đến trẻ em g&aacute;i v&agrave; phụ nữ ở độ tuổi 20 v&agrave; 30. Rối loạn xảy ra tr&ecirc;n to&agrave;n thế giới, nhưng l&agrave; phổ biến nhất ở phụ nữ ch&acirc;u &Aacute;.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-size:18.0pt;line-height:150%;font-family:Arial\">C&aacute;c biến chứng<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Mức độ nghi&ecirc;m trọng của bệnh vi&ecirc;m động mạch Takayasu c&oacute; thể kh&aacute;c nhau. Ở một số người, điều kiện vẫn c&ograve;n nhẹ v&agrave; kh&ocirc;ng tạo ra biến chứng. Nhưng ở những người kh&aacute;c, chu kỳ k&eacute;o d&agrave;i hoặc định kỳ của chứng vi&ecirc;m v&agrave; chữa bệnh trong c&aacute;c động mạch c&oacute; thể dẫn đến một hoặc nhiều điều sau đ&acirc;y:<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Củng cố v&agrave; thu hẹp c&aacute;c mạch m&aacute;u, c&oacute; thể g&acirc;y giảm lưu lượng m&aacute;u đến c&aacute;c cơ quan v&agrave; c&aacute;c m&ocirc;</span><span style=\"font-family:\r\nArial;mso-ansi-language:EN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Huyết &aacute;p cao, thường l&agrave; kết quả của việc giảm lưu lượng m&aacute;u đến thận</span><span style=\"font-family:Arial;mso-ansi-language:\r\nEN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Vi&ecirc;m tim hoặc cơ tim, t&uacute;i bao quanh tim (vi&ecirc;m m&agrave;ng ngo&agrave;i tim) hoặc của c&aacute;c van tim (valvulitis)</span><span style=\"font-family:\r\nArial;mso-ansi-language:EN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Suy tim, do tr&agrave;o ngược cao &aacute;p, vi&ecirc;m cơ tim hoặc động mạch chủ - một t&igrave;nh trạng trong đ&oacute; van động mạch chủ bị lỗi cho ph&eacute;p m&aacute;u bị r&ograve; rỉ trở lại v&agrave;o tim - hoặc kết hợp</span><span style=\"font-family:\r\nArial;mso-ansi-language:EN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Đột quỵ</span><span style=\"font-family:Arial;\r\nmso-ansi-language:EN-US\">,</span><span lang=\"VI\" style=\"font-family:Arial\"> thiếu m&aacute;u cục bộ, một loại đột quỵ xảy ra như l&agrave; kết quả của lưu lượng m&aacute;u giảm hoặc bị chặn trong động mạch dẫn đến n&atilde;o</span><span style=\"font-family:Arial;\r\nmso-ansi-language:EN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Thiếu m&aacute;u cục bộ tho&aacute;ng qua, một cơn đột quỵ tạm thời c&oacute; tất cả c&aacute;c triệu chứng của một cơn đột quỵ</span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">,</span><span lang=\"VI\" style=\"font-family:Arial\"> thiếu m&aacute;u cục bộ m&agrave; kh&ocirc;ng g&acirc;y thiệt hại l&acirc;u d&agrave;i</span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Chứng ph&igrave;nh động mạch trong động mạch chủ, xảy ra khi c&aacute;c bức t</span><span style=\"font-family:Arial;mso-ansi-language:\r\nEN-US\">h&agrave;nh </span><span lang=\"VI\" style=\"font-family:Arial\">của c&aacute;c mạch m&aacute;u bị yếu đi v&agrave; căng ra, tạo lồi ra c&oacute; khả năng vỡ</span><span style=\"font-family:\r\nArial;mso-ansi-language:EN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Nhồi m&aacute;u cơ tim - mặc d&ugrave; kh&ocirc;ng phổ biến, n&oacute; c&oacute; thể xảy ra như l&agrave; kết quả của giảm lưu lượng m&aacute;u đến tim</span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family:Arial;mso-ansi-language:EN-US\">Bệnh phổi</span><span lang=\"VI\" style=\"font-family:Arial\">, khi trở th&agrave;nh bệnh động mạch phổi</span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Trong c&aacute;c nghi&ecirc;n cứu c&aacute;c phụ nữ mang thai với bệnh vi&ecirc;m động mạch Takayasu, hầu hết phụ nữ </span><span style=\"font-family:\r\nArial;mso-ansi-language:EN-US\">sinh</span><span lang=\"VI\" style=\"font-family:\r\nArial\"> em b&eacute; khỏe mạnh. Tuy nhi&ecirc;n, bệnh c&oacute; thể g&acirc;y ra rủi ro cho</span><span style=\"font-family:Arial;mso-ansi-language:EN-US\"> mẹ</span><span lang=\"VI\" style=\"font-family:Arial\"> v&agrave; em b&eacute;, v&agrave; c&aacute;c loại thuốc để điều trị n&oacute; cũng c&oacute; thể g&acirc;y ra vấn đề. Nếu c&oacute; bệnh vi&ecirc;m động mạch Takayasu v&agrave; đang c&oacute; kế hoạch mang thai, điều quan trọng l&agrave; l&agrave;m việc với b&aacute;c sĩ để ph&aacute;t triển một kế hoạch to&agrave;n diện để hạn chế biến chứng của thai kỳ trước khi thụ thai. Ngo&agrave;i ra, sẽ được gi&aacute;m s&aacute;t chặt chẽ trong suốt thai kỳ.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-size:18.0pt;line-height:150%;font-family:Arial\">Kiểm tra v&agrave; chẩn đo&aacute;n</span><span style=\"font-size:18.0pt;line-height:150%;font-family:\r\nArial;mso-ansi-language:EN-US\"><o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Takayasu của bệnh vi&ecirc;m động mạch c&oacute; thể được thử th&aacute;ch để ph&aacute;t hiện, v&agrave; một số người đi năm m&agrave; kh&ocirc;ng c&oacute; một chẩn đo&aacute;n ch&iacute;nh x&aacute;c. B&aacute;c sĩ c&oacute; thể sử dụng một số bước sau đ&acirc;y v&agrave; kiểm tra để loại trừ c&aacute;c điều kiện kh&aacute;c gần tương tự như của bệnh vi&ecirc;m động mạch Takayasu v&agrave; x&aacute;c định chẩn đo&aacute;n:</span><span lang=\"VI\" style=\"font-size:18.0pt;line-height:150%;font-family:\r\nArial\"><o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Lịch sử y tế v&agrave; </span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">kh&aacute;m l&acirc;m s&agrave;ng</span><span lang=\"VI\" style=\"font-family:Arial\">. B&aacute;c sĩ sẽ kiểm tra v&agrave; hỏi c&acirc;u hỏi về sức khỏe chung, bao gồm c&aacute;c c&acirc;u hỏi về tim v&agrave; bệnh mạch m&aacute;u.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">X&eacute;t nghiệm m&aacute;u. B&aacute;c sĩ c&oacute; thể y&ecirc;u cầu x&eacute;t nghiệm m&aacute;u để kiểm tra c&aacute;c dấu hiệu của vi&ecirc;m trong cơ thể, chẳng hạn như cao tế b&agrave;o m&aacute;u trắng hoặc mức cao C-reactive protein. X&eacute;t nghiệm m&aacute;u thường được sử dụng để gi&uacute;p x&aacute;c định rối loạn vi&ecirc;m nhiễm được gọi l&agrave; tốc độ lắng hồng cầu (ERS). B&aacute;c sĩ cũng c&oacute; thể kiểm tra số lượng tế b&agrave;o hồng cầu.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Chụp động mạch. Theo truyền thống, c&aacute;c b&aacute;c sĩ sử dụng kiểm tra X-quang chụp mạch</span><span style=\"font-family:Arial;\r\nmso-ansi-language:EN-US\">,</span><span lang=\"VI\" style=\"font-family:Arial\"> l&agrave; một trong c&aacute;c b&agrave;i kiểm tra</span><span lang=\"VI\" style=\"font-family:Arial;\r\nmso-ansi-language:EN-US\"> </span><span lang=\"VI\" style=\"font-family:Arial\">dứt kho&aacute;t hơn để chẩn đo&aacute;n bệnh vi&ecirc;m động mạch Takayasu. Trong chụp động mạch, một ống mỏng, dẻo gọi l&agrave; ống th&ocirc;ng được đưa v&agrave;o mạch m&aacute;u lớn. Một chất nhuộm đặc biệt (tương phản trung b&igrave;nh) sau đ&oacute; được ti&ecirc;m v&agrave;o ống th&ocirc;ng, v&agrave; X-quang được thực hiện như nhuộm đầy động mạch hoặc tĩnh mạch. C&aacute;c h&igrave;nh ảnh kết quả cho ph&eacute;p b&aacute;c sĩ xem nếu m&aacute;u chảy b&igrave;nh thường hoặc nếu n&oacute; chậm lại hoặc gi&aacute;n đoạn do bị thu hẹp hoặc tắc nghẽn </span><span style=\"font-family:Arial;mso-ansi-language:\r\nEN-US\">do </span><span lang=\"VI\" style=\"font-family:Arial\">c&aacute;c mạch m&aacute;u. Một người c&oacute; bệnh vi&ecirc;m động mạch Takayasu n&oacute;i chung c&oacute; một số khu vực hẹp.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Chụp cộng hưởng từ (MRA). C&agrave;ng ng&agrave;y, c&aacute;c b&aacute;c sĩ sử dụng h&igrave;nh thức &iacute;t x&acirc;m lấn chụp động mạch ở vị tr&iacute; truyền thống chụp mạch</span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">,</span><span lang=\"VI\" style=\"font-family:Arial\"> l&agrave; một thử nghiệm cho bệnh vi&ecirc;m động mạch Takayasu. MRA sản xuất h&igrave;nh ảnh chi tiết c&aacute;c mạch m&aacute;u m&agrave; kh&ocirc;ng cần sử dụng ống th&ocirc;ng hay X-quang, mặc d&ugrave; m&ocirc;i trường tương phản thường được sử dụng. MRA hoạt động bằng c&aacute;ch sử dụng s&oacute;ng v&ocirc; tuyến trong một từ trường mạnh để sản xuất dữ liệu m&agrave; m&aacute;y t&iacute;nh biến th&agrave;nh h&igrave;nh ảnh chi tiết của m&ocirc; l&aacute;t.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">H&igrave;nh ảnh cộng hưởng từ (MRI). MRI tương tự như MRA ở chỗ n&oacute; sử dụng s&oacute;ng v&ocirc; tuyến v&agrave; một từ trường để tạo ra h&igrave;nh ảnh chi tiết của c&aacute;c cơ quan trong cơ thể v&agrave; cho ph&eacute;p b&aacute;c sĩ kiểm tra thiệt hại c&oacute; thể xảy ra.</span><span style=\"font-family:Arial;mso-ansi-language:EN-US\"><o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Vi t&iacute;nh chụp cắt lớp (CT). Đ&acirc;y l&agrave; một h&igrave;nh thức kh&ocirc;ng x&acirc;m lấn của chụp động mạch kết hợp ph&acirc;n t&iacute;ch tr&ecirc;n m&aacute;y vi t&iacute;nh của h&igrave;nh ảnh X-ray với việc sử dụng thuốc nhuộm tương phản để cho ph&eacute;p b&aacute;c sĩ kiểm tra cấu tr&uacute;c của động mạch chủ v&agrave; c&aacute;c nh&aacute;nh gần đ&oacute; của n&oacute;, v&agrave; theo d&otilde;i lưu lượng m&aacute;u.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Si&ecirc;u &acirc;m Doppler, một phi&ecirc;n bản tinh vi hơn của si&ecirc;u &acirc;m th&ocirc;ng thường, c&oacute; khả năng tạo ra h&igrave;nh ảnh c&oacute; độ ph&acirc;n giải của c&aacute;c bức t</span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">h&agrave;nh</span><span lang=\"VI\" style=\"font-family:Arial\"> của động mạch nhất định, chẳng hạn như động mạch cảnh v&agrave; động mạch dưới đ&ograve;n. N&oacute; c&oacute; thể ph&aacute;t hiện những thay đổi tinh tế trong c&aacute;c động mạch trước khi c&aacute;c kỹ thuật h&igrave;nh ảnh kh&aacute;c. Si&ecirc;u &acirc;m Doppler cũng c&oacute; thể gi&uacute;p ph&acirc;n biệt giữa bệnh vi&ecirc;m động mạch Takayasu v&agrave; xơ vữa động mạch, l&agrave; t&igrave;nh trạng phổ biến hơn g&acirc;y ra bởi sự t&iacute;ch tụ của c&aacute;c hạt cholesterol v&agrave; c&aacute;c mảnh vỡ tế b&agrave;o kh&aacute;c trong động mạch.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Kh&ocirc;ng giống như c&aacute;c loại vi&ecirc;m mạch, loại bỏ v&agrave; ph&acirc;n t&iacute;ch m&ocirc; (sinh thiết) kh&ocirc;ng thường được sử dụng để chẩn đo&aacute;n bệnh vi&ecirc;m động mạch Takayasu. <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Bởi v&igrave; của bệnh vi&ecirc;m động mạch Takayasu c&oacute; một xu hướng t&aacute;i ph&aacute;t hoặc b&ugrave;ng l&ecirc;n sau khi thuy&ecirc;n giảm trong một thời gian, c&aacute;c x&eacute;t nghiệm n&agrave;y c&oacute; thể được sử dụng kh&ocirc;ng chỉ để chẩn đo&aacute;n m&agrave; c&ograve;n để theo d&otilde;i tiến triển của bệnh v&agrave; theo d&otilde;i hiệu quả điều trị. Một số thuốc d&ugrave;ng cho bệnh vi&ecirc;m động mạch Takayasu c&oacute; thể c&oacute; t&aacute;c dụng c&oacute; khả năng g&acirc;y hại về l&acirc;u d&agrave;i, v&igrave; vậy điều quan trọng cho biết khi uống thuốc l&agrave; c&oacute; lợi v&agrave; rủi ro của n&oacute; lớn hơn lợi &iacute;ch của n&oacute;.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-size:18.0pt;line-height:150%;font-family:Arial\">Phương ph&aacute;p điều trị v&agrave; thuốc<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Mục ti&ecirc;u của điều trị l&agrave; kiểm so&aacute;t vi&ecirc;m v&agrave; ngăn chặn thiệt hại th&ecirc;m cho c&aacute;c mạch m&aacute;u, với c&aacute;c t&aacute;c dụng phụ d&agrave;i hạn &iacute;t nhất. Takayasu đ&ocirc;i khi c&oacute; thể kh&oacute; điều trị bởi v&igrave; ngay cả khi xuất hiện thuy&ecirc;n giảm, bệnh hoạt động c&oacute; thể vẫn tiếp tục &quot;&acirc;m thầm.&quot; Ngo&agrave;i ra, thời gian một số người được chẩn đo&aacute;n, n&oacute; c&oacute; thể thiệt hại kh&ocirc;ng thể đảo ngược c&oacute; thể đ&atilde; xảy ra.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Mặt kh&aacute;c, nếu điều kiện tương đối ổn định v&agrave; kh&ocirc;ng biến chứng, c&oacute; thể kh&ocirc;ng cần điều trị. <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Điều trị thường bao gồm c&aacute;c loại thuốc, v&agrave; trong một số trường hợp</span><span style=\"font-family:Arial;mso-ansi-language:\r\nEN-US\"> phải</span><span lang=\"VI\" style=\"font-family:Arial\"> phẫu thuật. <o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-size:14.0pt;line-height:150%;font-family:Arial\">Thuốc</span><span style=\"font-size:14.0pt;line-height:150%;font-family:Arial;mso-ansi-language:\r\nEN-US\"><o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Nhiều người trong số c&aacute;c loại thuốc n&agrave;y c&oacute; t&aacute;c dụng phụ nghi&ecirc;m trọng, l&acirc;u d&agrave;i, v&igrave; vậy b&aacute;c sĩ sẽ cố gắng để c&acirc;n bằng lợi &iacute;ch chống lại c&aacute;c rủi ro tiềm năng bằng c&aacute;ch kiểm so&aacute;t liều lượng của thuốc v&agrave; độ d&agrave;i của thời gian </span><span style=\"font-family:Arial;mso-ansi-language:\r\nEN-US\">d&ugrave;ng</span><span lang=\"VI\" style=\"font-family:Arial\"> ch&uacute;ng.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Corticosteroid. D&ograve;ng đầu ti&ecirc;n của điều trị thường l&agrave; với một corticosteroid như prednisone hoặc methylprednisolone (Medrol). Khoảng một nửa số người được điều trị với corticosteroid đ&aacute;p ứng tốt. Thường bắt đầu cảm thấy tốt hơn chỉ trong một v&agrave;i ng&agrave;y, nhưng cần phải tiếp tục d&ugrave;ng thuốc một thời gian d&agrave;i. Sau th&aacute;ng đầu ti&ecirc;n, b&aacute;c sĩ c&oacute; thể dần dần bắt đầu giảm liều cho đến khi đạt đến liều thấp nhất cần để kiểm so&aacute;t vi&ecirc;m. Một số c&aacute;c triệu chứng c&oacute; thể trở lại trong thời gian n&agrave;y. D&agrave;i hạn t&aacute;c dụng phụ của corticosteroid bao gồm đục thủy tinh thể, lượng đường trong m&aacute;u cao, nguy cơ tăng c&aacute;c bệnh nhiễm tr&ugrave;ng, mất canxi xương, kinh nguyệt kh&ocirc;ng đều, ức chế tuyến thượng thận sản xuất hormone, mỏng da, b&eacute;o ph&igrave;, dễ bị bầm t&iacute;m v&agrave; chậm l&agrave;nh vết thương.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Thuốc g&acirc;y độc tế b&agrave;o. Nếu t&igrave;nh trạng kh&ocirc;ng đ&aacute;p ứng tốt với corticosteroid hoặc gặp kh&oacute; khăn giảm dần thuốc, c&oacute; thể cần điều trị với c&aacute;c thuốc g&acirc;y độc tế b&agrave;o, chẳng hạn như methotrexate (Trexall, Rheumatrex) hoặc azathioprine (Imuran, Azasan). Những loại thuốc n&agrave;y ức chế vi&ecirc;m trong c&aacute;c mạch m&aacute;u, nhưng c&oacute; nguy cơ. C&oacute; thể l&agrave;m tăng t&iacute;nh nhạy cảm để nhiễm tr&ugrave;ng, cũng như nguy cơ ph&aacute;t triển khối u n&uacute;t bạch huyết (lymphoma) v&agrave; ung thư da.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Thuốc cấy gh&eacute;p. Một số người đ&aacute;p ứng tốt với thuốc đ&atilde; ph&aacute;t triển cho những người nhận được cấy gh&eacute;p nội tạng. Những loại thuốc n&agrave;y, bao gồm cả mycophenolate (CellCept), &aacute;p </span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">chế</span><span lang=\"VI\" style=\"font-family:Arial\"> hệ thống miễn dịch, v&agrave; đ&atilde; c&oacute; hiệu quả giảm vi&ecirc;m mạch m&aacute;u ở những người c&oacute; bệnh vi&ecirc;m động mạch Takayasu. T&aacute;c dụng phụ bao gồm đau bụng, sốt, t&aacute;o b&oacute;n, nhức đầu v&agrave; sưng. Những loại thuốc n&agrave;y c&oacute; thể kh&ocirc;ng được thực hiện trong thời kỳ mang thai.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-size:14.0pt;line-height:150%;font-family:Arial\">Phẫu thuật</span><span style=\"font-size:14.0pt;line-height:150%;font-family:Arial;mso-ansi-language:\r\nEN-US\"><o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Nếu động mạch trở n&ecirc;n thu hẹp hoặc bị chặn, phẫu thuật c&oacute; thể l&agrave; cần thiết để mở hoặc bỏ qua c&aacute;c động mạch để cho ph&eacute;p d&ograve;ng chảy li&ecirc;n tục của m&aacute;u. Th&ocirc;ng thường, điều n&agrave;y gi&uacute;p cải thiện c&aacute;c triệu chứng như huyết &aacute;p cao v&agrave; đau ngực. Trong một số trường hợp, mặc d&ugrave;, thu hẹp hoặc tắc nghẽn c&oacute; thể t&aacute;i ph&aacute;t, đ&ograve;i hỏi một thủ tục thứ hai. Ngo&agrave;i ra, nếu ph&aacute;t triển chứng ph&igrave;nh động mạch lớn, phẫu thuật c&oacute; thể cần thiết để ngăn chặn ch&uacute;ng vỡ. Những thủ tục n&agrave;y, tốt nhất thực hiện khi chứng vi&ecirc;m c&aacute;c động mạch </span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">nặng</span><span lang=\"VI\" style=\"font-family:Arial\"> bao gồm:<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Phẫu thuật </span><span style=\"font-family:\r\nArial;mso-ansi-language:EN-US\">bắc cầu. </span><span lang=\"VI\" style=\"font-family:\r\nArial\">Trong thủ tục n&agrave;y, một động mạch hoặc tĩnh mạch được lấy ra từ một phần kh&aacute;c của cơ thể v&agrave; gắn liền với động mạch bị chặn, cung cấp một đường v&ograve;ng cho m&aacute;u chảy qua.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Nong mạch v&agrave;nh</span><span style=\"font-family:\r\nArial;mso-ansi-language:EN-US\"> qua da</span><span lang=\"VI\" style=\"font-family:\r\nArial\">. Trong thủ tục n&agrave;y, một quả b&oacute;ng nhỏ được luồn qua mạch m&aacute;u v&agrave; th&agrave;nh động mạch bị ảnh hưởng. Khi ở trong</span><span style=\"font-family:Arial;\r\nmso-ansi-language:EN-US\">,</span><span lang=\"VI\" style=\"font-family:Arial\"> b&oacute;ng được mở để mở rộng c&aacute;c khu vực bị chặn.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Stenting. D&acirc;y cuộn lưới gọi l&agrave; stent c&oacute; thể được đưa v&agrave;o khu vực bằng c&aacute;ch tạo h&igrave;nh mạch. C&aacute;c ống đỡ động mạch gi&uacute;p chống đỡ mở động mạch để ngăn chặn c&aacute;c mạch m&aacute;u thu hẹp lại.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-size:18.0pt;line-height:150%;font-family:Arial\">Đối ph&oacute; v&agrave; hỗ trợ<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Khi bệnh vi&ecirc;m động mạch Takayasu được x&aacute;c định v&agrave; điều trị sớm, ti&ecirc;n lượng thường l&agrave; tốt. Một trong những th&aacute;ch thức lớn nhất c&oacute; thể đối ph&oacute; với c&aacute;c t&aacute;c dụng phụ của thuốc. Những gợi &yacute; sau đ&acirc;y c&oacute; thể gi&uacute;p:<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Hiểu r&otilde; t&igrave;nh trạng. Tất cả mọi thứ c&oacute; thể về bệnh vi&ecirc;m động mạch Takayasu v&agrave; điều trị của n&oacute;. Biết t&aacute;c dụng phụ c&oacute; thể c&oacute; bất cứ loại thuốc n&agrave;o c&oacute; v&agrave; b&aacute;o c&aacute;o bất kỳ thay đổi sức khỏe với b&aacute;c sĩ.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Ăn một chế độ ăn uống l&agrave;nh mạnh. Ăn cũng c&oacute; thể gi&uacute;p ngăn ngừa c&aacute;c vấn đề tiềm năng c&oacute; thể dẫn đến t&igrave;nh trạng </span><span style=\"font-family:Arial;mso-ansi-language:EN-US\">bệnh </span><span lang=\"VI\" style=\"font-family:Arial\">v&agrave; </span><span style=\"font-family:Arial;mso-ansi-language:\r\nEN-US\">t&aacute;c dụng phụ của </span><span lang=\"VI\" style=\"font-family:Arial\">thuốc, chẳng hạn như huyết &aacute;p cao, lo&atilde;ng xương v&agrave; bệnh tiểu đường. Nhấn mạnh tr&aacute;i c&acirc;y tươi v&agrave; rau quả, ngũ cốc nguy&ecirc;n hạt v&agrave; c&aacute;c loại thịt nạc v&agrave; c&aacute;, trong khi hạn chế muối, đường v&agrave; rượu.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">H&atilde;y chắc chắn để c&oacute; được số lượng đầy đủ canxi v&agrave; vitamin D gi&uacute;p ngăn ngừa lo&atilde;ng xương, một t&aacute;c dụng phụ chủ yếu của điều trị bằng corticosteroid. Nếu t&igrave;m thấy n&oacute; kh&oacute; để c&oacute; canxi từ chế độ ăn uống bởi v&igrave; kh&ocirc;ng ăn c&aacute;c sản phẩm từ sữa, v&iacute; dụ, h&atilde;y n&oacute;i chuyện với b&aacute;c sĩ về bổ sung canxi, thường được kết hợp với vitamin D v&agrave; c&oacute; thể gi&uacute;p chống lo&atilde;ng xương.<o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: justify;\"><span lang=\"VI\" style=\"font-family:Arial\">Tập thể dục thường xuy&ecirc;n. Aerobic tập thể dục thường xuy&ecirc;n, chẳng hạn như đi bộ, c&oacute; thể gi&uacute;p ngăn ngừa mất xương, huyết &aacute;p cao v&agrave; tiểu đường. N&oacute; cũng c&oacute; lợi cho tim v&agrave; phổi. Ngo&agrave;i ra, nhiều người thấy rằng tập thể dục cải thiện t&acirc;m trạng của v&agrave; &yacute; nghĩa tổng thể của hạnh ph&uacute;c.</span><span style=\"font-family:Arial;mso-ansi-language:EN-US\"><o:p></o:p></span></p>\r\n<p style=\"margin-top: 6pt; line-height: 150%; text-align: right;\"><b><i><span style=\"font-family:Arial;mso-ansi-language:EN-US\"><span style=\"color: rgb(128, 0, 0);\"></span><o:p></o:p></span></i></b><span lang=\"VI\" style=\"font-family:Arial\"><br>\r\n</span></p></span>\n', 2);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(28, 'Còn ống động mạch (PDA)', '\n   <h1 class=\"title\">Còn ống động mạch (PDA)</h1>   <span class=\"des\">Một ống động mạch nhỏ thường không gây ra triệu chứng. Bác sĩ có thể khám phá ra nó trong một bài kiểm tra thường xuyên. Một trẻ sơ sinh với một ống động mạch lớn hơn thường có vấn đề tăng cân và có các dấu hiệu và triệu chứng. </span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&ograve;n ống động mạch (PDA) l&agrave; tồn tại dai dẳng ống giữa hai mạch m&aacute;u lớn h&agrave;ng đầu từ tim. Đ&acirc;y khuyết tật tim l&uacute;c mới sinh (bẩm sinh) thường tự đ&oacute;ng hoặc c&oacute; thể điều trị dễ d&agrave;ng. Nếu kh&ocirc;ng điều trị, ống động mạch c&oacute; thể g&acirc;y ra qu&aacute; nhiều m&aacute;u chảy qua tim, l&agrave;m suy yếu c&aacute;c cơ tim, g&acirc;y ra suy tim v&agrave; c&aacute;c biến chứng kh&aacute;c.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một ống động mạch nhỏ thường kh&ocirc;ng g&acirc;y ra triệu chứng. B&aacute;c sĩ c&oacute; thể kh&aacute;m ph&aacute; ra n&oacute; trong một b&agrave;i kiểm tra thường xuy&ecirc;n. Một trẻ sơ sinh với một ống động mạch lớn hơn thường c&oacute; vấn đề tăng c&acirc;n v&agrave; c&oacute; c&aacute;c dấu hiệu v&agrave; triệu chứng. Một đứa trẻ lớn tuổi c&oacute; một ống động mạch c&oacute; thể kh&ocirc;ng hoạt động như b&igrave;nh thường, c&oacute; thể dễ mệt mỏi hơn v&agrave; c&oacute; thể bị nhiễm tr&ugrave;ng phổi thường xuy&ecirc;n. Đ&ocirc;i khi, một ống động mạch nhỏ c&oacute; thể kh&ocirc;ng được ph&aacute;t hiện cho đến khi trưởng th&agrave;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều trị t&ugrave;y chọn để sửa chữa một ống động mạch bao gồm gi&aacute;m s&aacute;t, thuốc v&agrave; phẫu thuật. </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Triệu chứng ống động mạch kh&aacute;c nhau với c&aacute;c k&iacute;ch thước của khuyết tật v&agrave; tuổi thai của trẻ sơ sinh l&uacute;c mới sinh. Một PDA nhỏ c&oacute; thể kh&ocirc;ng g&acirc;y ra dấu hiệu hoặc triệu chứng, v&agrave; c&oacute; thể kh&ocirc;ng bị ph&aacute;t hiện trong một thời gian, thậm ch&iacute; cho đến khi trưởng th&agrave;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một PDA lớn c&oacute; thể g&acirc;y ra c&aacute;c dấu hiệu của suy tim ngay sau khi sinh. Một trẻ sơ sinh thiếu th&aacute;ng c&oacute; thể c&oacute; c&aacute;c vấn đề kh&aacute;c li&ecirc;n quan được sinh ra qu&aacute; sớm, v&agrave; kiểm tra li&ecirc;n quan c&oacute; thể ph&aacute;t hiện ra một khiếm khuyết tim.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">B&aacute;c sĩ đầu ti&ecirc;n c&oacute; thể nghi ngờ một khuyết tật tim trong một kiểm tra thường xuy&ecirc;n trong khi nghe tim của b&eacute; th&ocirc;ng qua ống nghe. </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một PDA lớn, t&igrave;m thấy trong giai đoạn ph&ocirc;i thai hoặc trẻ em, c&oacute; thể g&acirc;y ra: </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ăn k&eacute;m, k&eacute;m tăng trưởng.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Ra mồ h&ocirc;i khi kh&oacute;c hoặc chơi.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Li&ecirc;n tục thở nhanh hoặc kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Dễ mệt mỏi.<br>\r\n</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tim đập nhanh.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thường xuy&ecirc;n bị nhiễm tr&ugrave;ng phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một m&agrave;u da hơi xanh hoặc n&acirc;u sẫm.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Đến gặp b&aacute;c sĩ khi</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Gọi b&aacute;c sĩ nếu trẻ sơ sinh hoặc con: </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mệt mỏi một c&aacute;ch dễ d&agrave;ng khi ăn hoặc chơi</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&ocirc;ng tăng c&acirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trở n&ecirc;n kh&oacute; thở khi ăn hoặc kh&oacute;c.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Lu&ocirc;n lu&ocirc;n thở nhanh hoặc kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Chuyển n&acirc;u sẫm hoặc xanh khi kh&oacute;c hoặc ăn.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một ống động mạch kh&ocirc;ng tự đ&oacute;ng phổ biến hơn ở trẻ sinh non, nhưng hiếm gặp ở trẻ sinh ra đủ th&aacute;ng. </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khi một em b&eacute; ph&aacute;t triển trong tử cung, kết nối mạch m&aacute;u (ống động mạch) giữa hai mạch m&aacute;u lớn h&agrave;ng đầu từ tim - động mạch chủ v&agrave; động mạch phổi - l&agrave; một phần b&igrave;nh thường v&agrave; cần thiết của lưu th&ocirc;ng m&aacute;u của b&eacute; khi ở trong tử cung. Nhưng, kết nối n&agrave;y được đ&oacute;ng trong v&ograve;ng hai hoặc ba ng&agrave;y sau khi sinh để điều chỉnh tim của trẻ sơ sinh đến cuộc sống b&ecirc;n ngo&agrave;i tử cung. Ở trẻ sơ sinh thiếu th&aacute;ng, kết nối thường tự đ&oacute;ng trong v&ograve;ng một v&agrave;i tuần sau khi sinh. Nhưng nếu n&oacute; vẫn c&ograve;n mở, được gọi l&agrave; tồn tại ống động mạch.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Việc mở bất thường g&acirc;y ra qu&aacute; nhiều m&aacute;u lưu th&ocirc;ng đến phổi v&agrave; tim. Nếu kh&ocirc;ng được điều trị, &aacute;p lực m&aacute;u trong phổi c&oacute; thể tăng (phổi tăng huyết &aacute;p) v&agrave; c&oacute; thể l&agrave;m tim suy yếu.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Khuyết tật tim bẩm sinh ph&aacute;t sinh từ vấn đề đầu trong ph&aacute;t triển của tim - nhưng thường kh&ocirc;ng c&oacute; nguy&ecirc;n nh&acirc;n r&otilde; r&agrave;ng. Di truyền v&agrave; yếu tố m&ocirc;i trường c&oacute; thể đ&oacute;ng một vai tr&ograve;.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Yếu tố nguy cơ</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Yếu tố nguy cơ c&oacute; tồn tại ống động mạch bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Được sinh ra qu&aacute; sớm.</span></b><span style=\"font-family: Arial;\"> Tồn tại ống động mạch (PDA) xảy ra phổ biến hơn ở những em b&eacute; được sinh ra qu&aacute; sớm so với c&aacute;c em b&eacute; được sinh ra đủ th&aacute;ng.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">C&oacute; dị tật tim kh&aacute;c.</span></b><span style=\"font-family: Arial;\"> Em b&eacute; c&oacute; vấn đề tim mạch kh&aacute;c khi ch&uacute;ng được sinh ra (dị tật tim bẩm sinh) cũng nhiều khả năng tồn tại ống động mạch.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Lịch sử gia đ&igrave;nh v&agrave; điều kiện di truyền kh&aacute;c.</span></b><span style=\"font-family: Arial;\"> Nếu c&oacute; tiền sử gia đ&igrave;nh khuyết tật tim, nhiều khả năng con c&oacute; thể c&ograve;n ống động mạch. Điều kiện di truyền kh&aacute;c như hội chứng Down, cũng đ&atilde; được li&ecirc;n kết với một cơ hội gia tăng của việc c&oacute; PDA.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Nhiễm Rubella trong thai kỳ.</span></b><span style=\"font-family: Arial;\"> Bị nhiễm rubella (sởi Đức) trong khi mang thai c&oacute; thể l&agrave;m tăng nguy cơ dị tật tim. C&aacute;c virus rubella qua nhau thai v&agrave; l&acirc;y lan qua hệ thống c&aacute;c mạch m&aacute;u tuần ho&agrave;n của b&eacute;, l&agrave;m tổn hại v&agrave; c&aacute;c cơ quan, bao gồm cả tr&aacute;i tim.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Kh&oacute; kiểm so&aacute;t bệnh tiểu đường trong thai kỳ.</span></b><span style=\"font-family: Arial;\"> Bệnh tiểu đường kh&ocirc;ng được kiểm so&aacute;t trong người mẹ sẽ ảnh hưởng đến lượng đường trong m&aacute;u của con m&igrave;nh, g&acirc;y ra nhiều hiệu ứng g&acirc;y hại cho em b&eacute; ph&aacute;t triển.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ma t&uacute;y hoặc uống rượu hoặc tiếp x&uacute;c với c&aacute;c chất nhất định trong qu&aacute; tr&igrave;nh mang thai.</span></b><span style=\"font-family: Arial;\"> Sử dụng c&aacute;c loại thuốc nhất định, rượu hoặc ma t&uacute;y hoặc tiếp x&uacute;c với h&oacute;a chất hoặc ph&oacute;ng xạ trong khi mang thai c&oacute; thể g&acirc;y hại cho em b&eacute; ph&aacute;t triển.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c biến chứng</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một ống động mạch nhỏ c&oacute; thể kh&ocirc;ng g&acirc;y ra bất kỳ biến chứng. Khiếm khuyết lớn kh&ocirc;ng được điều trị c&oacute; thể g&acirc;y ra:</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Cao &aacute;p trong phổi (phổi tăng huyết &aacute;p).</span></b><span style=\"font-family: Arial;\"> Nếu qu&aacute; nhiều m&aacute;u tiếp tục lưu th&ocirc;ng qua c&aacute;c động mạch ch&iacute;nh của tr&aacute;i tim th&ocirc;ng qua ống động mạch, n&oacute; c&oacute; thể dẫn đến tăng &aacute;p phổi. Phổi tăng huyết &aacute;p c&oacute; thể g&acirc;y tổn thương phổi vĩnh viễn. Một ống động mạch lớn c&oacute; thể dẫn đến hội chứng Eisenmenger, một loại kh&ocirc;ng thể đảo ngược của tăng &aacute;p phổi.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Suy tim.</span></b><span style=\"font-family: Arial;\"> Ống động mạch cuối c&ugrave;ng c&oacute; thể g&acirc;y ra c&aacute;c cơ tim suy yếu, dẫn đến suy tim. Suy tim l&agrave; một bệnh m&atilde;n t&iacute;nh trong đ&oacute; tim kh&ocirc;ng thể bơm hiệu quả.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Nhiễm tr&ugrave;ng tim (vi&ecirc;m nội t&acirc;m mạc).</span></b><span style=\"font-family: Arial;\"> Những người c&oacute; vấn đề về cấu tr&uacute;c tim, chẳng hạn như c&ograve;n ống động mạch, c&oacute; nguy cơ cao hơn vi&ecirc;m nội t&acirc;m mạc nhiễm khuẩn hơn l&agrave; d&acirc;n số n&oacute;i chung. Vi&ecirc;m nội t&acirc;m mạc nhiễm tr&ugrave;ng l&agrave; t&igrave;nh trạng vi&ecirc;m lớp l&oacute;t b&ecirc;n trong của tim g&acirc;y ra do nhiễm tr&ugrave;ng bởi vi khuẩn.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Nhịp tim kh&ocirc;ng đều (loạn nhịp tim).</span></b><span style=\"font-family: Arial;\"> Mở rộng buồng tim do ống động mạch l&agrave;m tăng nguy cơ loạn nhịp tim. Điều n&agrave;y l&agrave;m tăng nguy cơ v&agrave; thường xảy ra chỉ với một ống động mạch lớn.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Ống động mạch v&agrave; mang thai</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mang thai thường l&agrave; một mối quan t&acirc;m đối với phụ nữ sinh ra với một khuyết tật tim. Hầu hết phụ nữ với ống động mạch nhỏ c&oacute; thể chịu đựng thời kỳ mang thai m&agrave; kh&ocirc;ng c&oacute; bất kỳ vấn đề. Tuy nhi&ecirc;n, c&oacute; một khiếm khuyết lớn hơn hoặc c&oacute; biến chứng, như suy tim, loạn nhịp tim hoặc cao huyết &aacute;p động mạch phổi, c&oacute; thể l&agrave;m tăng nguy cơ biến chứng trong thai kỳ. Một phụ nữ bị hội chứng Eisenmenger kh&ocirc;ng n&ecirc;n c&oacute; thai v&igrave; n&oacute; c&oacute; thể g&acirc;y nguy hiểm cho đời sống của người phụ nữ.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Bất cứ ai c&oacute; một khiếm khuyết tim bẩm sinh, sửa chữa hay kh&ocirc;ng, những người đang c&acirc;n nhắc việc bắt đầu một gia đ&igrave;nh, n&ecirc;n thảo luận cẩn thận trước với b&aacute;c sĩ của m&igrave;nh. Trong một số trường hợp, tham vấn định kiến với c&aacute;c b&aacute;c sĩ chuy&ecirc;n về tim mạch, di truyền học v&agrave; c&oacute; nguy cơ cao chăm s&oacute;c sản khoa l&agrave; cần thiết. Một số thuốc tim c&oacute; thể g&acirc;y ra vấn đề nghi&ecirc;m trọng cho một em b&eacute; ph&aacute;t triển v&agrave; c&oacute; thể cần phải được dừng lại, điều chỉnh trước khi c&oacute; thai.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">B&aacute;c sĩ đầu ti&ecirc;n c&oacute; thể nghi ngờ c&oacute; một ống động mạch dựa tr&ecirc;n lắng nghe nhịp tim của trẻ. Ống động mạch c&oacute; thể g&acirc;y ra một tiếng thổi tim c&oacute; thể nghe được qua ống nghe. Nếu b&aacute;c sĩ nghe thấy một tiếng thổi tim hoặc thấy c&aacute;c dấu hiệu hoặc triệu chứng của một khuyết tật tim, người đ&oacute; c&oacute; thể y&ecirc;u cầu một hoặc một số c&aacute;c x&eacute;t nghiệm n&agrave;y:</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Si&ecirc;u &acirc;m tim.</span></b><span style=\"font-family: Arial;\"> Si&ecirc;u &acirc;m tim sử dụng s&oacute;ng &acirc;m thanh để tạo ra một h&igrave;nh ảnh video của tim. H&igrave;nh ảnh n&agrave;y c&oacute; thể gi&uacute;p b&aacute;c sĩ nh&igrave;n thấy c&aacute;c buồng tim v&agrave; đ&aacute;nh gi&aacute; tim bơm như thế n&agrave;o. Thử nghiệm n&agrave;y cũng kiểm tra c&aacute;c van tim v&agrave; sẽ cho bất kỳ khuyết tật tim kh&aacute;c.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Chụp X-quang.</span></b><span style=\"font-family: Arial;\"> H&igrave;nh ảnh X ray gi&uacute;p b&aacute;c sĩ thấy t&igrave;nh trạng của phổi v&agrave; tim của b&eacute; v&agrave; số lượng m&aacute;u trong phổi. X-quang c&oacute; thể x&aacute;c định c&aacute;c điều kiện kh&aacute;c hơn l&agrave; một khuyết tật tim.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Điện t&acirc;m đồ (ECG).</span></b><span style=\"font-family: Arial;\"> Thử nghiệm n&agrave;y ghi lại hoạt động điện của tim. Thử nghiệm n&agrave;y sẽ gi&uacute;p chẩn đo&aacute;n dị tật tim hay c&aacute;c vấn đề nhịp.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đặt ống th&ocirc;ng tim.</span></b><span style=\"font-family: Arial;\"> X&eacute;t nghiệm n&agrave;y thường kh&ocirc;ng cần thiết để chẩn đo&aacute;n một PDA một m&igrave;nh, nhưng c&oacute; thể được thực hiện để kiểm tra tim khuyết tật bẩm sinh kh&aacute;c được t&igrave;m thấy trong si&ecirc;u &acirc;m tim. Trong thử nghiệm n&agrave;y, một ống th&ocirc;ng được ch&egrave;n v&agrave;o mạch m&aacute;u ở trẻ - h&aacute;ng hay c&aacute;nh tay v&agrave; hướng dẫn n&oacute; v&agrave;o tim. Th&ocirc;ng qua đặt ống th&ocirc;ng, c&aacute;c b&aacute;c sĩ c&oacute; thể thực hiện c&aacute;c thủ tục để đ&oacute;ng ống động mạch.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Chụp cắt lớp vi t&iacute;nh tim (CT) hoặc chụp cộng hưởng từ (MRI).</span></b><span style=\"font-family: Arial;\"> C&aacute;c x&eacute;t nghiệm n&agrave;y c&oacute; thể được sử dụng để chẩn đo&aacute;n c&aacute;c vấn đề về tim, thường ở người lớn. Một ống động mạch c&oacute; thể được ph&aacute;t hiện khi CT hay MRI tim đang được thực hiện v&igrave; l&yacute; do kh&aacute;c. Trong CT scan tim, nằm tr&ecirc;n một bảng b&ecirc;n trong một m&aacute;y h&igrave;nh b&aacute;nh r&aacute;n. Ống tia X b&ecirc;n trong m&aacute;y quay xung quanh cơ thể v&agrave; thu thập h&igrave;nh ảnh của tim v&agrave; ngực.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong một MRI tim, nằm tr&ecirc;n một bảng b&ecirc;n trong một m&aacute;y giống như ống d&agrave;i tạo ra một từ trường. Từ trường gắn c&aacute;c hạt nguy&ecirc;n tử trong một số của c&aacute;c tế b&agrave;o. Khi s&oacute;ng radio được ph&aacute;t s&oacute;ng đối với c&aacute;c hạt li&ecirc;n kết, sản xuất ra t&iacute;n hiệu thay đổi t&ugrave;y theo loại m&ocirc; của ch&uacute;ng. C&aacute;c t&iacute;n hiệu tạo ra h&igrave;nh ảnh của tr&aacute;i tim.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Điều trị c&ograve;n ống động mạch phụ thuộc v&agrave;o tuổi của người được điều trị. </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Cảnh gi&aacute;c chờ đợi.</span></b><span style=\"font-family: Arial;\"> Một em b&eacute; sinh non, ống động mạch thường tự đ&oacute;ng trong những tuần sau khi sinh. C&aacute;c b&aacute;c sĩ sẽ theo d&otilde;i tim của em b&eacute; trong những tuần để chắc chắn rằng c&aacute;c mạch m&aacute;u mở được đ&oacute;ng đ&uacute;ng c&aacute;ch. Đối với trẻ đủ th&aacute;ng, trẻ em v&agrave; người lớn c&oacute; PDA nhỏ m&agrave; kh&ocirc;ng g&acirc;y ra vấn đề sức khỏe kh&aacute;c, c&oacute; thể b&aacute;c sĩ sẽ khuy&ecirc;n n&ecirc;n theo d&otilde;i c&aacute;c điều kiện v&agrave; kh&ocirc;ng c&oacute; bất kỳ thủ tục kh&aacute;c để đ&oacute;ng PDA.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Chống vi&ecirc;m kh&ocirc;ng steroid.</span></b><span style=\"font-family: Arial;\"> Đối với trẻ sinh non, b&aacute;c sĩ c&oacute; thể sử dụng c&aacute;c loại thuốc chống vi&ecirc;m kh&ocirc;ng steroid (NSAID) như ibuprofen hoặc indomethacin, để gi&uacute;p đỡ một PDA. NSAIDs khối giống như h&oacute;a chất nội tiết tố trong cơ thể giữ cho mở PDA. NSAID sẽ kh&ocirc;ng đ&oacute;ng PDA trong hạn trẻ đủ th&aacute;ng, trẻ em hay người lớn.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Phẫu thuật tim mở.</span></b><span style=\"font-family: Arial;\"> Nếu thuốc kh&ocirc;ng đ&oacute;ng cửa PDA, v&agrave; t&igrave;nh trạng của b&eacute; đ&atilde; g&acirc;y ra c&aacute;c vấn đề y tế, phẫu thuật tim mở c&oacute; thể được khuyến kh&iacute;ch.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong qu&aacute; tr&igrave;nh phẫu thuật, b&aacute;c sĩ phẫu thuật sẽ l&agrave;m một cắt nhỏ giữa c&aacute;c xương sườn để đạt tới tr&aacute;i tim v&agrave; sửa chữa ống mở bằng c&aacute;ch sử dụng kh&acirc;u hoặc clip. Sau khi phẫu thuật, trẻ em sẽ vẫn ở trong bệnh viện v&agrave;i ng&agrave;y để quan s&aacute;t. N&oacute; thường mất một v&agrave;i tuần để ho&agrave;n to&agrave;n hồi phục. Phẫu thuật mở tim cũng c&oacute; thể được d&ugrave;ng cho người lớn người c&oacute; PDA g&acirc;y ra vấn đề sức khỏe.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thủ tục Catheter.</span></b><span style=\"font-family: Arial;\"> Catheter thủ tục, &iacute;t x&acirc;m lấn hơn phẫu thuật tim mở, kh&ocirc;ng phải l&agrave; một lựa chọn ngay lập tức cho trẻ sinh non, v&igrave; l&agrave; qu&aacute; nhỏ. Tuy nhi&ecirc;n, nếu em b&eacute; kh&ocirc;ng c&oacute; bất kỳ vấn đề sức khỏe li&ecirc;n quan đến PDA của m&igrave;nh, b&aacute;c sĩ c&oacute; thể khuy&ecirc;n n&ecirc;n chờ đợi cho đến khi b&eacute; được khoảng 6 th&aacute;ng tuổi để thực hiện một quy tr&igrave;nh đặt ống th&ocirc;ng để sửa chữa PDA. Ống th&ocirc;ng c&oacute; xu hướng c&oacute; biến chứng &iacute;t hơn v&agrave; ngắn hơn thời gian hồi phục so với phẫu thuật tim mở. Catheter thủ tục cũng c&oacute; thể được d&ugrave;ng để điều trị trẻ đủ th&aacute;ng, trẻ em v&agrave; người lớn.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong một quy tr&igrave;nh đặt ống th&ocirc;ng, một ống nhỏ (ống th&ocirc;ng) được ch&egrave;n v&agrave;o mạch m&aacute;u ở h&aacute;ng v&agrave; luồng l&ecirc;n đến tim. Th&ocirc;ng qua ống th&ocirc;ng, một plug hoặc cuộn d&acirc;y được lắp để đ&oacute;ng ống động mạch.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Thủ tục n&agrave;y c&oacute; thể được thực hiện tr&ecirc;n cơ sở bệnh nh&acirc;n ngoại tr&uacute;, c&oacute; nghĩa l&agrave; c&oacute; thể sẽ kh&ocirc;ng cần phải ở lại qua đ&ecirc;m trong bệnh viện. C&aacute;c biến chứng từ thủ tục ống th&ocirc;ng bao gồm chảy m&aacute;u, nhiễm tr&ugrave;ng, hoặc chuyển động của c&aacute;c plug hoặc cuộn d&acirc;y từ nơi n&oacute; được đặt trong tr&aacute;i tim.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Kh&aacute;ng sinh dự ph&ograve;ng</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong qu&aacute; khứ, n&oacute; đ&atilde; được khuyến c&aacute;o rằng những người c&oacute; PDA d&ugrave;ng kh&aacute;ng sinh trước khi l&agrave;m nha khoa v&agrave; một số loại thủ tục phẫu thuật để ngăn chặn sự l&acirc;y nhiễm của tr&aacute;i tim (vi&ecirc;m nội t&acirc;m mạc nhiễm khuẩn). </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&aacute;ng sinh dự ph&ograve;ng kh&ocirc;ng c&ograve;n được đề nghị cho hầu hết mọi người với ống động mạch tồn tại. Tuy nhi&ecirc;n, một số người vẫn c&ograve;n cần thuốc kh&aacute;ng sinh, chẳng hạn như những người:</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; điều kiện hoặc van tim nh&acirc;n tạo kh&aacute;c.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; một khiếm khuyết lớn g&acirc;y ra mức độ oxy trong m&aacute;u thấp.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đ&atilde; c&oacute; một van tim nh&acirc;n tạo bằng vật liệu sửa chữa.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu đ&atilde; từng được biết rằng cần phải d&ugrave;ng kh&aacute;ng sinh trước khi bất kỳ thủ tục, n&oacute;i chuyện với b&aacute;c sĩ về việc liệu kh&aacute;ng sinh c&oacute; thật sự cần thiết. </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phong c&aacute;ch sống v&agrave; biện ph&aacute;p khắc phục</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; một khiếm khuyết tim bẩm sinh, hoặc đ&atilde; c&oacute; phẫu thuật để sửa chữa, c&oacute; thể c&oacute; một số lo ngại về chăm s&oacute;c sau. Dưới đ&acirc;y l&agrave; một số vấn đề c&oacute; thể nghĩ về:</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ngăn ngừa nhiễm tr&ugrave;ng.</span></b><span style=\"font-family: Arial;\"> Đối với hầu hết mọi người với ống động mạch, thường xuy&ecirc;n đ&aacute;nh răng v&agrave; d&ugrave;ng chỉ nha khoa răng kết hợp với việc kiểm tra răng miệng thường xuy&ecirc;n l&agrave; c&aacute;ch tốt nhất để gi&uacute;p ngăn ngừa nhiễm tr&ugrave;ng. Trong một số trường hợp, c&oacute; thể cần phải d&ugrave;ng kh&aacute;ng sinh dự ph&ograve;ng trước khi nha khoa v&agrave; phẫu thuật nhất định. B&aacute;c sĩ sẽ gi&uacute;p t&igrave;m hiểu nếu điều n&agrave;y l&agrave; cần thiết.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tập thể dục v&agrave; vui chơi.</span></b><span style=\"font-family: Arial;\"> Phụ huynh của c&aacute;c trẻ em bị dị tật tim bẩm sinh thường lo lắng về yếu tố nguy cơ v&agrave; hoạt động mạnh mẽ ngay cả sau khi điều trị th&agrave;nh c&ocirc;ng. Mặc d&ugrave; một số trẻ c&oacute; thể cần phải hạn chế số lượng hoặc loại tập thể dục, hầu hết trẻ em c&oacute; ống động mạch sẽ c&oacute; cuộc sống b&igrave;nh thường. B&aacute;c sĩ c&oacute; thể tư vấn về những hoạt động được an to&agrave;n cho con m&igrave;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Ph&ograve;ng chống</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Trong hầu hết trường hợp, kh&ocirc;ng thể l&agrave;m bất cứ điều g&igrave; để ngăn chặn c&oacute; một ống động mạch, hoặc bất kỳ khuyết tật tim kh&aacute;c. Tuy nhi&ecirc;n, điều quan trọng để l&agrave;m mọi thứ c&oacute; thể để c&oacute; một thai kỳ khỏe mạnh. Dưới đ&acirc;y l&agrave; những điều cơ bản:</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Cần phải cẩn thận trước khi sinh, thậm ch&iacute; trước khi đang mang thai.</span></b><span style=\"font-family: Arial;\"> Bỏ thuốc l&aacute;, giảm căng thẳng, ngăn chặn kiểm so&aacute;t sinh sản - đ&oacute; l&agrave; tất cả những điều cần phải n&oacute;i chuyện với b&aacute;c sĩ về trước khi c&oacute; thai. Ngo&agrave;i ra, h&atilde;y chắc chắn rằng n&oacute;i chuyện với b&aacute;c sĩ về bất cứ loại thuốc đang d&ugrave;ng.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ăn một chế độ ăn uống c&acirc;n bằng.</span></b><span style=\"font-family: Arial;\"> Bao gồm một bổ sung vitamin c&oacute; chứa axit folic. Ngo&agrave;i ra, hạn chế caffeine.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tập thể dục thường xuy&ecirc;n.</span></b><span style=\"font-family: Arial;\"> L&agrave;m việc với b&aacute;c sĩ để ph&aacute;t triển một kế hoạch tập thể dục ph&ugrave; hợp.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tr&aacute;nh rủi ro.</span></b><span style=\"font-family: Arial;\"> Ch&uacute;ng bao gồm c&aacute;c chất c&oacute; hại như rượu, thuốc l&aacute; v&agrave; ma t&uacute;y bất hợp ph&aacute;p. Ngo&agrave;i ra, tr&aacute;nh chụp X-quang, bồn tắm n&oacute;ng v&agrave; ph&ograve;ng tắm hơi.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tr&aacute;nh nhiễm tr&ugrave;ng.</span></b><span style=\"font-family: Arial;\"> H&atilde;y chắc chắn được cập nhật tr&ecirc;n tất cả c&aacute;c chủng ngừa trước khi mang thai. Một số loại bệnh nhiễm tr&ugrave;ng c&oacute; thể c&oacute; hại cho một em b&eacute; đang ph&aacute;t triển.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Giữ bệnh tiểu đường được kiểm so&aacute;t.</span></b><span style=\"font-family: Arial;\"> Nếu bị tiểu đường, l&agrave;m việc với b&aacute;c sĩ để chắc chắn n&oacute; được kiểm so&aacute;t tốt trước v&agrave; sau khi mang thai.</span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; tiền sử gia đ&igrave;nh khuyết tật tim hoặc rối loạn di truyền kh&aacute;c, xem x&eacute;t việc n&oacute;i chuyện với một cố vấn di truyền trước khi mang thai. </span></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: right;\"><b style=\"\"><i style=\"\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(128, 0, 0);\"></span></span></i></b></p>\r\n<p style=\"margin: 12pt 0in 0.0001pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">&nbsp;</span></p></span>\n', 2);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(29, 'Đau ngực', '\n   <h1 class=\"title\">Đau ngực</h1>   <span class=\"des\">Đau ngực! Có thể đến đột ngột bất cứ lúc nào, may mắn thay đau ngực không luôn luôn là cơn đau tim và vấn đề tim mạch, có thể là: Do tiêu hóa, nguyên nhân cơ xương - sụn - thần kinh, do bệnh hô hấp gây ra và các nguyên nhân khác như hồi hộp, giời leo, ung thư.</span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Định nghĩa</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau ngực c&oacute; thể đến đột ngột bất cứ l&uacute;c n&agrave;o. Cố gắng bỏ qua n&oacute; l&uacute;c đầu, nhưng đau ngực c&oacute; thể g&acirc;y sợ h&atilde;i v&agrave; lo lắng. C&oacute; thể được c&oacute; một cơn đau tim ? </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau ngực l&agrave; một trong những l&yacute; do phổ biến hầu hết mọi người k&ecirc;u gọi gi&uacute;p đỡ khẩn cấp y tế. Mỗi năm c&aacute;c b&aacute;c sĩ ph&ograve;ng cấp cứu đ&aacute;nh gi&aacute; v&agrave; điều trị cho h&agrave;ng triệu người đau ngực. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">May mắn thay, đau ngực kh&ocirc;ng phải lu&ocirc;n lu&ocirc;n l&agrave; t&iacute;n hiệu một cơn đau tim. Th&ocirc;ng thường đau ngực kh&ocirc;ng li&ecirc;n quan đến bất kỳ vấn đề tim mạch. Nhưng ngay cả khi đau ngực kh&ocirc;ng c&oacute; g&igrave; với hệ thống tim mạch, vấn đề vẫn c&oacute; thể quan trọng v&agrave; gi&aacute; trị của thời gian trong một ph&ograve;ng cấp cứu để đ&aacute;nh gi&aacute;. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c triệu chứng</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Một loạt c&aacute;c vấn đề sức khỏe c&oacute; thể g&acirc;y ra đau ngực. Trong nhiều trường hợp, nguy&ecirc;n nh&acirc;n cơ bản kh&ocirc;ng c&oacute; g&igrave; với tim, mặc d&ugrave; kh&ocirc;ng dễ d&agrave;ng để x&aacute;c định. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Đau ngực c&oacute; li&ecirc;n quan đến vấn đề tim mạch </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">N&oacute;i chung đau ngực, li&ecirc;n quan đến một cơn đau tim hay vấn đề kh&aacute;c c&oacute; li&ecirc;n quan đến tim khi c&oacute; một hoặc nhiều điều sau đ&acirc;y: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">&Aacute;p lực, b&oacute;p nghẹt hoặc tức ngực. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau lan đến cổ, quai h&agrave;m, vai v&agrave; c&aacute;nh tay, đặc biệt l&agrave; c&aacute;nh tay tr&aacute;i.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau k&eacute;o d&agrave;i hơn một v&agrave;i ph&uacute;t, biến mất v&agrave; trở lại hoặc thay đổi cường độ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; thở, ra mồ h&ocirc;i, ch&oacute;ng mặt hoặc buồn n&ocirc;n.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Đau ngực c&oacute; li&ecirc;n quan đến vấn đề ngo&agrave;i tim </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&oacute; nghĩa l&agrave; kh&ocirc;ng li&ecirc;n quan đến một vấn tim v&agrave; thường xuy&ecirc;n li&ecirc;n kết với: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Cảm gi&aacute;c n&oacute;ng r&aacute;t sau xương ức xương ức.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Hương vị chua hoặc cảm gi&aacute;c của thực phẩm nhập lại miệng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Kh&oacute; nuốt.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau m&agrave; tốt hơn hoặc tồi tệ hơn khi thay đổi vị tr&iacute; cơ thể.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau m&agrave; tăng cường độ khi h&iacute;t thở s&acirc;u hoặc ho.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau khi đẩy v&agrave;o ngực.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; đau ngực mới hoặc kh&ocirc;ng giải th&iacute;ch được hoặc nghi ngờ đang bị đau tim, h&atilde;y gọi cho trợ gi&uacute;p y tế khẩn cấp ngay lập tức. Đừng ph&iacute; thời gian cố gắng để tự chẩn đo&aacute;n c&aacute;c triệu chứng đau tim. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Mỗi ph&uacute;t l&agrave; rất quan trọng nếu đang c&oacute; một cơn đau tim. Một chuyến đi đến ph&ograve;ng cấp cứu c&oacute; thể cứu cuộc sống, hoặc mang lại y&ecirc;n t&acirc;m nếu kh&ocirc;ng c&oacute; g&igrave; l&agrave; sai nghi&ecirc;m trọng với sức khỏe. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đừng tự m&igrave;nh l&aacute;i xe đến bệnh viện, trừ khi kh&ocirc;ng c&oacute; lựa chọn kh&aacute;c. L&aacute;i xe tự m&igrave;nh đặt v&agrave;o nguy cơ nếu t&igrave;nh trạng đột nhi&ecirc;n xấu đi. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau ngực c&oacute; nhiều nguy&ecirc;n nh&acirc;n c&oacute; thể, tất cả đều xứng đ&aacute;ng được chăm s&oacute;c y tế. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n tim </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đau tim.</span></b><span style=\"font-family: Arial;\"> Một cơn đau tim l&agrave; một kết quả của một cục m&aacute;u đ&ocirc;ng ngăn chặn lưu lượng m&aacute;u đến cơ tim. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đau thắt ngực.</span></b><span style=\"font-family: Arial;\"> Th&agrave;nh mạch cứng, d&agrave;y, c&oacute; chứa mảng cholesterol dần dần c&oacute; thể x&acirc;y dựng l&ecirc;n tr&ecirc;n c&aacute;c bức th&agrave;nh b&ecirc;n trong của c&aacute;c động mạch mang m&aacute;u đến tim. Những mảng b&aacute;m tạm thời thu hẹp c&aacute;c động mạch v&agrave; hạn chế nguồn cung cấp m&aacute;u của tim, đặc biệt trong thời gian gắng sức. Hạn chế lưu lượng m&aacute;u tới tim c&oacute; thể thường g&acirc;y ra đau ngực ổn định hoặc đau thắt ngực kh&ocirc;ng ổn định.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">B&oacute;c t&aacute;ch động mạch chủ.</span></b><span style=\"font-family: Arial;\"> T&igrave;nh trạng n&agrave;y đe dọa t&iacute;nh mạng li&ecirc;n quan đến động mạch ch&iacute;nh dẫn từ tim - động mạch chủ. Nếu c&aacute;c lớp b&ecirc;n trong của mạch m&aacute;u t&aacute;ch ri&ecirc;ng biệt, khiến m&aacute;u chảy giữa ch&uacute;ng, kết quả l&agrave; bất ngờ r&aacute;ch. B&oacute;c t&aacute;ch động mạch chủ c&oacute; thể do một c&uacute; đ&aacute;nh mạnh v&agrave;o ngực hoặc ph&aacute;t triển như l&agrave; một biến chứng của cao huyết &aacute;p kh&ocirc;ng kiểm so&aacute;t được.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Co thắt mạch v&agrave;nh.</span></b><span style=\"font-family: Arial;\"> Trong co thắt mạch v&agrave;nh, đ&ocirc;i khi được gọi l&agrave; đau thắt ngực Prinzmetal \'s, c&aacute;c động mạch cung cấp m&aacute;u cho cơ tim co thắt, tạm thời ngừng lưu lượng m&aacute;u. N&oacute; xảy ra ở phần c&ograve;n lại v&agrave; c&oacute; thể c&ugrave;ng tồn tại với bệnh động mạch v&agrave;nh - một t&iacute;ch tụ mảng b&aacute;m trong động mạch v&agrave;nh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">M&agrave;ng ngo&agrave;i tim.</span></b><span style=\"font-family: Arial;\"> Một vi&ecirc;m nhiễm của m&agrave;ng xung quanh tim, ngắn ngủi v&agrave; thường li&ecirc;n quan đến một nhiễm virus.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">C&aacute;c vấn đề li&ecirc;n quan đến tim.</span></b><i style=\"\"><span style=\"font-family: Arial;\"> </span></i><span style=\"font-family: Arial;\">Vấn đề về tim kh&aacute;c, chẳng hạn như vi&ecirc;m cơ tim, một t&igrave;nh trạng vi&ecirc;m của tim thường l&agrave; do bị nhiễm virus, c&oacute; thể g&acirc;y ra đau ngực. Một số loại rối loạn cơ tim như bệnh cơ tim ph&igrave; đại, cũng c&oacute; thể g&acirc;y đau ngực.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n do ti&ecirc;u h&oacute;a</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ợ n&oacute;ng.</span></b><span style=\"font-family: Arial;\"> Acid dạ d&agrave;y tr&agrave;o ngược v&agrave;o ống thực quản c&oacute; thể g&acirc;y ra chứng ợ n&oacute;ng, đau r&aacute;t sau xương ức xương ức. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Co thắt thực quản.</span></b><span style=\"font-family: Arial;\"> Rối loạn của thực quản, c&aacute;c ống chạy từ cổ họng đến dạ d&agrave;y, c&oacute; thể l&agrave;m cho nuốt kh&oacute; khăn v&agrave; thậm ch&iacute; đau đớn. Thực quản co thắt, một vấn đề c&oacute; ảnh hưởng đến một nh&oacute;m nhỏ những người c&oacute; đau ngực. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Tho&aacute;t vị ho&agrave;nh.</span></b><span style=\"font-family: Arial;\"> Trong vấn đề n&agrave;y, một phần của dạ d&agrave;y trượt l&ecirc;n ph&iacute;a tr&ecirc;n cơ ho&agrave;nh v&agrave;o ngực. Điều n&agrave;y c&oacute; thể g&acirc;y ra &aacute;p lực ngực hoặc đau, đặc biệt l&agrave; sau khi ăn, cũng như chứng ợ n&oacute;ng.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Co thắt t&acirc;m vị.</span></b><span style=\"font-family: Arial;\"> Trong rối loạn nuốt, van trong thực quản dưới kh&ocirc;ng mở đ&uacute;ng để cho ph&eacute;p đưa thực phẩm v&agrave;o dạ d&agrave;y. Thay v&agrave;o đ&oacute;, thực phẩm tr&agrave;n v&agrave;o thực quản, g&acirc;y đau đớn.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Vấn đề t&uacute;i mật hoặc tụy.</span></b><span style=\"font-family: Arial;\"> Sỏi mật hay vi&ecirc;m t&uacute;i mật hoặc tuyến tụy cấp t&iacute;nh c&oacute; thể g&acirc;y ra đau bụng lan đến ngực. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n cơ xương </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Vi&ecirc;n sụn sườn.</span></b><span style=\"font-family: Arial;\"> Trong vấn đề n&agrave;y, c&ograve;n được gọi l&agrave; hội chứng Tietze - c&aacute;c sụn của khung sườn, đặc biệt l&agrave; sụn sườn gắn v&agrave;o xương ức bị vi&ecirc;m. Kết quả l&agrave; đau ngực, thường trở n&ecirc;n tồi tệ khi ấn v&agrave;o xương ức hoặc tr&ecirc;n c&aacute;c xương sườn gần xương ức.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đau cơ bắp.</span></b><span style=\"font-family: Arial;\"> Hội chứng đau m&atilde;n t&iacute;nh, như bệnh đau cơ xơ, c&oacute; thể đau dai dẳng li&ecirc;n quan đến cơ ngực.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Bị thương d&acirc;y thần kinh hay bị ch&egrave;n &eacute;p xương sườn.</span></b><span style=\"font-family: Arial;\"> Một vết th&acirc;m t&iacute;m hoặc bị g&atilde;y xương sườn, cũng như ch&egrave;n &eacute;p thần kinh c&oacute; thể g&acirc;y ra đau ngực.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">H&ocirc; hấp g&acirc;y ra </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Thuy&ecirc;n tắc phổi.</span></b><span style=\"font-family: Arial;\"> Điều n&agrave;y g&acirc;y ra đau ngực, xảy ra khi một cục m&aacute;u đ&ocirc;ng trong động mạch phổi, ngăn chặn lưu lượng m&aacute;u đến m&ocirc; phổi. T&igrave;nh trạng n&agrave;y đe dọa đến t&iacute;nh mạng xảy ra m&agrave; kh&ocirc;ng c&oacute; trước yếu tố nguy cơ, chẳng hạn như phẫu thuật gần đ&acirc;y hoặc cố định.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Phổi.</span></b><span style=\"font-family: Arial;\"> Đau nh&oacute;i ngực tại địa điểm v&agrave; tăng hơn khi h&iacute;t hay ho xảy ra khi phổi, m&agrave;ng phổi bị vi&ecirc;m. Hiếm hơn l&agrave; do tự miễn dịch, chẳng hạn như lupus. L&agrave; một trong những bệnh tự miễn dịch m&agrave; trong đ&oacute; hệ thống miễn dịch của cơ thể tấn c&ocirc;ng nhầm lẫn m&ocirc; khỏe mạnh.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">C&aacute;c vấn đề kh&aacute;c về phổi.</span></b><span style=\"font-family: Arial;\"> Cao &aacute;p trong động mạch mang m&aacute;u tới phổi v&agrave; bệnh suyễn cũng c&oacute; thể tạo ra đau ngực. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">C&aacute;c nguy&ecirc;n nh&acirc;n kh&aacute;c </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Hồi hộp - hoảng sợ.</span></b><span style=\"font-family: Arial;\"> Nếu trong thời kỳ của sự sợ h&atilde;i m&atilde;nh liệt k&egrave;m theo đau ngực, nhịp tim nhanh, thở nhanh, ra mồ h&ocirc;i v&agrave; kh&oacute; thở.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Bệnh giời leo.</span></b><span style=\"font-family: Arial;\"> L&acirc;y nhiễm của c&aacute;c d&acirc;y thần kinh g&acirc;y ra bởi virus c&oacute; thể g&acirc;y đau v&agrave; mụn nước ph&iacute;a sau lưng, xung quanh th&agrave;nh ngực. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ung thư.</span></b><span style=\"font-family: Arial;\"> Hiếm khi, ung thư li&ecirc;n quan đến việc ngực hoặc ung thư đ&atilde; lan rộng từ một phần kh&aacute;c của cơ thể c&oacute; thể g&acirc;y ra đau ngực.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">C&aacute;c x&eacute;t nghiệm v&agrave; chẩn đo&aacute;n</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Tại ph&ograve;ng cấp cứu, việc đ&aacute;nh gi&aacute; đau ngực c&oacute; thể sẽ c&oacute; huyết &aacute;p, nhịp tim v&agrave; kiểm tra nhiệt độ ngay lập tức. Ngo&agrave;i ra, b&aacute;c sĩ sẽ hỏi một số c&acirc;u hỏi về đau ngực. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau ngực kh&ocirc;ng phải lu&ocirc;n lu&ocirc;n l&agrave; t&iacute;n hiệu một cơn đau tim. Nhưng đ&oacute; l&agrave; những g&igrave; b&aacute;c sĩ ph&ograve;ng cấp cứu sẽ thử nghiệm cho lần đầu ti&ecirc;n, bởi v&igrave; n&oacute; c&oacute; khả năng đe dọa trực tiếp nhất đến cuộc sống. Họ cũng c&oacute; thể kiểm tra sự b&oacute;c t&aacute;ch động mạch chủ hoặc vấn đề về phổi đe dọa t&iacute;nh mạng, chẳng hạn như thuy&ecirc;n tắc phổi hoặc tăng &aacute;p mạch phổi hoặc tr&agrave;n kh&iacute; m&agrave;ng phổi c&oacute; thể g&acirc;y đau ngực. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c x&eacute;t nghiệm c&oacute; thể phải x&aacute;c định nguy&ecirc;n nh&acirc;n của đau ngực bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Điện t&acirc;m đồ (ECG).</span></b><span style=\"font-family: Arial;\"> Thử nghiệm n&agrave;y c&oacute; thể gi&uacute;p b&aacute;c sĩ chẩn đo&aacute;n bệnh tim cũng như c&aacute;c vấn đề tim mạch kh&aacute;c. N&oacute; ghi lại hoạt động điện của tim th&ocirc;ng qua c&aacute;c điện cực gắn liền với l&agrave;n da. Nhịp tim v&agrave; nhịp điệu v&agrave; c&aacute;c xung điện đi qua tim được ghi nhận l&agrave; s&oacute;ng hiển thị tr&ecirc;n một m&agrave;n h&igrave;nh hoặc in tr&ecirc;n giấy. V&igrave; bị thương, cơ tim kh&ocirc;ng thực hiện xung điện b&igrave;nh thường, c&aacute;c ECG c&oacute; thể cho thấy một cơn đau tim đ&atilde; xảy ra hoặc đang xẩy ra.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">X&eacute;t nghiệm m&aacute;u.</span></b><span style=\"font-family: Arial;\"> B&aacute;c sĩ c&oacute; thể y&ecirc;u cầu x&eacute;t nghiệm m&aacute;u để kiểm tra mức độ gia tăng của một số enzym thường được t&igrave;m thấy trong cơ tim. Thiệt hại cho c&aacute;c tế b&agrave;o tim từ một cơn đau tim c&oacute; thể cho ph&eacute;p c&aacute;c enzym r&ograve; rỉ v&agrave;o m&aacute;u, trong khoảng thời gian v&agrave;i giờ.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">X -quang ngực.</span></b><span style=\"font-family: Arial;\"> X -quang ngực cho ph&eacute;p c&aacute;c b&aacute;c sĩ để kiểm tra t&igrave;nh trạng của phổi, k&iacute;ch thước v&agrave; h&igrave;nh dạng của tim v&agrave; c&aacute;c mạch m&aacute;u lớn. C&aacute;c b&aacute;c sĩ cũng c&oacute; thể sử dụng một X -quang ngực để kiểm tra khối u ở ngực v&agrave; để t&igrave;m c&aacute;c vấn đề về phổi c&oacute; thể g&acirc;y đau ngực, như vi&ecirc;m phổi hoặc tr&agrave;n kh&iacute;.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Nghiệm ph&aacute;p gắng sức.</span></b><span style=\"font-family: Arial;\"> Những biện ph&aacute;p kiểm tra tim v&agrave; mạch m&aacute;u đ&aacute;p ứng với gắng sức, c&oacute; thể bởi cơn đau c&oacute; li&ecirc;n quan đến tim. C&oacute; nhiều loại x&eacute;t nghiệm gắng sức. C&oacute; thể được y&ecirc;u cầu đi bộ tr&ecirc;n m&aacute;y chạy bộ hay đạp xe đạp trong khi nối với một ECG. Hoặc c&oacute; thể được cho một loại thuốc ti&ecirc;m tĩnh mạch để k&iacute;ch th&iacute;ch tim trong một c&aacute;ch tương tự như tập thể dục. Gắng sức c&oacute; thể được kết hợp với h&igrave;nh ảnh của tim bằng c&aacute;ch sử dụng si&ecirc;u &acirc;m hoặc vật liệu ph&oacute;ng xạ (hạt nh&acirc;n qu&eacute;t).</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Si&ecirc;u &acirc;m tim.</span></b><span style=\"font-family: Arial;\"> Si&ecirc;u &acirc;m tim sử dụng s&oacute;ng &acirc;m để tạo ra một h&igrave;nh ảnh video của tim. H&igrave;nh ảnh n&agrave;y c&oacute; thể gi&uacute;p b&aacute;c sĩ x&aacute;c định c&aacute;c vấn đề về tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đặt ống th&ocirc;ng mạch v&agrave;nh (angiogram).</span></b><span style=\"font-family: Arial;\"> Thử nghiệm n&agrave;y gi&uacute;p b&aacute;c sĩ x&aacute;c định c&aacute;c động mạch cấp m&aacute;u cho cơ tim c&oacute; thể thu hẹp hoặc bị chặn. Một chất nhuộm lỏng được ti&ecirc;m v&agrave;o c&aacute;c động mạch của tim th&ocirc;ng qua một ống th&ocirc;ng, thường l&agrave; ở h&aacute;ng đến c&aacute;c động mạch trong tim. Khi nhuộm đầy c&aacute;c động mạch, n&oacute; trở th&agrave;nh nh&igrave;n th���y tr&ecirc;n X-quang v&agrave; video.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Chụp cắt lớp vi t&iacute;nh (bao gồm cả angiogram v&agrave;nh CT).</span></b><span style=\"font-family: Arial;\"> C&aacute;c loại kh&aacute;c nhau của qu&eacute;t CT c&oacute; thể được d&ugrave;ng để kiểm tra động mạch tim cho c&aacute;c dấu hiệu của canxi, trong đ&oacute; chỉ ra rằng mảng xơ vữa động mạch c&oacute; thể được t&iacute;ch lũy v&agrave; chặn c&aacute;c động mạch cung cấp m&aacute;u cho tim. Qu&eacute;t CT cũng c&oacute; thể được thực hiện với thuốc nhuộm để nh&igrave;n thẳng v&agrave;o động mạch tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Chụp cộng hưởng từ (MRI).</span></b><span style=\"font-family: Arial;\"> MRI l&agrave; một kỹ thuật h&igrave;nh ảnh c&oacute; sử dụng từ trường v&agrave; s&oacute;ng radio để tạo ra h&igrave;nh ảnh mặt cắt ngang của cơ thể. MRI của tim đ&ocirc;i khi được thực hiện để t&igrave;m bằng chứng về thiệt hại tim hoặc vi&ecirc;m vi&ecirc;m cơ tim. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Nội soi.</span></b><span style=\"font-family: Arial;\"> Trong kiểm tra, dụng cụ gắn liền với một m&aacute;y ảnh được truyền xuống cổ họng, cho ph&eacute;p b&aacute;c sĩ xem thực quản v&agrave; dạ d&agrave;y v&agrave; kiểm tra c&aacute;c vấn đề về dạ d&agrave;y c&oacute; thể g&acirc;y đau ngực. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nhiều loại đau ngực c&oacute; thể l&uacute;c đầu ti&ecirc;n dường như li&ecirc;n quan đến vấn đề về tim. Nhưng thường sau khi đ&aacute;nh gi&aacute; cẩn thận c&aacute;c b&aacute;c sĩ c&oacute; thể ph&acirc;n biệt c&aacute;c triệu chứng đau ngực do bệnh tim hoặc kh&ocirc;ng. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị v&agrave; thuốc</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Nguy&ecirc;n nh&acirc;n tim</span><span style=\"font-family: Arial;\"> </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu xuất hiện vấn đề về tim l&agrave; nguy&ecirc;n nh&acirc;n của đau ngực, c&oacute; thể cung cấp thuốc như: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Aspirin.</span></b><span style=\"font-family: Arial;\"> Aspirin ức chế đ&ocirc;ng m&aacute;u, gi&uacute;p duy tr&igrave; lưu lượng m&aacute;u qua động mạch tim bị hẹp, aspirin c&oacute; thể l&agrave;m giảm đ&aacute;ng kể tỷ lệ tử vong. C&oacute; thể được y&ecirc;u cầu nhai aspirin để đẩy nhanh sự hấp thụ của n&oacute;. Aspirin được khuyến kh&iacute;ch cho hầu hết những người đ&atilde; c&oacute; một cơn đau tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Nitroglycerin.</span></b><span style=\"font-family: Arial;\"> Thuốc n&agrave;y để điều trị đau thắt ngực, mở rộng tạm thời sự thu hẹp c&aacute;c mạch m&aacute;u, cải thiện lưu lượng m&aacute;u đến v&agrave; đi từ tim.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Beta blockers.</span></b><span style=\"font-family: Arial;\"> C&aacute;c thuốc n&agrave;y gi&uacute;p thư gi&atilde;n cơ tim, l&agrave;m chậm nhịp tim v&agrave; giảm huyết &aacute;p, l&agrave;m giảm nhu cầu nhu cầu &ocirc; xy cơ tim. C&aacute;c loại thuốc n&agrave;y gi&uacute;p hạn chế số lượng thiệt hại trong một cuộc tấn c&ocirc;ng tim v&agrave; ngăn ngừa đau tim t&aacute;i ph&aacute;t.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Huyết khối.</span></b><span style=\"font-family: Arial;\"> C&aacute;c thuốc n&agrave;y, c&ograve;n gọi l&agrave; clotbusters, gi&uacute;p h&ograve;a tan một cục m&aacute;u đ&ocirc;ng ngăn chặn lưu lượng m&aacute;u tới tim. C&aacute;c thuốc n&agrave;y c&oacute; hiệu quả nhất trong v&ograve;ng một giờ sau khi c&aacute;c triệu chứng của một cơn đau tim bắt đầu. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ranolazine (Ranexa).</span></b><span style=\"font-family: Arial;\"> Đ&acirc;y l&agrave; một loại thuốc tương đối mới trong điều trị đau thắt ngực m&atilde;n t&iacute;nh. N&oacute; chỉ được sử dụng khi c&aacute;c loại thuốc kh&aacute;c chống đau thắt đ&atilde; kh&ocirc;ng hiệu quả bởi v&igrave; n&oacute; c&oacute; thể g&acirc;y ra một vấn đề được gọi l&agrave; QT k&eacute;o d&agrave;i, c&oacute; thể l&agrave;m tăng nguy cơ của c&aacute;c vấn đề nhịp tim. N&oacute; n&ecirc;n được sử dụng với c&aacute;c thuốc đau thắt ngực kh&aacute;c, chẳng hạn như thuốc chẹn k&ecirc;nh calci, chẹn beta hoặc nitroglycerin.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Angiotensin - men chuyển (ACE) v&agrave; ức chế thụ thể angiotensin (ARBS).</span></b><span style=\"font-family: Arial;\"> C&aacute;c thuốc n&agrave;y cho ph&eacute;p m&aacute;u chảy từ tim dễ d&agrave;ng hơn. B&aacute;c sĩ c&oacute; thể k&ecirc; toa thuốc ức chế ACE hoặc ARBS nếu đ&atilde; c&oacute; một cơn đau tim trung b&igrave;nh đến nặng đ&atilde; giảm c&ocirc;ng suất bơm tim. C&aacute;c thuốc n&agrave;y cũng l&agrave;m thấp huyết &aacute;p v&agrave; c&oacute; thể ngăn chặn một cơn đau tim lần thứ hai.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Chẹn k&ecirc;nh canxi.</span></b><span style=\"font-family: Arial;\"> Khi điều trị co thắt động mạch v&agrave;nh, c&aacute;c b&aacute;c sĩ đ&ocirc;i khi sử dụng thuốc tim mạch như thuốc chẹn k&ecirc;nh canxi để thư gi&atilde;n c&aacute;c động mạch v&agrave;nh v&agrave; ngăn ngừa co thắt.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Phương ph&aacute;p điều trị đau tim </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu l&agrave; r&otilde; r&agrave;ng đang c&oacute; một cơn đau tim, c&oacute; thể được điều trị bằng thuốc clotbusting hoặc trải qua một thủ tục phẫu thuật như: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Nong mạch v&agrave; stenting.</span></b><span style=\"font-family: Arial;\"> Trong khi nong mạch v&agrave;nh, c&ograve;n được gọi l&agrave; can thiệp mạch v&agrave;nh qua da (PCI), b&aacute;c sĩ ch&egrave;n một ống th&ocirc;ng với một quả b&oacute;ng đặc biệt v&agrave;o một động mạch v&agrave;nh bị tắc. B&oacute;ng được bơm căng để mở ra c&aacute;c động mạch v&agrave; phục hồi lưu lượng m&aacute;u tới tim. Sau đ&oacute;, một cuộn d&acirc;y nhỏ lưới (stent) thường được ch&egrave;n v&agrave;o để giữ cho động mạch mở. Nhiều người sẽ đi thẳng từ ph&ograve;ng khẩn cấp tới c&aacute;c ph&ograve;ng đặt ống th&ocirc;ng để nong mạch được thực hiện c&agrave;ng nhanh c&agrave;ng tốt. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Phẫu thuật cầu nối.</span></b><span style=\"font-family: Arial;\"> Thủ tục n&agrave;y tạo ra một lộ tr&igrave;nh thay thế, để m&aacute;u đi quanh qua động mạch v&agrave;nh bị chặn. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Điều trị đau thắt ngực </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c b&aacute;c sĩ điều trị đau thắt ngực thường đầu ti&ecirc;n với thuốc. C&oacute; thể sẽ bắt đầu tiếp nhận thuốc tại ph&ograve;ng cấp cứu, bao gồm aspirin, nitroglycerin, thuốc chẹn beta v&agrave; chất l&agrave;m lo&atilde;ng m&aacute;u. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&oacute; đau thắt ngực kh&ocirc;ng ổn định, ngay lập tức c&oacute; thể cần đặt ống th&ocirc;ng mạch v&agrave;nh để sau đ&oacute; nong mạch v&agrave; stenting. Trong một số trường hợp, c&oacute; thể cần phải phẫu thuật mạch v&agrave;nh. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Điều trị c&aacute;c vấn đề kh&aacute;c</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">C&aacute;c vấn đề tim v&agrave; phổi c&oacute; thể điều trị ban đầu tại ph&ograve;ng cấp cứu. Nếu l&agrave; r&otilde; r&agrave;ng đang gặp một nghẽn mạch phổi, sẽ c&oacute; thể được điều trị khẩn cấp bằng thuốc l&agrave;m lo&atilde;ng m&aacute;u, đ&ocirc;i khi bao gồm thuốc ti&ecirc;u huyết khối. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">B&oacute;c t&aacute;ch động mạch chủ thường đ&ograve;i hỏi phải phẫu thuật khẩn cấp. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Điều trị nguy&ecirc;n nh&acirc;n kh&ocirc;ng do tim kh&aacute;c</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu c&aacute;c b&aacute;c sĩ ph&ograve;ng cấp cứu x&aacute;c định đang trong nguy hiểm, c&oacute; thể gọi b&aacute;c sĩ ri&ecirc;ng hoặc một chuy&ecirc;n gia để đ&aacute;nh gi&aacute; th&ecirc;m. Điều trị cho nguy&ecirc;n nh&acirc;n của đau ngực kh&ocirc;ng do tim phụ thuộc v&agrave;o loại vấn đề. Những vấn đề n&agrave;y v&agrave; điều trị bao gồm: </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Ợ n&oacute;ng.</span></b><span style=\"font-family: Arial;\"> Nếu c&aacute;c triệu chứng gợi &yacute; ợ n&oacute;ng, c&oacute; thể sẽ phải d&ugrave;ng một ttoa hoặc thuốc kh&aacute;ng acid trong ph&ograve;ng cấp cứu. Hầu hết c&aacute;c vấn đề của ợ n&oacute;ng l&agrave; sự kiện c&ocirc; lập do ăn qu&aacute; nhiều hoặc do ăn thức ăn b&eacute;o. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Nếu vấn đề ợ n&oacute;ng thường xuy&ecirc;n (&iacute;t nhất một lần một tuần), b&aacute;c sĩ hoặc b&aacute;c sĩ chuy&ecirc;n về dạ d&agrave;y v&agrave; c&aacute;c vấn đề đường ruột (gastroenterologist) c&oacute; thể y&ecirc;u cầu phải trải qua thử nghiệm th&ecirc;m. C&ograve;n kh&ocirc;ng được điều trị, m&atilde;n t&iacute;nh, thường xuy&ecirc;n ợ n&oacute;ng đ&ocirc;i khi c&oacute; thể dẫn đến sẹo v&agrave; thu hẹp thực quản. Điều trị ợ n&oacute;ng mạn t&iacute;nh c&oacute; thể bao gồm sửa đổi chế độ ăn uống, thuốc kh&aacute;ng acid, ngăn chặn acid hoặc thuốc theo toa kh&aacute;c, trong một số trường hợp cần phẫu thuật. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Hồi hộp &ndash; hoảng sợ.</span></b><span style=\"font-family: Arial;\"> Điều n&agrave;y g&acirc;y lo lắng li&ecirc;n quan đến đau ngực c&oacute; thể được điều trị bằng thuốc chống lo lắng, kỹ thuật thư gi&atilde;n v&agrave; tư vấn để t&igrave;m ra những g&igrave; c&oacute; thể được k&iacute;ch hoạt c&aacute;c cuộc tấn. Hồi hộp thường nhầm lẫn với c&aacute;c cơn đau tim, v&agrave; nhiều người được thấy trong c&aacute;c ph&ograve;ng khẩn cấp cho vấn đề n&agrave;y. Nhưng một khi t&igrave;nh trạng được chẩn đo&aacute;n, c&oacute; thể được điều trị để gi&uacute;p c&oacute; được kiểm so&aacute;t. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Phổi.</span></b><span style=\"font-family: Arial;\"> Vi&ecirc;m m&agrave;ng ph&ocirc;̉i, phổi, c&oacute; thể do nhiều nguy&ecirc;n nh&acirc;n, v&agrave; hiếm hơn l&agrave; điều kiện tự miễn như lupus. B&aacute;c sĩ sẽ muốn x&aacute;c định v&agrave; điều trị c&aacute;c bệnh tiềm ẩn g&acirc;y ra. thuốc giảm đau c&oacute; thể gi&uacute;p giảm thiểu sự đau đớn cho đến khi giảm vi&ecirc;m.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Vi&ecirc;m sụn sườn.</span></b><span style=\"font-family: Arial;\"> Điều trị cho vi&ecirc;m sụn của lồng xương sườn thường l&agrave; thuốc chống vi&ecirc;m kh&ocirc;ng steroid, như ibuprofen. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Đau cơ bắp, xương sườn bị thương hoặc d&acirc;y thần kinh bị ch&egrave;n &eacute;p.</span></b><span style=\"font-family: Arial;\"> Đau ngực từ xương sườn bị thương, bị ch&egrave;n &eacute;p d&acirc;y thần kinh v&agrave; cơ bắp cải thiện theo thời gian v&agrave; c&aacute;c biện ph&aacute;p tự chăm s&oacute;c được đề nghị.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Rối loạn nuốt.</span></b><span style=\"font-family: Arial;\"> C&aacute;c rối loạn c&oacute; nhiều nguy&ecirc;n nh&acirc;n, trong đ&oacute; thường c&oacute; thể được điều trị bằng thuốc, phẫu thuật nhỏ hoặc c&aacute;c kỹ thuật nội soi. Sẽ được giới thiệu đến một b&aacute;c sỹ chuy&ecirc;n khoa để đ&aacute;nh gi&aacute; v&agrave; điều trị.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">Bệnh giời leo.</span></b><span style=\"font-family: Arial;\"> Điều trị bằng acyclovir hay một thuốc tương tự như kh&aacute;ng virus l&agrave; tốt nhất bắt đầu c&agrave;ng nhanh c&agrave;ng tốt, tốt hơn trong v&ograve;ng 24 giờ, kể từ sự khởi đầu của đau hay r&aacute;t, v&agrave; trước sự xuất hiện của mụn nước. C&aacute;c b&aacute;c sĩ sử dụng phương ph&aacute;p điều trị kh&aacute;c, chẳng hạn như thuốc giảm đau v&agrave; thuốc kh&aacute;ng histamine, để kiểm so&aacute;t c&aacute;c triệu chứng như đau v&agrave; ngứa.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><b style=\"\"><span style=\"font-family: Arial;\">T&uacute;i mật hoặc tụy.</span></b><span style=\"font-family: Arial;\"> C&oacute; thể cần phẫu thuật để điều trị vi&ecirc;m t&uacute;i mật hoặc tụy g&acirc;y ra đau, tỏa ra từ bụng v&agrave;o ngực.</span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\"><span style=\"font-family: Arial;\">Đau ngực c&oacute; thể l&agrave; một trong những triệu chứng kh&oacute; khăn nhất để giải th&iacute;ch. Nhưng d&agrave;nh thời gian trong ph&ograve;ng cấp cứu khi đau ngực được đ&aacute;nh gi&aacute; c&oacute; thể mang lại cho sự an t&acirc;m, v&agrave; thậm ch&iacute; c&oacute; thể cứu sống. </span></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: right;\"><b style=\"\"><i style=\"\"><span style=\"font-family: Arial;\"><span style=\"color: rgb(128, 0, 0);\"></span></span></i></b></p>\r\n<p style=\"margin: 12pt 0in 6pt; line-height: 150%; text-align: justify;\" class=\"MsoNormal\"><span style=\"font-family: Arial;\">&nbsp;</span></p></span>\n', 2);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(30, 'Định hướng điều trị bệnh tim bẩm sinh bằng siêu âm doppler', '\n   <h1 class=\"title\">Định hướng điều trị bệnh tim bẩm sinh bằng siêu âm doppler</h1>   <span class=\"des\">85% các ca là TLT phần màng, 15% phầu phễu, các phần khác hiếm gặp hơn: Phần tiếp nhận, phần cơ bè (với khả năng có nhiều lỗ nhỏ).</span>\n   <span><p></p>\r\n<p class=\"MsoNormal\" style=\"mso-margin-top-alt:auto;mso-margin-bottom-alt:auto;\r\ntext-align:justify\"><span lang=\"SV\" style=\"font-size:18.0pt;font-family:Arial;\r\nmso-ansi-language:SV\">Bệnh tim kh&ocirc;ng t&iacute;m</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-size:14.0pt;line-height:\r\n150%;font-family:Arial\">Th&ocirc;ng li&ecirc;n thất</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">20% c&aacute;c loại tim bẩm sinh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">85% c&aacute;c ca l&agrave; th&ocirc;ng li&ecirc;n thất phần m&agrave;ng, 15% phầu phễu, c&aacute;c phần kh&aacute;c hiếm gặp hơn: Phần tiếp nhận, phần cơ b&egrave; (với khả năng c&oacute; nhiều lỗ nhỏ).</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Lỗ th&ocirc;ng v&agrave;i mm, hiệu số &aacute;p lực thất tr&aacute;i - thất phải <span style=\"mso-spacerun:yes\">&nbsp;</span>kh&ocirc;ng thay đổi. Nếu lỗ th&ocirc;ng lớn hơn <span style=\"letter-spacing:-.2pt\">nhưng &lt; 10mm </span></span><span style=\"font-family:Symbol;letter-spacing:-.2pt\">&THORN;</span><span style=\"font-family:\r\nArial;letter-spacing:-.2pt\"> &aacute;p lực thất phải <span style=\"mso-spacerun:yes\">&nbsp;</span>tăng. Nếu lỗ th&ocirc;ng &gt; 10mm g&acirc;y tăng &aacute;p thất phải <span style=\"mso-spacerun:yes\">&nbsp;</span>v&agrave; động mạch phổi đưa đến c&acirc;n bằng &aacute;p lực thất tr&aacute;i - thất phải <span style=\"mso-spacerun:yes\">&nbsp;</span>.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Th&ocirc;ng li&ecirc;n thất c&oacute; </span><span style=\"font-family:Symbol\">f</span><span style=\"font-family:\r\nArial\"> &gt; </span><span style=\"font-family:Symbol\">f</span><span style=\"font-family:Arial\"> v&ograve;ng van Ao: Luồng th&ocirc;ng T- P giảm theo thời gian rồi đảo shunt do tăng kh&aacute;ng lực động mạch phổi v&agrave; hẹp động mạch phổi thứ ph&aacute;t thường tại phễu (giảm rồi đảo g&acirc;y ra hội chứng Eisenmenger rồi cuối c&ugrave;ng giống T4F tr&ecirc;n bệnh cảnh l&acirc;m s&agrave;ng).</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Trước 2 tuổi biến chuyển thay đổi kh&aacute;ng lực mạch phổi v&agrave; hẹp phễu thường xảy ra.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Th&ocirc;ng li&ecirc;n thất sau 30-40 tuổi rất &iacute;t gặp v&igrave; đa số th&ocirc;ng li&ecirc;n thất lỗ nhỏ đều đ&oacute;ng b&iacute;t theo thời gian.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Si&ecirc;u &acirc;m: lỗ th&ocirc;ng &gt; 3mm, tr&ecirc;n 2D c&oacute; thể thấy trường hợp kh&aacute;c c&oacute; thể d&ugrave;ng Doppler v&agrave; Doppler mầu để ph&aacute;t hiện.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span style=\"font-family:Arial\">Điều trị:</span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Tuổi th&iacute;ch hợp phẫu thuật l&agrave; 2 tuổi, trường hợp nhũ nhi phẫu thuật khi c&oacute; tăng &aacute;p động mạch phổi (</span><span style=\"font-family:Symbol\">&sup3;</span><span style=\"font-family:Arial\"> 70mmHg, phẫu thuật khoảng 9-12 th&aacute;ng) hoặc c&oacute; rối loạn h&ocirc; hấp (ĐMP gi&atilde;n v&agrave; tăng &aacute;p).</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Th&ocirc;ng li&ecirc;n thất c&oacute; thể tự đ&oacute;ng, c&oacute; thể biến chứng Eisenmenger.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Th&ocirc;ng li&ecirc;n thất lỗ nhỏ: kh&ocirc;ng tăng &aacute;p động mạch phổi </span><span style=\"font-family:Symbol\">&THORN;</span><span style=\"font-family:Arial\"> kh&ocirc;ng cần phẫu thuật, phẫu thuật khi c&oacute; triệu chứng .</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Th&ocirc;ng li&ecirc;n thất lỗ nhỏ c&oacute; &aacute;p lực động mạch phổi </span><span style=\"font-family:Symbol\">&sup3;</span><span style=\"font-family:Arial\"> 70mmHg k&egrave;m suy tim kh&ocirc;ng đỡ d&ugrave; điều trị nội khoa </span><span style=\"font-family:Symbol\">&THORN;</span><span style=\"font-family:Arial\"> cần phẫu thuật. Nếu điều trị nội suy tim đỡ th&igrave; kiểm tra l&uacute;c 6 th&aacute;ng tuổi, nếu &aacute;p lực động mạch phổi </span><span style=\"font-family:Symbol\">&sup3;</span><span style=\"font-family:Arial\"> 70 mmHg </span><span style=\"font-family:Symbol\">&THORN;</span><span style=\"font-family:Arial\"> phẫu thuật , nếu &lt; 70 mmHg k&egrave;m th&ocirc;ng li&ecirc;n thất nhỏ lại </span><span style=\"font-family:Symbol\">&THORN;</span><span style=\"font-family:\r\nArial\"> hẹn 6 th&aacute;ng sau kiểm tra lại.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Th&ocirc;ng li&ecirc;n thất với &aacute;p lực động mạch phổi &lt; 70 mmHg k&egrave;m kh&ocirc;ng suy tim hoặc suy tim điều trị nội ổn định </span><span style=\"font-family:Symbol\">&THORN;</span><span style=\"font-family:Arial\"> kiểm tra si&ecirc;u &acirc;m 6 th&aacute;ng/lần.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Th&ocirc;ng li&ecirc;n thất ở độ tuổi 4-5 với &aacute;p lực động mạch phổi </span><span style=\"font-family:\r\nSymbol\">&pound;</span><span style=\"font-family:Arial\"> 55mmHg k&egrave;m QP/QS </span><span style=\"font-family:Symbol\">&raquo;</span><span style=\"font-family:Arial\"> 1,5-2 </span><span style=\"font-family:Symbol\">&THORN;</span><span style=\"font-family:Arial\"> phẫu thuật nếu </span><span style=\"font-family:Symbol\">&pound;</span><span style=\"font-family:\r\nArial\"> 1,5 </span><span style=\"font-family:Symbol\">&THORN;</span><span style=\"font-family:Arial\"> si&ecirc;u &acirc;m 6 th&aacute;ng/lần.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Người lớn c&oacute; th&ocirc;ng li&ecirc;n thất lỗ nhỏ k&egrave;m &aacute;p lực động mạch phổi b&igrave;nh thường với QP/QS &lt; 1,3 </span><span style=\"font-family:Symbol\">&THORN;</span><span style=\"font-family:\r\nArial\"> điều trị nội, nếu QP/QS </span><span style=\"font-family:Symbol\">&raquo;</span><span style=\"font-family:Arial\"> 1,3-1,5 </span><span style=\"font-family:Symbol\">&THORN;</span><span style=\"font-family:Arial\"> xem x&eacute;t phẫu thuật, nếu QP/QS &gt; 1,5 v&agrave; &aacute;p lực động mạch phổi </span><span style=\"font-family:Symbol\">&pound;</span><span style=\"font-family:Arial\"> 55mmHg </span><span style=\"font-family:Symbol\">&THORN;</span><span style=\"font-family:Arial\"> cần phẫu thuật.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Người lớn với th&ocirc;ng li&ecirc;n thất lỗ lớn với &aacute;p lực động mạch phổi </span><span style=\"font-family:Symbol\">&sup3;</span><span style=\"font-family:Arial\"> 70mmHg nhưng QP/QS thấp: kh&ocirc;ng n&ecirc;n phẫu thuật.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-size:14.0pt;line-height:\r\n150%;font-family:Arial\">Th&ocirc;ng li&ecirc;n nhĩ</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Chiếm khoảng 15% tim bẩm sinh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Th&ocirc;ng li&ecirc;n nhĩ lỗ thứ nhất: gần v&ograve;ng nối nhĩ thất, k&iacute;ch thước c&oacute; thể lớn tới như nhĩ chung. Tổn thương thường k&egrave;m l&agrave; th&ocirc;ng li&ecirc;n thất v&ugrave;ng nhận hoặc Nhĩ thất chung.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Th&ocirc;ng li&ecirc;n nhĩ lỗ thứ hai: gần lỗ bầu dục (cần ph&acirc;n biệt v&igrave; c&oacute; thể gặp lỗ bầu dục th&ocirc;ng thường ở 10-35% người b&igrave;nh thường). K&iacute;ch thước lỗ th&ocirc;ng li&ecirc;n nhĩ từ 1-3 cm, bất thường k&egrave;m theo thường l&agrave; ph&igrave;nh v&aacute;ch nhĩ.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Th&ocirc;ng li&ecirc;n nhĩ v&ugrave;ng xoang TM: khuyết ở chỗ đổ v&agrave;o nhĩ phải của TMC. V&ugrave;ng tĩnh mạch chủtr&ecirc;n nhiều hơn, v&ugrave;ng tĩnh mạch chủdưới rất hiếm. K&egrave;m theo bất thường kh&ocirc;ng ho&agrave;n to&agrave;n TMP c&oacute; thể gặp.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Th&ocirc;ng li&ecirc;n nhĩ v&ugrave;ng xoang v&agrave;nh: khuyết v&ugrave;ng l&oacute;c xoang v&agrave;nh g&acirc;y một th&ocirc;ng li&ecirc;n nhĩ giữa nhĩ tr&aacute;i v&agrave; xoang v&agrave;nh, luồng th&ocirc;ng sẽ quan trọng hơn nếu c&oacute; tĩnh mạch chủtr&ecirc;n tr&aacute;i đổ v&agrave;o xoang v&agrave;nh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial;letter-spacing:\r\n-.3pt\">Si&ecirc;u &acirc;m 2D, Doppler sẽ cho biết vị tr&iacute;, k&iacute;ch thước, ph&acirc;n loại, chiều luồng th&ocirc;ng.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Doppler xung cho h&igrave;nh ảnh một d&ograve;ng xoay li&ecirc;n tục c&oacute; &iacute;t nhất 3 đỉnh từ giữa t&acirc;m thu đến cuối t&acirc;m trương.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">K&iacute;ch thước thất phải, nhĩ phải, chuyển động nghịch thường v&aacute;ch li&ecirc;n thất n&oacute;i l&ecirc;n buồng tim phải tăng tải thể t&iacute;ch.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Đo k&iacute;ch thước động mạch phổi, &aacute;p lực động mạch phổi t&acirc;m thu, t&acirc;m trương v&agrave; trung b&igrave;nh. Ch&uacute; &yacute; khảo s&aacute;t vị tr&iacute; tĩnh mạch phổi.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span style=\"font-family:Arial\">Điều trị nội khoa:</span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Đ&oacute;ng lỗ th&ocirc;ng bằng d&ugrave;.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span style=\"font-family:Arial\">Điều trị ngoại khoa: </span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Phẫu thuật cho tất cả th&ocirc;ng li&ecirc;n nhĩ c&oacute; QP/QS </span><span style=\"font-family:Symbol\">&sup3;</span><span style=\"font-family:Arial\"> 1,5 d&ugrave; c&oacute; hay kh&ocirc;ng triệu chứng cơ năng.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">N&ecirc;n phẫu thuật ở độ tuổi 3-5, th&ocirc;ng li&ecirc;n nhĩ c&oacute; thể tự đ&oacute;ng 14-66% do đ&oacute; kh&ocirc;ng n&ecirc;n phẫu thuật ở độ tuổi &lt; 1 ngoại trừ suy tim hay tăng &aacute;p động mạch phổi kh&ocirc;ng kiểm so&aacute;t được.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Th&ocirc;ng li&ecirc;n nhĩ ph&aacute;t hiện ở tuổi gi&agrave; vẫn c&oacute; thể mổ được.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Th&ocirc;ng li&ecirc;n nhĩ thường biến chứng tăng &aacute;p động mạch phổi ở tuổi 30-40, c&ograve;n phẫu thuật được khi sức cản mạch phổi &lt; 14 đơn vị/m<sup>2</sup> v&agrave; độ b&atilde;o ho&agrave; O<sub>2</sub> m&aacute;u động mạch </span><span style=\"font-family:Symbol\">&sup3;</span><span style=\"font-family:Arial\"> 92%, nếu th&ocirc;ng li&ecirc;n nhĩ k&egrave;m hở 2 l&aacute; nặng c&oacute; thể phẫu thuật được d&ugrave; cản sức mạch phổi &gt; 14 đơn vị/m<sub>2</sub> (n&ecirc;n sửa van).</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"PT-BR\" style=\"font-family:Arial;\r\nletter-spacing:-.2pt;mso-ansi-language:PT-BR\">Kh&ocirc;ng phẫu thuật khi &aacute;p lực động mạch phổi </span><span style=\"font-family:Symbol;letter-spacing:-.2pt\">&raquo;</span><span style=\"font-family:Arial;letter-spacing:-.2pt;mso-ansi-language:PT-BR\"> <span lang=\"PT-BR\">&aacute;p lực động mạch chủ, độ b&atilde;o h&ograve;a O<sub>2</sub> l&uacute;c nghỉ &lt; </span></span><span lang=\"PT-BR\" style=\"font-family:Arial;mso-ansi-language:PT-BR\">92%.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Th&ocirc;ng li&ecirc;n nhĩ</span><span lang=\"PT-BR\" style=\"font-family:Arial;mso-ansi-language:PT-BR\"> ở người 50-60 tuổi c&oacute; &aacute;p lực động mạch phổi ~50mmHg vẫn cho kết quả tốt.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-size:14.0pt;line-height:\r\n150%;font-family:Arial\">C&ograve;n ống động mạch</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Chiếm 10% tim bẩm sinh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">15 giờ sau sinh của b&eacute; đủ th&aacute;ng, lớp trung m&ocirc; co thắt dẫn đến tắc nghẽn ống động mạch cơ năng.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Cuối tuần 8, ống động mạch đ&oacute;ng 90% c&aacute;c trường hợp với sự vỡ xuất huyết của lớp dưới nội m&ocirc; v&agrave; h&igrave;nh th&agrave;nh lớp xơ b&iacute;t l&ograve;ng ống động mạch, đ&oacute;ng bắt đầu từ ph&iacute;a đầu động mạch phổi. Đ&ocirc;i khi ta thấy gi&atilde;n giả ph&igrave;nh ống động mạch ở giai đoạn sơ sinh, n&oacute; sẽ giảm tự nhi&ecirc;n.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\"><span style=\"mso-spacerun:yes\">&nbsp;</span>Ống động mạch d&agrave;i 2-15mm, rộng 5-15mm</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\"><span style=\"mso-spacerun:yes\">&nbsp;</span>Ống động mạch </span><span style=\"font-family:\r\nSymbol\">&pound;</span><span style=\"font-family:Arial\"> 7mm: &aacute;p lực động mạch phổi tăng nhẹ, khi c&oacute; tăng &aacute;p lực động mạch phổi th&igrave; thường k&iacute;ch thước lỗ th&ocirc;ng &gt; 7mm.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Khi c&oacute; luồng th&ocirc;ng 2 chiều hoặc th&ocirc;ng phải - tr&aacute;i th&igrave; chống chỉ định phẫu thuật.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span style=\"font-family:Arial\">Điều trị nội khoa: </span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">D&ugrave;ng Indometacin.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span style=\"font-family:Arial\">Điều trị ngoại khoa: </span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Cần phẫu thuật trước khi c&oacute; biến chứng tăng &aacute;p động mạch phổi cơ học l&agrave;m thay đổi chiều shunt (Eisenmenger).</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Trẻ sơ sinh biến chứng suy tim cần điều trị nội t&iacute;ch cực, nếu kh&ocirc;ng kiểm so&aacute;t được th&igrave; b&iacute;t ống th&ocirc;ng bằng th&ocirc;ng tim hoặc phẫu thuật.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Điều trị nội kiểm so&aacute;t được suy tim, &aacute;p lực động mạch phổi </span><span style=\"font-family:Symbol\">&sup3;</span><span style=\"font-family:Arial\"> 70mmHg cần si&ecirc;u &acirc;m lại v&agrave;o l&uacute;c 5 th&aacute;ng tuổi. Nếu &aacute;p lực động mạch phổi vẫn &gt; 70mmHg, cần phẫu thuật trước th&aacute;ng thứ 6 để tr&aacute;nh đảo shunt. Nếu &aacute;p lực động mạch phổi giảm th&igrave; kiểm tra lại v&agrave;o th&aacute;ng thứ 12, c&ograve;n ống động mạch th&igrave; phẫu thuật khi 1-2 tuổi.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Suy tim hoặc trẻ kh&ocirc;ng lớn d&ugrave; đ&atilde; điều trị nội t&iacute;ch cực c&oacute; thể phẫu thuật ở bất kỳ tuổi n&agrave;o kể cả sơ sinh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Sức cản mạch phổi &gt; 10 đơn vị/m<sup>2</sup> kh&ocirc;ng c&oacute; chỉ định phẫu thuật.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">OĐM ngắn, v&ocirc;i ho&aacute; nhiều cần phẫu thuật tim hở.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">B&iacute;t ống động mạch kh&ocirc;ng bằng phẫu thuật</span><span style=\"font-family:Arial\"><br>\r\n</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-size:14.0pt;line-height:\r\n150%;font-family:Arial\">Cửa sổ phế chủ</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">0,3% tim bẩm sinh</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Khuyết v&aacute;ch xoắn ốc ngăn chia động mạch chủ- động mạch phổi nằm tr&ecirc;n van sigma từ v&agrave;i mm đến 2cm, đường k&iacute;nh từ 5-30mm.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Chỉ định điều trị giống ống động mạch.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-size:14.0pt;line-height:\r\n150%;font-family:Arial\">K&ecirc;nh nhĩ thất</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">K&ecirc;nh nhĩ thất to&agrave;n phần: Th&ocirc;ng li&ecirc;n thất, th&ocirc;ng li&ecirc;n nhĩ v&agrave; tổn thương van nhĩ thất nặng, van 2 l&aacute; v&agrave; 3 l&aacute; c&oacute; chung một lỗ van nhĩ thất chung v&agrave; thường c&oacute; 5 mảnh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">K&ecirc;nh nhĩ thất b&aacute;n phần: th&ocirc;ng li&ecirc;n nhĩ kiểu lỗ thứ 2 hoặc t&acirc;m nhĩ độc nhất. Van 2 l&aacute; thường c&oacute; kẽ chia van l&agrave;m 3 mảnh. Van nhĩ thất chung trước sau được nối bằng một mảnh nhỏ m&ocirc; van do đ&oacute; chia lỗ nhĩ thất th&agrave;nh 2 lỗ ri&ecirc;ng biệt.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span style=\"font-family:Arial\">Si&ecirc;u &acirc;m:</span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"SV\" style=\"font-family:Arial;\r\nmso-ansi-language:SV\">v&aacute;ch li&ecirc;n nhĩ, v&aacute;ch li&ecirc;n thất. Van nhĩ thất: xem khe van, van nhĩ thất c&oacute; gắn v&agrave;o v&aacute;ch li&ecirc;n thất (k&ecirc;nh nhĩ thất b&aacute;n phần) hay kh&ocirc;ng gắn (to&agrave;n phần).</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"SV\" style=\"font-family:Arial;\r\nmso-ansi-language:SV\">Th&ocirc;ng li&ecirc;n nhĩ c&oacute; thể ở dạng lỗ thứ nhất hay t&acirc;m nhĩ độc nhất ở dạng th&ocirc;ng li&ecirc;n nhĩ lỗ thứ nhất c&oacute; thể phối hợp với lỗ thứ 2. Khi xoang v&agrave;nh gi&atilde;n rộng do tồn tại tĩnh mạch chủ tr&ecirc;n tr&aacute;i đổ v&agrave;o c&oacute; thể lầm với th&ocirc;ng li&ecirc;n nhĩ lỗ thứ nhất. th&ocirc;ng li&ecirc;n nhĩ lỗ thứ nhất thường nh&igrave;n r&otilde; v&agrave;o kỳ t&acirc;m thu.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"SV\" style=\"font-family:Arial;\r\nmso-ansi-language:SV\">Th&ocirc;ng li&ecirc;n thất trong k&ecirc;nh nhĩ thất do vị tr&iacute; nằm s&aacute;t với c&aacute;c d&acirc;y chằng n&ecirc;n c&oacute; thể bị che lấp ở kỳ t&acirc;m trương, do đ&oacute; nh&igrave;n r&otilde; ở kỳ t&acirc;m thu.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"SV\" style=\"font-family:Arial;\r\nmso-ansi-language:SV\">Van nhĩ thất chung thường l&agrave; năm mảnh hoặc c&oacute; một lưỡi m&ocirc; sợi nối từ v&ograve;ng nhĩ thất tới l&aacute; van, bắc cầu ph&iacute;a sau do đ&oacute; chia ra 2 lỗ van nhĩ thất v&agrave; 2 bộ van nhĩ thất.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"SV\" style=\"font-family:Arial;\r\nmso-ansi-language:SV\">Khe van 2 l&aacute; sẽ hướng về ph&iacute;a v&aacute;ch li&ecirc;n thất buồng nhận, c&ograve;n khe của van 2 l&aacute; thực sự sẽ hướng về v&aacute;ch li&ecirc;n thất <span style=\"mso-spacerun:yes\">&nbsp;</span>của buồng tống.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"PT-BR\" style=\"font-family:Arial;\r\nmso-ansi-language:PT-BR\">Cột cơ nằm ở 2h v&agrave; 4h thay v&igrave; 4h v&agrave; 8h.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b style=\"mso-bidi-font-weight:normal\"><span lang=\"SV\" style=\"font-family:Arial;mso-ansi-language:SV\">K&ecirc;nh nhĩ thất</span></b><b><span lang=\"PT-BR\" style=\"font-family:Arial;mso-ansi-language:PT-BR\"> ho&agrave;n to&agrave;n:</span></b><span lang=\"PT-BR\" style=\"font-family:Arial;mso-ansi-language:PT-BR\"> Ti&ecirc;n lượng xấu, trẻ thường chết 2 năm đầu, cần mổ sớm trước 6 th&aacute;ng tuổi, sau 1 tuổi kh&ocirc;ng c&ograve;n chỉ định phẫu thuật.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b style=\"mso-bidi-font-weight:normal\"><span lang=\"SV\" style=\"font-family:Arial;mso-ansi-language:SV\">K&ecirc;nh nhĩ thất</span></b><b><span lang=\"PT-BR\" style=\"font-family:Arial;mso-ansi-language:PT-BR\"> b&aacute;n phần:</span></b><span lang=\"PT-BR\" style=\"font-family:Arial;mso-ansi-language:PT-BR\"> Ti&ecirc;n lượng tốt hơn, cần mổ trước khi tăng &aacute;p động mạch phổi cơ học.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-size:14.0pt;\r\nline-height:150%;font-family:Arial;mso-ansi-language:NL\">Hẹp van động mạch phổi</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">10% tim bẩm sinh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Van động mạch phổi c&oacute; 3 l&aacute;: L&aacute; trước, l&aacute; sau phải v&agrave; sau tr&aacute;i.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Hẹp tại van:</span></b><span lang=\"NL\" style=\"font-family:\r\nArial;mso-ansi-language:NL\"> L&aacute; van dầy, d&iacute;nh nhau ở m&eacute;p n&ecirc;n tạo th&agrave;nh một c&aacute;i ch&oacute;p chồi ra , ở đầu ch&oacute;p th&agrave;nh một lỗ nhỏ </span><span style=\"font-family:\r\nSymbol\">f</span><span lang=\"NL\" style=\"font-family:Arial;mso-ansi-language:NL\"> v&agrave;i mm.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Hẹp tr&ecirc;n van:</span></b><span lang=\"NL\" style=\"font-family:\r\nArial;mso-ansi-language:NL\"> Hiếm hơn hẹp van, c&oacute; thể vị tr&iacute; ngay tr&ecirc;n van, c&oacute; thể hẹp tại th&acirc;n động mạch phổi nơi ph&acirc;n chia c&aacute;c nh&aacute;nh gần hoặc tại c&aacute;c nh&aacute;nh xa.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Hẹp dưới van:</span></b><span lang=\"NL\" style=\"font-family:\r\nArial;mso-ansi-language:NL\"> Hẹp tại phễu hay ngay trong l&ograve;ng thất phải .</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Điều trị nội: </span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Hẹp van động mạch phổi trẻ nhỏ, trẻ lớn v&agrave; người lớn: được coi l&agrave; nhẹ khi độ ch&ecirc;nh &aacute;p lực thất phải / động mạch phổi </span><span style=\"font-family:Symbol\">&pound;</span><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\"> 25mmHg; nặng vừa từ 25-50mmHg, can thiệp c&agrave;ng chậm c&agrave;ng hẹp nặng hơn do v&ugrave;ng phễu ph&igrave; đại.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><i><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Sơ sinh hẹp van động mạch phổi nặng:</span></i><span lang=\"NL\" style=\"font-family:Arial;mso-ansi-language:NL\"> truyền prostaglandine E1 gi&uacute;p ống động mạch th&ocirc;ng thương để c&oacute; m&aacute;u l&ecirc;n phổi, v&agrave;o ng&agrave;y ổn định th&igrave; chỉ định nong b&oacute;ng hoặc phẫu thuật ngay.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><i><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Trẻ nhỏ (<sup> </sup>1<sup>th</sup> -12<sup>th</sup>):</span></i><span lang=\"NL\" style=\"font-family:Arial;mso-ansi-language:NL\"> hẹp van động mạch phổi nặng d&ugrave; chưa c&oacute; triệu chứng cơ năng cần nong b&oacute;ng hoặc phẫu thuật ngay, kh&ocirc;ng can thiệp khi hẹp nhẹ, hẹp vừa cần xem x&eacute;t từng trường hợp. V&iacute; dụ: d&ugrave; hẹp vừa nhưng giảm sản thất phải <span style=\"mso-spacerun:yes\">&nbsp;</span>cần phẫu thuật ngay.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Hẹp van động mạch phổi ở trẻ lớn v&agrave; người lớn ở thể nặng,vừa &iacute;t tiến triển đến nặng do đ&oacute; &iacute;t cần can thiệp tuy nhi&ecirc;n nếu k&egrave;m giảm sản thất phải <span style=\"mso-spacerun:yes\">&nbsp;</span>cũng cần phẫu thuật sớm.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><i><span lang=\"NL\" style=\"font-family:\r\nArial;mso-ansi-language:NL\">T&oacute;m lại:</span></i></b><span lang=\"NL\" style=\"font-family:Arial;mso-ansi-language:NL\"> chỉ định phẫu thuật khi: b&eacute; &lt; 2 tuổi nếu hẹp kh&iacute;t; 8-12 tuổi với &aacute;p lực thất phải <span style=\"mso-spacerun:yes\">&nbsp;</span>&gt; 70mmHg, &aacute;p lực thất phải <span style=\"mso-spacerun:yes\">&nbsp;</span>&lt; 70mmHg với &aacute;p lực cuối t&acirc;m trương thất phải <span style=\"mso-spacerun:yes\">&nbsp;</span>&gt; 10mmHg.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"PT-BR\" style=\"font-size:14.0pt;\r\nline-height:150%;font-family:Arial;mso-ansi-language:PT-BR\">Hẹp eo động mạch chủ</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"PT-BR\" style=\"font-family:Arial;\r\nmso-ansi-language:PT-BR\">7,5% c&aacute;c loại tim bẩm sinh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span lang=\"PT-BR\" style=\"font-family:\r\nArial;mso-ansi-language:PT-BR\">Đơn thuần:</span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Ph&aacute;t sinh THA thượng lưu khi l&ograve;ng Ao &lt; 50% b&igrave;nh thường.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Chỉ định phẫu thuật c&aacute;c ca c&oacute; </span><span style=\"font-family:Symbol\">f</span><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\"> Ao &lt; 50% v&agrave; giảm hay mất mạch bẹn hoặc c&oacute; tăng huyết &aacute;p thượng chi huyết &aacute;p</span><span style=\"font-family:Symbol\">&sup3;</span><span lang=\"NL\" style=\"font-family:Arial;mso-ansi-language:NL\"> 150mmHg. Tuổi th&iacute;ch hợp phẫu thuật l&agrave; 2-5 tuổi.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Hội chứng:</span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Hẹp eo động mạch chủ kh&ocirc;ng th&iacute;ch ứng g&acirc;y suy tim sớm. Kh&ocirc;ng c&oacute; sự kh&aacute;c biệt giữa huyết &aacute;pthượng chi v&agrave; hạ chi v&igrave; tổn thương phối hợp l&agrave; một Shunt (OĐM).</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Th&ocirc;ng li&ecirc;n thất k&egrave;m theo l&agrave;m suy tim sớm.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Tử vong &gt; 50% ở tuổi sơ sinh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span style=\"font-family:Arial\">T&oacute;m lại: </span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">&nbsp;95% ở phần tr&ecirc;n Ao xuống, ngay s&aacute;t động mạch dưới đ&ograve;n tr&aacute;i, người ta ph&acirc;n biệt: trước ống động mạch, sau ống động mạch.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Trẻ sơ sinh hẹp eo động mạch chủ nặng (suy tim, biến dưỡng m&aacute;u, mất mạch đ&ugrave;i, sốc) truyền prostaglandine E1 gi&uacute;p mở ống động mạch, phẫu thuật hoặc nong b&oacute;ng ngay giờ thứ 6-12 hay ngay sau đ&oacute;.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Phẫu thuật, thời gian tuỳ triệu chứng suy tim. Trẻ sơ sinh suy tim nặng </span><span style=\"font-family:Symbol\">&reg;</span><span style=\"font-family:Arial\"> can thiệp ngay sau khi tạm ổn huyết động. Trẻ nhỏ kh&ocirc;ng triệu chứng suy tim, độ ch&ecirc;nh &aacute;p lực t&acirc;m thu tr&ecirc;n 40 mmHg, một số t&aacute;c giả chủ trương n&ecirc;n phẫu thuật v&agrave;o 3-6 th&aacute;ng tuổi kh&ocirc;ng n&ecirc;n qu&aacute; 6 th&aacute;ng tuổi.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-size:14.0pt;line-height:\r\n150%;font-family:Arial\">Ph&igrave;nh xoang Valsava</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">0,15-0,35% tim bẩm sinh</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Ph&igrave;nh xoang bắt nguồn từ đ&aacute;y tim lan ra c&aacute;c buồng tim.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Xoang v&agrave;nh tr&aacute;i kh&ocirc;ng c&oacute; nguồn gốc từ v&aacute;ch h&agrave;nh thất n&ecirc;n hiếm ph&igrave;nh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Si&ecirc;u &acirc;m 2D ph&aacute;t hiện t&uacute;i ph&igrave;nh, kết hợp Doppler để x&aacute;c định đ&atilde; vỡ ?</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">V&ugrave;ng ph&igrave;nh xoang sẽ lớn v&agrave; mất c&acirc;n xứng.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Nếu kh&ocirc;ng vỡ theo d&otilde;i định kỳ bằng si&ecirc;u &acirc;m.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Phẫu thuật khi c&oacute; biến chứng hay thể t&iacute;ch tim tăng.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Chỉ định phẫu thuật tuyệt đối khi vỡ.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Cần điều trị tổn thương k&egrave;m theo.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-size:14.0pt;line-height:\r\n150%;font-family:Arial\">Kh&ocirc;ng tương hợp nhĩ- thất- đại động mạch</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">1% tim bẩm sinh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span style=\"font-family:Arial\">Dạng đơn độc:</span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Nhĩ tr&aacute;i nối thất phải <span style=\"mso-spacerun:yes\">&nbsp;</span>qua van dạng 3 l&aacute;.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"FR\" style=\"font-family:Arial;\r\nmso-ansi-language:FR\">Nhĩ phải nối thất tr&aacute;i <span style=\"mso-spacerun:yes\">&nbsp;</span>qua van dạng 2 l&aacute;.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Động mạch chủ từ thất phải , động mạch phổi từ thất tr&aacute;i .</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">C&oacute; thể van 3 l&aacute; dạng Ebstein, van 2 l&aacute; bất thường.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Dạng k&egrave;m th&ocirc;ng li&ecirc;n thất:</span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Ngo&agrave;i bất tương hợp c&ograve;n k&egrave;m theo th&ocirc;ng li&ecirc;n thất. </span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nletter-spacing:-.3pt;mso-ansi-language:NL\">Điều trị: Xiết bớt PA, để bảo vệ mạch m&aacute;u phổi, chờ 18-24 th&aacute;ng th&igrave; đ&oacute;ng VSD.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span lang=\"NL\" style=\"font-family:Arial;\r\nletter-spacing:-.3pt;mso-ansi-language:NL\">Dạng k&egrave;m th&ocirc;ng li&ecirc;n thất v&agrave; hẹp động mạch phổi:</span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Ngo&agrave;i bất tương hợp c&ograve;n k&egrave;m th&ocirc;ng li&ecirc;n thất v&agrave; hẹp động mạch phổi.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Dưới 2 tuổi: T&iacute;m nặng k&egrave;m HC &gt; 6,5 triệu. Sửa tạm thời bằng nối động mạch dưới đ&ograve;n v&agrave; động mạch phổi.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Tr&ecirc;n 2 tuổi: Kh&ocirc;ng c&oacute; tổn thương phối hợp, đặt ống nối thất tr&aacute;i - động mạch chủ, thất phải - động mạch phổi.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-size:14.0pt;\r\nline-height:150%;font-family:Arial;mso-ansi-language:NL\">Kh&ocirc;ng lỗ van động mạch chủ</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">1-2% tim bẩm sinh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">T&acirc;m thất tr&aacute;i kh&ocirc;ng ph&aacute;t triển.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Chẩn đo&aacute;n bằng h&igrave;nh ảnh bởi c&aacute;c mặt cắt cơ b���n.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-size:14.0pt;\r\nline-height:150%;font-family:Arial;mso-ansi-language:NL\">Kh&ocirc;ng lỗ van động mạch phổi</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-size:14.0pt;\r\nline-height:150%;font-family:Arial;mso-ansi-language:NL\">Khối u tim </span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">75% l&agrave; l&agrave;nh t&iacute;nh (myxome), 25% l&agrave; &aacute;c t&iacute;nh(sarcome).</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Myxom:</span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nletter-spacing:-.3pt;mso-ansi-language:NL\">75% ở nhĩ tr&aacute;i, thường b&aacute;m v&agrave;o nội t&acirc;m mạc, đ&aacute;y rộng, c&oacute; cuống, </span><span style=\"font-family:Symbol;letter-spacing:\r\n-.3pt\">f</span><span lang=\"NL\" style=\"font-family:Arial;letter-spacing:-.3pt;\r\nmso-ansi-language:NL\"> u từ 1-15cm.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">U nhầy thường kh&ocirc;ng đồng nhất tr&ecirc;n si&ecirc;u &acirc;m 2D v&agrave; khong c&oacute; từng lớp nhu huyết khối, hay nằm ở phần trước v&agrave; giữa của nhĩ tr&aacute;i trong khi huyết khối ở th&agrave;nh sau nhĩ tr&aacute;i.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">C&aacute;c bướu tim nguy&ecirc;n ph&aacute;t l&agrave;nh t&iacute;nh kh&aacute;c:</span></b><span lang=\"NL\" style=\"font-family:Arial;mso-ansi-language:NL\"> U cơ v&acirc;n, u xơ, u m&ocirc; mỡ...</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Sarcom:</span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Thrombus:</span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Thường l&agrave; biến chứng của nhồi m&aacute;u cơ tim hay gặp ở thất tr&aacute;i, thất phải. thường nằm ở mỏm tim thất tr&aacute;i.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">C&oacute; thể thấy h&igrave;nh ảnh cản &acirc;m tự ph&aacute;t (SC).</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-size:18.0pt;\r\nline-height:150%;font-family:Arial;mso-ansi-language:NL\">Bệnh tim bẩm sinh t&iacute;m</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Si&ecirc;u &acirc;m: cần khảo s&aacute;t c&oacute; hệ thống ở tất cả c&aacute;c tim bẩm sinh t&iacute;m:</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Situs solitus hay situs invensus.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"FR\" style=\"font-family:Arial;\r\nletter-spacing:-.5pt;mso-ansi-language:FR\">Tương quan nhĩ thất.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"PT-BR\" style=\"font-family:Arial;\r\nmso-ansi-language:PT-BR\">Tương quan giữa thất v&agrave; đại ĐM.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">L&agrave;m tổng quan to&agrave;n bộ để tr&aacute;nh s&oacute;t tổn thương.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-size:14.0pt;line-height:\r\n150%;font-family:Arial\">T4F (th&ocirc;ng li&ecirc;n thất)</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">9% tim bẩm sinh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Trong T4F thường gặp hẹp phễu động mạch phổi đơn độc, hoặc hẹp phẫu k&egrave;m hẹp van , rất hiếm hẹp van&nbsp; đơn độc.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Th&acirc;n động mạch phổi thường k&eacute;m ph&aacute;t triển ở c&aacute;c dạng hẹp động mạch phổi của T4F, thường kh&ocirc;ng c&oacute; gi&atilde;n sau chỗ hẹp. Hẹp ở nơi xuất ph&aacute;t nh&aacute;nh hoặc nh&aacute;nh rất hiếm.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Th&ocirc;ng li&ecirc;n thất thường rộng, phần m&agrave;ng lan l&ecirc;n v&ugrave;ng phễu.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">ĐMC cưỡi ngựa, nặng khi </span><span style=\"font-family:Symbol\">&sup3;</span><span style=\"font-family:Arial\"> 50%. Lu&ocirc;n c&oacute; sự li&ecirc;n tục van 2 l&aacute;- động mạch chủ (ph&acirc;n biệt với thất phải <span style=\"mso-spacerun:yes\">&nbsp;</span>2 đường ra).</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Tổn thương phối hợp c&oacute; thể l&agrave;: Cung động mạch chủ nằm b&ecirc;n phải, bất thường trở về của tĩnh mạch to&agrave;n th&acirc;n, tĩnh mạch chủ tr&ecirc;n tr&aacute;i đổ v&agrave;o xoang v&agrave;nh, C&ograve;n ống động mạch, tồn tại lỗ bầu dục (2/3 T4F gi&agrave;), bất thường động mạch v&agrave;nh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span style=\"font-family:Arial\">Điều trị ngoại khoa:</span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Phẫu thuật l&yacute; tưởng l&agrave; 2-3 tuổi (trong khoảng 6 th&aacute;ng đến 5 tuổi phẫu thuật &iacute;t nguy cơ hơn)</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Động mạch phổi k&eacute;m ph&aacute;t triển &lt; 50% (t&iacute;nh theo diện t&iacute;ch da); bất thường đường đi động mạch v&agrave;nh; trẻ &lt; 5 kg c&ugrave;ng với Hct &gt; 70% c&oacute; triệu chứng cơ năng nặng thường mổ tạm th���i.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">V&ograve;ng<span style=\"letter-spacing:-.25pt\"> van động mạch phổi nhỏ v&agrave; </span></span><span style=\"font-family:Symbol;letter-spacing:-.25pt\">[</span><span style=\"font-family:Arial;letter-spacing:-.25pt\">động mạch phổi(phải) + động mạch phổi(tr&aacute;i)</span><span style=\"font-family:Symbol;letter-spacing:-.25pt\">]</span><span style=\"font-family:Arial;letter-spacing:-.25pt\"> / động mạch chủ xuống &lt; 1,5 k&egrave;m diện t&iacute;ch da &lt; 0,48 m<sup>2</sup> th&igrave; phẫu thuật tạm thời (đo ở kỳ t&acirc;m thu). Trẻ sơ sinh c&oacute; triệu chứng cơ năng nặng c&oacute; thể phẫu thuật ở th&aacute;ng tuổi thứ 3.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial;letter-spacing:\r\n-.3pt\">Trẻ &iacute;t triệu chứng cơ năng: theo d&otilde;i 6 th&aacute;ng/lần, đợi mổ l&uacute;c 24 th&aacute;ng tuổi.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-size:14.0pt;line-height:\r\n150%;font-family:Arial\">Chuyển gốc đại động mạch</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Ngo&agrave;i lỗ bầu dục th&ocirc;ng thương v&agrave; c&ograve;n ống động mạch ngay sau sinh, bệnh c&oacute; thể ở dạng đơn thuần hay phức tạp (k&egrave;m th&ocirc;ng li&ecirc;n thất, k&egrave;m hẹp động mạch phổi).</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span style=\"font-family:Arial\">Đơn thuần:</span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Tim lu&ocirc;n b&ecirc;n tr&aacute;i v&agrave; thuộc loại Situs solitus.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Tương hợp nhĩ thất.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">ĐMP từ thất tr&aacute;i , c&oacute; sự li&ecirc;n tục giữa động mạch phổi - van 2 l&aacute;.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">ĐMC nằm ph&iacute;a trước v&agrave; c&oacute; thể chếch tr&aacute;i hoặc phải so với động mạch phổi, c&oacute; khi 2 động mạch nằm song song.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span style=\"font-family:Arial\">Phức tạp:</span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">L&agrave; loại đơn thuần k&egrave;m với một hoặc nhiều tổn thương: th&ocirc;ng li&ecirc;n thất, hẹp động mạch phổi, hẹp eo Ao...</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">OĐM: Ngay sau sinh shunt 2 chiều v&agrave;i ng&agrave;y sau shunt theo chiều chủ-phổi.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Lỗ bầu dục: Shunt 2 chiều, từ phổi qua chủ cuối t&acirc;m thu, từ chủ qua phổi cuối t&acirc;m trương.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span style=\"font-family:Arial\">Điều trị:</span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><i><span style=\"font-family:Arial\">Nội khoa:</span></i><span style=\"font-family:Arial\"> Truyền Prostaglandin E1 để giữ ống động mạch mở, c&oacute; hiệu quả trong v&agrave;i ng&agrave;y.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Hầu hết trẻ sơ sinh c&oacute; ho&agrave;n vị đại động mạch đơn thuần đều tử vong trong v&agrave;i ng&agrave;y.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><i><span style=\"font-family:Arial\">Thể k&egrave;m </span></i><i style=\"mso-bidi-font-style:normal\"><span style=\"font-family:Arial\">th&ocirc;ng li&ecirc;n thất<span style=\"mso-bidi-font-style:italic\">:</span></span></i><span style=\"font-family:Arial\"> Tiến triển nặng ngay như dạng đơn thuần, tốt nhất phẫu thuật c&agrave;ng sớm c&agrave;ng tốt n&ecirc;n mổ trước 1 tuổi thường sau 2 tuần tuổi.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><i><span style=\"font-family:Arial\">Thể ho&agrave;n vị k&egrave;m th&ocirc;ng li&ecirc;n thất v&agrave; hẹp động mạch chủ:</span></i><span style=\"font-family:Arial\"> Xem x&eacute;t từng trường hợp</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-size:14.0pt;line-height:\r\n150%;font-family:Arial\">Thất phải hai đường ra</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">0,5% tim bẩm sinh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Hai đại động mạch đều từ thất phải , hoặc động mạch chủ cưỡi ngựa m&agrave; tr&ecirc;n 50% thuộc thất phải , van động mạch chủ nằm cao gần bằng van động mạch phổi.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Th&ocirc;ng li&ecirc;n thất l&agrave; tổn thương lu&ocirc;n lu&ocirc;n c&oacute;, thường l&agrave; quanh m&agrave;ng.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Hẹp động mạch phổi thường l&agrave; hẹp phễu hoặc hẹp cả phễu v&agrave; van, tuỳ theo vị tr&iacute; của th&ocirc;ng li&ecirc;n thất v&agrave; c&oacute; hẹp động mạch phổi hay kh&ocirc;ng, người ta chia ra 3 dạng kh&aacute;c nhau.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Tổn thương phối hợp thường l&agrave;: nối liền bất thường tĩnh mạch phổi hoặc tĩnh mạch chủ, k&ecirc;nh nhĩ thất, hẹp 2 l&aacute; bẩm sinh, kh&ocirc;ng lỗ van Ao.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Trẻ sơ sinh cần sửa chữa tạm thời.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Tr&ecirc;n 6 th&aacute;ng: phẫu thuật to&agrave;n diện nếu: hai thất c&oacute; k&iacute;ch thước gần b&igrave;nh thường, c&aacute;c van nhĩ thất c&oacute; k&iacute;ch thước v&agrave; chức năng gần b&igrave;nh thường, mạng động mạch phổi c&oacute; k&iacute;ch thước b&igrave;nh thường, kh&ocirc;ng c&oacute; nhiều th&ocirc;ng li&ecirc;n thất phần cơ.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"FR\" style=\"font-size:14.0pt;\r\nline-height:150%;font-family:Arial;mso-ansi-language:FR\">Tim một t&acirc;m thất</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"FR\" style=\"font-family:Arial;\r\nmso-ansi-language:FR\">1% tim bẩm sinh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Lu&ocirc;n lu&ocirc;n c&oacute; tổn thương phối hợp.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Thể phức tạp kh&ocirc;ng sống qu&aacute; 6 th&aacute;ng ở 50% số bệnh nh&acirc;n.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Giải phẫu tạm thời: Để điều chỉnh lưu lượng phổi, lưu lượng chủ.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Khi chức năng thất tốt, &aacute;p lực động mạch phổi trung b&igrave;nh &lt; 20mmHg v&agrave; sức cả<span style=\"letter-spacing:-.4pt\">n mạch phổi c&ograve;n b&igrave;nh thường th&igrave; tiến h&agrave;nh phẫu thuật to&agrave;n diện .</span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-size:14.0pt;line-height:\r\n150%;font-family:Arial\">Th&acirc;n chung động mạch</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">1% tim bẩm sinh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Chỉ c&oacute; một th&acirc;n xuất ph&aacute;t từ đ&aacute;y tim.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">C&oacute; ba bất thường: Bất thường trong tim,bất thường van của th&acirc;n chung v&agrave; bất thường của c&aacute;c động mạch từ th&acirc;n chung.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Bất thường trong tim: th&ocirc;ng li&ecirc;n thất, c&oacute; sự li&ecirc;n tục về m&ocirc; sợi giữa van th&acirc;n chung với van hai l&aacute; hoặc cả van 2 l&aacute;, van 3 l&aacute;, &iacute;t thấy hơn l&agrave; bất thường TMP, kh&ocirc;ng lỗ van 3 l&aacute;, k&ecirc;nh nhĩ thất, t&acirc;m thất độc nhất.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Bất thường van th&acirc;n chung: thay đổi từ 1-6 van, hẹp, hở...</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">C&aacute;c động mạch xuất ph&aacute;t từ th&acirc;n chung: Bất thường về chỗ xuất ph&aacute;t động mạch v&agrave;nh, cung Ao c&oacute; thể c&oacute; 1 hoặc 2 cung, nằm phải hoặc tr&aacute;i trục kh&iacute; quản, thực quản. Ao ngang c&oacute; thể teo nhỏ, nu&ocirc;i dưới cơ thể qua ống động mạch. Từ bất thường động mạch phổi người ta chia ra l&agrave;m nhiều dạng th&acirc;n chung.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial;letter-spacing:\r\n-.4pt\">Điều trị triệu chứng suy tim xung huyết, tử vong 85% trong năm đầu ti&ecirc;n.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">C<span style=\"letter-spacing:-.4pt\">&oacute; thể phẫu thuật ở trẻ sơ sinh, 2-3 th&aacute;ng tuổi, thường l&agrave; 3 tuổi mổ lần 2.</span></span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-size:14.0pt;line-height:\r\n150%;font-family:Arial\">Ebstein</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">0,6% tim bẩm sinh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Nằm trong bệnh tắc nghẽn thất thất phải .</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Tổn thương ở van 3 l&aacute; do dư m&ocirc; van, d&iacute;nh l&aacute; v&aacute;ch v&agrave; l&aacute; sau dưới v&agrave;o th&agrave;nh thất phải , xa v&ograve;ng nối nhĩ thất, thể nhẹ xảy ra ở l&aacute; v&aacute;ch, thể nặng cả l&aacute; v&aacute;ch v&agrave; l&aacute; sau dưới.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Ứ m&aacute;u ở nhĩ phải v&agrave; buồng ho&aacute; nhĩ của thất.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Si&ecirc;u &acirc;m: L&aacute; v&aacute;ch của 3 l&aacute; so với l&aacute; lớn van 2 l&aacute;: độ ch&ecirc;nh &gt; 15mm ở trẻ em v&agrave; &gt; 20mm ở người lớn.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Trẻ sơ sinh chưa c&oacute; khả năng phẫu thuật, điều trị bằng O2, lợi tiểu, Digoxin.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Trẻ lớn v&agrave; người lớn: phẫu thuật được chỉ định khi: t&iacute;m nặng, suy tim, ứ huyết hoặc loạn nhịp tim.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-size:14.0pt;line-height:\r\n150%;font-family:Arial\">Kh&ocirc;ng lỗ van động mạch phổi k&egrave;m th&ocirc;ng li&ecirc;n thất</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">0,2% tim bẩm sinh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Kh&ocirc;ng c&oacute; sự li&ecirc;n tục giữa thất phải <span style=\"mso-spacerun:yes\">&nbsp;</span>với động mạch phổi, giống bất thường của bệnh nối liền bất thường tĩnh mạch phổi.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Th&acirc;n động mạch phổi 3/4 trường hợp teo nhỏ, 1/4 kh&ocirc;ng thấy. động mạch phổi phải v&agrave; tr&aacute;i c&oacute; thể th&ocirc;ng thương hoặc kh&ocirc;ng.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Tưới m&aacute;u cho phổi bởi ống động mạch, động mạch b&agrave;ng hệ, động mạch phế quản hoặc m&agrave;ng phổi.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Điều trị n&ocirc;i bằng </span><span lang=\"PT-BR\" style=\"font-family:Arial;mso-ansi-language:\r\nPT-BR\">Digoxin, lợi tiểu, gi&atilde;n mạch.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"PT-BR\" style=\"font-family:Arial;\r\nletter-spacing:-.4pt;mso-ansi-language:PT-BR\">K&iacute;ch thước động mạch phổi trung t&acirc;m </span><span style=\"font-family:Symbol;letter-spacing:-.4pt\">&sup3;</span><span lang=\"PT-BR\" style=\"font-family:Arial;letter-spacing:-.4pt;mso-ansi-language:\r\nPT-BR\"> 50% trị số b&igrave;nh thường c&oacute; thể mổ triệt để được.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-size:14.0pt;line-height:\r\n150%;font-family:Arial\">TMP đổ về bất thường</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Tr&ecirc;n 2% tim bẩm sinh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span style=\"font-family:Arial\">B&aacute;n phần:</span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Thường th&igrave; tĩnh mạch phổi tr&aacute;i v&agrave;o tĩnh mạch chủtr&ecirc;n tr&aacute;i v&agrave; xoang v&agrave;nh; tĩnh mạch phổi phải sẽ đổ v&agrave;o tĩnh mạch chủtr&ecirc;n phải, tĩnh mạch chủdưới v&agrave; nhĩ phải.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Tĩnh mạch phổi phải hay gặp bất thường hơn.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">&nbsp;Một số trường hợp c&oacute; k&egrave;m với th&ocirc;ng li&ecirc;n nhĩ.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span style=\"font-family:Arial\">Ho&agrave;n to&agrave;n:</span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">2/3 nối liền tĩnh mạch phổi bất thường ho&agrave;n to&agrave;n l&agrave; tổn thương đơn độc, 1/3 l&agrave; tổn thương phối hợp với: th&ocirc;ng li&ecirc;n thất, T4F, thất phải hai đường ra.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">C&aacute;c tĩnh mạch phổi gom v&agrave;o một ống thu thập rồi từ đ&oacute; mới nối liền với c&aacute;c cấu tr&uacute;c tr&ecirc;n tim, tại tim hoặc dưới tim.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\nline-height:150%\"><span style=\"font-family:Arial\">Nhĩ tr&aacute;i v&agrave; thất tr&aacute;i <span style=\"mso-spacerun:yes\">&nbsp;</span>thường nhỏ nhưng kh&ocirc;ng teo.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-size:14.0pt;line-height:\r\n150%;font-family:Arial\">Trở về bất thường tĩnh mạch hệ thống</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">C&oacute; thể xảy ra đơn độc, nhưng thường phối hợp với bất thường kh&aacute;c của tim.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span style=\"font-family:Arial\">Bất thường của tĩnh mạch chủ v&agrave;o nhĩ phải:</span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial;letter-spacing:\r\n-.5pt\">Thường kh&ocirc;ng c&oacute; biểu hiện l&acirc;m s&agrave;ng hoặc c&oacute; biểu hiện của tổn thương phối hợp.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Tĩnh mạch chủ tr&ecirc;n tr&aacute;i đổ v&agrave;o xoang v&agrave;nh: 0,3% tim bẩm sinh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Tĩnh mạch chủ dưới nối với nhĩ phải qua trung gian tĩnh mạch azygos.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span style=\"font-family:Arial\">Nối liền bất thường b&aacute;n phần tĩnh mạch chủv&agrave;o nhĩ phải:</span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Tĩnh mạch chủ tr&ecirc;n tr&aacute;i đổ v&agrave;o<span style=\"mso-bidi-font-weight:bold\"> nhĩ phải</span>.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Tĩnh mạch chủ tr&ecirc;n phải đổ v&agrave;o nhĩ tr&aacute;i.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Tĩnh mạch chủ dưới đổ v&agrave;o nhĩ tr&aacute;i.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span style=\"font-family:Arial\">Nối to&agrave;n bộ tĩnh mạch chủ v&agrave;o </span></b><b style=\"mso-bidi-font-weight:normal\"><span style=\"font-family:Arial\">nhĩ tr&aacute;i<span style=\"mso-bidi-font-weight:bold\"> (hiếm gặp):</span></span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><b><span style=\"font-family:Arial\">Bất thường c&aacute;c van của xoang tĩnh mạch:</span></b></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Van Eustachi (tĩnh mạch chủ dưới), van Thebesius (xoang v&agrave;nh).</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">C&aacute;c van n&agrave;y kh&ocirc;ng tho&aacute;i ho&aacute; sẽ tạo th&agrave;nh một m&agrave;ng.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-size:14.0pt;line-height:\r\n150%;font-family:Arial\">Kh&ocirc;ng lỗ van 3 l&aacute;</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">3% tim bẩm sinh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Van 3 l&aacute; kh&ocirc;ng ph&aacute;t triển.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Th&agrave;nh nhĩ phải dầy, l&ograve;ng nhĩ phải kh&ocirc;ng tăng k&iacute;ch thước, 10% c&oacute; ho&aacute;n vị đại động mạch, bất thường tiểu nhĩ.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Th&ocirc;ng li&ecirc;n nhĩ c&oacute; nhiều tuyp.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Th&ocirc;ng li&ecirc;n thất, thất tr&aacute;i <span style=\"mso-spacerun:yes\">&nbsp;</span>l&agrave; buồng ch&iacute;nh, thất phải <span style=\"mso-spacerun:yes\">&nbsp;</span>l&agrave; buồng phụ.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Phẫu thuật hỗ trợ nhằm ổn định lưu lượng phổi ở trẻ </span><span style=\"font-family:Symbol\">&pound;</span><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\"> 3 th&aacute;ng tuổi.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Phẫu thuật sửa chữa khi 2-4 tuổi với điều kiện: nhịp xoang, tĩnh mạch chủb&igrave;nh thường, NP k&iacute;ch thước b&igrave;nh thường co b&oacute;p tốt, &aacute;p lực động mạch phổi trung b&igrave;nh &lt; 15mmHg, v&ograve;ng van động mạch phổi b&igrave;nh thường, chức năng thất tr&aacute;i <span style=\"mso-spacerun:yes\">&nbsp;</span>tốt, kh&ocirc;ng hở van 2 l&aacute;, nh&aacute;nh động mạch phổi kh&ocirc;ng hẹp c&oacute; thể chấp nhận được.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-size:18.0pt;\r\nline-height:150%;font-family:Arial;mso-ansi-language:NL\">Bệnh tim bảm sinh ở người lớn</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-size:14.0pt;\r\nline-height:150%;font-family:Arial;mso-ansi-language:NL\">Th&ocirc;ng li&ecirc;n nhĩ</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Th&ocirc;ng li&ecirc;n nhĩ lỗ thứ ph&aacute;t chỉ c&oacute; tần suất 7% trong tim bẩm sinh trẻ em nhưng lại l&ecirc;n đến 40% tim bẩm sinh ở người lớn. C&oacute; 4 thể th&ocirc;ng li&ecirc;n nhĩ: Ti&ecirc;n ph&aacute;t 15%; Thứ ph&aacute;t 75%; Xoang tĩnh mạch 10%; Xoang v&agrave;nh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Biến chứng th&ocirc;ng li&ecirc;n nhĩ chưa phẫu thuật: tăng &aacute;p động mạch phổi, suy tim phải, rung nhĩ v&agrave; tai biến mạch n&atilde;o.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Th&ocirc;ng li&ecirc;n nhĩ lỗ nhỏ với tỉ lệ QP/QS &lt; 1,5 kh&ocirc;ng cần phẫu thuật.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">N&ecirc;n phẫu thuật trước 25 tuổi v&agrave; &aacute;p lực động mạch phổi t&acirc;m thu </span><span style=\"font-family:Symbol\">&pound;</span><span lang=\"NL\" style=\"font-family:Arial;mso-ansi-language:NL\"> 40mmHg. Chỉ định c&ograve;n phẫu thuật được hay kh&ocirc;ng dựa v&agrave;o &aacute;p lực động mạch phổi, sức cản mạch phổi v&agrave; QP/QS.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">50 - 60 tuổi c&oacute; ASD c&ugrave;ng &aacute;p lực: PA t&acirc;m thu </span><span style=\"font-family:Symbol\">&raquo;</span><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\"> 50mmHg, phẫu thuật vẫn cho kết quả tốt.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-size:14.0pt;\r\nline-height:150%;font-family:Arial;mso-ansi-language:NL\">Th&ocirc;ng li&ecirc;n thất</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Người lớn &iacute;t gặp v&igrave; th&ocirc;ng li&ecirc;n thất cần phẫu thuật sớm để tr&aacute;nh Eisenmenger.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Biến chứng của th&ocirc;ng li&ecirc;n thất l&agrave;: tăng &aacute;p động mạch phổi, suy tim tr&aacute;i, vi&ecirc;m nội t&acirc;m mạc nhiễm tr&ugrave;ng, hở van Ao.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Chỉ định phẫu thuật th&ocirc;ng li&ecirc;n thất dựa v&agrave;o &aacute;p lực động mạch phổi v&agrave; triệu chứng suy tim tr&aacute;i.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Người lớn th&ocirc;ng li&ecirc;n thất lỗ nhỏ với &aacute;p lực động mạch phổi b&igrave;nh thường v&agrave; QP/QS &lt; 1,3 kh&ocirc;ng cần phẫu thuật. Nếu trong khoảng 1,3-1,5 cần xem x&eacute;t từng trường hợp. Nếu QP/QS &gt; 1,5 v&agrave; &aacute;p lực động mạch phổi </span><span style=\"font-family:Symbol\">&pound;</span><span lang=\"NL\" style=\"font-family:Arial;mso-ansi-language:NL\"> 50mmHg cần phẫu thuật.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Người lớn c&oacute; th&ocirc;ng li&ecirc;n thất lỗ lớn với &aacute;p lực động mạch phổi </span><span style=\"font-family:Symbol\">&sup3;</span><span lang=\"NL\" style=\"font-family:Arial;mso-ansi-language:NL\"> 70mmHg nhưng QP/QS thấp do sức cản mạch phổi cao tr&ecirc;n 7 đơn vị/m<sup>2</sup> kh&ocirc;ng n&ecirc;n phẫu thuật.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-size:14.0pt;\r\nline-height:150%;font-family:Arial;mso-ansi-language:NL\">C&ograve;n ống động mạch</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">OĐM cần phẫu thuật trước khi biến chứng tăng &aacute;p động mạch phổi l&agrave;m th&agrave;nh hội chứng Eisenmenger.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Biến chứng của c&ograve;n ống động mạch bao gồm: suy tim tr&aacute;i, tăng &aacute;p động mạch phổi, vi&ecirc;m nội t&acirc;m mạc nhiễm tr&ugrave;ng v&agrave; v&ocirc;i ho&aacute; th&agrave;nh ống động mạch bắt đầu từ ph&iacute;a Ao.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Tất cả bệnh nh&acirc;n C&ograve;n ống động mạch cần phẫu thuật, ngoại trừ ống động mạch nhỏ kh&ocirc;ng nghe &acirc;m thổi k&egrave;m &aacute;p lực động mạch phổi b&igrave;nh thường.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-size:14.0pt;\r\nline-height:150%;font-family:Arial;mso-ansi-language:NL\">Hẹp van động mạch phổi</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">96% bệnh nh&acirc;n hẹp van động mạch phổi c&ograve;n sống ở tuổi 25.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Biến chứng của hẹp van động mạch phổi kh&ocirc;ng phẫu thuật l&agrave;: Loạn nhịp thất, vi&ecirc;m nội t&acirc;m mạc nhiễm tr&ugrave;ng, suy tim phải, tiến triển ng&agrave;y c&agrave;ng nặng mức độ hẹp.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Chỉ định phẫu thuật hẹp van động mạch phổi dựa tr&ecirc;n độ ch&ecirc;nh &aacute;p lực thất thất phải - động mạch phổi v&agrave; triệu chứng cơ năng.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Ở người lớn thể hẹp van động mạch phổi thể vừa &iacute;t tiến triển đến nặng do đ&oacute; &iacute;t cần can thiệp tuy nhi&ecirc;n nếu k&egrave;m thiểu sản thất phải <span style=\"mso-spacerun:yes\">&nbsp;</span>cũng cần phải phẫu thuật sớm.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-size:14.0pt;\r\nline-height:150%;font-family:Arial;mso-ansi-language:NL\">Hep van động mạch chủ</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Nghẽn đường ra thất tr&aacute;i <span style=\"mso-spacerun:yes\">&nbsp;</span>c&oacute; thể hẹp van Ao, tr&ecirc;n hoặc dưới van; ở người lớn 50% hẹp van Ao do van Ao hai mảnh hẹp từ bẩm sinh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">C&aacute;c biến chứng hẹp van Ao kh&ocirc;ng phẫu thuật: đột tử, loạn nhịp thất, suy tim tr&aacute;i.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Chỉ định phẫu thuật dựa v&agrave;o triệu chứng cơ năng (cơn đau thắt ngực, kh&oacute; thở gắng sức, ngất) v&agrave; mức độ hẹp.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Cần phẫu thuật khi hẹp van Ao nặng hoặc độ ch&ecirc;nh &aacute;p lực thất tr&aacute;i - động mạch chủ </span><span style=\"font-family:Symbol\">&sup3;</span><span lang=\"NL\" style=\"font-family:Arial;mso-ansi-language:NL\"> 70mmHg.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-size:14.0pt;\r\nline-height:150%;font-family:Arial;mso-ansi-language:NL\">Hẹp eo động mạch chủ</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">90% hẹp ở phần đầu Ao xuống, ngay s&aacute;t ĐM dưới đ&ograve;n tr&aacute;i. Người ta ph&acirc;n biệt hẹp Ao: trước, sau OĐM.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Biến chứng: suy tim, bệnh van Ao, vỡ hoặc b&oacute;c t&aacute;ch Ao gần chỗ hẹp...</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"NL\" style=\"font-family:Arial;\r\nmso-ansi-language:NL\">Tr&aacute;nh bỏ s&oacute;t hẹp eo Ao cần khảo s&aacute;t tất cả bệnh nh&acirc;n cao huyết &aacute;p&lt; 50 tuổi xem c&oacute; hẹp eo Ao hay kh&ocirc;ng.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"IT\" style=\"font-family:Arial;\r\nmso-ansi-language:IT\">Điều trị nội bằng truyền prostaglandin E1.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"IT\" style=\"font-family:Arial;\r\nmso-ansi-language:IT\">Điều trị bằng nong b&oacute;ng vẫn được ưa chuộng ở trẻ sơ sinh v&agrave; trẻ nhỏ hơn 1 tuổi.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"IT\" style=\"font-family:Arial;\r\nmso-ansi-language:IT\">Chỉ định phẫu thuật sớm hay muộn tuỳ thuộc v&agrave;o triệu chứng suy tim.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"IT\" style=\"font-family:Arial;\r\nmso-ansi-language:IT\">ở trẻ nhỏ kh&ocirc;ng triệu chứng suy tim, hẹp eo Ao c&oacute; &yacute; nghĩa khi độ ch&ecirc;nh &aacute;p lực t&acirc;m thu &gt; 40mmHg. Thời điểm phẫu thuật th&iacute;ch hợp từ 3<sup>th </sup>&ndash; 12<sup>th</sup>.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"IT\" style=\"font-size:14.0pt;\r\nline-height:150%;font-family:Arial;mso-ansi-language:IT\">Hội chứng Eisenmenger</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span lang=\"IT\" style=\"font-family:Arial;\r\nmso-ansi-language:IT\">Tim bẩm sinh c&oacute; th&ocirc;ng tr&aacute;i-phải kh&ocirc;ng được phẫu thuật sớm, th&ocirc;ng thường trước 12 th&aacute;ng tuổi c&oacute; biến đổi cơ học hệ mạch phổi. Khi tăng AL:PA cơ học, d&ugrave; c&oacute; phẫu thuật b&iacute;t d&ograve;ng chảy th&ocirc;ng, AL:PA kh&ocirc;ng giảm sau phẫu thuật, l&uacute;c n&agrave;y d&ograve;ng chảy 2 chiều hoặc đảo ho&agrave;n to&agrave;n dẫn đến suy tim. Khoảng 5% bệnh ASD c&oacute; thể bị chứng Eisenmenger v&agrave;o tuổi trưởng th&agrave;nh.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Bệnh nh&acirc;n bị hội chứng Eisenmeger c&oacute; triệu chứng v&agrave;o khoảng 40 tuổi: hở 3 l&aacute; do gi&atilde;n v&ograve;ng van RV, suy tim, loạn nhịp nhĩ. Bệnh nh&acirc;n c&oacute; thể tử vong v&igrave; giảm O<sub>2</sub> m&aacute;u cấp hoặc loạn nhịp thất.</span></p>\r\n<p class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:auto;\r\ntext-align:justify;line-height:150%\"><span style=\"font-family:Arial\">Biện ph&aacute;p điều trị duy nhất l&agrave; gh&eacute;p tim phổi hoặc gh&eacute;p một l&aacute; phổi k&egrave;m đ&oacute;ng lỗ th&ocirc;ng.</span></p>\r\n<p align=\"right\" class=\"MsoNormal\" style=\"margin-top:12.0pt;mso-margin-bottom-alt:\r\nauto;text-align:right;line-height:150%\"><b><i><span style=\"font-family:Arial;\r\ncolor:maroon\"></span></i></b></p>\r\n<p>&nbsp;</p></span>\n', 2);
INSERT INTO `disease` (`id`, `title`, `content`, `idcat`) VALUES
(31, 'Định hướng điều trị bệnh tim bằng siêu âm doppler', '\n   <h1 class=\"title\">Định hướng điều trị bệnh tim bằng siêu âm doppler</h1>   <span class=\"des\">Hở van 2 lá cấp hoặc mãn, có thể hở đơn thuần hoặc kết hợp. Nguyên nhân chủ yếu là thấp tim, có khoảng 20% là sinh lý ở người bình thường.</span>\n   <span><p style=\"text-align: justify;\"><span style=\"font-size: 18pt; font-family: Arial;\">Bệnh van tim</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span style=\"font-family: Arial;\">C&aacute;c bệnh van tim c&oacute; thể điều trị hoặc kết hợp điều trị nội ngoại khoa, tuỳ giai đoạn v&agrave; nguy&ecirc;n nh&acirc;n.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Hở van hai l&aacute;</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span style=\"font-family: Arial;\">Hở van 2 l&aacute; cấp hoặc m&atilde;n, c&oacute; thể hở đơn thuần hoặc kết hợp. Nguy&ecirc;n nh&acirc;n chủ yếu l&agrave; thấp tim, c&oacute; khoảng 20% l&agrave; sinh l&yacute; ở người b&igrave;nh thường.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span style=\"font-family: Arial;\">Hở van 2 l&aacute; nặng, EF 40 - 50% l&agrave; đ&atilde; tổn thương cơ tim nhiều, nếu &lt; 40% nguy cơ khi mổ rất cao, d&ugrave; thay van cơ tim cũng kh&ocirc;ng hồi phục.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span style=\"font-family: Arial;\">Khi thấy l&aacute; van dầy, vận động l&aacute; sau giảm, l&aacute; trước sa hay r&uacute;t ngắn, d&acirc;y chằng dầy, d&iacute;nh nhau: nghĩ đến Hở van 2 l&aacute; hậu thấp.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span style=\"font-family: Arial; letter-spacing: 0.3pt;\">C&oacute; thể thấy h&igrave;nh ảnh v&ograve;ng van c&oacute; đậm độ cao k&egrave;m b&oacute;ng lưng: v&ocirc;i h&oacute;a v&ograve;ng van.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"SV\" style=\"font-family: Arial;\">Mảnh s&ugrave;i, d&acirc;y chằng đứt l&agrave;m sa van, c&oacute; thể l&aacute; van r&aacute;ch cho d&ograve;ng hở ngoại vi: Osler</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span style=\"font-family: Arial;\">L&aacute; van dầy, d&agrave;i, sa van: Tho&aacute;i ho&aacute; Mucine.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span style=\"font-family: Arial;\">Điện t&acirc;m đồ: Thường gặp l&agrave; gi&atilde;n nhĩ tr&aacute;i hoặc rung nhĩ.số &iacute;t c&oacute; gi&atilde;n thất tr&aacute;i, dầy thất phải.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><b><span style=\"font-family: Arial;\">Điều trị nội: </span></b></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span style=\"font-family: Arial;\">Ưc chế men chuyển, Digoxin khi rung nhĩ (Điều trị nội khoa kh&ocirc;ng giảm tiến triển bệnh, cần phẫu thuật ngay.)</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><b><span style=\"font-family: Arial;\">Điều trị ngoại khoa: </span></b></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span style=\"font-family: Arial;\">Hở van 2 l&aacute; </span><span style=\"font-family: Symbol;\">&sup3;</span><span style=\"font-family: Arial;\"> 3 + NYHA </span><span style=\"font-family: Symbol;\">&sup3;</span><span style=\"font-family: Arial;\"> 3: phẫu thuật. </span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span style=\"font-family: Arial;\">Hở van 2 l&aacute; </span><span style=\"font-family: Symbol;\">&sup3;</span><span style=\"font-family: Arial;\"> 3 + NYHA </span><span style=\"font-family: Symbol;\">&pound;</span><span style=\"font-family: Arial;\"> 2: theo d&otilde;i, nếu nhĩ tr&aacute;i, thất tr&aacute;i lớn dần hay rung nhĩ xuất hiện th&igrave; phải phẫu thuật.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span style=\"font-family: Arial;\">Ti&ecirc;n lượng xấu khi phẫu thuật: BN lớn tuổi, NYHA 4, điều trị nội &iacute;t thuy&ecirc;<span style=\"letter-spacing: 0.3pt;\">n giảm, kết hợp bệnh động mạch v&agrave;nh, đường k&iacute;nh thất tr&aacute;i &gt; 25% b&igrave;nh thường, &aacute;p lực động mạch phổi &gt; 100mmHg, EF &lt; 40% (EF ở bệnh nh&acirc;n Hở van 2 l&aacute; b&igrave;nh thường &gt; 70%, ngay cả khi bệnh nặng, suy tim mất b&ugrave; EF cũng chỉ giảm nhẹ).</span></span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Hẹp van 2 l&aacute;</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"SV\" style=\"font-family: Arial;\">Van 2 l&aacute; gồm 2 l&aacute;, l&aacute; trước lớn, l&aacute; sau nhỏ. Kỳ t&acirc;m trương S = 4-6cm<sup>2</sup>, coi l&agrave; hẹp khi S </span><span style=\"font-family: Symbol;\">&pound;</span><span lang=\"SV\" style=\"font-family: Arial;\"> 2 cm<sup>2 </sup>(1,2 cm<sup>2</sup>/m<sup>2</sup>da). Hẹp kh&iacute;t khi S </span><span style=\"font-family: Symbol;\">&pound;</span><span lang=\"SV\" style=\"font-family: Arial;\"> 1 cm<sup>2</sup> (0,6cm<sup>2</sup>/m<sup>2</sup> da).</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"SV\" style=\"font-family: Arial; letter-spacing: -0.2pt;\">Khi &aacute;p lực động mạch phổi &gt; 70 mmHg sẽ vượt qu&aacute; sức chịu đựng thất phải </span><span style=\"font-family: Symbol; letter-spacing: -0.2pt;\">&reg;</span><span lang=\"SV\" style=\"font-family: Arial; letter-spacing: -0.2pt;\"> suy&nbsp; thất phải v&agrave; hở 3 l&aacute;.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"SV\" style=\"font-family: Arial;\">Hở van 2 l&aacute; nhẹ khi: S &gt; 2 cm<sup>2</sup>, vừa: 1-2cm<sup>2</sup>, nặng &lt; 1 cm<sup>2</sup>, rất nặng &lt; 0,8cm<sup>2</sup></span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><b><span lang=\"SV\" style=\"font-family: Arial;\">Điều trị nội: </span></b></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"SV\" style=\"font-family: Arial;\">Ph&ograve;ng thấp, ph&ograve;ng vi&ecirc;m nội t&acirc;m mạc nhiễm tr&ugrave;ng khi nhổ răng...</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"SV\" style=\"font-family: Arial;\">Chỉ giảm được triệu chứng cơ năng, chưa ngăn được tiến triển bệnh.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"SV\" style=\"font-family: Arial;\">Chẹn </span><span style=\"font-family: Symbol;\">b</span><span lang=\"SV\" style=\"font-family: Arial;\"> để chậm tần số tim, Digoxin khi c&oacute; rung nhĩ, lợi tiểu khi c&oacute; tăng &aacute;p PA hoặc suy tim phải, kh&aacute;ng đ&ocirc;ng khi rung nhĩ hoặc m&aacute;u đ&ocirc;ng nhĩ tr&aacute;i hoặc hẹp kh&iacute;t m&agrave; nhĩ tr&aacute;i &gt; 50mm.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><b><span lang=\"SV\" style=\"font-family: Arial;\">Điều trị ngoại: </span></b></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><i><span lang=\"SV\" style=\"font-family: Arial;\">Nong van - sửa van khi van tổn thương &iacute;t v&agrave; khi: </span></i></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"SV\" style=\"font-family: Arial;\">Hẹp van 2 l&aacute; kh&iacute;t</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"SV\" style=\"font-family: Arial;\">Hẹp van 2 l&aacute; nặng + biến chứng rung nhĩ mới.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"NL\" style=\"font-family: Arial;\">Hẹp van 2 l&aacute; nặng + NYHA </span><span style=\"font-family: Symbol;\">&sup3;</span><span lang=\"NL\" style=\"font-family: Arial;\"> 2.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"NL\" style=\"font-family: Arial;\">C&oacute; cơn thuy&ecirc;n tắc d&ugrave; đang c&oacute; hay kh&ocirc;ng điều trị kh&aacute;ng đ&ocirc;ng.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"NL\" style=\"font-family: Arial;\">K&egrave;m tăng &aacute;p ĐMP.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><i><span lang=\"SV\" style=\"font-family: Arial;\">Thay van: </span></i></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"SV\" style=\"font-family: Arial;\">Van bị tổn thương nhiều + NYHA </span><span lang=\"SV\" style=\"font-family: Symbol;\">&sup3;</span><span lang=\"SV\" style=\"font-family: Arial;\"> 3.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"NL\" style=\"font-size: 14pt; line-height: 150%; font-family: Arial;\">Hở van động mạch chủ</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"NL\" style=\"font-family: Arial;\">L&aacute; van dầy nhưng c&ograve;n giữ nguy&ecirc;n độ mở: Hậu thấp.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"NL\" style=\"font-family: Arial;\">Van ĐMC hai mảnh.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"FR\" style=\"font-family: Arial;\">Van sigma sa xuống thất tr&aacute;i: Sa van.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"NL\" style=\"font-family: Arial;\">Mảnh s&ugrave;i tr&ecirc;n l&aacute; van, m&eacute;p van, c&oacute; khi r&aacute;ch van, c&oacute; thể thấy van sigma lật ngược v&agrave;o trong thất tr&aacute;i: Osler.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"NL\" style=\"font-family: Arial;\">ĐMC l&ecirc;n </span><span style=\"font-family: Symbol;\">f</span><span lang=\"NL\" style=\"font-family: Arial;\"> &gt; 42 mm + t&aacute;ch v&aacute;ch song song &gt; 16 mm </span><span style=\"font-family: Symbol;\">&THORN;</span><span lang=\"NL\" style=\"font-family: Arial;\"> b&oacute;c t&aacute;ch động mạch chủ.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"NL\" style=\"font-family: Arial;\">Gia tăng </span><span style=\"font-family: Symbol;\">f</span><span lang=\"NL\" style=\"font-family: Arial;\"> ĐMC &gt; 55mm: T&uacute;i ph&igrave;nh động mạch chủ.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"NL\" style=\"font-family: Arial;\">T&uacute;i ph&igrave;nh xoang Valsalva: sa 1 van sigma động mạch chủ v&agrave;o buồng tim, thường l&agrave; thất phải đ&ocirc;i khi NP v&agrave; rất hiếm ở thất tr&aacute;i.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"NL\" style=\"font-family: Arial;\">Khi đường k&iacute;nh thất tr&aacute;i &gt; 70 mm ở t&acirc;m trương v&agrave; nhất l&agrave; &gt; 50 t&acirc;m thu, k&egrave;m ph&acirc;n xuất co b&oacute;p &lt; 25% l&agrave; biểu hiện tổn thương cơ tim, cần được phẫu thuật.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><b><span lang=\"NL\" style=\"font-family: Arial;\">Điều trị:</span></b></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><i><span lang=\"NL\" style=\"font-family: Arial;\">Nội khoa: </span></i></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"NL\" style=\"font-family: Arial;\">Ph&ograve;ng thấp, ngừa vi&ecirc;m nội t&acirc;m mạc, điều trị suy tim.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"NL\" style=\"font-family: Arial;\">Khi chưa c&oacute; rối loạn chức năng thất tr&aacute;i chỉ d&ugrave;ng lợi tiểu + gi&atilde;n mạch. khi thất tr&aacute;i lớn, EF giảm cần th&ecirc;m Digoxin (thuốc gi&atilde;n mạch bắt đầu bằng liều thấp </span><span style=\"font-family: Symbol;\">&reg;</span><span lang=\"NL\" style=\"font-family: Arial;\"> ức chế men chuyển, Ca...)</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"NL\" style=\"font-family: Arial;\">Hở van ĐMC độ 1-2 chưa c&oacute; suy tim kh&ocirc;ng cần điều trị, hở độ 3-4 d&ugrave; chưa c&oacute; triệu chứng cơ năng cần c&acirc;n nhắc d&ugrave;ng gi&atilde;n mạch, Nifedipin c&oacute; thể l&agrave;m chậm rối loạn chức năng thất tr&aacute;i.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><i><span lang=\"NL\" style=\"font-family: Arial;\">Ngoại khoa: </span></i></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"PT-BR\" style=\"font-family: Arial;\">Hở van </span><span lang=\"NL\" style=\"font-family: Arial;\">động mạch chủ</span><span lang=\"PT-BR\" style=\"font-family: Arial;\"> + NYHA </span><span style=\"font-family: Symbol;\">&sup3;</span><span lang=\"PT-BR\" style=\"font-family: Arial;\"> 3 tử vong 95% sau 10 năm.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"PT-BR\" style=\"font-family: Arial;\">Hở van </span><span lang=\"NL\" style=\"font-family: Arial;\">động mạch chủ</span><span lang=\"PT-BR\" style=\"font-family: Arial;\"> độ&nbsp; </span><span style=\"font-family: Symbol;\">&sup3;</span><span lang=\"PT-BR\" style=\"font-family: Arial;\"> 3 + NYHA </span><span style=\"font-family: Symbol;\">&sup3;</span><span lang=\"PT-BR\" style=\"font-family: Arial;\"> 3 cần phẫu thuật d&ugrave; chưa c&oacute; rối loạn chức năng thất tr&aacute;i.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"PT-BR\" style=\"font-family: Arial;\">EF &lt; 55%, đường k&iacute;nh t&acirc;m thu thất tr&aacute;i &gt; 50mm: l&ecirc;n lịch phẫu thuật trong 12 th&aacute;ng d&ugrave; chưa c&oacute; triệu chứng cơ năng.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><span lang=\"PT-BR\" style=\"font-family: Arial;\">Hở van</span><span lang=\"PT-BR\" style=\"font-family: Arial;\"> </span><span lang=\"NL\" style=\"font-family: Arial;\">động mạch chủ</span><span lang=\"NL\" style=\"font-family: Arial;\"> </span><span lang=\"PT-BR\" style=\"font-family: Arial;\">+ chưa c&oacute; triệu chứng cơ năng hoặc chưa c&oacute; rối loạn chức năng thất tr&aacute;i: Theo d&otilde;i 3-6 th&aacute;ng/lần.</span></p>\r\n<p style=\"margin-top: 12pt; text-align: justify; line-height: 150%;\" class=\"MsoNormal\"><i><span lang=\"NL\" style=\"fon